(2, 'Anatomy', 'PM2393. Derivatives of Mesoderm', '<div _ngcontent-bnw-c142=\\ class=\ng-tns-c142-9 ng-star-inserted\ style=\\>\n      <div _ngcontent-bnw-c142=\\ class=\ng-tns-c142-9 ng-star-inserted\><p>&nbsp;</p>\n<table>\n<tbody>\n<tr>\n<td colspan=\2\><strong>Mesoderm derivatives</strong></td>\n</tr>\n<tr>\n<td><strong>Paraxial mesoderm</strong></td>\n<td>\n<ul>\n<li>Skeletal muscles of the trunk, limbs,<strong>&nbsp;</strong>head, and neck</li>\n<li>Extraocular muscles</li>\n<li>Intrinsic muscles of the tongue</li>\n<li>Bones: Vertebrae, ribs</li>\n<li>Dermis of Skin</li>\n<li>Duramater</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td><strong>Intermediate mesoderm</strong></td>\n<td>\n<ul>\n<li>Urinary system: Kidneys, ureters, trigone of the urinary bladder</li>\n<li>Dorsal part of prostatic urethra (males)</li>\n<li>Gonads: Testes and ovaries</li>\n<li>Genital ducts</li>\n<li>Adrenal cortex</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td><strong>Lateral plate mesoderm</strong></td>\n<td>\n<ul>\n<li>Bones: Sternum, limb bones</li>\n<li>Serous membranes of body cavities: Pleura, pericardium, peritoneum</li>\n<li>GIT layers: Lamina propria, muscularis mucosae, submucosa, muscularis externa, adventitia</li>\n<li>Blood cells: Microglia, Kupffer cells</li>\n<li>Cardiovascular system</li>\n<li>Lymphatic system</li>\n<li>Laryngeal cartilages</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(3, 'Anatomy,', 'PM2351. Embryology of the ear', '<div _ngcontent-bnw-c142=\\ class=\ng-tns-c142-9 ng-star-inserted\ style=\\>\n      <div _ngcontent-bnw-c142=\\ class=\ng-tns-c142-9 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Structures of the ear</strong></td>\n<td><strong>Embryological origin</strong></td>\n</tr>\n<tr>\n<td>Auricle or pinna</td>\n<td>1st and 2nd pharyngeal arch</td>\n</tr>\n<tr>\n<td>External auditory meatus</td>\n<td>1st pharyngeal arch</td>\n</tr>\n<tr>\n<td>Tympanic membrane</td>\n<td>All 3 germ layers</td>\n</tr>\n<tr>\n<td>Malleus and incus</td>\n<td>1st pharyngeal arch</td>\n</tr>\n<tr>\n<td>Stapes suprastructure</td>\n<td>2nd pharyngeal arch</td>\n</tr>\n<tr>\n<td>Stapes footplate and annular ligament</td>\n<td>Otic capsule</td>\n</tr>\n<tr>\n<td>Tympanic cavity</td>\n<td>1st pharyngeal pouch</td>\n</tr>\n<tr>\n<td>Eustachian tube</td>\n<td>1st pharyngeal pouch</td>\n</tr>\n<tr>\n<td>Membranous labyrinth</td>\n<td>Otic vesicle</td>\n</tr>\n<tr>\n<td>Semicircular canals and utricle</td>\n<td>Pars superior</td>\n</tr>\n<tr>\n<td>Saccule and cochlea</td>\n<td>Pars inferior</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(4, 'Obstetrics & Gynaecology,', 'PM2348. Homologous structures in male and female genitalia', '<div _ngcontent-bnw-c142=\\ class=\ng-tns-c142-9 ng-star-inserted\ style=\\>\n      <div _ngcontent-bnw-c142=\\ class=\ng-tns-c142-9 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Females</strong></td>\n<td><strong>Embryonal structure</strong></td>\n<td><strong>Males</strong></td>\n</tr>\n<tr>\n<td>Ovary</td>\n<td><strong>Genital ridge</strong></td>\n<td>Testis</td>\n</tr>\n<tr>\n<td>Ova</td>\n<td><strong>Primordial germ cells</strong></td>\n<td>Spermatozoa</td>\n</tr>\n<tr>\n<td>Labia majora</td>\n<td><strong>Labioscrotal swelling</strong></td>\n<td>Scrotum</td>\n</tr>\n<tr>\n<td>Labia minora</td>\n<td><strong>Urogenital folds</strong></td>\n<td>Ventral aspect of the penis - penile urethra</td>\n</tr>\n<tr>\n<td>Glans clitoris</td>\n<td><strong>Genital tubercle</strong></td>\n<td>Glans penis</td>\n</tr>\n<tr>\n<td>\n<p class=\p1\>Vestibular bulbs</p>\n&nbsp;</td>\n<td><strong>Genital tubercle</strong></td>\n<td>\n<p class=\p1\>Corpus cavernosum</p>\n<p class=\p1\>Corpus spongiosum</p>\n</td>\n</tr>\n<tr>\n<td>\n<p class=\p1\>Urethral and paraurethral glands /Skenes glands</p>\nLower part of Vagina&nbsp;</td>\n<td><strong>Urogenital sinus</strong></td>\n<td>&nbsp;\n<p class=\p1\>Prostate gland</p>\n</td>\n</tr>\n<tr>\n<td>Greater vestibular glands/Bartholins glands</td>\n<td><strong>Urogenital sinus</strong></td>\n<td>Bulbourethral glands/Cowpers glands</td>\n</tr>\n<tr>\n<td>Granulosa cells</td>\n<td><strong>Sex cords</strong></td>\n<td>Seminiferous tubules and Sertoli cells</td>\n</tr>\n<tr>\n<td>Round ligament</td>\n<td><strong>Gubernaculum</strong></td>\n<td>Gubernaculum testis</td>\n</tr>\n<tr>\n<td>Epoophoron and paroophoron</td>\n<td><strong>Mesonephric tubules</strong></td>\n<td>Efferent ductules and paradidymis</td>\n</tr>\n<tr>\n<td>Gartner duct (remnant structure)</td>\n<td><strong>Mesonephric duct/Wolffian duct</strong></td>\n<td>Epididymis, ductus deferens, and ejaculatory duct</td>\n</tr>\n<tr>\n<td>Uterus, fallopian tube, cervix and the upper vaginal wall</td>\n<td><strong>Paramesonephric duct/Mullerian duct</strong></td>\n<td>Prostatic utricle and the appendix of testis (remnant structure)</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(5, 'Obstetrics & Gynaecology,', 'PM2236. Twinning', '<div _ngcontent-bnw-c142=\\ class=\ng-tns-c142-9 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-bnw-c142=\\ class=\ng-tns-c142-9 ng-star-inserted\>\n        <img _ngcontent-bnw-c142=\\ class=\img-responsive ng-tns-c142-9 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/c4c8753494db4694a4b67e77bcea1502x1280x5795.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(6, 'Surgery', 'PM2508. Patient Positioning in Operation Theatre', '<div _ngcontent-cnv-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n      <!---->\n      <div _ngcontent-cnv-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-cnv-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/ada28967e12f4224bf885433eb416e6cx600x2287.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(7, 'Anatomy', 'PM1974. Neural crest derivatives', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table class=\t1\ cellspacing=\0\ cellpadding=\0\>\n<tbody>\n<tr>\n<td class=\td1\ valign=\top\>\n<p class=\p1\><strong>Neural</strong></p>\n</td>\n<td class=\td1\ valign=\top\>\n<p class=\p1\><strong>Mesenchymal</strong></p>\n</td>\n</tr>\n<tr>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>All ganglions</p>\n</td>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>Connective tissue and bones of the face and skull&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>All enteric plexus</p>\n</td>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>Dermis of the head and face</p>\n</td>\n</tr>\n<tr>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>Schwann cells and Satellite glial cells</p>\n</td>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>Odontoblasts (Dentine)</p>\n</td>\n</tr>\n<tr>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>Adrenal Medulla</p>\n</td>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>Arachnoid and Piamater&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>Parafolliular C cells of Thyroid</p>\n</td>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>Conotruncal septum in the heart</p>\n</td>\n</tr>\n<tr>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>Melanoblast/Melanocytes (except Retinal Pigment Epithelium)</p>\n</td>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>&nbsp;</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(8, 'Ophthalmology,', 'PM1888. Summary of ocular embryogenesis', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<div><strong>Period after conception</strong></div>\n</td>\n<td>\n<div><strong>Major milestone</strong></div>\n</td>\n</tr>\n<tr>\n<td>\n<div>3 weeks</div>\n</td>\n<td>\n<div>\n<ul>\n<li><strong>Optic groove&nbsp;</strong>appears</li>\n</ul>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div>4<sup>th</sup> week</div>\n</td>\n<td>\n<div>\n<ul>\n<li>Optic pit develops into an optic vesicle</li>\n<li><strong>Lens plate</strong> forms</li>\n<li><strong>Embryonic fissure</strong> develops&nbsp;</li>\n</ul>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div>1<sup>st</sup> month</div>\n</td>\n<td>\n<div>\n<ul>\n<li><strong>Lens pit</strong> develops, followed by formation of the&nbsp;<strong>lens vesicle</strong></li>\n<li><strong>Hyaloid vessels</strong> develop</li>\n</ul>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div>1.5 months</div>\n</td>\n<td>\n<div>\n<ul>\n<li>Closure of the embryonic fissure</li>\n<li>Differentiation of retinal pigment epithelium</li>\n<li>Appearance of eyelid folds and&nbsp;<strong>nasolacrimal duct</strong></li>\n</ul>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div>7<sup>th</sup> week</div>\n</td>\n<td>\n<div>\n<ul>\n<li>Embryonic nucleus of the&nbsp;lens</li>\n<li><strong>Sclera</strong> begins to form</li>\n<li>Migration of waves of the neural crest</li>\n<li>1<sup>st</sup> wave: corneal and trabecular endothelium</li>\n<li>2<sup>nd</sup> wave: corneal stroma</li>\n<li>3<sup>rd</sup> wave: iris stroma</li>\n</ul>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div>3<sup>rd</sup>&nbsp; month</div>\n</td>\n<td>\n<div>\n<ul>\n<li>Differentiation of precursors of rods and cone</li>\n<li><strong>Anterior chamber</strong> appears</li>\n<li><strong>Fetal nucleus</strong> starts to develop</li>\n<li>Sclera condenses&nbsp;</li>\n</ul>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div>4<sup>th</sup> month</div>\n</td>\n<td>\n<div>\n<ul>\n<li>Formation of&nbsp;<strong>retinal vasculature</strong> begins</li>\n<li><strong>Hyaloid vessels regress</strong></li>\n<li>Formation of physiological optic disc cup and <strong>lamina cribrosa</strong></li>\n<li><strong>Canal of Schlemm</strong> appears</li>\n<li><strong>Bowmans membrane</strong> develops</li>\n<li>Formation of the&nbsp;major arterial circle and <strong>sphincter muscle </strong>of the&nbsp;iris</li>\n</ul>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div>5<sup>th</sup> month</div>\n</td>\n<td>\n<div>\n<ul>\n<li>Photoreceptors differentiate</li>\n<li>Eyelid separation begins</li>\n</ul>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div>6<sup>th</sup> month</div>\n</td>\n<td>\n<div>\n<ul>\n<li>Differentiation of the&nbsp;<strong>dilator pupillae</strong> muscle</li>\n<li><strong>Nasolacrimal</strong> system becomes<strong> patent</strong></li>\n<li>Cones differentiate</li>\n</ul>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div>7<sup>th</sup> month</div>\n</td>\n<td>\n<div>\n<ul>\n<li>Rods differentiate</li>\n<li><strong>Myelination of optic nerve</strong> begins</li>\n</ul>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div>8<sup>th</sup> month</div>\n</td>\n<td>\n<div>\n<ul>\n<li>Completion of the&nbsp;anterior chamber angle</li>\n<li><strong>Hyaloid vessels disappear</strong></li>\n</ul>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div>9<sup>th</sup> month</div>\n</td>\n<td>\n<div>\n<ul>\n<li>Pupillary membrane disappears</li>\n</ul>\n</div>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(9, 'Ophthalmology,', 'PM1239. Embryological derivatives of the eye', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Neural Ectoderm</strong></p>\n</td>\n<td>\n<p><strong>Surface Ectoderm</strong></p>\n</td>\n<td>\n<p><strong>Mesoderm</strong></p>\n</td>\n<td>\n<p><strong>Neural Crest </strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Smooth muscles of iris (sphincter and dilator pupillae)</p>\n</td>\n<td>\n<p>Crystalline lens</p>\n</td>\n<td>\n<p>Extraocular muscles&nbsp;</p>\n</td>\n<td>\n<p>Corneal stroma, keratocytes, and endothelium</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Ciliary epithelium</p>\n</td>\n<td>\n<p>Corneal epithelium</p>\n</td>\n<td>\n<p>Superotemporal part of Sclera</p>\n</td>\n<td>\n<p>Sclera&nbsp;(except&nbsp;supero-temporally)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Iris epithelium&nbsp;</p>\n</td>\n<td>\n<p>Conjunctival epithelium</p>\n</td>\n<td>\n<p>Choroidal vessels</p>\n</td>\n<td>\n<p>Iris and&nbsp; Choroid stroma</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Fibers of the optic nerve</p>\n</td>\n<td>\n<p>Lacrimal gland&nbsp;</p>\n</td>\n<td>\n<p>Primary vitreous</p>\n</td>\n<td>\n<p>Uveal&nbsp;melanocytes</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Secondary and tertiary vitreous</p>\n</td>\n<td>\n<p>Tarsal glands</p>\n</td>\n<td>\n<p>Vascular&nbsp;&nbsp;&nbsp;&nbsp; endothelium</p>\n</td>\n<td>\n<p>Conjunctival melanocytes</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Retina and retinal pigment epithelium</p>\n</td>\n<td>&nbsp;</td>\n<td>&nbsp;</td>\n<td>\n<p>Ciliary muscles</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(10, 'Paediatrics,', 'PM1215. Locations of Congenital Heart Disease', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://cdn1.dailyrounds.org/uploads/51b03a1175d241a3a9e4f641a4f8880ax631x622.PNG\><!---->\n      </div><!---->\n    </div><!---->'),
(11, 'Anatomy,', 'PM1065. Difference between gastroschisis and omphalocele', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Characteristic</strong></td>\n<td><strong>Gastroschisis</strong></td>\n<td><strong>Omphalocele</strong></td>\n</tr>\n<tr>\n<td>Defect size (diameter)</td>\n<td>2-3 cm</td>\n<td>2-15 cm</td>\n</tr>\n<tr>\n<td>Sac</td>\n<td>Never</td>\n<td>Always, may be ruptured</td>\n</tr>\n<tr>\n<td>Gestational age</td>\n<td>Prematurity</td>\n<td>Term</td>\n</tr>\n<tr>\n<td>Umbilical cord</td>\n<td>Adjacent to the defect (defect is seen usually on its right side)&nbsp;</td>\n<td>Attached to sac</td>\n</tr>\n<tr>\n<td>Herniated viscera</td>\n<td>Small bowel, stomach, colon</td>\n<td>Small bowel, stomach, colon, liver</td>\n</tr>\n<tr>\n<td>Malrotation</td>\n<td>Yes</td>\n<td>Yes</td>\n</tr>\n<tr>\n<td>Bowel character</td>\n<td>Inflammatory, edematous</td>\n<td>Normal</td>\n</tr>\n<tr>\n<td>Enternal nutrition</td>\n<td>Delayed</td>\n<td>Normal</td>\n</tr>\n<tr>\n<td>Associated abnormalities</td>\n<td>Uncommon (10% atresia)</td>\n<td>Common (50%)</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(12, 'Anatomy', 'PM0981. Embryological structures and their derivatives in the genital system', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\205\>\n<p><strong>Embryological structure</strong></p>\n</td>\n<td width=\205\>\n<p><strong>Derivative in female</strong></p>\n</td>\n<td width=\205\>\n<p><strong>Derivative in male</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\205\>\n<p>Genital ridge</p>\n</td>\n<td width=\205\>\n<p>Ovary</p>\n</td>\n<td width=\205\>\n<p>Testis</p>\n</td>\n</tr>\n<tr>\n<td width=\205\>\n<p>Genital swelling</p>\n</td>\n<td width=\205\>\n<p>Labia majora</p>\n</td>\n<td width=\205\>\n<p>Scrotum</p>\n</td>\n</tr>\n<tr>\n<td width=\205\>\n<p>Genital fold</p>\n</td>\n<td width=\205\>\n<p>Labia minora</p>\n</td>\n<td width=\205\>\n<p>Ventral aspect of the penis, penile urethra</p>\n</td>\n</tr>\n<tr>\n<td width=\205\>\n<p>Genital tubercle</p>\n</td>\n<td width=\205\>\n<p>Clitoris</p>\n</td>\n<td width=\205\>\n<p>Glans penis</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(13, 'Anatomy', 'PM0957. Brainstem Nuclei Derived from Alar and Basal Lamina', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\278\>\n<p><strong>Alar Lamina</strong></p>\n</td>\n<td width=\200\>\n<p><strong>Basal Lamina</strong></p>\n</td>\n</tr>\n<tr>\n<td colspan=\2\ width=\278\>\n<p><strong>Midbrain&nbsp;</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\278\>\n<p>Colliculi</p>\n<p>Substantia nigra</p>\n<p>Red nucleus</p>\n<p>Mesencephalic nucleus of trigeminal nerve (GSA)</p>\n</td>\n<td width=\200\>\n<p>Oculomotor nucleus (GSE)</p>\n<p>Edinger-Westphal nucleus (GVE)</p>\n<p>Trochlear nucleus (GSE)</p>\n</td>\n</tr>\n<tr>\n<td colspan=\2\ width=\278\>\n<p><strong>Pons&nbsp;</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\278\>\n<p>Pontine nuclei</p>\n<p>Vestibular nuclei (SSA)</p>\n<p>Cochlear nuclei (SSA)</p>\n<p>Main sensory nucleus of trigeminal nerve (GSA)</p>\n<p>Nucleus of spinal tract of trigeminal nerve (GSA)</p>\n<p>Nucleus of tractus solitaries (SVA and GVA)</p>\n</td>\n<td width=\200\>\n<p>Motor nucleus of trigeminal nerve (SVE)</p>\n<p>Motor nucleus of facial nerve (SVE)</p>\n<p>Nucleus of abducent nerve (GSE)</p>\n<p>Superior salivatory nucleus (GVE)</p>\n<p>Lacrimatory nucleus (GVE)</p>\n</td>\n</tr>\n<tr>\n<td colspan=\2\ width=\278\>\n<p><strong>Medulla Oblongata&nbsp;</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\278\>\n<p>Inferior olivary nuclei</p>\n<p>Vestibular nuclei (SSA)</p>\n<p>Nucleus of spinal tract of trigeminal nerve (GSA)</p>\n<p>Nucleus of tractus solitarius(SVA and GVA)</p>\n<p>Part of dorsal nucleus vagus (GVA)</p>\n</td>\n<td width=\200\>\n<p>Part of dorsal nucleus of vagus (GVE)</p>\n<p>Inferior salivatory nucleus (GVE)</p>\n<p>Nucleus ambiguous (SVE)</p>\n<p>Hypoglossal nucleus (GSE)</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p><strong>Abbreviations</strong></p>\n<p>GSA: General Somatic Afferent</p>\n<p>GSE: General Somatic Efferent</p>\n<p>GVA: General Visceral Afferent</p>\n<p>GVE: General Visceral Efferent</p>\n<p>SSA: Special Somatic Afferent</p>\n<p>SVA: Special Visceral Afferent</p>\n<p>SVE: Special Visceral Efferent</p></div><!---->\n      <!---->\n    </div><!---->'),
(14, 'Anatomy', 'PM0955. Divisions of the Developing Brain', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/d1a0b9bf78c84473b8735b0a4eb1c2f4x1280x1183.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\146\>\n<tbody>\n<tr>\n<td><span><strong>Divisions of the Developing Brain</strong></span></td>\n</tr>\n<tr>\n<td><span><strong>Forebrain vesicle/Prosencephalon</strong></span></td>\n</tr>\n<tr>\n<td>\n<p><span>Telencephalon</span></p>\n<ul>\n<li><span>Cerebral cortex</span></li>\n<li><span>Subcortical white matter</span></li>\n<li><span>Amygdala</span></li>\n<li><span>Basal ganglia</span></li>\n<li><span>Hippocampus</span></li>\n<li><span>Olfactory bulb and tract</span></li>\n<li><span>Cavities: Lateral ventricles</span></li>\n</ul>\n<p><span>Diencephalon</span></p>\n<ul>\n<li><span>Thalamus</span></li>\n<li><span>Hypothalamus</span></li>\n<li><span>Epithalamus</span></li>\n<li><span>Subthalamus</span></li>\n<li><span>Optic nerve and retina</span></li>\n<li><span>Cavities: 3rd ventricle</span></li>\n</ul>\n</td>\n</tr>\n<tr>\n<td><span><strong>Midbrain vesicle/Mesencephalon</strong></span></td>\n</tr>\n<tr>\n<td>\n<p><span>Mesencephalon</span></p>\n<ul>\n<li><span>Midbrain</span></li>\n<li><span>Cavities: Cerebral Aqueduct</span></li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>\n<p><span><strong>Hindbrain&nbsp;vesicle/Rhombencephalon</strong></span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><span>Metencephalon</span></p>\n<ul>\n<li><span>Pons</span></li>\n<li><span>Cerebellum&nbsp;</span></li>\n</ul>\n<p><span>Myelencephalon</span></p>\n<ul>\n<li><span>Medulla oblongata</span></li>\n</ul>\n<p><span>Common Cavity: 4th Ventricle&nbsp;</span></p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(15, 'Anatomy,', 'PM0930. Development of Larynx', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td><strong>Part</strong></td>\n<td>\n<p><strong>Derivatives</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>2<sup>nd</sup>&nbsp;arch<sup>&nbsp;</sup></strong></p>\n</td>\n<td>\n<p>Upper part of the body of <strong>hyoid </strong>bone, lesser cornu&nbsp;of hyoid bone, stylohyoid ligament</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>3<sup>rd&nbsp;&nbsp;</sup>arch</strong></p>\n</td>\n<td>\n<p>Lower part of the body of <strong>hyoid</strong> bone and greater cornua</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>4<sup>th</sup> arch</strong></p>\n</td>\n<td>\n<p>Upper part of <strong>thyroid</strong> cartilage</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>6<sup>th</sup> arch<sup>&nbsp;</sup></strong></p>\n</td>\n<td>\n<p>Lower part of <strong>thyroid</strong> cartilage, <strong>cricoid</strong> cartilage, <strong>corniculate</strong> cartilage, <strong>cuneiform</strong> cartilage, <strong>intrinsic muscles of larynx</strong></p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(16, 'Anatomy', 'PM0923. Blood Supply and Derivatives of the Primitive Gut', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td><strong>Blood Supply&nbsp;</strong></td>\n<td><strong>Adult derivatives</strong></td>\n</tr>\n<tr>\n<td>Foregut</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td rowspan=\5\>Celiac artery (lower esophagus to&nbsp; duodenum)</td>\n<td>Pharynx</td>\n</tr>\n<tr>\n<td>Esophagus</td>\n</tr>\n<tr>\n<td>Stomach</td>\n</tr>\n<tr>\n<td>Upper duodenum</td>\n</tr>\n<tr>\n<td>Glands of pharyngeal pouches, respiratory tract, liver, gall bladder, pancreas</td>\n</tr>\n<tr>\n<td>Midgut</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td rowspan=\5\>Superior mesentric artery</td>\n<td>Lower duodenum</td>\n</tr>\n<tr>\n<td>Jejunum and ileum</td>\n</tr>\n<tr>\n<td>Cecum and vermiform appendix</td>\n</tr>\n<tr>\n<td>Ascending colon</td>\n</tr>\n<tr>\n<td>Proximal 2/3 of transverse colon</td>\n</tr>\n<tr>\n<td>Hindgut</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td rowspan=\5\>Inferior&nbsp;mesentric artery</td>\n<td>Distal 1/3 of transverse colon</td>\n</tr>\n<tr>\n<td>Descending&nbsp;colon</td>\n</tr>\n<tr>\n<td>Rectum</td>\n</tr>\n<tr>\n<td>Superior part of anal canal</td>\n</tr>\n<tr>\n<td>Urogenital sinus</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(17, 'Anatomy', 'PM0894. Derivatives of Various Aortic Arches', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>The <strong>aortic arches</strong> or <strong>pharyngeal arch arteries</strong> are a series of six paired embryological vascular structures which give rise to the great arteries of the neck and head. They are ventral to the dorsal aorta and arise from the aortic sac.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/9aeeaefabc8046dfa0c43fcd35211902x1280x1431.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>The following table shows various arterial structures derived from the aortic arches:<strong>&nbsp;</strong></p>\n<table>\n<tbody>\n<tr>\n<td width=\208\>\n<p><strong>Aortic arches</strong></p>\n</td>\n<td width=\208\>\n<p><strong>Arterial Structures</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\208\>\n<p>First aortic arches</p>\n</td>\n<td width=\208\>\n<p>Part of maxillary arteries bilaterally</p>\n</td>\n</tr>\n<tr>\n<td width=\208\>\n<p>Second aortic arches</p>\n</td>\n<td width=\208\>\n<p>Stapedial and hyoid arteries bilaterally</p>\n</td>\n</tr>\n<tr>\n<td width=\208\>\n<p>Third aortic arches</p>\n</td>\n<td width=\208\>\n<p>Common carotid arteries and proximal portion of internal carotid arteries bilaterally</p>\n</td>\n</tr>\n<tr>\n<td width=\208\>\n<p>Fourth aortic arches</p>\n</td>\n<td width=\208\>\n<p>Right: Proximal portion of the right subclavian artery</p>\n<p>Left: aortic arch</p>\n</td>\n</tr>\n<tr>\n<td width=\208\>\n<p>Fifth aortic arch</p>\n</td>\n<td width=\208\>\n<p>Regress completely</p>\n</td>\n</tr>\n<tr>\n<td width=\208\>\n<p>Sixth aortic arch</p>\n</td>\n<td width=\208\>\n<p>Right: proximal right pulmonary artery</p>\n<p>Left: proximal left pulmonary artery, ductus arteriosus</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(18, 'Anatomy', 'PM0893. Remnants of Various Embryonic Parts of the Cardiovascular System', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\312\>\n<p><strong>Embryonic part</strong></p>\n</td>\n<td width=\312\>\n<p><strong>Remnants </strong></p>\n</td>\n</tr>\n<tr>\n<td width=\312\>\n<p>Ductus arteriosus</p>\n</td>\n<td width=\312\>\n<p>Ligamentum arteriosum</p>\n</td>\n</tr>\n<tr>\n<td width=\312\>\n<p>Umbilical artery</p>\n</td>\n<td width=\312\>\n<p>Proximal part: Superior vesical artery; Distal part: Medial umbilical ligament</p>\n</td>\n</tr>\n<tr>\n<td width=\312\>\n<p>Umbilical vein (left)</p>\n</td>\n<td width=\312\>\n<p>Ligamentum teres</p>\n</td>\n</tr>\n<tr>\n<td width=\312\>\n<p>Ductus venosus</p>\n</td>\n<td width=\312\>\n<p>Ligamentum venosum</p>\n</td>\n</tr>\n<tr>\n<td width=\312\>\n<p>Septum primum</p>\n</td>\n<td width=\312\>\n<p>Floor of fossa ovalis</p>\n</td>\n</tr>\n<tr>\n<td width=\312\>\n<p>Septum secundum</p>\n</td>\n<td width=\312\>\n<p>Lower free edge: Annulus ovalis</p>\n<p>&nbsp;curved upper Margin: limbus fossa ovalis</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(19, 'Anatomy', 'PM0876. Development of Interatrial septum', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ul>\n<li>The<strong> Inter-atrial septum</strong>&nbsp;develops from Septum Primum and Septum Secundum</li>\n<li>The <strong>septum primum</strong> begins to develop at 28 days. It is a thin, crescentic fold of endocardium that arises craniodorsally and grows down to the AV cushions, leaving an <strong>ostium primum</strong> below its free edge.</li>\n<li>Septum primum&nbsp;<strong>fuses with the AV cushions</strong> at approximately 35 days, obliterating the ostium primum.</li>\n<li>The <strong>ostium secundum</strong> is an opening in the <strong>upper part of the septum primum</strong>. It forms at about 33 days, i.e., before the ostium primum closes.</li>\n<li>The <strong>septum secundum</strong>&nbsp;is a thick muscular septum that arises to the <strong>right of the septum primum</strong>.<strong>&nbsp;</strong>It grows from the roof of the atrium but <strong>does not reach the AV cushion</strong>. The gap between the two septa is known as <strong>foramen ovale</strong>.</li>\n<li>After birth, the <strong>foramen ovale is obliterated</strong> by the fusion of the septum primum with the septum secundum.</li>\n<li>In terms of adult anatomy, the <strong>annulus ovalis/limbus of fossa ovalis</strong> is the lower free edge of the <strong>septum secundum</strong>, while the floor of <strong>fossa ovalis</strong> represents the <strong>septum primum</strong>.</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/3985caef13264683a923fe2cbfd41b8dx1280x2019.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(20, 'Anatomy', 'PM0775. Timeline of Various Events Occurring during the Pre-embryonic Phase Development (1st two weeks)', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\122\>\n<p><strong>Time after Fertilization</strong></p>\n</td>\n<td width=\439\>\n<p><strong>Event</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\122\>\n<p>&lt; 24 hrs</p>\n</td>\n<td width=\439\>\n<p>Zygote is formed; cleavage begins immediately</p>\n</td>\n</tr>\n<tr>\n<td width=\122\>\n<p>2–3 days</p>\n</td>\n<td width=\439\>\n<p>5–8 cell stage</p>\n</td>\n</tr>\n<tr>\n<td width=\122\>\n<p>3–4 days</p>\n</td>\n<td width=\439\>\n<p>Stage of compaction and formation of morula with 16 cells</p>\n</td>\n</tr>\n<tr>\n<td width=\122\>\n<p>4–5 days</p>\n</td>\n<td width=\439\>\n<p>Formation of blastocyst</p>\n</td>\n</tr>\n<tr>\n<td width=\122\>\n<p>5–6 days</p>\n</td>\n<td width=\439\>\n<p>Zona pellucida disappears</p>\n</td>\n</tr>\n<tr>\n<td width=\122\>\n<p>6–7 days</p>\n</td>\n<td width=\439\>\n<p>Implantation of the blastocyst</p>\n</td>\n</tr>\n<tr>\n<td width=\122\>\n<p>9 days</p>\n</td>\n<td width=\439\>\n<p>Bilaminar disc formation</p>\n</td>\n</tr>\n<tr>\n<td width=\122\>\n<p>12 days</p>\n</td>\n<td width=\439\>\n<p>Formation of extraembryonic mesoderm</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(21, 'Anatomy', 'PM0724. Classification of Stem Cells', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\205\>\n<p><strong>Types of stem cells</strong></p>\n</td>\n<td width=\205\>\n<p><strong>Capacity to differentiate</strong></p>\n</td>\n<td width=\205\>\n<p><strong>Examples</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\205\>\n<p>Totipotent cells</p>\n</td>\n<td width=\205\>\n<p>They can form embryonic and extraembryonic cells</p>\n</td>\n<td width=\205\>\n<p>Zygote, early blastomeres</p>\n</td>\n</tr>\n<tr>\n<td width=\205\>\n<p>Pluripotent cells</p>\n</td>\n<td width=\205\>\n<p>They can form all cell types of an embryonic body but cannot form that of the placenta and umbilical cord</p>\n</td>\n<td width=\205\>\n<p>Inner cell mass</p>\n</td>\n</tr>\n<tr>\n<td width=\205\>\n<p>Multipotent cells</p>\n</td>\n<td width=\205\>\n<p>They form more than one category of cells</p>\n</td>\n<td width=\205\>\n<p>Hematopoietic stem cells</p>\n</td>\n</tr>\n<tr>\n<td width=\205\>\n<p>Oligopotent cells</p>\n</td>\n<td width=\205\>\n<p>They form only one category of cells</p>\n</td>\n<td width=\205\>\n<p>Vascular stem cells</p>\n</td>\n</tr>\n<tr>\n<td width=\205\>\n<p>Unipotent</p>\n</td>\n<td width=\205\>\n<p>They can form only one type of cell</p>\n</td>\n<td width=\205\>\n<p>Liver cell</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(22, 'Paediatrics,', 'PM0535. Clinical features of DiGeorge syndrome- Mnemonic', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>DiGeorge</strong> <strong>syndrome</strong>&nbsp;is a cellular immunodeficiency disorder caused by the deletion of a small segment of <strong>chromosome 22 (22q11)&nbsp;</strong>leading to a developmental defect involving the endodermal derivatives of the <strong>third and fourth pharyngeal pouches</strong>.&nbsp;(Thymus and parathyroid glands)</p>\n<p>The clinical features can be remembered with the <strong>mnemonic</strong> – <strong>CATCH</strong></p>\n<ul>\n<li><strong>C – C</strong>ardiac defects</li>\n<li><strong>A – A</strong>bnormal facies</li>\n<li><strong>T – T</strong>hymic aplasia</li>\n<li><strong>C – C</strong>left palate</li>\n<li><strong>H – H</strong>ypocalcemia</li>\n</ul>\n<p>The image below shows the abnormal facies in DiGeorge syndrome.&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/5b6e236b65034a479699ad3df12785ffx720x731.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(23, 'Pathology,', 'PM0177. Tissue Stem Cells and their Location', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\283\>\n<tbody>\n<tr>\n<td><strong>Tissue&nbsp;</strong></td>\n<td><strong>Location&nbsp;</strong></td>\n</tr>\n<tr>\n<td>Brain&nbsp;</td>\n<td>Subventricular&nbsp;zone and dentate gyrus</td>\n</tr>\n<tr>\n<td>Skin&nbsp;</td>\n<td>Bulge region of the&nbsp;hair follicle&nbsp;</td>\n</tr>\n<tr>\n<td>Cornea&nbsp;</td>\n<td>Limbus&nbsp;</td>\n</tr>\n<tr>\n<td>Intestine&nbsp;</td>\n<td>Paneth cells near base of crypt&nbsp;</td>\n</tr>\n<tr>\n<td>Liver&nbsp;&nbsp;</td>\n<td>Canal of&nbsp;Hering&nbsp;</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(24, 'Physiology,', 'PM2250. Layers of the cerebellar cortex', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>The cerebellar cortex is divided into 3 layers. They are:</p>\n<ul>\n<li><strong>Molecular layer</strong> - It is the <strong>outermost</strong> layer. It contains <strong>stellate cells </strong>and<strong> basket cells</strong>. They receive excitatory input from the parallel fibers and form&nbsp;<strong>inhibitory</strong>&nbsp;synapses on the Purkinje cells. It contains many myelinated and unmyelinated nerve fibers giving it a pale appearance in H and E staining.</li>\n<li><strong>Purkinje layer</strong> -&nbsp;It is the <strong>middle</strong> layer. It contain flask-shaped cell bodies of <strong>Purkinje cells</strong>. Their dendrites pass into the molecular layer. The axons of the Purkinje cells passes through the granular layer into the white matter and synapses with the cerebellar nuclei.</li>\n<li><strong>Granular layer</strong> - It is the <strong>innermost</strong> layer. It contains the cell bodies of the <strong>granule cells </strong>as well as&nbsp;<strong>Golgi cells</strong>. It receives excitatory input from the mossy fibers. They end on the dendrites of the granule cells and these complexes are called <strong>glomeruli</strong>. The axon from the granule cells travels to the molecular layer and bifurcates in a T-shape. These branches form the T and are called parallel fibers. These parallel fibers form excitatory synapses with the Purkinje cells. The granule cells form the <strong>only excitatory neurons</strong> in the cerebellar cortex. Golgi cells receive input from parallel fibers and mossy fibers. They send inhibitory output to the granule cells.</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/9d1e054c1658410d84897598ebb34ca3x1280x1767.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Neurotransmitters released:</p>\n<ul>\n<li>Stellate, basket, Golgi and Purkinje cells- <strong>GABA</strong></li>\n<li>Granule cells - <strong>Glutamate</strong></li>\n</ul>\n<p>The <strong>only output</strong> from the cerebellar cortex is <strong>inhibitory</strong> from the <strong>Purkinje cells</strong> to the <strong>deep cerebellar nuclei</strong>.</p></div><!---->\n      <!---->\n    </div><!---->'),
(25, 'Anatomy,', 'PM2248. Layers of Cerebral Cortex', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Layers of Cerebral Cortex</strong></td>\n</tr>\n<tr>\n<td>\n<p><strong>Molecular layer (I)</strong></p>\n<ul>\n<li>the superficial layer deep to the pia mater.</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>External granular layer (II)</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>External pyramidal layer (III)</strong></p>\n<ul>\n<li>gives rise to association and commissural fibers.</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Internal granular layer (IV)</strong></p>\n<ul>\n<li>receives thalamocortical fibers from the thalamic nuclei of the ventral tier (e.g., ventral posterolateral [VPL] and ventral posteromedial [VPM] nuclei).</li>\n<li>in the striate cortex (area 17), receives input from the lateral geniculate body</li>\n<li>myelinated fibers of this layer form the stripe of Gennari—visible to the naked eye</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Internal pyramidal layer (V)</strong></p>\n<ul>\n<li>gives rise to corticobulbar, corticospinal, and corticostriatal fibers</li>\n<li>contains the giant cells of Betz that are found only in the motor cortex (area 4) of the precentral gyrus and the anterior paracentral lobule</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Multiform layer (VI)</strong></p>\n<ul>\n<li>the deepest layer of the cortex. It gives rise to projection, commissural, and association fibers.</li>\n<li>the major source of corticothalamic fibers.</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Note: </strong>Cerebrum has 2 types of layer arrangements,&nbsp;neocortex has 6 layers and allocortex has 3 layers.&nbsp;</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/bba3f8444ad44c6395141769698f8909x1280x1851.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(26, 'Dermatology,', 'PM1880. Cells of the epidermis', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Cells of the epidermis</strong></td>\n<td><strong>Origin and location</strong></td>\n<td><strong>Characteristic features</strong></td>\n</tr>\n<tr>\n<td><strong>Keratino-cytes</strong></td>\n<td>\n<p><strong>Present in- All Layers of Epidermis</strong> (90-95% of all epidermal cells)</p>\n<p><strong>Derived from-</strong>Ectoderm</p>\n<p>Differentiated <strong>in situ</strong></p>\n</td>\n<td>Rich in the <strong>intermediate filaments</strong> called <strong>keratin</strong> that provides mechanical strength to the cell. The keratinocytes in basal layer divide and generate the upper layers.&nbsp;</td>\n</tr>\n<tr>\n<td><strong>Melano-cytes</strong></td>\n<td>\n<p><strong>Present in- Stratum basale</strong></p>\n<p><strong>Derived from-Neural Crest</strong></p>\n<p><strong>Migrate&nbsp;to the epidermis</strong></p>\n</td>\n<td>Synthesize <strong>melanin</strong> in granules called <strong>melanosomes</strong> that is transferred to keratinocytes of epidermis <strong>through elongated processes called dendrites</strong>. Each melanocyte transfers melanosomes to <strong>36 </strong>keratinocytes. This is called as<strong> Epidermal melanin unit</strong> (36K:1M)&nbsp; &nbsp;</td>\n</tr>\n<tr>\n<td>\n<p><strong>Merkel&nbsp; &nbsp;</strong><strong>cells</strong></p>\n</td>\n<td>\n<p><strong>Present in-Stratum basale</strong> (RETE RIDGES)</p>\n<p><strong>Derived from-Ectoderm/ Neural Crest</strong></p>\n<p><strong>Differentiates</strong><strong> in-situ</strong></p>\n</td>\n<td>Also known as <strong>Touch cells or Haascheiban cells</strong>.&nbsp;They are&nbsp;<strong>Type 1 slow adapting</strong> <strong>touch receptors</strong>. <strong>&nbsp;</strong></td>\n</tr>\n<tr>\n<td><strong>Langerhans cells</strong></td>\n<td>\n<p><strong>Present in-Stratum spinosum</strong></p>\n<p><strong>Derived from-&nbsp;</strong>Bone Marrow</p>\n<p><strong>Migrate</strong>&nbsp;to Epidermis</p>\n</td>\n<td>They are dendritic <strong>Antigen Presenting Cells (APCs)</strong>. &nbsp; Characterized by presence of rod shaped or Tennis Racket shaped granules called <strong>Birbeck granules</strong> which are seen on&nbsp;electron microscopy. &nbsp; Immunohistochemical Markers are <strong>CD1a</strong>,<strong> CD207(Langerin) and S100</strong>. <strong>&nbsp;</strong>&nbsp;</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/874d6f95161c4f0895a7ce32c3c33ca5x1280x1169.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(27, 'Anatomy', 'PM1071. Various Types of Epithelium, Their Location and Functions', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/088fb644eda04a0a9a022d1ae8b2113ex1280x3136.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(28, 'Anatomy', 'PM1075. Various Types of Exocrine Glands with Examples', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/59cee07cf6e04b0b8386a46a65ff2d90x1280x1498.PNG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Simple gland:</strong> All the secretory cells of an exocrine gland discharge into one duct<strong>.</strong></p>\n<p><strong>Compound gland:</strong> There are a number of groups of secretory cells, each group discharging into its own duct. These ducts unite to form larger ducts that ultimately drain on to an epithelial surface.</p>\n<p>In both simple and compound glands, the secretory cells may be arranged in various ways.</p>\n<ol>\n<li>The secretory element may be <strong>tubular</strong>. The tube may be straight, coiled, or branched.</li>\n<li>The cells may form rounded sacs or <strong>acini</strong>.</li>\n<li>They may form flask shaped structures called <strong>alveoli</strong>.</li>\n</ol></div><!---->\n      <!---->\n    </div><!---->'),
(29, 'Anatomy', 'PM0709. Comparison between Types of Sweat Glands', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\205\>\n<p><strong>Feature</strong></p>\n</td>\n<td width=\205\>\n<p><strong>Apocrine sweat gland</strong></p>\n</td>\n<td width=\205\>\n<p><strong>Eccrine sweat gland</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\205\>\n<p>Activity</p>\n</td>\n<td width=\205\>\n<p>Active at puberty</p>\n</td>\n<td width=\205\>\n<p>Throughout life</p>\n</td>\n</tr>\n<tr>\n<td width=\205\>\n<p>Opening on the surface</p>\n</td>\n<td width=\205\>\n<p>Around hair shaft</p>\n</td>\n<td width=\205\>\n<p>Through sweat pores</p>\n</td>\n</tr>\n<tr>\n<td width=\205\>\n<p>Function</p>\n</td>\n<td width=\205\>\n<p>Provides peculiar odour</p>\n</td>\n<td width=\205\>\n<p>Maintains temperature</p>\n</td>\n</tr>\n<tr>\n<td width=\205\>\n<p>Nervous control</p>\n</td>\n<td width=\205\>\n<p>Postganglionic sympathetic neurons which are adrenergic</p>\n</td>\n<td width=\205\>\n<p>Postganglionic sympathetic neurons which are cholinergic</p>\n</td>\n</tr>\n<tr>\n<td width=\205\>\n<p>Secretions</p>\n</td>\n<td width=\205\>\n<p>Viscid with lipids and proteins</p>\n</td>\n<td width=\205\>\n<p>Watery with salts</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(30, 'Physiology,', 'PM0706. Microanatomy of Muscle Fibres', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ul>\n<li>Each muscle consists of numerous <strong>muscle</strong> <strong>fibres</strong>.</li>\n<li>Each skeletal muscle fibre is <strong>multi</strong> <strong>nucleated</strong>, bonded by <strong>sarcolemma</strong>, enclosing <strong>sarcoplasm</strong>, smooth muscle and cardiac muscles are uninucleated.&nbsp;</li>\n<li>Within the <strong>sarcoplasm</strong>, there are several hundreds of <strong>nuclei</strong> arranged in the periphery just beneath the sarcolemma.</li>\n<li>The sarcoplasm also consists of evenly distributed, longitudinal threads called <strong>myofibrils</strong>.</li>\n<li><strong>Each myofibril</strong> consists of <strong>thick (myosin) and thin (actin) filaments</strong> overlapping each other.</li>\n<li>The portion of myofibril which consists of thick filaments is known as <strong>A band</strong> or <strong>dark band</strong>, and the portion of a myofibril between two A bands consisting of only thin filaments is known <strong>I band or Light band</strong>.</li>\n<li>Thus the <strong>A band</strong> consists of <strong>both thick and thin filaments</strong>, where as <strong>I band</strong> consists of only thin filaments.</li>\n<li>In the <strong>middle of ‘A’ band</strong>, there are <strong>only thick filaments</strong> with no overlapping thin filaments. This zone is known as ‘<strong>H’ zone</strong>. In the middle of H zone, there is a dark band known as ‘<strong>M’ band</strong>.</li>\n<li>In the <strong>middle of I band</strong>, there is a dark ‘<strong>Z’ disc or Krauses membrane</strong>.</li>\n<li>The segment of the myofibril between two Z discs is known as the <strong>sarcomere</strong>.</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->');
INSERT INTO db_pearl_m (pid, subject, pearl_title, pearl_desc) VALUES
(31, 'Anatomy,', 'PM2326. Brodmann areas', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/985d9f9c21e142b787a38323bc997372x600x535.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/1c16e1eaa21a41bf97b6cbe2769e08f8x600x487.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Area of the Brain(Brodmann)</strong></td>\n<td><strong>Location</strong></td>\n</tr>\n<tr>\n<td><strong>3, 1</strong> and <strong>2</strong> -&nbsp;Primary sensory area</td>\n<td>\n<p>Postcentral gyrus</p>\n<p>Extends to the posterior part of the paracentral lobule medially</p>\n</td>\n</tr>\n<tr>\n<td><strong>4</strong> -&nbsp;Primary motor area</td>\n<td>\n<p>Precentral gyrus</p>\n<p>Extends to the anterior part of the paracentral lobule medially</p>\n</td>\n</tr>\n<tr>\n<td><strong>6</strong>-&nbsp;Premotor area</td>\n<td>\n<p>Anterior to Area 4</p>\n<p>In the posterior parts of superior, middle and inferior frontal gyri</p>\n</td>\n</tr>\n<tr>\n<td><strong>5</strong> and <strong>7</strong> -&nbsp;Sensory association area</td>\n<td>Superior parietal lobule</td>\n</tr>\n<tr>\n<td><strong>8</strong> -&nbsp;Frontal eye field</td>\n<td>Posterior part of middle frontal gyrus, anterior to the facial area of the precentral gyrus</td>\n</tr>\n<tr>\n<td><strong>44</strong> and&nbsp;<strong>45</strong> -&nbsp;Broca’s(Motor speech) area</td>\n<td>Inferior frontal gyrus of the dominant hemisphere</td>\n</tr>\n<tr>\n<td>\n<p><strong>41</strong> and <strong>42</strong> -&nbsp;</p>\n<p>Primary auditory area</p>\n</td>\n<td>Superior surface of the superior temporal gyrus(Heschl’s gyrus)</td>\n</tr>\n<tr>\n<td><strong>22</strong>* -&nbsp;Wernicke’s(Sensory speech) area</td>\n<td>Superior temporal gyrus</td>\n</tr>\n<tr>\n<td><strong>17</strong> -&nbsp;Primary visual(striate) area</td>\n<td>Walls and floor of the posterior part of calcarine sulcus (postcalcarine sulcus) in the occipital lobe</td>\n</tr>\n<tr>\n<td>\n<p><strong>18</strong> and&nbsp;<strong>19</strong>-&nbsp;</p>\n<p>Visual association area</p>\n</td>\n<td>Remaining part of the medial and superolateral surfaces of the occipital lobe</td>\n</tr>\n<tr>\n<td><strong>28</strong> -&nbsp;Primary olfactory area</td>\n<td>Anterior part of the parahippocampal gyrus and uncus</td>\n</tr>\n<tr>\n<td><strong>43</strong> -&nbsp;Gustatory(taste) area</td>\n<td>Inferior part of the parietal lobe, posterior to the general sensory area of the mouth</td>\n</tr>\n</tbody>\n</table>\n<p>*There is uncertainty about the extent of the Wernickes area. Some authorities consider areas 39 and 40 as part of Wernickes area in addition to area 22.</p></div><!---->\n      <!---->\n    </div><!---->'),
(32, 'Anatomy', 'PM2269. Blood supply of internal capsule.', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><div>&nbsp;</div>\n<table><colgroup><col></colgroup>\n<tbody>\n<tr>\n<td>\n<div>Blood supply of internal capsule.</div>\n</td>\n</tr>\n<tr>\n<td>\n<p>Anterior limb&nbsp;</p>\n<ul>\n<li>Recurrent artery of Heubner</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>\n<div>Genu&nbsp;</div>\n<ul>\n<li>Recurrent artery of Heubner</li>\n<li>Charcots artery (lenticulostriate artery or artery of cerebral hemorrrahge)</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>\n<div>Posterior limb</div>\n<ul>\n<li>Charcots artery (branch of Middle cerebral artery)</li>\n<li>Anterior choroidal artery ( branch of internal carotid artery)</li>\n<li>Posterolateral branches of posterior cerebral artery</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>\n<div>Sublentiform part</div>\n<ul>\n<li>Anterior choroidal artery</li>\n<li>Posterior cerebral artery</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>\n<div>Retrolentiform part</div>\n<ul>\n<li>Posterolateral branches of posterior cerebral artery</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(33, 'Anatomy', 'PM2266. Internal carotid artery branches', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table><colgroup><col></colgroup>\n<tbody>\n<tr>\n<td>\n<div><strong>Part</strong></div>\n</td>\n<td>\n<div><strong>Course</strong></div>\n</td>\n<td><strong>Branches</strong></td>\n</tr>\n<tr>\n<td>\n<div><strong>Cervical part</strong></div>\n</td>\n<td>\n<div>Originates from the bifurcation of the common carotid artery. It has a dilation at the lower end which is known as carotid sinus. It ascends in front of the transverse process of the upper three cervical vertebrae.</div>\n</td>\n<td>\n<div>&nbsp;No branches given</div>\n</td>\n</tr>\n<tr>\n<td>\n<div><strong>Petrous part</strong></div>\n</td>\n<td>\n<div>Passes through the carotid canal in the petrous part of the temporal bone</div>\n</td>\n<td>\n<ul>\n<li>Caroticotympanic branch</li>\n<li>Artery to the pterygoid canal</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>\n<div><strong>Cavernous part</strong></div>\n</td>\n<td>\n<div>Runs in the cavernous sinus</div>\n</td>\n<td>\n<ul>\n<li>Branch to the trigeminal ganglion</li>\n<li>Meningeal branch</li>\n<li>Branch to the cavernous sinus</li>\n<li>Hypophyseal branches</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>\n<div><strong>Cerebral part/ Supracavernal part</strong></div>\n</td>\n<td>\n<div>Enters subarachnoid space by piercing arachnoid mater and divides into the anterior and middle cerebral arteries.</div>\n</td>\n<td>\n<ul>\n<li>Ophthalmic artery</li>\n<li>Anterior choroidal artery</li>\n<li>Posterior communicating artery</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table>\n<p>The petrous, cavernous and intracranial parts of the internal carotid artery are called the <strong>carotid siphon</strong> because of its sigmoid course.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/88f367df1dc04e41ab088b2970715e7ax1280x1642.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(34, 'Anatomy', 'PM2261. Thalamus and its connections', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><div>\n<table width=\665\>\n<tbody>\n<tr>\n<td>Nuclei</td>\n<td>Input</td>\n<td>Output</td>\n<td>Function</td>\n</tr>\n<tr>\n<td>Anterior&nbsp;</td>\n<td>\n<p>Mamillothalamic tract, cingulate gyrus, hypothalamus</p>\n</td>\n<td>Cingulate gyrus, hypothalamus</td>\n<td>Regulation of alertness and attention, acquisition of memory</td>\n</tr>\n<tr>\n<td>Dorsomedial</td>\n<td>\n<p>Prefrontal cortex, hypothalamus, other thalamic nuclei</p>\n</td>\n<td>Prefrontal cortex, hypothalamus, other thalamic nuclei</td>\n<td>Integration of somatic, visceral, and olfactory information and relation to emotional feelings and subjective states</td>\n</tr>\n<tr>\n<td>Lateral dorsal, lateral posterior and pulvinar</td>\n<td>Cerebral cortex and other thalamic nuclei&nbsp;</td>\n<td>Cerebral cortex and other thalamic nuclei</td>\n<td>&nbsp;Unknown</td>\n</tr>\n<tr>\n<td>Ventral postero lateral</td>\n<td>\n<p>Medial lemniscus</p>\n<p>spinothlamic pathway</p>\n</td>\n<td>Primary somatosensory area cortex</td>\n<td>Relays common sensations to consciousness</td>\n</tr>\n<tr>\n<td>Ventral posteromedial</td>\n<td>\n<p>Gustatory</p>\n<p>Trigeminal</p>\n</td>\n<td>Primary somatosensory area cortex</td>\n<td>Relays common sensations to consciousness</td>\n</tr>\n<tr>\n<td>Ventral anterior</td>\n<td>Reticular formation, substantia nigra, corpus striatum, premotor cortex, other thalamic nuclei</td>\n<td>Reticular formation, substantia nigra, corpus striatum, premotor cortex, other thalamic nuclei</td>\n<td>Influences activity of motor cortex</td>\n</tr>\n<tr>\n<td>Ventral lateral</td>\n<td>Cerebellum, reticular formation, substantia nigra, corpus striatum, premotor cortex, other thalamic nuclei</td>\n<td>Cerebellum, reticular formation, substantia nigra, corpus striatum, premotor cortex, other thalamic nuclei</td>\n<td>Influences activity of motor cortex</td>\n</tr>\n<tr>\n<td>Intralaminar</td>\n<td>Reticular formation, spinothalamic and trigeminothalamic tracts</td>\n<td>To cerebral cortex via other thalamic nuclei and corpus striatum</td>\n<td>Influences level of consciousness and alertness</td>\n</tr>\n<tr>\n<td>Midline</td>\n<td>Reticular formation</td>\n<td>Unknown</td>\n<td>Unknown</td>\n</tr>\n<tr>\n<td>Reticular</td>\n<td>Cerebral cortex, reticular formation</td>\n<td>Other thalamic nuclei</td>\n<td>Cerebral cortex regulating thalamus</td>\n</tr>\n<tr>\n<td>Medial geniculate body</td>\n<td>Inferior colliculus, lateral lemniscus from the predominantly opposite ear</td>\n<td>Auditory radiation to superior temporal gyrus</td>\n<td>Hearing</td>\n</tr>\n<tr>\n<td>Lateral geniculate body</td>\n<td>Optic tract</td>\n<td>Optic radiation to the visual cortex of occipital lobe</td>\n<td>Visual information from the opposite field of vision</td>\n</tr>\n</tbody>\n</table>\n&nbsp;</div></div><!---->\n      <!---->\n    </div><!---->'),
(35, 'Anatomy', 'PM2258. Phylogenetic Classification of cerebellum', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Cerebellum Phylogenetic Classification:&nbsp;</strong></td>\n</tr>\n<tr>\n<td>\n<p><strong>Archicerebellum (oldest part)</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Lobes: Flocculonodular lobe, Lingula</p>\n<p>Main connection:&nbsp;Vestibulo-cerebellar</p>\n<p>Nuclei: Fastigial&nbsp;</p>\n<p>Function:&nbsp;Maintenance of body equilibrium</p>\n</td>\n</tr>\n<tr>\n<td><strong>Paleocerebellum</strong></td>\n</tr>\n<tr>\n<td>\n<p>Lobes: Whole anterior lobe (except Lingula), Pyramid and Uvula</p>\n<p>Main connection: Spino-cerebellar</p>\n<p>Nuclei: Interposed (Globose and Emboliform)&nbsp;</p>\n<p>Function:&nbsp;Maintenance of muscle tone and finer control of movements</p>\n</td>\n</tr>\n<tr>\n<td><strong>Neocerebellum (most recent part)</strong></td>\n</tr>\n<tr>\n<td>\n<p>Whole&nbsp;posterior lobe except Pyramid and Uvula</p>\n<p>Main connection:&nbsp; Cortico-ponto-cerebellar</p>\n<p>Nuclei: Dentate</p>\n<p>Function: Fine coordination of voluntary movements</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(36, 'Anatomy', 'PM2256. Boundaries of the 3rd ventricle', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Roof:</strong> Choroid plexus</p>\n<p><strong>Lateral wall:</strong></p>\n<ul>\n<li>Thalamus</li>\n<li>Hypothalamus</li>\n</ul>\n<p><strong>Anterior wall:&nbsp;</strong></p>\n<ul>\n<li>Anterior commissure</li>\n<li>Lamina terminalis</li>\n</ul>\n<p><strong>Posterior wall:&nbsp;</strong></p>\n<ul>\n<li>Pineal gland</li>\n<li>Posterior commissure</li>\n<li>Aqueduct of Sylvius</li>\n</ul>\n<p><strong>Floor:&nbsp;</strong></p>\n<ul>\n<li>Optic chiasma</li>\n<li>Infundibulum</li>\n<li>Mamillary bodies</li>\n<li>Posterior perforating substance</li>\n<li>Tegmentum of midbrain</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/0adcffec16404c7494c80b6792ccd690x600x523.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(37, 'Anatomy', 'PM2255. Cerebellar Circuitry', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Cerebellar circuitry includes:</p>\n<p>Afferents- Climbing and mossy fibers</p>\n<ul>\n<li>Climbing fibers -\n<ul>\n<li>Origin - contralateral inferior olivary nucleus in the medulla oblongata</li>\n<li>Terminates on - Purkinje cell</li>\n</ul>\n</li>\n<li>Mossy fibers -\n<ul>\n<li>Origin - multiple sources in the spinal cord and brainstem</li>\n<li>Terminates on - Granule cell</li>\n</ul>\n</li>\n</ul>\n<p>Intrinsic components</p>\n<ul>\n<li>Granule cell backward inhibition - done by Golgi cells- prevent transmission from mossy fibers to the granule cells</li>\n<li>Purkinje cells forward inhibition- done by stellate and basket cells</li>\n</ul>\n<p>Efferents -</p>\n<ul>\n<li>The output from the cerebellar cortex - from Purkinje cells inhibitory output to the deep cerebellar nuclei</li>\n<li>The output from the cerebellum - from deep cerebellar nuclei\n<ul>\n<li>Globose, emboliform, and dentate - via superior cerebellar peduncle</li>\n<li>Fastigial - via inferior cerebellar peduncle</li>\n</ul>\n</li>\n</ul>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/08d7e9790f8b4fe4afdec9aa33c384b3x1280x1767.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(38, 'Anatomy', 'PM2253. Cerebellar peduncles and their connections', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Peduncle</strong></td>\n<td><strong>Afferent fibers</strong></td>\n<td><strong>Efferent fibers</strong></td>\n</tr>\n<tr>\n<td>\n<p>Superior cerebellar peduncle</p>\n</td>\n<td>\n<p>Anterior spinocerebellar tract</p>\n</td>\n<td>\n<ul>\n<li>Globose - emboliform-rubral pathway</li>\n<li>Dentatothalamic pathway</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Middle cerebellar peduncle -<strong>Largest</strong>, most lateral and <strong>only afferent</strong></td>\n<td>Corticopontocerebellar pathway</td>\n<td>-</td>\n</tr>\n<tr>\n<td>Inferior cerebellar peduncle</td>\n<td>\n<ul>\n<li>Posterior spinocerebellar tract</li>\n<li>Cuneocerebellar tract</li>\n<li>Olivocerebellar tract</li>\n<li>Reticulocerebellar tract</li>\n<li>Trigeminocerebellar tract</li>\n<li>Vestibulocerebellar tract</li>\n</ul>\n</td>\n<td>\n<ul>\n<li>Fastigial vestibular pathway</li>\n<li>Fastigial reticular pathway</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/34cdc17d883b41d688f40950d3f0c6efx600x489.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(39, 'Anatomy', 'PM2251. Neurons and fibers of the cerebellum', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Neurons and fibers of the cerebellum&nbsp;</strong></td>\n</tr>\n<tr>\n<td><strong>Purkinje cell</strong></td>\n</tr>\n<tr>\n<td>\n<p>conveys the only output from the cerebellar cortex</p>\n<p>projects inhibitory output (gamma-aminobutyric acid [GABA]) to the cerebellar and&nbsp;vestibular nuclei</p>\n<p>excited by parallel and climbing fibers</p>\n<p>inhibited (by GABA) by basket and stellate cells</p>\n</td>\n</tr>\n<tr>\n<td><strong>Granule cell</strong></td>\n</tr>\n<tr>\n<td>\n<p>excites (by glutamate) Purkinje, basket, stellate, and Golgi cells via parallel fibers</p>\n<p>inhibited by Golgi cells</p>\n<p>excited by mossy fibers</p>\n</td>\n</tr>\n<tr>\n<td><strong>Mossy fibers</strong></td>\n</tr>\n<tr>\n<td>\n<p>fiber type: afferent excitatory fibers</p>\n<p>tracts: spinocerebellar and pontocerebellar tracts</p>\n<p>transmitter:glutamate</p>\n<p>termination: on granule cells</p>\n<p>excite granule cells to discharge via their parallel fibers</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Climbing fibers</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>fiber type: afferent excitatory fibers&nbsp;</p>\n<p>tract: olivocerebellar tract</p>\n<p>transmitter: aspartate</p>\n<p>termination: on neurons of the cerebellar nuclei and on dendrites of Purkinje cell</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p>afferent excitatory ﬁbers of the spinocerebellar</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/d592c57158bc4d40b3fdeae3b56e79c5x1280x1767.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(40, 'Anatomy', 'PM2247. Internal capsule parts and fibers', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>Part of Internal<br>capsule</td>\n<td>Motor<br>fibers/descending tracts</td>\n<td>Sensory fibers/<br>ascending tracts</td>\n</tr>\n<tr>\n<td>Anterior limb</td>\n<td>\n<ul>\n<li>Frontopontine</li>\n</ul>\n</td>\n<td>Anterior thalamic radiation</td>\n</tr>\n<tr>\n<td><strong>Genu</strong></td>\n<td>\n<ul>\n<li>Corticonuclear</li>\n<li>Corticospinal</li>\n</ul>\n</td>\n<td>Superior thalamic<br>radiation</td>\n</tr>\n<tr>\n<td><strong>Posterior limb</strong></td>\n<td>\n<ul>\n<li><strong>Corticospinal</strong></li>\n<li>Corticorubral</li>\n<li>Frontopontine</li>\n</ul>\n</td>\n<td>Superior thalamic<br>radiation</td>\n</tr>\n<tr>\n<td>Retrolentiform part</td>\n<td>\n<ul>\n<li>Parietopontine</li>\n<li>Occipitopontine</li>\n</ul>\n<p>&nbsp;</p>\n<ul>\n<li>Occipitotectal</li>\n</ul>\n</td>\n<td>\n<ul>\n<li>Posterior thalamic<br>radiation</li>\n</ul>\n<p>&nbsp;</p>\n<ul>\n<li>Optic<br>radiation</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Sublentiform part</td>\n<td>\n<ul>\n<li>Temporopontine</li>\n<li>Parietopontine</li>\n</ul>\n</td>\n<td>Auditory radiation</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(41, 'Anatomy', 'PM2246. Corpus  Callosum', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Parts of the corpus callosum:</strong></p>\n<ul>\n<li><strong>Rostrum:</strong> connects frontal lobes (eg: orbitofrontal cortex)</li>\n<li><strong>Genu:</strong> connects frontal lobes. It is the anterior-most part.</li>\n<li><strong>Trunk or Body:</strong> connects temporal lobes.</li>\n<li><strong>Splenium:</strong>&nbsp;connects the two occipital lobes. It is the thickest part.</li>\n</ul>\n<p>Note: Isthmus is a part which separates the body from splenium, some books mention it as a part.</p>\n<ul>\n<li>The fibers of the genu curve forward into frontal lobes and form <strong>forceps minor</strong>.</li>\n<li>The fibers of the trunk extend laterally as radiation of corpus callosum.</li>\n<li>The fibers from the trunk and the splenium which form the lateral wall of the inferior horn of the lateral ventricle constitute the <strong>tapetum.</strong></li>\n<li>The fibers from the splenium arching backwards into the occipital lobe form <strong>forceps major.</strong></li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/cdf78937ae524771aec56a67e252b9bax1280x1188.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(42, 'Anatomy', 'PM2240. White matter tracts of the brain', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Commissural fibers:</strong>&nbsp;connect corresponding regions of two hemispheres.</p>\n<p>They include</p>\n<ul>\n<li>Corpus callosum</li>\n<li>Anterior and posterior commissure</li>\n<li>Habenular commissure</li>\n<li>Hippocampal commissure of fornix</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Association fibers:</strong>&nbsp;connect various cortical regions within the same hemisphere.</p>\n<p>They are classified as&nbsp;</p>\n<ul>\n<li>Short association fibers: lie immediately under the cortex and connect adjacent gyri. Eg- arcuate fasciculus</li>\n<li>Long association fibers:\n<ul>\n<li>Uncinate fasciculus</li>\n<li>Cingulum</li>\n<li>Superior longitudinal fasciculus</li>\n<li>Inferior longitudinal fasciculus</li>\n<li>Fronto-occipital fasciculus</li>\n<li>Fornix (except hippocampal commissure of fornix)</li>\n</ul>\n</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/34dcc45f34114e9e968caa2f3060181ex1280x1285.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Projection fibers:</strong>&nbsp;fibers passing to and from the brainstem to the cerebral cortex.&nbsp;</p>\n<ul>\n<li>Internal capsule</li>\n<li>Corona radiata</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/545b77e4b1dd4a6f803e840b3678549bx600x581.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(43, 'Anatomy', 'PM2239. Dural Venous Sinuses', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\368\>\n<tbody>\n<tr>\n<td>Dural venous sinus</td>\n<td>Drains into</td>\n</tr>\n<tr>\n<td colspan=\2\>Unpaired sinuses</td>\n</tr>\n<tr>\n<td>Superior sagittal sinus</td>\n<td>Confluence of sinuses or continues as right transverse sinus</td>\n</tr>\n<tr>\n<td>Inferior sagittal sinus</td>\n<td>joins the great cerebral vein to form the straight sinus</td>\n</tr>\n<tr>\n<td>&nbsp;Straight sinus</td>\n<td>forms the left transverse sinus</td>\n</tr>\n<tr>\n<td>Occipital sinus</td>\n<td>Confluence of sinuses</td>\n</tr>\n<tr>\n<td colspan=\2\>Paired sinuses</td>\n</tr>\n<tr>\n<td>Superior petrosal sinus</td>\n<td>transverse sinus</td>\n</tr>\n<tr>\n<td>Inferior petrosal sinus</td>\n<td>Internal jugular vein</td>\n</tr>\n<tr>\n<td>Transverse sinus</td>\n<td>continue as sigmoid sinus</td>\n</tr>\n<tr>\n<td>Sigmoid sinus</td>\n<td>continuous with the internal jugular vein</td>\n</tr>\n<tr>\n<td>Cavernous sinus</td>\n<td>Superior and inferior petrosal sinuses</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/e717fc57ad1c4c5eb83a21118bb9589cx600x1277.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(44, 'Anatomy', 'PM2233. Spinal Tract Decussations.', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/e64f7962a697405da865e5cdcc262263x1280x2558.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table title=\Page 397\ border=\1\>\n<tbody>\n<tr>\n<td class=\\><strong>Name</strong></td>\n<td class=\\>\n<div><strong>Decussation</strong></div>\n</td>\n</tr>\n<tr>\n<td class=\\>\n<div>Gracile</div>\n<div>fasciculus</div>\n&nbsp;</td>\n<td class=\\>\n<div>In medulla</div>\n</td>\n</tr>\n<tr>\n<td class=\\>\n<div>Cuneate</div>\n<div>fasciculus</div>\n&nbsp;</td>\n<td class=\\>\n<div>In medulla</div>\n</td>\n</tr>\n<tr>\n<td class=\\>\n<div>Spino-</div>\n<div>thalamic</div>\n&nbsp;</td>\n<td class=\\>\n<div>In spinal cord</div>\n</td>\n</tr>\n<tr>\n<td class=\\>\n<div>Spino-</div>\n<div>reticular</div>\n&nbsp;</td>\n<td class=\\>\n<div>In spinal cord (some fibers)</div>\n</td>\n</tr>\n<tr>\n<td class=\\>\n<div>Posterior</div>\n<div>spino-</div>\n<div>cerebellar</div>\n&nbsp;</td>\n<td class=\\>\n<div>None</div>\n</td>\n</tr>\n<tr>\n<td class=\\>\n<div>Anterior</div>\n<div>spino-</div>\n<div>cerebellar</div>\n&nbsp;</td>\n<td class=\\>\n<div>In spinal cord</div>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table title=\Page 397\ border=\1\>\n<tbody>\n<tr>\n<td class=\\><strong>&nbsp;Name</strong></td>\n<td class=\\>\n<div><strong>&nbsp;</strong></div>\n<div><strong>Decussation</strong></div>\n<div><strong>&nbsp;</strong></div>\n</td>\n</tr>\n<tr>\n<td class=\\>\n<div>Lateral</div>\n<div>cortico-</div>\n<div>spinal</div>\n&nbsp;</td>\n<td class=\\>\n<div>In medulla</div>\n</td>\n</tr>\n<tr>\n<td class=\\>\n<div>Anterior</div>\n<div>cortico-</div>\n<div>spinal</div>\n&nbsp;</td>\n<td class=\\>\n<div>In spinal cord</div>\n</td>\n</tr>\n<tr>\n<td class=\\>\n<div>Tecto-</div>\n<div>spinal</div>\n&nbsp;</td>\n<td class=\\>\n<div>In midbrain</div>\n</td>\n</tr>\n<tr>\n<td class=\\>\n<div>Lateral</div>\n<div>reticulo-</div>\n<div>spinal</div>\n&nbsp;</td>\n<td class=\\>\n<div>None</div>\n</td>\n</tr>\n<tr>\n<td class=\\>\n<div>Medial</div>\n<div>reticulo-</div>\n<div>spinal</div>\n&nbsp;</td>\n<td class=\\>\n<div>None</div>\n</td>\n</tr>\n<tr>\n<td class=\\>\n<div>Lateral</div>\n<div>vestibulo-</div>\n<div>spinal</div>\n&nbsp;</td>\n<td class=\\>\n<div>None</div>\n</td>\n</tr>\n<tr>\n<td class=\\>\n<div>Medial</div>\n<div>vestibulo-</div>\n<div>spinal</div>\n&nbsp;</td>\n<td class=\\>\n<div>In medulla (c fibers)</div>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(45, 'Anatomy,', 'PM2232. Descending tracts of spinal cord', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\699\>\n<tbody>\n<tr>\n<td>Tract</td>\n<td>Corticospinal tract</td>\n<td>Reticulospinal tract</td>\n<td>Tectospinal tract</td>\n<td>Rubrospinal tract</td>\n<td>Vestibulospinal tract</td>\n<td>Olivospinal tract</td>\n</tr>\n<tr>\n<td>Function</td>\n<td>Control of rapid, skilled, voluntary movements</td>\n<td>Inhibits or facilitate voluntary movement</td>\n<td>Control of reflex postural movements concerning sight</td>\n<td>Facilitates activity of flexor muscles and inhibits the activity of extensor muscles</td>\n<td>Facilitates activity of extensor muscles and inhibits the activity of flexor muscles</td>\n<td>Unknown</td>\n</tr>\n<tr>\n<td>Origin</td>\n<td>Primary motor cortex (area 4), secondary motor cortex (area 6), parietal lobes</td>\n<td>\n<p>Reticular formation</p>\n</td>\n<td>Superior colliculus</td>\n<td>Red nucleus</td>\n<td>Vestibular nuclei</td>\n<td>Inferior olivary nuclei</td>\n</tr>\n<tr>\n<td>Site of crossover</td>\n<td>\n<p>Lateral corticospinal tract - in lower medulla&nbsp;</p>\n<p>Anterior corticospinal tract - spinal cord at the level of destination</p>\n</td>\n<td>\n<p>Some decussate at various levels</p>\n<p>Some fibers dont decussate</p>\n</td>\n<td>Soon after origin</td>\n<td>Immediately after origin</td>\n<td><strong>Uncrossed</strong></td>\n<td>Brainstem</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/a9152c84c6aa407a86afa6da5100e86ex1280x1248.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(46, 'Physiology,', 'PM2231. Ascending tracts of spinal cord', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<table>\n<tbody>\n<tr>\n<td><strong>Pathway</strong></td>\n<td><strong>Dorsal column</strong></td>\n<td><strong>Anterior spinothalamic tract</strong></td>\n<td><strong>Lateral spinothalamic tract</strong></td>\n</tr>\n<tr>\n<td>Receptor</td>\n<td>Meissner corpuscles, pacinian corpuscles, muscle spindles, golgi tendon organs</td>\n<td>Free nerve endings</td>\n<td>Free nerve endings&nbsp;</td>\n</tr>\n<tr>\n<td>First-order neuron</td>\n<td>\n<p>First-order neurons from the receptors enter spinal cord from dorsal root &gt;</p>\n<p>ascend ipsilaterally as fasciculus gracilis and fasciculus cuneatus &gt;</p>\n<p>synapse in nucleus gracilis and nucleus cuneatus in medulla &gt;&nbsp;</p>\n</td>\n<td colspan=\2\>First-order neurons from dorsal root ganglion ascend for a few segments and synapse with the cells in the posterior gray column &gt;</td>\n</tr>\n<tr>\n<td>Second-order neuron</td>\n<td>\n<p>second-order neurons from nucleus gracilis and nucleus cuneatus continue as <strong>internal arcuate fibers</strong> &gt;&nbsp;</p>\n<p>decussate in the lower medulla and ascends as <strong>medial lemniscus</strong> on the contralateral side &gt;</p>\n<p>synapse in the ventral posterolateral nucleus of the thalamus</p>\n</td>\n<td colspan=\2\>\n<p>Second-order neurons from the posterior gray column crossover to the opposite side and ascend as anterior and lateral spinothalamic tract &gt;</p>\n<p>form the <strong>spinal lemniscus</strong> in the medulla &gt;</p>\n<p>ascend through pons and synapses in the ventral posterolateral nucleus of thalamus &gt;</p>\n</td>\n</tr>\n<tr>\n<td>Third-order neuron</td>\n<td>third-order neuron from the ventral posterolateral nucleus of thalamus ascend in the posterior limb of internal capsule and corona radiata and terminate in somatosensory cortex</td>\n<td colspan=\2\>Third-order neurons from the ventral posterolateral nucleus of thalamus ascend through the posterior limb of internal capsule and corona radiata and terminate in the somatosensory cortex</td>\n</tr>\n<tr>\n<td>Decussation</td>\n<td>Medulla</td>\n<td>Spinal cord - few segments above the entry point&nbsp;&nbsp;</td>\n<td>Spinal cord- one segment above the entry point</td>\n</tr>\n<tr>\n<td>Sensations carried</td>\n<td>Proprioception, Fine touch(discriminative touch), pressure intesity, vibration, position sense</td>\n<td>Crude touch, pressure&nbsp;</td>\n<td>Pain and temperature</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/7bf456a051bd463f8309af97c40c6506x1280x1248.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(47, 'Anatomy', 'PM2141. Origins of cranial nerves at the base of the brain', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/521c0fb7637a49bc98549d02ec59d22dx600x618.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Cranial nerve origins as seen in gross anatomy:</p>\n<ul>\n<li>III cranial nerve: Midbrain at the level of the superior colliculus. &nbsp;</li>\n<li>IV cranial nerve: Midbrain at the level of inferior colliculus - the only nerve the <strong>exits from the dorsal aspect</strong></li>\n<li>V cranial nerve: The substance of pons&nbsp;</li>\n<li>VI, VII, and VIII cranial nerves:&nbsp; Pontomedullary junction</li>\n<li>IX, X, and XI cranial nerves(nucleus ambiguus): Posterolateral sulcus&nbsp; &nbsp;</li>\n<li>XII cranial nerve: Anterolateral sulcus (between pyramid and olive)</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(48, 'Anatomy', 'PM2140. Arrangement of cranial nerve nuclei in the brainstem', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Functional Component</strong></td>\n<td><strong>Cranial nerves&nbsp;</strong></td>\n</tr>\n<tr>\n<td>\n<p>General Somatic Efferent (SE/GSE)</p>\n</td>\n<td>3,4,6,12</td>\n</tr>\n<tr>\n<td>\n<p>Special Visceral Efferent (SVE/BrE)</p>\n</td>\n<td>\n<p>5,7,9,10,11</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>General Visceral Efferent&nbsp; (GVE)</p>\n</td>\n<td>3,7,9,10</td>\n</tr>\n<tr>\n<td>\n<p>General Visceral Afferent (GVA)</p>\n</td>\n<td rowspan=\2\>7,9,10</td>\n</tr>\n<tr>\n<td>\n<p>Special Visceral Afferent&nbsp;(BrA/SVA)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>General Somatic Afferent (SA/GSA)</p>\n</td>\n<td>5,7,9,10</td>\n</tr>\n<tr>\n<td>\n<p>Special Somatic Afferent (SSA)</p>\n</td>\n<td>2,8</td>\n</tr>\n</tbody>\n</table>\n<p>GVE represents the parasympathetic supply; so all cranial nerves having parasympathetic fibers, have the GVE component.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/f85b03ec72444bf9ba28fdda5b29fc3cx1280x1567.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(49, 'Anatomy,', 'PM2121. Functions of Main Hypothalamic Nuclei', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<table border=\1\ width=\416\>\n<tbody>\n<tr>\n<td>Hypothalamic Nucleus</td>\n<td>Function</td>\n</tr>\n<tr>\n<td>Supraoptic&nbsp;</td>\n<td>Synthesizes vasopressin (ADH)</td>\n</tr>\n<tr>\n<td>Paraventricular</td>\n<td>Synthesizes oxytocin</td>\n</tr>\n<tr>\n<td>Preoptic &amp; anterior Nuclei</td>\n<td>Control parasymphathetic system</td>\n</tr>\n<tr>\n<td>Posterior &amp; Lateral</td>\n<td>Control symphathetic system</td>\n</tr>\n<tr>\n<td>Anterior</td>\n<td>Regulate temperature(response to heat)</td>\n</tr>\n<tr>\n<td>Posterior</td>\n<td>Regulate temperature(response to cold)</td>\n</tr>\n<tr>\n<td>Lateral</td>\n<td>\n<div class=\page\ title=\Page 655\>\n<div class=\section\>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>Initiate eating and increase food intake (Hunger center)<br>Increase water intake (Thirst center)</p>\n</div>\n</div>\n</div>\n</div>\n</td>\n</tr>\n<tr>\n<td>Medial</td>\n<td>\n<div class=\page\ title=\Page 655\>\n<div class=\section\>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>Inhibit eating and reduce food intake (satiety center)</p>\n</div>\n</div>\n</div>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div class=\page\ title=\Page 655\>\n<div class=\section\>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>Suprachiasmatic</p>\n</div>\n</div>\n</div>\n</div>\n</td>\n<td>Controls circadian rhythm</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(50, 'Anatomy', 'PM2063. Arteries supplying specific parts of the brain', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Parts of Brain</strong></td>\n<td><strong>Arteries supplying it</strong></td>\n</tr>\n<tr>\n<td>Basal ganglia</td>\n<td>\n<ul>\n<li>Lateral striate artery - a branch of MCA</li>\n<li>Medial striate artery - a branch of ACA</li>\n<li>Anterior and posterior&nbsp; choroidal arteries</li>\n<li>Thalamogeniculate branch of PCA</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Internal capsule</td>\n<td>\n<ul>\n<li>Lateral striate artery - a branch of MCA</li>\n<li>Medial striate artery (Recurrent artery of Heubner) - a branch of ACA</li>\n<li>Anterior choroidal artery - a branch of ICA</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Thalamus</td>\n<td>\n<ul>\n<li>Posterior communicating arteries</li>\n<li>Posterior cerebral arteries</li>\n<li>Basilar artery</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Midbrain</td>\n<td>\n<ul>\n<li>Posterior cerebral arteries</li>\n<li>Superior cerebellar arteries</li>\n<li>Basilar artery</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Pons</td>\n<td>\n<ul>\n<li>Basilar artery</li>\n<li>Anterior inferior cerebellar artery</li>\n<li>Superior cerebellar arteries.</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Medulla oblongata</td>\n<td>\n<ul>\n<li>Vertebral artery</li>\n<li>Anterior and posterior spinal arteries</li>\n<li>Posterior inferior cerebellar arteries</li>\n<li>Basilar artery</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Cerebellum</td>\n<td>\n<ul>\n<li>Superior cerebellar arteries</li>\n<li>Anterior inferior cerebellar arteries</li>\n<li>Posterior inferior cerebellar arteries</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p>ACA - Anterior cerebral artery</p>\n<p>MCA- Middle cerebral artery</p>\n<p>PCA - Posterior cerebral artery</p>\n<p>ICA - Internal carotid artery</p></div><!---->\n      <!---->\n    </div><!---->'),
(51, 'Anatomy,', 'PM2017. Abnormal movements', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<table>\n<tbody>\n<tr>\n<td><strong>&nbsp;Movement</strong></td>\n<td><strong>Site</strong></td>\n<td><strong>Description</strong></td>\n<td><strong>Cause</strong></td>\n</tr>\n<tr>\n<td>Intentional tremor</td>\n<td>Cerebellum</td>\n<td>Only on activity&nbsp;</td>\n<td>\n<ul>\n<li>Trauma</li>\n<li>Stroke</li>\n<li>Alcohol</li>\n<li>Drugs</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Resting tremor</td>\n<td>\n<p>Substantia nigra</p>\n<p>Red nucleus</p>\n</td>\n<td>Disappear on movement</td>\n<td>\n<ul>\n<li>&nbsp;Parkinsonism</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Hyperkinetic tremor</td>\n<td>Sympathetic overactivity&nbsp;</td>\n<td>Precipitated by emotion , stress</td>\n<td>\n<ul>\n<li>Anxiety&nbsp;</li>\n<li>Hyperthyroidism&nbsp;</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Asterixis</td>\n<td>Cortex</td>\n<td>Inability to sustain a muscle contraction while performing a complex motor movement</td>\n<td>\n<ul>\n<li>Hepatic encephalopathy&nbsp;</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Chorea</td>\n<td>Caudate nucleus and putamen</td>\n<td>&nbsp;Rapid jerky movement</td>\n<td>\n<ul>\n<li>Rheumatic chorea</li>\n<li>Huntington chorea</li>\n<li>Drug induced&nbsp;</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Hemibalismus</td>\n<td>Subthalamic nucleus</td>\n<td>Unilateral wild flinging violent movement of large proximal joints</td>\n<td>\n<ul>\n<li>Vascular&gt;degenerative&nbsp;</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Athetosis</td>\n<td>Basal ganglia- Globus Pallidum</td>\n<td>Slow writhing movement involving distal joints</td>\n<td>\n<ul>\n<li>Vascular&nbsp;</li>\n<li>Degenerative&nbsp;</li>\n<li>Infiltrative</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(52, 'Anatomy', 'PM2010. Classification of sulci of brain', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Types of sulci</strong>:</p>\n<p>On the basis of function, theyre divided as:</p>\n<ul>\n<li><strong>Limiting sulcus</strong> - A sulcus that separates areas differing in structure and function at its floor. E.g. <strong>Central sulcus</strong> - separates motor and sensory area.</li>\n<li><strong>Axial sulcus -&nbsp;</strong>Sulcus that develops along the axis of a rapidly growing/ developing area. E.g.&nbsp;<strong>Posterior part of calcarine sulcus</strong> - primary visual area grows along this sulcus.</li>\n<li><strong>Operculated sulcus - </strong>A sulcus that separates two areas by its lip and contains a third area in its walls.<strong>&nbsp;</strong>E.g.&nbsp;<strong>Lunate sulcus</strong> - separates striate and peristriate areas.</li>\n</ul>\n<p>On the basis of depth, theyre divided as:</p>\n<ul>\n<li><strong>Complete sulcus -&nbsp;</strong>Deep enough to indent the wall of the lateral ventricle. E.g&nbsp;<strong>Anterior part of calcarine sulcus&nbsp;</strong>-&nbsp; forms calcar avis at the lateral ventricle,&nbsp;<strong>Collateral sulcus</strong> -&nbsp; forms collateral eminence at the inferior horn of the lateral ventricle</li>\n<li><strong>Incomplete sulcus</strong> - Superficially situated and does not indent the lateral ventricle. E.g. <strong>paracentral sulcus</strong></li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(53, 'Medicine,', 'PM1982. Medullary syndromes and Weber syndrome', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table class=\t1\ cellspacing=\0\ cellpadding=\0\>\n<tbody>\n<tr>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>Syndrome</p>\n</td>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>Vessel involved</p>\n</td>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>Clinical features</p>\n</td>\n</tr>\n<tr>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>Medial Medullary syndrome / Dejerine syndrome</p>\n</td>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>Vertebral artery/Anterior spinal artery</p>\n</td>\n<td class=\td1\ valign=\top\>\n<ul>\n<li class=\p1\>C/L loss of conscious proprioception</li>\n<li class=\p1\>C/L hemiplegia</li>\n<li class=\p1\>I/L tongue paralysis</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>Lateral Medullary syndrome / Wallenberg syndrome</p>\n</td>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>Vertebral artery&gt;Posterior inferior cerebellar artery</p>\n</td>\n<td class=\td1\ valign=\top\>\n<ul>\n<li class=\p1\>C/L loss of pain/temperature on the body</li>\n<li class=\p1\>I/L loss of pain/temperature on face</li>\n<li class=\p1\>Ataxia</li>\n<li class=\p1\>Nystagmus</li>\n<li class=\p1\>Vertigo</li>\n<li class=\p1\>Horners syndrome</li>\n<li class=\p1\>dysphagia</li>\n<li class=\p1\>Ipsilateral bulbar paralysis</li>\n<li class=\p1\>Loss of gag reflex</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>Webers syndrome</p>\n</td>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>Posterior cerebral artery</p>\n</td>\n<td class=\td1\ valign=\top\>\n<ul>\n<li class=\p1\>I/L Oculomotor nerve palsy - ptosis with fixed dilated pupil pointed down and out</li>\n<li class=\p1\>C/L hemiplegia</li>\n<li class=\p1\>C/L paralysis of the lower face</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table>\n<p>C/L- Contralateral</p>\n<p>I/L-Ipsilateral</p></div><!---->\n      <!---->\n    </div><!---->'),
(54, 'Medicine,', 'PM1687. Site of Lesion of Movement Disorders', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\312\>\n<p><strong>Movement disorder</strong></p>\n</td>\n<td width=\312\>\n<p><strong>Site of Lesion</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\312\>\n<p>Athetosis</p>\n</td>\n<td width=\312\>Globus pallidus</td>\n</tr>\n<tr>\n<td width=\312\>\n<p>Chorea</p>\n</td>\n<td width=\312\>\n<p>Putamen, Caudate</p>\n</td>\n</tr>\n<tr>\n<td width=\312\>\n<p>Hemiballismus</p>\n</td>\n<td width=\312\>\n<p>Subthalamic Nucleus</p>\n</td>\n</tr>\n<tr>\n<td width=\312\>\n<p>Parkinsonism</p>\n</td>\n<td width=\312\>\n<p>Substantia Nigra&nbsp;</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p><strong>&nbsp;</strong></p></div><!---->\n      <!---->\n    </div><!---->');
