(66, 'Anatomy', 'PM2404. Lymphatic drainage of the tongue', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/a1fe4d06952446bca97ad6f44b6a06a4x1200x2849.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td colspan=\2\>\n<p><strong>Lymphatics of tongue</strong>&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Tip of tongue</p>\n</td>\n<td>\n<p>Submental lymph nodes</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Marginal part of anterior 2/3<sup>rd</sup> of tongue</p>\n</td>\n<td>\n<p>U/L Submandibular nodes</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Central part of anterior 2/3<sup>rd</sup> of tongue</p>\n</td>\n<td>B/L Jugulodigastric and Jugulo-omohyoid lymph nodes</td>\n</tr>\n<tr>\n<td>\n<p>Posterior 1/3<sup>rd</sup> of tongue</p>\n</td>\n<td>\n<p>B/L Jugulodigastric and Jugulo-omohyoid lymph nodes</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Root of tongue</p>\n</td>\n<td>\n<p>B/L upper deep cervical nodes</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(67, 'Anatomy', 'PM2403. Branches of mandibular nerve', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td colspan=\2\><strong>Branches of mandibular nerve</strong></td>\n</tr>\n<tr>\n<td colspan=\2\><strong>Branches from the main trunk</strong></td>\n</tr>\n<tr>\n<td>\n<p><strong>Branches</strong></p>\n</td>\n<td>\n<p><strong>Sensory and motor supply</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Nerve to Medial Pterygoid (motor)</p>\n</td>\n<td>\n<p><strong>Medial Pterygoid</strong></p>\n<p>Tensor Tympani</p>\n<p>Tensor Veli Palatini muscles</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Nervous spinosus (sensory)</p>\n</td>\n<td>Dura of the middle cranial fossa</td>\n</tr>\n<tr>\n<td colspan=\2\><strong>Branches from the anterior division</strong>&nbsp;&nbsp;</td>\n</tr>\n<tr>\n<td>\n<p>Muscular branches (motor)</p>\n</td>\n<td><strong>Lateral pterygoid</strong> and <strong>Masseter</strong></td>\n</tr>\n<tr>\n<td>\n<p>Deep temporal nerves (2 branches)</p>\n</td>\n<td><strong>Temporalis muscle</strong> and periosteum of temporal fossa</td>\n</tr>\n<tr>\n<td>\n<p>Buccal nerve (sensory)</p>\n</td>\n<td>Buccal mucosa</td>\n</tr>\n<tr>\n<td colspan=\2\><strong>Branches from the posterior division</strong>&nbsp;&nbsp;</td>\n</tr>\n<tr>\n<td>Auriculotemporal and&nbsp;lingual nerve&nbsp;</td>\n<td>Sensory supply</td>\n</tr>\n<tr>\n<td>\n<p>Inferior alveolar nerve (motor and sensory)</p>\n</td>\n<td>\n<p>Mylohyoid</p>\n<p>Anterior belly of the Digastric</p>\n<p>Sensation to the lower teeth</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/u