(110, 'Anatomy', 'PM1291. Bronchopulmonary Segments', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Lobe </strong></p>\n</td>\n<td>\n<p><strong>Right lung </strong></p>\n</td>\n<td>\n<p><strong>Left lung </strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Superior lobe</p>\n</td>\n<td>\n<p>I apical</p>\n<p>II posterior</p>\n<p>III anterior</p>\n</td>\n<td>\n<p>I apical</p>\n<p>II posterior</p>\n<p>III anterior</p>\n<p>IV superior lingual</p>\n<p>V inferior lingual</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Middle lobe</p>\n</td>\n<td>\n<p>IV lateral</p>\n<p>V medial</p>\n</td>\n<td>\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Inferior lobe</p>\n</td>\n<td>\n<p>VI superior (apical)</p>\n<p>VII medial basal</p>\n<p>VIII anterior basal</p>\n<p>IX lateral basal</p>\n<p>X posterior basal</p>\n</td>\n<td>\n<p>VI superior (apical)</p>\n<p>VII medial basal</p>\n<p>VIII anterior basal</p>\n<p>IX lateral basal</p>\n<p>X posterior basal</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p><strong>Note :</strong>&nbsp;</p>\n<div class=\page\ title=\Page 607\>\n<div class=\section\>\n<div class=\layoutArea\>\n<div class=\column\>\n<p><strong>Apicoposterior segment:</strong>&nbsp;Left (apical + posterior) segments.&nbsp; &nbsp;</p>\n<p><strong>Anteromedial basal segment: </strong>Left&nbsp;(anterior + medial basal) segments.</p>\n</div>\n</div>\n</div>\n</div></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/01868187d7f74433b19480bb937cf49ex1280x2559.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/968042a00a124d5a89c8e1c6cc219882x1280x2559.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(111, 'Anatomy', 'PM1284. Surface marking for Lungs and Pleura', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\201\>\n<p><strong>Level </strong></p>\n</td>\n<td width=\201\>\n<p><strong>Costodiaphragmatic line (inferior border of pleura)</strong></p>\n</td>\n<td width=\201\>\n<p><strong>Lower border of lung (2 ribs higher)</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\201\>\n<p>Mid-clavicular line</p>\n</td>\n<td width=\201\>\n<p>8<sup>th</sup> rib</p>\n</td>\n<td width=\201\>\n<p>6<sup>th</sup> rib</p>\n</td>\n</tr>\n<tr>\n<td width=\201\>\n<p>Mid-axillary line</p>\n</td>\n<td width=\201\>\n<p>10<sup>th</sup> rib</p>\n</td>\n<td width=\201\>\n<p>8<sup>th</sup> rib</p>\n</td>\n</tr>\n<tr>\n<td width=\201\>\n<p>Paravertebral line</p>\n</td>\n<td width=\201\>\n<p>12<sup>th</sup> rib</p>\n</td>\n<td width=\201\>\n<p>10<sup>th</sup> rib</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/44d7ff6591af4dd5bbac052f2cd379e2x1280x1818.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(112, 'Anatomy,', 'PM1281. Respiratory Muscles', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\651\>\n<tbody>\n<tr>\n<td>\n<p><strong>Type of respiration </strong></p>\n</td>\n<td>\n<p><strong>Inspiration (rib elevators) </strong></p>\n</td>\n<td>\n<p><strong>Expiration (rib depressors)</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Quiet respiration</p>\n</td>\n<td>\n<p>Diaphragm (chief muscle)</p>\n<p>External intercostals</p>\n</td>\n<td>\n<p>Passive</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Deep inspiration</p>\n</td>\n<td>\n<p>External intercostals</p>\n<p>Scalene muscles</p>\n<p>Sternocleidomastoid</p>\n<p>Levator costarum</p>\n<p>Serratus posterior superior</p>\n<p>Innermost intercostals</p>\n</td>\n<td>\n<p>Passive</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Forced respiration</p>\n</td>\n<td>\n<p>Levator scapulae</p>\n<p>Trapezius</p>\n<p>Rhomboids</p>\n<p>Pectoral muscles</p>\n<p>Serratus anterior</p>\n</td>\n<td>\n<p>Anterior abdominals</p>\n<p>Internal intercostals</p>\n<p>Transverse thoracis</p>\n<p>Serratus posterior inferior</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(113, 'Anatomy', 'PM1162. Structures related to the mediastinal surface and hilum of the lungs', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Right lung:</p>\n<ul>\n<li>Anterior to the hilum -<strong>&nbsp;superior vena cava </strong>and <strong>right brachiocephalic vein. </strong></li>\n<li>Posterior to this groove-&nbsp;&nbsp;<strong>azygous vein. </strong></li>\n<li>Behind the hilum and pulmonary ligament- <strong>oesophagus</strong>.</li>\n<li>The <strong>cardiac impression</strong>&nbsp;-&nbsp;right auricle,&nbsp;right atrium and&nbsp;right ventricle</li>\n<li>Posteroinferiorly -<strong>&nbsp;inferior vena cava</strong></li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/31cd8fd201e643a389790a1188c717cfx600x690.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Left lung:</p>\n<ul>\n<li>The <strong>cardiac impression -&nbsp;</strong>left ventricle and auricle and right ventricle</li>\n<li>Large groove that arches over the hilum and descends behind -&nbsp;&nbsp;<strong>Arch of aorta and descending aorta.</strong></li>\n<li>Arrow groove that ascends to the apex-&nbsp;<strong>left subclavian artery</strong></li>\n<li>Superior to the aortic groove-&nbsp;&nbsp;<strong>thoracic duct and esophagus</strong>.</li>\n<li>Anterior to subclavian groove -&nbsp;<strong>left brachiocephalic vein</strong>.</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/ce67d3683b64459a8f933032f965f622x600x722.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(114, 'Anatomy', 'PM1161. Surface Marking of the Heart Valves', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/6534f6581a6745628bd38e1e22b0f752x1280x2708.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(115, 'Anatomy', 'PM0857. Spinous Processes and their Significance', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/288a49ff123a49228684741d1c3d8cdex1280x1586.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Spinous process </strong></p>\n</td>\n<td>\n<p><strong>Significance </strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>C2 spinous process</p>\n</td>\n<td>\n<p>Most superior bony protuberance in the midline inferior to the skull</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>C7 spinous process</p>\n</td>\n<td>\n<p>Lower extent of ligamentum nuchae</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>T3</strong> spinous process</p>\n</td>\n<td>\n<p>Level of the <strong>root of the spine </strong>of the scapula</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>T7</strong> spinous process</p>\n</td>\n<td>\n<p>Level of the <strong>inferior angle</strong> of the scapula</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>T12 spinous process</p>\n</td>\n<td>\n<p>Midpoint of a vertical line between the inferior angle of the scapula and the iliac crest</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>L4 spinous process</p>\n</td>\n<td>\n<p>Level marks the highest point of the iliac crest</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>S2 spinous process</p>\n</td>\n<td>\n<p>Sacral dimples that mark the position of the posterior superior iliac spine are level</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>In thoracic vertebrae, the tips of <strong>thoracic spinous processes lie at the level of the vertebral body below. </strong></p>\n<p>E.g., T3 spinous process lies at the level of T4 vertebral body</p>\n<p>In lumbar and sacral regions, the <strong>tips of spinous processes reflect the position of their corresponding vertebral bodies</strong></p>\n<p>E.g., L 4 spinous process lies at approximately the L4 vertebral level</p></div><!---->\n      <!---->\n    </div><!---->'),
(116, 'Anatomy', 'PM0699. Distribution of Coronary Arteries', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\234\>\n<p><strong>Right Coronary Artery (RCA)</strong></p>\n</td>\n<td width=\234\>\n<p><strong>Left Coronary Artery (LCA)</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\234\>\n<ul>\n<li><strong>RV</strong></li>\n<li><strong>RA</strong></li>\n<li><strong>Posteroinferior 1/3<sup>rd</sup> of IVS</strong></li>\n<li><strong>SA node in 65% patients</strong></li>\n<li><strong>AV node in 80% patients </strong></li>\n<li>Variable part of diaphragmatic aspect of LV</li>\n<li>Part of LA</li>\n</ul>\n</td>\n<td width=\234\>\n<ul>\n<li><strong>Most of LV</strong></li>\n<li><strong>Most of LA </strong></li>\n<li><strong>Anterior 2/3<sup>rd</sup> of IVS</strong></li>\n<li>Narrow strip of RV</li>\n<li>SA node in 35% people</li>\n<li>AV node in 20% people</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(117, 'Obstetrics & Gynaecology,', 'PM2366. Perineal Body', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>The perineal body is not a structure but an <strong>aggregation of fibromuscular tissue</strong> located in the midline at the junction between the anal and urogenital triangles, just ventral to the anal sphincter.</p>\n<p><strong>Ten muscles</strong> of the perineum converge and interlace in the perineal body.</p>\n<table>\n<tbody>\n<tr>\n<td><strong>Category</strong></td>\n<td><strong>Muscles</strong></td>\n</tr>\n<tr>\n<td><strong>Two unpaired</strong></td>\n<td>\n<ul>\n<li>Fibres of longitudinal muscle coat of anal canal</li>\n<li>Fibres from External anal sphincter</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td><strong>Four Paired</strong></td>\n<td>\n<ul>\n<li>Bulbospongiosus</li>\n<li>Superficial transverse perenei</li>\n<li>Deep transverse perenei</li>\n<li>Levator ani</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table>\n<p>Note: In females, sphincter urethrovaginalis is also attached here.</p></div><!---->\n      <!---->\n    </div><!---->'),
(118, 'Anatomy', 'PM2282. Gluteal Muscles', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><div>&nbsp;</div>\n<table><colgroup><col></colgroup>\n<tbody>\n<tr>\n<td>\n<div><strong>Gluteus Medius</strong></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><strong>Origin</strong>- Outer surface of <strong>ilium</strong> between anterior and posterior gluteal lines.</div>\n<div><strong>Insertion</strong> -Oblique ridge on the <strong>lateral</strong> surface of the <strong>greater trochanter.</strong></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><strong>Gluteus Minimus</strong></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><strong>Origin</strong>- Outer surface of ilium between anterior and inferior gluteal lines</div>\n<div><strong>Insertion</strong> -Ridge on the <strong>anterior</strong> surface of <strong>greater trochanter</strong></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><strong>Gluteus Maximus</strong></div>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Origin</strong> - Posterior aspect of dorsal ilium posterior to posterior gluteal line, posterior superior iliac crest, posterior inferior aspect of sacrum and coccyx, and sacrotuberous ligament</p>\n<p><strong>Insertion</strong> -&nbsp;Primarily in <strong>fascia lata</strong> at the iliotibial band; also into the gluteal tuberosity on posterior femoral surface</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(119, 'Anatomy', 'PM2281. Sciatic nerve divisions and supply', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><div>&nbsp;</div>\n<table border=\1\><colgroup><col></colgroup>\n<tbody>\n<tr>\n<td>\n<div><strong>Sciatic nerve divisions and its supply&nbsp;</strong></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><strong>Tibial division</strong></div>\n</td>\n</tr>\n<tr>\n<td>\n<div>Motor:&nbsp;</div>\n<div>semitendinous,&nbsp;semimembranous,&nbsp;long head biceps femoris,&nbsp;gastrocnemius&nbsp;popliteus,&nbsp;plantaris,&nbsp;soleus,&nbsp;tibialis posterior,&nbsp;flexor digitorum longus,&nbsp;flexor hallucis longus, abductor hallucis, flexor digit­orum brevis, flexor hallucis brevis, addcutor hallucis, foot interossei, abductor digiti minimi.</div>\n<div>&nbsp;</div>\n<div>Sensory:&nbsp;</div>\n<div>none</div>\n</td>\n</tr>\n<tr>\n<td>\n<div><strong>Peroneal division</strong></div>\n</td>\n</tr>\n<tr>\n<td>\n<div>Motor:</div>\n<div>short head of biceps femoris, foot interossei, Tibialis anterior, Extensor Hallucis longus, Extensor digitorum longus and Peroneus tertius, Peroneus Longus, Peroneus Brevis.</div>\n<div>&nbsp;</div>\n<div>Sensory:</div>\n<div>articular branch to knee joint</div>\n<div>sural nerve branch&nbsp;</div>\n<div>- closeby structure: saphenous vein, achilles tendon</div>\n<div>- anastomoses: br. of peroneal nerve</div>\n<div>- termination as: lateral dorsal cutaneous nerve&nbsp;&nbsp;</div>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(120, 'Anatomy', 'PM2280. Various muscles of the gluteal region and their innervation', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Muscles</strong></p>\n</td>\n<td>\n<p><strong>Innervation</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Gluteus maximus</p>\n</td>\n<td>\n<p>&nbsp;Inferior gluteal nerve</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Gluteus medius and minimus</p>\n</td>\n<td>\n<p>&nbsp;Superior gluteal nerve</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Pyriformis</p>\n</td>\n<td>\n<p>&nbsp;Nerve to pyriformis</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Obturator internus, Gemellus superior</p>\n</td>\n<td>\n<p>&nbsp;Nerve to obturator internus</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Quadratus femoris, Gemellus inferior</p>\n</td>\n<td>\n<p>&nbsp;Nerve to quadratus femoris</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(121, 'Anatomy', 'PM2214. Lymphatic drainage of pelvic structures', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\ width=\620\>\n<tbody>\n<tr>\n<td><strong>LYMPH NODES</strong></td>\n<td><strong>STRUCTURES DRAINING</strong></td>\n</tr>\n<tr>\n<td>Para - aortic lymph nodes</td>\n<td>Ovaries /Testis</td>\n</tr>\n<tr>\n<td>External iliac lymph nodes</td>\n<td>Uterine body, cervix, superior bladder</td>\n</tr>\n<tr>\n<td>Internal iliac lymph nodes</td>\n<td>Prostate, cervix, corpus cavernosum,proximal vagina</td>\n</tr>\n<tr>\n<td>Superficial inguinal lymph nodes</td>\n<td>Distal vagina, vulva, scrotum, distal anal canal</td>\n</tr>\n<tr>\n<td>Deep inguinal lymph nodes</td>\n<td>Glans penis</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p>*Ref - Snells Clinical Anatomy - 4e&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(122, 'Anatomy,', 'PM2080. Calots triangle and hepatobiliary triangle', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Boundaries</strong></td>\n<td><strong>Calots Triangle</strong></td>\n<td><strong>Hepatobiliary Triangle</strong></td>\n</tr>\n<tr>\n<td>\n<p>Superiorly</p>\n</td>\n<td>Superior border of the cystic artery</td>\n<td>Inferior surface of the liver</td>\n</tr>\n<tr>\n<td>\n<p>Medially</p>\n</td>\n<td colspan=\2\>Common hepatic duct</td>\n</tr>\n<tr>\n<td>\n<p>Inferiorly</p>\n</td>\n<td colspan=\2\>Cystic duct</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/31ed9f9d009e43dbb6eef6616e0696bfx1280x1611.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Contents of Calot’s triangle:</p>\n<ul>\n<li><strong>Lymph node of Lund</strong> - Sentinel lymph node for the gallbladder</li>\n<li><strong>Branching of cystic artery</strong> from the right hepatic artery</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(123, 'Anatomy', 'PM2078. Common sites of porto-systemic anastomosis in portal hypertension', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ul>\n<li>Left gastric vein [portal] to Distal oesophageal [systemic] veins: Presents with oesophageal and gastric variceal bleeding</li>\n<li>Superior rectal veins to Middle and inferior rectal veins: Presents with rectal varices and lower GI tract bleeding</li>\n<li>Tributaries of left branch of portal vein in ligamentum teres to Periumbilical branches of epigastric and intercostal veins: Forms <strong>caput medusae</strong></li>\n<li>Tributaries of right branch of portal vein overlying ‘bare area’ of liver to Retroperitoneal veins draining into azygos, hemiazygos, lumbar, intercostal and phrenic veins</li>\n<li>Omental and colonic veins near hepatic and splenic ﬂexures to Retroperitoneal veins near hepatic and splenic ﬂexures</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(124, 'Anatomy,', 'PM1851. Fallopian tube', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Gross anatomy:</strong></p>\n<p>The fallopian tube is divided into the following parts:</p>\n<div class=\page\ title=\Page 31\>\n<div class=\section\>\n<div class=\layoutArea\>\n<div class=\column\>\n<ul>\n<li><strong>Intramural/Interstitial portion:</strong>&nbsp;&nbsp;\n<ul>\n<li>Intrauterine</li>\n<li>The narrowest and shortest part</li>\n</ul>\n</li>\n<li><strong>Isthmus:</strong>\n<ul>\n<li>The most common site for tubectomy</li>\n</ul>\n</li>\n<li><strong>Ampulla:</strong>\n<ul>\n<li>The longest and widest part</li>\n<li>The most common site for - fertilization and ectopic pregnancy</li>\n</ul>\n</li>\n<li><strong>Infundibulum</strong></li>\n</ul>\n</div>\n</div>\n</div>\n</div></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/6b727bd9f7374ff5820d26e5f0792421x1279x1178.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Histology:</strong></p>\n<p>The ampulla, isthmus and infundibulum are extrauterine. Theyre covered by three layers:&nbsp;</p>\n<div class=\section\>\n<ul class=\p-rich_text_list p-rich_text_list__bullet\>\n<li><strong>Mesosalpinx</strong>&nbsp;- It is the peritoneal layer. Also called serosa.</li>\n<li><strong>Myosalpinx -&nbsp;</strong>It contains smooth muscle and has 2 layers:\n<ul>\n<li>Inner circular&nbsp;</li>\n<li>Outer longitudinal</li>\n</ul>\n</li>\n<li><strong>Endosalpinx</strong>&nbsp;- It is the mucosal layer. It has the following features:\n<ul>\n<li>Numerous branching folds, filling the entire lumen</li>\n<li>Basement membrane</li>\n<li>Ciliated, columnar epithelium&nbsp;</li>\n<li>Peg cells - non-ciliated epithelium. They help in the capacitation of sperms.&nbsp;</li>\n</ul>\n</li>\n</ul>\n</div></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/5cb4de67c0044d44bfa3a39d78a56832x510x533.GIF\><!---->\n      </div><!---->\n    </div><!---->'),
(125, 'Anatomy,', 'PM1580. Characteristic differentiating features of the jejunum and ileum', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Characteristic</strong></p>\n</td>\n<td>\n<p><strong>Jejunum</strong></p>\n</td>\n<td>\n<p><strong>Ileum</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Position</p>\n</td>\n<td>\n<p>Upper 2/5</p>\n</td>\n<td>\n<p>Lower 3/5</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Diameter</p>\n</td>\n<td>\n<p>More</p>\n</td>\n<td>\n<p>Less</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Wall</p>\n</td>\n<td>\n<p>Thick</p>\n</td>\n<td>\n<p>Thin</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Circular folds</p>\n</td>\n<td>\n<p>Large and numerous, with large villi</p>\n</td>\n<td>\n<p>Few and small, with less abundant villi</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Vascularity</p>\n</td>\n<td>\n<p>More</p>\n</td>\n<td>\n<p>Less</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Vasa recta</p>\n</td>\n<td>\n<p>Long</p>\n</td>\n<td>\n<p>Short</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Colour</p>\n</td>\n<td>\n<p>Deep red</p>\n</td>\n<td>\n<p>Pale pink</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Lymphatic follicles</p>\n</td>\n<td>\n<p>Solitary</p>\n</td>\n<td>\n<p>Aggregated</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Fat in mesentry</p>\n</td>\n<td>\n<p>Less</p>\n</td>\n<td>\n<p>More</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(126, 'Surgery,', 'PM1371. Passaro’s Triangle', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Around <strong>90% of gastrinomas</strong> are found in this triangle.</p>\n<p>Its boundaries are as follows:</p>\n<ul>\n<li>Junction of the cystic duct and common bile duct</li>\n<li>Junction of the 2<sup>nd</sup> and 3<sup>rd</sup> parts of the duodenum</li>\n<li>Junction of the body and neck of the pancreas</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/78538d8853224efd93d30bd4a826420dx1280x1347.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(127, 'Obstetrics & Gynaecology,', 'PM1352. Nerve supply in the pelvis', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Organ </strong>&nbsp;</td>\n<td><strong>Spinal Segments </strong>&nbsp;</td>\n<td><strong>Nerves</strong></td>\n</tr>\n<tr>\n<td>Perineum, vulva, lower vagina</td>\n<td>S2–4</td>\n<td>Pudendal, inguinal, genitofemoral, posterofemoral cutaneous</td>\n</tr>\n<tr>\n<td>Upper vagina, cervix, lower uterine segment</td>\n<td>S2–4</td>\n<td>Pelvic parasympathetic</td>\n</tr>\n<tr>\n<td>Uterine fundus, proximal fallopian tubes, broad ligament</td>\n<td>T11–12, L1</td>\n<td>Sympathetic via hypogastric plexus</td>\n</tr>\n<tr>\n<td>Outer two-thirds of fallopian tubes</td>\n<td>T9–10</td>\n<td>Sympathetic via aortic and superior mesenteric plexus</td>\n</tr>\n<tr>\n<td>Ovaries</td>\n<td>T10-T11</td>\n<td>Sympathetic via renal and aortic plexus and celiac and mesenteric ganglia</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<div class=\page\ title=\Page 35\>&nbsp;</div></div><!---->\n      <!---->\n    </div><!---->'),
(128, 'Obstetrics & Gynaecology,', 'PM1351. Contents of the Broad ligament', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ul>\n<li>One tube - Fallopian tube</li>\n<li>Two ligaments:\n<ul>\n<li>Round ligament of uterus</li>\n<li>Ovarian ligament</li>\n</ul>\n</li>\n<li>Two arteries:\n<ul>\n<li>Uterine artery</li>\n<li>Ovarian artery</li>\n</ul>\n</li>\n<li>Two nerve plexus:\n<ul>\n<li>Uterovaginal plexus</li>\n<li>Ovarian plexus</li>\n</ul>\n</li>\n<li>Three embryological remnants:\n<ul>\n<li>Epoophoron (Wolffian body)</li>\n<li>Paroophoron (Wolffian body)</li>\n<li>Gartner’s duct (Wolffian duct)</li>\n</ul>\n</li>\n<li>Other structures:\n<ul>\n<li>Lymph vessels and lymph nodes</li>\n<li>Fibrous connective tissue</li>\n</ul>\n</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/11e9e8c00e094ece8e73a5e02653ace5x600x652.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(129, 'Anatomy,', 'PM1292. Length of various structures in the human body', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\109\>\n<p><strong>Length</strong></p>\n</td>\n<td width=\336\>\n<p><strong>Structures</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\109\>\n<p><strong>4 cm</strong></p>\n</td>\n<td width=\336\>\n<p><strong>Inguinal canal</strong></p>\n<p><strong>Female urethra</strong></p>\n<p><strong>Anal canal</strong></p>\n<p>Auditory tube</p>\n<p>Cystic duct</p>\n<p><strong>Optic nerve</strong></p>\n<p>Prostatic urethra</p>\n</td>\n</tr>\n<tr>\n<td width=\109\>\n<p><strong>5 cm</strong></p>\n</td>\n<td width=\336\>\n<p>Male larynx</p>\n<p>Left main bronchus</p>\n<p>Parotid and submandibular ducts</p>\n<p>Testes</p>\n<p><strong>First part of the duodenum</strong></p>\n<p><strong>Neck of the femur</strong></p>\n<p><strong>Medial and lateral walls of the orbit</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\109\>\n<p><strong>7.5 cm</strong></p>\n</td>\n<td width=\336\>\n<p><strong>Anterior wall of the vagina</strong></p>\n<p>Virgin uterus</p>\n<p>Second part of the duodenum</p>\n<p><strong>Bile duct</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\109\>\n<p><strong>9 cm&nbsp;</strong></p>\n</td>\n<td width=\336\>\n<p>Appendix</p>\n<p>Multiparous uterus</p>\n<p><strong>Posterior wall of the vagina</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\109\>\n<p><strong>11 cm</strong></p>\n</td>\n<td width=\336\>\n<p><strong>Trachea</strong></p>\n<p>Kidney</p>\n<p>Uterine tube</p>\n<p>Third part of the duodenum</p>\n</td>\n</tr>\n<tr>\n<td width=\109\>\n<p><strong>12 cm</strong></p>\n</td>\n<td width=\336\>\n<p><strong>Pharynx</strong></p>\n<p><strong>Rectum</strong></p>\n<p>Spleen</p>\n</td>\n</tr>\n<tr>\n<td width=\109\>\n<p><strong>15 cm</strong></p>\n</td>\n<td width=\336\>\n<p>Adductor canal</p>\n<p><strong>Ascending colon</strong></p>\n<p>Pancreas</p>\n<p><strong>Root of mesentry</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\109\>\n<p><strong>25 cm</strong></p>\n</td>\n<td width=\336\>\n<p><strong>Esophagus</strong></p>\n<p><strong>Duodenum</strong></p>\n<p><strong>Ureter</strong></p>\n<p><strong>Descending colon</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\109\>\n<p><strong>45 cm</strong></p>\n</td>\n<td width=\336\>\n<p><strong>Spinal cord</strong></p>\n<p><strong>Thoracic duct</strong></p>\n<p><strong>Vas deferens</strong></p>\n<p><strong>Sartorius</strong></p>\n<p><strong>Femur</strong></p>\n<p><strong>Transverse colon</strong></p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(130, 'Anatomy,', 'PM1265. Features of anal canal above and below pectinate line', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p>&nbsp;</p>\n</td>\n<td>\n<p><strong>Above Pectinate line</strong></p>\n</td>\n<td>\n<p><strong>Below Pectinate line</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Embryonic origin</strong></p>\n</td>\n<td>\n<p><strong>Endoderm</strong></p>\n</td>\n<td>\n<p><strong>Ectoderm</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Lining</p>\n</td>\n<td>\n<p>Simple columnar</p>\n</td>\n<td>\n<p><strong>Stratified squamous</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Arterial supply</p>\n</td>\n<td>\n<p>Superior rectal artery</p>\n</td>\n<td>\n<p>Inferior rectal&nbsp; artery</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Venous drainage</p>\n</td>\n<td>\n<p><strong>Portal: </strong>Superior rectal vein</p>\n</td>\n<td>\n<p><strong>Systemic:</strong>&nbsp;Inferior rectal vein</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Lymphatic drainage</p>\n</td>\n<td>\n<p>To pelvic and lumbar nodes</p>\n</td>\n<td>\n<p>To inguinal nodes</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Nerve supply</p>\n</td>\n<td>\n<p>Visceral—Autonomic fibers</p>\n</td>\n<td>\n<p><strong>Somatic</strong><strong>—Inferior rectal nerves </strong>(branches of pudendal nerve )</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Physiology</p>\n</td>\n<td>\n<p>Sensation quickly diminishes</p>\n</td>\n<td>\n<p><strong>Sensitive to pain</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Cancer</p>\n</td>\n<td>\n<p><strong>Adenocarcinoma</strong></p>\n</td>\n<td>\n<p>Squamous cell carcinoma</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Varices</p>\n</td>\n<td>\n<p>Internal hemorrhoids, Painless</p>\n</td>\n<td>\n<p><strong>External hemorrhoids, Painful</strong></p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(131, 'Anatomy', 'PM1237. Sacral Plexus', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Sacral plexus</strong>&nbsp;is formed from the ventral rami of <strong>L4–S4</strong> spinal nerves.&nbsp;</p>\n<p>The following table shows the branches of sacral plexus, their root value, muscles innervated, and cutaneous branches.<strong><u>&nbsp;</u></strong></p>\n<table>\n<tbody>\n<tr>\n<td>\n<p><strong>Nerve</strong></p>\n</td>\n<td>\n<p><strong>Root value</strong></p>\n</td>\n<td>\n<p><strong>Innervated muscles</strong></p>\n</td>\n<td>\n<p><strong>Cutaneous branches</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Superior gluteal</p>\n</td>\n<td>\n<p>L4-S1 (From dorsal division)</p>\n</td>\n<td>\n<p>Gluteus medius</p>\n<p>Gluteus minimus</p>\n<p>Tensor fasciae lata</p>\n</td>\n<td>\n<p><strong>&nbsp;</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Inferior gluteal</p>\n</td>\n<td>\n<p>L5-S2 (From dorsal division</p>\n</td>\n<td>\n<p>Gluteus maximus</p>\n</td>\n<td>\n<p><strong>&nbsp;</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Posterior femoral&nbsp;cutaneous nerve&nbsp;</p>\n</td>\n<td>\n<p>S1-S3</p>\n<p>(From dorsal division of S1, S2 and ventral division of S2, S3)</p>\n</td>\n<td>\n<p>&nbsp;</p>\n</td>\n<td>\n<p>Posterior cutaneous femoral</p>\n<p>•&nbsp;Inferior cluneal nerves</p>\n<p>•&nbsp;Perineal branches</p>\n</td>\n</tr>\n<tr>\n<td colspan=\4\>\n<p><strong>Direct branches from plexus</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Nerve to Piriformis</p>\n</td>\n<td>\n<p>S1-2</p>\n</td>\n<td>\n<p>Piriformis</p>\n</td>\n<td>\n<p><strong>&nbsp;</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Nerve to Obturator internus</p>\n</td>\n<td>\n<p>L5, S1-2</p>\n</td>\n<td>\n<p>Obturator internus&nbsp;and&nbsp;Superior gemellus</p>\n</td>\n<td>\n<p><strong>&nbsp;</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Nerve to Quadratus femoris</p>\n</td>\n<td>\n<p>L4-5, S1</p>\n</td>\n<td>\n<p>Quadratus femoris&nbsp;and&nbsp;Inferior gemellus</p>\n</td>\n<td>\n<p><strong>&nbsp;</strong></p>\n</td>\n</tr>\n<tr>\n<td colspan=\4\>\n<p><strong>Sciatic Nerve</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Sciatic</p>\n</td>\n<td>\n<p>L4-S3</p>\n</td>\n<td>\n<p>Semitendinosus&nbsp;(Tib)</p>\n<p>Semimembranosus&nbsp;(Tib)</p>\n<p>Biceps femoris</p>\n<p>• Long head (Tib)</p>\n<p>• Short head (Fib)</p>\n<p>Adductor magnus&nbsp;(medial part, Tib)</p>\n</td>\n<td>\n<p><strong>&nbsp;</strong></p>\n</td>\n</tr>\n<tr>\n<td rowspan=\2\>\n<p><strong>Common fibular</strong></p>\n</td>\n<td rowspan=\2\>\n<p>L4-S2</p>\n</td>\n<td rowspan=\2\>\n<p>&nbsp;</p>\n</td>\n<td>\n<p>Lateral sural cutaneous</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Communicating fibular</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Tibial nerve</strong></p>\n</td>\n<td>\n<p>L4-S3</p>\n</td>\n<td>\n<p>Triceps surae</p>\n<p>Plantaris</p>\n<p>Popliteus</p>\n<p>Tibialis posterior</p>\n<p>Flexor digitorum longus</p>\n<p>Flexor hallucis longus</p>\n</td>\n<td>\n<p>Medial sural cutaneous, Lateral calcaneal, Medial&nbsp; calcaneal, Lateral dorsal cutaneous</p>\n</td>\n</tr>\n<tr>\n<td colspan=\4\>\n<p><strong>Pudendal and coccygeal</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Pudendal</p>\n<p>(Pudendal plexus)</p>\n</td>\n<td>\n<p>S2-S4</p>\n</td>\n<td>\n<p>Muscles of the&nbsp;pelvic floor:</p>\n<p>Levator ani</p>\n<p>Superficial transverse perineal</p>\n<p>Deep transverse perineal</p>\n<p>Bulbospongiosus</p>\n<p>Ischiocavernosus</p>\n<p>Sphincter anus externus</p>\n<p>Urethral sphincter</p>\n</td>\n<td>\n<p>Inferior rectal</p>\n<p>Perineal</p>\n<p>•&nbsp;Posterior scrotal/labial</p>\n<p>•&nbsp;Dorsal penis/clitoris</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Coccygeal</p>\n<p>(Coccygeal plexus)</p>\n</td>\n<td>\n<p>S5-Co1</p>\n</td>\n<td>\n<p>Coccygeus</p>\n</td>\n<td>\n<p>Anococcygeal</p>\n<p>Dorsal branches</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/1eaa725d82cc403dae459834ac9588eax1279x1531.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(132, 'Obstetrics & Gynaecology,', 'PM1166. Supports of the uterus', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>A. Primary supports</strong></p>\n<ul>\n<li><strong>Muscular or active support of the uterus:</strong>\n<ul>\n<li>Pelvic diaphragm</li>\n<li>Perineal body</li>\n<li>Urogenital diaphragm</li>\n</ul>\n</li>\n<li><strong>Ligamentous (fibromuscular or mechanical) support:</strong>\n<ul>\n<li>Transverse cervical ligaments of Mackenrodt or cardinal ligament</li>\n<li>Uterosacral ligament</li>\n<li>Round ligament of the uterus</li>\n<li>Pubocervical ligament</li>\n<li>Uterine axis</li>\n</ul>\n</li>\n</ul>\n<p><strong>B. Secondary supports formed by peritoneal ligaments:</strong></p>\n<ul>\n<li>Broad ligament</li>\n<li>Uterovesical fold of peritoneum</li>\n<li>Rectovaginal fold of peritoneum</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(133, 'Surgery,', 'PM1159. Couinauds liver segments', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>The <strong>Couinaud classification</strong> is currently the most widely used system to describe functional liver anatomy.</p>\n<ul>\n<li>Based on the <strong>distribution of hepatic portal venous branches</strong> in the parenchyma</li>\n<li>Each segment has its own dual vascular inflow, biliary drainage and lymphatic drainage</li>\n</ul>\n<p><strong>Segment I</strong> corresponds to the gross anatomical <strong>caudate lobe</strong> and is considered as posterior segment.</p>\n<p>Other segments:</p>\n<table width=\0\>\n<tbody>\n<tr>\n<td width=\238\>\n<p><strong>Liver sections</strong></p>\n</td>\n<td width=\286\>\n<p><strong>Couinauds Segments</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\238\>\n<p>Right anterior sections</p>\n</td>\n<td width=\286\>\n<p>Segments V,VIII</p>\n</td>\n</tr>\n<tr>\n<td width=\238\>\n<p>Right Posterior sections</p>\n</td>\n<td width=\286\>\n<p>Segments VI, VII</p>\n</td>\n</tr>\n<tr>\n<td width=\238\>\n<p>Left medial sections</p>\n</td>\n<td width=\286\>\n<p>Segment IV (<strong>quadrate lobe</strong>)</p>\n</td>\n</tr>\n<tr>\n<td width=\238\>\n<p>Left lateral sections</p>\n</td>\n<td width=\286\>\n<p>Segment II,&nbsp;III</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/8db329e3f1de43dbb358b097349abc84x600x1249.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(134, 'Anatomy,', 'PM1129. Relations of epiploic foramen and lesser sac', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Relations of epiploic foramen:</strong></p>\n<table>\n<tbody>\n<tr>\n<td>\n<p>Anterior</p>\n</td>\n<td>\n<ul>\n<li>Hepatoduodenal ligament containing hepatic artery, portal vein, and common bile duct</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>\n<p>Posterior</p>\n</td>\n<td>\n<ul>\n<li>Inferior vena cava</li>\n<li>Right suprarenal gland</li>\n<li>Body of T12 vertebra</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>\n<p>Superior</p>\n</td>\n<td>\n<ul>\n<li>Caudate lobe of the liver</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>\n<p>Inferior</p>\n</td>\n<td>\n<ul>\n<li>1st part of duodenum</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>Relations of lesser sac:</strong></p>\n<table>\n<tbody>\n<tr>\n<td>\n<p>Anterior</p>\n</td>\n<td>\n<ul>\n<li>Caudate lobe of liver</li>\n<li>Lesser omentum</li>\n<li>Stomach</li>\n<li>First 2 layers of the greater omentum</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>\n<p>Posterior</p>\n</td>\n<td>\n<p>Structures forming the stomach bed:</p>\n<ul>\n<li>Pancreas</li>\n<li>Splenic artery</li>\n<li>Left kidney and suprarenal gland</li>\n<li>Commencement of abdominal aorta and coeliac artery</li>\n<li>Left crus of the diaphragm</li>\n<li>Splenic flexure of the colon</li>\n<li>Transverse mesocolon</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>\n<p>Superior</p>\n</td>\n<td>\n<ul>\n<li>Reflection of the peritoneum from the esophagus to the diaphragm</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>\n<p>Inferior</p>\n</td>\n<td>\n<ul>\n<li>Continuation of 2nd and 3rd layers of the greater omentum</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/9940ab3ecf6944718889681351185ca3x1280x1549.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(135, 'Anatomy,', 'PM1128. Peritoneal folds and their contents', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>The following table shows various peritoneal folds (omenta, mesenteries and ligaments) and their contents.</p>\n<table width=\100%\>\n<tbody>\n<tr>\n<td width=\51%\>\n<p><strong>Peritoneal</strong> <strong>fold</strong></p>\n</td>\n<td width=\48%\>\n<p><strong>Content</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\51%\>\n<p>Lesser omentum</p>\n<p>a)&nbsp;&nbsp;&nbsp; Hepatoduodenal ligament&nbsp;</p>\n<p>b)&nbsp;&nbsp;&nbsp; Hepatogastric ligament&nbsp;</p>\n</td>\n<td width=\48%\>\n<p>&nbsp;</p>\n<p>Hepatic artery, portal vein, and common bile duct</p>\n<p>Right and left gastric vessels</p>\n</td>\n</tr>\n<tr>\n<td width=\51%\>\n<p>Greater omentum (along the greater curvature of the stomach)</p>\n</td>\n<td width=\48%\>\n<p>Right and left gastro-epiploic vessels</p>\n</td>\n</tr>\n<tr>\n<td width=\51%\>\n<p>Gastrosplenic ligament</p>\n</td>\n<td width=\48%\>\n<p>Short gastric and left gastro-epiploic branches of splenic artery</p>\n</td>\n</tr>\n<tr>\n<td width=\51%\>\n<p>Gastrophrenic ligament</p>\n</td>\n<td width=\48%\>\n<p>Left inferior phrenic artery</p>\n</td>\n</tr>\n<tr>\n<td width=\51%\>\n<p>Gastrocolic ligament</p>\n</td>\n<td width=\48%\>\n<p>Right gastroepiploic artery</p>\n</td>\n</tr>\n<tr>\n<td width=\51%\>\n<p>Splenorenal/Lienorenal ligament</p>\n</td>\n<td width=\48%\>\n<p>Splenic vessels. Also contains the tail of the pancreas</p>\n</td>\n</tr>\n<tr>\n<td width=\51%\>\n<p>Mesentery</p>\n</td>\n<td width=\48%\>\n<p>Jejunal and ileal branches of superior mesenteric artery</p>\n</td>\n</tr>\n<tr>\n<td width=\51%\>\n<p>Transverse mesocolon</p>\n</td>\n<td width=\48%\>\n<p>Middle colic vessels</p>\n</td>\n</tr>\n<tr>\n<td width=\51%\>\n<p>Sigmoid mesocolon</p>\n</td>\n<td width=\48%\>\n<p>Sigmoid and superior rectal vessels</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/682a83236eea4834801e008fc9aa9611x1280x3716.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(136, 'Anatomy,', 'PM1045. The Triangle of Doom', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>The triangle of doom</strong> is bounded</p>\n<ul>\n<li>Medially by the vas deferens</li>\n<li>Laterally by the vessels of the spermatic cord (gonadal vessels)</li>\n<li>Inferiorly by peritoneal reflection</li>\n</ul>\n<p><strong>Apex </strong>oriented superiorly at <strong>internal ring</strong>.</p>\n<p><strong>The contents</strong> of the space include</p>\n<ul>\n<li>External iliac vessels</li>\n<li>Deep circumflex iliac vein</li>\n<li>Genital branch of the genitofemoral nerve</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/3b35f594bf0b4482aff734ac2f73ab62x1280x1055.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(137, 'Surgery,', 'PM1038. Boundaries of Hesselbach’s triangle', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ul>\n<li>Lateral - inferior epigastric artery</li>\n<li>Medial - lateral border of rectus abdominis</li>\n<li>Inferior - inguinal ligament</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/18dc8430e3d0464aa62433640a547105x600x645.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Direct inguinal hernia</strong> emerges through<strong> Hesselbach’s triangle</strong> (Hence situated <strong>medial to inferior epigastric artery</strong>)</p>\n<p><strong>Indirect inguinal hernia </strong>enters deep inguinal ring (Hence it is <strong>lateral to inferior epigastric artery)</strong></p></div><!---->\n      <!---->\n    </div><!---->');
INSERT INTO db_pearl_m (pid, subject, pearl_title, pearl_desc) VALUES
(138, 'Surgery,', 'PM1037. Inguinal canal', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>It is a <strong>4 cm long intermuscular canal </strong>directed downwards, forwards, and medially.</p>\n<p>It extends <strong>from the deep inguinal ring to the superficial ring.</strong></p>\n<p><strong>Boundaries:</strong></p>\n<div class=\panel-body ng-scope\>\n<table width=\601\>\n<tbody>\n<tr>\n<td>\n<p>Boundary</p>\n</td>\n<td>Structures forming</td>\n</tr>\n<tr>\n<td>\n<p>Anterior wall</p>\n</td>\n<td>\n<ul>\n<li>Skin, superficial fascia, external oblique aponeurosis (whole extent)</li>\n<li>Muscle fibers of internal oblique (lateral 1/3<sup>rd</sup>)</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>\n<p>Posterior wall</p>\n</td>\n<td>\n<ul>\n<li>Parietal peritoneum, extraperitoneal tissues, fascia transversalis (whole extent)</li>\n<li>Medially reinforced by the conjoint tendon and reflected part of the inguinal ligament</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>\n<p>Roof</p>\n</td>\n<td>\n<ul>\n<li>Arched fibers of internal oblique and transversus abdominis forming the conjoint tendon medially</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>\n<p>Floor</p>\n</td>\n<td>\n<ul>\n<li>Grooved surface of the inguinal ligament</li>\n<li>Lacunar ligament at the medial end</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table>\n&nbsp;</div></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Deep inguinal ring&nbsp;</strong>is an oval opening in the&nbsp;<strong>fascia transversalis</strong> situated 1.25cm above the mid-inguinal point.</p>\n<p><strong>Superficial inguinal ring </strong>is a&nbsp;triangular opening in<strong> external oblique aponeurosis</strong> situated 1cm above and lateral to pubic crest (or medial to the pubic tubercle).</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/5f5fca36f32146b3bfa76b1c85145cfex600x606.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/4556d0df7eaa4ac1b0c1d9f99e82d1f3x720x620.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(139, 'Anatomy,', 'PM0592. Abdominal planes and their landmarks', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/3a8342468e1f4b4eac719ca7d878730cx1280x2558.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Planes that divide the abdomen into 9 regions are:</p>\n<ul>\n<li><strong>Horizontal planes</strong>&nbsp;\n<ul>\n<li>At the level of<strong> L3</strong> - <strong>Subcostal plane</strong> (between 10<sup>th</sup> costal cartilages). It corresponds to the 3<sup>rd</sup> part of the duodenum</li>\n<li>At the level of<strong> L5</strong> - <strong>Transtubercular plane</strong> (between tubercles of iliac crests). It corresponds to the origin of inferior vena cava.&nbsp;</li>\n</ul>\n</li>\n<li><strong>Vertical planes</strong>&nbsp;(paramedian or lateral planes)\n<ul>\n<li>From mid-clavicular point to mid-inguinal point on both sides&nbsp;</li>\n</ul>\n</li>\n</ul>\n<p>The&nbsp;nine regions&nbsp;of the abdomen include:</p>\n<ul>\n<li>Right hypochondriac, epigastric, left hypochondriac</li>\n<li>Right lumbar, umbilical (central), left lumbar</li>\n<li>Right iliac, hypogastric (suprapubic), left iliac</li>\n</ul>\n<p>Other planes and angles in the abdomen are:</p>\n<ul class=\ul1\>\n<li class=\li1\><strong>Transpyloric </strong>(Addisons)<strong> plane -&nbsp;</strong>passes&nbsp;<strong>midway</strong>&nbsp;between the&nbsp;<strong>suprasternal notch</strong>&nbsp;and the&nbsp;<strong>upper border of the pubic</strong>&nbsp;<strong>symphysis</strong>.</li>\n<li class=\li1\><strong>Supracristal plane -</strong> passes through the <strong>highest point </strong>of the<strong> iliac crest</strong> on both sides. It corresponds to the level of the<strong> L4 body</strong> or L4/5 disc and its spinous process. It is an important landmark when performing a lumbar puncture or spinal injection. The bifurcation of the abdominal aorta lies approximately in this plane.</li>\n<li class=\li1\>The <strong>plane </strong>of the <strong>pubic crest -</strong>&nbsp;passes through the <strong>upper border </strong>of the<strong> pubic symphysis</strong>. In supine, this plane intersects the tip of the coccyx and the femoral head.</li>\n<li class=\li1\><strong>Renal angle -</strong>&nbsp;between the <strong>12th rib</strong> and the outer border of<strong> erector spinae </strong>muscle. The lower border of normal kidneys may occasionally be felt here.&nbsp;</li>\n<li class=\li1\><strong>Lumbar triangle of Petit -</strong>&nbsp;formed below by the <strong>iliac crest</strong>, anteriorly by <strong>external oblique,</strong> and posteriorly by <strong>latissimus dorsi</strong>. Lumbar hernia can occur here.&nbsp;</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(140, 'Anatomy,', 'PM0565. Retroperitoneal structures', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>The retroperitoneal structures are posterior to the peritoneal sac and do not have a mesentery. (Mnemonic:<strong> SAD PUCKER)</strong></p>\n<ul>\n<li><strong>S</strong>uprarenal gland</li>\n<li><strong>A</strong>orta</li>\n<li><strong>D</strong>uodenum (2<sup>nd</sup> and 3<sup>rd</sup> Part)</li>\n<li><strong>P</strong>ancreas (except the tail)</li>\n<li><strong>U</strong>reters</li>\n<li><strong>C</strong>eliac Plexus</li>\n<li><strong>C</strong>olon (Ascending and Descending)</li>\n<li><strong>K</strong>idneys</li>\n<li><strong>E</strong>sophagus</li>\n<li><strong>R</strong>ectum</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(141, 'Surgery,', 'PM0563. Inguinal canal and spermatic cord contents', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Inguinal canal contents:</strong></p>\n<table width=\494\>\n<tbody>\n<tr>\n<td><strong>Male</strong></td>\n<td><strong>Female</strong></td>\n</tr>\n<tr>\n<td>\n<ul>\n<li><strong>Spermatic cord </strong>&nbsp;(with genital branch of genitofemoral nerve)</li>\n<li><strong>Ilioinguinal nerve</strong></li>\n</ul>\n</td>\n<td>\n<ul>\n<li><strong>Round ligament</strong> of the uterus&nbsp;</li>\n<li>Genital branch of genitofemoral nerve</li>\n<li><strong>Ilioinguinal nerve</strong></li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/89792db95d08484bb9c1ea17a218e53fx1279x1221.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Spermatic cord contents:</strong></p>\n<ul>\n<li>Three arteries:\n<ul>\n<li>Testicular artery</li>\n<li>Deferential artery</li>\n<li>Cremasteric artery</li>\n</ul>\n</li>\n<li>Two nerves:\n<ul>\n<li>Genital branch of the genitofemoral nerve</li>\n<li>Sympathetic nerve fibers</li>\n</ul>\n</li>\n<li>Three other structures:\n<ul>\n<li>Ductus deferens</li>\n<li>Pampiniform plexus of veins</li>\n<li>Lymphatic vessels</li>\n</ul>\n</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/b8ee5e974da547ccb2d3ff910e6fdd22x1279x1263.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(142, 'Anatomy,', 'PM0560. Transition of layers from abdominal wall to scrotum', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Layers of the abdominal wall</strong></p>\n</td>\n<td>\n<p><strong>Corresponding layers of the scrotum</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Skin</p>\n</td>\n<td>\n<p>Skin</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Superficial fascia</p>\n<p>(Camper and Scarpa)</p>\n</td>\n<td>\n<p>Colles’ fascia and dartos muscle</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>External oblique muscle</p>\n</td>\n<td>\n<p>External spermatic fascia</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Internal oblique muscle</p>\n</td>\n<td>\n<p>Cremasteric fascia and muscle</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>&nbsp;Transverse abdominus muscle</p>\n</td>\n<td>\n<p>Does not continue</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Transversalis fascia</p>\n</td>\n<td>\n<p>Internal spermatic fascia</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Extraperitoneal fat</p>\n</td>\n<td>\n<p>Extraperitoneal fat</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Parietal peritoneum</p>\n</td>\n<td>\n<p>Tunica vaginalis</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/ca5415f018714345a1bf62527049964dx1280x1869.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(143, 'Anatomy,', 'PM0559. Branches of the abdominal aorta', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Branches of the abdominal aorta:</strong></p>\n<ul>\n<li>Anterior branches (unpaired):\n<ul>\n<li>Celiac trunk - T12</li>\n<li>Superior mesenteric artery - L1</li>\n<li>Inferior mesenteric artery - L3</li>\n</ul>\n</li>\n<li>Lateral branches (paired):\n<ul>\n<li>Inferior phrenic artery - T12</li>\n<li>Middle suprarenal artery - T12</li>\n<li>Renal artery - L1, L2</li>\n<li>Gonadal artery - L2</li>\n</ul>\n</li>\n<li>Dorsal branches:\n<ul>\n<li>Lumbar artery (four pairs) - L1 to L4</li>\n<li>Median sacral artery - L4&nbsp;</li>\n</ul>\n</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(144, 'Anatomy', 'PM2401. Eponyms named after Friedrich Trendelenburg', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ul>\n<li><strong>Trendelenburg sign</strong>: This sign is demonstrated by asking the patient to stand on one limb. When the patient stands on the affected limb, the pelvis severely drops over the swing limb.&nbsp;This is seen in the paralysis of gluteus medius and minimus, dislocated hip, fracture neck of femur</li>\n<li><strong>Trendelenburg gait</strong>: This gait is seen in patients with Trendelenburg sign. The body swings to the opposite side every time when the weight is borne on the same side.&nbsp;</li>\n<li><strong>Trendelenberg position: </strong>It is a position in which the patient is placed on a bed which is put into incline such that the patients head is lower than his feet&nbsp;(<strong>head-down position</strong>)</li>\n<li><strong>Reverse Trendelenberg position: </strong>It is a position in which the patient is placed on a bed which is put into incline such that the patients feet are lower than his head (head-up position)</li>\n<li><strong>Brodie Trendelenburg test:&nbsp;</strong>It is a test for presence of incompetent valves in lower limb.</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(145, 'Anatomy', 'PM2396. Muscles causing the movements at Hip joint', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\517\>\n<tbody>\n<tr>\n<td><strong>Movements</strong></td>\n<td><strong>Principal muscles involved in the movements</strong></td>\n</tr>\n<tr>\n<td>Flexion</td>\n<td>\n<p>Psoas major</p>\n<p>Iliacus</p>\n<p>Rectus femoris</p>\n</td>\n</tr>\n<tr>\n<td>Extension</td>\n<td>\n<p>Gluteus maximus</p>\n<p>Biceps femoris</p>\n<p>Semitendinosus</p>\n<p>Semimembranosus (hamstrings)</p>\n<p>&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>Abduction</td>\n<td>\n<p>Gluteus medius</p>\n<p>Gluteus minimus</p>\n<p>Gluteus maximus (upper muscle fibres)</p>\n<p>Assisted by tensor fascia lata, piriformis and sartorius.</p>\n</td>\n</tr>\n<tr>\n<td>Adduction</td>\n<td>\n<p>Adductor longus</p>\n<p>Adductor brevis</p>\n<p>Adductor magnus</p>\n<p>Gracilis&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>Medial rotation</td>\n<td>\n<p>Gluteus medius</p>\n<p>Gluteus minimus</p>\n<p>Tensor Fascia Lata</p>\n<p>Adductor muscles.</p>\n</td>\n</tr>\n<tr>\n<td>Lateral rotation</td>\n<td>\n<p>Gluteus maximus</p>\n<p>Obturator internus</p>\n<p>Superior and inferior gemelli</p>\n<p>Quadratus femoris</p>\n<p>Piriformis</p>\n<p>Assisted by obturator externus and sartorius</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(146, 'Anatomy', 'PM2395. Comparative features of the medial and lateral longitudinal arches of the foot.', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\100%\>\n<tbody>\n<tr>\n<td width=\35%\>\n<p><strong>Features</strong></p>\n</td>\n<td width=\32%\>\n<p><strong>Medial Longitudinal arch</strong></p>\n</td>\n<td width=\32%\>\n<p><strong>Lateral longitudinal arch</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\35%\>\n<p>Anterior pillar</p>\n</td>\n<td width=\32%\>\n<p>Talus, navicular, 3 cuneiform and 1<sup>st</sup>, 2nd and 3<sup>rd</sup> metatarsal</p>\n</td>\n<td width=\32%\>\n<p>Cuboid and 4<sup>th</sup>, 5<sup>th</sup> metatarsals</p>\n</td>\n</tr>\n<tr>\n<td width=\35%\>\n<p>Posterior pillar</p>\n</td>\n<td width=\32%\>\n<p>Medial half of calcaneum</p>\n</td>\n<td width=\32%\>\n<p>Lateral half of calcaneum</p>\n</td>\n</tr>\n<tr>\n<td width=\35%\>\n<p>Main joint involved</p>\n</td>\n<td width=\32%\>\n<p>Talo calcaneonavicular joint (TCN)</p>\n</td>\n<td width=\32%\>\n<p>Calcaneocuboid joint</p>\n</td>\n</tr>\n<tr>\n<td width=\35%\>\n<p>Summit</p>\n</td>\n<td width=\32%\>\n<p>Superior articular surface of Talus</p>\n</td>\n<td width=\32%\>\n<p>Superior articular surface of Calcaneum (at the leveled Subtalar joint)</p>\n</td>\n</tr>\n<tr>\n<td width=\35%\>\n<p>Extremities (piers)</p>\n</td>\n<td width=\32%\>\n<p>Tuberosity of calcaneum posteriorly and Heads of 1<sup>st</sup>, 2<sup>nd</sup>, 3<sup>rd</sup> metatarsal anteriorly</p>\n</td>\n<td width=\32%\>\n<p>Plantar surface of calcaneum posteriorly and Heads of 4<sup>th</sup>, 5<sup>th</sup> metatarsals anteriorly</p>\n</td>\n</tr>\n<tr>\n<td width=\35%\>\n<p>Ligament/intersegmental ties</p>\n</td>\n<td width=\32%\>\n<p>Plantar calcaneonavicular ligament (<strong>spring ligament</strong>)</p>\n</td>\n<td width=\32%\>\n<p>Long and short plantar ligaments</p>\n</td>\n</tr>\n<tr>\n<td width=\35%\>\n<p>Tie beams</p>\n</td>\n<td width=\32%\>\n<p>Plantar aponeurosis (medial part)</p>\n<p>Abductor hallucis</p>\n<p>Medial part of&nbsp; flexor digitorum brevis</p>\n</td>\n<td width=\32%\>\n<p>Plantar aponeurosis (lateral part)</p>\n<p>Abductor digiti minimi</p>\n</td>\n</tr>\n<tr>\n<td width=\35%\>\n<p>Slings</p>\n</td>\n<td width=\32%\>\n<p>Tibialis anterior&nbsp;</p>\n<p>Tibialis posterior</p>\n<p>Flexor hallucis longus</p>\n<p>Flexor digitorum longus</p>\n<p>&nbsp;</p>\n</td>\n<td width=\32%\>\n<p>Peroneus longus</p>\n<p>Peroneus brevis</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(147, 'Anatomy', 'PM2354. Adductor Canal', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/84378bd2e4bf4e9db5353d045f805a19x600x694.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td colspan=\2\><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Boundaries of Adductor Canal</strong></td>\n</tr>\n<tr>\n<td><strong>Antero-lateral wall</strong></td>\n<td>Vastus medialis</td>\n</tr>\n<tr>\n<td><strong>Postero-medial wall (Floor)</strong></td>\n<td>\n<p>Adductor longus (above),</p>\n<p>Adductor magnus (below)</p>\n</td>\n</tr>\n<tr>\n<td><strong>Medial wall(Roof)</strong></td>\n<td>\n<p>Fibrous band connecting vastus medialis with adductor longus<strong>(Subsartorial fascia)</strong></p>\n<p>Sartorius</p>\n<p>Subsartorial nerve plexus</p>\n</td>\n</tr>\n<tr>\n<td><strong>Contents</strong></td>\n<td>\n<p>Femoral artery</p>\n<p>Femoral vein</p>\n<p>Saphenous nerve</p>\n<p>Nerve to vastus medialis</p>\n<p>Descending genicular artery&nbsp;</p>\n<p>Deep lymph vessels</p>\n<p>Two terminal divisions of the obturator nerve</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(148, 'Anatomy,', 'PM2284. Movements and muscles tested - lower limb', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<table>\n<tbody>\n<tr>\n<td colspan=\4\ width=\154\>\n<p><strong>Finding Location of a lesion in the lower limb:</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\154\>\n<p><strong>Movement</strong></p>\n</td>\n<td width=\182\>\n<p><strong>Muscle</strong></p>\n</td>\n<td width=\154\>\n<p><strong>Spinal nerve level</strong></p>\n</td>\n<td width=\154\>\n<p><strong>Nerve</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\154\>\n<p>Hip flexion</p>\n</td>\n<td width=\182\>\n<p>Iliopsoas</p>\n</td>\n<td width=\154\>\n<p>L1, 2</p>\n</td>\n<td width=\154\>\n<p>Femoral</p>\n</td>\n</tr>\n<tr>\n<td width=\154\>\n<p>Hip adduction</p>\n</td>\n<td width=\182\>\n<p>Adductors</p>\n</td>\n<td width=\154\>\n<p>L2, 3</p>\n</td>\n<td width=\154\>\n<p>Obturator</p>\n</td>\n</tr>\n<tr>\n<td width=\154\>\n<p>Hip extension</p>\n</td>\n<td width=\182\>\n<p>Gluteus maximus</p>\n</td>\n<td width=\154\>\n<p>L5, S1</p>\n</td>\n<td width=\154\>\n<p>Inferior gluteal</p>\n</td>\n</tr>\n<tr>\n<td width=\154\>\n<p>Knee flexion</p>\n</td>\n<td width=\182\>\n<p>Hamstrings</p>\n</td>\n<td width=\154\>\n<p>S1</p>\n</td>\n<td width=\154\>\n<p>Sciatic</p>\n</td>\n</tr>\n<tr>\n<td width=\154\>\n<p>Knee extension</p>\n</td>\n<td width=\182\>\n<p>Quadriceps femoris</p>\n</td>\n<td width=\154\>\n<p>L3, 4</p>\n</td>\n<td width=\154\>\n<p>Femoral</p>\n</td>\n</tr>\n<tr>\n<td width=\154\>\n<p>Ankle dorsiflexion</p>\n</td>\n<td width=\182\>\n<p>Tibialis anterior</p>\n</td>\n<td width=\154\>\n<p>L4</p>\n</td>\n<td width=\154\>\n<p>Deep fibular</p>\n</td>\n</tr>\n<tr>\n<td width=\154\>\n<p>Ankle eversion</p>\n</td>\n<td width=\182\>\n<p>Fibularis longus and fibularis brevis</p>\n</td>\n<td width=\154\>\n<p>L5, S1</p>\n</td>\n<td width=\154\>\n<p>Superficial fibular</p>\n</td>\n</tr>\n<tr>\n<td width=\154\>\n<p>Ankle inversion</p>\n</td>\n<td width=\182\>\n<p>Tibialis posterior</p>\n</td>\n<td width=\154\>\n<p>L4, 5</p>\n</td>\n<td width=\154\>\n<p>Tibial</p>\n</td>\n</tr>\n<tr>\n<td width=\154\>\n<p>Ankle plantar flexion</p>\n</td>\n<td width=\182\>\n<p>Gastrocnemius/soleus</p>\n</td>\n<td width=\154\>\n<p>S1, 2</p>\n</td>\n<td width=\154\>\n<p>Tibial</p>\n</td>\n</tr>\n<tr>\n<td width=\154\>\n<p>Great toe extension</p>\n</td>\n<td width=\182\>\n<p>Extensor hallucis longus</p>\n</td>\n<td width=\154\>\n<p>L5</p>\n</td>\n<td width=\154\>\n<p>Deep fibular</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(149, 'Anatomy', 'PM2283. Various muscles of the leg and their innervation:', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<table>\n<tbody>\n<tr>\n<td>\n<p><strong>Muscles of the anterior compartment of the leg and dorsum of foot</strong></p>\n</td>\n<td>\n<p><strong>Nerve Supply</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Tibialis anterior</p>\n</td>\n<td rowspan=\5\>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>Deep Peroneal Nerve</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Extensor hallucis longus</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Extensor digitorum longus</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Peroneus tertius</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Extensor digitorum brevis</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Muscles of the lateral component of the leg</strong></p>\n</td>\n<td>\n<p>&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Peroneus longus</p>\n</td>\n<td rowspan=\2\>\n<p>&nbsp;</p>\n<p>Superficial Peroneal Nerve</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Peroneus brevis</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Superficial muscles of the&nbsp;</strong><strong>posterior compartment of the leg</strong></p>\n</td>\n<td>\n<p>&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Gastrocnemius</p>\n</td>\n<td rowspan=\3\>\n<p>&nbsp;</p>\n<p>Tibial Nerve</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Soleus</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Plantaris</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Deep muscles of the posterior compartment of the leg</strong></p>\n</td>\n<td>\n<p>&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Popliteus</p>\n</td>\n<td rowspan=\4\>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>Tibial Nerve</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Flexor digitorum longus</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Flexor hallucis longus</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Tibialis posterior</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(150, 'Anatomy', 'PM1241. Femoral Triangle', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>The femoral triangle is a depressed, intermuscular space present in the anteromedial aspect of the proximal thigh.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/6c2eaf00759943729de1af4074dcc219x600x1189.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Boundaries of Femoral triangle are:</strong></p>\n<p><strong>Medially</strong>- Medial border of Adductor longus</p>\n<p><strong>Laterally</strong>- Medial border of Sartorius</p>\n<p><strong>Base</strong>- Inguinal ligament</p>\n<p><strong>Floor</strong> (lateral to medial)- Iliacus, Psoas major, Pectineus, Adductor longus</p>\n<p><strong>Roof</strong>- Skin, Superficial fascia, Fascia lata, superficial branches of femoral vessels–superficial epigastric, superficial external pudendal, superficial circumflex iliac vessels and GSV.&nbsp;</p>\n<p>Note: Femoral branch of genitofemoral nerve which forms a content of the triangle also forms its roof as it pierces the fascia to supply the skin over the triangle.&nbsp;</p>\n<p><strong>Contents:</strong></p>\n<ul>\n<li>Femoral sheath containing lymph node of Cloquet, <strong>femoral vein, femoral artery, femoral branch of genitofemoral nerve.</strong></li>\n<li>Femoral nerve and its branches</li>\n<li>Lateral femoral cutaneous nerve of thigh</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(151, 'Anatomy', 'PM1236. Lumbar Plexus', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Lumbar plexus</strong>&nbsp;is formed by the divisions of the first four lumbar nerves (<strong>L1-L4</strong>) and from contributions of the subcostal nerve (<strong>T12)</strong>, which is the last thoracic nerve.&nbsp;</p>\n<p>The following table shows the branches of the lumbar plexus, their root value, muscles innervated, and cutaneous branches:&nbsp;</p>\n<table border=\all\>\n<tbody>\n<tr>\n<td>\n<p><strong>Nerve</strong></p>\n</td>\n<td>\n<p><strong>Root value</strong></p>\n</td>\n<td>\n<p><strong>Innervated muscles</strong></p>\n</td>\n<td>\n<p><strong>Cutaneous branches</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Iliohypogastric</p>\n</td>\n<td>\n<p>T12-L1</p>\n</td>\n<td rowspan=\2\>\n<p>Transversus abdominis</p>\n<p>Abdominal internal oblique</p>\n</td>\n<td>\n<p>Anterior cutaneous ramus</p>\n<p>Lateral cutaneous ramus</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Ilioinguinal</p>\n</td>\n<td>\n<p>L1</p>\n</td>\n<td>\n<p>Anterior scrotal nerves in males</p>\n<p>Anterior labial nerves in females</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Genitofemoral</p>\n</td>\n<td>\n<p>L1, L2</p>\n</td>\n<td>\n<p>Cremaster in males</p>\n</td>\n<td>\n<p>Femoral ramus</p>\n<p>Genital ramus</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Lateral femoral cutaneous nerve of thigh</p>\n</td>\n<td>\n<p>L2, L3</p>\n</td>\n<td>\n<p>------</p>\n</td>\n<td>\n<p>Lateral femoral cutaneous</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Obturator</p>\n</td>\n<td>\n<p>L2, L3, L4</p>\n</td>\n<td>\n<p>Obturator externus</p>\n<p>Adductor longus</p>\n<p>Adductor brevis</p>\n<p>Gracilis</p>\n<p>Pectineus</p>\n<p>Adductor magnus</p>\n</td>\n<td>\n<p>Cutaneous ramus</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Femoral</p>\n</td>\n<td>\n<p>L2, L3, L4</p>\n</td>\n<td>\n<p>Iliacus</p>\n<p>Pectineus</p>\n<p>Sartorius</p>\n<p>Quadriceps femoris</p>\n</td>\n<td>\n<p>Anterior cutaneous branches</p>\n<p>Saphenous</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Segmental branches</p>\n</td>\n<td>\n<p>T12-L4</p>\n</td>\n<td>\n<p>Psoas major</p>\n<p>Quadratus lumborum</p>\n</td>\n<td>\n<p>&nbsp;</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/da0be4b4cd4a4ebfa93c36cb93f4e8edx600x824.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(152, 'Anatomy,', 'PM1050. Anatomy of Femoral Canal', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/fe0e0019edd541f18ba6f16ab11eec83x1280x3069.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(153, 'Anatomy,', 'PM0127. Unhappy triad of knee joint', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>It is also called <strong>ODonoghues triad</strong>/ terrible triad/ horrible triad of the knee. It mostly occurs in football, basketball &amp; rugby players,&nbsp;due to a lateral blow to the knee.</p>\n<p>It is characterized by injury to:&nbsp;</p>\n<ul>\n<li><strong>M</strong>edial meniscus</li>\n<li><strong>A</strong>nterior cruciate ligament</li>\n<li><strong>T</strong>ibial collateral ligament</li>\n</ul>\n<p>(Mnemonic – MAT)</p>\n<p>The image below shows the unhappy triad of the knee joint on MRI (PDW SPIR):</p>\n<ol>\n<li>Rupture of the anterior cruciate ligament</li>\n<li>Rupture of the medial collateral ligament</li>\n<li>Inner meniscal tear (image shows complex rupture of the posterior horn).</li>\n</ol></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/a4c2d7a49f734c69a162b45753aaf44ax800x388.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(154, 'Medicine,', 'PM1642. Spinal Cord Segments and Corresponding Vertebral Bodies.', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table class=\t1\ cellspacing=\0\ cellpadding=\0\>\n<tbody>\n<tr>\n<td class=\td1\ valign=\top\>\n<p class=\p1\><strong>Spinal Cord Level</strong></p>\n</td>\n<td class=\td1\ valign=\top\>\n<p class=\p1\><strong>Corresponding Vertebral Body</strong></p>\n</td>\n</tr>\n<tr>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>Upper cervical</p>\n</td>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>Same as cord level</p>\n</td>\n</tr>\n<tr>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>Lower cervical</p>\n</td>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>1 level higher</p>\n</td>\n</tr>\n<tr>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>Upper thoracic</p>\n</td>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>2 levels higher</p>\n</td>\n</tr>\n<tr>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>Lower thoracic</p>\n</td>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>2 to 3 levels higher</p>\n</td>\n</tr>\n<tr>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>Lumbar</p>\n</td>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>T10-T12</p>\n</td>\n</tr>\n<tr>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>Sacral</p>\n</td>\n<td class=\td1\ valign=\top\>\n<p class=\p1\>T12-L1</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(155, 'Anatomy', 'PM1160. Vertebral Spines as Landmarks for the Viscera', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Spine/space </strong></p>\n</td>\n<td>\n<p><strong>Vertebral body level </strong></p>\n</td>\n<td>\n<p><strong>Viscera </strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>C5</p>\n</td>\n<td>\n<p>C6</p>\n</td>\n<td>\n<p>Cricoid cartilage</p>\n<p>Start of esophagus</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>C7</p>\n</td>\n<td>\n<p>C7-T1</p>\n</td>\n<td>\n<p>Lung apex</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>T3</p>\n</td>\n<td>\n<p>Upper T4</p>\n</td>\n<td>\n<p>Aorta reaches vertebral column,</p>\n<p>Medial part of the scapular spine</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>T3-T4 space</p>\n</td>\n<td>\n<p>T4-T5 disc</p>\n</td>\n<td>\n<p>Sternal plane/angle of Louis</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>T4</p>\n</td>\n<td>\n<p>Upper T5</p>\n</td>\n<td>\n<p>Concavity of aortic arch</p>\n<p>Azygos vein into SVC</p>\n<p>Bifurcation of pulmonary trunk</p>\n<p>Upper border of heart</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>T6</p>\n</td>\n<td>\n<p>T7</p>\n</td>\n<td>\n<p>Tracheal bifurcation (The level of bifurcation of the trachea varies from the lower third of the T4 to the middle of the T7.&nbsp;The average position is at the level of the lower third of the T5 )&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>T7</p>\n</td>\n<td>\n<p>Upper T8</p>\n</td>\n<td>\n<p>Inferior angle of scapula</p>\n<p>Lower border of heart</p>\n<p>IVC crosses diaphragm</p>\n<p>Central tendon of diaphragm</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>T12-L1 space</p>\n</td>\n<td>\n<p>Mid L1</p>\n</td>\n<td>\n<p>Lowest level of pleura</p>\n<p>Pylorus, transpyloric plane</p>\n<p>Hilum of left kidney</p>\n<p>Origin of renal arteries and superior mesenteric artery</p>\n<p>Neck of pancreas</p>\n<p>Spinal cord termination&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>L1-L2 space</p>\n</td>\n<td>\n<p>Mid L2</p>\n</td>\n<td>\n<p>Head of pancreas</p>\n<p>Duodenojejunal flexure</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>L2-L3 space</p>\n</td>\n<td>\n<p>Mid L3</p>\n</td>\n<td>\n<p>Lower border of kidney&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>L3-L4 space</p>\n</td>\n<td>\n<p>Mid L4</p>\n</td>\n<td>\n<p>Bifurcation of aorta</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>L4-L5 space</p>\n</td>\n<td>\n<p>Mid L5</p>\n</td>\n<td>\n<p>Formation of IVC</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(156, 'Anatomy', 'PM2389. General rules of ossification', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>1. Primary ossification center is single and appears before birth.&nbsp;</p>\n<p><strong>Exceptions:&nbsp;</strong>Carpal bones&nbsp;ossify after birth. Clavicle&nbsp;has 2 primary ossification centers.</p>\n<p>2. Secondary ossification centers can be single or multiple and appear after birth.</p>\n<p><strong>Exceptions:</strong> Secondary center for lower end of femur appears before birth (9<sup>th</sup> month of fetal life). Epiphysis of the upper end of tibia (proximal epiphysis) may appear before birth in some individuals.</p>\n<p>3. Secondary centers of ossification which appear first are last to unite.</p>\n<p><strong>Exceptions:</strong> Lower end of fibula. The secondary center at the lower end of fibula appears during the first year (before the upper end which appears in 3<sup>rd</sup> or 4<sup>th</sup> year) but fuses between 15<sup>th</sup> and 17<sup>th</sup> year, before the upper end that fuses between the 17<sup>th</sup> and 19<sup>th</sup> year.</p>\n<p>4. The secondary ossification centers fuse together to form a single epiphysis, which then fuses with the diaphysis.</p>\n<p><strong>Exceptions:</strong>&nbsp;Upper end of femur. The three secondary centers at the upper end of femur (head - 1<sup>st</sup> year, greater trochanter- 4<sup>th</sup> year, lesser trochanter- 12<sup>th</sup> year) fuse independently with the shaft.&nbsp;</p>\n<p>5. The direction of the nutrient artery is opposite to the direction of the growing end of the bone. &nbsp; &nbsp; &nbsp; &nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(157, 'Anatomy', 'PM2388. Types of Synovial Joints', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td colspan=\4\><strong>Classification of Synovial Joints</strong></td>\n</tr>\n<tr>\n<td><strong>Type</strong></td>\n<td><strong>Articular surfaces</strong></td>\n<td><strong>Movement</strong></td>\n<td><strong>Examples</strong></td>\n</tr>\n<tr>\n<td><strong>1. Plane</strong></td>\n<td>Almost flat</td>\n<td>Minimal sliding movements</td>\n<td>Intercarpal, intermetacarpal, carpometacarpal (except first), intertarsal, intermetatarsal, tarsometatarsal, joints between articular processes of vertebrae, cricothyroid joint, cricoarytenoid joint, superior tibiofibular, interchondral joint (5-9 ribs), costovertebral, costotransverse, acromioclavicular, chondrosternal (except first) joints</td>\n</tr>\n<tr>\n<td><strong>2. Hinge</strong></td>\n<td>Pulley shaped</td>\n<td>Uniaxial - flexion &amp; extension</td>\n<td>Elbow, ankle, interphalangeal joints</td>\n</tr>\n<tr>\n<td><strong>3. Pivot</strong></td>\n<td>Central pivot (peg) surrounded by osteoligamentous ring</td>\n<td>Uniaxial - rotation (supination and pronation)</td>\n<td>Superior and inferior radioulnar joints, median atlantoaxial joint</td>\n</tr>\n<tr>\n<td><strong>4. Condylar (bicondylar)</strong></td>\n<td>2 convex condyles articulate with concave or flat surfaces on the other side</td>\n<td>Biaxial - but predominantly one axis movement (flexion &amp; extension) with limited rotation</td>\n<td>Knee joint, temporomandibular joint</td>\n</tr>\n<tr>\n<td><strong>5. Ellipsoid</strong></td>\n<td>Oval convex surface fitting into an elliptical concavity</td>\n<td>Biaxial - flexion &amp; extension, abduction &amp; adduction, circumduction</td>\n<td>Radiocarpal (wrist), metacarpophalangeal joints</td>\n</tr>\n<tr>\n<td><strong>6. Saddle</strong></td>\n<td>Each articular surface has a concave and convex region fitting the convex and concave regions of the other surface</td>\n<td>Biaxial - mainly 2 axis, flexion &amp; extension,&nbsp; abduction &amp; adduction; very limited coupled axial rotation (conjunct rotation)&nbsp;</td>\n<td>First carpometacarpal joint (of thumb), calcaneocuboid joints, incudomalleolar joint, sternoclavicular joint</td>\n</tr>\n<tr>\n<td><strong>7. Ball &amp; Socket</strong></td>\n<td>Globular head fitting into a cup-shaped socket</td>\n<td>Multiaxial - flexion &amp; extension, abduction &amp; adduction, circumduction, and rotation</td>\n<td>Shoulder, hip, talocalcaneonavicular, incudostapedial joint</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(158, 'Anatomy', 'PM2313. Types of Cartilage', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Type of Cartilage</strong></td>\n<td><strong>Hyaline Cartilage</strong></td>\n<td><strong>Elastic Cartilage</strong></td>\n<td><strong>Fibrocartilage</strong></td>\n</tr>\n<tr>\n<td><strong>Colour</strong></td>\n<td><span>Bluish opalescent</span></td>\n<td><span>Yellow</span></td>\n<td><span>White</span></td>\n</tr>\n<tr>\n<td><strong>Collagen type</strong></td>\n<td>Type 2</td>\n<td>Type 2</td>\n<td>Type 1 (mainly) and type 2</td>\n</tr>\n<tr>\n<td><strong>Perichondrium</strong></td>\n<td>Present (except at epiphyses &amp; articular cartilage)</td>\n<td>Present</td>\n<td>Absent</td>\n</tr>\n<tr>\n<td><strong>Calcification</strong></td>\n<td>Occurs with old age</td>\n<td>No calcification</td>\n<td>Occurs only with bone repair</td>\n</tr>\n<tr>\n<td><strong>Cells</strong></td>\n<td>Chondroblasts &amp; chondrocytes</td>\n<td>Chondroblasts &amp; chondrocytes</td>\n<td>Fibroblasts &amp; chondrocytes</td>\n</tr>\n<tr>\n<td><strong>Fibres</strong></td>\n<td>Thin collagen</td>\n<td>Elastic fibres</td>\n<td>Thick collagen</td>\n</tr>\n<tr>\n<td><strong>Examples</strong></td>\n<td>\n<p>Arytenoids except for apices</p>\n<p>Cricoid cartilage</p>\n<p>Thyroid cartilage</p>\n<p>Trachea and bronchi</p>\n<p>Articular cartilage at ends of bones</p>\n<p>Epiphyseal plate</p>\n<p>Costal cartilages of ribs</p>\n<p>Temporary skeleton in embryo</p>\n</td>\n<td>\n<p>Apices of arytenoid</p>\n<p>Epiglottis</p>\n<p>Corniculate cartilages</p>\n<p>Cuneiform cartilages</p>\n<p>Eustachian tube</p>\n<p>Pinna(Auricle)</p>\n<p>External auditory meatus&nbsp;</p>\n</td>\n<td>\n<p>Articular discs e.g. of Temporomandibular joint and Sternoclavicular joint</p>\n<p>Intervertebral discs</p>\n<p>Menisci</p>\n<p>Glenoid and acetabular labrum&nbsp;&nbsp;&nbsp;</p>\n<p>Pubic symphysis</p>\n<p>Manubriosternal joint</p>\n<p>Lining of the bony grooves for tendons</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/c35a8db73d4b4edc82a707f81f3dcfdfx1280x3831.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(159, 'Physiology,', 'PM1973. Classification of muscles according to their shape/direction of muscle fibers', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table class=\t1\ border=\1\ cellspacing=\0\ cellpadding=\0\>\n<tbody>\n<tr>\n<td class=\td1\ valign=\top\>\n<p class=\p1\><strong>Muscles with parallel fasciculi</strong></p>\n</td>\n<td class=\td1\ valign=\top\>\n<p class=\p1\><strong>Muscles with oblique fasciculi</strong></p>\n</td>\n<td class=\td1\ valign=\top\>\n<p class=\p1\><strong>Muscles with spiral or twisted fasciculi</strong></p>\n<p class=\p2\><strong>&nbsp;</strong></p>\n</td>\n</tr>\n<tr>\n<td class=\td1\ valign=\top\>\n<p class=\p1\><strong>Quadrilateral:</strong> Thyrohyoid</p>\n</td>\n<td class=\td1\ valign=\top\>\n<p class=\p1\><strong>Triangular:&nbsp;</strong>Temporalis, Adductor longus,&nbsp;</p>\n</td>\n<td class=\td1\ valign=\top\>\n<p class=\p1\><strong>Spiral:</strong> Pectoralis major, Latissimus dorsi, Supinator and&nbsp;Trapezius</p>\n</td>\n</tr>\n<tr>\n<td class=\td1\ valign=\top\>\n<p class=\p1\><strong>Strap like:</strong> Sartorius, Sternohyoid</p>\n</td>\n<td class=\td1\ valign=\top\>\n<p class=\p1\><strong>Unipennate:&nbsp;</strong>Extensor digitorum longus, Flexor pollicis longus, Peroneus tertius, Palmar interossei</p>\n</td>\n<td class=\td1\ valign=\top\>\n<p class=\p1\><strong>Partially cruciate and partially spiral:</strong> Masseter, Adductor magnus, Sternoclediomastoid&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td class=\td1\ valign=\top\>\n<p class=\p1\><strong>Strap like with tendinous intersections:</strong> Rectus abdominis</p>\n</td>\n<td class=\td1\ valign=\top\>\n<p class=\p1\><strong>Bipennate:</strong> Rectus femoris, Flexor hallucis longus, Peroneus longus, Dorsal interossei</p>\n</td>\n<td class=\td1\ valign=\top\>\n<p class=\p2\>&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td class=\td1\ valign=\top\>\n<p class=\p1\><strong>Fusiform:</strong> Biceps brachii, digastric</p>\n</td>\n<td class=\td1\ valign=\top\>\n<p class=\p1\><strong>Multipennate: </strong>Deltoid, Subscapularis</p>\n</td>\n<td class=\td1\ valign=\top\>&nbsp;</td>\n</tr>\n<tr>\n<td class=\td1\ valign=\top\>\n<p class=\p2\>&nbsp;</p>\n</td>\n<td class=\td1\ valign=\top\>\n<p class=\p1\><strong>Circumpennate:</strong> Tibialis anterior</p>\n</td>\n<td class=\td1\ valign=\top\>&nbsp;</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(160, 'Anatomy', 'PM0708. Comparison of Lymph and Blood Capillaries', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\308\>\n<p><strong>Lymph capillaries</strong></p>\n</td>\n<td width=\308\>\n<p><strong>Blood capillaries</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Colourless, difficult to observe</p>\n</td>\n<td width=\308\>\n<p>Reddish, easy to observe</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Blind (closed at the tip)</p>\n</td>\n<td width=\308\>\n<p>Joined to arterioles at one end and to venules at another end</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Wider than blood capillaries</p>\n</td>\n<td width=\308\>\n<p>Narrower than lymph capillaries</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Wall consist of thin endothelium and poorly developed basement membrane</p>\n</td>\n<td width=\308\>\n<p>Wall consist of normal endothelium and basement membrane</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Contain colourless lymph</p>\n</td>\n<td width=\308\>\n<p>Contain red blood</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Have relatively low pressure</p>\n</td>\n<td width=\308\>\n<p>Have relatively high pressure</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Absorb tissue fluid from intercellular spaces</p>\n</td>\n<td width=\308\>\n<p>Add tissue fluid to intercellular spaces</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->');
