(190, 'Biochemistry', 'PM0030. Sites of various pathways in cell', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Sites of metabolism</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Mitochondria</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Fatty acid oxidation (βoxidation)</p>\n<p>Acetyl CoA production</p>\n<p>TCA Cycle</p>\n<p>Oxidative phosphorylation</p>\n<p>Ketogenesis</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Cytoplasm</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Glycolysis</p>\n<p>HMP Shunt</p>\n<p>Synthesis of steroids(SER)</p>\n<p>Proteins (RER and Ribosomes)</p>\n<p>Fatty acids</p>\n<p>Cholesterol</p>\n<p>Nucleotides</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Both</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Heme synthesis</p>\n<p>Urea cycle</p>\n<p>Gluconeogenesis</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(191, 'Biochemistry', 'PM0002. Glucose transporters and their locations', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Tissue location</strong></td>\n<td><strong>Function</strong></td>\n</tr>\n<tr>\n<td><strong>GLUT 1</strong></td>\n</tr>\n<tr>\n<td><strong>Brain, RBC, Placenta</strong>, kidney, colon</td>\n<td>Basal glucose uptake</td>\n</tr>\n<tr>\n<td><strong>GLUT 2</strong></td>\n</tr>\n<tr>\n<td><strong>Liver</strong>, pancreatic beta cell, small intestine, kidney</td>\n<td>Rapid uptake/release of glucose. Low affinity and high km</td>\n</tr>\n<tr>\n<td><strong>GLUT 3</strong></td>\n</tr>\n<tr>\n<td><strong>Neurons</strong>, kidney, placenta</td>\n<td>High affinity for glucose</td>\n</tr>\n<tr>\n<td><strong>GLUT 4</strong></td>\n</tr>\n<tr>\n<td>Heart, skeletal muscle, adipose tissue</td>\n<td>Insulin-dependent glucose uptake</td>\n</tr>\n<tr>\n<td><strong>GLUT 5</strong></td>\n</tr>\n<tr>\n<td>Small intestine, testis, sperm</td>\n<td><strong>Fructose transporter</strong></td>\n</tr>\n<tr>\n<td><strong>GLUT 8</strong></td>\n</tr>\n<tr>\n<td><strong>Blastocyst</strong>, testis, brain</td>\n<td>Insulin-dependent glucose uptake in blastocyst. Transports glucose to Mature spermatozoa.</td>\n</tr>\n<tr>\n<td><strong>GLUT 9</strong></td>\n</tr>\n<tr>\n<td>Liver, kidney</td>\n<td><strong>Urate transporter</strong></td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(192, 'Biochemistry,', 'PM2322. Carcinoid syndrome', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/db331361dc434bed8535d417b2cfcb4fx1280x1845.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(193, 'Biochemistry,', 'PM2310. Maple syrup urine disease(MSUD)', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/431a47e4c13446e19249e1e6d5459e66x1280x1324.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(194, 'Medicine,', 'PM2307. Menkes disease', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/3f0c58fe358940b393c96afe12ef3455x1280x1951.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(195, 'Biochemistry', 'PM2306. Synthesis of collagen and its disorders', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\291\>\n<tbody>\n<tr>\n<td>1. Collagen backbone</td>\n<td>Preprocollagen - Glycin-proline-lysine</td>\n</tr>\n<tr>\n<td>2. Hydroxylation</td>\n<td>\n<p>Of proline and lysine in the presence of Vit C.</p>\n<p>Deficiency of Vit C causes <strong>Scurvy.</strong></p>\n</td>\n</tr>\n<tr>\n<td>3. Glycosylation</td>\n<td>Formation of procollagen and resulting triple helix</td>\n</tr>\n<tr>\n<td>4. Cleavage of terminals of procollagen</td>\n<td>Resulting in the formation of tropocollagen</td>\n</tr>\n<tr>\n<td>5. Cross-linking by lysyl oxidase (copper-containing enzyme)</td>\n<td>\n<p>Formation of mature collagen.</p>\n<p>Defect causes <strong>Menkes disease</strong> and <strong>Ehlers-Danlos syndrome</strong>)</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(196, 'Paediatrics,', 'PM2305. Phenylketonuria', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/1a79bc9a0618454db8f2bfa6916ae3bbx1280x1001.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ol>\n<li>BH<sub>4</sub> - tetrahydrobiopterin (or sapropterin, a synthetic form of BH<sub>4</sub>)</li>\n</ol></div><!---->\n      <!---->\n    </div><!---->'),
(197, 'Paediatrics,', 'PM2304. Homocystinuria', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/c84307c25b08404ebad0ed6c97f95b35x1280x1169.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>The concept for treatment:</p>\n<ul>\n<li>Since homocysteine is produced from methionine, decreased dietary intake of methionine decreases the formation of homocysteine.</li>\n<li>As the end product of this cycle is cysteine, increased dietary intake of cysteine will decrease the conversion of methionine to homocysteine through negative feedback.</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(198, 'Biochemistry', 'PM2196. Amino Acid Derivatives', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/7b53b3afcc1c47e792f684ad0097deeex1280x1310.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(199, 'Biochemistry', 'PM1791. Types of Chromatography', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Chromatography types</strong></td>\n<td><strong>Property used for separation</strong></td>\n</tr>\n<tr>\n<td>Paper chromatography</td>\n<td>Based on <strong>polarity</strong>, <strong>Least polar</strong> moves <strong>faster</strong></td>\n</tr>\n<tr>\n<td>Thin layer chromatography</td>\n<td>Based on <strong>polarity</strong>, <strong>Least polar</strong> moves <strong>faster</strong></td>\n</tr>\n<tr>\n<td>Ion exchange chromatography</td>\n<td>Based on <strong>charge – charge</strong> interaction</td>\n</tr>\n<tr>\n<td>Size exclusion chromatography</td>\n<td>Based on <strong>descending order</strong> of <strong>Stokes radius</strong> which is a function of <strong>molecular mass and shape</strong></td>\n</tr>\n<tr>\n<td>Affinity chromatography</td>\n<td>Based on <strong>specific ligand binding</strong> behaviour</td>\n</tr>\n<tr>\n<td>Hydrophobic interaction chromatography</td>\n<td>Based on <strong>hydrophobic </strong>interaction</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(200, 'Biochemistry', 'PM1402. Types of flavoprotein', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Flavoprotein oxidases</strong></td>\n<td><strong>Location</strong></td>\n<td><strong>Action</strong></td>\n</tr>\n<tr>\n<td>L-amino acid oxidase</td>\n<td>Kidney</td>\n<td>Specificity for the oxidative deamination of the naturally occurring l-amino acids</td>\n</tr>\n<tr>\n<td>Xanthine oxidase</td>\n<td>Uricotelic animals</td>\n<td>Conversion of purine bases to uric acid</td>\n</tr>\n<tr>\n<td>Aldehyde dehydrogenase</td>\n<td>Mammalianlivers</td>\n<td>Acts upon aldehydes and <em>N</em>-heterocyclic substrates&nbsp;</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(201, 'Biochemistry', 'PM1401. Structure of proteins', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/0fd214b3bb95411197397c70b51294d9x1280x2196.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(202, 'Paediatrics,', 'PM0627. Inborn Errors of Amino Acid Metabolism and Their Associated Urine Odor', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td>\n<p><strong>Inborn Error of Metabolism</strong></p>\n</td>\n<td>\n<p><strong>Urine Odor</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Maple syrup urine disease</p>\n</td>\n<td>\n<p>Maple syrup</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Hawkinsinuria</p>\n</td>\n<td>\n<p>Swimming pool</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>3-Hydroxy-3-methylglutaric aciduria</p>\n</td>\n<td>\n<p>Cat urine</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Isovaleric acidemia</p>\n<p>Glutaric acidemia (type II)</p>\n</td>\n<td>\n<p>Sweaty feet</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Hypermethioninemia</p>\n<p>Tyrosinemia</p>\n</td>\n<td>\n<p>Boiled cabbage</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Multiple carboxylase deficiency</p>\n</td>\n<td>\n<p>Tomcat urine</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Phenylketonuria</p>\n</td>\n<td>\n<p>Mousy</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Trimethylaminuria</p>\n</td>\n<td>\n<p>Rotting fish</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Oasthouse syndrome</p>\n</td>\n<td>\n<p>Dried malt/hops</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(203, 'Paediatrics,', 'PM0625. Amino acid Enzymatic Disorders and the Associated Enzymes', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Disorder</strong></p>\n</td>\n<td>\n<p><strong>Enzyme defect</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Phenylketonuria</p>\n</td>\n<td>\n<p>Phenylalanine hydroxylase</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Tyrosinosis (Tyrosinemia type I)</p>\n</td>\n<td>\n<p>Maleyl Acetate isomerase or Fumarylacetoacetate hydrolase</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Maple syrup urine disease&nbsp;</p>\n</td>\n<td>\n<p>Branched-chain alpha-keto acid dehydrogenase(BCKAD)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Alkaptonuria</p>\n</td>\n<td>\n<p>Homogentisate oxidase</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Albinism</p>\n</td>\n<td>\n<p>Tyrosinase</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Homocystinuria type I</p>\n</td>\n<td>\n<p>Cystathionine synthase</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Homocystinuria type II</p>\n</td>\n<td>\n<p>Defect in methylcobalamin formation</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Cystathioninuria</p>\n</td>\n<td>\n<p>Cystathionase</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Tyrosinemia type II or Richner-Hanhart syndrome</p>\n</td>\n<td>\n<p>Tyrosine transaminase or Tyrosine aminotransferase</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Primary hyperoxaluria</p>\n</td>\n<td>\n<p>Alanine glyoxalate aminotransferase</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Hypervalinemia</p>\n</td>\n<td>\n<p>Valine transaminase</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Isovaleric acidemia</p>\n</td>\n<td>\n<p>Isovaleryl CoA dehydrogenase</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Histidinemia</p>\n</td>\n<td>\n<p>Histidase</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Hyperprolinemia type I</p>\n</td>\n<td>\n<p>Proline oxidase</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(204, 'Paediatrics,', 'PM0590. Types of tyrosinemia  with deficient enzymes', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<div><strong>Disease</strong></div>\n</td>\n<td>\n<div><strong>Enzyme-deficient</strong></div>\n</td>\n</tr>\n<tr>\n<td>\n<div>Tyrosinemia type 1 (Hepatorenal, Hereditary tyrosinemia, Tyrosinosis)</div>\n</td>\n<td>\n<div>Fumarylacetoacetate hydrolase (FAAH)</div>\n</td>\n</tr>\n<tr>\n<td>\n<div>Tyrosinemia type 2 (Oculocutaneous)</div>\n</td>\n<td>\n<div>Tyrosine aminotransferase</div>\n</td>\n</tr>\n<tr>\n<td>\n<div>Tyrosinemia type 3 (Neonatal tyrosinemia)</div>\n</td>\n<td>\n<div>4-Hydroxyphenylpyruvate dioxygenase (4-HPPD)</div>\n</td>\n</tr>\n<tr>\n<td>\n<div>Hawkinsinuria</div>\n</td>\n<td>\n<div>Missense mutations in the gene for 4-HPPD</div>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(205, 'Biochemistry', 'PM0579. Amino Acids and their Biological Amines', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Amino Acid</strong></p>\n</td>\n<td>\n<p><strong>Biogenic Amines &amp; their Derivatives</strong></p>\n</td>\n<td>\n<p><strong>Significance</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Histidine</p>\n</td>\n<td>\n<p>Histamine</p>\n</td>\n<td>\n<p>Inflammatory mediator, vasodilator, bronchoconstrictor</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Tyrosine</p>\n</td>\n<td>\n<p>Tyramine</p>\n<p>Norepinephrine</p>\n<p>Epinephrine</p>\n<p>Dopamine</p>\n<p>Melanin</p>\n</td>\n<td>\n<p>Regulation of blood pressure</p>\n<p>Neurotransmitter in the sympathetic system</p>\n<p>Involved in fight and flight responses</p>\n<p>Pigmentation of skin</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Tryptophan</p>\n</td>\n<td>\n<p>Serotonin</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>Melatonin</p>\n</td>\n<td>\n<p>Neurotransmitter in the CNS</p>\n<p>Regulation of mood, temperature, blood pressure, GI motility</p>\n<p>Sleep-wake cycle</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Lysine</p>\n</td>\n<td>\n<p>Cadaverine</p>\n</td>\n<td>\n<p>Partially responsible for the distinctive smell of urine and semen</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Glutamic acid</p>\n</td>\n<td>\n<p>GABA</p>\n</td>\n<td>\n<p>Inhibitory neurotransmitter in the CNS</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Serine</p>\n</td>\n<td>\n<p>Ethanolamine</p>\n<p>&nbsp;</p>\n<p>Choline</p>\n</td>\n<td>\n<p>A component of the cell membrane phospholipids (cephalin)</p>\n<p>Synthesis of acetylcholine, component of surfactant</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p>Note<strong>: Tyramine&nbsp;</strong>is an indirectly acting sympathomimetic and is known to cause <strong>cheese reaction</strong>, a type of hypertensive crisis when taken along with certain drugs such as MAO inhibitors.</p></div><!---->\n   