(210, 'Biochemistry', 'PM0572. Reactions or Tests to Identify Specific Groups or Amino Acids', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\231\>\n<p><strong>Reactions</strong></p>\n</td>\n<td width=\231\>\n<p><strong>Specific Group or Amino Acid</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Biuret Reaction</p>\n</td>\n<td width=\231\>\n<p>Two peptide linkages</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Ninhydrin Reaction</p>\n</td>\n<td width=\231\>\n<p>Alpha-amino acids</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Xanthoproteic Reaction</p>\n</td>\n<td width=\231\>\n<p>Benzene ring of aromatic amino acids</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Millons Reaction</p>\n</td>\n<td width=\231\>\n<p>Phenolic group (Tyrosine)</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Hopkins-Cole Reaction</p>\n</td>\n<td width=\231\>\n<p>Indole ring (Tryptophan)</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Sakaguchi Reaction</p>\n</td>\n<td width=\231\>\n<p>Guanidine group (Arginine)</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Nitroprusside Reaction</p>\n</td>\n<td width=\231\>\n<p>Sulfhydryl groups (Cysteine)</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Sulfur Test</p>\n</td>\n<td width=\231\>\n<p>Sulfhydryl groups (Cysteine)</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Pauly’s Test</p>\n</td>\n<td width=\231\>\n<p>Imidazole ring (Histidine)</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Folin-Ciocalteu’s Test</p>\n</td>\n<td width=\231\>\n<p>Phenolic group (Tyrosine)</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(211, 'Biochemistry', 'PM0571. Amino Acids and their Special Groups', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\135\>\n<p><strong>Amino Acid</strong></p>\n</td>\n<td width=\157\>\n<p><strong>Special Groups</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\135\>\n<p>Arginine</p>\n</td>\n<td width=\157\>\n<p>Guanidinium</p>\n</td>\n</tr>\n<tr>\n<td width=\135\>\n<p>Phenylalanine</p>\n</td>\n<td width=\157\>\n<p>Benzene</p>\n</td>\n</tr>\n<tr>\n<td width=\135\>\n<p>Tyrosine</p>\n</td>\n<td width=\157\>\n<p>Phenol</p>\n</td>\n</tr>\n<tr>\n<td width=\135\>\n<p>Histidine</p>\n</td>\n<td width=\157\>\n<p>Imidazole</p>\n</td>\n</tr>\n<tr>\n<td width=\135\>\n<p>Proline</p>\n</td>\n<td width=\157\>\n<p>Pyrrolidine</p>\n</td>\n</tr>\n<tr>\n<td width=\135\>\n<p>Methionine</p>\n</td>\n<td width=\157\>\n<p>Thioether Linkage</p>\n</td>\n</tr>\n<tr>\n<td width=\135\>\n<p>Tryptophan</p>\n</td>\n<td width=\157\>\n<p>Indole</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(212, 'Biochemistry', 'PM0570. Classification of amino acids  on the basis of their polarity and nature (Acidic/Basic)', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<table>\n<tbody>\n<tr>\n<td>\n<p><strong>Polar/Hydrophilic</strong></p>\n</td>\n<td>\n<p><strong>Non-Polar/Hydrophobic</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<ol>\n<li>Serine</li>\n<li>Threonine*</li>\n<li>Asparagine</li>\n<li>Glutamine</li>\n<li>Aspartic acid</li>\n<li>Glutamic acid</li>\n<li>Lysine*</li>\n<li>Arginine</li>\n<li>Histidine*</li>\n<li>Cysteine&nbsp;</li>\n<li>Glycine</li>\n</ol>\n</td>\n<td>\n<ol>\n<li>Alanine</li>\n<li>Valine*</li>\n<li>Leucine*</li>\n<li>Isoleucine*</li>\n<li>Phenylalanine*</li>\n<li>Tryptophan*</li>\n<li>Methionine*</li>\n<li>Proline</li>\n<li>Tyrosine</li>\n</ol>\n<p>&nbsp;</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table>\n<tbody>\n<tr>\n<td>\n<p><strong>Acidic</strong></p>\n</td>\n<td>\n<p><strong>Basic</strong></p>\n</td>\n</tr>\n<tr>\n<td>&nbsp;\n<ol>\n<li>Aspartic acid (<strong>most acidic)</strong></li>\n<li>Glutamic acid</li>\n</ol>\n</td>\n<td>\n<ol>\n<li>Lysine*</li>\n<li>Arginine (<strong>most basic)</strong></li>\n<li>Histidine</li>\n</ol>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;* Denotes one of the nine essential amino acids.&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(213, 'Biochemistry', 'PM0569. Amino Acid Classification Based on Metabolism (Glucogenic/Ketogenic ) and Nutritional requirement (Essential/Non essential)', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td>&nbsp;</td>\n<td>\n<p><strong>Glucogenic</strong></p>\n</td>\n<td>\n<p><strong>Ketogenic</strong></p>\n</td>\n<td>\n<p><strong>Both</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Non-essential</p>\n</td>\n<td>\n<p>Alanine</p>\n<p>Arginine (semi-essential)</p>\n<p>Asparagine</p>\n<p>Aspartate</p>\n<p>Cysteine</p>\n<p>Glutamate</p>\n<p>Glutamine</p>\n<p>Glycine</p>\n<p>Proline</p>\n<p>Serine</p>\n</td>\n<td>&nbsp;</td>\n<td>Tyrosine</td>\n</tr>\n<tr>\n<td>\n<p>Essential</p>\n</td>\n<td>\n<p>Histidine</p>\n<p>Methionine</p>\n<p>Valine</p>\n<p>Threonine</p>\n</td>\n<td>\n<p>Leucine</p>\n<p>Lysine</p>\n</td>\n<td>\n<p>Isoleucine</p>\n<p>Phenylalanine</p>\n<p>Tryptophan</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(214, 'Biochemistry', 'PM2219. Cholesterol synthesis', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/df36006c4ce64fe88295b170ef95d661x600x910.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(215, 'Medicine,', 'PM2169. Familial Dyslipidemias', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>Type</td>\n<td>Increased levels&nbsp;</td>\n<td>Manifestation</td>\n</tr>\n<tr>\n<td>1. Hyperchylomicronemia(type I hyperlipoproteinemia)</td>\n<td>Chylomicrons, VLDL, TG&nbsp;</td>\n<td>Pancreatitis, hepatosplenomegaly, and xanthomas</td>\n</tr>\n<tr>\n<td>2.&nbsp;Familial hypercholesterolemia(type II&nbsp;hyperlipoproteinemia)</td>\n<td>LDL and cholesterol</td>\n<td>Atherosclerosis and xanthomas</td>\n</tr>\n<tr>\n<td>3. Dysbetalipoproteinemia(type III hyperlipoproteinemia)</td>\n<td>Chylomicrons and VLDL</td>\n<td>Atherosclerosis and xanthomas</td>\n</tr>\n<tr>\n<td>4. Hypertriglyceridemia(type IV hyperlipoproteinemia)</td>\n<td>VLDL and TG</td>\n<td>Pancreatitis</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(216, 'Biochemistry', 'PM2109. Unsaturated Fatty acids', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Unsaturated Fatty acids</p>\n<table width=\467\>\n<tbody>\n<tr>\n<td>Common name</td>\n<td>No. of Carbon atoms: <strong>Number of double bonds</strong>; Position of double bonds</td>\n<td>Family&nbsp;</td>\n<td>Occurrence</td>\n</tr>\n<tr>\n<td>Palmitoleic</td>\n<td>16:<strong>1</strong>;9</td>\n<td>ω7</td>\n<td>In nearly all fats</td>\n</tr>\n<tr>\n<td>Oleic</td>\n<td>18:<strong>1</strong>;9</td>\n<td>ω9</td>\n<td>The most common fatty acid in<br>natural fats, olive oil</td>\n</tr>\n<tr>\n<td>Elaidic</td>\n<td>18:<strong>1</strong>;9</td>\n<td>ω9</td>\n<td>Hydrogenated and ruminant fats</td>\n</tr>\n<tr>\n<td>Linoleic</td>\n<td>18:<strong>2</strong>;9,12</td>\n<td>ω6</td>\n<td>Corn, peanut, cottonseed, soybean, and<br>many plant oils</td>\n</tr>\n<tr>\n<td>γ-Linolenic</td>\n<td>18:<strong>3</strong>;6,9,12</td>\n<td>ω6</td>\n<td>Some plants, eg, oil of evening primrose,<br>borage oil; minor fatty acid in animals</td>\n</tr>\n<tr>\n<td>α-Linolenic</td>\n<td>18:<strong>3</strong>;9,12,15</td>\n<td>ω3</td>\n<td>Linseed oil</td>\n</tr>\n<tr>\n<td>Arachidonic</td>\n<td>20:<strong>4</strong>;5,8,11,14</td>\n<td>ω6</td>\n<td>Animal fats,&nbsp; Phospholipids in animals</td>\n</tr>\n<tr>\n<td>Timnodonic acid</td>\n<td>20:<strong>5</strong>;5,8,11,14,17</td>\n<td>ω3</td>\n<td>Fish oils, eg.&nbsp;cod<br>liver, mackerel, menhaden, salmon oils</td>\n</tr>\n<tr>\n<td>Cervonic acid</td>\n<td>22:<strong>6</strong>;4,7,10,13,16,19</td>\n<td>ω3</td>\n<td>Fish oils, algal oils, Phospholipids in brain</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(217, 'Biochemistry', 'PM1018. Hormone sensitive lipase  Vs lipoprotein lipase', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>&nbsp;</td>\n<td>&nbsp;HSL</td>\n<td>&nbsp;Lipoprotein lipase</td>\n</tr>\n<tr>\n<td>Location</td>\n<td>&nbsp;Adipocyte</td>\n<td>&nbsp;Capillaries</td>\n</tr>\n<tr>\n<td>Action</td>\n<td>&nbsp;Hydrolyse TAG in adipose&nbsp; &nbsp;tissue</td>\n<td>&nbsp;Hydrolyse TAG in chylomicron , VLDL</td>\n</tr>\n<tr>\n<td>Acts in the state of</td>\n<td>&nbsp;Fasting (Glucagon)</td>\n<td>&nbsp;Fed (Insulin)</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(218, 'Biochemistry', 'PM0768. Action of various prostaglandins on different organs:', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>Organs</td>\n<td><strong>PGE<sub>2</sub></strong></td>\n<td><strong>PGF<sub>2α</sub></strong></td>\n<td><strong>PGI<sub>2</sub></strong></td>\n<td><strong>TXA<sub>2</sub></strong></td>\n</tr>\n<tr>\n<td><strong>Blood vessel</strong></td>\n<td>Vasodilatation, ↓ BP</td>\n<td>Vasodilatation (mostly),&nbsp;Largerveins constrict, little effect on BP</td>\n<td>Vasodilatation,&nbsp;↓ ↓ BP</td>\n<td>Vasoconstriction</td>\n</tr>\n<tr>\n<td><strong>Heart</strong></td>\n<td>Weak inotropic, reflexcardiac stimulation</td>\n<td>Weak inotropic</td>\n<td>-</td>\n<td>-</td>\n</tr>\n<tr>\n<td><strong>Platelet</strong></td>\n<td>Variable effect</td>\n<td>-</td>\n<td>Antiaggregatory</td>\n<td>Aggregation &amp; release reaction</td>\n</tr>\n<tr>\n<td><strong>Uterus</strong></td>\n<td>Contraction (in vivo),Softening of cervix</td>\n<td>Contraction (in vivo &amp; in vitro),Softening of cervix</td>\n<td>-</td>\n<td>-</td>\n</tr>\n<tr>\n<td><strong>Bronchi</strong></td>\n<td>Dilatation, Inhibit histamine release</td>\n<td>Constriction</td>\n<td>Dilatation, Inhibit histamine release</td>\n<td>Constriction</td>\n</tr>\n<tr>\n<td><strong>Stomach</strong></td>\n<td>↓ acid secretion, ↑ mucus production</td>\n<td>-</td>\n<td>↓ acid secretion,mucosal vasodilatation</td>\n<td>-</td>\n</tr>\n<tr>\n<td><strong>Intestine</strong></td>\n<td>Contracts longitudinal &amp; relaxes, circular muscles, peristalsis ↑, Cl¯ &amp; water secretion↑</td>\n<td>Spasmogenic, ↑ fluid &amp; electrolytesecretion (weak)</td>\n<td>Weak spasmogenic, inhibit toxin-induced fluid secretion</td>\n<td>Weak spasmogenic</td>\n</tr>\n<tr>\n<td><strong>Kidney</strong></td>\n<td>Natriuresis,&nbsp;↓ Cl¯ reabsorption, inhibit ADH action,vasodilatation, renin release</td>\n<td>-</td>\n<td>Natriuresis, vasodilatation,renin release</td>\n<td>Vasoconstriction</td>\n</tr>\n<tr>\n<td><strong>CNS</strong></td>\n<td>Pyrogenic, variety of effects on i.e. inj.</td>\n<td>-</td>\n<td>-</td>\n<td>-</td>\n</tr>\n<tr>\n<td><strong>Release of NA</strong></td>\n<td>↑ or ↓</td>\n<td>↑ or ↓</td>\n<td>-</td>\n<td>-</td>\n</tr>\n<tr>\n<td><strong>Afferent nerve</strong></td>\n<td>Sensitize to noxious stimuli</td>\n<td>-</td>\n<td>Same as PGE2</td>\n<td>-</td>\n</tr>\n<tr>\n<td><strong>Endocrine</strong></td>\n<td>Release of ant. Pituitaryhormones, steroids, insulin;TSH-like action</td>\n<td>Release of gonadotropins &amp;prolactin</td>\n<td>-</td>\n<td>-</td>\n</tr>\n<tr>\n<td><strong>Metabolism</strong></td>\n<td>Antilipolytic, insulin like action,mobilization of bone Ca<sup>2+</sup></td>\n<td>-</td>\n<td>-</td>\n<td>-</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(219, 'Biochemistry', 'PM0767. Enzymes for COX pathway', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>COX pathway products</td>\n<td>Enzymes for synthesis</td>\n</tr>\n<tr>\n<td>PGI<sub>2</sub></td>\n<td>Prostacyclin synthase</td>\n</tr>\n<tr>\n<td>PGD<sub>2</sub></td>\n<td>Isomerase</td>\n</tr>\n<tr>\n<td>PGE<sub>2</sub></td>\n<td>Isomerase</td>\n</tr>\n<tr>\n<td>PGF<sub>2α</sub></td>\n<td>Reductase</td>\n</tr>\n<tr>\n<td>TXA<sub>2</sub></td>\n<td>Thromboxane synthase</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(220, 'Biochemistry,', 'PM0765. Difference in COX-1 and COX-2', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>COX-1</td>\n<td>COX-2</td>\n</tr>\n<tr>\n<td>Synthesize PGs that have housekeeping function (Gastroprotective, haemostasis, renoprotective)</td>\n<td>Synthesize PGs that mediates Inflammation, pain &amp; fever</td>\n</tr>\n<tr>\n<td>Range of expression is 2-4 folds</td>\n<td>Range of expression is 10-80 folds</td>\n</tr>\n<tr>\n<td>Constitutive enzyme</td>\n<td>Inducible enzyme</td>\n</tr>\n<tr>\n<td>Present in most of the cells (Platelets, endothelium, kidney, stomach)</td>\n<td>Inflammatory sites (Synoviocytes, Fibroblast, Monocytes)&nbsp;</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(221, 'Biochemistry', 'PM0753. Examples of Sphingolipidoses (Lipids-Storage Disease)', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/eaf86bca861342ff84c303230efbb8bax1280x2017.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>S. No.</strong></p>\n</td>\n<td>\n<p><strong>Disease</strong></p>\n</td>\n<td>\n<p><strong>Enzyme Deficiency</strong></p>\n</td>\n<td>\n<p><strong>Lipid </strong></p>\n<p><strong>Accumulating</strong></p>\n</td>\n<td>\n<p><strong>Clinical Symptoms</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>1</p>\n</td>\n<td>\n<p>Tay–Sachs disease</p>\n</td>\n<td>\n<p>Hexosaminidase A</p>\n</td>\n<td>\n<p>Ganglioside</p>\n</td>\n<td>\n<p>Mental retardation, blindness, muscular weakness</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>2</p>\n</td>\n<td>\n<p>Sandhoffs disease</p>\n</td>\n<td>\n<p>Hexosaminidase A and B</p>\n</td>\n<td>\n<p>Globoside &amp; Ganglioside</p>\n</td>\n<td>\n<p>Similar to Tay–Sachs disease but also has hepatosplenomegaly and skeletal dysplasias</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>3</p>\n</td>\n<td>\n<p>Fabrys disease</p>\n</td>\n<td>\n<p>α-Galactosidase</p>\n</td>\n<td>\n<p>Globotriaosyl-ceramide</p>\n</td>\n<td>\n<p>Skin rash, kidney failure (full symptoms only in males; X-linked recessive)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>4</p>\n</td>\n<td>\n<p>Metachromatic leukodystrophy</p>\n</td>\n<td>\n<p>Arylsulfatase A</p>\n</td>\n<td>\n<p>3-Sulfogalactosyl-ceramide</p>\n</td>\n<td>\n<p>Mental retardation and psychologic disturbances in adults; demyelination</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>5</p>\n</td>\n<td>\n<p>Krabbe disease</p>\n</td>\n<td>\n<p>β-Galactosidase</p>\n</td>\n<td>\n<p>Galactosylceramide</p>\n</td>\n<td>\n<p>Mental retardation; myelin almost absent</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>6</p>\n</td>\n<td>\n<p>Gauchers disease</p>\n</td>\n<td>\n<p>β-Glucosidase</p>\n</td>\n<td>\n<p>Glucosylceramide</p>\n</td>\n<td>\n<p>Enlarged liver and spleen, erosion of long bones, mental retardation in infants</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>7</p>\n</td>\n<td>\n<p>Niemann–Pick disease</p>\n</td>\n<td>\n<p>Sphingomyelinase</p>\n</td>\n<td>\n<p>Sphingomyelin</p>\n</td>\n<td>\n<p>Enlarged liver and spleen, mental retardation; fatal in early life</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>8</p>\n</td>\n<td>\n<p>Farbers disease</p>\n</td>\n<td>\n<p>Ceramidase</p>\n</td>\n<td>\n<p>Ceramide</p>\n</td>\n<td>\n<p>Hoarseness, dermatitis, skeletal deformation, mental retardation; fatal in early life</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(222, 'Biochemistry', 'PM0750. Composition of Glycosphingolipids', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td width=\308\>\n<p><strong>Glycosphingolipid </strong></p>\n</td>\n<td width=\308\>\n<p><strong>Composition</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Cerebroside (glucocerebroside)</p>\n</td>\n<td width=\308\>\n<p>Cer-Glc</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>Globoside (lactosylceramide)</td>\n<td width=\308\>&nbsp;Cer-Glc-Gal</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Ganglioside (GM 3)</p>\n</td>\n<td width=\308\>\n<p>Cer-Glc-Gal-NANA</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Globoside (Forssman antigen)</p>\n</td>\n<td width=\308\>\n<p>Cer-Glc-Gal-Gal-GalNac-GalNac</p>\n</td>\n</tr>\n<tr>\n<td colspan=\2\ width=\616\>\n<p>Cer = ceramide; Gal = galactose</p>\n<p>Glc = glucose; GalNac = N-acetylgalactosamine<br><br>NANA = n-acetylneuraminic acid</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(223, 'Biochemistry', 'PM0749. Types of Sphingolipids', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\215\>\n<p>Glycosphingolipid</p>\n</td>\n<td width=\189\>\n<p>Cerebrosides</p>\n<p>Gangliosides</p>\n<p>Globosides</p>\n<p>&nbsp;</p>\n</td>\n<td width=\212\>\n<p>Oligosaccharide moiety attached to the ceramide unit</p>\n</td>\n</tr>\n<tr>\n<td width=\215\>\n<p>Sphingophospholipid</p>\n</td>\n<td width=\189\>\n<p>Sphingomyelin</p>\n</td>\n<td width=\212\>\n<p>Phosphate attached to the ceramide unit</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(224, 'Biochemistry', 'PM0748. Classification of Lipids on the Basis of Base-promoted Hydrolysis', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td colspan=\2\ width=\308\>\n<p><strong>Saponifiable lipids (yield free fatty acid salts residue)</strong></p>\n</td>\n<td width=\308\>\n<p><strong>Non-saponifiable lipids(no fatty acid residues)</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\154\>\n<p>&nbsp;Glycerol Esters</p>\n</td>\n<td width=\154\>\n<p>Non-glycerol Esters</p>\n</td>\n<td rowspan=\2\ width=\308\>\n<p>Steroids</p>\n<p>Eicosanoids</p>\n<p>Terpenes</p>\n<p>Pheromones</p>\n<p>Fat-soluble vitamins&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td width=\154\>\n<p>Triacylglycerols</p>\n<p>Phosphoglycerols</p>\n</td>\n<td width=\154\>\n<p>Waxes</p>\n<p>Sphingolipids</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(225, 'Paediatrics,', 'PM0747. Pulmonary Surfactant Composition', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\291\>\n<p><strong>Constituents</strong></p>\n</td>\n<td width=\104\>\n<p><strong>Percentage</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\291\>\n<p>Phospholipids, mainly dipalmitoylphosphatidylcholine (DPPC)</p>\n</td>\n<td width=\104\>\n<p>70% to 80%</p>\n</td>\n</tr>\n<tr>\n<td width=\291\>\n<p>Proteins</p>\n</td>\n<td width=\104\>\n<p>10%</p>\n</td>\n</tr>\n<tr>\n<td width=\291\>\n<p>Neutral lipids</p>\n</td>\n<td width=\104\>\n<p>10%</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(226, 'Biochemistry', 'PM0746. Types of Phospholipids', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\329\>\n<p><strong>Glycerophospholipids</strong></p>\n</td>\n<td width=\208\>\n<p><strong>Sphingophospholipids</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\329\>\n<p>Phosphatidylcholine or lecithin</p>\n<p>Phosphatidylethanolamine or cephalin</p>\n<p>Phosphatidylinositol</p>\n<p>Phosphatidylserine</p>\n<p>Plasmalogens</p>\n<p>Lysophospholipids or lysolecithin</p>\n<p>Cardiolipin</p>\n</td>\n<td width=\208\>\n<p>Sphingomyeline</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(227, 'Biochemistry', 'PM0745. Distribution of  Various Apolipoproteins on Lipoproteins', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\215\>\n<p><strong>Lipoprotein</strong></p>\n</td>\n<td width=\246\>\n<p><strong>Apolipoprotein</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\215\>\n<p>Chylomicrons</p>\n</td>\n<td width=\246\>\n<p>A-I, A-II, A-IV, B-48, C-I, C-II, C-III, E</p>\n</td>\n</tr>\n<tr>\n<td width=\215\>\n<p>Chylomicrons remnants</p>\n</td>\n<td width=\246\>\n<p>B-48, E</p>\n</td>\n</tr>\n<tr>\n<td width=\215\>\n<p>VLDL</p>\n</td>\n<td width=\246\>\n<p>B-100, C-I, C-II, C-III, E</p>\n</td>\n</tr>\n<tr>\n<td width=\215\>\n<p>IDL</p>\n</td>\n<td width=\246\>\n<p>B-100, E</p>\n</td>\n</tr>\n<tr>\n<td width=\215\>\n<p>LDL</p>\n</td>\n<td width=\246\>\n<p>B-100</p>\n</td>\n</tr>\n<tr>\n<td width=\215\>\n<p>HDL</p>\n</td>\n<td width=\246\>\n<p>A-I, A-II, A-IV, C-I, C-II, C-III, D, E</p>\n</td>\n</tr>\n<tr>\n<td width=\215\>\n<p>Pre- β- HDL</p>\n</td>\n<td width=\246\>\n<p>A-I</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(228, 'Biochemistry', 'PM0742. Lipoprotein: Types, Sources and Action', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\140\>\n<p><strong>Lipoproteins</strong></p>\n</td>\n<td width=\271\>\n<p><strong>Source</strong></p>\n</td>\n<td width=\205\>\n<p><strong>Action</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\140\>\n<p>Chylomicrons</p>\n</td>\n<td width=\271\>\n<p>Derived from intestinal absorption of triacylglycerol and other lipids</p>\n</td>\n<td width=\205\>\n<p>Carry exogenous/dietary triacylglycerol from the intestine to peripheral organs</p>\n</td>\n</tr>\n<tr>\n<td width=\140\>\n<p>Very low density lipoproteins (VLDL)</p>\n</td>\n<td width=\271\>\n<p>Derived from the liver</p>\n</td>\n<td width=\205\>\n<p>Carry endogenous triacylglycerol from the liver to peripheral organs</p>\n</td>\n</tr>\n<tr>\n<td width=\140\>\n<p>Low-density lipoproteins (LDL)</p>\n</td>\n<td width=\271\>\n<p>Derived from VLDL</p>\n</td>\n<td width=\205\>\n<p>Involved in cholesterol transport</p>\n</td>\n</tr>\n<tr>\n<td width=\140\>\n<p>Highdensity lipoproteins (HDL)</p>\n</td>\n<td width=\271\>\n<p>Derived from Liver, intestine, VLDL, chylomicrons</p>\n</td>\n<td width=\205\>\n<p>Involved in reverse cholesterol &amp; Phospholipids transport and also in VLDL and chylomicron metabolism</p>\n</td>\n</tr>\n<tr>\n<td width=\140\>\n<p>Intermediate density lipoproteins(IDL)</p>\n</td>\n<td width=\271\>\n<p>VLDL</p>\n</td>\n<td width=\205\>\n<p>Triacylglycerol &amp; cholesterol transport</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(229, 'Biochemistry', 'PM0741. Fatty Acid Elongation', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\177\>\n<p>Elongation&nbsp; Site</p>\n</td>\n<td width=\439\>\n<p>Elongation steps</p>\n</td>\n</tr>\n<tr>\n<td width=\177\>\n<p>Cytosol</p>\n</td>\n<td width=\439\>\n<p>De novo lipogenesis forming palmitate, myristate and stearate&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td width=\177\>\n<p>Mitochondria</p>\n</td>\n<td width=\439\>\n<p>Fatty acid elongation occurs by a reversal of beta-oxidation.</p>\n<p>Elongation acts primarily on fatty acyl CoA substrates shorter than 16 carbons.</p>\n</td>\n</tr>\n<tr>\n<td width=\177\>\n<p>Endoplasmic Reticulum</p>\n</td>\n<td width=\439\>\n<p>Elongating fatty acids 12-carbons and longer.</p>\n<p>Utilizes fatty acids derived from de novo lipogenesis, as well as fatty acids derived from the diet.</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(230, 'Biochemistry', 'PM0736. Rotheras Test Procedure', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/73b9ba92c9384966a6760940ce51d4fcx287x140.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Rotheras test, positive for ketone bodies.</p></div><!---->\n      <!---->\n    </div><!---->'),
(231, 'Biochemistry', 'PM0735. Ketone body utilisation', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/c4f64f2c484149b98ae69f977b81491ax600x597.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>1.&nbsp;3-Hydroxybutyrate dehydrogenase</p>\n<p>2.&nbsp;CoA Transferase</p>\n<p>3.&nbsp;Thiolase</p></div><!---->\n      <!---->\n    </div><!---->'),
(232, 'Biochemistry', 'PM0734. Acetyl CoA Utilization', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/14ab8ff3813548b784d602ba185d89f6x1280x1163.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(233, 'Biochemistry', 'PM0733. Energetics of beta oxidation of palmitic acid (16 C fatty acid)', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>One&nbsp;cycle of&nbsp;β-oxidation of a fatty acid produces&nbsp;1 FADH<sub>2</sub>, 1 NADH, and 1 acetyl-CoA.</p>\n<p>Therefore the energetics of palmitic acid (C<sub>16</sub>)&nbsp;:</p>\n<table>\n<tbody>\n<tr>\n<td>\n<p>Mechanism</p>\n</td>\n<td>\n<p>ATP Formed (mol)/mol Product</p>\n</td>\n<td>\n<p>Total ATP Formed</p>\n<p>(mol)/mol</p>\n</td>\n</tr>\n<tr>\n<td rowspan=\2\>\n<p>Beta oxidation - 7 cycles</p>\n</td>\n<td>\n<p>7 FADH<sub>2&nbsp;</sub>(7x1.5)</p>\n</td>\n<td>\n<p>10.5</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>7 NADH (7x2.5)</p>\n</td>\n<td>\n<p>17.5</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>From Acetyl CoA (TCA cycle)</p>\n</td>\n<td>\n<p>8 Acetyl CoA (8x10)</p>\n</td>\n<td>\n<p>80</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Energy used in the activation</p>\n</td>\n<td>\n<p>&nbsp;</p>\n</td>\n<td>\n<p>-2</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Net ATP yield</p>\n</td>\n<td colspan=\2\>\n<p><strong>106</strong></p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->');
INSERT INTO db_pearl_m (pid, subject, pearl_title, pearl_desc) VALUES
(234, 'Biochemistry', 'PM0729. Types, Location and Properties of Fatty acid oxidation', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td>\n<p><strong>Types of Oxidation</strong></p>\n</td>\n<td>\n<p><strong>Location</strong></p>\n</td>\n<td>\n<p><strong>Properties</strong></p>\n</td>\n<td>\n<p><strong>Associated Disease</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Beta oxidation</p>\n</td>\n<td>\n<p>Mitochondria</p>\n</td>\n<td>\n<p>A catabolic process by which two-carbons are cleaved from the fatty acid molecule and released as acetyl CoA starting from the carboxyl end.</p>\n<p>The chain is broken between the α(2) and&nbsp;β(3) carbon.</p>\n<p>1 round of beta-oxidation yield 1FADH2, 1NADH, and 1 Acetyl CoA.</p>\n</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>\n<p>Alpha oxidation</p>\n</td>\n<td>\n<p>Peroxisome</p>\n</td>\n<td>\n<p>The presence of the 3-methyl group of phytanic acid prevents degradation by beta-oxidation.</p>\n<p>The chain is broken between the terminal carboxyl group and α(2) carbon removing that carbon as CO2. The remaining chain is activated with CoA and then undergoes&nbsp;β-Oxidation.</p>\n<p>It is not an energy-yielding process.</p>\n</td>\n<td>\n<p>Classic Refsums Disease</p>\n<p>Defective enzyme- Phytanoyl Coa hydroxylase.</p>\n<p>Clinical features include:</p>\n<p>Ichthyosis, Retinitis pigmentosa, Decreased vision, Peripheral neuropathy, Ataxia</p>\n<p class=\p2\>&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Omega oxidation</p>\n</td>\n<td>\n<p>Endoplasmic Reticulum</p>\n</td>\n<td>\n<p>An alternative pathway to beta-oxidation occurs in microsomes.</p>\n<p>Involves the&nbsp;oxidation&nbsp;of the ω carbon (terminal methyl group) resulting in dicarboxylic acid.</p>\n<p>It is not an energy-yielding process.</p>\n</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>\n<p>Peroxisomal Beta oxidation</p>\n</td>\n<td>\n<p>Peroxisome</p>\n</td>\n<td>\n<p>Very-long-chain fatty acids undergo preliminary β-oxidation in peroxisomes up to&nbsp;octanoyl CoA.&nbsp;The shortened fatty acids undergo further β-oxidation in the mitochondria.</p>\n<p>It is not an energy-yielding process.</p>\n</td>\n<td>\n<p>Zellweger (cerebrohepatorenal) syndrome</p>\n<p>A rare inherited absence of peroxisomes in all tissues.</p>\n<p class=\p2\>They accumulate C<span class=\s1\>26</span>—C<span class=\s1\>38 </span>polyenoic acids in brain tissue and also exhibit a generalized loss of peroxisomal functions. The disease causes severe neurologic symptoms, and most patients die in the first year of life.</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(235, 'Biochemistry', 'PM0720. Enzymes Involved in Fatty acid Transfer Across Mitochondrial membrane', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\205\>\n<p>Enzyme</p>\n</td>\n<td width=\205\>\n<p>Site of action</p>\n</td>\n<td width=\205\>\n<p>Action</p>\n</td>\n</tr>\n<tr>\n<td width=\205\>\n<p>Acyl-CoA synthetase</p>\n</td>\n<td width=\205\>\n<p>Outer mitochondrial membrane</p>\n</td>\n<td width=\205\>\n<p>Converts free fatty acid into Acyl CoA</p>\n</td>\n</tr>\n<tr>\n<td width=\205\>\n<p>Carnitine palmitoyl transferase-I</p>\n</td>\n<td width=\205\>\n<p>Outer mitochondrial membrane</p>\n</td>\n<td width=\205\>\n<p>Transfers long-chain acyl group from CoA to carnitine, forming acylcarnitine.</p>\n</td>\n</tr>\n<tr>\n<td width=\205\>\n<p>Carnitine-acylcarnitine translocase</p>\n</td>\n<td width=\205\>\n<p>Inner&nbsp; mitochondrial membrane</p>\n</td>\n<td width=\205\>\n<p>Help Acylcarnitine to penetrate the inner membrane</p>\n</td>\n</tr>\n<tr>\n<td width=\205\>\n<p>Carnitine palmitoyl transferase-II</p>\n</td>\n<td width=\205\>\n<p>Inner&nbsp; mitochondrial membrane</p>\n</td>\n<td width=\205\>\n<p>Along with carnitine palmitoyl transferase I oxidizes long-chain fatty acids in the mitochondria.&nbsp;</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/7d27598800f5469bade0f988478cd9f3x1280x1913.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(236, 'Biochemistry', 'PM0714. Composition of oils', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Oil</strong></p>\n</td>\n<td>\n<p><strong>Saturated fatty acid</strong></p>\n</td>\n<td>\n<p><strong>Mono-unsaturated fatty acid</strong></p>\n</td>\n<td>\n<p><strong>Poly-unsaturated fatty acid</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Coconut oil</p>\n</td>\n<td>\n<p><strong>92.1%</strong></p>\n</td>\n<td>\n<p>6.2%</p>\n</td>\n<td>\n<p>1.6%</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Sunflower oil</p>\n</td>\n<td>\n<p>11%</p>\n</td>\n<td>\n<p><strong>30%</strong></p>\n</td>\n<td>\n<p>&nbsp;62%</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Soybean oil</p>\n</td>\n<td>\n<p>14%</p>\n</td>\n<td>\n<p>23%</p>\n</td>\n<td>\n<p>63%</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Safflower oil</p>\n</td>\n<td>\n<p>10%</p>\n</td>\n<td>\n<p>14%</p>\n</td>\n<td>\n<p><strong>76%</strong></p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(237, 'Biochemistry', 'PM0712. Saturated fatty acids', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Saturated fatty acids</p>\n<table>\n<tbody>\n<tr>\n<td>Common name</td>\n<td>No. of Carbon atoms</td>\n<td>Occurrence</td>\n</tr>\n<tr>\n<td>Acetic acid</td>\n<td>2</td>\n<td>End product of carbohydrate fermentation in ruminants</td>\n</tr>\n<tr>\n<td>Butyric acid</td>\n<td>4</td>\n<td>Butter</td>\n</tr>\n<tr>\n<td>Valeric acid</td>\n<td>5</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>Caproic acid</td>\n<td>6</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>Lauric acid</td>\n<td>12</td>\n<td>Spermaceti, cinnamon, palm kernel,<br>coconut oils, laurels, butter</td>\n</tr>\n<tr>\n<td>Myristic acid</td>\n<td>14</td>\n<td>Nutmeg, palm kernel, coconut oils,<br>myrtles, butter</td>\n</tr>\n<tr>\n<td>Palmitic acid</td>\n<td>16</td>\n<td>Common in all animal and plant fats</td>\n</tr>\n<tr>\n<td>Stearic acid</td>\n<td>18</td>\n<td>&nbsp;</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(238, 'Biochemistry', 'PM0641. Ketogenesis', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/6d83bb1f03624a0486d7aad26f9fe2b8x1280x2005.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(239, 'Biochemistry', 'PM0638. Biosynthesis of Fatty acids', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://cdn1.dailyrounds.org/uploads/2673d3200a674b2bba3a4d486bab813fx556x716.PNG\><!---->\n      </div><!---->\n    </div><!---->'),
(240, 'Biochemistry', 'PM0637. Beta-oxidation of Fatty Acids', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/e5378985a79641669d91f9ecd81a4d9bx907x799.PNG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Note:&nbsp;<strong>Carnitine palmitoyltransferase I</strong>&nbsp;(<strong>CPT1</strong>) is also known as&nbsp;<strong>carnitine acyltransferase I</strong>,</p></div><!---->\n      <!---->\n    </div><!---->'),
(241, 'Biochemistry', 'PM0631. Classification of Lipids', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/ad4b42c8ef9e4716bb7eaa0e81ebd201x600x789.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(242, 'Biochemistry', 'PM0480. Fatty Acid Synthesis Pathway', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\308\>\n<p>Steps</p>\n</td>\n<td width=\308\>\n<p>Reactions</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Activation</p>\n</td>\n<td width=\308\>\n<p>Acetyl-CoA carboxylation</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Elongation</p>\n</td>\n<td width=\308\>\n<p>The malonyl-CoA pathway</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Condensation step&nbsp;</p>\n</td>\n<td width=\308\>\n<p>Covalent linking of acetyl-CoA and Malonyl-CoA (with the release of CoA and carbon dioxide)</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Reduction step&nbsp;</p>\n</td>\n<td width=\308\>\n<p>Acetoacetyl-ACP to ß-hydroxybutryl-ACP (using up one NADPH2)</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Dehydration step&nbsp;</p>\n</td>\n<td width=\308\>\n<p>ß-hydroxybutryl-ACP to crotonyl-ACP (with the release of a water molecule)</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Another reduction step</p>\n</td>\n<td width=\308\>\n<p>Crotonyl-ACP to butryl-ACP (using up a second NADPH2)</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(243, 'Biochemistry', 'PM2426. Enzyme classification', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>There are seven groups of enzymes according to IUBMB.</p>\n<table>\n<tbody>\n<tr>\n<td>E.C. NO.</td>\n<td>Enzyme class</td>\n<td>Type of reaction catalyzed with examples</td>\n</tr>\n<tr>\n<td>1</td>\n<td><strong>O</strong>xidoreductases</td>\n<td>\n<p>Catalyze redox reactions</p>\n<ul>\n<li>Dehydrogenase</li>\n<li>Oxygenase</li>\n<li>Oxidase</li>\n<li>Catalase</li>\n<li>Peroxidase</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>2</td>\n<td><strong>T</strong>ransferases</td>\n<td>\n<p>Catalyze the transfer of functional groups from one substrate to another.</p>\n<ul>\n<li>Kinases</li>\n<li>Transaminases</li>\n<li>Transmethylases</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>3</td>\n<td><strong>H</strong>ydrolases</td>\n<td>\n<p>Catalyze hydrolytic cleavage of C-C, C-O, C-N, and other covalent bonds.</p>\n<ul>\n<li>Lipase</li>\n<li>Arginase</li>\n<li>Pepsin</li>\n<li>Esterase</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>4</td>\n<td><strong>L</strong>yases</td>\n<td>\n<p>Catalyze the cleavage of C-C, C-O, C-N, and other covalent bonds by atom elimination, thereby generating double bonds.</p>\n<ul>\n<li>Aldolase</li>\n<li>Fumarase</li>\n<li>HMG-CoA lyase</li>\n<li>Arginosuccinase</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>5</td>\n<td><strong>I</strong>somerases</td>\n<td>\n<p>Catalyze structural changes within a molecule.</p>\n<ul>\n<li>Phosphohexoisomerase</li>\n<li>Mutase</li>\n<li>Racemase</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>6</td>\n<td><strong>L</strong>igases</td>\n<td>\n<p>Catalyze the joining together of two molecules in reactions.</p>\n<ul>\n<li>Acetyl CoA carboxylase</li>\n<li>PRPP synthetase</li>\n<li>Carbamoyl phosphate synthetase</li>\n<li>Glutamine synthetase</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>7</td>\n<td><strong>T</strong>ranslocases</td>\n<td>Catalyze the movement of molecules or ions across membranes.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(244, 'Biochemistry', 'PM2425. Metals as cofactors and prosthetic groups in enzymes', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Metal</strong></td>\n<td><strong>&nbsp;Enzymes</strong></td>\n</tr>\n<tr>\n<td>Zinc</td>\n<td>\n<p>Carbonic anhydrase</p>\n<p>Carboxypeptidase</p>\n<p>Alcohol dehydrogenase</p>\n<p>Alkaline phosphatase</p>\n<p>ALA dehydratase</p>\n<p>Lactate dehydrogenase</p>\n</td>\n</tr>\n<tr>\n<td>Selenium</td>\n<td>\n<p>Glutathione peroxidase</p>\n<p>Thioredoxin reductase</p>\n<p>De-iodinase</p>\n</td>\n</tr>\n<tr>\n<td>Magnesium</td>\n<td>\n<p>Phosphofructokinase</p>\n<p>Hexokinase</p>\n<p>Glucose -6-phosphatase</p>\n<p>Mutase</p>\n<p>Enolase</p>\n</td>\n</tr>\n<tr>\n<td>Copper</td>\n<td>\n<p>Cytosolic superoxide dismutase</p>\n<p>Tyrosinase</p>\n<p>Cyt c oxidase</p>\n<p>Lysyl oxidase</p>\n<p>Amino acid oxidase</p>\n</td>\n</tr>\n<tr>\n<td>Molybdenum</td>\n<td>\n<p>Dinitrogenase</p>\n<p>Xanthine oxidase</p>\n<p>Sulfite oxidase</p>\n</td>\n</tr>\n<tr>\n<td>Manganese</td>\n<td>\n<p>Mitochondrial superoxide dismutase</p>\n<p>Enolase</p>\n<p>Arginase</p>\n<p>Hexokinase</p>\n<p>Phosphofructokinase</p>\n<p>Ribonucleotide reductase</p>\n</td>\n</tr>\n<tr>\n<td>Iron</td>\n<td>Succinate Dehydrogenase</td>\n</tr>\n<tr>\n<td>Calcium</td>\n<td>\n<p>&nbsp; &nbsp;Lipase</p>\n<p>Lecithinase</p>\n</td>\n</tr>\n<tr>\n<td>Nickel</td>\n<td>&nbsp; &nbsp;Urease</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(245, 'Biochemistry', 'PM2421. Heme containing proteins', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Protein</strong></td>\n<td><strong>Function</strong></td>\n</tr>\n<tr>\n<td>&nbsp;Hemoglobin</td>\n<td>&nbsp; Transport of oxygen in the blood</td>\n</tr>\n<tr>\n<td>&nbsp;Myoglobin</td>\n<td>&nbsp; Storage of oxygen in muscle</td>\n</tr>\n<tr>\n<td>&nbsp;Cytochrome C</td>\n<td>&nbsp; Involvement in the electron transport chain</td>\n</tr>\n<tr>\n<td>&nbsp;Cytochrome P450</td>\n<td>&nbsp;Hydroxylation of xenobiotics</td>\n</tr>\n<tr>\n<td>&nbsp;Catalase</td>\n<td>&nbsp;Degradation of hydrogen peroxide</td>\n</tr>\n<tr>\n<td>&nbsp;Tryptophan pyrrolase</td>\n<td>&nbsp;Oxidation of tryptophan</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(246, 'Biochemistry', 'PM2312. Enzymes active in phosphorylated and dephosphorylated state', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Phosphorylated state</strong></p>\n<p>(Low insulin)</p>\n</td>\n<td>\n<p><strong>Dephosphorylated state</strong></p>\n<p>(High insulin)</p>\n</td>\n</tr>\n<tr>\n<td>\n<ul>\n<li>Fructose 2,6 bisphosphatase</li>\n<li>PEP carboxykinase</li>\n<li>Pyruvate carboxylase</li>\n<li>Glycogen phosphorylase</li>\n<li>Hormone-sensitive lipase</li>\n</ul>\n</td>\n<td>\n<ul>\n<li>Phosphofructokinase</li>\n<li>Pyruvate dehydrogenase</li>\n<li>Glycogen synthase</li>\n<li>Acetyl CoA carboxylase</li>\n<li>HMG CoA reductase</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(247, 'Biochemistry,', 'PM1398. Drugs precipitating porphyria: \BOOST CHAP\', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>The mnemonic \<strong>BOOST CHAP</strong>\ can be used to remember the drugs precipitating porphyria.</p>\n<p><strong>B</strong>: Barbiturates</p>\n<p><strong>O</strong>: OCPs</p>\n<p><strong>O</strong>: Orphenadrine</p>\n<p><strong>S</strong>: Sulphonamides</p>\n<p><strong>T</strong>: Tetracyclines</p>\n<p><strong>C</strong>: Chloramphenicol, carbamazepine</p>\n<p><strong>H</strong>: Halothane, Hydralazine</p>\n<p><strong>A</strong>: Alcohol</p>\n<p><strong>P</strong>: Phenytoin</p></div><!---->\n      <!---->\n    </div><!---->'),
(248, 'Biochemistry', 'PM0913. Some Examples of Suicidal Inhibitors', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\308\>\n<p><strong>Suicidal Inhibitors</strong></p>\n</td>\n<td width=\308\>\n<p><strong>Enzyme</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Allopurinol</p>\n</td>\n<td width=\308\>\n<p>Xanthine oxidase</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Aspirin</p>\n</td>\n<td width=\308\>\n<p>Cyclooxygenase</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>5-Flurouracil</p>\n</td>\n<td width=\308\>\n<p>Thymidylate synthase</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Penicillin</p>\n</td>\n<td width=\308\>\n<p>Transpeptidase</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>DFMO</p>\n</td>\n<td width=\308\>\n<p>Ornithine Decarboxylase</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(249, 'Biochemistry', 'PM0912. Difference between Competitive & Non-Competitive inhibitors', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Reversible inhibitors are of two types:</p>\n<ul>\n<li>Competitive inhibitors</li>\n<li>Non-Competitive inhibitors</li>\n</ul>\n<p>Difference between Competitive &amp; Non-Competitive inhibitors</p>\n<table>\n<tbody>\n<tr>\n<td width=\308\>\n<p><strong>Competitive inhibitors</strong></p>\n</td>\n<td width=\308\>\n<p><strong>Non-Competitive inhibitors</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Succinate dehydrogenase is inhibited by Malonate</p>\n</td>\n<td width=\308\>\n<p>Pyruvate kinase is inhibited by alanine</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Substrate &amp; inhibitor are chemically similar &amp; have same shape</p>\n</td>\n<td width=\308\>\n<p>Substrate &amp; inhibitor are chemically not similar &amp; have different shape</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Inhibitor binds to active site</p>\n</td>\n<td width=\308\>\n<p>Inhibitor binds away from active site, i.e., at allosteric site</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Inhibitor does not change the shape of the active site</p>\n</td>\n<td width=\308\>\n<p>Inhibitor changes the shape of the active site</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Increase in substrate concentration reduces the inhibition</p>\n</td>\n<td width=\308\>\n<p>Increase in substrate concentration does not affect the inhibition</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Vmax remains constant</p>\n</td>\n<td width=\308\>\n<p>Vmax decreases</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Km Increases</p>\n</td>\n<td width=\308\>\n<p>Km remains constant</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(250, 'Biochemistry', 'PM0911. Difference between Reversible and Irreversible Inhibitors', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\308\>\n<p><strong>Reversible Inhibitors</strong></p>\n</td>\n<td width=\308\>\n<p><strong>Irreversible Inhibitors</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Binds via non-covalent interaction</p>\n</td>\n<td width=\308\>\n<p>Binds via covalent interactions</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>No chemical change</p>\n</td>\n<td width=\308\>\n<p>Inhibitor binds to the substrate &amp; prevent catalytic activity of enzymes</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Can be reversed, as there is no bonding between the inhibitor &amp; substrate</p>\n</td>\n<td width=\308\>\n<p>Irreversibility due to strong covalent bonding</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(251, 'Biochemistry,', 'PM0855. Principal Serum Enzymes Used in Clinical Diagnosis', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\250\>\n<tbody>\n<tr>\n<td>&nbsp;<strong>Serum Enzyme</strong></td>\n<td><strong>Major Diagnostic Use/Location</strong></td>\n</tr>\n<tr>\n<td colspan=\2\>Aminotransferases&nbsp;</td>\n</tr>\n<tr>\n<td>Aspartate aminotransferase (AST/SGOT)</td>\n<td>Liver function</td>\n</tr>\n<tr>\n<td>Alanine aminotransferase (ALT/SGPT)</td>\n<td>Viral hepatitis</td>\n</tr>\n<tr>\n<td colspan=\2\>Creatine kinase</td>\n</tr>\n<tr>\n<td>CK-MM</td>\n<td>Skeletal muscle,&nbsp;Muscle disorders</td>\n</tr>\n<tr>\n<td>CK-BB</td>\n<td>Brain,&nbsp;Brain injury, meningitis, abnormal cell growth, severe shock, stroke, hypothermia, or restricted blood flow to the bowel.</td>\n</tr>\n<tr>\n<td>CK-MB</td>\n<td>Heart and skeletal muscle, Myocardial Infarction, Muscular dystrophy</td>\n</tr>\n<tr>\n<td colspan=\2\>Lactate dehydrogenase (LDH)</td>\n</tr>\n<tr>\n<td>HHHH (I<sub>1</sub>)/LDH-1</td>\n<td>Heart &amp; RBC;Myocardial infarction</td>\n</tr>\n<tr>\n<td>HHHM(I<sub>2</sub>)/LDH-2</td>\n<td>Reticuloendothelial system</td>\n</tr>\n<tr>\n<td>HHMM(I<sub>3</sub>)/LDH-3</td>\n<td>Lungs</td>\n</tr>\n<tr>\n<td>HMMM(I<sub>4</sub>)/LDH-4</td>\n<td>Liver &amp; Skeletal Muscles</td>\n</tr>\n<tr>\n<td>MMMM(I<sub>5</sub>)/LDH-5</td>\n<td>Liver &amp; Skeletal Muscles; Liver diseases</td>\n</tr>\n<tr>\n<td colspan=\2\>Others</td>\n</tr>\n<tr>\n<td>Amylase, Lipase and Acid Phosphatase</td>\n<td>Acute pancreatitis</td>\n</tr>\n<tr>\n<td>Ceruloplasmin</td>\n<td>Hepatolenticular degeneration (Wilson disease)</td>\n</tr>\n<tr>\n<td>γ-Glutamyl transferase</td>\n<td>Liver diseases (Alcoholic Liver Disease)</td>\n</tr>\n<tr>\n<td>β-Glucocerebrosidase</td>\n<td>Gaucher disease</td>\n</tr>\n<tr>\n<td>Alkaline Phosphatase (isozymes)</td>\n<td>Bone disorders, obstructive liver diseases</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(252, 'Biochemistry', 'PM0844. Types of porphyria', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Clinically porphyrias can be classified into:</p>\n<ul>\n<li>Hepatic porphyria</li>\n<li>Erythropoietic porphyria</li>\n</ul>\n<p><strong>Hepatic porphyria</strong> is caused due to enzyme deficiency <strong>early</strong> in the pathway of heme synthesis. There is an accumulation of<strong> ALA and PBG</strong>. The patient principally presents with <strong>neurovisceral symptoms</strong>.</p>\n<p><strong>Erythropoietic porphyria</strong> is caused due to enzyme deficiency <strong>late</strong> in the pathway of heme synthesis. There is an accumulation of <strong>porphyrins</strong> which leads to <strong>cutaneous photosensitivity</strong>.</p>\n<p><strong>Image: Heme synthesis pathway:</strong></p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/bfe6e70c1b9c4d3292b7f3c41271337ax1280x2068.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td>\n<div><span><strong>Porphyria type</strong></span></div>\n</td>\n<td>\n<div><span><strong>Deficient&nbsp;</strong> <strong>enzyme</strong></span></div>\n</td>\n<td>\n<div><span><strong>Lab findings</strong></span></div>\n</td>\n<td>&nbsp;</td>\n<td>\n<div><span><strong>Symptoms</strong></span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span>X-linked dominant protoporphyria</span></div>\n<div><span>(XLDPP)</span></div>\n</td>\n<td>\n<div><span>δ-aminolevulinate synthase 2</span></div>\n<div><span>(ALAS - 2)</span></div>\n</td>\n<td>Decreased RBC count and hemoglobin</td>\n<td>\n<div><span>Cutaneous photosensitivity</span></div>\n</td>\n<td>\n<div><span>Photosensitivity</span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span>Aminolevulinate dehydratase deficiency porphyria</span></div>\n<div><span>(ALADP)</span></div>\n</td>\n<td>\n<div><span>δ-aminolevulinate dehydratase</span></div>\n<div><span>(ALAD)</span></div>\n</td>\n<td>Increased urinary ALA and coproporphyrin III&nbsp;</td>\n<td><span>Neurovisceral symptoms</span></td>\n<td>\n<div><span>Abdominal pain, neuropathy</span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span>Acute intermittent porphyria</span></div>\n<div><span>(AIP)</span></div>\n</td>\n<td>\n<div><span>Hydroxymethylbilane synthase</span></div>\n<div><span>(Uroporphyrin I synthase)</span></div>\n</td>\n<td>Increased urinary ALA and PBG&nbsp;</td>\n<td><span>Neurovisceral symptoms</span>&nbsp;</td>\n<td>\n<div><span>Periodic abdominal pain,&nbsp;peripheral neuropathy, psychiatric symptoms, tachycardia</span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span>Congenital Erythropoietic porphyria</span></div>\n<div><span>(CEP)</span></div>\n</td>\n<td>\n<div><span>Uroporphyrinogen III synthase</span></div>\n<div><span>(UROS)</span></div>\n</td>\n<td>Increased urinary, fecal and red cell uroporphyrin I</td>\n<td><span>Cutaneous photosensitivity</span></td>\n<td>\n<div><span>Severe photosensitivity with erythema, swelling and blistering, hemolytic anemia, splenomegaly</span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span>Porphyria cutanea tarda (PCT)</span></div>\n</td>\n<td>\n<div><span>Uroporphyrinogen decarboxylase</span></div>\n<div><span>(UROD)</span></div>\n</td>\n<td>Increased uroporphyrin I</td>\n<td>&nbsp;\n<div><span>Cutaneous photosensitivity</span></div>\n</td>\n<td>\n<div><span>Photosensitivity with&nbsp;vesicles&nbsp;and&nbsp;bullae</span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span>Hereditary coproporphyria</span></div>\n<div><span>(HCP)</span></div>\n</td>\n<td>\n<div><span>Coproporphyrinogen oxidase</span></div>\n<div><span>(CPOX)</span></div>\n</td>\n<td>Increased urinary ALA, PBG, and coproporphyrin III and fecal coproporphyrin III</td>\n<td>Both</td>\n<td>\n<div><span>Photosensitivity, neurologic symptoms, abdominal colic</span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span>Variegate porphyria</span></div>\n<div><span>(VP)</span></div>\n</td>\n<td>\n<div><span>Protoporphyrinogen oxidase</span></div>\n<div><span>(PPOX)</span></div>\n</td>\n<td>Increased urinary ALA, PBG, and coproporphyrin III and fecal protoporphyrin IX</td>\n<td>Both</td>\n<td>\n<div><span>Photosensitivity, neurologic symptoms, developmental delay</span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span>Erythropoietic protoporphyria</span></div>\n<div><span>(EPP)</span></div>\n</td>\n<td>\n<div><span>Ferrochelatase</span></div>\n<div><span>(FECH)</span></div>\n</td>\n<td>Increased fecal and red cell protoporphyrin IX&nbsp;</td>\n<td><span>Cutaneous photosensitivity</span></td>\n<td>\n<div><span>Non-blistering Photosensitivity with skin lesions</span></div>\n</td>\n</tr>\n</tbody>\n</table>\n<div><span>&nbsp;</span></div>\n<div><span>Most porphyrias are <strong>autosomal dominant</strong> <strong>except</strong></span></div>\n<div>\n<ul>\n<li><span>ALAD enzyme deficiency porphyria</span></li>\n<li><span>Congenital erythropoietic porphyria</span></li>\n<li><span>Erythropoietic protoporphyria</span></li>\n<li><span>X-linked protoporphyria</span></li>\n</ul>\n</div></div><!---->\n      <!---->\n    </div><!---->'),
(253, 'Biochemistry', 'PM0766. Site of synthesis of various Cox products', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>COX pathway products</td>\n<td>Site of synthesis</td>\n</tr>\n<tr>\n<td>Whole range of COX products</td>\n<td>Lung &amp; Spleen</td>\n</tr>\n<tr>\n<td>Thromboxane</td>\n<td>Primarily Platelets</td>\n</tr>\n<tr>\n<td>Prostacyclin</td>\n<td>Endothelium of blood vessels</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(254, 'Paediatrics,', 'PM0595. Human Porphyrias and Their Features', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>&nbsp;</td>\n<td>\n<div><span><strong>Acute intermittent porphyria</strong></span></div>\n</td>\n<td>\n<div><span><strong>Porphyria cutanea tarda</strong></span></div>\n</td>\n<td>\n<div><span><strong>Erythropoietic protoporphyria</strong></span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span><strong>Presenting symptoms</strong></span></div>\n</td>\n<td>\n<div><span>Neurologic</span></div>\n</td>\n<td>\n<div><span>Chronic skin blistering, fragility</span></div>\n</td>\n<td>\n<div><span>Acute skin swelling, pain</span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span><strong>Onset</strong></span></div>\n</td>\n<td>\n<div><span>Adult-onset</span></div>\n</td>\n<td><span>Adult-onset</span></td>\n<td><span>Childhood-onset</span></td>\n</tr>\n<tr>\n<td>\n<div><span><strong>Exacerbating factors</strong></span></div>\n</td>\n<td>\n<div><span>Drugs (like P450 inducers), progesterone, dietary restriction</span></div>\n</td>\n<td>\n<div><span>Iron, alcohol, smoking, estrogens, hepatitis C, HIV, halogenated hydrocarbons</span></div>\n</td>\n<td>\n<div><span>---</span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span><strong>Most important screening Tests</strong></span></div>\n</td>\n<td>\n<div><span>Urinary porphobilinogen</span></div>\n</td>\n<td>\n<div><span>Plasma (or urine)</span></div>\n<div><span>porphyrins</span></div>\n</td>\n<td>\n<div><span>Erythrocyte (or plasma) porphyrins</span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span><strong>Treatment</strong></span></div>\n</td>\n<td>\n<div><span>Hemin, glucose</span></div>\n</td>\n<td>\n<div><span>Phlebotomy, low-dose hydroxychloroquine</span></div>\n</td>\n<td>\n<div><span>Beta-carotene</span></div>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(255, 'Biochemistry', 'PM0032. Rate determining enzymes and their key regulators', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\750\>\n<tbody>\n<tr>\n<td>Pathway</td>\n<td>Enzyme&nbsp;</td>\n<td>Regulating factors</td>\n</tr>\n<tr>\n<td>Glycolysis</td>\n<td>Phosphofructokinase (PFK-1)</td>\n<td>\n<p>AMP+, Fructose 2,6 bi-phosphate+</p>\n<p>ATP-, Citrate-</p>\n</td>\n</tr>\n<tr>\n<td>&nbsp;gluconeogenesis</td>\n<td>Fructose-1,6-bisphosphatase</td>\n<td>\n<p>ATP+, Acetyl CoA+</p>\n<p>AMP-, Fructose 2,6 bi-phosphate-</p>\n</td>\n</tr>\n<tr>\n<td>&nbsp;Krebs cycle</td>\n<td>Isocitrate dehydrogenase</td>\n<td>\n<p>ADP+,</p>\n<p>ATP-, NADH-</p>\n</td>\n</tr>\n<tr>\n<td>&nbsp;glycogenesis</td>\n<td>Glycogen synthase</td>\n<td>\n<p>Glucose-6-phosphate+, insulin+, cortisol+,</p>\n<p>Epinephrine-, glucagon-</p>\n</td>\n</tr>\n<tr>\n<td>&nbsp;glycogenolysis</td>\n<td>Glycogen phosphorylase</td>\n<td>\n<p>Epinephrine+, glucagon+, AMP+</p>\n<p>Glucose-6-phosphate-, insulin-, ATP-</p>\n</td>\n</tr>\n<tr>\n<td>HMP shunt&nbsp;</td>\n<td>Glucose-6-phosphate dehydrogenase (G6PD)</td>\n<td>\n<p>NADP+</p>\n<p>NADPH-</p>\n</td>\n</tr>\n<tr>\n<td>&nbsp;Denovo pyrimidine synthesis</td>\n<td>Carbamoyl phosphate synthetase II</td>\n<td>\n<p>ATP+</p>\n<p>UTP-</p>\n</td>\n</tr>\n<tr>\n<td>&nbsp;Denovo purine synthesis</td>\n<td>Glutamine-phosphoribosylpyrophosphate (PRPP) amidotransferase</td>\n<td>AMP-, IMP-, GMP-</td>\n</tr>\n<tr>\n<td>&nbsp;Urea cycle</td>\n<td>Carbamoyl phosphate synthetase I</td>\n<td>N-acetyl glutamate (NAG)+</td>\n</tr>\n<tr>\n<td>&nbsp;Fatty acid synthesis</td>\n<td>Acetyl-CoA carboxylase (ACC)</td>\n<td>\n<p>Insulin+, citrate+</p>\n<p>Glucagon-, palmitoyl-CoA-</p>\n</td>\n</tr>\n<tr>\n<td>&nbsp;Fatty acid oxidation</td>\n<td>Carnitine acyltransferase I</td>\n<td>Malonyl-CoA-</td>\n</tr>\n<tr>\n<td>&nbsp;Cholesterol synthesis</td>\n<td>HMG-CoA reductase</td>\n<td>\n<p>Insulin+, thyroxine+</p>\n<p>Glucagon-, cholesterol-</p>\n</td>\n</tr>\n<tr>\n<td>Ketogenesis</td>\n<td>HMG-CoA synthase</td>\n<td>&nbsp;</td>\n</tr>\n</tbody>\n</table>\n<p>+ Positive effect on the pathway.</p>\n<p>- Negative effect on the pathway.</p></div><!---->\n      <!---->\n    </div><!---->'),
(256, 'Pathology,', 'PM2419. Factors affecting iron absorption', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Enhancers:</strong></p>\n<ul>\n<li>Ascorbic acid</li>\n<li>Citric acid</li>\n<li>Amino acid</li>\n<li>Sugars in the diet</li>\n</ul>\n<p><strong>Inhibitors:</strong></p>\n<ul>\n<li>Tannins</li>\n<li>Carbonate</li>\n<li>Oxalate</li>\n<li>Phosphate</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(257, 'Orthopaedics,', 'PM2197. Parathyroid Diseases', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/c6b63d4374934158b4021e7aa5b159f2x1200x1541.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(258, 'Community Medicine,', 'PM1826. Folic Acid in Pregnancy', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Condition</strong></td>\n<td><strong>Daily dosage of folic acid</strong></td>\n</tr>\n<tr>\n<td>Prophylaxis - to prevent neural tube defects (NTDs)</td>\n<td>\n<p>400 mcg (WHO)</p>\n<p>500 mcg (<span>MoHFW- India)</span></p>\n</td>\n</tr>\n<tr>\n<td>Treatment of folic acid deficiency</td>\n<td>\n<p>800 mcg (WHO)</p>\n<p>1 mg (MoHFW-India)</p>\n</td>\n</tr>\n<tr>\n<td>Previous child with NTD</td>\n<td>\n<p>5 mg<strong>*</strong> (WHO)</p>\n<p>4 mg (NCBI)</p>\n<p>5 mg (MoHFW-India)</p>\n</td>\n</tr>\n<tr>\n<td>Sickle cell disease</td>\n<td>5 mg&nbsp;</td>\n</tr>\n<tr>\n<td>Thalassemia</td>\n<td>5 mg</td>\n</tr>\n<tr>\n<td>Epilepsy</td>\n<td>4 mg</td>\n</tr>\n</tbody>\n</table>\n<p>WHO: World Health Organization</p>\n<p>MoHFW-India: Ministry of Health and Family Welfare</p>\n<p><strong>*</strong>more acceptable (most references)</p>\n<p><strong>Controversial</strong>: Latest ACOG guidelines mention 0.4mg of folic acid to be given to epileptic pregnant women / pregnant women on ASM (Anti-seizure medications).<br>Williams Obstetrics, 26th Edition and Harrisons Medicine 21st edition mention the dose to be 4mg per day.</p></div><!---->\n      <!---->\n    </div><!---->'),
(259, 'Biochemistry', 'PM1158. Types of Antioxidants', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\0\>\n<tbody>\n<tr>\n<td>\n<p><strong>Preventive Antioxidants</strong></p>\n</td>\n<td>\n<p><strong>Chain-breaking Antioxidants</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<ul>\n<li>Catalase</li>\n<li>Glutathione peroxidase</li>\n<li>Chelator of metal Ions-Ceruloplasmin &amp; Transferrin</li>\n</ul>\n</td>\n<td><br>\n<ul>\n<li>Superoxide dismutase</li>\n<li>Urate</li>\n<li>Vitamin E</li>\n<li>Polyphenols</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(260, 'Biochemistry', 'PM1157. Reactive Oxygen Species', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td colspan=\2\>\n<p><strong>Radicals</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Reactive oxygen species</strong></p>\n</td>\n<td>\n<p><strong>Antioxidant</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Superoxide</p>\n</td>\n<td>\n<p>Vitamin C , SOD</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Hydroxyl</p>\n</td>\n<td>\n<p>Vitamin C, Glutathione</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Hydroperoxyl</p>\n</td>\n<td>\n<p>Vitamin C , Vitamin E, Glutathione, Carotenoid, Ubiquinol</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Alkoxyl</p>\n</td>\n<td>\n<p>Vitamin C</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Peroxyl</p>\n</td>\n<td>\n<p>Vitamin C , Vitamin E, Glutathione, Carotenoid, Ubiquinol</p>\n</td>\n</tr>\n<tr>\n<td>Singlet Oxygen</td>\n<td>&nbsp;Vitamin C , Carotenoid</td>\n</tr>\n</tbody>\n</table>\n<table>\n<tbody>\n<tr>\n<td colspan=\2\>\n<p><strong>Non-Radicals</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Reactive oxygen species</strong></p>\n</td>\n<td>\n<p><strong>Antioxidant</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Hydrogen peroxide</p>\n</td>\n<td>\n<p>Vitamin C, Glutathione, catalase</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(261, 'Biochemistry', 'PM1156. Daily Recommended Dose of Water-Soluble Vitamins', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\215\>\n<p>Vitamins</p>\n</td>\n<td width=\401\>\n<p>Recommended daily dose</p>\n</td>\n</tr>\n<tr>\n<td width=\215\>\n<p>Vitamin B1</p>\n</td>\n<td width=\401\>\n<p>1–1.5 mg/day</p>\n</td>\n</tr>\n<tr>\n<td width=\215\>\n<p>Vitamin B2</p>\n</td>\n<td width=\401\>\n<p>1–1.5 mg/day</p>\n</td>\n</tr>\n<tr>\n<td width=\215\>\n<p>Vitamin B3</p>\n</td>\n<td width=\401\>\n<p>16–20 mg/day</p>\n<p>Lactation: 20<u>+</u>5 mg/day</p>\n</td>\n</tr>\n<tr>\n<td width=\215\>\n<p>Pantothenic acid</p>\n</td>\n<td width=\401\>\n<p>8–10 mg/day</p>\n</td>\n</tr>\n<tr>\n<td width=\215\>\n<p>Vitamin B6</p>\n</td>\n<td width=\401\>\n<p>1–2 mg/day</p>\n</td>\n</tr>\n<tr>\n<td width=\215\>\n<p>Biotin</p>\n</td>\n<td width=\401\>\n<p>30&nbsp;μg/day</p>\n</td>\n</tr>\n<tr>\n<td width=\215\>\n<p>Folic acid</p>\n</td>\n<td width=\401\>\n<p>300–400 μg/day</p>\n<p>Pregnancy: 600 μg/day</p>\n<p>Lactation:500 μg/day</p>\n</td>\n</tr>\n<tr>\n<td width=\215\>\n<p>Vitamin B12</p>\n</td>\n<td width=\401\>\n<p>1.5–2.5 μg/day</p>\n<p>Pregnancy: 2.5–2.8 μg/day</p>\n<p>Lactation: 2.5 μg/day</p>\n</td>\n</tr>\n<tr>\n<td width=\215\>\n<p>Vitamin C</p>\n</td>\n<td width=\401\>\n<p>75 mg/day</p>\n<p>Pregnancy &amp; lactation: 100–120 mg/day</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(262, 'Biochemistry', 'PM1155. Conditions Associated with Pellagra-like Symptoms', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\196\>\n<p>Condition/Disease</p>\n</td>\n<td width=\420\>\n<p>Reason for niacin deficiency</p>\n</td>\n</tr>\n<tr>\n<td width=\196\>\n<p>Hartnup disease</p>\n</td>\n<td width=\420\>\n<p>Intestinal &amp; renal malabsorption of tryptophan</p>\n</td>\n</tr>\n<tr>\n<td width=\196\>\n<p>Carcinoid syndrome</p>\n</td>\n<td width=\420\>\n<p>Overproduction of serotonin that causes diversion of&nbsp; tryptophan from NAD+ Pathway</p>\n</td>\n</tr>\n<tr>\n<td width=\196\>\n<p>Vitamin B6 deficiency</p>\n</td>\n<td width=\420\>\n<p>Defective kynureninase pathway</p>\n</td>\n</tr>\n<tr>\n<td width=\196\>\n<p>Maize rich diet</p>\n</td>\n<td width=\420\>\n<p>Niacin is in unavailable niacytin form</p>\n</td>\n</tr>\n<tr>\n<td width=\196\>\n<p>Jowar diet (Sorghum)</p>\n</td>\n<td width=\420\>\n<p>Increase leucine content inhibit QPRTase enzyme</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(263, 'Medicine,', 'PM1154. Vitamin Deficiency States (Water-soluble Vitamins)', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td>\n<p><strong>Vitamin</strong></p>\n</td>\n<td>\n<p><strong>Deficiency State</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Thiamine (<strong>B<sub>1</sub></strong>)</p>\n</td>\n<td>\n<p><strong>Beriberi</strong></p>\n<p>Wernicke–Korsakoff’s syndrome</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Riboflavin (<strong>B<span>2</span></strong>)</p>\n</td>\n<td>\n<p>Cheilosis, Magenta-coloured tongue,&nbsp;</p>\n<p>Glossitis, Seborrheic dermatitis</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Niacin (<strong>B<span>3</span></strong>)</p>\n</td>\n<td>\n<p><strong>Pellagra</strong></p>\n<p>Dermatitis, Diarrhea, Dementia</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Pantothenic acid (<strong>B<span>5</span></strong>)</p>\n</td>\n<td>\n<p><strong>Burning foot syndrome</strong> (historically called as \Nutritional melalgia\)</p>\n<div class=\page\ title=\Page 90\>\n<div class=\section\>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>Dermatitis, enteritis, alopecia, ataxia</p>\n</div>\n</div>\n</div>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<p>Pyridoxine (<strong>B<span>6</span></strong>)</p>\n</td>\n<td>\n<p>Microcytic anemia, convulsions, depression</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Biotin (<strong>B<span>7</span></strong>)</p>\n</td>\n<td>\n<p>Relatively unknown</p>\n<p>Dermatitis, atrophic glossitis, alopecia, paraesthesia, muscle pain, anorexia, hallucinations</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Folic acid (<strong>B<span>9</span></strong>)</p>\n</td>\n<td>\n<p>Megaloblastic anemia, neuropathy, neural tube defects, atrophic glossitis</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Cobalamin (<strong>B<span>12</span></strong>)</p>\n</td>\n<td>\n<p>Pernicious anemia, dementia, spinal cord degeneration, homocysteinemia</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Ascorbic acid (<strong>C</strong>)</p>\n</td>\n<td>\n<p><strong>Scurvy</strong></p>\n<p>Ecchymosis, bleeding gums, poor wound healing, fatigue, joint effusion, Scorbutic rosary</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(264, 'Biochemistry', 'PM1153. Biochemical Assay for Vitamin Deficiency State', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\308\>\n<p><strong>Vitamin</strong></p>\n</td>\n<td width=\308\>\n<p><strong>Assay and test</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Vitamin B1 (Thiamine)</p>\n</td>\n<td width=\308\>\n<p>RBC Trans-ketolase</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Vitamin B2 (Riboflavin)</p>\n</td>\n<td width=\308\>\n<p>RBC glutathione reductase</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Vitamin B6 (Pyridoxine)</p>\n</td>\n<td width=\308\>\n<p>RBC transaminase</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Vitamin B9 (Folic acid)</p>\n</td>\n<td width=\308\>\n<p>FIGLU excretion/Histidine load test</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p>Vitamin B12 (Cobalamine)</p>\n</td>\n<td width=\308\>\n<p>Serum methyl malonate level</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(265, 'Biochemistry', 'PM1131. Active Forms and Functions of Water-Soluble Vitamins', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td rowspan=\2\>\n<p>Vitamin</p>\n</td>\n<td colspan=\2\>\n<p>Coenzyme</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Name</p>\n</td>\n<td>\n<p>Function</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>B1(Thiamine)</p>\n</td>\n<td>\n<p>Thiamine pyrophosphate (TPP)</p>\n</td>\n<td>\n<p>Decarboxylation</p>\n<p>Transketolase reaction</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>B2(Riboflavin)</p>\n</td>\n<td>\n<p>Flavin-adenine-dinucelotide (FAD)</p>\n</td>\n<td>\n<p>Hydrogen Transfer</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>B3(Nicotinic acid, niacin)</p>\n</td>\n<td>\n<p>Nicotinamide-adenine dinucleotide(NAD<sup>+</sup>)</p>\n</td>\n<td>\n<p>Hydrogen Transfer</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>B5(Pantothenic acid)</p>\n</td>\n<td>\n<p>Coenzyme A</p>\n</td>\n<td>\n<p>Transfer of an acyl group</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>B6(Pyridoxine)</p>\n</td>\n<td>\n<p>Pyridoxal Phosphate(PLP)</p>\n</td>\n<td>\n<p>Transamination</p>\n<p>Decarboxylation</p>\n<p>Co-factor of glycogen phosphorylase</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>B7(Biotin)</p>\n</td>\n<td>\n<p>Biotin</p>\n</td>\n<td>\n<p>Carboxylation</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>B9(Folic acid)</p>\n</td>\n<td>\n<p>Tetrahydrofolic acid (THFA)</p>\n</td>\n<td>\n<p>Donor of methyl/formyl group</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>B12(Cobalamin)</p>\n</td>\n<td>\n<p>Methylcobalamin and Adenosylcobalamin</p>\n</td>\n<td>\n<p>Co-factor of methionine synthase and methylmalonyl&nbsp;CoA mutase</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>C (Ascorbic acid)</p>\n</td>\n<td>Ascorbic acid and dehydroascorbic acid</td>\n<td>\n<p>Oxidation-reduction</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(266, 'Biochemistry', 'PM1120. Active Forms of Vitamin A', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p>Active Form</p>\n</td>\n<td>\n<p>Function</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Retinol</p>\n</td>\n<td>\n<p>Reproduction</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Retinal</p>\n</td>\n<td>\n<p>Vision</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Retinoic acid</p>\n</td>\n<td>\n<p>Epithelial differentiation, gene expression, growth and development</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(267, 'Biochemistry', 'PM1117. Basal Metabolic Rate', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\196\>\n<p><strong>BMR is increased in</strong></p>\n</td>\n<td width=\246\>\n<p><strong>BMR is decreased in</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\196\>\n<p>Fever</p>\n</td>\n<td width=\246\>\n<p>Protein energy malnutrition</p>\n</td>\n</tr>\n<tr>\n<td width=\196\>\n<p>Hyperthyroidism</p>\n</td>\n<td width=\246\>\n<p>Hypothyroidism</p>\n</td>\n</tr>\n<tr>\n<td width=\196\>\n<p>Cold environment</p>\n</td>\n<td width=\246\>\n<p>Addison disease</p>\n</td>\n</tr>\n<tr>\n<td rowspan=\2\ width=\196\>\n<p>Cachexia</p>\n<p>&nbsp;</p>\n</td>\n<td width=\246\>\n<p>Starvation</p>\n</td>\n</tr>\n<tr>\n<td width=\196\>\n<p>Cushing’s syndrome</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(268, 'Biochemistry', 'PM1116. Calorific Value of Food Stuff', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\140\>\n<p>Food Stuff</p>\n</td>\n<td width=\198\>\n<p>Caloric Value (KCal/g)</p>\n</td>\n</tr>\n<tr>\n<td width=\140\>\n<p>Carbohydrate</p>\n</td>\n<td width=\198\>\n<p>4</p>\n</td>\n</tr>\n<tr>\n<td width=\140\>\n<p>Protein</p>\n</td>\n<td width=\198\>\n<p>4.2</p>\n</td>\n</tr>\n<tr>\n<td width=\140\>\n<p>Fat</p>\n</td>\n<td width=\198\>\n<p>9</p>\n</td>\n</tr>\n<tr>\n<td width=\140\>\n<p>Alcohol</p>\n</td>\n<td width=\198\>\n<p>7</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->');
INSERT INTO db_pearl_m (pid, subject, pearl_title, pearl_desc) VALUES
(269, 'Biochemistry,', 'PM0602. Major Sites of Absorption of Hematopoietic Factors', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ul>\n<li><strong>D</strong>uodenum + upper jejunum: <strong>I</strong>ron</li>\n<li><strong>J</strong>ejunum: <strong>F</strong>olate</li>\n<li>Terminal <strong>I</strong>leum: Vitamin <strong>B</strong>12</li>\n</ul>\n<p><strong>Mnemonic - D</strong>ude<strong> I</strong>s <strong>J</strong>ust <strong>F</strong>eeling <strong>I</strong>ll <strong>B</strong>ro</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/7b5aa9349c7142d495c2d6b273d05861x902x206.PNG\><!---->\n      </div><!---->\n    </div><!---->'),
(270, 'Community Medicine,', 'PM0089. Fat-soluble vitamins', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td rowspan=\2\><strong>Vitamin</strong></td>\n<td rowspan=\2\><strong>Active form</strong></td>\n<td rowspan=\2\><strong>Deficiency</strong></td>\n<td rowspan=\2\><strong>Toxicity</strong></td>\n<td colspan=\3\><strong>Required dietary allowance</strong></td>\n</tr>\n<tr>\n<td>Children</td>\n<td>Male</td>\n<td>Female</td>\n</tr>\n<tr>\n<td>Vitamin A</td>\n<td>\n<p>Retinol</p>\n<p>Retinal</p>\n<p>Retinoic acid</p>\n</td>\n<td>\n<p>Night blindness, xerophthalmia,</p>\n<p>Acne, psoriasis,</p>\n<p>Impaired immunity</p>\n</td>\n<td>\n<p>Affects lysosomes</p>\n<p>Acute- pseudotumor cerebrii, exfoliative dermatitis</p>\n<p>Chronic-fractures</p>\n</td>\n<td>\n<p>390-630mcg (1-9 years)</p>\n<p>770-1000mcg (10-18 years)</p>\n</td>\n<td>1000mcg&nbsp;</td>\n<td>&nbsp;\n<p>Adult - 840mcg</p>\n<p>Pregnancy - 900mcg</p>\n<p>Lactation - 950 mcg</p>\n</td>\n</tr>\n<tr>\n<td>Vitamin D</td>\n<td>1,25-Dihydro-cholecalciferol</td>\n<td>\n<p>Children- Rickets</p>\n<p>Adults-osteomalacia</p>\n</td>\n<td>Calcinosis</td>\n<td>&nbsp;\n<p>400 IU (&lt;1 year)</p>\n<p>600 IU (&gt;1 year)</p>\n</td>\n<td>&nbsp;600 IU</td>\n<td>\n<p>Adult - 600 IU</p>\n<p>Pregnancy - 600 IU</p>\n<p>Lactation - 600 IU</p>\n</td>\n</tr>\n<tr>\n<td>Vitamin E</td>\n<td>α-tocopherol</td>\n<td>\n<p>Hemolytic anemia,</p>\n<p>axonal degeneration,</p>\n<p>spinocerebellar ataxia</p>\n</td>\n<td>\n<p>Reduced platelet aggregation,&nbsp;</p>\n<p>Interferes with vitamin K</p>\n</td>\n<td>&nbsp;5-8 mg</td>\n<td>&nbsp;10 mg</td>\n<td>\n<p>Adult - 8 mg</p>\n<p>Pregnancy - 10 mg</p>\n<p>Lactation - 12 mg</p>\n</td>\n</tr>\n<tr>\n<td>Vitamin K</td>\n<td>\n<p>Menedione</p>\n<p>Menoquinone</p>\n<p>Phyloquinone</p>\n</td>\n<td>\n<p>Elevated prothrombin time and bleeding</p>\n<p>Haemorrhagic disease of newborn</p>\n</td>\n<td>\n<p>Hemolysis</p>\n<p>Hyperbilirubinemia</p>\n<p>Kernicterus</p>\n</td>\n<td>\n<p>&lt;1 year - 2-3&nbsp;µg</p>\n<p>Children - 30-50&nbsp;µg</p>\n&nbsp;</td>\n<td>55 µg&nbsp;</td>\n<td>55 µg&nbsp;</td>\n</tr>\n</tbody>\n</table>\n<p>Note:The <strong>RDA</strong> for <strong>Vitamin D</strong> in the <strong>elderly</strong> (&gt;60 years) is <strong>800 IU</strong>.</p></div><!---->\n      <!---->\n    </div><!---->'),
(271, 'Biochemistry', 'PM2327. Purine salvage deficiencies: Lesch-Nyhan syndrome and Adenosine deaminase deficiency(SCID)', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Severe Combined Immunodeficiency(SCID):</strong></p>\n<p><strong>Adenosine deaminase</strong>&nbsp;enzyme deficiency is one of the major cause of SCID. It is also called as&nbsp;<strong>Glanzmann–Riniker syndrome.</strong></p>\n<p>SCID patients are usually affected by severe bacterial, viral, or fungal infections early in life and often present with interstitial lung disease, chronic diarrhea, and failure to thrive.</p>\n<p><strong>Lesch-Nyhan syndrome:</strong></p>\n<p>It is due to deficiency of the enzyme<strong> Hypoxanthine-guanine phosphoribosyltransferase(HGPRT). </strong>It is an X-linked recessive disease.</p>\n<p>Clinical features:</p>\n<ul>\n<li><span class=\fontstyle0\>Intellectual disability</span></li>\n<li><span class=\fontstyle0\>Self-mutilation</span></li>\n<li><span class=\fontstyle0\>Aggression</span></li>\n<li>Hyperuricemia&nbsp;</li>\n<li><span class=\fontstyle0\>Gout<br></span></li>\n</ul>\n<p><span class=\fontstyle0\>Treatment:</span></p>\n<ul>\n<li><span class=\fontstyle0\>Allopurinol</span></li>\n<li><span class=\fontstyle0\>Febuxostat</span></li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(272, 'Biochemistry', 'PM2151. Operon system', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td>Control of operon/ Regulation of gene expression&nbsp;</td>\n<td>Type of operon</td>\n<td>Metabolite</td>\n<td>Final outcome</td>\n<td>Examples</td>\n</tr>\n<tr>\n<td rowspan=\2\>\n<p><strong>Negative control</strong></p>\n<p>(an active repressor <strong>inhibits transcription</strong>)</p>\n&nbsp;</td>\n<td>\n<p><strong>Inducible operons</strong></p>\n<p>(repressor bound to operon)</p>\n</td>\n<td>binds to repressor and inhibits its binding to the operon by causing a conformational change</td>\n<td>Transcription</td>\n<td><strong>lactose operon</strong></td>\n</tr>\n<tr>\n<td>\n<p><strong>Repressible operons</strong></p>\n<p>(repressor unable to bind to operon)</p>\n</td>\n<td>binds to repressor and induces its binding to the operon by causing a conformational change</td>\n<td>No&nbsp;Transcription</td>\n<td><strong>Tryptophan operon</strong></td>\n</tr>\n<tr>\n<td rowspan=\2\>\n<p><strong>Positive control</strong></p>\n<p>(an active activator/ regulator <strong>promotes transcription</strong>)</p>\n&nbsp;</td>\n<td>\n<p><strong>Inducible operons</strong></p>\n<p>(activator unable to bind to operon)</p>\n</td>\n<td>binds to activator and induces its binding to operon by causing a conformational change</td>\n<td>Transcription</td>\n<td>cAMP-CAT operon</td>\n</tr>\n<tr>\n<td>\n<p><strong>Repressible operons&nbsp;</strong></p>\n<p>(activator bound to operon)</p>\n</td>\n<td>binds to activator and inhibits its binding to operon by causing a conformational change&nbsp;</td>\n<td>No Transcription</td>\n<td>&nbsp;</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-i