(349, 'Physiology', 'PM1794. Effect of ANS in the Heart', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<table width=\323\>\n<tbody>\n<tr>\n<td>&nbsp;</td>\n<td colspan=\2\><strong>Adrenergic System</strong> &nbsp;</td>\n<td colspan=\2\><strong>Cholinergic System</strong></td>\n</tr>\n<tr>\n<td>&nbsp;</td>\n<td><strong>Action</strong></td>\n<td><strong>Receptor</strong></td>\n<td><strong>Action</strong></td>\n<td><strong>Receptor</strong></td>\n</tr>\n<tr>\n<td><strong>SA node</strong></td>\n<td>Accelerates Heart rate ++</td>\n<td>β<strong>1 &gt; </strong>β2</td>\n<td>Decelerates heart rate + +</td>\n<td><strong>M2</strong></td>\n</tr>\n<tr>\n<td><strong>Contractility</strong></td>\n<td>Increases contractility</td>\n<td>β<strong>1 &gt; </strong>β2</td>\n<td>Decreases contractility (Only atria)&nbsp;</td>\n<td><strong>M2</strong></td>\n</tr>\n<tr>\n<td><strong>Ectopic pacemakers</strong></td>\n<td>Accelerates</td>\n<td>β<strong>1 &gt; </strong>β2</td>\n<td>&nbsp;</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td><strong>Conduction velocity</strong></td>\n<td>Increases conduction velocity</td>\n<td>β<strong>1 &gt; </strong>β2</td>\n<td>Decreases conduction velocity (Only Atria, AV node)</td>\n<td><strong>M2</strong></td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(350, 'Pharmacology,', 'PM1793. Effect of ANS in the Eye', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\483\>\n<tbody>\n<tr>\n<td>&nbsp;</td>\n<td colspan=\2\><strong>Adrenergic System</strong></td>\n<td colspan=\2\><strong>Cholinergic system</strong></td>\n</tr>\n<tr>\n<td>&nbsp;</td>\n<td><strong>Action</strong></td>\n<td><strong>Receptor</strong></td>\n<td><strong>Action</strong></td>\n<td><strong>Receptor</strong></td>\n</tr>\n<tr>\n<td><strong>Iris radial muscle</strong></td>\n<td>Contraction - <strong>Mydriasis</strong></td>\n<td><strong>α1</strong></td>\n<td>---------</td>\n<td>---------</td>\n</tr>\n<tr>\n<td><strong>Iris circular muscle</strong></td>\n<td>---------</td>\n<td>---------</td>\n<td>Contraction - <strong>Miosis</strong>&nbsp;</td>\n<td><strong>M3</strong></td>\n</tr>\n<tr>\n<td><strong>Ciliary Muscle</strong></td>\n<td>Relaxation – Far vision</td>\n<td>β2</td>\n<td>Contraction – Near vision</td>\n<td><strong>M3</strong></td>\n</tr>\n<tr>\n<td><strong>Lacrimal glands</strong></td>\n<td>Secretion +</td>\n<td><strong>α</strong></td>\n<td>Secretion +</td>\n<td><strong>M3, M2</strong></td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(351, 'Physiology', 'PM1744. Agents causing contraction or relaxation of mesangial cells', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Contraction of mesangial cells:</strong></p>\n<ul>\n<li>Endothelins</li>\n<li>Angiotensin II</li>\n<li>Vasopressin</li>\n<li>Norepinephrine</li>\n<li>Platelet-activating factor</li>\n<li>Platelet-derived growth factor</li>\n<li>Thromboxane A2</li>\n<li>PGF2</li>\n<li>Leukotrienes C4 and D4</li>\n<li>Histamine</li>\n</ul>\n<p><strong>Relaxation of mesangial cells:</strong></p>\n<ul>\n<li>ANP</li>\n<li>Dopamine</li>\n<li>PGE2</li>\n<li>cAMP</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(352, 'Physiology', 'PM1323. Neurotransmitters and Hormones that influence Feeding and Satiety Centers in the Hypothalamus', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Increased Appetite (Orexigenic)</strong></p>\n</td>\n<td>\n<p><strong>Decreased Appetite (Anorexigenic)</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Neuropeptide Y</strong></p>\n<p>Agouti-related protein (AGRP)</p>\n<p>Melanin-concentrating hormone (MCH)</p>\n<p><strong>Orexins A and B</strong></p>\n<p>Endorphins</p>\n<p>Galanin</p>\n<p>Amino acids (glutamate and γ aminobutyric acid)</p>\n<p>Cortisol</p>\n<p><strong>Ghrelin</strong></p>\n<p>Growth hormone-releasing hormone (GHRH)</p>\n<p>Endocannabinoids</p>\n</td>\n<td>\n<p><strong>Melanocyte-stimulating hormone</strong> (MSH)</p>\n<p><strong>Leptin</strong></p>\n<p>Serotonin</p>\n<p>Norepinephrine</p>\n<p><strong>Corticotropin-releasing hormone (CRH)</strong></p>\n<p><strong>Insulin</strong></p>\n<p><strong>Cholecystokinin (CCK)</strong></p>\n<p>Glucagon-like peptide</p>\n<p><strong>Cocaine-and amphetamine-regulated transcript (CART)</strong></p>\n<p>Bombesin</p>\n<p>Peptide YY</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(353, 'Physiology', 'PM0998. Effects of Various Lesions and Brainstem Transections', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Label </strong></p>\n</td>\n<td>\n<p><strong>Level of transection</strong></p>\n</td>\n<td>\n<p><strong>Vagi intact</strong></p>\n</td>\n<td>\n<p><strong>Vagi cut</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>A</strong></p>\n</td>\n<td>\n<p>Complete transection above pons</p>\n</td>\n<td>\n<p>Regular breathing continues</p>\n</td>\n<td>\n<p>Regular breathing continues but depth of inspiration increases</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>B</strong></p>\n</td>\n<td>\n<p>Mid-pontine level section</p>\n</td>\n<td>\n<p>Regular breathing continues</p>\n</td>\n<td>\n<p>Apneusis develops</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>C</strong></p>\n</td>\n<td>\n<p>Transection midway between pons and medulla</p>\n</td>\n<td>\n<p>Irregular but rhythmic respiration</p>\n</td>\n<td>\n<p>Irregular but rhythmic respiration</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>D</strong></p>\n</td>\n<td>\n<p>Complete transection below medulla</p>\n</td>\n<td>\n<p>Spontaneous respiration ceases</p>\n</td>\n<td>\n<p>Spontaneous respiration ceases</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/39cb62fbde5d48f1a31a124e477fe181x1280x1749.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p class=\p2\>CP - middle cerebellar peduncle</p>\n<p class=\p2\>DRG - Dorsal group of respiratory neurons</p>\n<p class=\p2\>IC - inferior colliculus</p>\n<p class=\p2\>NPBL - nucleus parabrachialis (pneumotaxic center)</p>\n<p class=\p2\>VRG - ventral group of respiratory neurons</p>\n<p class=\p2\>4th vent - fourth ventricle.</p></div><!---->\n      <!---->\n    </div><!---->'),
(354, 'Physiology', 'PM0997. Respiratory Centres', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Respiratory center component</strong></p>\n</td>\n<td>\n<p><strong>Primary function</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Pre-Bőtzinger complex</p>\n</td>\n<td>\n<p>Pacemaker cells of spontaneous or automatic breathing</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Dorsal Respiratory Group (DRG)</p>\n</td>\n<td>\n<p>Respiratory rhythm generator (inspiratory ramp signal)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Ventral Respiratory Group (VRG)</p>\n</td>\n<td>\n<p>Overdrive mechanism</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Pneumotaxic centre</p>\n</td>\n<td>\n<p>Controls the “switch off” point of inspiratory ramp signal</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Apneustic centre</p>\n</td>\n<td>\n<p>Delays switch off signal- promotes inspiration</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Vagi</p>\n</td>\n<td>\n<p>Inhibits inspiratory discharge</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(355, 'Physiology', 'PM0922. Functions of hypothalamus', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\408\>\n<tbody>\n<tr>\n<td>\n<ul>\n<li><strong>Ventromedial</strong>\n<ul>\n<li>Satiety</li>\n</ul>\n</li>\n<li><strong>Arcuate</strong>\n<ul>\n<li>Either increase or decrease appetite</li>\n</ul>\n</li>\n<li><strong>Paraventricular nucleus</strong>\n<ul>\n<li>Oxytocin release</li>\n<li>Satiety</li>\n<li>ACTH and beta lipotropin and blood pressure</li>\n</ul>\n</li>\n<li><strong>Medial Preoptic and anterior hypothalamus</strong>\n<ul>\n<li>Body temperature regulation - <br>Anterior: responds to heat <br>Posterior: responds to cold</li>\n<li>Sweating</li>\n</ul>\n</li>\n<li><strong>Supra optic</strong>\n<ul>\n<li>Vasopressin</li>\n</ul>\n</li>\n<li><strong>Preoptic</strong>\n<ul>\n<li>&nbsp;FSH and LH via GRH</li>\n</ul>\n</li>\n<li><strong>Anterior hypothalamus</strong>\n<ul>\n<li>Osmoreceptor</li>\n</ul>\n</li>\n<li><strong>Lateral hypothalamus</strong>\n<ul>\n<li>Hunger</li>\n<li>Thirst</li>\n</ul>\n</li>\n</ul>\n<p>&nbsp;</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(356, 'Physiology', 'PM0921. Reward and punishment centers in brain', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://cdn1.dailyrounds.org/uploads/e51a34e1b7404306a6bdd3bce25f48e1x592x398.PNG\><!---->\n      </div><!---->\n    </div><!---->'),
(357, 'Physiology', 'PM0919. Divisions of thalamic nuclei', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://cdn1.dailyrounds.org/uploads/d5110100ee3242acbe4948e4d5235bd5x492x438.PNG\><!---->\n      </div><!---->\n    </div><!---->'),
(358, 'Physiology', 'PM0918. Divisions of thalamus', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Three major divisions</strong></p>\n<ul>\n<li>Anterior nuclear group</li>\n<li>Medial nuclear group</li>\n<li>Lateral nuclear group</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/2425864f3b5246a29517d34dcb964bb4x1280x2636.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\></div><!---->\n      <!---->\n    </div><!---->'),
(359, 'Physiology', 'PM0917. Functions of principal afferent systems to cerebellum', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>Afferent Tracts</td>\n<td>Transmits</td>\n</tr>\n<tr>\n<td><strong>Vestibulocerebellar</strong></td>\n<td>Vestibular impulses from labyrinths, direct and via vestibular nuclei</td>\n</tr>\n<tr>\n<td><strong>Dorsal spinocerebellar</strong></td>\n<td>Proprioceptive and exteroceptive impulses from body</td>\n</tr>\n<tr>\n<td><strong>Ventral spinocerebellar</strong></td>\n<td>Proprioceptive and exteroceptive impulses from body</td>\n</tr>\n<tr>\n<td><strong>Cuneocerebellar</strong></td>\n<td>Proprioceptive impulses, especially from head and neck</td>\n</tr>\n<tr>\n<td><strong>Tectocerebellar</strong></td>\n<td>Auditory and visual impulses via inferior and superior colliculi</td>\n</tr>\n<tr>\n<td><strong>Pontocerebellar</strong></td>\n<td>Impulses from motor and other parts of cerebral cortex via pontine nuclei</td>\n</tr>\n<tr>\n<td><strong>Olivocerebellar</strong></td>\n<td>Proprioceptive input from whole body via relay in inferior olive</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(360, 'Physiology', 'PM0915. Functional divisions of the cerebellum', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>&nbsp;</td>\n<td><strong>Dorsal Spinocerebellum</strong></td>\n<td><strong>Ventral Spinocerebellum</strong></td>\n<td><strong>Cerebrocerebellum (neocerebellum)</strong></td>\n<td><strong>Vestibulocerebellum (flocculonodular lobe)&nbsp;</strong></td>\n</tr>\n<tr>\n<td><strong>Constituents</strong></td>\n<td>Vermis (except the nodulus)</td>\n<td>The adjacent medial portion of the hemisphere</td>\n<td>The lateral portion of hemispheres</td>\n<td>Nodulus and flocculus&nbsp;</td>\n</tr>\n<tr>\n<td><strong>Afferent (input)</strong></td>\n<td>Somatosensory from the trunk, visual, auditory, and vestibular</td>\n<td>Somatosensory input from the limbs</td>\n<td>From the cerebral motor cortex, premotor, and somatosensory cortex</td>\n<td>&nbsp;Vestibular input, visual input, somatosensory from the neck</td>\n</tr>\n<tr>\n<td><strong>Efferent (output)</strong></td>\n<td>Via Fastigial nucleus to the medial descending system</td>\n<td>Via interposed nucleus to lateral corticospinal and rubrospinal systems</td>\n<td>Via dentate nucleus to motor, premotor and prefrontal cortex</td>\n<td>&nbsp;To vestibular nuclei in the brain stem (direct projection)</td>\n</tr>\n<tr>\n<td><strong>Functions &nbsp; &nbsp;</strong></td>\n<td>\n<p>Maintains posture and helps in the execution of gross movements</p>\n<p>It controls the interplay between agonist and antagonist group of muscles</p>\n</td>\n<td>Controls the&nbsp; distal muscles of the limbs (smoothens and coordinates movements)</td>\n<td>Planning the sequence of voluntary movements &nbsp; &nbsp;</td>\n<td>\n<p>&nbsp;Participates in maintaining equilibrium and posture</p>\n<p>&nbsp;</p>\n<p>Controls ocular movements</p>\n<p>It is responsible for regulating the stability of the head and body in space</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/c0baab5ab6b74bfc9431db10c9c9fdfbx720x842.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(361, 'Physiology', 'PM0907. Forms of Memory', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>From physiologic point of view memory is divided into explicit and implicit forms.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/1c881fbfcdfc4d689ab3dcd3b68aad8fx1280x802.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/9629b6b0ef314e459062d7b6674917b4x1280x928.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(362, 'Physiology,', 'PM0905. Difference between  NREM and REM Sleep', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\472\>\n<tbody>\n<tr>\n<td width=\146\>\n<p><strong>Physiological Process</strong></p>\n</td>\n<td width=\177\>\n<p><strong>NREM</strong></p>\n</td>\n<td width=\149\>\n<p><strong>REM</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\146\>\n<p>Brain activity</p>\n</td>\n<td width=\177\>\n<p>Decreases from wakefulness</p>\n</td>\n<td width=\149\>\n<p>Increases in motor and sensory areas, while other areas are similar to&nbsp;NREM</p>\n</td>\n</tr>\n<tr>\n<td width=\146\>\n<p>Heart rate</p>\n</td>\n<td width=\177\>\n<p>Slows from wakefulness</p>\n</td>\n<td width=\149\>\n<p>Increases and varies compared to&nbsp;NREM</p>\n</td>\n</tr>\n<tr>\n<td width=\146\>\n<p>Blood pressure</p>\n</td>\n<td width=\177\>\n<p>Decreases from wakefulness</p>\n</td>\n<td width=\149\>\n<p>Increases (up to 30 percent) and varies from&nbsp;NREM</p>\n</td>\n</tr>\n<tr>\n<td width=\146\>\n<p>Sympathetic nerve activity</p>\n</td>\n<td width=\177\>\n<p>Decreases from wakefulness</p>\n</td>\n<td width=\149\>\n<p>Increases significantly from wakefulness</p>\n</td>\n</tr>\n<tr>\n<td width=\146\>\n<p>Muscle tone</p>\n</td>\n<td width=\177\>\n<p>Similar to wakefulness</p>\n</td>\n<td width=\149\>\n<p>Absent</p>\n</td>\n</tr>\n<tr>\n<td width=\146\>\n<p>Blood flow to brain</p>\n</td>\n<td width=\177\>\n<p>Decreases from wakefulness</p>\n</td>\n<td width=\149\>\n<p>Increases from&nbsp;NREM, depending on brain region</p>\n</td>\n</tr>\n<tr>\n<td width=\146\>\n<p>Respiration</p>\n</td>\n<td width=\177\>\n<p>Decreases from wakefulness</p>\n</td>\n<td width=\149\>\n<p>Increases and&nbsp; varies from&nbsp;NREM, but may show brief stoppages; coughing suppressed</p>\n</td>\n</tr>\n<tr>\n<td width=\146\>\n<p>Airway resistance</p>\n</td>\n<td width=\177\>\n<p>Increases from wakefulness</p>\n</td>\n<td width=\149\>\n<p>Increases and&nbsp; varies from wakefulness</p>\n</td>\n</tr>\n<tr>\n<td width=\146\>\n<p>Body temperature</p>\n</td>\n<td width=\177\>\n<p>Is regulated at lower set point than wakefulness; shivering initiated at lower temperature than during wakefulness</p>\n</td>\n<td width=\149\>\n<p>Is not regulated; no shivering or sweating; temperature drifts toward that of the local environment</p>\n</td>\n</tr>\n<tr>\n<td width=\146\>\n<p>Sexual arousal</p>\n</td>\n<td width=\177\>\n<p>Occurs infrequently</p>\n</td>\n<td width=\149\>\n<p>Greater than&nbsp;NREM</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(363, 'Physiology', 'PM0904. Neurochemical changes in Sleep-Wake Cycle', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/b66dc216f1a34c7cb8b3c905e77a9949x1280x1914.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(364, 'Physiology', 'PM0903. Distribution of Sleep Stages in Young Adults', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\107\>\n<p><strong>Stage</strong></p>\n</td>\n<td width=\135\>\n<p><strong>% of Total Sleep Time</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\107\>\n<p>NREM 1</p>\n</td>\n<td width=\135\>\n<p>5</p>\n</td>\n</tr>\n<tr>\n<td width=\107\>\n<p>NREM 2</p>\n</td>\n<td width=\135\>\n<p>50-60</p>\n</td>\n</tr>\n<tr>\n<td width=\107\>\n<p>NREM 3 and 4</p>\n</td>\n<td width=\135\>\n<p>15-20</p>\n</td>\n</tr>\n<tr>\n<td width=\107\>\n<p>REM</p>\n</td>\n<td width=\135\>\n<p>20-25</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(365, 'Medicine,', 'PM0902. Stages of Arousal and Sleep', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Stage</strong></p>\n</td>\n<td>\n<p><strong>Behaviour</strong></p>\n</td>\n<td>\n<p><strong>EEG</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Alert wakefulness</p>\n</td>\n<td>\n<p>Awake, alert with eyes open</p>\n</td>\n<td>\n<p><strong>Beta rhythm</strong>(14-30 Hz)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Relaxed wakefulness</p>\n</td>\n<td>\n<p>Awake, relaxed with eyes closed</p>\n</td>\n<td>\n<ul>\n<li><strong>Mainly alpha rhythm</strong> (8–13 Hz)</li>\n<li>Changes to the beta rhythm in response to internal or external stimuli.</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>\n<p>Relaxed drowsiness</p>\n</td>\n<td>\n<ul>\n<li>Fatigued and tired</li>\n<li>The head droops, momentary lapses of attention</li>\n<li>Sleepy but not asleep</li>\n</ul>\n</td>\n<td>\n<p><strong>Decrease in alpha-wave</strong> amplitude and frequency</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>NREM 1 (N1) (5%)</p>\n</td>\n<td>\n<ul>\n<li><strong>Light sleep-easily aroused</strong> by moderate stimuli or even by muscle jerks</li>\n<li>Continuous lack of awareness</li>\n</ul>\n</td>\n<td>\n<ul>\n<li><strong>Mixed frequency waves&nbsp;</strong> (predominantly 2-7 Hz).</li>\n<li>Gap in alpha rhythm is filled with theta (4–8 Hz) and delta (slower than 4 Hz) activity</li>\n<li><strong>Vertex waves</strong></li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>\n<p>NREM 2 (N2) (50-60%)</p>\n</td>\n<td>\n<p>Further lack of sensitivity to activation and arousal</p>\n</td>\n<td>\n<ul>\n<li><strong>Predominant theta activity</strong> (4-7 Hz)</li>\n<li><strong>K-complex and sleep spindles</strong> occur periodically</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>\n<p>NREM 3 and 4 (N3) (15-20%)</p>\n</td>\n<td>\n<ul>\n<li><strong>Deep sleep</strong></li>\n<li>Arousal only with vigorous stimuli</li>\n<li><strong>Sleep-arousal most difficult</strong></li>\n</ul>\n</td>\n<td>\n<p><strong>Delta&gt;theta activity</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>REM (paradoxical) sleep (20-25%)</p>\n</td>\n<td>\n<ul>\n<li>50–90 min after sleep onset, episodes repeated every 60–90 min, each episode lasting about 10 min</li>\n<li><strong>Dreaming occurs, rapid eye movements,</strong> increase in brain O<sub>2 </sub>consumption</li>\n</ul>\n</td>\n<td>\n<ul>\n<li>EEG resembles that of alert awake state.</li>\n<li><strong>Saw tooth waves</strong></li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table>\n<p>Note:&nbsp;According to the new classification, stage 3 and 4 of NREM are grouped together as N3. So as per recent staging NREM has 3 stages (N1, N2, N3)</p></div><!---->\n      <!---->\n    </div><!---->'),
(366, 'Medicine,', 'PM0901. EEG Waves', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\510\>\n<tbody>\n<tr>\n<td width=\59\>\n<p><strong>EEG Waves</strong></p>\n</td>\n<td width=\69\>\n<p><strong>Frequency (in Hz)</strong></p>\n</td>\n<td width=\71\>\n<p><strong>Amplitude (in μV)</strong></p>\n</td>\n<td width=\135\>\n<p><strong>Location where it is best-recorded</strong></p>\n</td>\n<td width=\177\>\n<p><strong>Reactivity </strong></p>\n</td>\n</tr>\n<tr>\n<td width=\59\>\n<p>Alpha rhythm</p>\n</td>\n<td width=\69\>\n<p>8-13</p>\n</td>\n<td width=\71\>\n<p>50-100</p>\n</td>\n<td width=\135\>\n<p>Parieto-occipital regions</p>\n</td>\n<td width=\177\>\n<p>Adults at rest- awake but relaxed with eyes closed, wandering mind</p>\n</td>\n</tr>\n<tr>\n<td width=\59\>\n<p>Beta rhythm</p>\n</td>\n<td width=\69\>\n<p>13-30</p>\n</td>\n<td width=\71\>\n<p>5-10</p>\n</td>\n<td width=\135\>\n<p>Frontal regions</p>\n</td>\n<td width=\177\>\n<p>Adults with eyes open</p>\n</td>\n</tr>\n<tr>\n<td width=\59\>\n<p>Theta waves</p>\n</td>\n<td width=\69\>\n<p>4-8</p>\n</td>\n<td width=\71\>\n<p>&lt;200</p>\n</td>\n<td width=\135\>\n<p>Parietal and temporal regions in children;</p>\n<p>In experimental animals- hippocampus</p>\n</td>\n<td width=\177\>\n<p>Normally seen in children during moderate sleep;</p>\n<p>In adults who are in deep relaxation and meditation;</p>\n<p>Seen in excess- depression</p>\n</td>\n</tr>\n<tr>\n<td width=\59\>\n<p>Delta waves</p>\n</td>\n<td width=\69\>\n<p>0.5-4</p>\n</td>\n<td width=\71\>\n<p>20-200 (large amplitude)</p>\n</td>\n<td width=\135\>\n<p>Occipito-temporal</p>\n</td>\n<td width=\177\>\n<p>Deep sleep;</p>\n<p>Appearance in awake state- Organic brain damage</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(367, 'Medicine,', 'PM0840. Features of Motor Neuron Lesions', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\239\>\n<p><strong>Upper Motor Neuron Lesion</strong></p>\n</td>\n<td width=\239\>\n<p><strong>Lower Motor Neuron Lesion</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\239\>\n<p>Upper motor neuron typically refer to corticospinal tract neurons that innervate spinal motor neurons, but they can also include brainstem neurons that control spinal motor neurons</p>\n</td>\n<td width=\239\>\n<p>Lower motor neurons are those whose axons terminate on skeletal muscles</p>\n</td>\n</tr>\n<tr>\n<td width=\239\>\n<p>Little or no muscle wasting&nbsp;→ weakness of relatively large group of muscles</p>\n</td>\n<td width=\239\>\n<p>Muscle atrophy → muscle weakness of an individual and specific muscle</p>\n</td>\n</tr>\n<tr>\n<td width=\239\>\n<p>Hypertonia</p>\n</td>\n<td width=\239\>\n<p>Hypotonia</p>\n</td>\n</tr>\n<tr>\n<td width=\239\>\n<p>Increased or exaggerated deep tendon reflexes (hyperreflexia)</p>\n</td>\n<td width=\239\>\n<p>Hyporeflexia or areflexia</p>\n</td>\n</tr>\n<tr>\n<td width=\239\>\n<p>Spasticity</p>\n</td>\n<td width=\239\>\n<p>Flaccidity</p>\n</td>\n</tr>\n<tr>\n<td width=\239\>\n<p>Positive Babinski sign (abnormal plantar extensor reflex)</p>\n</td>\n<td width=\239\>\n<p>Fasciculations</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(368, 'Physiology', 'PM0838. Postural Reflexes and Centres', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Reflexes</strong></p>\n</td>\n<td>\n<p><strong>Integrated in</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Stretch reflex</p>\n<p>Positive supporting reaction</p>\n<p>Negative supporting reaction</p>\n</td>\n<td>\n<p>Spinal cord</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Tonic labyrinthine and tonic neck reflexes (Antigravity reflexes, attitudinal reflexes)</p>\n</td>\n<td>\n<p>Medulla</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Righting reflexes (except optical righting reflexes)</p>\n<p>Labyrinthine righting reflexes</p>\n<p>Neck righting reflexes</p>\n<p>Body on head righting reflexes</p>\n<p>Body on body righting reflexes</p>\n</td>\n<td>\n<p>Midbrain</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Conditioned reflex</p>\n<p>Optical righting reflexes</p>\n<p>Hopping and placing reaction</p>\n</td>\n<td>\n<p>Cortex</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(369, 'Physiology', 'PM0837. Summary of Muscle Reflexes', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Reflex</strong></p>\n</td>\n<td>\n<p><strong>Number of synapses</strong></p>\n</td>\n<td>\n<p><strong>Stimulus</strong></p>\n</td>\n<td>\n<p><strong>Afferent fibers</strong></p>\n</td>\n<td>\n<p><strong>Response</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Stretch reflex</p>\n<p>(knee-jerk)</p>\n</td>\n<td>\n<p>Monosynaptic</p>\n</td>\n<td>\n<p>Muscle is stretched</p>\n</td>\n<td>\n<p>Ia</p>\n</td>\n<td>\n<p>Contraction of the muscle</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Golgi tendon reflex/ Inverse stretch reflex (clasp-knife)</p>\n</td>\n<td>\n<p>Disynaptic</p>\n</td>\n<td>\n<p>Muscle contraction</p>\n</td>\n<td>\n<p>Ib</p>\n</td>\n<td>\n<p>Relaxation of muscle</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Flexor-withdrawal reflex (after touching a hot stove)</p>\n</td>\n<td>\n<p>Polysynaptic</p>\n</td>\n<td>\n<p>Pain</p>\n</td>\n<td>\n<p>II, III, and IV</p>\n</td>\n<td>\n<p>Ipsilateral flexion; contralateral extension</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->');
INSERT INTO db_pearl_m (pid, subject, pearl_title, pearl_desc) VALUES
(370, 'ENT,', 'PM0836. Central Auditory Pathway', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/a62bcecbf06942c5be3d165abac9655cx1280x2105.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>E.Coli - MA</strong> (mnemonic)</p>\n<p><strong>E</strong>ighth nerve&nbsp;</p>\n<p><strong>C</strong>ochlear nuclei&nbsp;</p>\n<p><strong>O</strong>livary complex&nbsp;</p>\n<p><strong>L</strong>ateral lemniscus&nbsp;</p>\n<p><strong>I</strong>nferior colliculus&nbsp;</p>\n<p><strong>M</strong>edial geniculate body&nbsp;</p>\n<p><strong>A</strong>uditory cortex</p>\n<p>Most of the primary auditory cortex is in the temporal lobe, but the association cortices extend over much of the insular lobe and even onto the lateral portion of the parietal lobe.</p></div><!---->\n      <!---->\n    </div><!---->'),
(371, 'Physiology', 'PM0834. Properties of Rod Vision and Cone Vision', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>Rods</td>\n<td>Cones</td>\n</tr>\n<tr>\n<td>\n<ul>\n<li>120 million</li>\n<li>More in peripheral retina</li>\n<li>High sensitivity</li>\n<li>Night vision (Scotopic vision)</li>\n<li>Low acuity</li>\n<li>Gray colour vision (achromatic vision)</li>\n<li>On activation produces hyperpolarising current</li>\n</ul>\n</td>\n<td>\n<ul>\n<li>6 million in number</li>\n<li>More Concentrated in fovea</li>\n<li>Low sensitivity</li>\n<li>Day vision (Photopic vision)</li>\n<li>High acuity</li>\n<li>Colour vision (Chromatic vision)</li>\n<li>On activation produces hyperpolarising current</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/17d09d85563542a39727b8a060b7747fx1280x2558.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(372, 'Ophthalmology,', 'PM0833. Differences between Rods and Cones', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/2e0bcea6190f41a8a96d5b7e9f3ed74fx490x475.PNG\><!---->\n      </div><!---->\n    </div><!---->'),
(373, 'Ophthalmology,', 'PM0789. Order of neurons in the visual pathway', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td colspan=\2\><strong>Order of neurons in the visual pathway</strong></td>\n</tr>\n<tr>\n<td>\n<p>First-order neurons</p>\n</td>\n<td>\n<p>Photoreceptors <br>(<strong>rods and cones</strong>)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Second-order neurons</p>\n</td>\n<td>\n<p><strong>Bipolar cells</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Third-order neurons</p>\n</td>\n<td>\n<p>Ganglion cells <br>(<strong>optic nerve</strong>)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Fourth-order neurons</p>\n</td>\n<td>\n<p>Axons from the lateral geniculate body <br>(<strong>optic radiation</strong>)</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/cdcd5b915c494c13b1a523d97fa017b0x1280x1418.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(374, 'Physiology', 'PM0770. Dual Pain Pathway', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Features</strong></p>\n</td>\n<td>\n<p><strong>Neo Spinothalamic Tract</strong></p>\n</td>\n<td>\n<p><strong>Paleo Spinothalamic Tract</strong>&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Conduction Velocity</p>\n</td>\n<td>\n<p>Fast (6 to 30 m/s)</p>\n</td>\n<td>\n<p>Slow (0.5 to 2 m/s)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Nociceptor</p>\n</td>\n<td>\n<p>Aδ</p>\n</td>\n<td>\n<p>C</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Neurotransmitter</p>\n</td>\n<td>\n<p>Glutamate</p>\n</td>\n<td>\n<p>Substance P</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Major relay of second order neuron</p>\n</td>\n<td>\n<p>Thalamus</p>\n</td>\n<td>\n<p>Reticular Nuclei, Tectum, periaqueductal region</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Thalamic Nucleus</p>\n</td>\n<td>\n<p>Ventrobasal complex</p>\n</td>\n<td>\n<p>Intralaminar Nucleus</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Projections of third order neuron</p>\n</td>\n<td>\n<p>Somatosensory cortex</p>\n</td>\n<td>\n<p>Frontal lobe, Limbic cortex, Insula</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Localisation</p>\n</td>\n<td>\n<p>Good when stimulated with Dorsal column pathway</p>\n</td>\n<td>\n<p>Poor</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Function</p>\n</td>\n<td>\n<p>Perception of Discriminative aspect of pain</p>\n</td>\n<td>\n<p>Perception of Affect component of pain</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(375, 'Physiology', 'PM0764. Somatosensory Pathways', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>Dorsal Column-Medial Lemniscal System</td>\n<td>Anterolateral System</td>\n</tr>\n<tr>\n<td>\n<ul>\n<li>Fine touch</li>\n<li>Tactile localisation</li>\n<li>Two-point discrimination</li>\n<li>Proprioception</li>\n<li>Pressure sensations related to fine degrees of judgement of pressure intensity</li>\n</ul>\n</td>\n<td>\n<ul>\n<li>Pain</li>\n<li>Thermal sensations</li>\n<li>Crude touch and pressure sensations</li>\n<li>Itch and tickle sensations</li>\n<li>Sexual sensations</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <!---->\n      </div><!---->\n    </div><!---->'),
(376, 'Physiology', 'PM0728. Cutaneous Mechanoreceptor Systems', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\526\>\n<tbody>\n<tr>\n<td width=\131\>\n<p><strong>Receptor </strong></p>\n</td>\n<td width=\90\>\n<p>Merkel’s disc</p>\n</td>\n<td width=\108\>\n<p>Meissner corpuscle</p>\n</td>\n<td width=\99\>\n<p>Ruffini end organ</p>\n</td>\n<td width=\99\>\n<p>Pacinian corpuscle</p>\n</td>\n</tr>\n<tr>\n<td width=\131\>\n<p><strong>Location</strong></p>\n</td>\n<td width=\90\>\n<p>Tip of epidermis(Basal keratinocytes)</p>\n</td>\n<td width=\108\>\n<p>Dermal papillae</p>\n</td>\n<td width=\99\>\n<p>Dermis</p>\n</td>\n<td width=\99\>\n<p>Dermis</p>\n</td>\n</tr>\n<tr>\n<td width=\131\>\n<p><strong>Best stimuli</strong></p>\n</td>\n<td width=\90\>\n<p>&nbsp;</p>\n<p>Sustained pressure and touch<span class=\Apple-converted-space\>&nbsp;</span></p>\n</td>\n<td width=\108\>\n<p>&nbsp;</p>\n<p>Slow vibration<span class=\Apple-converted-space\>&nbsp;</span></p>\n</td>\n<td width=\99\>\n<p>&nbsp;</p>\n<p>Stretch and fluttering vibration<span class=\Apple-converted-space\>&nbsp;</span></p>\n</td>\n<td width=\99\>\n<p>&nbsp;</p>\n<p>Fast vibration and deep pressure<span class=\Apple-converted-space\>&nbsp;</span></p>\n</td>\n</tr>\n<tr>\n<td width=\131\>\n<p><strong>Adaptation </strong></p>\n</td>\n<td width=\90\>\n<p>Slow</p>\n</td>\n<td width=\108\>\n<p>Rapid</p>\n</td>\n<td width=\99\>\n<p>Slow</p>\n</td>\n<td width=\99\>\n<p>Rapid</p>\n</td>\n</tr>\n<tr>\n<td width=\131\>\n<p><strong>Best frequency</strong></p>\n</td>\n<td width=\90\>\n<p>5</p>\n</td>\n<td width=\108\>\n<p>50</p>\n</td>\n<td width=\99\>\n<p>-</p>\n</td>\n<td width=\99\>\n<p>200</p>\n</td>\n</tr>\n<tr>\n<td width=\131\>\n<p><strong>Threshold for vibration</strong></p>\n</td>\n<td width=\90\>\n<p>8</p>\n</td>\n<td width=\108\>\n<p>2</p>\n</td>\n<td width=\99\>\n<p>40</p>\n</td>\n<td width=\99\>\n<p>0.01</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(377, 'Physiology', 'PM0727. Classification of Senses based on Type of Receptors', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>Types of Receptors</td>\n</tr>\n<tr>\n<td>MECHANORECEPTORS</td>\n</tr>\n<tr>\n<td>\n<ul>\n<li>Pacinian corpuscles,</li>\n<li>Meissners corpuscles</li>\n<li>Merkels discs</li>\n<li>Golgi Tendon and Muscle spindles</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>THERMORECEPTORS</td>\n</tr>\n<tr>\n<td>\n<ul>\n<li>Warm and Cold Receptors</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>NOCICEPTORS</td>\n</tr>\n<tr>\n<td>\n<ul>\n<li>Free nerve endings</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>ELECTROMAGNETIC RECEPTORS</td>\n</tr>\n<tr>\n<td>\n<ul>\n<li>Rods and Cons</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>CHEMORECEPTORS</td>\n</tr>\n<tr>\n<td>\n<ul>\n<li>Olfactory and gustatory receptors</li>\n<li>Receptors of aortic and carotid bodies</li>\n<li>Receptors in hypothalamus for Blood Glucose</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <!---->\n      </div><!---->\n    </div><!---->'),
(378, 'Physiology', 'PM0725. Classification of Sense based on source of Stimulus', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\517\>\n<tbody>\n<tr>\n<td colspan=\2\>Receptor Classification</td>\n</tr>\n<tr>\n<td>INTEROCEPTORS</td>\n<td>Baroreceptors</td>\n</tr>\n<tr>\n<td>EXTEROCEPTORS</td>\n<td>Touch receptors</td>\n</tr>\n<tr>\n<td>TELECEPTORS</td>\n<td>Rods &amp; Cones</td>\n</tr>\n<tr>\n<td>PROPRIOCEPTOS</td>\n<td>Muscle spindle &amp; Golgi tendon organs</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(379, 'Physiology,', 'PM0686. Opioid Receptors', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td>\n<p><strong>Receptor</strong></p>\n</td>\n<td>\n<p><strong>Effect</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>μ</strong></p>\n</td>\n<td>\n<p>Miosis</p>\n<p>Urine retention</p>\n<p>Sedation</p>\n<p>Slowed GI transit - constipation</p>\n<p>Supraspinal and spinal analgesia</p>\n<p>Respiratory depression</p>\n<p>Muscular rigidity</p>\n<p>Euphoria&nbsp;</p>\n<p>Modulation of hormone and neurotransmitter release</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>δ</strong></p>\n</td>\n<td>\n<p>Supraspinal and spinal analgesia&nbsp;</p>\n<p>Modulation of hormone<br>and neurotransmitter release</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>κ</strong></p>\n</td>\n<td>\n<p>Slowed GI transit - constipation</p>\n<p>Supraspinal and spinal analgesia</p>\n<p>Psychotomimetic effect- Dysphoria</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(380, 'Physiology', 'PM0685. Neurotransmitter and Location of their Nuclear Group', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Neurotransmitter</strong></p>\n</td>\n<td>\n<p><strong>Location of their nuclear group</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Norepinephrine</p>\n</td>\n<td>\n<p>Locus coeruleus</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Serotonin</p>\n</td>\n<td>\n<p>Raphe nuclei</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Dopamine</p>\n</td>\n<td>\n<p>Substantia nigra and ventral tegmental area</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Acetylcholine</p>\n</td>\n<td>\n<p>Basal forebrain complex</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>GABA</p>\n</td>\n<td>\n<p>Nucleus accumbens</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Histamine</p>\n</td>\n<td>\n<p>Hypothalamus</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(381, 'Physiology', 'PM0684. Neurotransmitters and Type of Receptor', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Neurotransmitter </strong></p>\n</td>\n<td>\n<p><strong>Receptor </strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Glutamate</p>\n</td>\n<td>\n<p>Ligand-gated ion channel-kainate,&nbsp;NMDA, AMPA</p>\n<p>Metabotropic (mGluR)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>GABA</p>\n</td>\n<td>\n<p>Ligand-gated (GABA A and GABA C)</p>\n<p>GPCR (GABA B)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Acetylcholine</p>\n</td>\n<td>\n<p>Muscarinic – GPCR</p>\n<p>Nicotinic – Ligand</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Norepinephrine</p>\n</td>\n<td>\n<p>GPCR</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Serotonin</p>\n</td>\n<td>\n<p>GPCR (except 5HT3 which is ligand gated)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Dopamine</p>\n</td>\n<td>\n<p>GPCR</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(382, 'Physiology', 'PM0683. Neurotransmitter and Catabolic Enzymes', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<table>\n<tbody>\n<tr>\n<td><strong>Neurotransmitter&nbsp;</strong></td>\n<td><strong>Major enzymes&nbsp;</strong></td>\n<td><strong>Major final end product&nbsp;</strong></td>\n</tr>\n<tr>\n<td>Epinephrine, norepinephrine, dopamine</td>\n<td>Monoamine oxidase, Catecholamine-o-methyltransferase&nbsp;</td>\n<td>Vanillyl mandelate(VMA)&nbsp;</td>\n</tr>\n<tr>\n<td>serotonin, dopamine</td>\n<td>Monoamine oxidase&nbsp;</td>\n<td>5-Hydroxy indole acetic acid(5-HIAA)&nbsp;</td>\n</tr>\n<tr>\n<td>Histamine&nbsp;</td>\n<td>Diamine oxidase, N-Methyltransferase&nbsp;</td>\n<td>Imidazoleacetic acid, N-methylimidazole-acetic acid&nbsp;</td>\n</tr>\n<tr>\n<td>GABA&nbsp;</td>\n<td>Aminotransferase&nbsp;</td>\n<td><strong>Succinate&nbsp;</strong></td>\n</tr>\n<tr>\n<td>Acetylcholine</td>\n<td>&nbsp;Acetylcholine esterase</td>\n<td>&nbsp;choline</td>\n</tr>\n</tbody>\n</table>\n<p><strong>Clinical importance:</strong></p>\n<p class=\redesignmargintopadded\>5-HIAA testing is a 24-hour urine test that is&nbsp;<strong>specific to carcinoid tumors</strong>.&nbsp;This test is the most common biochemical test for carcinoid tumors associated with carcinoid syndrome.</p>\n<p class=\redesignmargintopadded\>Symptoms like secretory diarrhoea and valvular heart disease in carcinoid syndrome are due to serotonin.</p></div><!---->\n      <!---->\n    </div><!---->'),
(383, 'Physiology', 'PM0682. Acetyl Choline Receptors', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\212\>\n<p><strong>Type</strong></p>\n</td>\n<td width=\213\>\n<p><strong>Location</strong></p>\n</td>\n</tr>\n<tr>\n<td colspan=\2\ width=\425\>\n<p><strong>Nicotinic (ionotropic) receptors</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\212\>\n<p>N<sub>M</sub></p>\n</td>\n<td width=\213\>\n<p>Neuromuscular junction</p>\n</td>\n</tr>\n<tr>\n<td width=\212\>\n<p>N<sub>N</sub></p>\n</td>\n<td width=\213\>\n<p>Autonomic ganglia</p>\n</td>\n</tr>\n<tr>\n<td colspan=\2\ width=\425\>\n<p><strong>Muscarinic (metabotropic) receptors</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\212\>\n<p>M<sub>1</sub>, M<sub>4</sub>, M<sub>5</sub></p>\n</td>\n<td width=\213\>\n<p>CNS</p>\n</td>\n</tr>\n<tr>\n<td width=\212\>\n<p>M<sub>2</sub></p>\n</td>\n<td width=\213\>\n<p>Heart</p>\n</td>\n</tr>\n<tr>\n<td width=\212\>\n<p>M<sub>3</sub></p>\n</td>\n<td width=\213\>\n<p>Glands and smooth muscle</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(384, 'Physiology', 'PM0680. Differences between Chemical and Electrical Synapses', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\144\>\n<p><strong>Property</strong></p>\n</td>\n<td width=\141\>\n<p><strong>Chemical Synapse</strong></p>\n</td>\n<td width=\140\>\n<p><strong>Electrical Synapse</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\144\>\n<p><strong>Distance between pre- and post-synaptic membranes</strong></p>\n</td>\n<td width=\141\>\n<p>20-40 nm</p>\n</td>\n<td width=\140\>\n<p>4nm</p>\n</td>\n</tr>\n<tr>\n<td width=\144\>\n<p><strong>Cytoplasmic continuity between pre- and post-synaptic cells</strong></p>\n</td>\n<td width=\141\>\n<p>No</p>\n</td>\n<td width=\140\>\n<p>Yes</p>\n</td>\n</tr>\n<tr>\n<td width=\144\>\n<p><strong>Ultrastructural components</strong></p>\n</td>\n<td width=\141\>\n<p>Presynaptic vesicles and active zones; post synaptic receptors</p>\n</td>\n<td width=\140\>\n<p>Gap junctions</p>\n</td>\n</tr>\n<tr>\n<td width=\144\>\n<p><strong>Agents of transmission</strong></p>\n</td>\n<td width=\141\>\n<p>Chemical transmitter</p>\n</td>\n<td width=\140\>\n<p>Ion current</p>\n</td>\n</tr>\n<tr>\n<td width=\144\>\n<p><strong>Synaptic delay</strong></p>\n</td>\n<td width=\141\>\n<p>Significant: atleast 0.3 ms, usually 1-5 ms or longer</p>\n</td>\n<td width=\140\>\n<p>Virtually absent</p>\n</td>\n</tr>\n<tr>\n<td width=\144\>\n<p><strong>Direction of transmission</strong></p>\n</td>\n<td width=\141\>\n<p>Unidirectional</p>\n</td>\n<td width=\140\>\n<p>Usually bidirectional</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(385, 'Physiology,', 'PM0406. Oculocardiac Reflex', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Traction on the extraocular muscles or pressure on the globe causes bradycardia, atrioventricular block, ventricular ectopy, or asystole.</p>\n<p>In particular, it is seen with traction on the <strong>medial rectus</strong> muscle, but it can occur with stimulation of any of the orbital contents, including the periosteum.</p>\n<p><strong>Reflex arc involves</strong>-</p>\n<ul>\n<li>Afferent: Orbital contents to ciliary ganglion to ophthalmic division of the <strong>trigeminal nerve</strong> to the sensory nucleus of trigeminal</li>\n<li>Efferent: <strong>Vagus nerve</strong> to the heart.</li>\n</ul>\n<p><strong>Procedures evoking the reflex-</strong></p>\n<ul>\n<li><strong>Strabismus surgery</strong> in the paediatric age (most common)&nbsp;</li>\n<li>Cataract extraction</li>\n<li>Enucleation</li>\n<li>Retinal detachment repair.</li>\n</ul>\n<p><strong>Management-</strong></p>\n<ul>\n<li>Temporary cessation of surgical stimulation until heart rate increases</li>\n<li>Adequate ventilation, oxygenation, and depth of anaesthesia</li>\n<li><strong>IV atropine</strong> (10 mcg/kg) if bradycardia persists</li>\n<li>In recalcitrant episodes, local anaesthetic infiltration of the rectus muscles.</li>\n</ul>\n<p>Pretreatment with intravenous <strong>atropine</strong> or<strong> glycopyrrolate</strong> can be effective in preventing this condition.</p>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(386, 'Psychiatry,', 'PM0380. Events occurring in NREM and REM sleep', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Events occurring during <strong>NREM&nbsp;stages 3 and 4</strong>:</p>\n<table>\n<tbody>\n<tr>\n<td>\n<p><span><strong>Events</strong></span></p>\n</td>\n<td>\n<p><span><strong>Description</strong></span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><span><strong>Somnambulism</strong></span></p>\n</td>\n<td>\n<p><span>Sleepwalking</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><span><strong>Sleep terror or Night terror</strong></span></p>\n</td>\n<td>\n<p><span>Also called pavor nocturnus.</span></p>\n<p><span>Awakens with a scream or intense anxiety. Has increased autonomic disturbances.</span></p>\n<p><span>They do not have a memory of this when they wake up.</span></p>\n<p><span>Runs in families and seen more frequently in childhood and boys.</span></p>\n<p><span>Treatment with benzodiazepines is rarely required.</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><span><strong>Nocturnal enuresis</strong></span></p>\n</td>\n<td>\n<p><span>Behavioral therapy is the mainstay.</span></p>\n<p><span>Drugs like desmopressin and imipramine are used.</span></p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p>Events occurring in <strong>REM sleep</strong>:</p>\n<table>\n<tbody>\n<tr>\n<td>\n<p><span><strong>Events</strong></span></p>\n</td>\n<td>\n<p><span><strong>Description</strong></span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><span><strong>Nightmares</strong></span></p>\n</td>\n<td>\n<p><span>Fearful sleep without autonomic disturbances that are well remembered in the morning.</span></p>\n<p><span>TCAs may be used for treatment.</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><span><strong>Nocturnal penile tumescence</strong></span></p>\n</td>\n<td>\n<p><span>A normal phenomenon that can be used to differentiate between psychogenic and organic impotence.</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><span><strong>Narcolepsy</strong></span></p>\n</td>\n<td>\n<p><span>A disorder of the REM sleep characterized by disturbed sleep in the night, leading to excessive sleep in the morning.</span></p>\n<p><span>Associated with symptoms such as cataplexy, hypnogogic and hypnopompic hallucination, and sleep paralysis.</span></p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;<strong>*Hypnogogic hallucinations</strong> are those that occur at the start of sleep and <strong>hypnopompic hallucinations</strong> are those that occur at the end of sleep.</p>\n<p>&nbsp;</p>\n<p>Others:</p>\n<table>\n<tbody>\n<tr>\n<td>\n<p><span><strong>Events</strong></span></p>\n</td>\n<td>\n<p><span><strong>Description</strong></span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><span><strong>Bruxism&nbsp;</strong>(teeth grinding)</span></p>\n</td>\n<td>\n<p><span>Occurs in stage 2 NREM sleep.</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><span><strong>Frontal lobe epilepsy</strong></span></p>\n</td>\n<td>\n<p><span>Occurs in stage 2 NREM sleep.</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><span><strong>Sleep talking</strong></span></p>\n</td>\n<td>\n<p><span>Occurs in all stages of sleep and may accompany night terrors and sleepwalking.</span></p>\n<p><span>Common in children.</span></p>\n<p><span>No treatment is necessary.</span></p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(387, 'Physiology', 'PM0200. Effect of the Autonomic Nervous System on Heart', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\141\>\n<p><strong>ANS</strong></p>\n</td>\n<td colspan=\2\ width=\285\>\n<p><strong>Effects</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\141\>\n<p>Right vagus</p>\n</td>\n<td width=\142\>\n<p>Inhibits SA node</p>\n</td>\n<td width=\143\>\n<p>Decreases the heart rate</p>\n</td>\n</tr>\n<tr>\n<td width=\141\>\n<p>Left vagus</p>\n</td>\n<td width=\142\>\n<p>Inhibits AV node</p>\n</td>\n<td width=\143\>\n<p>Slow AV conduction</p>\n</td>\n</tr>\n<tr>\n<td width=\141\>\n<p>Right Stellate ganglion</p>\n</td>\n<td width=\142\>\n<p>Stimulates SA node</p>\n</td>\n<td width=\143\>\n<p>Increases the heart rate</p>\n</td>\n</tr>\n<tr>\n<td width=\141\>\n<p>Left stellate ganglion</p>\n</td>\n<td width=\142\>\n<p>Stimulate AV node</p>\n</td>\n<td width=\143\>\n<p>Shortened AV conduction time</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(388, 'Pharmacology,', 'PM0093. Basics of autonomic nervous system', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><span><strong>Autonomic nervous system</strong></span></td>\n<td><span><strong>Para-sympathetic system</strong></span></td>\n<td><span><strong>Sympathetic system</strong></span></td>\n</tr>\n<tr>\n<td><span>Outflow</span></td>\n<td>\n<div><span>Cranio-sacral</span></div>\n<ul>\n<li>\n<div><span>Cranial nerve nuclei - III, VII, IX, X</span></div>\n</li>\n<li>\n<div><span>Sacral segments - S2 to S4</span></div>\n</li>\n</ul>\n</td>\n<td><span>Thoraco-lumbar</span>\n<ul>\n<li><span>T1 - L2</span></li>\n</ul>\n</td>\n</tr>\n<tr>\n<td><span>Pre-ganglionic receptor</span></td>\n<td><span>N<sub>n</sub> (Nicotinic neuronal)</span></td>\n<td><span>N<sub>n</sub></span></td>\n</tr>\n<tr>\n<td>\n<p><span>Pre-ganglionic neurotransmitter&nbsp;</span><span>(from CNS)</span></p>\n</td>\n<td><span>Acetylcholine</span></td>\n<td><span>Acetylcholine</span></td>\n</tr>\n<tr>\n<td><span>Post-ganglionic receptors</span></td>\n<td><span>Muscarinic or Nicotinic</span></td>\n<td><span>Alpha or Beta</span></td>\n</tr>\n<tr>\n<td>\n<p><span>Post-Ganglionic neurotransmitter&nbsp;</span><span>(to the organ)</span></p>\n</td>\n<td><span>Acetylcholine</span></td>\n<td><span>Nor-epinephrine</span><br><span>Exceptions</span>\n<ul>\n<li><span>Renal system-Dopamine</span></li>\n<li><span>Sweat and adrenal glands - Acetylcholine</span></li>\n</ul>\n</td>\n</tr>\n<tr>\n<td><span>Features</span></td>\n<td>\n<ul>\n<li><span>The ganglion is located closer to the effector organ</span></li>\n<li><span>Pre-ganglionic fibers are longer</span></li>\n</ul>\n</td>\n<td>\n<ul>\n<li><span>The ganglion is located closer to the spinal cord</span></li>\n<li><span>Post-ganglionic fibers are longer</span></li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><span>Note: The nerve roots arising from the CNS are all nicotinic cholinergic.</span></p></div><!---->\n      <!---->\n    </div><!---->'),
(389, 'Physiology', 'PM1835. Flow-volume graph/Loop', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/0d448a20301a4373b39e03a726675434x1280x3207.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\></div><!---->\n      <!---->\n    </div><!---->'),
(390, 'Medicine,', 'PM1012. Pulmonary surfactant', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>It <strong>lowers</strong> the surface <strong>tension</strong> of the&nbsp;water layer at the alveolar surface, which <strong>increases</strong> lung <strong>compliance</strong>, thereby making it easier for the lungs to expand.&nbsp;It is secreted by<strong> type II</strong> alveolar cells. It is a mixture of phospholipids and protein.</p>\n<p>4 unique apoproteins of surfactant: Surfactant proteins are (SP)-A, SP-B, SP-C, and SP-D.</p>\n<ul>\n<li><strong>SP-A</strong> - Regulation of the feedback uptake of surfactant by the type II alveolar epithelial cells that secrete it.</li>\n<li><strong>SP-B &amp; SP-C</strong> - Key protein members of the monomolecular film of surfactant.</li>\n<li><strong>SP-D</strong> - Organization of SP-B and SP-C into the surfactant layer.</li>\n</ul>\n<p>A deep breath increases its secretion by stretching the type II cells. Its concentration decreases when breaths are small.</p>\n<p>In the fetus, surfactant synthesis is variable. The surfactant may be present as early as gestational week 24 and is almost always present by gestational <strong>week 35.</strong></p></div><!---->\n      <!---->\n    </div><!---->'),
(391, 'Medicine,', 'PM1010. Lung volumes and capacities', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td>\n<p><strong>Measurement</strong></p>\n</td>\n<td>\n<p><strong>Description</strong></p>\n</td>\n<td>\n<p><strong>Normal Value</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Tidal volume (TV)</strong></p>\n</td>\n<td>\n<p>The amount of air that moves into the lungs with each inspiration (or the amount that moves out with each expiration) during quiet breathing</p>\n</td>\n<td>\n<p>500–750 mL.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Inspiratory reserve volume&nbsp;</strong><strong>(IRV)</strong></p>\n</td>\n<td>\n<p>The air inspired with a maximal inspiratory effort in excess of the TV</p>\n</td>\n<td>\n<p>∼3 L</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Expiratory reserve volume (ERV)</strong></p>\n</td>\n<td>\n<p>The volume expelled by an active expiratory effort after passive expiration</p>\n</td>\n<td>\n<p>∼1.1 L</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Residual volume (RV)</strong></p>\n</td>\n<td>\n<p>The air left in the lungs after a maximal expiratory effort</p>\n</td>\n<td>\n<p>∼1.2 L</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Total lung capacity (TLC)</strong></p>\n</td>\n<td>\n<p>IRV+TV+ERV+RV</p>\n</td>\n<td>\n<p>∼5.8 L</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Vital capacity (VC)</strong></p>\n</td>\n<td>\n<p>IRV+TV+ERV</p>\n</td>\n<td>\n<p>∼4.5 L</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Inspiratory capacity (IC)</strong></p>\n</td>\n<td>\n<p>TV+IRV</p>\n</td>\n<td>\n<p>∼3.5 L</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Expiratory capacity (EC)</strong></p>\n</td>\n<td>\n<p>TV+ERV</p>\n</td>\n<td>\n<p>∼1.6 L</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Functional residual capacity (FRC)</strong></p>\n</td>\n<td>\n<p>ERV+RV</p>\n</td>\n<td>\n<p>∼2.3 L</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/06c833b7f0ac40998f328de5bbe2816bx1279x973.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(392, 'Medicine,', 'PM1009. Acclimatization to high altitude', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://cdn1.dailyrounds.org/uploads/07859b7f9cce4e0ab7654ab13244b120x1061x606.PNG\><!---->\n      </div><!---->\n    </div><!---->'),
(393, 'Medicine,', 'PM1008. Characteristics of different types of Hypoxia', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>&nbsp;</strong></p>\n</td>\n<td>\n<p><strong>Hypoxic</strong></p>\n</td>\n<td>\n<p><strong>Anaemic</strong></p>\n</td>\n<td>\n<p><strong>Stagnant</strong></p>\n</td>\n<td>\n<p><strong>Histotoxic</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Etiology</strong></p>\n</td>\n<td>\n<p>↓ in paO<sub>2</sub></p>\n</td>\n<td>\n<p>↓ in available Hb</p>\n</td>\n<td>\n<p>O<sub>2</sub> carrying capacity is normal but O<sub>2</sub> delivery is decreased</p>\n</td>\n<td>\n<p>No O<sub>2</sub> utilization at tissue level</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Examples</strong></p>\n</td>\n<td>\n<p>High altitude, lung disease</p>\n</td>\n<td>\n<p>Anemia, CO poisoning</p>\n</td>\n<td>\n<p>Heart failure, shock, Haemorrhage</p>\n</td>\n<td>\n<p>Cyanide Poisoning</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>paO<sub>2</sub></strong></p>\n</td>\n<td>\n<p>↓</p>\n</td>\n<td>\n<p>Normal</p>\n</td>\n<td>\n<p>Normal</p>\n</td>\n<td>\n<p>Normal</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Dissolved O<sub>2 </sub>content</strong></p>\n</td>\n<td>\n<p>↓</p>\n</td>\n<td>\n<p>Normal</p>\n</td>\n<td>\n<p>Normal</p>\n</td>\n<td>\n<p>Normal</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>O<sub>2</sub> combined with Hb</strong></p>\n</td>\n<td>\n<p>↓</p>\n</td>\n<td>\n<p>↓</p>\n</td>\n<td>\n<p>Normal</p>\n</td>\n<td>\n<p>Normal</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Chemoreceptor stimulation</strong></p>\n</td>\n<td>\n<p>+</p>\n</td>\n<td>\n<p>Not stimulated</p>\n</td>\n<td>\n<p>+++</p>\n</td>\n<td>\n<p>+++</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Amount of reduced Hb</strong></p>\n</td>\n<td>\n<p>↑</p>\n</td>\n<td>\n<p>Total Hb↓, HHb↑</p>\n</td>\n<td>\n<p>↑↑↑</p>\n</td>\n<td>\n<p>↓</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Cyanosis </strong></p>\n</td>\n<td>\n<p>Can be present</p>\n</td>\n<td>\n<p>Unlikely</p>\n</td>\n<td>\n<p>Can be present</p>\n</td>\n<td>\n<p>unlikely</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(394, 'Physiology', 'PM1001. Respiratory responses to exercise', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\395\>\n<tbody>\n<tr>\n<td width=\165\>\n<p><strong>Parameter</strong></p>\n</td>\n<td width=\231\>\n<p><strong>Response</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\165\>\n<p>O<sub>2</sub> consumption</p>\n</td>\n<td width=\231\>\n<p>↑</p>\n</td>\n</tr>\n<tr>\n<td width=\165\>\n<p>CO<sub>2</sub> production</p>\n</td>\n<td width=\231\>\n<p>↑</p>\n</td>\n</tr>\n<tr>\n<td width=\165\>\n<p>Ventilation rate</p>\n</td>\n<td width=\231\>\n<p>↑ (matches O<sub>2</sub> consumption/CO<sub>2</sub> production)</p>\n</td>\n</tr>\n<tr>\n<td width=\165\>\n<p>Arterial Po<sub>2</sub> and Pco<sub>2</sub></p>\n</td>\n<td width=\231\>\n<p>No change</p>\n</td>\n</tr>\n<tr>\n<td width=\165\>\n<p>Arterial pH</p>\n</td>\n<td width=\231\>\n<p>No change in moderate exercise</p>\n<p>↓ in strenuous exercise (lactic acidosis)</p>\n</td>\n</tr>\n<tr>\n<td width=\165\>\n<p>Venous Pco<sub>2</sub></p>\n</td>\n<td width=\231\>\n<p>↑</p>\n</td>\n</tr>\n<tr>\n<td width=\165\>\n<p>Pulmonary blood flow (cardiac output)</p>\n</td>\n<td width=\231\>\n<p>↑</p>\n</td>\n</tr>\n<tr>\n<td width=\165\>\n<p>V/Q ratios</p>\n</td>\n<td width=\231\>\n<p>More evenly distributed in lung</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(395, 'Physiology,', 'PM0996. Factors affecting 2,3 -DPG', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>↑ in 2, 3-DPG</strong></td>\n<td><strong>↓ in 2, 3-DPG</strong></td>\n</tr>\n<tr>\n<td>\n<ul>\n<li>Chronic hypoxia</li>\n<li>Anemia</li>\n<li>Acclimitization (high altitude)</li>\n<li>Thyroid hormone</li>\n<li>Growth hormone</li>\n<li>Androgens</li>\n<li>Exercise</li>\n</ul>\n</td>\n<td>\n<ul>\n<li>Stored blood</li>\n<li>Acidosis</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(396, 'Physiology', 'PM0983. Structure of Airways', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\755\>\n<tbody>\n<tr>\n<td>\n<p><strong>Features</strong></p>\n</td>\n<td>\n<p><strong>Trachea </strong></p>\n</td>\n<td>\n<p><strong>Bronchi&nbsp;</strong></p>\n</td>\n<td>\n<p><strong>Terminal bronchioles </strong></p>\n</td>\n<td>\n<p><strong>Respiratory bronchioles </strong></p>\n</td>\n<td>\n<p><strong>Alveoli </strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Cartilage </strong></p>\n</td>\n<td>\n<p>Present</p>\n</td>\n<td>\n<p>Present</p>\n</td>\n<td>\n<p>Absent</p>\n</td>\n<td>\n<p>Absent</p>\n</td>\n<td>\n<p>Absent</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Smooth muscle </strong></p>\n</td>\n<td>\n<p>Little</p>\n</td>\n<td>\n<p>Little</p>\n</td>\n<td>\n<p>Maximum</p>\n</td>\n<td>\n<p>More</p>\n</td>\n<td>\n<p>Absent</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Epithelium </strong></p>\n</td>\n<td>\n<p>Pseudostratified ciliated columnar epithelium</p>\n</td>\n<td>\n<p>Pseudostratified ciliated columnar epithelium</p>\n</td>\n<td>\n<p>Ciliated Cuboidal epithelium</p>\n</td>\n<td>\n<p>Ciliated cuboidal epithelium</p>\n</td>\n<td>\n<p>Non-ciliated simple squamous epithelium</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Secretory glands </strong></p>\n</td>\n<td>\n<p>Present</p>\n</td>\n<td>\n<p>Present</p>\n</td>\n<td>\n<p>Absent</p>\n</td>\n<td>\n<p>Absent</p>\n</td>\n<td>\n<p>Absent</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(397, 'Physiology', 'PM0113. Zones of Pulmonary Blood Flow', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Zone 1</strong></p>\n<p><strong>Zero perfusion </strong></p>\n<ul>\n<li>Does not exist in normal lungs.</li>\n<li>Occurs when the Pulmonary arterial pressure becomes less than alveolar air pressure.</li>\n<li>Pulmonary capillaries become collapsed.</li>\n</ul>\n<p><strong>Zone 2</strong></p>\n<p><strong>Intermittent blood flow </strong></p>\n<ul>\n<li>This occurs during systole when the pulmonary arterial pressure raises more than alveolar air pressure.</li>\n<li>In normal lungs, this zone occurs from apex to hilum of the lungs.</li>\n</ul>\n<p><strong>Zone 3</strong></p>\n<p><strong>Continuous blood flow </strong></p>\n<ul>\n<li>Here pulmonary arterial pressure is greater than alveolar air pressure during the entire cardiac cycle.</li>\n<li>In normal lungs, this zone occurs from the middle zone of lungs to base.</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(398, 'Physiology', 'PM0105. Physiological significance of Hering-Breuer inflation and deflation reflex', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Advantage of Hering-Breuer inflation reflex</strong></p>\n<ul>\n<li>By cutting down the inspiration time-\n<ul>\n<li>Prevents overinflation of the lungs</li>\n<li>Lungs operate at lower lung volumes thus wastage of energy is prevented</li>\n</ul>\n</li>\n</ul>\n<p><strong>Advantage of Hering-Breuer deflation reflex</strong></p>\n<ul>\n<li>Prevents atelectasis of the lungs</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(399, 'Physiology', 'PM0104. Respiratory responses mediated by airway and lung receptors', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/38e7d7cee2ab441caecb6c037e97f626x833x612.PNG\><!---->\n      </div><!---->\n    </div><!---->'),
(400, 'Physiology', 'PM0103. Side effect of opioids on Respiration', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>There are NK1 receptors and μ-opioid receptors on the neurons of pre-BOTC (pre-Bötzinger complex), and substance P stimulates and opioids inhibit respiration by acting on them. Thus, depression of respiration is a side effect that limits the use of opioids in the treatment of pain.</p></div><!---->\n      <!---->\n    </div><!---->'),
(401, 'Physiology', 'PM0096. Oxygen myoglobin dissociation curve', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ul>\n<li>The shape of O2-myoglobin dissociation curve is a rectangular hyperbola. It lies to the left of OHDC.</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(402, 'Physiology', 'PM0095. P50', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>P<sub>50</sub>= the partial pressure of oxygen at which Hb is 50% saturated. Its value is about 26mmHg</p>\n<p>When the oxygen hemoglobin dissociation curve shifts to the right, P<sub>50</sub> increases.</p>\n<p>Or in other words, higher the P<sub>50</sub>, lower is the affinity of Hemoglobin for O<sub>2.</sub></p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/59e08c6c2d5c4a5ea66fb8e49162cc29x934x712.PNG\><!---->\n      </div><!---->\n    </div><!---->'),
(403, 'Physiology', 'PM0090. Hyaline membrane disease', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;Infant Respiratory Distress Syndrome(IRDS):</p>\n<ul>\n<li>Surfactant deficiency is an important cause of I<strong>nfant respiratory distress syndrome (IRDS)</strong> also known as <strong>hyaline membrane disease</strong>&nbsp;develops in infants born before their surfactant system is functional.</li>\n<li>In IRDS there is <strong>high surface tension</strong> in the lungs resulting in <strong>atelectasis.</strong></li>\n<li>During<strong> fetal life</strong>, Cl- &nbsp;is secreted along&nbsp;with fluid by the pulmonary epithelial cells. <strong>At birth</strong>, there is a shift to Na+ reabsorption by these cells via ENaC and fluid is absorbed along with Na+.</li>\n<li>Prolonged immaturity of ENaC contributes to pulmonary edema in IRDS.</li>\n<li><strong>Overproduction/ dysregulation</strong> of <strong>surfactant proteins</strong> can also lead to respiratory distress and is the cause of <strong>Pulmonary Alveolar Proteinosis (PAP)</strong>.</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(404, 'Medicine,', 'PM0087. Lung Compliance', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Compliance is the change in lung volume per unit change in the surrounding pressure.</p>\n<p>It is represented by the formula:</p>\n<p><strong>Compliance = ΔV/ΔP</strong></p>\n<p><strong>Factors Affecting Compliance</strong></p>\n<ul>\n<li><strong>Lung volume</strong>:<strong> smaller</strong> the lungs, <strong>smaller</strong> is the compliance</li>\n<li>For a given lung size, the compliance becomes less at extremes of lung volume</li>\n<li><strong>Compliance</strong> is slightly greater when measured during<strong> deflation</strong> than during in<strong>flation</strong></li>\n<li>If the surface tension is more, compliance is less</li>\n<li>Compliance measured with saline is more than compliance measured with air.</li>\n</ul>\n<p><strong>Compliance is decreased</strong>&nbsp;in conditions such as:</p>\n<ul>\n<li><strong>Pulmonary</strong>&nbsp;<strong>fibrosis</strong>: Due to increased fibrous tissue in the lung, making it less elastic, and thus less expandable.</li>\n<li><strong>Alveolar</strong>&nbsp;<strong>edema</strong>, which prevents the expansion of some alveoli.</li>\n<li>If lung remains&nbsp;<strong>unventilated</strong>&nbsp;for a long time, in a low volume lung, due to atelectasis (collapse) of lung units or increased surface tension.</li>\n<li>Increased pulmonary venous pressure.</li>\n</ul>\n<p><strong>Compliance is increased&nbsp;</strong>in conditions such as:</p>\n<ul>\n<li>Pulmonary emphysema</li>\n<li><strong>Normal</strong>&nbsp;<strong>aging</strong>&nbsp;lung</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(405, 'Physiology', 'PM0084. Important Points about Bronchial Tree', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ul>\n<li>Glands and cartilage are maximum in trachea and bronchi.</li>\n<li>Smooth muscle is maximum in terminal bronchioles.</li>\n<li>Cilia are present upto respiratory bronchiole.</li>\n<li>Clara cells—the non-ciliated cuboidal epithelial cells that secrete important defense markers and serve as progenitor cells after injury<strong>–</strong>make up a large portion of epithelial lining in the latter portions of the conducting airway (terminal bronchioles).</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(406, 'Physiology,', 'PM2416. JVP waveform and the abnormalities', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/18bbe4a977c84587903e8637a0d9303cx1280x1005.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>1) The <strong>a-wave</strong> corresponds to <strong>atrial contraction</strong>.</p>\n<table>\n<tbody>\n<tr>\n<td><strong>Abnormality</strong></td>\n<td><strong>Conditions</strong></td>\n</tr>\n<tr>\n<td><strong>Prominent a-waves</strong></td>\n<td>Pulmonary hypertension, right atrial thrombus/ mass, and tricuspid stenosis</td>\n</tr>\n<tr>\n<td><strong>Cannon a-waves</strong></td>\n<td>\n<p>Complete AV block, ventricular tachycardia, and premature atrial/ junctional/ ventricular beats</p>\n</td>\n</tr>\n<tr>\n<td><strong>Absent a-waves</strong></td>\n<td>Atrial fibrillation</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p>2) The <strong>c-wave</strong> corresponds to the <strong>bulging of tricuspid valve</strong> into right atrium during ventricular contraction.</p>\n<p>&nbsp;</p>\n<p>3) The <strong>x-descent</strong> corresponds to <strong>atrial relaxation</strong> during atrial diastole.</p>\n<table>\n<tbody>\n<tr>\n<td><strong>Abnormality</strong></td>\n<td><strong>Conditions</strong></td>\n</tr>\n<tr>\n<td><strong>Absent x-descent</strong></td>\n<td>Severe tricuspid regurgitation</td>\n</tr>\n<tr>\n<td><strong>Rapid x-descent</strong></td>\n<td>Cardiac tamponade</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p>4) The <strong>v-wave</strong> corresponds to the <strong>venous filling</strong> of right atrium.</p>\n<table>\n<tbody>\n<tr>\n<td><strong>Abnormality</strong></td>\n<td><strong>Conditions</strong></td>\n</tr>\n<tr>\n<td><strong>Dominant v-waves</strong></td>\n<td>Tricuspid regurgitation</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p>5) The <strong>y-descent</strong> corresponds to the&nbsp;<strong>passive emptying of the right atrium</strong> into right ventricle.</p>\n<table>\n<tbody>\n<tr>\n<td><strong>Abnormality</strong></td>\n<td><strong>Conditions</strong></td>\n</tr>\n<tr>\n<td><strong>Prolonged y-descent</strong></td>\n<td>Tricuspid stenosis</td>\n</tr>\n<tr>\n<td><strong>Rapid y-descent</strong></td>\n<td>\n<p>Constrictive pericarditis and isolated severe tricuspid regurgitation</p>\n</td>\n</tr>\n<tr>\n<td><strong>Absent y-descent</strong></td>\n<td>Cardiac tamponade</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->');
INSERT INTO db_pearl_m (pid, subject, pearl_title, pearl_desc) VALUES
(407, 'Physiology', 'PM2360. Cardiac pressure - volume loop', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>The volume-pressure curve represents the changes in volume and pressure in the left ventricle during a single cardiac cycle. The relationship between ventricular pressure(Y-axis) and volume (X-axis) throughout the cardiac cycle is represented by this loop.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/9e16df890d0c42a982ca9f3d4a89ee5ax1280x1425.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Phase I: Period of filling (between A and B)</p>\n<p>Phase II: Period of isovolumic contraction&nbsp;(between B and C)</p>\n<p>Phase III: Period of ejection&nbsp;(between C and D)</p>\n<p>Phase IV: Period of isovolumic relaxation&nbsp;(between D and A)</p>\n<p>where,</p>\n<ul>\n<li>A - Mitral valve opens</li>\n</ul>\n<ul>\n<li>B - Mitral valve closes</li>\n</ul>\n<ul>\n<li>C - Aortic valve opens</li>\n</ul>\n<ul>\n<li>D - Aortic valve closes</li>\n</ul>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(408, 'Physiology', 'PM2341. Cardiac action potential', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/8bcb5562f2364898be34ccc5d5699871x1280x1241.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(409, 'Physiology', 'PM2337. Pacemaker potential', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/9295c7d5637f47559417f23733dbf3bbx449x459.PNG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/432cb8746ec2493083cd12c222dc5a8bx1279x1606.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(410, 'Physiology', 'PM1830. Central and Peripheral Chemoreceptors', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>&nbsp;</td>\n<td><strong>Central chemoreceptor</strong></td>\n<td><strong>Peripheral chemoreceptor</strong></td>\n</tr>\n<tr>\n<td>Location</td>\n<td>Ventral aspect of medulla</td>\n<td>Carotid bodies (Bifurcation of common carotid artery) and aortic bodies (Arch of aorta)</td>\n</tr>\n<tr>\n<td>Stimulus</td>\n<td>Arterial PCO<sub>2</sub> and H<sup>+</sup> ions (Sensitive to changes in arterial PCo<sub>2</sub> but stimulus is H<sup>+</sup> ions in CSF)</td>\n<td>Arterial PO<sub>2</sub> (dissolved form), PCO<sub>2</sub> and H<sup>+</sup> ions</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(411, 'Medicine,', 'PM1211. ECG Leads', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>LA – Left arm: RA – Right arm: LL – Left leg: LF – Left foot</p>\n<ul>\n<li><strong>Limb leads measure the electrical activity in the frontal plane</strong></li>\n<li><strong>Chest leads measure the electrical activity in the horizontal plane<br><br><br></strong></li>\n</ul>\n<table border=\1\>\n<tbody>\n<tr>\n<td rowspan=\2\>Limb Lead</td>\n<td colspan=\2\>Location</td>\n<td rowspan=\2\>Augmented limb leads</td>\n<td rowspan=\2\>Location</td>\n<td rowspan=\2\>Chest leads</td>\n<td rowspan=\2\>Location</td>\n</tr>\n<tr>\n<td>+</td>\n<td>-</td>\n</tr>\n<tr>\n<td>I</td>\n<td>LA<br><br></td>\n<td>&nbsp;RA</td>\n<td>aVF</td>\n<td>LF +ve</td>\n<td>V1</td>\n<td>4th intercostal space. just to the right of the sternum</td>\n</tr>\n<tr>\n<td>II</td>\n<td>LL<br><br></td>\n<td>&nbsp;RA</td>\n<td>aVL</td>\n<td>LA +ve</td>\n<td>V2</td>\n<td>4th intercostal space. just to the left of the sternum</td>\n</tr>\n<tr>\n<td>III</td>\n<td>LL<br><br></td>\n<td>LA&nbsp;</td>\n<td>aVR</td>\n<td>RA +ve</td>\n<td>V3</td>\n<td>midway between V2 and V4</td>\n</tr>\n<tr>\n<td colspan=\5\ rowspan=\3\>&nbsp;</td>\n<td>V4</td>\n<td>Midclavicular line. fifth intercostal</td>\n</tr>\n<tr>\n<td>V5</td>\n<td>Anterior axillary line, same level as V4</td>\n</tr>\n<tr>\n<td>V6</td>\n<td>Midaxillary line, the same level as V4 and V5</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(412, 'Physiology', 'PM0288. Summary of Pathogenesis of Major Findings in Congestive Heart Failure', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Summary of Pathogenesis of Major Findings in Congestive Heart Failure</strong></p>\n<table width=\454\>\n<tbody>\n<tr>\n<td width=\129\>\n<p><strong>Abnormality</strong></p>\n</td>\n<td width=\325\>\n<p><strong>Cause</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\129\>\n<p>Weakness, exercise intolerance, fatigue</p>\n</td>\n<td width=\325\>\n<p>Left ventricle output inadequate to perfuse muscles; especially, failure of output to rise with exercise</p>\n</td>\n</tr>\n<tr>\n<td width=\129\>\n<p>Ankle, sacral edema</p>\n</td>\n<td width=\325\>\n<p>Increased peripheral venous pressure →increased fluid transudation.</p>\n</td>\n</tr>\n<tr>\n<td width=\129\>\n<p>Hepatomegaly</p>\n</td>\n<td width=\325\>\n<p>Increased peripheral venous pressure →increased resistance to portal flow.</p>\n</td>\n</tr>\n<tr>\n<td width=\129\>\n<p>Pulmonary congestion</p>\n</td>\n<td width=\325\>\n<p>Increased pulmonary venous pressure →pulmonary venous distention and transudation of fluid into air spaces</p>\n</td>\n</tr>\n<tr>\n<td width=\129\>\n<p>Dyspnea on exertion</p>\n</td>\n<td width=\325\>\n<p>Failure of left ventricular output to rise during exercise → increased pulmonary venous pressure</p>\n</td>\n</tr>\n<tr>\n<td width=\129\>\n<p>Paroxysmal dyspnea, pulmonary edema</p>\n</td>\n<td width=\325\>\n<p>Probably a sudden failure of left heart output to keep up with the right heart output →acute rise in pulmonary venous and capillary pressure →transudation of fluid into air spaces.</p>\n</td>\n</tr>\n<tr>\n<td width=\129\>\n<p>Orthopnea</p>\n</td>\n<td width=\325\>\n<p>Normal pooling of blood in lungs in supine position added to the already congested pulmonary vascular system; increased venous return not put out by the left ventricle. (Relieved by sitting up, raising the head of the bed, lying on extra pillows.)</p>\n</td>\n</tr>\n<tr>\n<td width=\129\>\n<p>Cardiac dilation</p>\n</td>\n<td width=\325\>\n<p>Greater ventricular end-diastolic volume.</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(413, 'Physiology', 'PM0285. Types of Blood Vessels', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Types of Blood Vessels</strong></p>\n<table>\n<tbody>\n<tr>\n<td width=\72\>\n<p><strong>TYPES</strong></p>\n</td>\n<td width=\336\>\n<p><strong>FEATURES</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\72\>\n<p>Windkessel vessels</p>\n</td>\n<td width=\336\>\n<p>Aorta and large arteries.</p>\n<p>Abundant elastic tissues in the inner and external elastic lamina</p>\n<p>Shows elastic recoil (<strong>windkessel effect</strong>) when stretched</p>\n</td>\n</tr>\n<tr>\n<td width=\72\>\n<p>Resistance vessels</p>\n</td>\n<td width=\336\>\n<p>Arterioles</p>\n<p>Have less elastic tissue and lot of smooth muscle (maximum wall thickness to lumen ratio)</p>\n<p><strong>Major site of resistance</strong> to blood flow</p>\n</td>\n</tr>\n<tr>\n<td width=\72\>\n<p>Exchange vessels</p>\n</td>\n<td width=\336\>\n<p>Capillaries</p>\n<p>Do not have smooth muscles, consist of single layer of endothelial cells surrounded by a basement membrane</p>\n<p>Site of exchange of nutrients, water and gases</p>\n<p><strong>Types</strong></p>\n<ul>\n<li>Continuous- brain, retina (have tight junctions)</li>\n<li>Fenestrated- glomeruli, choroid plexus</li>\n<li>Sinusoidal- liver, spleen</li>\n</ul>\n<p>Blood flow regulation is by contraction and relaxation of the arterioles and precapillary sphincter</p>\n</td>\n</tr>\n<tr>\n<td width=\72\>\n<p>Capacitance vessels</p>\n</td>\n<td width=\336\>\n<p>Venules &amp; veins</p>\n<p>Thin-walled, easily distensible (<strong>maximum percentage of blood volume)</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\72\>\n<p>Shunt vessels</p>\n</td>\n<td width=\336\>\n<p>AV anastomoses</p>\n<p>Have thick muscular walls with rich innervation</p>\n<p>Found in – finger tips, ear lobes</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(414, 'Physiology', 'PM0223. Factors Affecting Heart Rate', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\479\>\n<p><strong>Heart rate is accelerated by</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\479\>\n<ul>\n<li>Decreased activity of arterial baroreceptors</li>\n<li>Increased activity of atrial stretch receptors</li>\n<li>Inspiration</li>\n<li>Excitement</li>\n<li>Anger</li>\n<li>Pain</li>\n<li>Hypoxia</li>\n<li>Exercise</li>\n<li>Thyroid hormones</li>\n<li>Fever</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td width=\479\>\n<p><strong>The Heart is slowed by</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\479\>\n<ul>\n<li>Increased activity of arterial baroreceptors</li>\n<li>Expiration</li>\n<li>Grief</li>\n<li>Stimulation of pain fibers in trigeminal nerve</li>\n<li>Increased intracranial pressure</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(415, 'Physiology', 'PM0221. Humoral Regulation of BP', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/05f60d2ff68442eeb6b59339cce74ab5x692x403.PNG\><!---->\n      </div><!---->\n    </div><!---->'),
(416, 'Physiology', 'PM0220. Normal value for hemodynamic parameters', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td colspan=\2\ width=\284\>\n<p><strong>Parameter </strong></p>\n</td>\n<td width=\141\>\n<p><strong>Pressure (mmHg)</strong></p>\n</td>\n</tr>\n<tr>\n<td rowspan=\3\ width=\143\>\n<p>Arterial BP</p>\n</td>\n<td width=\141\>\n<p>Systolic</p>\n</td>\n<td width=\141\>\n<p>90-140</p>\n</td>\n</tr>\n<tr>\n<td width=\141\>\n<p>Diastolic</p>\n</td>\n<td width=\141\>\n<p>60-90</p>\n</td>\n</tr>\n<tr>\n<td width=\141\>\n<p>Mean Arterial Pressure (MAP)</p>\n</td>\n<td width=\141\>\n<p>70-105</p>\n</td>\n</tr>\n<tr>\n<td rowspan=\2\ width=\143\>\n<p>Right ventricle</p>\n</td>\n<td width=\141\>\n<p>Systolic</p>\n</td>\n<td width=\141\>\n<p>15-25</p>\n</td>\n</tr>\n<tr>\n<td width=\141\>\n<p>Diastolic</p>\n</td>\n<td width=\141\>\n<p>0-8</p>\n</td>\n</tr>\n<tr>\n<td rowspan=\3\ width=\143\>\n<p>Pulmonary artery</p>\n</td>\n<td width=\141\>\n<p>Systolic</p>\n</td>\n<td width=\141\>\n<p>15-25</p>\n</td>\n</tr>\n<tr>\n<td width=\141\>\n<p>Diastolic</p>\n</td>\n<td width=\141\>\n<p>8-15</p>\n</td>\n</tr>\n<tr>\n<td width=\141\>\n<p>Mean</p>\n</td>\n<td width=\141\>\n<p>10-20</p>\n</td>\n</tr>\n<tr>\n<td width=\143\>\n<p>Pulmonary capillary wedge pressure</p>\n</td>\n<td width=\141\>\n<p>Mean</p>\n</td>\n<td width=\141\>\n<p>6-12</p>\n</td>\n</tr>\n<tr>\n<td width=\143\>\n<p>Left atrium</p>\n</td>\n<td width=\141\>\n<p>Mean</p>\n</td>\n<td width=\141\>\n<p>6-12</p>\n</td>\n</tr>\n<tr>\n<td width=\143\>\n<p>Right atrium (central venous pressure)</p>\n</td>\n<td width=\141\>\n<p>Mean</p>\n</td>\n<td width=\141\>\n<p>2-6</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(417, 'Physiology', 'PM0219. Phases of Korotkoff Sounds', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Phase</strong></p>\n</td>\n<td>\n<p><strong>Description</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>K-1</p>\n</td>\n<td>\n<p>Tapping sound</p>\n<p>Corresponds to systolic BP</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>K-2</p>\n</td>\n<td>\n<p>Murmur (soft swishing sound)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>K-3</p>\n</td>\n<td>\n<p>Loud and clear tapping sound</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>K-4</p>\n</td>\n<td>\n<p>Muffled sound</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>K-5</p>\n</td>\n<td>\n<p>Disappearance of sounds</p>\n<p>Corresponds to Diastolic BP</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p><strong>NOTE- K-4 (muffled sound) corresponds to DBP in</strong>-</p>\n<ul>\n<li>Adults after exercise</li>\n<li>Children</li>\n<li>Hyperthyroidism</li>\n<li>Aortic regurgitation</li>\n<li>Patients with arteriovenous fistulas for hemodialysis</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(418, 'Physiology', 'PM0218. Blood Pressure and its determinants', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\239\>\n<p><strong>Type of Blood Pressure</strong></p>\n</td>\n<td width=\239\>\n<p><strong>Determinant</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\239\>\n<p>Systolic BP (SBP)</p>\n</td>\n<td width=\239\>\n<p>Stroke volume (directly proportional)</p>\n<p>Compliance of aorta (inversely proportional)</p>\n</td>\n</tr>\n<tr>\n<td width=\239\>\n<p>Diastolic BP (DBP)</p>\n</td>\n<td width=\239\>\n<p>Elastic recoil of aorta/major vessels (directly proportional)</p>\n</td>\n</tr>\n<tr>\n<td width=\239\>\n<p>Pulse pressure (PP)</p>\n</td>\n<td width=\239\>\n<p>Ratio of stroke volume to compliance of the arterial tree</p>\n</td>\n</tr>\n<tr>\n<td width=\239\>\n<p>Mean blood pressure (MAP)</p>\n</td>\n<td width=\239\>\n<p>Cardiac output</p>\n<p>Total peripheral resistance</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
