(420, 'Medicine,', 'PM0202. Augmented Unipolar Limb Leads', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\134\>\n<p><strong>Lead type</strong></p>\n</td>\n<td width=\137\>\n<p><strong>Positive input</strong></p>\n</td>\n<td width=\138\>\n<p><strong>Negative input</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\134\>\n<p>aVR</p>\n</td>\n<td width=\137\>\n<p>Right arm (VR)</p>\n</td>\n<td width=\138\>\n<p>Mean of left arm (VL) and left foot (VF) potential</p>\n</td>\n</tr>\n<tr>\n<td width=\134\>\n<p>aVL</p>\n</td>\n<td width=\137\>\n<p>Left arm (VL)</p>\n</td>\n<td width=\138\>\n<p>Mean of right arm (VR) and left foot (VF) potential</p>\n</td>\n</tr>\n<tr>\n<td width=\134\>\n<p>aVF</p>\n</td>\n<td width=\137\>\n<p>Left foot (VF)</p>\n</td>\n<td width=\138\>\n<p>Mean of right arm (VR) and left arm (VL) potential</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(421, 'Physiology', 'PM0201. Normal pattern of ECG in different Leads', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>LEADS Normal pattern of ECG in different Leads</strong></p>\n<table>\n<tbody>\n<tr>\n<td width=\211\>\n<p><strong>Lead</strong></p>\n</td>\n<td width=\215\>\n<p><strong>Features</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\211\>\n<p>aVR</p>\n</td>\n<td width=\215\>\n<p>All deflections are negative</p>\n</td>\n</tr>\n<tr>\n<td width=\211\>\n<p>aVL, aVF</p>\n</td>\n<td width=\215\>\n<p>Predominantly positive or biphasic</p>\n</td>\n</tr>\n<tr>\n<td width=\211\>\n<p>V1, V2</p>\n</td>\n<td width=\215\>\n<p>No Q wave, large S wave</p>\n</td>\n</tr>\n<tr>\n<td width=\211\>\n<p>V3</p>\n</td>\n<td width=\215\>\n<p>Biphasic</p>\n</td>\n</tr>\n<tr>\n<td width=\211\>\n<p>V4, V5, V6</p>\n</td>\n<td width=\215\>\n<p>Small Q wave, tall R wave</p>\n</td>\n</tr>\n<tr>\n<td width=\211\>\n<p>Leads I, II, III</p>\n</td>\n<td width=\215\>\n<p>All positive deflection, largest in lead II</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(422, 'Physiology', 'PM0199. Time of impulse arrival at different regions of the heart', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\369\>\n<tbody>\n<tr>\n<td><strong>Regions</strong></td>\n<td><strong>Time (in seconds)</strong></td>\n</tr>\n<tr>\n<td>SA node</td>\n<td>0</td>\n</tr>\n<tr>\n<td>AV node</td>\n<td>0.03 (from SA node to AV node)</td>\n</tr>\n<tr>\n<td><strong>Bundle of His&nbsp; </strong></td>\n<td><strong>0.12 (AV nodal conduction time or AV nodal delay)</strong></td>\n</tr>\n<tr>\n<td>Ventricular septum</td>\n<td>0.16 (conduction time through Bundle of His)</td>\n</tr>\n<tr>\n<td>Endocardium</td>\n<td>0.17-0.19 (starting of the ventricular depolarization</td>\n</tr>\n<tr>\n<td>Epicardium</td>\n<td>0.21-0.22 (complete depolarization of the heart)</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(423, 'Physiology', 'PM0198. Conduction Speeds in Cardiac Tissue', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td width=\134\>\n<p><strong>Tissue</strong></p>\n</td>\n<td width=\150\>\n<p><strong>Conduction rate(m/s)</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\134\>\n<p>SA Node</p>\n</td>\n<td width=\150\>\n<p>0.05</p>\n</td>\n</tr>\n<tr>\n<td width=\134\>\n<p>Atrial pathways</p>\n</td>\n<td width=\150\>\n<p>1</p>\n</td>\n</tr>\n<tr>\n<td width=\134\>\n<p>AV Node</p>\n</td>\n<td width=\150\>\n<p>0.05</p>\n</td>\n</tr>\n<tr>\n<td width=\134\>\n<p>Bundle of His</p>\n</td>\n<td width=\150\>\n<p>1</p>\n</td>\n</tr>\n<tr>\n<td width=\134\>\n<p>Purkinje system</p>\n</td>\n<td width=\150\>\n<p>4</p>\n</td>\n</tr>\n<tr>\n<td width=\134\>\n<p>Ventricular muscle</p>\n</td>\n<td width=\150\>\n<p>1</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(424, 'Physiology', 'PM0197. Firing rate of SA Node and Latent Pacemakers in the Heart', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\352\>\n<tbody>\n<tr>\n<td>\n<p><strong>Location</strong></p>\n</td>\n<td>\n<p><strong>Intrinsic firing rate(impulses/min)</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>SA Node</p>\n</td>\n<td>70-80</td>\n</tr>\n<tr>\n<td>\n<p>AV node</p>\n</td>\n<td>\n<p>40-60</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Bundle of His</p>\n</td>\n<td>\n<p>40</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Purkinje fibers</p>\n</td>\n<td>\n<p>15-20</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Ventricles</p>\n</td>\n<td>\n<p>~20</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(425, 'Physiology', 'PM0171. Heart sounds', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\48\>\n<p><strong>Heart Sound</strong></p>\n</td>\n<td width=\85\>\n<p><strong>Frequency</strong></p>\n</td>\n<td width=\71\>\n<p><strong>Duration</strong></p>\n</td>\n<td width=\166\>\n<p><strong>Associated Event</strong></p>\n</td>\n<td width=\92\>\n<p><strong>ECG Correlation</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\48\>\n<p>S1</p>\n</td>\n<td width=\85\>\n<p>25-45 Hz</p>\n</td>\n<td width=\71\>\n<p>0.15 sec</p>\n</td>\n<td width=\166\>\n<p>Closure of AV Valves-Onset of Ventricular systole</p>\n</td>\n<td width=\92\>\n<p>Later half of R wave</p>\n</td>\n</tr>\n<tr>\n<td width=\48\>\n<p>S2</p>\n</td>\n<td width=\85\>\n<p>50 Hz</p>\n</td>\n<td width=\71\>\n<p>0.12 sec</p>\n</td>\n<td width=\166\>\n<p>Closure of Semilunar Valves-Onset of ventricular diastole</p>\n</td>\n<td width=\92\>\n<p>Later half of T wave</p>\n</td>\n</tr>\n<tr>\n<td width=\48\>\n<p>S3</p>\n</td>\n<td width=\85\>\n<p>Low</p>\n</td>\n<td width=\71\>\n<p>0.1 sec</p>\n</td>\n<td width=\166\>\n<p>First rapid filling phase of ventricular diastole</p>\n</td>\n<td width=\92\>\n<p>Between the T and P wave</p>\n</td>\n</tr>\n<tr>\n<td width=\48\>\n<p>S4</p>\n</td>\n<td width=\85\>\n<p>Below 20 Hz</p>\n</td>\n<td width=\71\>\n<p>0.1 sec</p>\n</td>\n<td width=\166\>\n<p>Last rapid filling phase of ventricular diastole due to atrial contraction</p>\n</td>\n<td width=\92\>\n<p>Following P wave</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(426, 'Medicine,', 'PM0170. Cardiac parameters', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\154\>\n<p><strong>Parameter</strong></p>\n</td>\n<td width=\154\>\n<p><strong>Definition</strong></p>\n</td>\n<td width=\154\>\n<p><strong>Normal Value</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\154\>\n<p>Stroke Volume (SV)</p>\n</td>\n<td width=\154\>\n<p>Amount of blood ejected by each ventricle per stroke</p>\n</td>\n<td width=\154\>\n<p>70-90 ml</p>\n</td>\n</tr>\n<tr>\n<td width=\154\>\n<p>End-Diastolic volume (EDV)</p>\n</td>\n<td width=\154\>\n<p>Amount of blood in the ventricle at the end of diastole</p>\n</td>\n<td width=\154\>\n<p>130 ml</p>\n</td>\n</tr>\n<tr>\n<td width=\154\>\n<p>End-Systolic Volume</p>\n</td>\n<td width=\154\>\n<p>EDV-SV</p>\n</td>\n<td width=\154\>\n<p>~50 ml</p>\n</td>\n</tr>\n<tr>\n<td width=\154\>\n<p>Ejection Fraction</p>\n</td>\n<td width=\154\>\n<p>(SV/EDV) x100</p>\n</td>\n<td width=\154\>\n<p>55%-68%</p>\n</td>\n</tr>\n<tr>\n<td width=\154\>\n<p>Cardiac Output (CO)</p>\n</td>\n<td width=\154\>\n<p>SV x HR</p>\n</td>\n<td width=\154\>\n<p>~5 L/min</p>\n</td>\n</tr>\n<tr>\n<td width=\154\>\n<p>Cardiac Index</p>\n</td>\n<td width=\154\>\n<p>CO/BSA (m2)</p>\n</td>\n<td width=\154\>\n<p>3.2 L/min/m2</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>BSA - Body surface area</p>\n<p>HR - Heart rate</p></div><!---->\n      <!---->\n    </div><!---->'),
