(439, 'Physiology', 'PM1283. Daily GIT secretions', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Type of secretion</strong></p>\n</td>\n<td>\n<p><strong>Daily volume (mL)</strong></p>\n</td>\n<td>\n<p><strong>pH</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Saliva</p>\n</td>\n<td>\n<p>1500</p>\n</td>\n<td>\n<p>6.0-7.0</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Gastric secretion</p>\n</td>\n<td>\n<p>1500-2500</p>\n</td>\n<td>\n<p>1.0-3.5 (Most acidic)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Pancreatic secretion</p>\n</td>\n<td>\n<p>1000</p>\n</td>\n<td>\n<p>8.0-8.3</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Bile</p>\n</td>\n<td>\n<p>500</p>\n</td>\n<td>\n<p>7.8</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Small intestine secretion</p>\n</td>\n<td>\n<p>1800</p>\n</td>\n<td>\n<p>7.5-8.0</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Brunner’s gland secretion</p>\n</td>\n<td>\n<p>200</p>\n</td>\n<td>\n<p>8.0-8.9 (most alkaline)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Large intestine secretion</p>\n</td>\n<td>\n<p>200</p>\n</td>\n<td>\n<p>7.5-8.0</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Total</p>\n</td>\n<td>\n<p>6700</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(440, 'Physiology', 'PM1282. Regulation of pancreatic secretion', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Stimulant</strong></p>\n</td>\n<td>\n<p><strong>Acts on</strong></p>\n</td>\n<td>\n<p><strong>The second messenger involved</strong></p>\n</td>\n<td>\n<p><strong>Effect</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Secretin</p>\n<p>(S cells)</p>\n</td>\n<td>\n<p>Pancreatic ductal cell</p>\n</td>\n<td>\n<p>cAMP</p>\n</td>\n<td>\n<p>↑HCO3- in secretion</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>CCK</p>\n<p>(I cells)</p>\n</td>\n<td>\n<p>Acinar cell</p>\n</td>\n<td>\n<p>Phospholipase C (IP3/DAG)</p>\n</td>\n<td>\n<p>↑ release of zymogen granules (enzymes)</p>\n<p>Potentiates effect of secretin</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>ACh</p>\n<p>(vagi)</p>\n</td>\n<td>\n<p>Acinar cell</p>\n</td>\n<td>\n<p>Phospholipase C (IP3/DAG)</p>\n</td>\n<td>\n<p>↑ release of zymogen granules (enzymes)</p>\n<p>Potentiates effect of secretin</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(441, 'Physiology', 'PM1280. Phase of gastric secretion', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Phase</strong></p>\n</td>\n<td>\n<p><strong>% of HCl secretion</strong></p>\n</td>\n<td>\n<p><strong>Stimuli </strong></p>\n</td>\n<td>\n<p><strong>Mechanisms </strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Cephalic</p>\n</td>\n<td>\n<p>30</p>\n</td>\n<td>\n<p>Conditioning, smell, taste, chewing</p>\n</td>\n<td>\n<p>Vagus → Parietal cell</p>\n<p>Vagus →Gastrin → Parietal cell</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Gastric</p>\n</td>\n<td>\n<p>60</p>\n</td>\n<td>\n<p>Distension</p>\n</td>\n<td>\n<p>Vagus →Parietal cell</p>\n<p>Vagus →Gastrin →Parietal cell</p>\n</td>\n</tr>\n<tr>\n<td>&nbsp;</td>\n<td>&nbsp;</td>\n<td>\n<p>Distension of antrum</p>\n</td>\n<td>\n<p>Local reflex →Gastrin →Parietal cell</p>\n</td>\n</tr>\n<tr>\n<td>&nbsp;</td>\n<td>&nbsp;</td>\n<td>\n<p>Amino acids, small peptides in the gastric lumen</p>\n</td>\n<td>\n<p>Gastrin →Parietal cell</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Intestinal</p>\n</td>\n<td>\n<p>10</p>\n</td>\n<td>\n<p>Amino acids, small peptides in gastric lumen</p>\n</td>\n<td>\n<p>Gastrin →Parietal cell</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>All phases</p>\n</td>\n<td>&nbsp;</td>\n<td>&nbsp;</td>\n<td>\n<p>Gastrin → ECL cells →Histamine</p>\n<p>ACh →ECL cell →Histamine</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(442, 'Physiology', 'PM1279. Factors inhibiting gastric secretion', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Inhibiting factors</strong></p>\n</td>\n<td>\n<p><strong>Mechanism </strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Low pH (&lt; 3.0) in the stomach</strong></p>\n</td>\n<td>\n<p>When the pH of the stomach contents is &lt;3.0, <strong>gastrin secretion is inhibited and, by negative feedback</strong>, inhibits further H<sup>+</sup>&nbsp;secretion.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Somatostatin</strong></p>\n</td>\n<td>\n<p>Inhibits gastric H<sup>+</sup>&nbsp;secretion by a direct pathway and an indirect pathway.</p>\n<p><strong>Direct pathway→ </strong>somatostatin binds to receptors on the parietal cell that are coupled to adenylyl cyclase via a <strong>Gi </strong>protein, thus inhibiting adenylyl cyclase and <strong>decreasing cAMP </strong>levels. In this pathway, somatostatin antagonizes the stimulatory action of histamine on H+ secretion.</p>\n<p><strong>Indirect pathway</strong>→ somatostatin <strong>inhibits release of histamine and gastrin, </strong>thus decreasing H+ secretion indirectly.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Prostaglandins</strong></p>\n</td>\n<td>\n<p>Inhibit gastric H+ secretion by activating a <strong>Gi </strong>protein, inhibiting adenylyl cyclase and <strong>decreasing cAMP levels.</strong></p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->');
INSERT INTO db_pearl_m (pid, subject, pearl_title, pearl_desc) VALUES
(443, 'Physiology', 'PM1278. Stimuli for gastric secretion', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Stimuli</strong></p>\n</td>\n<td>\n<p><strong>Mechanism </strong></p>\n</td>\n<td>\n<p><strong>Applied aspects</strong></p>\n</td>\n</tr>\n<tr>\n<td rowspan=\2\>\n<p><strong>Vagal stimulation</strong></p>\n</td>\n<td>\n<p><strong>Direct pathway</strong></p>\n<p><strong>Vagus nerve innervates parietal cells </strong>and stimulates H<sup>+ </sup>secretion directly.</p>\n<p>The neurotransmitter at these synapses is <strong>Ach, </strong>the receptor on the parietal cells is <strong>muscarinic </strong>(M3)</p>\n<p>The second messengers for CCK are <strong>IP<sub>3 </sub></strong>and <strong>increased intracellular [Ca<sup>2+</sup>].</strong></p>\n</td>\n<td>\n<p><strong>Atropine, </strong>a cholinergic muscarinic antagonist, inhibits H<sup>+</sup> secretion by blocking the direct pathway, which uses ACh as a neurotransmitter. However, it does not block H<sup>+ </sup>secretion completely because it does not inhibit the indirect pathway, which uses GRP as a neurotransmitter</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Indirect pathway </strong></p>\n<p><strong>Vagus nerve innervates G cells </strong>and stimulates gastrin secretion, which then stimulates H<sup>+ </sup>secretion.</p>\n<p>The neurotransmitter at these synapses is <strong>GRP </strong>(not ACh).</p>\n</td>\n<td>\n<p><strong>Vagotomy </strong>eliminates both direct and indirect pathways.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Gastrin</strong></p>\n</td>\n<td>\n<p>Released in response to eating a meal (small peptides, distension of the stomach, vagal stimulation).</p>\n<p>Stimulates H<sup>+</sup>&nbsp;secretion by interacting with the cholecystokinin B (CCK<sub>B</sub>) receptor on the parietal cells.</p>\n<p>The second messenger for gastrin on the parietal cell is IP<sub>3</sub>/Ca<sup>2+.</sup></p>\n</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>\n<p><strong>Histamine</strong></p>\n</td>\n<td>\n<p>Released from enterochromaffin-like (ECL) cells in the gastric mucosa and diffuses to the nearby parietal cells.</p>\n<p>Stimulates H<sup>+</sup> secretion by activating <strong>H<sub>2 </sub>receptors </strong>on the parietal cell membrane.</p>\n<p>The H<sub>2</sub>&nbsp;receptor is coupled to adenylyl cyclase via a Gs protein.</p>\n<p>The second messenger for histamine is <strong>cAMP.</strong></p>\n</td>\n<td>\n<p>H<sub>2</sub>&nbsp;receptor–blocking drugs, such as <strong>cimetidine, </strong>inhibit H<sup>+ </sup>secretion by blocking the stimulatory effect of histamine.</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(444, 'Physiology', 'PM1277. Cells of the Gastric Glands', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Cell Type</strong></p>\n</td>\n<td>\n<p><strong>Part of stomach</strong></p>\n</td>\n<td>\n<p><strong>Secretion</strong></p>\n</td>\n<td>\n<p><strong>Stimulus for secretion</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Parietal cells</p>\n</td>\n<td>\n<p>Fundus, Body</p>\n</td>\n<td>\n<p>HCl</p>\n<p>Intrinsic factor</p>\n</td>\n<td>\n<p>Gastrin</p>\n<p>Vagal stimulation (Ach)</p>\n<p>Histamine</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Chief cells</p>\n</td>\n<td>\n<p>Fundus, Body</p>\n</td>\n<td>\n<p>Pepsinogen</p>\n<p>Gastric lipase</p>\n</td>\n<td>\n<p>Vagal stimulation</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Mucous cells</p>\n</td>\n<td>\n<p>Antrum, cardia, pylorus</p>\n</td>\n<td>\n<p>Mucous</p>\n</td>\n<td>\n<p>Vagal stimulation</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>G cells</p>\n</td>\n<td>\n<p>Antrum</p>\n</td>\n<td>\n<p>Gastrin</p>\n</td>\n<td>\n<p>Vagal stimulation (via GRP)</p>\n<p>Oligopeptides</p>\n<p>Inhibited by somatostatin</p>\n<p>Inhibited by H<sup>+</sup> in stomach</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Enterochromaffin like cells (ECL)</p>\n</td>\n<td>\n<p>Fundus</p>\n</td>\n<td>\n<p>Histamine</p>\n</td>\n<td>\n<p>Gastrin</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(445, 'Physiology', 'PM2330. Formulae in renal physiology', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><div class=\page\ title=\Page 355\>\n<div class=\section\>\n<table><colgroup><col><col><col> </colgroup>\n<tbody>\n<tr>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>Term</p>\n</div>\n</div>\n</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>Equation</p>\n</div>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>Clearance rate</p>\n</div>\n</div>\n</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>U<sub>s</sub> ×V / P<sub>s</sub></p>\n</div>\n</div>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>&nbsp;</p>\n</div>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>Glomerular filtration rate</p>\n</div>\n</div>\n</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>U<sub>inulin</sub> × V / P<sub>inulin</sub></p>\n</div>\n</div>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>&nbsp;</p>\n</div>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>Clearance ratio</p>\n</div>\n</div>\n</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>C<sub>s</sub>/C<sub>inulin</sub></p>\n</div>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>Effective renal plasma flow</p>\n</div>\n</div>\n</td>\n<td>\n<div class=\layoutArea\>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>E<sub>RPF</sub>=C<sub>PAH</sub>=U<sub>PAH</sub> ×V / P<sub>PAH</sub></p>\n</div>\n</div>\n</div>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>&nbsp;</p>\n</div>\n</div>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>&nbsp;</p>\n</div>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>Renal blood flow</p>\n</div>\n</div>\n</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>RPF / (1− Hematocrit)</p>\n</div>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>Excretion rate</p>\n</div>\n</div>\n</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>U<sub>s</sub> ×V</p>\n</div>\n</div>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>&nbsp;</p>\n</div>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>Reabsorption rate</p>\n</div>\n</div>\n</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>=Filtered load −Excretion rate</p>\n<p>=(GFR×P<sub>s</sub>)−(U<sub>s</sub> ×V)</p>\n</div>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>Secretion rate</p>\n</div>\n</div>\n</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>Excretion rate−Filtered load</p>\n</div>\n</div>\n</td>\n</tr>\n</tbody>\n</table>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>Cs - clearance rate of substance “s”</p>\n<p>EPAH&nbsp; - PAH extraction ratio</p>\n<p>ERPF - effective renal plasma flow</p>\n<p>GFR - glomerular filtration rate</p>\n<p>P - plasma concentration</p>\n<p>PAH - para-aminohippuric acid</p>\n<p>PPAH - renal arterial PAH concentration</p>\n<p>RBF - renal blood flow</p>\n<p>RPF - renal plasma flow</p>\n<p>S - a substance</p>\n<p>U - urine concentration</p>\n<p>V - urine flow rate</p>\n<p>VPAH - renal venous PAH concentration</p>\n</div>\n</div>\n</div>\n</div></div><!---->\n      <!---->\n    </div><!---->'),
(446, 'Medicine,', 'PM1361. Effects of changes in Starling Forces on GFR Renal Plasma Flow, and Filtration Fraction', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>&nbsp;Effect</strong></p>\n</td>\n<td>\n<p><strong>GFR</strong></p>\n</td>\n<td>\n<p><strong>RPF</strong></p>\n</td>\n<td>\n<p><strong>FF(GFR/RPF)</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Constriction of afferent arteriole (eg. Sympathetic stimulation) </strong></p>\n</td>\n<td>\n<p>↓</p>\n<p>(caused by ↓P<sub>G</sub>)</p>\n</td>\n<td>\n<p>↓</p>\n</td>\n<td>\n<p>No change</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Constriction of efferent arteriole (eg angiotensin II)</strong></p>\n</td>\n<td>\n<p>↑</p>\n<p>(caused by ↑P<sub>G</sub>)</p>\n</td>\n<td>\n<p>↓</p>\n</td>\n<td>\n<p>↑</p>\n<p>(↑GFR/↓RPF)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Increased plasma protein</strong></p>\n</td>\n<td>\n<p>↓</p>\n<p>(caused by↑π <sub>G</sub>)</p>\n</td>\n<td>\n<p>No change</p>\n</td>\n<td>\n<p>↓</p>\n<p>(↓GFR/unchanged RPF)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Decreased plasma protein</strong></p>\n</td>\n<td>\n<p>↑</p>\n</td>\n<td>\n<p>-</p>\n</td>\n<td>\n<p>&nbsp;↑</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Ureteral stone / constriction of the ureter</strong></p>\n</td>\n<td>\n<p>↓</p>\n<p>(caused by ↑P<sub>B</sub>)</p>\n</td>\n<td>\n<p>No change</p>\n</td>\n<td>\n<p>↓</p>\n<p>(↓GFR/unchanged RPF)</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Note: <strong>PB,</strong> Bowman’s capsule hydrostatic pressure; <strong>πG</strong>, glomerular capillary colloid osmotic pressure; <strong>PG</strong>, glomerular capillary hydrostatic pressure;&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(447, 'Physiology', 'PM1360. Factors Affecting GFR', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Determinants</strong></p>\n</td>\n<td>\n<p><strong>Physiological/Pathophysiological Causes</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>↓K<sub>f</sub>&nbsp;(filtration coefficient)→ ↓GFR</p>\n</td>\n<td>\n<p>Renal disease, diabetes mellitus, hypertension</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>↑P<sub>B</sub>&nbsp;→ ↓GFR</p>\n</td>\n<td>\n<p>Urinary tract obstruction (e.g., kidney stones)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>↑π<sub>G </sub>→ ↓GFR</p>\n</td>\n<td>\n<p>↓ Renal blood flow&nbsp;increased plasma proteins</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>↓P<sub>G</sub>&nbsp;→ ↓GFR</p>\n<p>a) ↑afferent arteriolar resistance (R<sub>A</sub>&nbsp;)→ ↓P<sub>G</sub></p>\n</td>\n<td>\n<p>↑ Sympathetic activity, (e.g., norepinephrine, endothelin)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>b) ↓systemic arterial pressure(A<sub>P</sub>&nbsp;)→ ↓P<sub>G</sub></p>\n</td>\n<td>\n<p>&nbsp;Arterial pressure</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>c) ↓efferent arteriolar resistance (R<sub>E </sub>)→ ↓P<sub>G</sub></p>\n</td>\n<td>\n<p>↓ Angiotensin II (drugs that block angiotensin II formation)</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>P<sub>G :&nbsp;</sub>Glomerular hydrostatic pressure</p>\n<p>π<sub>B :&nbsp;</sub>Colloid osmotic pressure of the proteins in Bowman’s capsule</p>\n<p>P<sub>B :&nbsp;</sub>Hydrostatic pressure in Bowman’s capsule</p>\n<p>π<sub>G :&nbsp;</sub>Colloid osmotic pressure of glomerular capillary plasma proteins&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(448, 'Physiology', 'PM1356. Differences between cortical and juxtamedullary nephron', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td>\n<p><strong>Features </strong></p>\n</td>\n<td>\n<p><strong>Cortical nephron </strong></p>\n</td>\n<td>\n<p><strong>Juxtamedullary nephron </strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Location of glomerulus </strong></p>\n</td>\n<td>\n<p>Upper region of cortex</p>\n</td>\n<td>\n<p>Near junction of cortex and medulla</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>% of total nephrons</strong></p>\n</td>\n<td>\n<p>85%</p>\n</td>\n<td>\n<p>15%</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Size of glomeruli </strong></p>\n</td>\n<td>\n<p>small</p>\n</td>\n<td>\n<p>larger</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Loop of Henle</strong></p>\n</td>\n<td>\n<p>Short</p>\n<p>extend up to outer layer of medulla</p>\n</td>\n<td>\n<p>Long</p>\n<p>extend deep into the medulla</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Efferent arterioles</strong></p>\n</td>\n<td>\n<p>Large diameter</p>\n<p>break into peritubular capillaries</p>\n</td>\n<td>\n<p>Small diameter</p>\n<p>continue as vasa recta</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Rate of filtration </strong></p>\n</td>\n<td>\n<p>Slow</p>\n</td>\n<td>\n<p>High</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Major function</strong></p>\n</td>\n<td>\n<p>Excretion of waste products</p>\n<p>in the urine</p>\n</td>\n<td>\n<p>Concentration of urine by</p>\n<p>countercurrent mechanism</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(449, 'Physiology', 'PM1355. Difference between PCT and DCT', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>PCT</strong></p>\n</td>\n<td>\n<p><strong>DCT</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Brush border present in the apical membrane</p>\n</td>\n<td>\n<p>No brush border</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Cytoplasmic carbonic anhydrase (type 2 &amp; type 4 ) present</p>\n</td>\n<td>\n<p>&nbsp;Cytoplasmic carbonic anhydrase (type 2 only)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Has ‘leaky’ tight junctions</p>\n</td>\n<td>\n<p>Has ‘tight’ tight junctions</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Paracellular transport is possible via leaky tight junctions</p>\n</td>\n<td>\n<p>No paracellular transport</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(450, 'Physiology', 'PM1347. Summary of Stimuli affecting Vasopressin Secretion', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Increased vasopressin secretion</strong></p>\n</td>\n<td>\n<p><strong>Decreased vasopressin secretion</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Increased effective osmotic pressure of the plasma</p>\n<p>Decreased ECF volume</p>\n<p>Pain, emotions, stress, exercise</p>\n<p>Nausea and vomiting</p>\n<p>Standing</p>\n<p>Angiotensin II</p>\n<p>Clofibrate, carbamazepine</p>\n</td>\n<td>\n<p>Decreased effective osmotic pressure of the plasma</p>\n<p>Increased ECF volume</p>\n<p>Alcohol</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(451, 'Physiology', 'PM1344. Factors that affect renin secretion', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Stimulatory</strong></p>\n<ul>\n<li>Increased sympathetic activity via renal nerves</li>\n<li>Increased circulating catecholamines</li>\n<li>Prostaglandins</li>\n<li>Na<sup>+ </sup>depletion</li>\n<li>Diuretics</li>\n<li>Upright posture</li>\n<li>Hemorrhage</li>\n<li>Hypotension</li>\n<li>Dehydration</li>\n<li>Cardiac failure</li>\n<li>Cirrhosis</li>\n<li>Constriction of the renal artery or aorta</li>\n<li>Various psychological stimuli</li>\n</ul>\n<p><strong>Inhibitory</strong></p>\n<ul>\n<li>Increased Na<sup>+</sup> and Cl<sup>-</sup> reabsorption across macula densa</li>\n<li>Increased afferent arteriolar pressure</li>\n<li>Angiotensin II</li>\n<li>ADH (vasopressin)</li>\n</ul>\n<p>&nbsp;</p>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(452, 'Physiology,', 'PM1338. Principal buffers in the body', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>Extracellular Buffers</td>\n<td>Intracellular Buffers</td>\n<td>Bone Buffers</td>\n</tr>\n<tr>\n<td>\n<ul>\n<li><strong>HCO</strong><sub><strong>3</strong>&nbsp;</sub>(bicarbonate) -Major buffer in ECF and body</li>\n<li>Plasma proteins</li>\n<li>Phosphate - minor</li>\n<li>NH<sub>3</sub>- minor</li>\n</ul>\n</td>\n<td>\n<ul>\n<li>Organic phosphates [e.g., AMP, ADP, ATP, 2,3-diphosphoglycerate (DPG)]</li>\n<li>Intracellular proteins</li>\n<li>Hemoglobin in RBCs</li>\n</ul>\n</td>\n<td>\n<p>It has been estimated that at least 40% of the buffering of an acute acid load takes place in the bone.</p>\n<p>In chronic acidosis continued buffering has adverse effects on bone mineralization and can result in bone diseases such as rickets, osteomalacia, and osteopenia.</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <!---->\n      </div><!---->\n    </div><!---->'),
(453, 'Physiology', 'PM1335. Renal tubule characteristics', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\459\>\n<tbody>\n<tr>\n<td rowspan=\2\ width=\126\>\n<p><strong>Segments </strong></p>\n</td>\n<td rowspan=\2\ width=\87\>\n<p><strong>Active NaCl Transport </strong></p>\n</td>\n<td colspan=\3\ width=\246\>\n<p><strong>Permeability </strong></p>\n</td>\n</tr>\n<tr>\n<td width=\85\>\n<p><strong>H<sub>2</sub>0</strong></p>\n</td>\n<td width=\78\>\n<p><strong>NaCl</strong></p>\n</td>\n<td width=\83\>\n<p><strong>Urea</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\126\>\n<p>Proximal tubule</p>\n</td>\n<td width=\87\>\n<p>++</p>\n</td>\n<td width=\85\>\n<p>++</p>\n</td>\n<td width=\78\>\n<p>+</p>\n</td>\n<td width=\83\>\n<p>+</p>\n</td>\n</tr>\n<tr>\n<td width=\126\>\n<p>Thin descending limb</p>\n</td>\n<td width=\87\>\n<p>0</p>\n</td>\n<td width=\85\>\n<p>++</p>\n</td>\n<td width=\78\>\n<p>+</p>\n</td>\n<td width=\83\>\n<p>+</p>\n</td>\n</tr>\n<tr>\n<td width=\126\>\n<p>Thin ascending limb</p>\n</td>\n<td width=\87\>\n<p>0</p>\n</td>\n<td width=\85\>\n<p>0</p>\n</td>\n<td width=\78\>\n<p>+</p>\n</td>\n<td width=\83\>\n<p>+</p>\n</td>\n</tr>\n<tr>\n<td width=\126\>\n<p>Thick ascending limb</p>\n</td>\n<td width=\87\>\n<p>++</p>\n</td>\n<td width=\85\>\n<p>0</p>\n</td>\n<td width=\78\>\n<p>0</p>\n</td>\n<td width=\83\>\n<p>0</p>\n</td>\n</tr>\n<tr>\n<td width=\126\>\n<p>Distal tubule</p>\n</td>\n<td width=\87\>\n<p>+</p>\n</td>\n<td width=\85\>\n<p>+ADH</p>\n</td>\n<td width=\78\>\n<p>0</p>\n</td>\n<td width=\83\>\n<p>0</p>\n</td>\n</tr>\n<tr>\n<td width=\126\>\n<p>Cortical collecting duct</p>\n</td>\n<td width=\87\>\n<p>+</p>\n</td>\n<td width=\85\>\n<p>+ADH</p>\n</td>\n<td width=\78\>\n<p>0</p>\n</td>\n<td width=\83\>\n<p>0</p>\n</td>\n</tr>\n<tr>\n<td width=\126\>\n<p>Inner medullary collecting duct</p>\n</td>\n<td width=\87\>\n<p>+</p>\n</td>\n<td width=\85\>\n<p>+ADH</p>\n</td>\n<td width=\78\>\n<p>0</p>\n</td>\n<td width=\83\>\n<p>+ADH</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p><strong>KEY:</strong> ADH, antidiuretic hormone; NaCl, sodium chloride; 0, minimal level of active transport or permeability; +, moderate level of active transport or permeability; ++, high level of active transport or permeability; +ADH, permeability to water or urea is increased by ADH.</p></div><!---->\n      <!---->\n    </div><!---->'),
(454, 'Physiology', 'PM1334. Summary of Transport Across Proximal and Distal Portions of the Nephron', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\476\>\n<tbody>\n<tr>\n<td colspan=\2\>\n<p><strong>Proximal Tubule</strong></p>\n</td>\n</tr>\n<tr>\n<td><strong>Reabsorption</strong></td>\n<td>\n<p><strong>Secretion</strong>&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>67% of filtered Na<sup>+</sup> actively reabsorbed, not subject to control; Cl<sup>–</sup> follows passively</p>\n<p>All filtered glucose and amino acids reabsorbed by secondary active transport; not subject to control</p>\n<p>Variable amounts of filtered PO4<sup>3- </sup>and other electrolytes reabsorbed; subject to control</p>\n<p>65% of filtered H<sub>2</sub>O osmotically reabsorbed; not subject to control</p>\n<p>50% of filtered urea passively reabsorbed; not subject to control</p>\n<p>Almost all filtered K<sup>+</sup> reabsorbed; not subject to control</p>\n</td>\n<td>\n<p>Variable H<sup>+</sup> secretion, depending on acid-base status of body</p>\n<p>Organic ion secretion; not subject to control</p>\n</td>\n</tr>\n<tr>\n<td colspan=\2\><strong>Distal tubule</strong></td>\n</tr>\n<tr>\n<td><strong>Reabsorption</strong></td>\n<td><strong>Secretion</strong></td>\n</tr>\n<tr>\n<td>\n<p>Variable Na<sup>+</sup> reabsorption, controlled by aldosterone; Cl<sup>–</sup> follows passively</p>\n<p>Variable H<sub>2</sub>O reabsorption, controlled by vasopressin</p>\n</td>\n<td>\n<p>Variable H<sup>+ </sup>secretion, depending on acid-base status of body</p>\n<p>Variable K<sup>+</sup> secretion, controlled by aldosterone</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(455, 'Physiology', 'PM1333. Water transport along nephron', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Segment</strong></p>\n</td>\n<td>\n<p><strong>Mechanism of Water Reabsorption</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Proximal tubule(67%)</p>\n</td>\n<td>\n<p>Osmosis, AQ 1</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Loop of Henle (15%)</p>\n</td>\n<td>\n<p>Descending thin limb only; aquaporin 1 channels</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Early distal convoluted tubule(0%)</p>\n</td>\n<td>\n<p>No water reabsorption</p>\n</td>\n</tr>\n<tr>\n<td>\n<div class=\page\ title=\Page 1010\>\n<div class=\section\>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>Late distal tubule and collecting duct (8-17%)</p>\n</div>\n</div>\n</div>\n</div>\n</td>\n<td>\n<p>Passive(aquaporin-2) under the influence of ADH</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(456, 'Physiology', 'PM1332. Transport proteins involved in the movement of Na+ and CL- across the apical membranes of renal tubular cells', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Site</strong></p>\n</td>\n<td>\n<p><strong>Apical transport</strong></p>\n</td>\n<td>\n<p><strong>Function</strong></p>\n</td>\n</tr>\n<tr>\n<td rowspan=\6\>\n<p>Proximal tubule(60%)</p>\n</td>\n<td>\n<p>Na<sup>+</sup>/glucose co transport</p>\n</td>\n<td>\n<p>Na<sup>+</sup> uptake , glucose uptake</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Na<sup>+</sup>/inorganic phosphate Cotransporter</p>\n</td>\n<td>\n<p>Na<sup>+</sup> uptake, phosphate uptake</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Na<sup>+</sup> amino acid Cotransporter</p>\n</td>\n<td>\n<p>Na<sup>+</sup> uptake, amino acid uptake</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Na<sup>+</sup>/lactate Cotransporter</p>\n</td>\n<td>\n<p>Na<sup>+</sup> uptake, lactate uptake</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Na<sup>+</sup>/H<sup>+</sup> exchanger</p>\n</td>\n<td>\n<p>Na<sup>+</sup> uptake , H<sup>+</sup> extrusion</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Cl<sup>-</sup> / base exchanger</p>\n</td>\n<td>\n<p>Cl<sup>- </sup>uptake</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Thick ascending limb(30%)</p>\n</td>\n<td>\n<p>Na<sup>+</sup>–K<sup>+</sup>–2Cl<sup>-</sup> Cotransporter</p>\n<p>Na<sup>+</sup>/H<sup>+</sup> exchanger</p>\n</td>\n<td>\n<p>Na<sup>+ </sup>uptake, Cl<sup>–</sup> uptake, K<sup>+</sup> uptake</p>\n<p>Na<sup>+</sup> uptake, H<sup>+</sup> extrusion</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Distal convoluted tubule(7%)</p>\n</td>\n<td>\n<p>NaCl Cotransporter</p>\n</td>\n<td>\n<p>Na<sup>+</sup> uptake, Cl<sup>–</sup> uptake</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Collecting duct(3%)</p>\n</td>\n<td>\n<p>Na<sup>+</sup> channel (ENaC)</p>\n</td>\n<td>\n<p>Na<sup>+</sup> uptake</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      