(465, 'Physiology', 'PM1498. Factors affecting insulin secretion', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Stimulators</strong></td>\n<td><strong>Inhibitors</strong></td>\n</tr>\n<tr>\n<td>\n<p>Glucose</p>\n<p>Mannose</p>\n</td>\n<td>\n<p>2-Deoxyglucose</p>\n<p>Mannoheptulose</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Glucagon</p>\n</td>\n<td>Somatostatin&nbsp;</td>\n</tr>\n<tr>\n<td>\n<p>Amino acids - leucine arginine</p>\n<p>Intestinal hormones - GIP, GLP-1, gastrin, secretin, CCK</p>\n</td>\n<td>K<sup>+</sup> depletion&nbsp;</td>\n</tr>\n<tr>\n<td>&nbsp;</td>\n<td>\n<p>&nbsp;</p>\n<p>Leptin</p>\n<p>&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Acetylcholine (Parasympathetic stimulation )</p>\n</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>\n<p>β-adrenergic stimulators</p>\n<p>Theophylline</p>\n<p>Sulfonylureas</p>\n</td>\n<td>\n<p>α-adrenergic stimulators (norepinephrine, epinephrine)β-Adrenergic blockers&nbsp; (propranolol)</p>\n<p>Diazoxide</p>\n<p>Thiazide diuretics</p>\n<p>Phenytoin&nbsp;</p>\n<p>Alloxan</p>\n<p>Microtubule inhibitors ( Taxanes, Vinca alkaloids)</p>\n<p>Insulin</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(466, 'Physiology', 'PM1495. Conditions that affect glucocorticoid secretion', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>Glucocorticoid secretion also increased</td>\n</tr>\n<tr>\n<td>\n<ul>\n<li>Surgery</li>\n<li>Anxiety</li>\n<li>Physical trauma</li>\n<li>Hemorrhage</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Glucorticoid secretion unaffected</td>\n</tr>\n<tr>\n<td>\n<ul>\n<li>High pottasium intake</li>\n<li>Low sodium intake</li>\n<li>Constriction of inferior vena cava in thorax</li>\n<li>Standing</li>\n<li>Secondary hyperaldosteronism (in some cases of heart failure, cirrhosis, and nephrosis)</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <!---->\n      </div><!---->\n    </div><!---->'),
(467, 'Physiology', 'PM1494. Secretion of Adrenal Hormones by the different Zones of the Adrenal Cortex and Adrenal Medulla', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Adrenal Cortex</strong></p>\n</td>\n<td>\n<p><strong>Adrenal Medulla </strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Zona glomerulosa(15%) </strong></p>\n<p>Mineralocorticoids</p>\n<p>Aldosterone</p>\n<p>Deoxycorticosterone</p>\n</td>\n<td rowspan=\3\>\n<p><strong>Norepinephrine</strong></p>\n<p><strong>Epinephrine</strong></p>\n<p><strong>Dopamine </strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Zona fasiculata (50%) </strong></p>\n<p>Glucocorticoids</p>\n<p>Cortisol</p>\n<p>Corticosterone</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Zona reticularis(7%) </strong></p>\n<p>Androgens:</p>\n<ul>\n<li>DHEA</li>\n<li>Androstenedione</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <!---->\n      </div><!---->\n    </div><!---->'),
(468, 'Pharmacology,', 'PM1490. Physiologic effects of thyroid hormone', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>System</td>\n<td>Effect</td>\n</tr>\n<tr>\n<td>Cardiovascular&nbsp;</td>\n<td>\n<ul>\n<li aria-level=\1\>Chronotropic and inotropic actions</li>\n<ul>\n<li aria-level=\2\>↑number of β-adrenergic receptors</li>\n<li aria-level=\2\>↑response to circulating catecholamines&nbsp;</li>\n<li aria-level=\2\>↑the proportion of α-myosin heavy chain</li>\n</ul>\n<li>↓peripheral vascular resistance</li>\n<li aria-level=\1\>↑blood volume (activates RAAS)</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Musculoskeletal&nbsp;</td>\n<td>\n<ul>\n<li aria-level=\1\>Catabolic action (protein breakdown)</li>\n<li aria-level=\1\>Promotes normal growth and skeletal maturation&nbsp;</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Nervous</td>\n<td>\n<ul>\n<li aria-level=\1\>Promotes normal brain development</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Gastrointestinal&nbsp;</td>\n<td>\n<ul>\n<li aria-level=\1\>↑Appetite and food intake&nbsp;</li>\n<li aria-level=\1\>↑GI motility&nbsp;</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Respiratory&nbsp;</td>\n<td>\n<ul>\n<li aria-level=\1\>↑Respiratory rate&nbsp;</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Others</td>\n<td>\n<ul>\n<li aria-level=\1\>Carbohydrates: Promotes uptake of glucose into cells by</li>\n<ul>\n<li aria-level=\2\>↑Insulin secretion</li>\n<li aria-level=\2\>↑Gluconeogenesis</li>\n<li aria-level=\2\>↑Glycogenolysis</li>\n</ul>\n<li aria-level=\1\>Lipids&nbsp;</li>\n<ul>\n<li aria-level=\2\>Catabolic action (stimulates lipolysis)</li>\n<li aria-level=\2\>↓LDL (formation of LDL receptors)</li>\n</ul>\n<li aria-level=\1\>Calorigenic: ↑basal metabolic rate&nbsp;</li>\n<li aria-level=\1\>Thermogenic: Heat production&nbsp;</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(469, 'Medicine,', 'PM1488. Types of vasopressin receptors and their functions', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><div class=\ng-scope\>\n<div class=\ng-scope\>\n<table border=\1\>\n<tbody>\n<tr>\n<td>Receptor</td>\n<td>Localization</td>\n<td>Functions</td>\n</tr>\n<tr>\n<td>V<sub>1a</sub></td>\n<td>\n<ul>\n<li>Vascular smooth muscle</li>\n<li>Platelets</li>\n<li>Hepatocytes</li>\n<li>Myometrium</li>\n</ul>\n</td>\n<td>\n<ul>\n<li>Vasoconstriction</li>\n<li>Platelet aggregation</li>\n<li>Glycogenolysis</li>\n<li>Uterine contraction</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>V3/V<sub>1b&nbsp;</sub></td>\n<td>\n<ul>\n<li>Anterior pituitary</li>\n</ul>\n</td>\n<td>\n<ul>\n<li>ACTH release</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>V<sub>2</sub></td>\n<td>\n<ul>\n<li>Basolateral membrane of collecting tubule</li>\n<li>Vascular endothelium</li>\n<li>Vascular&nbsp; smooth muscle</li>\n</ul>\n</td>\n<td>\n<ul>\n<li>Upregulation of Aqp2</li>\n<li>vwF &amp; factor 8 release</li>\n<li>Vasodilation</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table>\n</div>\n</div></div><!---->\n      <!---->\n    </div><!---->'),
(470, 'Physiology', 'PM1487. Factors Regulating GH Secretion', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Stimuli that increase GH secretion</strong></td>\n<td><strong>Stimuli that decrease GH secretion</strong></td>\n</tr>\n<tr>\n<td>\n<ul>\n<li>GHRH</li>\n<li>Testosterone, Estrogen</li>\n<li>Ghrelin</li>\n<li>Glucagon<br><br></li>\n<li>↓ Blood glucose</li>\n<li>↓ FFA</li>\n<li>↑ in certain amino acids (arginine)<br><br></li>\n<li>Fasting or protein deficiency</li>\n<li>Exercise</li>\n<li>Trauma, stress, excitement</li>\n<li>Drugs like Apomorphine, L-Dopa</li>\n</ul>\n</td>\n<td>\n<ul>\n<li>Somatostatin (GHIH)</li>\n<li>Exogenous GH</li>\n<li>Somatomedins (IGF-1)</li>\n<li>Cortisol<br><br></li>\n<li>↑ Blood glucose</li>\n<li>↑ FFA<br><br></li>\n<li>REM sleep</li>\n<li>Obesity</li>\n<li>Aging</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table>\n<p>GHRH - Growth hormone releasing hormone<br>GHIH - Growth hormone inhibiting hormone<br>FFA - Free fatty acids</p></div><!---->\n      <!---->\n    </div><!---->'),
(471, 'Physiology', 'PM1485. Direct and Indirect (mediated via IGF-1) effects of Growth Hormone', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Indirect effects of growth hormone are mediated via IGF-I.&nbsp;</p>\n<p>Direct effects of GH are shown below.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/4863370e2ad44958a01398e020bb1fe8x1280x923.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(472, 'Physiology,', 'PM1484. Factors affecting prolactin secretion', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Increased Prolactin Secretion</strong></td>\n<td><strong>Decreased Prolactin Secretion</strong></td>\n</tr>\n<tr>\n<td>Sleep</td>\n<td>Dopamine</td>\n</tr>\n<tr>\n<td>Stress</td>\n<td>L-Dopa</td>\n</tr>\n<tr>\n<td>Strenuous exercise</td>\n<td>Apomorphine</td>\n</tr>\n<tr>\n<td>Sexual intercourse in women</td>\n<td>Bromocriptine and related ergot derivatives</td>\n</tr>\n<tr>\n<td>Pregnancy</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>Nursing</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>Breast stimulation in non-lactating women</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>Estrogen</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>Hypothyroidism</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>TRH</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>Opioids</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>Phenothiazines, butyrophenones</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>\n<p>Atypical antipsychotics - Risperidone</p>\n</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>\n<p>Dopamine synthesis inhibitors - α-Methyldopa</p>\n</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>&nbsp;\n<p>H<sub>2</sub> antagonists - Cimetidine, ranitidine</p>\n</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>\n<p>Imipramines - Amitriptyline, amoxapine</p>\n</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>\n<p>Serotonin reuptake inhibitors -Fluoxetine</p>\n</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>\n<p>Calcium channel blockers - Verapamil</p>\n</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>&nbsp;\n<p>Thioxanthenes</p>\n<p>Metoclopramide</p>\n</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>\n<p>Catecholamine depletors - Reserpine</p>\n</td>\n<td>&nbsp;</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(473, 'Physiology', 'PM1483. Cells of Anterior Pituitary', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/843ae844c09346af99204e0a0f875354x1280x1382.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(474, 'Physiology', 'PM1346. Actions of Angiotensin II', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Actions of Angiotensin II</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Produces powerful arteriolar constriction and a rise in SBP and DBP</p>\n<p>Acts directly on the adrenal cortex to increase aldosterone secretion</p>\n<p>Facilitates the release of Norepinephrine by a direct action on postganglionic sympathetic neurons</p>\n<p>Constricts efferent arteriole, thereby increasing filteration fraction and glomerular filteration rate, when there is decreased renal bloodflow</p>\n<p>Direct effect on renal tubules to increase Na<sup>+ </sup>reabsorption</p>\n<p>Acts on brain to decrease sensitivity of baroreceptor reflex, potentiating the pressor effect of AT II</p>\n<p>Acts on brain to increase water intake and increase secretion of ADH and ACTH</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(475, 'Physiology', 'PM0776. Properties of thyroid hormone', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td><strong>Hormone property</strong></td>\n<td><strong>T3</strong></td>\n<td><strong>T4</strong></td>\n</tr>\n<tr>\n<td>Fraction of total hormone in unbound form</td>\n<td colspan=\2\>0.02%</td>\n</tr>\n<tr>\n<td>Half life</td>\n<td>1 day</td>\n<td>7 days</td>\n</tr>\n<tr>\n<td>Fraction directly from thyroid</td>\n<td>20%</td>\n<td>100%</td>\n</tr>\n<tr>\n<td>Intracellular hormone fraction</td>\n<td>~70%</td>\n<td>~20%</td>\n</tr>\n<tr>\n<td>Relative metabolic potency</td>\n<td>1</td>\n<td>0.3</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(476, 'Physiology', 'PM0375. Neurotransmitters and Neuromodulators', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\154\>\n<p><strong>Small-molecule transmitters</strong></p>\n</td>\n<td width=\251\>\n<p><strong>Large- molecule transmitters</strong></p>\n<p><strong>(Neuropeptide, Slowly Acting</strong></p>\n<p><strong>Transmitters or Growth Factors)</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\154\>\n<p><strong>Class I</strong></p>\n<p>Acetylcholine</p>\n<p><strong>Class II: The Amines</strong></p>\n<p>Norepinephrine</p>\n<p>Epinephrine</p>\n<p>Dopamine</p>\n<p>Serotonin</p>\n<p>Histamine</p>\n<p><strong>Class III: Amino Acids</strong></p>\n<p>Gamma-aminobutyric acid</p>\n<p>Glycine</p>\n<p>Glutamate</p>\n<p>Aspartate</p>\n<p><strong>Class IV</strong></p>\n<p>Nitric oxide</p>\n<p>ATP</p>\n</td>\n<td width=\251\>\n<p><strong>Hypothalamic-Releasing Hormones</strong></p>\n<p>TRH</p>\n<p>Luteinizing hormone–releasing hormone</p>\n<p>Somatostatin (growth hormone inhibitory factor)</p>\n<p><strong>Pituitary Peptides</strong></p>\n<p>ACTH</p>\n<p>β-Endorphin</p>\n<p>α-Melanocyte-stimulating hormone</p>\n<p>Prolactin</p>\n<p>LH</p>\n<p>TSH</p>\n<p>Growth hormone</p>\n<p>ADH</p>\n<p>Oxytocin</p>\n<p><strong>Peptides that Act on Gut and Brain</strong></p>\n<p>Enkephalin</p>\n<p>Substance P</p>\n<p>Gastrin</p>\n<p>Cholecystokinin</p>\n<p>VIP</p>\n<p>Nerve growth factor</p>\n<p>Brain-derived neurotropic factor</p>\n<p>Neurotensin</p>\n<p>Insulin</p>\n<p>Glucagon</p>\n<p><strong>From Other Tissues</strong></p>\n<p>Angiotensin II</p>\n<p>Bradykinin</p>\n<p>Carnosine</p>\n<p>Sleep peptides</p>\n<p>Calcitonin</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->');
