(477, 'Physiology', 'PM0374. Abnormalities due to Loss- Of or Gain-In Mutation of GPCR or G Proteins', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\134\>\n<p><strong>Site</strong></p>\n</td>\n<td width=\129\>\n<p><strong>Type of Mutation</strong></p>\n</td>\n<td width=\144\>\n<p><strong>Disease</strong></p>\n</td>\n</tr>\n<tr>\n<td colspan=\3\ width=\408\>\n<p><strong>Receptor (GPCR)</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\134\>\n<p>Conopsins</p>\n</td>\n<td width=\129\>\n<p>Loss</p>\n</td>\n<td width=\144\>\n<p>Color blindness</p>\n</td>\n</tr>\n<tr>\n<td width=\134\>\n<p>Rhodopsin</p>\n</td>\n<td width=\129\>\n<p>Loss</p>\n</td>\n<td width=\144\>\n<p>Congenital night blindness; two forms of retinitis pigmentosa</p>\n</td>\n</tr>\n<tr>\n<td width=\134\>\n<p>V<sub>2</sub> vasopressin</p>\n</td>\n<td width=\129\>\n<p>Loss</p>\n</td>\n<td width=\144\>\n<p>X-linked nephrogenic diabetes insipidus</p>\n</td>\n</tr>\n<tr>\n<td width=\134\>\n<p>ACTH</p>\n</td>\n<td width=\129\>\n<p>Loss</p>\n</td>\n<td width=\144\>\n<p>Familial glucocorticoid deficiency</p>\n</td>\n</tr>\n<tr>\n<td width=\134\>\n<p>TSH</p>\n</td>\n<td width=\129\>\n<p>Loss</p>\n</td>\n<td width=\144\>\n<p>Familial hypothyroidism</p>\n</td>\n</tr>\n<tr>\n<td width=\134\>\n<p>Thromboxane A2</p>\n</td>\n<td width=\129\>\n<p>Loss</p>\n</td>\n<td width=\144\>\n<p>Congenital bleeding</p>\n</td>\n</tr>\n<tr>\n<td width=\134\>\n<p>Endothelin B</p>\n</td>\n<td width=\129\>\n<p>Loss</p>\n</td>\n<td width=\144\>\n<p>Hirschsprung disease</p>\n</td>\n</tr>\n<tr>\n<td width=\134\>\n<p>LH</p>\n</td>\n<td width=\129\>\n<p>Gain</p>\n</td>\n<td width=\144\>\n<p>Familial male precocious puberty</p>\n</td>\n</tr>\n<tr>\n<td width=\134\>\n<p>TSH</p>\n</td>\n<td width=\129\>\n<p>Gain</p>\n</td>\n<td width=\144\>\n<p>Familial nonautoimmune hyperthyroidism</p>\n</td>\n</tr>\n<tr>\n<td width=\134\>\n<p>Ca<sup>2+</sup></p>\n</td>\n<td width=\129\>\n<p>Gain</p>\n</td>\n<td width=\144\>\n<p>Familial hypercalciuric hypocalcemia</p>\n</td>\n</tr>\n<tr>\n<td colspan=\3\ width=\408\>\n<p><strong>G protein</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\134\>\n<p>G<sub>s</sub> α</p>\n</td>\n<td width=\129\>\n<p>Loss</p>\n</td>\n<td width=\144\>\n<p>Pseudohypopara thyroidism type 1a</p>\n</td>\n</tr>\n<tr>\n<td width=\134\>\n<p>G<sub>s</sub> α</p>\n</td>\n<td width=\129\>\n<p>Gain/loss</p>\n</td>\n<td width=\144\>\n<p>Testotoxicosis</p>\n</td>\n</tr>\n<tr>\n<td width=\134\>\n<p>G<sub>s</sub> α</p>\n</td>\n<td width=\129\>\n<p>Gain (mosaic)</p>\n</td>\n<td width=\144\>\n<p>McCune-Albright syndrome</p>\n</td>\n</tr>\n<tr>\n<td width=\134\>\n<p>G<sub>s</sub> α</p>\n</td>\n<td width=\129\>\n<p>Gain</p>\n</td>\n<td width=\144\>\n<p>Somatotroph adenomas with acromegaly</p>\n</td>\n</tr>\n<tr>\n<td width=\134\>\n<p>G<sub>i</sub> α</p>\n</td>\n<td width=\129\>\n<p>Gain</p>\n</td>\n<td width=\144\>\n<p>Ovarian and adrenocortical tumors</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(478, 'Physiology', 'PM0373. Calmodulin Dependent Enzymes', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td colspan=\2\ width=\406\>\n<p><strong>Calmodulin Dependent Enzymes</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\155\>\n<p><strong>Enzyme </strong></p>\n</td>\n<td width=\251\>\n<p><strong>Important Physiological Effects</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\155\>\n<p>Myosin light-chain kinase</p>\n</td>\n<td width=\251\>\n<p>Contraction of smooth muscles</p>\n</td>\n</tr>\n<tr>\n<td width=\155\>\n<p>Phosphorylase kinase</p>\n</td>\n<td width=\251\>\n<p>Phosphorylation of enzyme glycogen phosphorylase to release glucose-1-phosphate from glycogen (glycogenolysis)</p>\n</td>\n</tr>\n<tr>\n<td width=\155\>\n<p>Ca <sup>2+</sup> /calmodulin kinase I</p>\n<p>Ca <sup>2+</sup> /calmodulin kinase II</p>\n</td>\n<td width=\251\>\n<p>Synaptic function</p>\n</td>\n</tr>\n<tr>\n<td width=\155\>\n<p>Ca <sup>2+</sup> /calmodulin kinase III</p>\n</td>\n<td width=\251\>\n<p>Protein synthesis</p>\n</td>\n</tr>\n<tr>\n<td colspan=\2\ width=\406\>\n<p><strong>Calmodulin Dependent Phosphatases</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\155\>\n<p><strong>Enzyme</strong></p>\n</td>\n<td width=\251\>\n<p><strong>Important Physiological Effects</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\155\>\n<p>Calcineurin</p>\n</td>\n<td width=\251\>\n<p>Inactivates Ca<sup>2+</sup> channels by dephosphorylating them</p>\n<p>Prominent role in activating T cells and is inhibited by some immunosuppressants</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(479, 'Pharmacology,', 'PM0371. Mechanisms of Action of Hormones', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\431\>\n<tbody>\n<tr>\n<td colspan=\2\><strong>Types of hormone receptors</strong></td>\n</tr>\n<tr>\n<td colspan=\2\><strong>Hormones that bind to intracellular receptors</strong></td>\n</tr>\n<tr>\n<td rowspan=\3\>Cytoplasmic receptors</td>\n<td>Glucocorticoids</td>\n</tr>\n<tr>\n<td>Mineralocorticoids</td>\n</tr>\n<tr>\n<td>Androgens</td>\n</tr>\n<tr>\n<td rowspan=\2\>Nuclear receptors</td>\n<td>Thyroid hormones</td>\n</tr>\n<tr>\n<td>Vitamins A and D</td>\n</tr>\n<tr>\n<td colspan=\2\><strong>Hormones that bind to cell surface receptors</strong></td>\n</tr>\n<tr>\n<td colspan=\2\>1. <strong>G-protein coupled receptors</strong> (GPCR)</td>\n</tr>\n<tr>\n<td rowspan=\9\><strong>Gs</strong>: stimulation of second messenger cAMP</td>\n<td>ACTH, CRH</td>\n</tr>\n<tr>\n<td>TSH, GHRH</td>\n</tr>\n<tr>\n<td>FSH, LH, hCG</td>\n</tr>\n<tr>\n<td>Vasopressin (V<sub>2</sub> receptors)</td>\n</tr>\n<tr>\n<td>Catecholamines (β receptors)</td>\n</tr>\n<tr>\n<td>Somatostatin</td>\n</tr>\n<tr>\n<td>Glucagon</td>\n</tr>\n<tr>\n<td>Secretin</td>\n</tr>\n<tr>\n<td>Parathyroid hormone</td>\n</tr>\n<tr>\n<td rowspan=\3\><strong>Gi:&nbsp;</strong>inhibition of second messenger cAMP</td>\n<td>Catecholamines (ɑ<sub>2</sub> receptors)</td>\n</tr>\n<tr>\n<td>Acetylcholine (M2, M4)</td>\n</tr>\n<tr>\n<td>Somatostatin</td>\n</tr>\n<tr>\n<td rowspan=\11\>\n<p><strong>Gq</strong>:&nbsp;second messenger is phospholipase&nbsp;C or cGMP</p>\n</td>\n<td><strong>Phospholipase C (IP3 DAG pathway):</strong></td>\n</tr>\n<tr>\n<td>GnRH, GHRH, TRH</td>\n</tr>\n<tr>\n<td>Catecholamines (ɑ<sub>1</sub> receptors)</td>\n</tr>\n<tr>\n<td>Vasopressin (V<sub>1</sub> receptor)</td>\n</tr>\n<tr>\n<td>Gastrin</td>\n</tr>\n<tr>\n<td>Cholecystokinin</td>\n</tr>\n<tr>\n<td>Oxytocin</td>\n</tr>\n<tr>\n<td>Acetylcholine (M1, M3, M5)</td>\n</tr>\n<tr>\n<td><strong>cGMP:</strong></td>\n</tr>\n<tr>\n<td>Atrial natriuretic peptide</td>\n</tr>\n<tr>\n<td>Nitric oxide</td>\n</tr>\n<tr>\n<td colspan=\2\>2. <strong>Tyrosine kinase</strong> receptors</td>\n</tr>\n<tr>\n<td rowspan=\5\>Through the <strong>JAK-STAT</strong> (non-receptor tyrosine kinase) or <strong>MAP kinase</strong> (receptor tyrosine kinase) pathway&nbsp;</td>\n<td>Growth factors like IGF, VEGF, FGF, PDGF</td>\n</tr>\n<tr>\n<td>Growth hormone</td>\n</tr>\n<tr>\n<td>Erythropoietin</td>\n</tr>\n<tr>\n<td>Insulin</td>\n</tr>\n<tr>\n<td>Prolactin</td>\n</tr>\n</tbody>\n</table>\n<p><br>Notes: <br>1. Even though the cytoplasmic receptors are present in the cytoplasm, once the <strong>hormone-receptor complex</strong> is formed it moves to the <strong>nucleus</strong> for its actions.<br>2. While receptor tyrosine kinases have intrinsic tyrosine kinase activity, non-receptor tyrosine kinases have an external tyrosine kinase component that interacts with the receptor as and when required.&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(480, 'Medicine,', 'PM0370. Classification of Hormones According to their Chemical Structure', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Proteins and polypeptide hormones</strong></p>\n<ul>\n<li>Large proteins (&gt; 50 amino acids)\n<ul>\n<li>Anterior pituitary hormones: FSH, LH,&nbsp;GH, Prolactin</li>\n<li>Parathyroid hormone</li>\n<li>Insulin</li>\n</ul>\n</li>\n<li>Small peptides (&lt; 50 amino acids)\n<ul>\n<li>Posterior pituitary hormones: Oxytocin, ADH</li>\n<li>ACTH</li>\n<li>Glucagon</li>\n</ul>\n</li>\n</ul>\n<p><strong>Steroid hormones</strong></p>\n<ul>\n<li>Cortisol and aldosterone</li>\n<li>Estrogen and progesterone</li>\n<li>Testosterone</li>\n<li>DHEA</li>\n</ul>\n<p><strong>Derivatives of a specific amino acid</strong></p>\n<ul>\n<li>Tyrosine\n<ul>\n<li>Thyroid hormones</li>\n<li>Catecholamines</li>\n</ul>\n</li>\n<li>Tryptophan\n<ul>\n<li>Serotonin (5-HT)</li>\n<li>Melatonin</li>\n</ul>\n</li>\n<li>Arginine\n<ul>\n<li>Nitric oxide</li>\n</ul>\n</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(481, 'Physiology', 'PM0338. Changes in Hormone Levels due to Aging', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Changes in hormone levels due to aging:</p>\n<table>\n<tbody>\n<tr>\n<td width=\160\>\n<p><strong>Decrease</strong></p>\n</td>\n<td width=\160\>\n<p><strong>No Change</strong></p>\n</td>\n<td width=\160\>\n<p><strong>Increase</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\160\>\n<p>Growth hormone&nbsp;</p>\n<p>Luteinizing hormone (men)</p>\n<p>Insulin growth factor I</p>\n<p>Testosterone</p>\n<p>Prolactin</p>\n<p>Estradiol 1</p>\n<p>DHEA and DHEAs&nbsp; Pregnenolone</p>\n<p>25(OH) vitamin D</p>\n<p>Aldosterone</p>\n<p>Vasoactive intestinal peptide</p>\n<p>Melatonin</p>\n</td>\n<td width=\160\>\n<p>&nbsp;</p>\n<p>Thyrotropin</p>\n<p>Thyroid hormones</p>\n<p>Epinephrine</p>\n<p>Glucagon-like peptide</p>\n<p>Gastric inhibitory polypeptide</p>\n</td>\n<td width=\160\>\n<p>Cholecystokinin</p>\n<p>Luteinizing hormone (women)</p>\n<p>Follicle-stimulating hormone</p>\n<p>Cortisol</p>\n<p>Norepinephrine</p>\n<p>Insulin</p>\n<p>Parathormone</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(482, 'Obstetrics & Gynaecology,', 'PM1511. Composition of human semen', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Color</strong></td>\n<td>White and opalescent</td>\n</tr>\n<tr>\n<td><strong>Sperm count</strong></td>\n<td>Average about 100 million/mL, with &lt;20% abnormal form</td>\n</tr>\n<tr>\n<td><strong>pH</strong></td>\n<td>7.35 - 7.50</td>\n</tr>\n<tr>\n<td><strong>Specific gravity</strong></td>\n<td>1.028</td>\n</tr>\n<tr>\n<td>\n<p><strong>Components from seminal vesicles</strong></p>\n<p>(contributes <strong>60%</strong> of total volume)</p>\n</td>\n<td>\n<p>Fructose</p>\n<p>Phosphorylcholine</p>\n<p>Ergothioneine</p>\n<p>Ascorbic acid</p>\n<p>Flavins</p>\n<p>Prostaglandins</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Components from prostate</strong></p>\n<p>(contributes <strong>20%</strong> of total volume)</p>\n</td>\n<td>\n<p>Spermine</p>\n<p>Citric acid</p>\n<p>Cholesterol and phospholipids</p>\n<p>Fibrinolysin and fibrinogenase</p>\n<p>Zinc</p>\n<p>Acid phosphatase</p>\n</td>\n</tr>\n<tr>\n<td><strong>Buffers</strong></td>\n<td>\n<p>Phosphate</p>\n<p>Bicarbonate</p>\n<p>Hyaluronidase</p>\n<p>Magnesium</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(483, 'Physiology,', 'PM1514. Hormonal Regulation of Menstrual Cycle', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/bbc6b519c9284220814b3e53c4f33f7ex1280x2327.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(484, 'ENT,', 'PM1751. Stapedial reflex', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Principle: A loud sound of&nbsp;<strong>70- 100dB above the hearing threshold</strong> of the patient, leads to <strong>bilateral contraction </strong>of the<strong> stapedius muscle</strong>.</p>\n<p><strong>Afferent</strong>: <strong>8<sup>th</sup></strong> cranial nerve.</p>\n<p><strong>Efferent</strong>: <strong>7<sup>th</sup></strong> cranial nerve.</p>\n<p>Centre: <strong>Superior olivary complex</strong> in the brainstem.</p>\n<p>In <strong>afferent palsy</strong>, the reflex is <strong>absent</strong> on <strong>both sides</strong>, and in <strong>efferent palsy</strong> reflex is <strong>absent</strong> only on <strong>the side</strong> of the lesion.</p>\n<p>Clinical significance:</p>\n<ul>\n<li>Can&nbsp;identify<strong> malingerers</strong>.</li>\n<li>To differentiate cochlear and retrocochlear hearing loss- the threshold is <strong>decreased</strong> in <strong>cochlear</strong> lesions because of recruitment whereas it is <strong>increased</strong> or even <strong>absent</strong> in <strong>retrocochlear</strong> lesions.</li>\n<li>Identify the <strong>site</strong> of the<strong> facial nerve lesion- </strong>its presence indicates that the lesion is beyond the nerve to stapedius.</li>\n</ul>\n<p><strong>Acoustic reflex decay</strong>- it is a feature of<strong> retrocochlear hearing loss</strong> (8<sup>th</sup> nerve lesion). A sustained tone of 500-1000Hz delivered 10dB above the acoustic reflex threshold, for a period of 10 seconds, brings the amplitude to 50% (abnormal decay).</p></div><!---->\n      <!---->\n    </div><!---->'),
(485, 'Physiology', 'PM1739. Taste sensations and their mediators', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Taste Sensations</strong></td>\n<td><strong>Mediated by</strong></td>\n</tr>\n<tr>\n<td>Salt</td>\n<td>Na+ channel</td>\n</tr>\n<tr>\n<td>Sweet</td>\n<td>G-protein coupledreceptors (GPCRs)<strong>T1R</strong> family</td>\n</tr>\n<tr>\n<td>Umami</td>\n<td>Glutamate receptor family (mGluR4) and also G- protein coulped receptors (GPCRs) <strong>T1R</strong> family</td>\n</tr>\n<tr>\n<td>Bitter sensations</td>\n<td>G-protein coupledreceptors (GPCRs)<strong>T2R</strong> family</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(486, 'ENT,', 'PM0539. Sensory receptors in the inner ear', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><span><strong>End Organ</strong></span></p>\n</td>\n<td>\n<p><span><strong>Receptor</strong></span></p>\n</td>\n<td>\n<p><span><strong>Action</strong></span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><span>Organ of Corti</span></p>\n</td>\n<td>\n<p><span>Hair cells</span></p>\n</td>\n<td>\n<p><span>Sound conduction</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><span>Ampulla of Semicircular ducts</span></p>\n</td>\n<td>\n<p><span>Cristae</span></p>\n</td>\n<td>\n<p><span>Angular momentum</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><span>Otolith organs: Utricle &amp; saccule</span></p>\n</td>\n<td>\n<p><span>Macula</span></p>\n</td>\n<td>\n<p><span>Linear momentum</span></p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(487, 'Physiology', 'PM2479. Physiologic effects of thyroid hormone', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Target Tissue</strong></p>\n</td>\n<td>\n<p><strong>Effect </strong></p>\n</td>\n<td>\n<p><strong>Mechanism </strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Heart</p>\n</td>\n<td>\n<p>Chronotropic and inotropic</p>\n</td>\n<td>\n<p>Increased number of β-adrenergic receptors</p>\n<p>Enhanced responses to circulating catecholamines</p>\n<p>Increased proportion of α-myosin heavy chain (with higher ATPase activity)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Adipose tissue</p>\n</td>\n<td>\n<p>Catabolic</p>\n</td>\n<td>\n<p>Stimulated lipolysis</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Muscle</p>\n</td>\n<td>\n<p>Catabolic</p>\n</td>\n<td>\n<p>Increased protein breakdown</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Bone</p>\n</td>\n<td>\n<p>Developmental</p>\n</td>\n<td>\n<p>Promote normal growth and skeletal development</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Nervous system</p>\n</td>\n<td>\n<p>Developmental</p>\n</td>\n<td>\n<p>Promote normal brain development</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Gut</p>\n</td>\n<td>\n<p>Metabolic</p>\n</td>\n<td>\n<p>Increased rate of carbohydrate absorption</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Lipoprotein</p>\n</td>\n<td>\n<p>Metabolic</p>\n</td>\n<td>\n<p>Formation of LDL receptors</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Other</p>\n</td>\n<td>\n<p>Calorigenic</p>\n</td>\n<td>\n<p>Stimulated oxygen consumption by metabolically active tissues (exceptions: testes, uterus, lymph nodes, spleen, anterior pituitary)</p>\n<p>Increased metabolic rate</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(488, 'Pharmacology', 'PM2414. Drugs causing QT prolongation and torsades de pointes', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Drugs Associated with QT Prolongation and Torsades de Pointes:</p>\n<p>Antiarrhythmics:</p>\n<ul>\n<li>Sotalol</li>\n<li>Amiodarone</li>\n<li>Quinidine</li>\n<li>Procainamide</li>\n<li>Dofetilide</li>\n<li>Ibutilide</li>\n</ul>\n<p>Antimicrobials:</p>\n<ul>\n<li>Levofloxacin</li>\n<li>Ciprofloxacin</li>\n<li>Gatifloxacin</li>\n<li>Moxifloxacin</li>\n<li>Clarithromycin</li>\n<li>Erythromycin</li>\n<li>Ketoconazole</li>\n<li>Itraconazole</li>\n<li>Rilpivirine</li>\n</ul>\n<p>Antidepressants:</p>\n<ul>\n<li>Amitriptyline</li>\n<li>Desipramine</li>\n<li>Imipramine</li>\n<li>Doxepin</li>\n<li>Fluoxetine</li>\n<li>Sertraline</li>\n<li>Venlafaxine</li>\n</ul>\n<p>Antipsychotics:</p>\n<ul>\n<li>Haloperidol</li>\n<li>Droperidol</li>\n<li>Quetiapine</li>\n<li>Ziprasidone</li>\n<li>Thioridazine</li>\n</ul>\n<p>Others:</p>\n<ul>\n<li>Cisapride</li>\n<li>Sumatriptan</li>\n<li>Zolmitriptan</li>\n<li>Arsenic</li>\n<li>Dolasetron</li>\n<li>Methadone<br>&nbsp;</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(489, 'Pharmacology', 'PM2379. Drug regulatory schedules 1940: (2016 update)', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\292\>\n<tbody>\n<tr>\n<td><strong>Schedule</strong></td>\n<td><strong>Details</strong></td>\n</tr>\n<tr>\n<td>Schedule C</td>\n<td><strong>Biological</strong> and other special products e.g. <strong>Toxins, Insulin, Sera &amp; Antibiotics</strong>.</td>\n</tr>\n<tr>\n<td>Schedule D</td>\n<td>List of drugs <strong>exempted from the provision of import</strong> of drugs.</td>\n</tr>\n<tr>\n<td>Schedule F</td>\n<td>\n<p><strong>Blood</strong> and blood products</p>\n<p>F I - Production of Bacterial <strong>vaccines, Anti- Sera</strong></p>\n<p>F II - Specification of standards for <strong>Surgical dressing</strong></p>\n<p>FF- Specification of standard <strong>Ophthalmic</strong> preparation</p>\n</td>\n</tr>\n<tr>\n<td>Schedule G</td>\n<td>With a label of <strong>Caution, dangerous</strong> to use except under <strong>medical supervision</strong>.</td>\n</tr>\n<tr>\n<td>Schedule H</td>\n<td><strong>Prescription</strong> drugs.</td>\n</tr>\n<tr>\n<td>Schedule J</td>\n<td>List of diseases that a drug may not prevent or cure. Eg - <strong>HIV &amp; Atherosclerosis</strong></td>\n</tr>\n<tr>\n<td>Schedule M</td>\n<td>Regulations regarding good manufacturing practices</td>\n</tr>\n<tr>\n<td>Schedule P</td>\n<td>Regulations regarding&nbsp;the <strong>life period</strong> and storage of various drugs.</td>\n</tr>\n<tr>\n<td>Schedule W</td>\n<td>Drugs marketed under <strong>generic</strong> names.</td>\n</tr>\n<tr>\n<td>Schedule X</td>\n<td>Drugs having <strong>abuse</strong> and dependence liability.</td>\n</tr>\n<tr>\n<td>Schedule Y</td>\n<td>Requirement and guidelines for<strong> import/ manufacture or Clinical trials</strong> in India</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(490, 'Pharmacology', 'PM2378. Zero order vs First order kinetics of drugs', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td width=\204\>\n<p><strong>Zero order</strong></p>\n</td>\n<td width=\204\>\n<p><strong>First order</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\204\>\n<p>Constant <strong>amount</strong> of drug is eliminated per unit time</p>\n</td>\n<td width=\204\>Constant <strong>fraction</strong> of drug is eliminated per unit time</td>\n</tr>\n<tr>\n<td width=\204\>\n<p>Rate of elimination is <strong>independent</strong> of plasma concentration</p>\n</td>\n<td width=\204\>Rate of elimination is <strong>proportional</strong> to the plasma concentration</td>\n</tr>\n<tr>\n<td width=\204\>\n<p>Clearance is <strong>more at low</strong> concentration and <strong>less at high</strong> concentration</p>\n</td>\n<td width=\204\>Clearance remains <strong>constant</strong>&nbsp;</td>\n</tr>\n<tr>\n<td width=\204\>\n<p>Half-life is <strong>less at low</strong> concentration and <strong>more at high</strong> concentration</p>\n</td>\n<td width=\204\>Half-life remains <strong>constant</strong></td>\n</tr>\n<tr>\n<td width=\204\>\n<p>Very <strong>few drugs</strong> follow zero-order kinetics, e.g.(<strong>WATT Power</strong>)</p>\n<ul>\n<li><strong>W</strong>arfarin</li>\n<li><strong>A</strong>lcohol,&nbsp;<strong>A</strong>spirin</li>\n<li><strong>T</strong>olbutamide</li>\n<li><strong>T</strong>heophylline&nbsp;</li>\n<li><strong>P</strong>henytoin</li>\n</ul>\n<p>&nbsp;</p>\n</td>\n<td width=\204\><strong>Most of the drugs</strong> follow first order kinetics&nbsp;</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/8bee4685a9ae43ae9d112e3d0790eaffx1280x2445.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(491, 'Pharmacology', 'PM2369. Drugs metabolised by glucuronidation', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ul>\n<li>Irinotecan</li>\n<li>Atazanavir</li>\n<li>Statins</li>\n<li>Benzodiazepines</li>\n<li>Estrogen</li>\n<li>morphine</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(492, 'Pharmacology', 'PM2368. Metabolism of drugs', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Drugs metabolized by cyp3A4 enzymes:</strong></p>\n<ul>\n<li>Protease inhibitors</li>\n<li>Statins</li>\n<li>Benzodiazepines</li>\n<li>Amiodarone</li>\n<li>Estrogen</li>\n<li>Erythromycin</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(493, 'Pharmacology', 'PM2367. Dialyzable and non-dialyzable drugs', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong><span class=\mw-headline\>Common dialyzable drugs:&nbsp;</span></strong></p>\n<ul>\n<li><strong>B</strong>arbiturates</li>\n<li><strong>L</strong>ithium</li>\n<li><strong>S</strong>alicylates</li>\n<li><strong>T</strong>heophylline/Caffeine (both are methylxanthines)</li>\n<li><strong>M</strong>ethanol</li>\n<li><strong>E</strong>thylene glycol</li>\n<li><strong>S</strong>odium valproate</li>\n<li><strong>I</strong>soniazid</li>\n<li><strong>C</strong>arbamazepine</li>\n</ul>\n<p><strong>Common Non-Dialyzable Drugs</strong> <strong>(AVOID Dialysis):</strong></p>\n<ul>\n<li><strong>A</strong>- Amphetamines</li>\n<li><strong> V</strong>- Verapamil</li>\n<li><strong>O</strong>- Opiates, Organophosphorous</li>\n<li><strong> I</strong>- Imipramine</li>\n<li><strong> D</strong>- Digoxin</li>\n<li><strong>Dialysis</strong>- Diazepam</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(494, 'Pharmacology', 'PM2365. Drugs which need Therapeutic Drug Monitoring (TDM)', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Drugs with a narrow therapeutic index require therapeutic drug monitoring (TDM).</p>\n<p>The mnemonic \<strong>DATLAAT MC</strong>\ can be used.</p>\n<ul>\n<li><strong>D</strong>: Digoxin</li>\n<li><strong>A</strong>: Anti-arrhythmic</li>\n<li><strong>T</strong>: Theophylline</li>\n<li><strong>L</strong>: Lithium</li>\n<li><strong>A</strong>: Aminoglycosides</li>\n<li><strong>A</strong>: Anti-epileptics</li>\n<li><strong>T</strong>: Tricyclic antidepressants</li>\n<li><strong>M</strong>: Methotrexate</li>\n<li><strong>C</strong>: Calcineurin inhibitors</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(495, 'Pharmacology', 'PM2364. Drugs having high first pass metabolism', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ul>\n<li>Lignocaine</li>\n<li>Propranolol</li>\n<li>Salbutamol</li>\n<li>Theophylline</li>\n<li>Imipramine</li>\n<li>Nitrates<br><br></li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(496, 'Pharmacology', 'PM2363. Drugs showing increased absorption in presence of food', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ul>\n<li>Lithium</li>\n<li>Fibrates</li>\n<li>Halofantrine</li>\n<li>Lumifantrine</li>\n<li>Griseofulvin</li>\n<li>Bedaquiline<br><br></li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(497, 'Obstetrics & Gynaecology,', 'PM2022. Teratogenic drugs and the birth defects', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td><strong>Drugs</strong></td>\n<td><strong>Adverse effects</strong></td>\n</tr>\n<tr>\n<td>Penicillamine</td>\n<td>Cutis laxa syndrome</td>\n</tr>\n<tr>\n<td><strong>Lithium</strong></td>\n<td><strong>Ebstein anomaly</strong></td>\n</tr>\n<tr>\n<td>Isotretinoin</td>\n<td>Facial-ear anomalies, heart disease, CNS anomalies</td>\n</tr>\n<tr>\n<td>Progesterone</td>\n<td>Masculinization of the female fetus</td>\n</tr>\n<tr>\n<td>Quinine</td>\n<td>Abortion, thrombocytopenia, deafness</td>\n</tr>\n<tr>\n<td>Topiramate</td>\n<td>Cleft lip</td>\n</tr>\n<tr>\n<td><strong>Valproate</strong></td>\n<td>Spina bifida, limb defects,&nbsp;<strong>autism spectrum disorder</strong>, facial and cardiac anomalies,&nbsp;<strong>Coarctation of the aorta</strong>, Aortic stenosis, Pulmonary atresia, Ventricular Septal Defect.&nbsp;</td>\n</tr>\n<tr>\n<td><strong>Vitamin D</strong></td>\n<td><strong>Supravalvular aortic stenosis</strong></td>\n</tr>\n<tr>\n<td><strong>Warfarin</strong></td>\n<td>Fetal bleeding, <strong>hypoplastic nasal structures</strong></td>\n</tr>\n<tr>\n<td><strong>Statins</strong></td>\n<td>Vertebral, anal, cardiac, tracheoesophageal fistula, renal, arterial, limb (<strong>VACTERAL</strong>) defects</td>\n</tr>\n<tr>\n<td><strong>Phenytoin</strong></td>\n<td>Ventricular septal defect, Atrial Septal Defect, Coarctation of the aorta, Patent Ductus Arteriosus</td>\n</tr>\n<tr>\n<td>ACE inhibitors and ARBs</td>\n<td>\n<p>Hypoplasia of organs, anuria, irreversible renal failure</p>\n</td>\n</tr>\n<tr>\n<td>Carbimazole and Methimazole</td>\n<td>&nbsp;\n<p>Choanal atresia, aplasia cutis, tracheoesophageal fistula and fetal goiter</p>\n</td>\n</tr>\n<tr>\n<td>Alcohol</td>\n<td>Atrial Septal Defect, Ventricular Septal Defect</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(498, 'Pathology,', 'PM1735. Difference between microsomal and non microsomal enzymes:', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>&nbsp;</strong></td>\n<td><strong>Microsomal enzymes</strong></td>\n<td><strong>Non microsomal enzymes</strong></td>\n</tr>\n<tr>\n<td><strong>Location</strong></td>\n<td><strong>Smooth endoplasmic reticulum</strong> (a system of microtubules inside the cell), primarily in liver, also in kidney, intestinal mucosa and lungs</td>\n<td><strong>Cytoplasm and mitochondria</strong> of hepatic cells as well as in other tissues including plasma.</td>\n</tr>\n<tr>\n<td><strong>Reactions catalysed</strong></td>\n<td>Most of the <strong>oxidations, reductions, hydrolysis and glucuronide conjugation</strong>.</td>\n<td>Some oxidations and reductions, many hydrolytic reactions and <strong>all conjugations except</strong> <strong>glucuronidation.</strong></td>\n</tr>\n<tr>\n<td><strong>Induction</strong></td>\n<td>Microsomal enzymes are <strong>inducible </strong>by drugs, diet and other agencies.</td>\n<td><strong>Not inducible</strong></td>\n</tr>\n<tr>\n<td><strong>Examples</strong></td>\n<td>Monooxygenases, cytochrome P450, UGTs,epoxide hydrolases, etc</td>\n<td>Esterases, amidases, some flavoprotein oxidasesand most conjugases</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(499, 'Pharmacology', 'PM1734. Phases of drug metabolism', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Phase I reactions: </strong>They<strong> a</strong><strong>ttach functional groups </strong>to the drugs. This usually leads to the inactivation of the drug.&nbsp;</p>\n<p>Examples:</p>\n<ul>\n<li>Oxidation</li>\n<li>Reduction</li>\n<li>Hydrolysis</li>\n<li>Cyclisation</li>\n<li>Deamination</li>\n</ul>\n<p><strong>Phase II reactions: </strong>They <strong>attach</strong> <strong>conjugates&nbsp;</strong>to the drugs. This&nbsp;facilitates drug elimination from the tissue.&nbsp;</p>\n<p>Examples:</p>\n<ul>\n<li>Glucuronide conjugation</li>\n<li>Sulfate conjugation</li>\n<li>Glycine conjugation</li>\n<li>Glutathione conjugation</li>\n<li>Acetylation</li>\n<li>Methylation</li>\n</ul>\n<p>All phase II reactions are mainly conducted by the non-microsomal enzymes except glucuronidation.</p></div><!---->\n      <!---->\n    </div><!---->'),
(500, 'Anaesthesia,', 'PM0505. The drugs which can be given via the Endotracheal Route', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ul>\n<li>Lidocaine</li>\n<li>Epinephrine</li>\n<li>Vasopressin</li>\n<li>Atropine</li>\n<li>Naloxone</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(501, 'Community Medicine,', 'PM0355. Phases of Clinical Trials', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Phase I</strong></p>\n<p>(First in human)</p>\n</td>\n<td>\n<p><strong>Phase II</strong></p>\n<p>(First in patient)</p>\n</td>\n<td>\n<p><strong>Phase III</strong></p>\n<p>(Multisite trial)</p>\n</td>\n<td>\n<p><strong>Phase IV</strong></p>\n<p>(Postmarketing)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>10–100&nbsp;</p>\n</td>\n<td>\n<p>50 - 500&nbsp;</p>\n</td>\n<td>\n<p>500 - 3000</p>\n</td>\n<td>\n<p>Large or according to study</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Usually healthy volunteers</p>\n</td>\n<td>\n<p>Patients with the target disease</p>\n</td>\n<td>\n<p>Patients&nbsp; with the target disease</p>\n</td>\n<td>\n<p>Real-world patients</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Open-label study</p>\n</td>\n<td>\n<p>Randomized and controlled trial or blinded</p>\n</td>\n<td>\n<p>Randomized and controlled trial or blinded</p>\n</td>\n<td>\n<p>Usually the observational or open-label</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Human pharmacology and safety</strong>: The study aims at safety and tolerability; PK parameters</p>\n</td>\n<td>\n<p><strong>Therapeutic exploration and dose-ranging</strong>: Efficacy and dose-ranging</p>\n</td>\n<td>\n<p>&nbsp;</p>\n<p><strong>Therapeutic confirmation/comparison</strong>: Indications&nbsp;are finalized and guidelines for therapeutic use are formulated. Monitoring of adverse reactions</p>\n</td>\n<td>\n<p><strong>Postmarketing surveillance/studies</strong>: Drug interactions; long-term or rare side effects</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Extends from Months to 1year</p>\n</td>\n<td>\n<p>1–2 years</p>\n</td>\n<td>\n<p>3–5 years</p>\n</td>\n<td>\n<p>Unending, after drug approval</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(502, 'Pharmacology', 'PM2136. Beta-blockers with vasodilating properties', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>The beta-blockers with <strong>vasodilating</strong> property are:</p>\n<ul>\n<li>Carvedilol</li>\n<li>Labetalol</li>\n<li>Bucindolol</li>\n<li>Celiprolol</li>\n<li>Nebivolol</li>\n<li>Nipradilol</li>\n<li>Carteolol</li>\n<li>Betaxolol</li>\n<li>Bopindolol</li>\n<li>Bevantolol</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(503, 'Medicine,', 'PM1579. Anti-obesity drugs', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Centrally acting </strong>anti-obesity drugs: The biologic effect of these agents on appetite regulation is produced by <strong>augmentation</strong> of the neurotransmission of three monoamines,&nbsp;<strong>norepinephrine</strong>, <strong>serotonin</strong> (<strong>5-HT</strong>) and&nbsp;<strong>dopamine</strong>.</p>\n<ul>\n<li><strong>Phentermine </strong>– Sympathomimetic (Phentermine is usually used in combination with <strong>topiramate</strong>)</li>\n<li><strong>Lorcaserin</strong> – Selective 5-HT2C receptor agonist</li>\n</ul>\n<p><strong>Peripherally acting </strong>medications:</p>\n<ul>\n<li><strong>Orlistat</strong> is a synthetic hydrogenated derivative of a naturally occurring <strong>lipase inhibitor,</strong> lipostatin, that is produced by the mold <em>Streptomyces toxytricini</em>.</li>\n</ul>\n<p>Anti-obesity drugs in development:</p>\n<ul>\n<li><strong>Bupropion</strong>:<strong>&nbsp;</strong>Dopamine and norepinephrine reuptake inhibitor</li>\n<li><strong>Naltrexone</strong>:<strong>&nbsp;</strong>Opioid receptor antagonist</li>\n</ul>\n<p><strong>Liraglutide</strong>:<strong>&nbsp;</strong>Glucagon-like peptide 1 receptor agonist</p>\n<p>Note: Tirzepatide is a GIP and GLP-1 agonist recently approved for weight loss treatment.</p></div><!---->\n      <!---->\n    </div><!---->'),
(504, 'Ophthalmology,', 'PM1170. Important adverse effects of anti-glaucoma drugs', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><span><strong>Anti-glaucoma drug</strong></span></td>\n<td><span><strong>The adverse effects</strong></span></td>\n</tr>\n<tr>\n<td><span>Beta-blockers</span></td>\n<td>\n<p><span>Corneal hypoesthesia</span></p>\n<p><span>Systemic side effects</span></p>\n</td>\n</tr>\n<tr>\n<td><span>Sympathomimetics</span></td>\n<td>\n<p><span>Lid retraction</span></p>\n<p><span>CNS depression and apnea in infants and children (&lt;2 years)</span></p>\n</td>\n</tr>\n<tr>\n<td><span>Prostaglandin analogs</span></td>\n<td>\n<p><span>Eyelash hypertrichosis</span></p>\n<p><span>Macular edema</span></p>\n<p><span>Iris pigmentation (Heterochromia iridis)</span></p>\n<p><span>Peri-orbital fat depletion</span></p>\n</td>\n</tr>\n<tr>\n<td><span>Miotics</span></td>\n<td>\n<p><span>Retinal detachment</span></p>\n<p><span>Cataract</span></p>\n</td>\n</tr>\n<tr>\n<td><span>Carbonic anhydrase inhibitors</span></td>\n<td>\n<p><span>Systemic side effects</span></p>\n<p><span>Corneal edema</span></p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(505, 'Pharmacology', 'PM1169. Cardioselective beta blockers: Mnemonic', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>C</strong>ardioselective <strong>B</strong>eta blockers <strong>A</strong>ct <strong>A</strong>t <strong>N</strong>othing <strong>B</strong>ut <strong>M</strong>yocardium <strong>E</strong>xclusively.</p>\n<ul>\n<li><strong>C</strong>: Celiprolol ( III )</li>\n<li><strong>B</strong>: Bisoprolol</li>\n<li><strong>A</strong>:&nbsp;Acebutolol</li>\n<li><strong>A</strong>: Atenolol</li>\n<li><strong>N</strong>: Nebivolol ( III )</li>\n<li><strong>B</strong>: Betaxolol ( III )</li>\n<li><strong>M</strong>: Metoprolol</li>\n<li><strong>E</strong>: Esmolol</li>\n</ul>\n<p>( III ) - Third generation beta blockers. All the others are second generation beta blockers</p></div><!---->\n      <!---->\n    </div><!---->'),
(506, 'Pharmacology', 'PM1168. Non-selective beta blockers with membrane stabilizing action', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ul>\n<li>Propranolol: highest </li>\n<li>Carvedilol </li>\n<li>Pindolol </li>\n<li>Labetalol</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(507, 'Pharmacology', 'PM1145. Muscarinic antagonists indicated for detrusor instability/overactive bladder', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Mechanism/Feature</strong></td>\n<td><strong>Drug</strong></td>\n</tr>\n<tr>\n<td>Direct smooth muscle relaxant &nbsp;</td>\n<td>\n<p>Flavoxate</p>\n<p>Oxybutynin</p>\n</td>\n</tr>\n<tr>\n<td>Bladder selective &nbsp;</td>\n<td>\n<p>Tolterodine</p>\n<p>Fesoterodine (prodrug)</p>\n</td>\n</tr>\n<tr>\n<td>Only quaternary compound &nbsp;</td>\n<td>Trospium</td>\n</tr>\n<tr>\n<td>M3 selective &nbsp;</td>\n<td>\n<p>Darifenacin</p>\n<p>Solifenacin</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(508, 'Pharmacology', 'PM1127. Dose dependent action of dopamine', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Dose</strong></td>\n<td><strong>Infusion rate </strong><br><strong>mcg/kg/min</strong>&nbsp;</td>\n<td><strong>Effects </strong></td>\n</tr>\n<tr>\n<td>Low</td>\n<td>&lt;2</td>\n<td><strong>D1</strong> and <strong>D2 </strong>stimulation(D1&gt;D2): Increase in renal blood flow and urine output</td>\n</tr>\n<tr>\n<td>Medium</td>\n<td>2-5</td>\n<td><strong>β<sub>1</sub></strong> stimulation: Additional cardiac stimulation</td>\n</tr>\n<tr>\n<td>High</td>\n<td>5-15</td>\n<td><strong>α<sub>1</sub></strong> stimulation: Vasoconstriction and increase in blood pressure</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(509, 'Pharmacology', 'PM1083. Cranial nerves involved in efferent and afferent outflow', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\213\>\n<p><strong>Parasympathetic outflow (efferent)</strong></p>\n</td>\n<td width=\213\>\n<p><strong>Cranial visceral sensory information (afferent)</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\213\>\n<p>III: Oculomotor</p>\n<p>VII: Facial</p>\n<p>IX: Glossopharyngeal</p>\n<p>X: Vagus</p>\n</td>\n<td width=\213\>\n<p>V: Trigeminal</p>\n<p>VII: Facial</p>\n<p>IX: Glossopharyngeal</p>\n<p>X: Vagus</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(510, 'Forensic Medicine,', 'PM0867. Muscarinic and Nicotinic Signs and symptoms of Organophosphorus Poisoning', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\ width=\527\>\n<tbody>\n<tr>\n<td><strong>Mechanism</strong></td>\n<td><strong>Site affected</strong></td>\n<td><strong>Symptoms</strong></td>\n</tr>\n<tr>\n<td rowspan=\6\><strong>Muscarinic</strong></td>\n<td>\n<p>Exocrine glands</p>\n</td>\n<td>\n<p>Salivation, lacrimation, perspiration</p>\n</td>\n</tr>\n<tr>\n<td>Eyes</td>\n<td>\n<p>Miosis, blurred vision</p>\n</td>\n</tr>\n<tr>\n<td>GIT</td>\n<td>Abdominal cramps, vomiting, diarrhea</td>\n</tr>\n<tr>\n<td>\n<p>Respiratory tract</p>\n</td>\n<td>Increased secretions, bronchoconstriction</td>\n</tr>\n<tr>\n<td>Bladder</td>\n<td>\n<p>Urinary frequency, incontinence</p>\n</td>\n</tr>\n<tr>\n<td>CVS</td>\n<td>\n<p>Bradycardia, hypotension</p>\n</td>\n</tr>\n<tr>\n<td rowspan=\2\><strong>Nicotinic</strong></td>\n<td>\n<p>Skeletal muscles</p>\n</td>\n<td>Muscle fasciculations, Flaccid paralysis</td>\n</tr>\n<tr>\n<td>\n<p>CVS</p>\n<p>(through cholinergic stimulation of sympathetic ganglia)</p>\n</td>\n<td>Tachycardia, Transient hypertension</td>\n</tr>\n<tr>\n<td><strong><span>Muscarinic and nicotinic</span></strong></td>\n<td>CNS</td>\n<td>Dizziness, lethargy, fatigue, headache, mental confusion, depression of respiratory centres, convulsions, coma</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(511, 'Pharmacology', 'PM0854. Benign prostatic hyperplasia treatment', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ul>\n<li>Static component: 5 alpha-reductase inhibitor [eg: Finasteride]</li>\n<li>Dynamic component: Alpha 1 blockers [eg: Tamsulosin, terazosin etc]</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(512, 'Ophthalmology,', 'PM0719. Antiglaucoma drugs: Mechanism of lowering intraocular pressure (IOP) at a glance', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Drugs that increase trabecular outflow</strong></p>\n<ul>\n<li>Miotics (e.g., pilocarpine)</li>\n<li>Epinephrine, dipivefrine</li>\n<li>Bimatoprost</li>\n<li>Rho-kinase inhibitor (Netarsudil)</li>\n</ul>\n<p><strong>Drugs that increase uveoscleral outflow</strong></p>\n<ul>\n<li>Prostaglandins (latanoprost)</li>\n<li>Epinephrine, dipivefrine</li>\n<li>Brimonidine</li>\n<li>Apraclonidine</li>\n</ul>\n<p><strong>Drugs that decrease aqueous production</strong></p>\n<ul>\n<li>Carbonic anhydrase inhibitors (e.g., acetazolamide, dorzolamide)</li>\n<li>Alpha receptor stimulators in ciliary process (e.g., epinephrine, dipivefrine, clonidine, brimonidine, apraclonidine)</li>\n<li>Beta-blockers</li>\n</ul>\n<p><strong>Hyperosmotic agents</strong> - (e.g., glycerol, mannitol, urea)</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/52d74961d2524b3bb5c22c2677259778x1280x1532.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(513, 'Medicine,', 'PM0131. Ipratropium vs Tiotropium', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><span>&nbsp;</span></td>\n<td>\n<p><span><strong>Ipratropium</strong></span></p>\n</td>\n<td>\n<p><span><strong>Tiotropium </strong></span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><span><strong>Duration of action</strong></span></p>\n</td>\n<td>\n<p><span>Short</span></p>\n</td>\n<td>\n<p><span>Long</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><span><strong>Frequency of administration</strong></span></p>\n</td>\n<td>\n<p><span>3-4 times/day</span></p>\n</td>\n<td>\n<p><span>Once daily</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><span><strong>Antimuscarinic action</strong></span></p>\n</td>\n<td>\n<p><span>Non-selective</span></p>\n</td>\n<td>\n<p><span>Relatively selective for M1 and M3 receptors</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><span><strong>Paradoxical broncho</strong></span></p>\n<p><span><strong>constriction</strong></span></p>\n</td>\n<td>\n<p><span>Present *</span></p>\n</td>\n<td>\n<p><span>Absent</span></p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>*Attributed to the nebulizer solution and other additives added. Blockade of prejunctional M2 receptors that normally inhibit acetylcholine release also can contribute.</p></div><!---->\n      <!---->\n    </div><!---->'),
(514, 'Pharmacology,', 'PM2320. Drugs used in heart failure', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><div>Drugs used in<strong> acute decompensated heart failure:</strong></div>\n<div>&nbsp;</div>\n<div>\n<table>\n<tbody>\n<tr>\n<td colspan=\2\><strong>Intravenous vasodilators</strong></td>\n</tr>\n<tr>\n<td>\n<p>Nitroglycerine</p>\n<p>Sodium nitroprusside</p>\n</td>\n<td>Decrease preload and afterload</td>\n</tr>\n<tr>\n<td>Nesiritide</td>\n<td>Recombinant human BNP</td>\n</tr>\n</tbody>\n</table>\n</div>\n<div>&nbsp;</div>\n<div>\n<table>\n<tbody>\n<tr>\n<td colspan=\2\><strong>Intravenous positive inotropes</strong></td>\n</tr>\n<tr>\n<td>\n<p>Dobutamine</p>\n<p>Dopamine</p>\n<p>Epinephrine</p>\n<p>Norepinephrine</p>\n</td>\n<td>β1 receptor-mediated stimulation of cardiac output</td>\n</tr>\n<tr>\n<td>\n<p>Enoximone</p>\n<p>Milrinone</p>\n</td>\n<td>PDE 3/4 inhibitors</td>\n</tr>\n<tr>\n<td>\n<p>Levosimendan</p>\n<p>Pimobendan</p>\n</td>\n<td>Combined Ca<sup>2+</sup> sensitizers and PDE3 inhibitors</td>\n</tr>\n</tbody>\n</table>\n</div>\n<div>&nbsp;</div>\n<div>\n<table>\n<tbody>\n<tr>\n<td colspan=\2\><strong>Drugs used for rapid symptom relief</strong></td>\n</tr>\n<tr>\n<td>\n<p>Bumetanide</p>\n<p>Furosemide</p>\n<p>Torasemide</p>\n</td>\n<td>Loop diuretics- used for symptomatic treatment of severe heart failure and acute decompensation.</td>\n</tr>\n<tr>\n<td>Morphine</td>\n<td>Opioid - to calm the patient and decreases cardiac O2 consumption</td>\n</tr>\n</tbody>\n</table>\n</div>\n<div>&nbsp;</div>\n<div>Drugs used in<strong> chronic heart failure:</strong></div>\n<div>\n<table>\n<tbody>\n<tr>\n<td><strong>Drugs</strong></td>\n<td><strong>Uses</strong></td>\n</tr>\n<tr>\n<td>ACE inhibitors</td>\n<td>Used as first-line therapy</td>\n</tr>\n<tr>\n<td>ARBs</td>\n<td>Used in case of intolerance to ACE inhibitors</td>\n</tr>\n<tr>\n<td>Beta-blockers</td>\n<td>Used as first-line therapy &nbsp;</td>\n</tr>\n<tr>\n<td>\n<p>Spironolactone</p>\n<p>Eplerenone</p>\n</td>\n<td>Mineralocorticoid receptor antagonists - first-line drugs for symptomatic heart failure.</td>\n</tr>\n<tr>\n<td>SGLT-2 inhibitors</td>\n<td>Used as 1st line therapy- reduces mortality and morbidity</td>\n</tr>\n<tr>\n<td>Sacubitril/Valsartan</td>\n<td>Neprilysin inhibitor/ARB(ARNI)</td>\n</tr>\n<tr>\n<td>Ivabradine</td>\n<td>Inhibitor of I<sub>f</sub>&nbsp;current in the sinoatrial node</td>\n</tr>\n</tbody>\n</table>\n</div>\n<div>&nbsp;</div></div><!---->\n      <!---->\n    </div><!---->');
INSERT INTO db_pearl_m (pid, subject, pearl_title, pearl_desc) VALUES
(515, 'Medicine,', 'PM1567. Anti-anginal drugs', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Anti-anginal drugs can be divided into the following categories:</p>\n<ul>\n<li><strong>Nitrates</strong> - dilates veins &gt; arteries, reduces preload</li>\n<li><strong>Calcium channel blockers</strong> - blocks L-type calcium channels\n<ul>\n<li>Dihydropyridines - amlodipine, nifedipine</li>\n<li>Non-dihydropyridines - verapamil, diltiazem</li>\n</ul>\n</li>\n<li><strong>Beta-blockers</strong> - decreases heart rate, contractility and oxygen demand</li>\n<li>Other drugs:&nbsp;\n<ul>\n<li><strong>Ranolazine</strong> - late Na<sup>+</sup>&nbsp;channel blocker</li>\n<li><strong>Ivabradine</strong> - inhibit funny currents in SA node</li>\n<li><strong>Nicorandil</strong> - K<sup>+</sup>-ATP channel agonist</li>\n<li><strong>Trimetazidine</strong> - pFOX inhibitor</li>\n<li><strong>Fasudil</strong> - rho-kinase inhibitor</li>\n</ul>\n</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(516, 'Medicine,', 'PM1224. Parenteral drugs used in hypertensive emergencies', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\113\>\n<p>&nbsp;</p>\n</td>\n<td width=\229\>\n<p><strong>Drugs</strong></p>\n</td>\n<td width=\119\>\n<p><strong>Contraindications</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\113\>\n<p>Beta-blockers</p>\n</td>\n<td width=\229\>Labetalol, esmolol, metoprolol</td>\n<td width=\119\>\n<p>Heart block, systolic heart failure, asthma bradycardia</p>\n</td>\n</tr>\n<tr>\n<td width=\113\>\n<p>Calcium channel blockers</p>\n</td>\n<td width=\229\>\n<p>Clevidipine, nicardipine</p>\n</td>\n<td width=\119\>\n<p>Advanced aortic stenosis&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td width=\113\>\n<p>D1 receptor agonist</p>\n</td>\n<td width=\229\>Fenoldopam</td>\n<td width=\119\>\n<p>Caution in glaucoma</p>\n</td>\n</tr>\n<tr>\n<td rowspan=\3\ width=\113\>\n<p>Vasodilators</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n</td>\n<td width=\229\>\n<p>Nitroglycerin</p>\n</td>\n<td width=\119\>Pericardial tamponade</td>\n</tr>\n<tr>\n<td width=\229\>\n<p>Nitroprusside</p>\n</td>\n<td width=\119\>Relatively contraindicated in liver failure and renal failure&nbsp;</td>\n</tr>\n<tr>\n<td width=\229\>\n<p>Hydralazine</p>\n</td>\n<td width=\119\>&nbsp;CAD, RHD</td>\n</tr>\n<tr>\n<td width=\113\>\n<p>ACE inhibitors</p>\n</td>\n<td width=\229\>\n<p>Enalaprilat</p>\n</td>\n<td width=\119\>History of angioedema</td>\n</tr>\n<tr>\n<td width=\113\>\n<p>Alpha-blocker</p>\n</td>\n<td width=\229\>\n<p>Phentolamine, Urapidil</p>\n</td>\n<td width=\119\>&nbsp;Aortic stenosis, AV shunt</td>\n</tr>\n<tr>\n<td width=\113\>\n<p>Centrally acting sympatholytics</p>\n</td>\n<td width=\229\>\n<p>Clonidine</p>\n</td>\n<td width=\119\>Severe AV block, sick-sinus syndrome</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(517, 'Pharmacology,', 'PM1209. Drugs used in Pulmonary Arterial Hypertension (PAH)', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Drug Class</strong></td>\n<td><strong>Examples</strong></td>\n</tr>\n<tr>\n<td>Prostacyclin derivative</td>\n<td>\n<p>Epoprostenol</p>\n<p>Iloprost</p>\n<p>Treprostinil</p>\n</td>\n</tr>\n<tr>\n<td>Endothelin receptor antagonist</td>\n<td>\n<p>Bosentan</p>\n<p>Ambrisentan</p>\n<p>Macitentan</p>\n</td>\n</tr>\n<tr>\n<td>PDE5 inhibitor</td>\n<td>\n<p>Sildenafil</p>\n<p>Tadalafil</p>\n</td>\n</tr>\n<tr>\n<td>Soluble guanyl cyclase stimulator</td>\n<td>Riociguat</td>\n</tr>\n<tr>\n<td>Calcium channel blocker</td>\n<td>\n<p>Nifedipine</p>\n<p>Diltiazem</p>\n<p>Amlodipine</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(518, 'Pharmacology', 'PM0392. Site and mechanism of action of diuretics', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><span><strong>Diuretics&nbsp;</strong></span></td>\n<td><span><strong>Site of action&nbsp;&nbsp;</strong></span></td>\n<td>\n<p><span><strong>Enzyme/receptor</strong><strong>/transporter inhibited&nbsp;</strong></span></p>\n</td>\n</tr>\n<tr>\n<td><span><strong>Carbonic anhydrase inhibitors&nbsp;&nbsp;</strong></span></td>\n<td><span>Proximal tubule&nbsp;&nbsp;</span></td>\n<td><span>Carbonic anhydrase&nbsp;&nbsp;</span></td>\n</tr>\n<tr>\n<td><span><strong>Loop diuretics&nbsp;</strong></span></td>\n<td><span>Thick ascending loop of Henle&nbsp;</span></td>\n<td><span>Na<sup>+</sup>- K<sup>+</sup>- 2Cl<sup>-</sup>&nbsp;symporter</span></td>\n</tr>\n<tr>\n<td><span><strong>Thiazides</strong></span></td>\n<td><span>Distal convoluted tubule&nbsp;</span></td>\n<td><span>Na<sup>+</sup>- Cl<sup>-</sup> symporter</span></td>\n</tr>\n<tr>\n<td><span><strong>Epithelial sodium channel inhibitors&nbsp;</strong></span></td>\n<td><span>Late distal tubule and collecting ducts&nbsp;&nbsp;</span></td>\n<td><span>ENaC&nbsp;</span></td>\n</tr>\n<tr>\n<td><span><strong>Aldosterone antagonists&nbsp;</strong></span></td>\n<td><span>Late distal tubule and collecting ducts&nbsp;</span></td>\n<td><span>Mineralocorticoid receptors (indirectly ENaC)&nbsp;</span></td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/1eebed7de1f34fd99539e6c7613f42f0x1280x1306.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(519, 'Medicine,', 'PM0115. Anti-arrhythmic drugs', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/7d6c4d5ed4a54f70b2030d71f3d06969x1280x1697.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><span><strong>Class of antiarrhythmic drugs&nbsp;</strong></span></p>\n</td>\n<td>\n<p><span>&nbsp;</span></p>\n<p><span><strong>Mechanism of action</strong></span></p>\n</td>\n<td>\n<p><span><strong>Effect on action potential duration</strong></span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><span><strong>Class I</strong></span></p>\n</td>\n<td><span>&nbsp;</span></td>\n<td><span>&nbsp;</span></td>\n</tr>\n<tr>\n<td>\n<p><span><strong>Class IA</strong></span></p>\n<p><span>Procainamide</span></p>\n<p><span>Quinidine</span></p>\n<p><span>Disopyramide</span></p>\n</td>\n<td>\n<p><span>&nbsp;</span></p>\n<p><span>Na+ channel blockers with significant K<sup>+</sup> channel blocking property</span></p>\n<p><span>&nbsp;</span></p>\n</td>\n<td>\n<p><span>Prolonged</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><span><strong>Class IB</strong></span></p>\n<p><span>Lidocaine</span></p>\n<p><span>Mexiletine</span></p>\n</td>\n<td><span>Na+ channel blockers with K<sup>+</sup> channel opening property&nbsp;</span></td>\n<td>\n<p><span>Shortened or no effect</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><span><strong>Class IC</strong></span></p>\n<p><span>Flecainide</span></p>\n<p><span>Propafenone</span></p>\n<p><span>Moricizine</span></p>\n</td>\n<td><span>Na+ channel blockers with negligible K<sup>+</sup> channel blocking property&nbsp;</span></td>\n<td>\n<p><span>Variable or no effect</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><span><strong>Class II</strong></span></p>\n</td>\n<td><span>&nbsp;</span></td>\n<td><span>&nbsp;</span></td>\n</tr>\n<tr>\n<td>\n<p><span>Propranolol</span></p>\n<p><span>Esmolol</span></p>\n</td>\n<td><span>β- blockers</span></td>\n<td><span>-&nbsp;</span></td>\n</tr>\n<tr>\n<td>\n<p><span><strong>Class III</strong></span></p>\n</td>\n<td><span>&nbsp;</span></td>\n<td><span>&nbsp;</span></td>\n</tr>\n<tr>\n<td>\n<p><span>Amiodarone, dronedarone</span></p>\n<p><span>Dofetilide</span></p>\n<p><span>Sotalol</span></p>\n<p><span>Ibutilide</span></p>\n<p><span>Vernakalant</span></p>\n</td>\n<td><span>K<sup>+</sup> channel blockers&nbsp;</span></td>\n<td><span>Prolonged&nbsp;</span></td>\n</tr>\n<tr>\n<td>\n<p><span><strong>Class IV</strong></span></p>\n</td>\n<td><span>&nbsp;</span></td>\n<td><span>&nbsp;</span></td>\n</tr>\n<tr>\n<td>\n<p><span>Verapamil</span></p>\n<p><span>Diltiazem</span></p>\n</td>\n<td><span>Ca<sup>2+</sup> channel blockers</span></td>\n<td><span>-</span></td>\n</tr>\n<tr>\n<td>\n<p><span><strong>Miscellaneous</strong></span></p>\n</td>\n<td><span>&nbsp;</span></td>\n<td><span>&nbsp;</span></td>\n</tr>\n<tr>\n<td>\n<p><span>Adenosine</span></p>\n</td>\n<td><span>Activates adenosine receptors</span></td>\n<td><span>Shortened</span></td>\n</tr>\n<tr>\n<td>\n<p><span>Digoxin</span></p>\n</td>\n<td><span>Na<sup>+</sup>-K<sup>+</sup> ATPase inhibitor</span></td>\n<td><span>Increases AV node refractoriness</span></td>\n</tr>\n<tr>\n<td>\n<p><span>Magnesium</span></p>\n</td>\n<td><span>Unknown</span></td>\n<td><span>-</span></td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(520, 'Pharmacology', 'PM0092. Types of calcium channels and their blockers', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Type&nbsp;of Calcium Channel</strong></td>\n<td><strong>Blockers</strong></td>\n</tr>\n<tr>\n<td>L type</td>\n<td>Verapamil, diltiazem, dihydropyridines</td>\n</tr>\n<tr>\n<td>T type</td>\n<td>Flunarizine, ethosuximide, mibefradil</td>\n</tr>\n<tr>\n<td>N type</td>\n<td>Gabapentin, ziconotide</td>\n</tr>\n<tr>\n<td>P/Q type</td>\n<td>Omega agatoxin IVa</td>\n</tr>\n<tr>\n<td>R type</td>\n<td>SNX-482, Omega agatoxin IIIa</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(521, 'Medicine,', 'PM0486. Effect of diuretics on excretion of electrolytes', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Diuretics</strong></td>\n<td><strong>Sodium</strong></td>\n<td><strong>Potassium</strong></td>\n<td><strong>Calcium</strong></td>\n</tr>\n<tr>\n<td><strong>Carbonic anhydrase inhibitors</strong></td>\n<td>↑</td>\n<td>↑↑</td>\n<td>No change</td>\n</tr>\n<tr>\n<td><strong>Osmotic diuretics</strong></td>\n<td>↑↑</td>\n<td>↑</td>\n<td>↑</td>\n</tr>\n<tr>\n<td><strong>Loop diuretics</strong></td>\n<td>↑↑</td>\n<td>↑↑</td>\n<td>↑↑</td>\n</tr>\n<tr>\n<td><strong>Thiazides</strong></td>\n<td>↑</td>\n<td>↑↑</td>\n<td>↓</td>\n</tr>\n<tr>\n<td><strong>Epithelial sodium channel blockers</strong></td>\n<td>↑</td>\n<td>↓</td>\n<td>↓</td>\n</tr>\n<tr>\n<td><strong>Aldosterone antagonists</strong></td>\n<td>↑</td>\n<td>↓</td>\n<td>Insufficient data</td>\n</tr>\n</tbody>\n</table>\n<p>Acute effects of thiazides on Ca<sup>2+</sup> excretion are variable but when administered chronically, it decreases Ca<sup>2+</sup> excretion.</p></div><!---->\n      <!---->\n    </div><!---->'),
(522, 'Pharmacology', 'PM2484. Mechanism of action of common anti-epileptics', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Mechanism of action of some common antiepileptics:</p>\n<table>\n<tbody>\n<tr>\n<td>Mechanism of action</td>\n<td>Antiepileptics</td>\n</tr>\n<tr>\n<td>Sodium channel blockers</td>\n<td>\n<ul>\n<li>Phenytoin</li>\n<li>Carbamazepine</li>\n<li>Oxcarbazepine</li>\n<li>Eslicarbazepine</li>\n<li>Lamotrigine &nbsp; &nbsp;&nbsp;</li>\n<li>Topiramate &nbsp; &nbsp; &nbsp;</li>\n<li>Felbamate</li>\n<li>Valproate</li>\n<li>Rufinamide</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Calcium-channel blockers</td>\n<td>\n<ul>\n<li>Valproate</li>\n<li>Lamotrigine &nbsp;&nbsp;</li>\n<li>Ethosuximide</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>GABA<sub>A&nbsp;</sub>receptor modulators</td>\n<td>\n<ul>\n<li>Benzodiazepine</li>\n<li>Phenobarbital</li>\n<li>Felbamate</li>\n<li>Primidone</li>\n<li>Topiramate</li>\n<li>Carbamazepine</li>\n<li>Oxcarbazepine</li>\n<li>Stiripentol</li>\n<li>Clobazam</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>GABA uptake inhibitors</td>\n<td>\n<ul>\n<li>Tiagabine</li>\n<li>Vigabatrin</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Synaptic vesicle glycoprotein 2A(SV2A) inhibitors</td>\n<td>\n<ul>\n<li>Levetiracetam</li>\n<li>Brivaracetam</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>NMDA receptor antagonist</td>\n<td>\n<ul>\n<li>Felbamate</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>AMPA/Kainate receptor antagonist</td>\n<td>\n<ul>\n<li>Phenobarbital</li>\n<li>Topiramate</li>\n<li>Perampanel</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>HCN channel activity enhancers</td>\n<td>\n<ul>\n<li>Lamotrigine</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Inhibitors of brain carbonic anhydrase</td>\n<td>\n<ul>\n<li>Acetazolamide</li>\n<li>Zonisamide</li>\n<li>Topiramate</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Potassium-channel openers</td>\n<td>\n<ul>\n<li>Retigabine</li>\n<li>Topiramate</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(523, 'Pharmacology,', 'PM2424. Medical treatment of Parkinsons disease', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Increase dopaminergic action</strong></td>\n<td><strong>Anti cholinergics</strong></td>\n</tr>\n<tr>\n<td>\n<p><strong>Dopamine precursor</strong><strong>&nbsp; &nbsp; &nbsp; &nbsp;</strong></p>\n</td>\n<td><strong>Anti muscarinic </strong></td>\n</tr>\n<tr>\n<td>Levodopa</td>\n<td>Benzhexol (trihexyphenidyl)</td>\n</tr>\n<tr>\n<td><strong>Dopa decarboxylase inhibitor </strong></td>\n<td>Benztropine</td>\n</tr>\n<tr>\n<td>Carbedopa</td>\n<td>Procyclidine</td>\n</tr>\n<tr>\n<td>Benserazide</td>\n<td><strong>H1 antagonist </strong></td>\n</tr>\n<tr>\n<td><strong>Dopamine agonist-Ergot derivatives</strong></td>\n<td>Promethazine</td>\n</tr>\n<tr>\n<td>Bromocriptine</td>\n<td>Orphenadrine</td>\n</tr>\n<tr>\n<td>Cabergoline</td>\n<td>Diphenhydramine</td>\n</tr>\n<tr>\n<td>Pergolide</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>Lisuride</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td><strong>Dopamine agonist-Non ergot&nbsp;derivatives</strong></td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>Ropinirole</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>Pramipexole</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>Rotigotine</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>Apomorphine</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td><strong>Dopamine facilitator </strong></td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>Amantadine</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td><strong>MAO-B inhibitor </strong></td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>Selegiline</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>Rasagiline</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td><strong>COMT inhibitor </strong></td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>Tolcapone</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>Entacapone</td>\n<td>&nbsp;</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(524, 'Psychiatry,', 'PM2418. Actions of antipsychotic drugs', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Dopaminergic pathway</strong></td>\n<td><strong>Significance</strong></td>\n<td><strong>Action of drug blocking D2 receptors</strong></td>\n</tr>\n<tr>\n<td><strong>Nigrostriatal</strong></td>\n<td>Movement</td>\n<td>Extrapyramidal symptoms</td>\n</tr>\n<tr>\n<td><strong>Mesolimbic</strong></td>\n<td>Speech, grasp of reality, mentation, and mood</td>\n<td>Reduces positive symptoms of schizophrenia</td>\n</tr>\n<tr>\n<td><strong>Mesocortical</strong></td>\n<td>Motivation, pleasure, socialization</td>\n<td>May increase negative symptoms of schizophrenia</td>\n</tr>\n<tr>\n<td><strong>Tuberoinfundibular pathway</strong></td>\n<td>Inhibits prolactin release</td>\n<td>Hyperprolactinemia</td>\n</tr>\n<tr>\n<td><strong>Chemoreceptor trigger zone</strong></td>\n<td>Vomiting reflex</td>\n<td>Anti emetic</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(525, 'Psychiatry,', 'PM2415. Adverse effects of specific antidepressants', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Drug</strong></p>\n</td>\n<td>\n<p><strong>Side effect</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Amitriptyline</strong></p>\n</td>\n<td>\n<p>sedation, antimuscarinic, <strong>hypotension</strong>, <strong>arrhythmia,</strong> seizures</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Fluoxetine</strong></p>\n</td>\n<td>\n<p>Nausea, anorexia,<strong> loss of libido,</strong> insomnia</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Trazodone</strong></p>\n</td>\n<td>\n<p>Sedative, <strong>priapism</strong>, hypotension</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Bupropion</strong></p>\n</td>\n<td>\n<p>Seizures, mild tachycardia, hypertension</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Mirtazapine</strong></p>\n</td>\n<td>\n<p>Sedative, increased appetite, weight gain</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(526, 'Anaesthesia,', 'PM2301. Unique features of Nitrous oxide', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Concentration-effect</strong></p>\n<ul>\n<li>The blood gas partition coefficient of N<sub>2</sub>O is 35 times higher&nbsp;than O<sub>2</sub> and N<sub>2,</sub>&nbsp;hence its rapid uptake diminishes its volume and&nbsp;concentrates&nbsp;the remaining anesthetics in the alveolus.</li>\n</ul>\n<p><strong>Second-gas effect</strong></p>\n<ul>\n<li>Concentration-effect of one gas over another is known as the second gas effect</li>\n<li>Increase in the partial pressure of the <strong>2nd gas</strong> (Oxygen and volatile agents) due to the <strong>rapid uptake</strong> of N<sub>2</sub>O during <strong>induction.</strong></li>\n</ul>\n<p><strong>Diffusion hypoxia (Finks effect)</strong></p>\n<ul>\n<li><strong>Rapid elimination </strong>of N<sub>2</sub>O during recovery causes a reduction in the partial pressure of oxygen leading to<strong> hypoxia</strong></li>\n<li>Avoided by using 100% oxygen for 10 minutes during recovery from anesthesia.</li>\n</ul>\n<p><strong>Nitrous oxide in gas-filled spaces</strong></p>\n<div class=\page\ title=\Page 181\>\n<div class=\layoutArea\>\n<div class=\column\>\n<ul>\n<li>It diffuses into air-containing cavities more rapidly than air (principally nitrogen) can be absorbed by the bloodstream</li>\n<li>If the walls surrounding the cavity are rigid, pressure rises instead of volume.</li>\n</ul>\n<p>Hence N<sub>2</sub>O is <strong>contraindicated</strong> in:</p>\n<ul>\n<li>Venous or arterial air embolism</li>\n<li>Acute intestinal obstruction</li>\n<li>Pneumocephalus, pneumothorax</li>\n<li>Pulmonary air cysts</li>\n<li>Intraocular air bubbles</li>\n<li>Tympanic membrane grafting.</li>\n</ul>\n</div>\n</div>\n</div></div><!---->\n      <!---->\n    </div><!---->'),
(527, 'Psychiatry,', 'PM2079. Antipsychotic drugs and their potential to cause side effects', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<div><span><strong>Side effect</strong></span></div>\n</td>\n<td>\n<div><span><strong>Highest potential</strong></span></div>\n</td>\n<td>\n<div><span><strong>Least potential</strong></span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span><strong>Anticholinergic</strong></span></div>\n</td>\n<td>\n<div><span>Chlorpromazine, Thioridazine&nbsp;</span></div>\n</td>\n<td>\n<div><span>Haloperidol, Risperidone</span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span><strong>Hypotension</strong></span></div>\n</td>\n<td>\n<div><span>Chlorpromazine, Clozapine</span></div>\n</td>\n<td>\n<div><span>Haloperidol, Aripiprazole</span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span><strong>Sedation</strong></span></div>\n</td>\n<td>\n<div><span>Clozapine, Chlorpromazine</span></div>\n</td>\n<td>\n<div><span>Aripiprazole, Ziprasidone</span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span><strong>Metabolic syndrome</strong>,&nbsp;<strong>Diabetes</strong></span></div>\n</td>\n<td>\n<div><span>Clozapine, Olanzapine</span></div>\n</td>\n<td>\n<div><span>Aripiprazole, Ziprasidone</span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span><strong>Weight gain</strong></span></div>\n</td>\n<td>\n<div><span>Clozapine, Olanzapine</span></div>\n</td>\n<td>\n<div><span>Aripiprazole, Haloperidol</span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span><strong>Hyperprolactinemia</strong></span></div>\n</td>\n<td>\n<div><span>Amisulpride, Haloperidol, Risperidone</span></div>\n</td>\n<td>\n<div><span>Aripiprazole, Quetiapine</span></div>\n<div><span>Clozapine, Olanzapine- almost zero risk (mild risk)</span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span><strong>Extrapyramidal effects</strong></span></div>\n</td>\n<td>\n<div><span>Haloperidol</span></div>\n</td>\n<td>\n<div><span>Clozapine, Quetiapine</span></div>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(528, 'Pharmacology,', 'PM1697. Important Adverse Effects of Anti-epileptic Drugs', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Antiepileptic drugs</strong></p>\n</td>\n<td>\n<p><strong>Adverse effects</strong></p>\n</td>\n</tr>\n<tr>\n<td>Carbamazepine</td>\n<td>Stevens-Johnson syndrome (SJS), diplopia, ataxia, hyponatremia</td>\n</tr>\n<tr>\n<td>Phenytoin</td>\n<td>Gum hyperplasia, hirsutism, osteomalacia, ataxia</td>\n</tr>\n<tr>\n<td>Valproic acid</td>\n<td>Hepatotoxicity, thrombocytopenia, transient alopecia, tremors, pancreatitis</td>\n</tr>\n<tr>\n<td>Phenobarbital</td>\n<td>Sedation, depression, ataxia, automatism</td>\n</tr>\n<tr>\n<td>Vigabatrin</td>\n<td>Loss of peripheral vision</td>\n</tr>\n<tr>\n<td>Felbamate</td>\n<td>Fulminant hepatic failure, aplastic anemia</td>\n</tr>\n<tr>\n<td>Lamotrigine</td>\n<td>Hypersensitivity reactions, SJS</td>\n</tr>\n<tr>\n<td>Topiramate</td>\n<td>Paresthesias, renal calculi, weight loss, acute glaucoma</td>\n</tr>\n<tr>\n<td>Oxcarbazepine</td>\n<td>Hyponatremia, SJS</td>\n</tr>\n<tr>\n<td>Ezogabine</td>\n<td>Urinary retention</td>\n</tr>\n<tr>\n<td>Zonisamide</td>\n<td>Renal calculi</td>\n</tr>\n<tr>\n<td>Perampanel</td>\n<td>Homicidal ideation</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(529, 'Pharmacology,', 'PM1695. First-line Agents in Different Types of Seizures', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Type of seizure&nbsp;</strong></td>\n<td><strong>1st-line antiepileptic drugs</strong></td>\n</tr>\n<tr>\n<td>\n<p>Focal seizures&nbsp;</p>\n</td>\n<td>\n<ul>\n<li>Carbamazepine</li>\n<li>Oxcarbazepine&nbsp;</li>\n<li>Lamotrigine</li>\n<li>Levetiracetam</li>\n<li>Phenytoin</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>GTCS&nbsp;</td>\n<td>\n<ul>\n<li aria-level=\1\>Sodium valproate</li>\n<li aria-level=\1\>Levetiracetam&nbsp;</li>\n<li aria-level=\1\>Lamotrigine&nbsp;</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Typical absence seizures&nbsp;</td>\n<td>\n<ul>\n<li><strong>Ethosuximide&nbsp;</strong>(drug of choice)</li>\n<li>Sodium valproate</li>\n<li>Lamotrigine</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Atypical absence seizures</td>\n<td>\n<ul>\n<li aria-level=\1\>Sodium valproate&nbsp;</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Myoclonic seizures</td>\n<td>\n<ul>\n<li aria-level=\1\><strong>Sodium valproate</strong> (drug of choice)</li>\n<li aria-level=\1\>Lamotrigine</li>\n<li aria-level=\1\>Topiramate</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(530, 'Psychiatry,', 'PM1560. Pure opioid antagonists', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Naloxone</strong>: short-acting opioid antagonist with duration of action of 1-2 hours. Used in opioid overdose and respiratory depression in newborns due to perinatal opioid use.</p>\n<p><strong>Naltrexone</strong>: long-acting opioid antagonist with effects lasting till 48 hours. Used for prevention of relapse in opioid addiction treatment. It is associated with hepatotoxicity. It does not suppress craving in opioid addiction but suppresses craving in chronic alcoholics.</p>\n<p><strong>Nalmefene</strong>: long-acting opioid antagonist with a half-life of 8-10 hours administered intravenously. Used in opioid overdose like naloxone.</p></div><!---->\n      <!---->\n    </div><!---->'),
(531, 'Medicine,', 'PM1163. CNS Stimulants and Depressants', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>CNS stimulants</strong></td>\n<td><strong>CNS depressants</strong></td>\n</tr>\n<tr>\n<td>\n<p>Directly acting on CNS:</p>\n<ul>\n<li>Cortical: Caffeine, Aminophylline, Amphetamine, Ephedrine</li>\n<li>Medullary: Picrotoxin, Adrenaline</li>\n<li>Spinal: Strychnine, Brucine</li>\n</ul>\n<p>Reflexly acting on CNS:</p>\n<ul>\n<li>Nicotine, Lobetine</li>\n</ul>\n</td>\n<td>\n<p>Sedatives: Diazepam, Oxazepam</p>\n<p>Hypnotics: Clonazepam, Nitrazepam</p>\n<p>Alcohol: Dose dependant sedative-hypnotic</p>\n<p>Narcotics: Morphine, Codeine</p>\n<p>General anesthetics: Barbiturates, Halothane</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(532, 'Anaesthesia,', 'PM0532. Classification of Local Anesthetics based on the Potency', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ol>\n<li><strong>Low Potency -&nbsp;</strong>Procaine</li>\n<li><strong>Intermediate Potency</strong>\n<ul>\n<li>Mepivacaine</li>\n<li>Prilocaine</li>\n<li>Chloroprocaine</li>\n<li>Lidocaine</li>\n</ul>\n</li>\n<li><strong>High Potency</strong>\n<ul>\n<li>Tetracaine</li>\n<li>Bupivacaine</li>\n<li>Etidocaine</li>\n</ul>\n</li>\n</ol></div><!---->\n      <!---->\n    </div><!---->'),
(533, 'Anaesthesia,', 'PM0531. Classification of local anesthetics based on duration of action', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Short</strong> duration of action ( 20-45 minutes):</p>\n<ul>\n<li>Procaine</li>\n<li>Chloroprocaine</li>\n</ul>\n<p><strong>Intermediate</strong> duration ( 60-120 minutes):</p>\n<ul>\n<li>Lidocaine</li>\n<li>Mepivacaine</li>\n<li>Prilocaine</li>\n</ul>\n<p><strong>Long</strong> duration (400-450 minutes):</p>\n<ul>\n<li>Tetracaine</li>\n<li>Bupivacaine</li>\n<li>Ropivacaine</li>\n<li>Etidocaine</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(534, 'Anaesthesia,', 'PM0529. Classification of opioids based on their action on opioid receptors', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><div class=\page\ title=\Page 569\>\n<table><colgroup><col><col><col><col><col><col><col><col> </colgroup>\n<tbody>\n<tr>\n<td colspan=\1\ rowspan=\2\>\n<div class=\layoutArea\>\n<div class=\column\>\n<p><strong>Generic Name</strong></p>\n</div>\n</div>\n</td>\n<td colspan=\3\ rowspan=\1\>\n<div class=\layoutArea\>\n<div class=\column\>\n<p><strong>Receptor Effects&nbsp;</strong></p>\n</div>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p><strong>µ</strong></p>\n</div>\n</div>\n</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p><strong>δ</strong></p>\n</div>\n</div>\n</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p><strong>κ</strong></p>\n</div>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p><strong>Morphine</strong></p>\n</div>\n</div>\n</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>+++</p>\n</div>\n</div>\n</td>\n<td>&nbsp;</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>+</p>\n</div>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p><strong>Methadone</strong></p>\n</div>\n</div>\n</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>+++</p>\n</div>\n</div>\n</td>\n<td>&nbsp;</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p><strong>Meperidine</strong></p>\n</div>\n</div>\n</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>+++</p>\n</div>\n</div>\n</td>\n<td>&nbsp;</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p><strong>Fentanyl</strong></p>\n</div>\n</div>\n</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>+++</p>\n</div>\n</div>\n</td>\n<td>&nbsp;</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p><strong>Sufentanil</strong></p>\n</div>\n</div>\n</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>+++</p>\n</div>\n</div>\n</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>+</p>\n</div>\n</div>\n</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>+</p>\n</div>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p><strong>Codeine</strong></p>\n</div>\n</div>\n</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>±</p>\n</div>\n</div>\n</td>\n<td>&nbsp;</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p><strong>Pentazocine</strong></p>\n</div>\n</div>\n</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>±</p>\n</div>\n</div>\n</td>\n<td>&nbsp;</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>+</p>\n</div>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p><strong>Nalbuphine</strong></p>\n</div>\n</div>\n</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>-</p>\n</div>\n</div>\n</td>\n<td>&nbsp;</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>++</p>\n</div>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p><strong>Buprenorphine</strong></p>\n</div>\n</div>\n</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>±</p>\n</div>\n</div>\n</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>-</p>\n</div>\n</div>\n</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>-</p>\n</div>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p><strong>Butorphanol</strong></p>\n</div>\n</div>\n</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>±</p>\n</div>\n</div>\n</td>\n<td>&nbsp;</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>+++</p>\n</div>\n</div>\n</td>\n</tr>\n</tbody>\n</table>\n<div class=\page\ title=\Page 569\>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>+++, ++, +, strong agonist; ±, partial or weak agonist; -, antagonist.</p>\n</div>\n</div>\n</div>\n</div></div><!---->\n      <!---->\n    </div><!---->'),
(535, 'Psychiatry,', 'PM0526. Non extrapyramidal side effects of antipsychotics', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Typical antipsychotics</strong></p>\n<table>\n<tbody>\n<tr>\n<td><strong>Drug</strong></td>\n<td><strong>Side effects</strong></td>\n</tr>\n<tr>\n<td>Chlorpromazine</td>\n<td>Corneal &amp; lenticular deposits, <strong>vortex keratopathy</strong>, obstructive jaundice</td>\n</tr>\n<tr>\n<td>Thioridazine</td>\n<td>Irreversible <strong>retinal pigmentation</strong>,&nbsp;QT prolongation</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>Atypical antipsychotics</strong></p>\n<table>\n<tbody>\n<tr>\n<td><strong>Drug</strong></td>\n<td><strong>Side effects</strong></td>\n</tr>\n<tr>\n<td>Clozapine</td>\n<td><strong>Agranulocytosis,</strong> metabolic syndrome, diabetes, weight gain, <strong>wet pillow syndrome</strong> (excessive night time drooling)</td>\n</tr>\n<tr>\n<td>Olanzapine</td>\n<td>Metabolic syndrome, diabetes, weight gain</td>\n</tr>\n<tr>\n<td>Quetiapine</td>\n<td><strong>Cataract</strong></td>\n</tr>\n<tr>\n<td>Ziprasidone</td>\n<td><strong>QT prolongation</strong></td>\n</tr>\n<tr>\n<td>Amisulpride</td>\n<td>Hyperprolactinemia</td>\n</tr>\n<tr>\n<td>Aripiprazole</td>\n<td><strong>Hypoprolactinemia</strong>&nbsp;</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(536, 'Anaesthesia,', 'PM0517. Ceiling effect of opioids', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\184\>\n<tbody>\n<tr>\n<td><strong>Drug </strong></td>\n<td><strong>Correlation of Respiratory Depression With Dose</strong></td>\n</tr>\n<tr>\n<td>Morphine</td>\n<td><br>\n<p>Increases proportionally with dose (i.e. no ceiling effect)</p>\n</td>\n</tr>\n<tr>\n<td>Buprenorphine</td>\n<td><br>\n<p>Ceiling effect at 0.15-1.2 mg in adults</p>\n</td>\n</tr>\n<tr>\n<td>Butorphanol</td>\n<td><br>\n<p>Ceiling effect at 30-60 μg/kg</p>\n</td>\n</tr>\n<tr>\n<td>Nalbuphine</td>\n<td>\n<p>Ceiling effect at 30 mg in adults</p>\n</td>\n</tr>\n<tr>\n<td>Pentazocine&nbsp;</td>\n<td>\n<p>Ceiling effect suggested</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(537, 'Anaesthesia,', 'PM0515. High Yield Points in Opioids', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ul>\n<li>Post thoracotomy pain managed by: IV fentanyl</li>\n<li>Drug of choice for controlling severe pain in cancer patients: Morphine</li>\n<li>Drug of choice for OPD analgesia: Alfentanyl</li>\n<li>Most potent: sufentanyl</li>\n<li>Shortest acting opioid-remifentanyl</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(538, 'Anaesthesia,', 'PM0514. Effects of opioid on its various receptors', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\541\>\n<tbody>\n<tr>\n<td><strong>Effects</strong></td>\n<td><strong>Mu(μ) receptor</strong></td>\n<td><strong>Kappa(κ) receptor</strong></td>\n<td><strong>Delta(δ) receptor</strong></td>\n</tr>\n<tr>\n<td><strong>IUPHAR name</strong></td>\n<td>MOP</td>\n<td>KOP</td>\n<td>DOP</td>\n</tr>\n<tr>\n<td><strong>Analgesia</strong></td>\n<td>\n<p>Supraspinal</p>\n<p>spinal</p>\n</td>\n<td>Supraspinal, spinal</td>\n<td>Supraspinal; spinal (modulates mu-receptor activity)</td>\n</tr>\n<tr>\n<td><strong>Cardiovascular&nbsp;</strong></td>\n<td>Bradycardia</td>\n<td>&nbsp;</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td><strong>Respiratory</strong></td>\n<td>Depression</td>\n<td>&nbsp;</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td><strong>Central nervous system</strong></td>\n<td>Euphoria, sedation, prolactin release,mild hypothermia, catalepsy, indifference to environmental stimulus</td>\n<td>Sedation, dysphoria, psychomimetic reactions (hallucinations, delirium)</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td><strong>Pupil</strong></td>\n<td>Miosis</td>\n<td>Miosis</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td><strong>Gastrointestinal</strong></td>\n<td>Inhibition of peristalsis, nausea, vomiting</td>\n<td>&nbsp;</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td><strong>Genitourinary</strong></td>\n<td>Urinary retention</td>\n<td>Diuresis(inhibition of vasopressin release)</td>\n<td>Urinary retention</td>\n</tr>\n<tr>\n<td><strong>Pruritus</strong></td>\n<td>&nbsp;Yes</td>\n<td>&nbsp;</td>\n<td>Yes&nbsp;</td>\n</tr>\n<tr>\n<td><strong>Physical dependence</strong></td>\n<td>&nbsp;Yes</td>\n<td>&nbsp;Low abuse potential</td>\n<td>Yes&nbsp;</td>\n</tr>\n<tr>\n<td><strong>Antishivering</strong></td>\n<td>&nbsp;</td>\n<td>&nbsp;Yes</td>\n<td>&nbsp;</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(539, 'Psychiatry,', 'PM0502. Lithium in bipolar disorder', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<div><strong>Guidelines for Lithium therapy</strong></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><strong>Indications</strong>:</div>\n<div>Mania, hypomania, bipolar disorder, etc.</div>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Blood levels of lithium</strong>:</p>\n<p>For <strong>therapeutic</strong> purpose (treatment of acute mania) :&nbsp;<strong>0.8-1.2 mEq/L</strong></p>\n<p>For <strong>prophylaxis</strong> (to prevent relapse of bipolar disorder):&nbsp;<strong>0.5-0.8 mEq/L</strong></p>\n<p><strong>Toxic</strong> levels of lithium:&nbsp;<strong>&gt;/= 1.5 mEq/L</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<div><strong>Contraindications</strong>:</div>\n<div>\n<ul>\n<li>CKD</li>\n<li>Heart disease</li>\n<li>Hyponatremia or diuretic use</li>\n</ul>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div><strong>Baseline studies:</strong></div>\n<div>\n<ul>\n<li>BUN (Blood Urea Nitrogen), Creatinine, Calcium, Urine analysis</li>\n<li>Thyroid function test</li>\n<li>ECG in patients with coronary risk factors</li>\n</ul>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div><strong>Acute a</strong><strong>dverse effects</strong>:</div>\n<div>\n<ul>\n<li>Tremor, ataxia, weakness</li>\n<li>Polyuria, polydipsia</li>\n<li>Vomiting, diarrhea</li>\n<li>Cognitive impairment</li>\n</ul>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div><strong>Chronic a</strong><strong>dverse effects</strong>:</div>\n<div>\n<ul>\n<li>Nephrogenic diabetes insipidus</li>\n<li>Thyroid dysfunction</li>\n<li>Hyperparathyroidism</li>\n</ul>\n</div>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(540, 'Anaesthesia,', 'PM0493. Anaesthetic drugs in patients with reduced renal function', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Inhaled anaesthetics:</strong></p>\n<ul>\n<li>Safe- halothane, isoflurane, desflurane</li>\n<li>Unsafe- methoxyflurane, enflurane, and sevoflurane</li>\n</ul>\n<p>&nbsp;<strong>Intravenous anaesthetics:</strong></p>\n<ul>\n<li>Safe- Propofol, Ultrashort acting barbiturates like thiopental, methohexital</li>\n<li>Unsafe- Benzodiazepines such as diazepam</li>\n</ul>\n<p>&nbsp;<strong>Opioids:</strong></p>\n<ul>\n<li>Safe- Remifentanil&gt; Fentanyl &gt; Alfentanil &gt; Sufentanil</li>\n<li>Unsafe:\n<ul>\n<li>Morphine (metabolized to morphine 3/6 glucuronide which is excreted through the kidney)</li>\n<li>Meperidine (metabolite normeperidine is excreted through the kidney)</li>\n<li>Hydromorphone (metabolite H-3-glucuronide accumulate in renal failure patients)</li>\n</ul>\n</li>\n</ul>\n<p>&nbsp;<strong>Muscle relaxants:</strong></p>\n<ul>\n<li>Safe- Atracurium, Cisatracurium</li>\n<li>Unsafe- Rocuronium, Mivacurium</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(541, 'Pharmacology,', 'PM0479. Extrapyramidal side effects of antipsychotic drugs', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<div><span><strong>Adverse reactions</strong></span></div>\n</td>\n<td>\n<div><span><strong>Time of onset</strong></span></div>\n</td>\n<td>\n<div><span><strong>Features</strong></span></div>\n</td>\n<td>\n<div><span><strong>Treatment</strong></span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span><strong>Acute dystonia</strong></span></div>\n</td>\n<td>\n<div class=\p1\><span>1 to 5 days</span></div>\n</td>\n<td>\n<div class=\p1\><span>Spasm of muscles of tongue, face, neck, back</span></div>\n</td>\n<td>\n<div class=\p1\><span>Diphenhydramine or benztropine</span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span><strong>Akathisia</strong></span></div>\n</td>\n<td>\n<div class=\p1\><span>5 to 60 days</span></div>\n</td>\n<td>\n<div class=\p1\><span>Restlessness</span></div>\n</td>\n<td>\n<div><span><strong>Propranolol</strong>,<strong>&nbsp;</strong>lorazepam, diphenhydramine, benztropine</span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span><strong>Parkinsonism</strong></span></div>\n</td>\n<td>\n<div class=\p1\><span>5 to 30 days</span></div>\n</td>\n<td>\n<div class=\p1\><span>Bradykinesia, rigidity, tremors</span></div>\n</td>\n<td>\n<div class=\p1\><span>Dose reduction, change medication, antiparkinsonian agents</span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span><strong>Tardive dyskinesia</strong></span></div>\n</td>\n<td>\n<div class=\p1\><span>Months to years</span></div>\n</td>\n<td>\n<div class=\p1\>\n<div class=\p1\><span>Orofacial dyskinesia (may be reversible with early recognition and drug discontinuation)</span></div>\n</div>\n</td>\n<td>\n<div class=\p1\><span>VMAT-2 inhibitors <strong>valbenazine</strong> and <strong>deutetrabenazine</strong></span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span><strong>Neuroleptic malignant syndrome</strong></span></div>\n</td>\n<td>\n<div class=\p1\><span>Weeks to months</span></div>\n</td>\n<td>\n<div class=\p1\><span>Extreme rigidity, fever, unstable blood pressure</span></div>\n</td>\n<td>\n<div class=\p1\><span><strong>Dantrolene</strong></span></div>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(542, 'Anaesthesia,', 'PM0422. Maximum dose of local anaesthetics', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Local Anesthetic</strong></p>\n</td>\n<td colspan=\2\>\n<p><strong>Maximum dose</strong></p>\n</td>\n</tr>\n<tr>\n<td>&nbsp;</td>\n<td>Plain</td>\n<td>\n<p>With adrenaline</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Lidocaine</p>\n</td>\n<td>\n<p><strong>4.5 mg/kg</strong>&nbsp;up to 300 mg&nbsp;</p>\n</td>\n<td>\n<p><strong>7 mg/kg </strong>up to 500 mg</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Bupivacaine&nbsp;</p>\n</td>\n<td>\n<p>175mg</p>\n</td>\n<td>200 mg</td>\n</tr>\n<tr>\n<td>\n<p>Ropivacaine</p>\n</td>\n<td>\n<p>200 mg</p>\n</td>\n<td>250 mg</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(543, 'Anaesthesia,', 'PM0420. Systemic Absorbtion of Local Anaesthetic in the Order of Occurrence', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Systemic absorption</strong> of a local anesthetic depends on <strong>vascularity.</strong></p>\n<p>The highest blood concentrations are seen following:</p>\n<ol>\n<li>Intravenous (intraarterial) injection (maximum)</li>\n<li>Intercostal nerve blockade</li>\n<li>Paracervical block</li>\n<li>Caudal epidural block</li>\n<li>Lumbar epidural block</li>\n<li>Brachial plexus block</li>\n<li>Sciatic block</li>\n<li>Subcutaneous injection (minimum)</li>\n</ol></div><!---->\n      <!---->\n    </div><!---->'),
(544, 'Pharmacology,', 'PM0415. Classification of local anesthetics based on chemical structure', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>&nbsp;</td>\n<td><strong>Amino ester</strong></td>\n<td><strong>Amino amide</strong></td>\n</tr>\n<tr>\n<td><strong>Stability</strong></td>\n<td>Relatively <strong>unstable</strong> in solution</td>\n<td>Relatively more <strong>stable</strong> due to the presence of methoxy group</td>\n</tr>\n<tr>\n<td>\n<p><strong>Metabolism&nbsp;</strong></p>\n</td>\n<td>\n<p><strong>Plasma cholinesterase</strong></p>\n<p>Exception:&nbsp;<strong>Cocaine - </strong>predominantly by&nbsp;hepatic carboxylesterase</p>\n</td>\n<td>\n<p><strong>Hepatic metabolism </strong>(amidase)</p>\n<p>Exception:&nbsp;<strong>Articaine</strong>&nbsp;- plasma cholinesterase.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Duration of action</strong></p>\n</td>\n<td>\n<p>Shorter-acting</p>\n<p>Exception:&nbsp;<strong>Tetracaine</strong></p>\n</td>\n<td>\n<p>Longer-acting</p>\n<p>Exception:&nbsp;<strong>Articaine</strong>&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td><strong>Allergic reaction</strong></td>\n<td>High incidence due to metabolite PABA</td>\n<td>Rare</td>\n</tr>\n<tr>\n<td>\n<p><strong>Examples&nbsp;</strong></p>\n</td>\n<td>\n<ul>\n<li>Proca<strong>i</strong>ne</li>\n<li>Chlorproca<strong>i</strong>ne</li>\n<li>Coca<strong>i</strong>ne</li>\n<li>Benzoca<strong>i</strong>ne</li>\n<li>Tetraca<strong>i</strong>ne</li>\n</ul>\n</td>\n<td>\n<ul>\n<li>L<strong>i</strong>doca<strong>i</strong>ne</li>\n<li>Mep<strong>i</strong>vaca<strong>i</strong>ne</li>\n<li>Pr<strong>i</strong>loca<strong>i</strong>ne</li>\n<li>Bup<strong>i</strong>vaca<strong>i</strong>ne</li>\n<li>Rop<strong>i</strong>vaca<strong>i</strong>ne</li>\n<li>Et<strong>i</strong>doca<strong>i</strong>ne</li>\n<li>D<strong>i</strong>buca<strong>i</strong>ne</li>\n<li>Art<strong>i</strong>ca<strong>i</strong>ne</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(545, 'Anaesthesia,', 'PM0364. Classification of  Opioids', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Naturally Occurring:</strong></p>\n<ul>\n<li>Morphine</li>\n<li>Codeine (methyl morphine)</li>\n<li>Papaverine</li>\n<li>Thebaine</li>\n</ul>\n<p><strong>Semisynthetic:</strong></p>\n<ul>\n<li>Heroin (diacetyl morphine)</li>\n<li>Dihydromorphone, morphinone</li>\n<li>Thebaine derivatives (e.g., etorphine, buprenorphine)</li>\n</ul>\n<p><strong>Synthetic:</strong></p>\n<ul>\n<li>Morphinan series (e.g., levorphanol, butorphanol)</li>\n<li>Diphenylpropylamine series (e.g., methadone)</li>\n<li>Benzomorphan series (e.g., pentazocine)</li>\n<li>Phenylpiperidine series (e.g., meperidine, fentanyl, sufentanil, alfentanil, remifentanil)</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(546, 'Psychiatry,', 'PM0340. Pharmacological treatment of psychosis', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>First-generation </strong>antipsychotics are generally not preferred due to the higher risk of<strong> extrapyramidal side effects/ tardive dyskinesia.</strong></p>\n<p><strong>Second-generation</strong> antipsychotics are preferred drugs:</p>\n<ul>\n<li>Risperidone</li>\n<li>Olanzapine</li>\n<li>Quetiapine</li>\n<li>Aripiprazole</li>\n<li>Ziprasidone</li>\n<li>Paliperidone</li>\n</ul>\n<p><strong>Benzodiazepines</strong> can be added to treat associated agitation. It is the <strong>drug of choice for catatonia</strong>.</p>\n<p>Special situations:</p>\n<ul>\n<li><strong>Emergency</strong>: Use a <strong>short-acting intramuscular agent</strong> such as <strong>haloperidol</strong>, fluphenazine, olanzapine, or ziprasidone.</li>\n<li>Chronic compliance: Long-acting injectable is used.</li>\n<li>Treatment resistance (2 failed trials): Consider Clozapine.</li>\n<li><strong>All meds ineffective</strong>: May consider <strong>ECT</strong>.</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->');
INSERT INTO db_pearl_m (pid, subject, pearl_title, pearl_desc) VALUES
(547, 'Anaesthesia,', 'PM0247. Muscle relaxant with histamine release.', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Muscle relaxants causing histamine release leading to hypotension, tachycardia, flushing:</p>\n<p><strong>Most</strong> likely:<strong>&nbsp;d-tubocurarine</strong></p>\n<p>Likely:<strong>&nbsp;</strong></p>\n<ul>\n<li>Atracurium</li>\n<li>Mivacurium</li>\n<li>Succinyl choline</li>\n</ul>\n<p>Least likely:</p>\n<ul>\n<li>Vecuronium</li>\n<li>Cisatracurium</li>\n<li>Rocuronium</li>\n<li>Pancuronium</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(548, 'Anaesthesia,', 'PM0242. Side effects of succinyl choline', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>The following are the side effects of succinyl choline:</strong></p>\n<ul>\n<li>Cardiovascular effects:\n<ul>\n<li>Sinus bradycardia</li>\n<li>Junctional rhythms</li>\n<li>Ventricular dysrhythmias</li>\n</ul>\n</li>\n<li>Hyperkalemia</li>\n<li>Myalgia</li>\n<li>Masseter spasm: Succinyl choline can trigger malignant hyperthermia</li>\n<li>Increased intracranial pressure:&nbsp;Succinylcholine may lead to an activation of the EEG and slight increases in CBF&nbsp;and ICP in some patients.</li>\n<li>Increased intraocular pressure</li>\n<li>Increased intragastric pressure</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(549, 'Pharmacology,', 'PM0241. High yield in muscle relaxants', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ul>\n<li>\n<div><strong>Shortest</strong> <strong>acting</strong> <strong>non-depolarizing</strong> muscle relaxant:\n<ul>\n<li>Gantacurium (Not FDA approved)</li>\n<li><strong>Mivacurium</strong>:&nbsp;<strong>12-20 minutes</strong>.<strong>&nbsp;</strong>(FDA approved)</li>\n</ul>\n</div>\n</li>\n<li>\n<div><strong>Shortest acting</strong> <strong>depolarizing</strong> muscle relaxant: <strong>Succinylcholine</strong> (3-6 minutes)</div>\n</li>\n<li>\n<div>Muscle relaxant having the <strong>maximum duration of action</strong>: <strong>Doxacurium</strong> (benzylisoquinoline) &gt; Pancuronium (Aminosteroid)</div>\n</li>\n<li>\n<div>Muscle relaxant causing pain on IV injection site: Rocuronium</div>\n</li>\n<li>\n<div>Muscle relaxant which <strong>crosses placenta</strong>: <strong>Gallamine</strong></div>\n</li>\n<li>\n<div>Muscle relaxant acting directly on muscle: Dantrolene</div>\n</li>\n<li>\n<div>Muscle relaxants with central action: benzodiazepine, mephenesin, baclofen, carisoprodol, metaxalone</div>\n</li>\n<li>\n<div>Muscle relaxant and mode of clearance:</div>\n<ul>\n<li>\n<div><strong>Hepatic</strong> clearance: Pancuronium,&nbsp;rapacuronium and vecuronium</div>\n</li>\n<li>\n<div><strong>Biliary</strong> clearance: Vecuronium and rocuronium</div>\n</li>\n<li>\n<div><strong>Renal</strong> clearance: Doxacurium, pancuronium, vecuronium, and pipecuronium</div>\n</li>\n</ul>\n</li>\n<li>\n<div>Muscle relaxant with <strong>sympathetic stimulation</strong>:</div>\n<ul>\n<li>\n<div><strong>Gallamine</strong></div>\n</li>\n<li>\n<div><strong>Pancuronium</strong></div>\n</li>\n</ul>\n</li>\n<li>\n<div>Muscle relaxant with <strong>vagolytic activity</strong>:</div>\n<ul>\n<li>\n<div><strong>Gallamine</strong></div>\n</li>\n<li>\n<div><strong>Pancuronium</strong></div>\n</li>\n<li>\n<div><strong>Rocuronium (mild)</strong></div>\n</li>\n</ul>\n</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(550, 'Anaesthesia,', 'PM0217. Conditions causing susceptibility to succinylcholine-induced hyperkalemia', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>The conditions that predispose to hyperkalemia following succinylcholine administration are:</p>\n<ul>\n<li>Burn injury</li>\n<li>Massive trauma, head and spinal cord injury</li>\n<li>Severe intra-abdominal infection</li>\n<li>Encephalitis, tetanus and stroke</li>\n<li>Neuropathy like Guillain-Barre syndrome</li>\n<li>Severe Parkinson’s disease</li>\n<li>Prolonged total body immobilization</li>\n<li>Hemorrhagic shock with metabolic acidosis</li>\n<li>Myopathies (eg, Duchenne’s dystrophy)</li>\n</ul>\n<p>So, the use of succinylcholine is relatively contraindicated in patients having these conditions.</p></div><!---->\n      <!---->\n    </div><!---->'),
(551, 'Anaesthesia,', 'PM0215. Types of muscle relaxants', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>There are 2 types of muscle relaxants: depolarizing and non-depolarizing.</p>\n<p><strong>Depolarizing</strong> Muscle Relaxant-<strong>&nbsp;Succinylcholine</strong></p>\n<p><strong>Non-Depolarizing</strong> Muscle Relaxants:</p>\n<ul>\n<li><strong>Ultra short-</strong>acting (&lt;10 minutes) - Gantacurium</li>\n<li><strong>Short</strong>-acting (10-20 minutes) -&nbsp;Mivacurium</li>\n<li><strong>Intermediate-</strong>acting (20-50 minutes)\n<ul>\n<li>Atracurium</li>\n<li>Cisatracurium</li>\n<li>Vecuronium</li>\n<li>Rocuronium</li>\n</ul>\n</li>\n<li><strong>Long-</strong>acting (&gt;50 minutes)\n<ul>\n<li>Pancuronium</li>\n<li>Doxacurium</li>\n<li>Pipecuronium</li>\n</ul>\n</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(552, 'Anaesthesia,', 'PM0181. A list of anaesthetics and their physiological effects', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td>\n<p><strong>Condition</strong></p>\n</td>\n<td>\n<p><strong>Anesthetics</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Increased Intracranial tension (ICT)</strong></p>\n</td>\n<td>\n<p>Sevoflurane</p>\n<p>Desflurane</p>\n<p>Isoflurane</p>\n<p>Enflurane</p>\n<p>Methoxiflurane</p>\n<p>Halothane</p>\n<p>Ketamine</p>\n<p>Nitrous Oxide (N2O)</p>\n<p>Succinyl-choline</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Decreased ICT</strong></p>\n</td>\n<td>\n<p>Barbiturates (e.g.thiopentone)</p>\n<p>Benzodiazepines</p>\n<p>Cyclopropane</p>\n<p>Droperidol</p>\n<p>Etomidate</p>\n<p>Lidocaine</p>\n<p>Propofol</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Increased Intraocular pressure (IOP)</strong></p>\n</td>\n<td>\n<p>Barbiturates (e.g. thiopentone)</p>\n<p>Cyclopropane</p>\n<p>Etomidate</p>\n<p>Succinyl Choline</p>\n<p>Ketamine</p>\n<p>N2O</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Decreased IOP</strong></p>\n</td>\n<td>\n<p>Morphine</p>\n<p>Halothane</p>\n<p>Hexamethonium</p>\n<p>Trimethaphan</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Increased Blood pressure</strong></p>\n</td>\n<td>\n<p>Ketamine</p>\n<p>Pentazocin</p>\n<p>Pancuronium</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Bronchodilation (preferred in asthamatics)</strong></p>\n</td>\n<td>\n<p>Ketamine (most potent)&nbsp;</p>\n<p>Sevofluorane</p>\n<p>Halothane&nbsp;</p>\n<p>Promethazine</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Bronchospasmodics (contraindicated in asthamatics)</strong></p>\n</td>\n<td>\n<p>d-TC</p>\n<p>N2O</p>\n<p>Thiopentone</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(553, 'Pharmacology,', 'PM0123. Adverse effects of Phenytoin', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><div>Phenytoin adverse effects - Most of them<strong> start with “H”</strong></div>\n<div>&nbsp;</div>\n<ul>\n<li><strong>H</strong>yperplasia of gums</li>\n<li><strong>H</strong>irsutism</li>\n<li><strong>H</strong>ypersensitivity reactions: rashes, lymphadenopathy</li>\n<li><strong>H</strong>yperglycemia</li>\n<li><strong>H</strong>ypocalcemia and osteomalacia</li>\n<li><strong>H</strong>allucinations and behavioral abnormalities</li>\n<li><strong>H</strong>epatotoxicity</li>\n<li>Fetal <strong>H</strong>ydantoin Syndrome</li>\n<li>Cerebellar and vestibular symptoms: ataxia, nystagmus</li>\n<li><strong>H</strong>ypotension, purple glove syndrome and arrhythmias on IV use</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(554, 'Pharmacology,', 'PM2442. WHO 2018 guidelines for post exposure prophylaxis regimen in HIV', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><div class=\page\ title=\Page 39\>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>An HIV post-exposure prophylaxis regimen with two ARV drugs is effective, but three drugs are preferred.</p>\n<p>The recent MOHFW January 2020 guidelines on the management of PEP in HIV recommends the replacement of 2 drug regimen with 3 drug regimen referring to WHO December 2018 guidelines on PEP management protocol in HIV. This shift towards recommending a three-drug regimen for<br>everyone was based on the availability of less toxic and better-tolerated medications.</p>\n<div class=\page\ title=\Page 39\>\n<div class=\layoutArea\>\n<div class=\column\>\n<p><strong>ADULTS AND ADOLESCENTS:-</strong></p>\n<p>Preferred backbone regimen -&nbsp;TDF + 3TC (or FTC)</p>\n<p>New drug - &nbsp;DTG is the preferred third drug for HIV post-exposure prophylaxis</p>\n<p>New drug -&nbsp;<span>ATV/r, DRV/r, LPV/r and RAL may be considered as alternative third drug options for post-exposure prophylaxis</span></p>\n<p><strong>CHILDREN:-</strong></p>\n<div class=\page\ title=\Page 39\>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>Preferred backbone regimen -&nbsp;AZT + 3TC</p>\n<div class=\page\ title=\Page 39\>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>ABC + 3TC or TDF + 3TC (or FTC) can be considered as alternative regimens</p>\n<p>New drug -&nbsp;DTG is the preferred third drug for HIV post-exposure prophylaxis</p>\n<p>New drug -&nbsp;<span>ATV/r, DRV/r, LPV/r and RAL may be considered as alternative third drug options for post-exposure prophylaxis</span></p>\n<p>&nbsp;</p>\n<p><span>Legend</span></p>\n<p><span>TDF - Tenofovir disoproxil fumarate&nbsp;</span></p>\n<p>3TC - Lamivudine&nbsp;</p>\n<p>FTC - Emtricitabine&nbsp;</p>\n<p>DTG - Dolutegravir&nbsp;</p>\n<p>ATV/r - Atazanavir + ritonavir&nbsp;</p>\n<p>DRV/r - Darunavir + ritonavir&nbsp;</p>\n<p>LPV/r - Lopinavir + ritonavir&nbsp;</p>\n<p>RAL - Raltegravir&nbsp;</p>\n<p>AZT - Zidovudine&nbsp;</p>\n<p>ABC - Abacavir&nbsp;</p>\n<p>&nbsp;</p>\n</div>\n</div>\n</div>\n<div class=\page\ title=\Page 39\>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>&nbsp;</p>\n</div>\n</div>\n</div>\n</div>\n</div>\n</div>\n<p>&nbsp;</p>\n</div>\n</div>\n</div>\n</div>\n</div>\n</div></div><!---->\n      <!---->\n    </div><!---->'),
(555, 'Microbiology,', 'PM2433. Acute bacterial meningitis - causes and empirical treatment', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Patient Group</strong></td>\n<td><strong>Bacterial Pathogen</strong></td>\n<td><strong>Empirical Therapy</strong></td>\n</tr>\n<tr>\n<td>Age&lt;1 month</td>\n<td>\n<p><em>E. coli</em></p>\n<p>Group B Streptococci</p>\n<p>Enterobacteriacea</p>\n<p><em>Listeria monocytogenes</em></p>\n</td>\n<td>3rd generation Cephalosporin+<br>Ampicillin</td>\n</tr>\n<tr>\n<td>1 month - 2 years</td>\n<td>\n<p><em>E. coli</em></p>\n<p>Group B Streptococci</p>\n<p><em>H. influenza</em></p>\n<p><em>S. pneumonia</em></p>\n<p><em>N. meningitidis</em></p>\n</td>\n<td>3rd generation Cephalosporin+<br>Ampicillin+<br>Vancomycin (if PRSP suspected)</td>\n</tr>\n<tr>\n<td>2 - 50 years and no risk factors for Listeria</td>\n<td><em>S. pneumoniae</em><br><em>N. meningitidis</em><br><br></td>\n<td>\n<p>3rd generation cephalosporin+<br>Vancomycin</p>\n</td>\n</tr>\n<tr>\n<td>Age &gt; 50 yrs and/or risk factors for Listeria</td>\n<td><em>S. pneumoniae</em><br><em>N. meningitidis</em><br><em>L. monocytogenes</em><br>Group B Streptococci</td>\n<td>\n<p>3rd generation cephalosporin+<br>Vancomycin+<br>Ampicillin</p>\n</td>\n</tr>\n<tr>\n<td>Post-craniotomy/Head trauma/CSF Rhinorrhea</td>\n<td>Gram negative bacilli</td>\n<td>\n<p>Ceftazidime+<br>Vancomycin</p>\n</td>\n</tr>\n<tr>\n<td>Post-shunt surgery/ Ommaya reservoir</td>\n<td>\n<p><em>S. aureus</em><br>Coagulase-negative staphylococci</p>\n</td>\n<td>Ceftazidime+<br>Vancomycin</td>\n</tr>\n<tr>\n<td>Immunocompromised state&nbsp;</td>\n<td>\n<p><em>&nbsp;S. pneumoniae&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </em></p>\n<p><em>N. meningitidis&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</em></p>\n<p><em>L. monocytogenes&nbsp; &nbsp; &nbsp; &nbsp;</em></p>\n<p>Gram negative bacilli&nbsp;</p>\n</td>\n<td>\n<p>Vancomycin+&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Ampicillin+&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Cefepime&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p>\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;OR&nbsp;</p>\n<p>Vancomycin+&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Meropenem&nbsp; &nbsp; &nbsp;&nbsp;</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>*PRSP= penicillin-resistant S pneumoniae&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(556, 'Pharmacology,', 'PM2339. Mechanism of action of Antifungal drugs', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/223d24b7e0d84721a94c23176114c9dax1280x2560.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(557, 'Medicine,', 'PM2190. Empirical Antibiotic Treatment of Community-Acquired Pneumonia (CAP)', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td>Situation&nbsp;</td>\n<td>Treatment Regimen</td>\n</tr>\n<tr>\n<td>&nbsp;<strong>Outpatient</strong></td>\n<td>\n<p><strong>Previously healthy</strong>:</p>\n<p>&nbsp;Macrolide / Doxycycline</p>\n<p><strong>Comorbidities</strong>:</p>\n<p>Respiratory Fluoroquinolone&nbsp;</p>\n<p><strong>&nbsp; &nbsp; &nbsp; Or&nbsp;&nbsp;</strong>&nbsp; &nbsp; &nbsp;</p>\n<p>β-lactam + Macrolide / Doxycycline</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Inpatients</strong></p>\n<p><strong>Non - ICU</strong></p>\n</td>\n<td>\n<p>Respiratory<strong>&nbsp;</strong>Fluoroquinolone&nbsp;</p>\n<p><strong>Or&nbsp;</strong></p>\n<p>β-lactam&nbsp;<strong>+</strong>&nbsp;Macrolide / Doxycycline</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Inpatients</strong></p>\n<p><strong>ICU</strong></p>\n</td>\n<td>\n<p>β-lactam</p>\n<p><strong>+</strong></p>\n<p>Respiratory Fluoroquinolone <strong>Or</strong>&nbsp;Azithromycin</p>\n</td>\n</tr>\n<tr>\n<td>\n<p class=\p1\><strong>If Pseudomonas</strong></p>\n&nbsp;</td>\n<td>\n<p><strong>&nbsp;</strong>Anti-pseudomonal&nbsp;β-lactam</p>\n<p><strong>+</strong></p>\n<p>Fluoroquinolone*&nbsp;<strong>Or</strong> Aminoglycoside</p>\n<p><strong>+ </strong></p>\n<p>Azithromycin</p>\n<p>&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>&nbsp;\n<p class=\p1\><strong>If CA-MRSA</strong></p>\n</td>\n<td>\n<p>Site based regimen</p>\n<p><strong>+</strong></p>\n<p>Linezolid <strong>Or&nbsp;</strong>Vancomycin</p>\n<p><strong>+</strong></p>\n<p>Clindamycin&nbsp;</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p><strong>Note:</strong></p>\n<p>Macrolide - Azithromycin / Clarithromycin</p>\n<p>Fluoroquinolones :</p>\n<ul>\n<li>Respiratory Fluoroquinolone - Levofloxacin* / Moxifloxacin / (Gemifloxacin - only outpatient)&nbsp;</li>\n<li>Ciprofloxacin*</li>\n</ul>\n<p>β-lactam :</p>\n<ul>\n<li>Outpatient with comorbidities&nbsp;\n<ul>\n<li>PO - Amoxicillin (+/- clavulanate)/ Cefpodoxime / Cefuroxime&nbsp;</li>\n<li>IV - Ceftriaxone</li>\n</ul>\n</li>\n<li>Inpatient, Non-ICU (IV)&nbsp;\n<ul>\n<li>Ampicillin / Ceftriaxone / Cefotaxime / Ertapenem</li>\n</ul>\n</li>\n<li>Inpatient, ICU (IV)&nbsp;\n<ul>\n<li>Ampicillin-Sulbactam / Ceftriaxone / Cefotaxime</li>\n</ul>\n</li>\n<li>Anti-pseudomonal&nbsp;β-lactam (IV)&nbsp;\n<ul>\n<li>Piperacillin-Tazobactam / Cefepime / Imipenem / Meropenem&nbsp;</li>\n</ul>\n</li>\n</ul>\n<p class=\p1\>Aminoglycoside - Amikacin / T<span class=\s1\>obramycin</span></p></div><!---->\n      <!---->\n    </div><!---->'),
(558, 'Microbiology,', 'PM2042. List of intrinsic drug resistance in bacteria', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<table>\n<tbody>\n<tr>\n<td><strong>Organism</strong></td>\n<td><strong>Intrinsic Drug Resistance</strong></td>\n</tr>\n<tr>\n<td>\n<p>Acinetobacter baumanii</p>\n</td>\n<td>\n<p>Ampicillin,Amoxicillin,Ertapenem,Trimethoprim,Chloramphenicol</p>\n</td>\n</tr>\n<tr>\n<td>Pseudomonas aeruginosa</td>\n<td>Ampicillin,Amoxicillin-Clavulanate,Ceftriaxone,Ertapenem,Tetracyclines/Tigecyclines, Trimethoprim-sulfamethoxazole,Chloramphenicol</td>\n</tr>\n<tr>\n<td>Bacteroides fragilis</td>\n<td>Aminoglycosides,Ampicillin</td>\n</tr>\n<tr>\n<td>Gram positive bacteria</td>\n<td>Aztreonam</td>\n</tr>\n<tr>\n<td>Staphylococcus saprophyticus&nbsp;</td>\n<td>Novobiocin</td>\n</tr>\n<tr>\n<td>Enterococcus fecalis/fecium</td>\n<td>Cephalosporins,Aminoglycosides,Clindamycin,Polymyxin B</td>\n</tr>\n<tr>\n<td>Clostridium species&nbsp;</td>\n<td>Aminoglycosides</td>\n</tr>\n<tr>\n<td>Gram negative bacteria</td>\n<td>Vancomycin</td>\n</tr>\n<tr>\n<td>Klebsiella pneumoniae</td>\n<td>Ampicillin</td>\n</tr>\n<tr>\n<td>Enterobacter</td>\n<td>Ampicillin</td>\n</tr>\n<tr>\n<td>Aerobic bacteria</td>\n<td>Metronidazole</td>\n</tr>\n<tr>\n<td>Lactobacillus &amp; Leuconostoc</td>\n<td>Vancomycin</td>\n</tr>\n<tr>\n<td>Burkholderia</td>\n<td>Polymyxins</td>\n</tr>\n</tbody>\n</table>\n<p><strong>&nbsp;</strong></p></div><!---->\n      <!---->\n    </div><!---->'),
(559, 'Dermatology,', 'PM1966. Lepra Reactions', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Lepra Reactions:</strong></p>\n<table border=\1\>\n<tbody>\n<tr>\n<td>\n<p><strong>Type 1 Reaction</strong></p>\n<p>(Reversal/Upgrading)</p>\n</td>\n<td>\n<p><strong>Type 2 Reaction</strong></p>\n<p>(Erythema Nodosum Leprosum)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Mainly seen in the&nbsp;<strong>Borderline</strong> spectrum (BT, BB, BL)</p>\n<p><strong>Most severe</strong> in Borderline Lepromatous(BL) leprosy</p>\n</td>\n<td>Mainly seen in <strong>LL</strong>(50%)&gt;BL(15%)</td>\n</tr>\n<tr>\n<td>\n<p>Usually seen <strong>within 12 months</strong> of initiation of Multidrug Therapy</p>\n</td>\n<td><strong>Unrelated</strong> to Multidrug therapy. May occur before, during or after treatment</td>\n</tr>\n<tr>\n<td>\n<p><strong>Type 4</strong> Hypersensitivity</p>\n<p>Due to <strong>enhanced cell-mediated response</strong>&nbsp;increased destruction of bacilli following treatment in reversal</p>\n</td>\n<td>\n<p><strong>Type 3</strong> Hypersensitivity</p>\n<p>Primarily Humoral</p>\n<p>(<strong>Immune complex Deposition</strong> in tissues and vessels)</p>\n<p>Cytokine involved - <strong>TNF-α</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Inflammation of <strong>existing skin lesions</strong> (red, painful edematous skin lesions) that may <strong>ulcerate</strong></p>\n<p>New lesions may occur (rare)</p>\n<p><strong>Neuritis</strong>&nbsp;and <strong>Nerve abscesses&nbsp;</strong>(red, painful inflamed nerves)</p>\n</td>\n<td>\n<p><strong>New, erythematous and tender</strong>, subcutaneous and dermal nodules that are widely distributed over extremities and face</p>\n<p>Existing lesions do not change</p>\n</td>\n</tr>\n<tr>\n<td><strong>No</strong> systemic involvement</td>\n<td><strong>Systemic symptoms</strong> present - fever, myalgias, arthralgias, anorexia, keratitis, iritis, nephritis, synovitis, orchitis, lymphadenopathy</td>\n</tr>\n<tr>\n<td>\n<p><strong>Treatment:</strong></p>\n<p>Supportive therapy for mild reactions with <strong>NSAIDs</strong></p>\n<p>In case of severe reactions with neuritis and eye lesions- <strong>Oral corticosteroids</strong></p>\n<p><strong>(Drug of Choice)</strong></p>\n<p>Nerve Abscess - Additional <strong>surgical drainage</strong></p>\n<p>Other drugs: Clofazimine, Cyclosporine</p>\n</td>\n<td>\n<p><strong>Treatment:</strong></p>\n<p><strong>Oral Corticosteroids - First-line drug<br><br>Clofazimine </strong>(Steroid dependent ENL)<strong><br></strong></p>\n<p><strong>Thalidomide -most effective </strong>but not used first due to various side effects</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(560, 'Microbiology,', 'PM1936. Drug resistance and the causative point mutations in M.tuberculosis', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<table>\n<tbody>\n<tr>\n<td><strong>Drug resistance</strong></td>\n<td><strong>Causative mutation</strong></td>\n</tr>\n<tr>\n<td>Rifampin</td>\n<td>rpoB gene&nbsp;</td>\n</tr>\n<tr>\n<td>Isoniazid </td>\n<td>katG gene &amp; inhA&nbsp;</td>\n</tr>\n<tr>\n<td>Pyrazinamide</td>\n<td>pncA gene</td>\n</tr>\n<tr>\n<td>Ethambutol</td>\n<td>embB gene&nbsp;</td>\n</tr>\n<tr>\n<td>Aminoglycosides </td>\n<td>rrs gene&nbsp;</td>\n</tr>\n<tr>\n<td>Fluoroquinolones</td>\n<td>gyrA-gyrB genes</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(561, 'Medicine,', 'PM1856. H. pylori eradication regimen', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<table>\n<tbody>\n<tr>\n<td>\n<p><strong>1<sup>st</sup> line</strong></p>\n<p><strong>[OCA/OCM - 14 days]</strong></p>\n</td>\n<td>\n<p><strong>2<sup>nd</sup> line</strong></p>\n<p><strong>[TOMB - 14 days]</strong></p>\n</td>\n<td>\n<p><strong>3<sup>rd</sup> line /Clarithromycin-resistant cases </strong></p>\n<p><strong>[LOA - 10 days]</strong></p>\n</td>\n<td>\n<p><strong>Sequential Regimen</strong></p>\n<p><strong>[OA (5days) + OCT (5days)]</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>O</strong>meprazole</p>\n</td>\n<td><strong>T</strong>etracycline</td>\n<td><strong>L</strong>evofloxacin</td>\n<td><strong>O</strong>meprazole</td>\n</tr>\n<tr>\n<td><strong>C</strong>larithromycin</td>\n<td><strong>O</strong>meprazole</td>\n<td><strong>O</strong>meprazole</td>\n<td><strong>C</strong>larithromycin</td>\n</tr>\n<tr>\n<td><strong>A</strong>moxicillin/</td>\n<td><strong>M</strong>etronidazole</td>\n<td><strong>A</strong>moxicillin</td>\n<td><strong>A</strong>moxicillin</td>\n</tr>\n<tr>\n<td><strong>M</strong>etronidazole</td>\n<td><strong>B</strong>ismuth</td>\n<td>&nbsp;</td>\n<td><strong>T</strong>inidazole</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(562, 'Medicine,', 'PM1720. ATT Regimen for TB Patients', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>As per the latest recommendations from <strong>National Tuberculosis Elimination Programme (NTEP)</strong>, treatment of TB is now based primarily on <strong>drug susceptibility testing</strong>. This is irrespective of new cases, previously treated cases or recurrent TB cases.&nbsp;<br>All cases of TB are treated with Drug Sensitive TB Regimen, as long as the patient is sensitive to isoniazid and rifampicin. (<strong>No classification into category I or II</strong>)</p>\n<table>\n<tbody>\n<tr>\n<td><strong>Type of TB Case</strong></td>\n<td><strong>Intensive Phase</strong></td>\n<td><strong>Continuation Phase</strong></td>\n</tr>\n<tr>\n<td>New and previously treated cases (H and R sensitive)</td>\n<td>2 months H R Z E</td>\n<td>4 months H R E</td>\n</tr>\n</tbody>\n</table>\n<h2><strong><span>Classification based on drug resistance.&nbsp;</span></strong></h2>\n<p><span>The<strong>&nbsp;</strong>cases are classified as:</span></p>\n<ul>\n<li><strong>Mono-resistant (MR):</strong> A TB patient, who is resistant to one 1st line anti-TB drug only.</li>\n<li><strong>Poly-Drug Resistant (PDR):</strong> A TB patient, who is resistant to more than one 1st line anti-TB drug, other than Rifampicin and isoniazid.&nbsp;</li>\n<li><strong>Rifampicin Resistant (RR):</strong> A TB patient, who is resistant to <strong>rifampicin</strong> with or without resistance to other drugs. Patients, who have rifampicin resistance, should be managed as if they are an MDR TB case.</li>\n<li><strong>Multi-Drug Resistant (MDR):</strong> A TB patient, who is resistant to <strong>both isoniazid and rifampicin.</strong></li>\n<li><strong>Extensively Drug-Resistant (XDR):</strong> A MDR TB patient who is additionally resistant to a <strong>fluoroquinolone</strong> (ofloxacin, levofloxacin, or moxifloxacin) and at least one additional group A drug (as of 2021, these are bedaquiline and linezolid)</li>\n</ul>\n<h2><strong><span>Standard DR TB regimen</span></strong></h2>\n<table>\n<tbody>\n<tr>\n<td><strong>Regimen class</strong></td>\n<td><strong>Drugs</strong></td>\n<td><strong>Comments</strong></td>\n</tr>\n<tr>\n<td><strong>H mono-resistant/ poly-drug resistant TB regimen</strong></td>\n<td>Lfx R E Z for 6 months</td>\n<td>\n<p>6 months long</p>\n<p>Oral drugs only</p>\n</td>\n</tr>\n<tr>\n<td><strong>Shorter oral bedaquiline regime (Shorter MDR)</strong></td>\n<td>\n<p><strong>Intensive Phase:</strong></p>\n<p>Lfx Bdq(6 months) Cfz Z Hh E Eto for 4-6 months</p>\n<p><strong>Continuation phase:</strong></p>\n<p>Lfx Cfz Z E for 5 months</p>\n</td>\n<td>\n<p>9-11 months long totally</p>\n<p>Injectables used in Intensive phase.</p>\n</td>\n</tr>\n<tr>\n<td><strong>Longer MDR TB regimen</strong></td>\n<td>Bdq(6 months or longer) Lfx Lzd Cfz Cs&nbsp;</td>\n<td>\n<p>18-20 months long</p>\n<p>Oral drugs only</p>\n<p>This regimen is used for XDR TB patients for 20 months.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<h2>&nbsp;</h2>\n<h2><strong><span>Criteria for patients to receive standard Drug Resistant TB regimen:</span></strong></h2>\n<table>\n<tbody>\n<tr>\n<td><strong>Standard Drug Resistant TB regimen</strong></td>\n<td><strong>Inclusion criteria</strong></td>\n<td><strong>Exclusion criteria</strong></td>\n</tr>\n<tr>\n<td><strong>H mono-resistant/poly-drug resistant TB regimen</strong></td>\n<td><strong>Isoniazid resistant</strong> TB with <strong>rifampicin sensitive</strong></td>\n<td>No specific criteria</td>\n</tr>\n<tr>\n<td><strong>Shorter MDR TB regimen</strong></td>\n<td>Patient with <strong>rifampicin resistant</strong> pulmonary or extra pulmonary TB</td>\n<td>Non drug sensitivity test based criteria:\n<ul>\n<li>Pregnancy</li>\n<li>Any extra pulmonary disease in PLHIV</li>\n<li>Disseminated meningeal or central nervous system TB</li>\n<li>Intolerance to any drug in the shorter MDR TB regimen</li>\n</ul>\nDrug sensitivity test based criteria:\n<ul>\n<li>If DST/DRT result for FQ or SLI is resistant (XDR TB)</li>\n<li>Presence of InhA mutation</li>\n<li>Resistant to Z&nbsp;</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td><strong>Longer MDR TB regimen</strong></td>\n<td>Patients in whom <strong>shorter MDR TB regimen cannot be considered</strong> due to any reason</td>\n<td>None</td>\n</tr>\n</tbody>\n</table>\n<p>Legend</p>\n<p>FQ: Fluoroquinolone</p>\n<p>SLI: Second Line Injectable</p>\n<p>PLHIV: People Living with HIV</p>\n<p>Lfx: Levofloxacin</p>\n<p>H: Isoniazid</p>\n<p>Hh: High Dose Isoniazid</p>\n<p>R: Rifampicin</p>\n<p>E: Ethambutol</p>\n<p>Z: Pyrazinamide</p>\n<p>Mfx: Moxifloxacin</p>\n<p>Km: Kanamycin</p>\n<p>Am: Amikacin</p>\n<p>Eto: Ethionamide</p>\n<p>Cfz: Clofazimine</p>\n<p>Bdq: Bedaquiline</p>\n<p>Lzd: Linezolid</p>\n<p>Cs: Cycloserine</p></div><!---->\n      <!---->\n    </div><!---->'),
(563, 'Community Medicine,', 'PM1696. Treatment of Malaria', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>&nbsp;</td>\n<td>\n<p><strong> Drug of Choice</strong></p>\n</td>\n<td>\n<p><strong>&nbsp;Primaquine Dose</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>P. vivax malaria </strong></p>\n</td>\n<td>\n<p><strong>Chloroquine</strong> 25 mg/kg divided as:</p>\n<p>10mg/kg on day 1</p>\n<p>10mg/kg on day 2</p>\n<p>5mg/kg on day 3</p>\n</td>\n<td>\n<p>0.25 mg/kg for 14 days</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>P. falciparum malaria</strong></p>\n<p>North East</p>\n<p>In other states</p>\n</td>\n<td>\n<p>&nbsp;</p>\n<p><strong>ACT-AL</strong> for 3 days</p>\n<p><strong>ACT-SP</strong> for 3 days&nbsp;</p>\n</td>\n<td>\n<p>&nbsp;</p>\n<p>0.75mg/kg on day 2</p>\n<p>0.75mg/kg on day 2</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Mixed infection</strong></p>\n<p>North East</p>\n<p>In other states</p>\n</td>\n<td>\n<p><strong>ACT-AL</strong> for 3 days</p>\n<p><strong>ACT-SP</strong> for 3 days</p>\n</td>\n<td>\n<p>&nbsp;</p>\n<p>0.25 mg/kg for 14 days</p>\n<p>0.25 mg/kg for 14 days</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Resistance to ACT/ Severe malaria </strong></p>\n</td>\n<td>\n<p>&nbsp;</p>\n<p><strong>Quinine </strong></p>\n</td>\n<td>\n<p>&nbsp;</p>\n<p><strong> -</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Pregnancy</strong></p>\n<p><strong>P. vivax</strong></p>\n</td>\n<td>\n<p><strong>Chloroquine </strong></p>\n</td>\n<td>\n<p><strong>Contraindicated</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Pregnancy</strong></p>\n<p><strong>P. falciparum</strong></p>\n</td>\n<td>\n<p>1<sup>st&nbsp;</sup>trimester: <strong>Quinine</strong></p>\n<p>2<sup>nd</sup> and 3<sup>rd</sup> trimester<strong>: Area specific ACT</strong></p>\n</td>\n<td>\n<p>&nbsp;</p>\n<p><strong>Contraindicated</strong></p>\n</td>\n</tr>\n</tbody>\n</table>\n<p><strong>ACT:&nbsp;</strong>artemisinin-based combination therapy</p>\n<p><strong>AL:</strong>&nbsp;artemether-lumefantrine</p>\n<p><strong>SP</strong>: Sulfadoxine-<wbr>Pyrimethamine</p></div><!---->\n      <!---->\n    </div><!---->'),
(564, 'Pharmacology', 'PM1587. Key points on the first line drugs used in the treatment of TB', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><span><strong>Drugs</strong></span></td>\n<td><span><strong>Isoniazid</strong></span></td>\n<td><span><strong>Rifamycins</strong></span></td>\n<td><span><strong>Pyrazinamide</strong></span></td>\n<td><span><strong>Ethambutol</strong></span></td>\n</tr>\n<tr>\n<td><span><strong>Mechanism of action</strong></span></td>\n<td>\n<p><span>Bactericidal</span></p>\n<p><span>Blocks mycolic acid synthesis.</span></p>\n</td>\n<td>\n<p><span>Bactericidal (maximum)</span></p>\n<p><span>Inhibition of RNA polymerase, leading to the inhibition of RNA synthesis.</span></p>\n</td>\n<td>\n<p><span>Bactericidal</span></p>\n<p><span>Inhibition of fatty acid synthase-I, leading to the inhibition of the cell membrane.</span></p>\n</td>\n<td>\n<p><span><strong>Bacteriostatic</strong></span></p>\n<p><span>Inhibition of arabinosyl transferase, leading to a reduction in arabinogalactan needed for the cell wall.</span></p>\n</td>\n</tr>\n<tr>\n<td><span><strong>Location of Mycobacteria</strong></span></td>\n<td><span>Intracellular +&nbsp; Extracellular</span></td>\n<td><span>Intracellular +&nbsp; Extracellular</span></td>\n<td><span>&nbsp;Intracellular</span></td>\n<td>&nbsp;<span>Extracellular</span></td>\n</tr>\n<tr>\n<td><span><strong>Metabolism</strong></span></td>\n<td><span>Liver&nbsp;</span></td>\n<td><span>Liver (maximum)</span></td>\n<td><span>&nbsp;Liver</span></td>\n<td>&nbsp;<span>Kidney</span></td>\n</tr>\n<tr>\n<td><span><strong>Unique side effects</strong></span></td>\n<td>\n<p><span>Shoulder hand syndrome and CNS side effects.</span></p>\n</td>\n<td><span>Orange urine, sweat, and tears. Flu-like symptoms with intermittent dosing.</span></td>\n<td>\n<p><span>The most hepatotoxic drug. It also causes hyperuricemia.</span></p>\n</td>\n<td>\n<p><span>Optic neuritis and hence, not preferred in children.</span></p>\n</td>\n</tr>\n<tr>\n<td><span><strong>Mechanism of resistance</strong></span></td>\n<td>\n<p><span>Kat G mutation- the most severe form of resistance.</span></p>\n<p><span>Inh a gene overexpression-&nbsp; confers cross-resistance with ethionamide.</span></p>\n</td>\n<td>\n<p class=\p1\><span>Mutation in <em>rpoB-</em>&nbsp;the gene coding for the <span class=\s1\>β </span>subunit of RNA polymerase.</span></p>\n</td>\n<td><span>Mutation of the&nbsp;<em>Pnca</em> gene.</span></td>\n<td>\n<p class=\p1\><span>Mutations in the <em>embB</em> gene.</span></p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(565, 'Medicine,', 'PM1518. Indications of Ivermectin', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ul>\n<li>Onchocerciasis</li>\n<li>Chemoprophylaxis of filariasis</li>\n<li>Strongyloidiasis</li>\n<li>Gnathostomiasis</li>\n<li>Scabies</li>\n<li>Head lice</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(566, 'Medicine,', 'PM1516. Classification of antiretroviral drugs', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>CLASS</strong></td>\n<td><strong>DRUGS</strong></td>\n</tr>\n<tr>\n<td>Nucleoside Reverse Transcriptase Inhibitors</td>\n<td>Zidovudine, Stavudine, Lamivudine, Abacavir, Emtricitabine, Didanosine</td>\n</tr>\n<tr>\n<td>Nucleotide Reverse Transcriptase Inhibitors</td>\n<td>Tenofovir</td>\n</tr>\n<tr>\n<td>Non-nucleoside Reverse Transcriptase Inhibitors</td>\n<td>Nevirapine, Efavirenz, Delavirdine,&nbsp;Etravirine</td>\n</tr>\n<tr>\n<td>Protease Inhibitors</td>\n<td>Saquinavir, Indinavir, Ritonavir, Nelfinavir, Lopinavir, Atazanavir, Tiprinavir, Darunavir</td>\n</tr>\n<tr>\n<td>Entry Inhibitors</td>\n<td>Enfuvirtide, Maraviroc</td>\n</tr>\n<tr>\n<td>Integrase Inhibitors</td>\n<td>Raltegravir, Elvitegravir, Dolutegravir</td>\n</tr>\n</tbody>\n</table>\n<p class=\p1\>&nbsp;</p>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(567, 'Pharmacology', 'PM1393. Cytochrome enzyme inducing potency of Rifamycins', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ul>\n<li>Rifampicin: Highest </li>\n<li>Rifapentine: Intermediate </li>\n<li>Rifabutin: Least</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(568, 'Pharmacology', 'PM1348. Nephrotoxicity risk with Amphotericin-B formulations (decreasing order)', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><div>Nephrotoxicity Risk with Amphotericin B&nbsp;formulations:</div>\n<div>\n<ul>\n<li>\n<div>Conventional amphotericin B (C-AMB) has the highest risk of nephrotoxicity.</div>\n</li>\n<li>\n<div>C-AMB &gt; Amphotericin B lipid complex &gt; Liposomal amphotericin B.</div>\n</li>\n</ul>\n</div>\n<div>Amphotericin B colloidal dispersion has not been compared with other lipid-based formulations.&nbsp;</div></div><!---->\n      <!---->\n    </div><!---->'),
(569, 'Medicine,', 'PM1271. Protein synthesis inhibitors: Primary subunit of ribosome binding', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Mnemonic: 50 <strong>CELLS</strong> arranged into 30 <strong>TEAM</strong></p>\n<p>50s subunit binding:</p>\n<ul>\n<li><strong>C</strong>: Chloramphenicol, Clindamycin</li>\n<li><strong>E</strong>: Erythromycin (macrolides)</li>\n<li><strong>L</strong>: Lincosamides</li>\n<li><strong>L</strong>: Linezolid</li>\n<li><strong>S</strong>: Streptogramins</li>\n</ul>\n<p>30s subunit binding</p>\n<ul>\n<li><strong>Te</strong>: Tetracyclines</li>\n<li><strong>Am</strong>: Aminoglycosides</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(570, 'Pharmacology', 'PM1270. General properties of the first line drugs in TB', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<div><span><strong>First-line drugs</strong></span></div>\n</td>\n<td>\n<div><span><strong>Isoniazid</strong></span></div>\n</td>\n<td>\n<div><span><strong>Rifampicin</strong></span></div>\n</td>\n<td>\n<div><span><strong>Pyrazinamide</strong></span></div>\n</td>\n<td>\n<div><span><strong>Ethambutol</strong></span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span><strong>Mechanism</strong></span></div>\n</td>\n<td>\n<div><span>Bactericidal</span></div>\n</td>\n<td>\n<div><span>Bactericidal</span></div>\n<div><span>(Maximum)</span></div>\n</td>\n<td>\n<div><span>Bactericidal</span></div>\n</td>\n<td>\n<div><span>Bacteriostatic</span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span><strong>Location of Mycobacteria</strong></span></div>\n</td>\n<td>\n<div><span>Intracellular +&nbsp; Extracellular</span></div>\n</td>\n<td>\n<div><span>Intracellular +&nbsp; Extracellular</span></div>\n</td>\n<td>\n<div><span>Intracellular</span></div>\n</td>\n<td>\n<div><span>Extracellular</span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span><strong>Metabolism</strong></span></div>\n</td>\n<td>\n<div><span>Liver</span></div>\n</td>\n<td>\n<div><span>Liver</span></div>\n<div><span>(Maximum)</span></div>\n</td>\n<td>\n<div><span>Liver</span></div>\n</td>\n<td>\n<div><span>Kidney</span></div>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(571, 'Pharmacology', 'PM1248. Withdrawn fluoroquinolones and the associated adverse events', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<table>\n<tbody>\n<tr>\n<td><span><strong>Withdrawn Fluoroquinolones</strong></span></td>\n<td><span><strong>The Associated Adverse Events</strong></span></td>\n</tr>\n<tr>\n<td><span>Lomefloxacin and&nbsp;Gatifloxacin</span></td>\n<td><span>Phototoxicity, QTc prolongation</span></td>\n</tr>\n<tr>\n<td><span>Trovafloxacin</span></td>\n<td><span>Hepatotoxicity</span></td>\n</tr>\n<tr>\n<td><span>Gatifloxacin</span></td>\n<td><span>Hypoglycemia</span></td>\n</tr>\n<tr>\n<td><span>Grepafloxacin</span></td>\n<td><span>Cardiotoxicity</span></td>\n</tr>\n<tr>\n<td><span>Temafloxacin</span></td>\n<td><span>Immune hemolytic anemia</span></td>\n</tr>\n<tr>\n<td><span>Clinafloxacin</span></td>\n<td><span>Phototoxicity</span></td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(572, 'Medicine,', 'PM1243. Cell wall synthesis inhibitors (mnemonic \BBC Van\)', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>BBC Van:</strong></p>\n<ul>\n<li><strong>B</strong>eta lactams</li>\n<li><strong>B</strong>acitracin</li>\n<li><strong>C</strong>ycloserine</li>\n<li><strong>Van</strong>comycin</li>\n<li>Fosfomycin</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(573, 'Medicine,', 'PM0738. Direct acting antivirals (DAA) - Drugs against Hepatitis-C virus', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>The directly acting antivirals used in the treatment of Hepatitis C infection include:</p>\n<table>\n<tbody>\n<tr>\n<td width=\336\>\n<p><strong>Mechanism of action&nbsp;</strong></p>\n</td>\n<td width=\336\>\n<p><strong>Example</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\336\>\n<p>NS5A inhibitors (asvir)</p>\n</td>\n<td width=\336\>\n<ul>\n<li><strong>Velpatasvir</strong></li>\n<li>Daclatasvir</li>\n<li>Ledipasvir</li>\n<li>Ombitasvir</li>\n<li>Elbasvir</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td width=\336\>\n<p>NS5B nucleos(t)ide polymerase inhibitor (buvir)</p>\n</td>\n<td width=\336\>\n<ul>\n<li><strong>Sofosbuvir</strong></li>\n</ul>\n</td>\n</tr>\n<tr>\n<td width=\336\>\n<p>NS5B non nucleos(t)ide polymerase inhibitor (buvir)</p>\n</td>\n<td width=\336\>\n<ul>\n<li>Dasabuvir</li>\n<li>Beclabuvir</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td width=\336\>\n<p>NS3/4A inhibitors (previr)</p>\n</td>\n<td width=\336\>\n<ul>\n<li>Boceprevir</li>\n<li>Telaprevir</li>\n<li>Simeprevir</li>\n<li>Paritaprevir</li>\n<li>Asunaprevir</li>\n<li>Grazoprevir</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;The following combinations of antiviral drugs can be used:</p>\n<ul>\n<li aria-level=\1\>Acute, chronic hepatitis, or compensated cirrhosis:\n<ul>\n<li>Velpatasvir (NS5A Inhibitor) + Sofosbuvir (NS5B inhibitor) x 12 weeks.</li>\n</ul>\n</li>\n<li aria-level=\1\>Decompensated cirrhosis:\n<ul>\n<li>Velpatasvir (NS5A Inhibitor) + Sofosbuvir (NS5B inhibitor) + Ribavirin (syntheticnucleotide analogue) x 12 weeks.</li>\n</ul>\n</li>\n</ul>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(574, 'Microbiology,', 'PM0452. Comparison of Mutational and Transferable Drug Resistance', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Mutational drug resistance</strong></p>\n</td>\n<td>\n<p><strong>Transferable drug resistance</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>One drug resistance at a time</p>\n</td>\n<td>\n<p>Multiple drug resistance</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Low degree resistance</p>\n</td>\n<td>\n<p>High degree resistance</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Can be overcome by high drug dose</p>\n</td>\n<td>\n<p>High dose ineffective</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Drug combinations can prevent</p>\n</td>\n<td>\n<p>Drug combinations cannot prevent</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Resistance does not spread</p>\n</td>\n<td>\n<p>Spreads to same or different species</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Mutants may be defective</p>\n</td>\n<td>\n<p>Not defective</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Virulence may be low</p>\n</td>\n<td>\n<p>Virulence not decreased</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(575, 'Pharmacology', 'PM2437. Classification of cephalosporins', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><div><strong>First-generation cephalosporins</strong></div>\n<div>\n<table>\n<tbody>\n<tr>\n<td><strong>Parenteral</strong></td>\n<td><strong>Oral</strong></td>\n</tr>\n<tr>\n<td>Cefazolin</td>\n<td>Cephalexin<br>Cefadroxil</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n</div>\n<div><strong>Second-generation cephalosporins</strong></div>\n<div>\n<table>\n<tbody>\n<tr>\n<td><strong>Parenteral</strong></td>\n<td><strong>Oral</strong></td>\n</tr>\n<tr>\n<td>Cefuroxime<br>Cefoxitin<br>Cefotetan</td>\n<td>Cefaclor<br>Cefuroxime axetil<br>Cefprozil</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n</div>\n<div><strong>Third-generation cephalosporins</strong></div>\n<div>\n<table>\n<tbody>\n<tr>\n<td><strong>Parenteral</strong></td>\n<td><strong>Oral</strong></td>\n</tr>\n<tr>\n<td>\n<div>Cefotaxime <br>Ceftizoxime <br>Ceftriaxone</div>\n<div>Ceftazidime (Antipseudomonal) <br>Cefoperazone</div>\n</td>\n<td>\n<p>Cefixime <br>Cefpodoxime <br>Cefdinir<br>Ceftibuten<br>Ceftamet&nbsp;</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n</div>\n<div><strong>Fourth-generation cephalosporins</strong></div>\n<div>\n<table>\n<tbody>\n<tr>\n<td><strong>Parenteral</strong></td>\n<td><strong>Oral</strong></td>\n</tr>\n<tr>\n<td>Cefepime (Antipseudomonal)<br>Cefpirome</td>\n<td>&nbsp; &nbsp;---</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n</div>\n<div><strong>Fifth-generation cephalosporins</strong></div>\n<table>\n<tbody>\n<tr>\n<td><strong>Parenteral (Anti-MRSA activity)</strong></td>\n<td><strong>Oral</strong></td>\n</tr>\n<tr>\n<td>\n<div>Ceftaroline<br>Ceftobiprole&nbsp;</div>\n</td>\n<td>\n<div>&nbsp; &nbsp;---</div>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(576, 'Pharmacology', 'PM2436. Drugs acting on the receptors of  autonomic nervous system', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/f6e046c55a374ae4ac81093fb8fb3994x1280x1240.JPEG\><img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/29bde36a7c0d481b8a382e860850c0c6x1280x1069.JPEG\><img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/100503ee428b41daa7181b8ec96067cax1280x1314.JPEG\><img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/b8855f99fd024bba8ec437b0c8ab5efbx1280x1992.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(577, 'Pharmacology,', 'PM2410. Classification of Anti-Thyroid Drugs', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;Drugs <strong>inhibiting iodide trapping</strong>:</p>\n<ul>\n<li>Thiocyanates</li>\n<li>Perchlorates</li>\n<li>Nitrates</li>\n</ul>\n<p>Drugs <strong>inhibiting hormone synthesis</strong>:</p>\n<ul>\n<li>Propylthiouracil</li>\n<li>Methimazole</li>\n<li>Carbimazole</li>\n</ul>\n<p>Drugs <strong>inhibiting hormone release</strong>:</p>\n<ul>\n<li>Iodine</li>\n<li>Sodium iodide</li>\n<li>Potassium iodide</li>\n<li>Organic iodide</li>\n</ul>\n<p>Drugs <strong>inhibiting peripheral conversion:</strong></p>\n<ul>\n<li>Propylthiouracil</li>\n<li>Amiodarone</li>\n<li>Iodinated contrast dyes (Iopanoic acid)</li>\n<li>Propranolol</li>\n<li>Glucocorticoids</li>\n</ul>\n<p>Drugs that <strong>destroy thyroid tissue</strong>:</p>\n<ul>\n<li>Radioactive iodine</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://cdn1.dailyrounds.org/uploads/c80ec5fa0ace4523a36894c3898a9be8.JPEG\><!---->\n      </div><!---->\n    </div><!---->');
INSERT INTO db_pearl_m (pid, subject, pearl_title, pearl_desc) VALUES
(578, 'Obstetrics & Gynaecology,', 'PM2328. Hormonal contraceptives', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><div dir=\ltr\>\n<p><strong>Oral pills:</strong></p>\n<ul>\n<li>Combined oral contraceptive pills (COC)\n<ul>\n<li>Mala D -&nbsp;Levonorgestrel 0.15 mg + Ethinyl estradiol &nbsp;0.03 mg + Ferrous fumarate</li>\n<li>Mala N -&nbsp;Levonorgestrel 0.15 mg +&nbsp;Ethinyl estradiol &nbsp;0.03 mg + Ferrous fumarate&nbsp;(Govt. supply, free of cost)</li>\n</ul>\n</li>\n<li>Progesterone only pills (POP) - suitable for lactating women\n<ul>\n<li>Norethisterone 350 μg</li>\n<li>Norgestrel 75 μg</li>\n<li>Levonorgestrel (LNG) 30 μg</li>\n</ul>\n</li>\n<li>Once a month or long-acting pills - highly disappointing results\n<ul>\n<li>Quinestrol (long-acting estrogen) + short-acting progesterone</li>\n</ul>\n</li>\n<li>Gossypol - male pill : Cottonseed derivative and causes azoospermia and severe oligospermia</li>\n<li>Post-coital pills or interceptive\n<ul>\n<li>Levonorgestrel 0.75 mg, 2 doses, 12 hours apart</li>\n<li>Mifepristone 10-600 mg</li>\n<li>Ulipristal 30 mg, within 5 days of intercourse</li>\n<li>Centchroman 60 mg, 2 tablets, within 24 hrs of intercourse</li>\n<li>GnRH agonists &nbsp;</li>\n<li>CuT IUCD, inserted within 5 days</li>\n</ul>\n</li>\n</ul>\n<p><strong> Injectable contraceptives</strong></p>\n<ul>\n<li>Progesterone only injectables\n<ul>\n<li>DMPA&nbsp;150 mg IM every 3 months</li>\n<li>NET-EN&nbsp;200 mg IM every 2 months</li>\n</ul>\n</li>\n<li>Combined injectables\n<ul>\n<li>Cyclofem - DMPA 25 mg + Estradiol cypionate 5 mg</li>\n<li>Mesigyna - NET-EN 50 mg &nbsp;+ Estradiol valerate 5 mg</li>\n</ul>\n</li>\n</ul>\n<p><strong> Subdermal implants</strong></p>\n<ul>\n<li>Norplant I -&nbsp;Levonorgestrel 35 mg</li>\n<li>Norplant II or Jadelle -&nbsp;2 rods with levonorgestrel 70 mg in each and released at a rate of 50 μg/day &nbsp;</li>\n</ul>\n<p><strong>Others</strong></p>\n<ul>\n<li>Hormonal IUCD -&nbsp;Mirena which contains LNG 52 mg with release rate of&nbsp;20 μg/day</li>\n<li>Orthoevra patch -&nbsp;Norelgestomin + Ethinyl estradiol weekly for 3 weeks, then patch free for 1 week</li>\n<li>Vaginal ring or Nuvaring - Etonogestrel + ethinyl estradiol daily release, use for 3 weeks with withdrawal for 1 week</li>\n</ul>\n</div></div><!---->\n      <!---->\n    </div><!---->'),
(579, 'Obstetrics & Gynaecology,', 'PM2225. Uses of Misoprostol', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>INDICATIONS</strong></td>\n<td><strong>PRIMARY DOSE OF MISOPROSTOL</strong></td>\n<td><strong>REMARKS</strong></td>\n</tr>\n<tr>\n<td>\n<p><strong>MEDICAL TERMINATION OF PREGNANCY</strong></p>\n<p>&nbsp;</p>\n</td>\n<td>\n<p>Combined Regimen(recommended):<br>Upto 12 weeks of gestation-800mcg vaginally, buccally or sublingually.<br><br></p>\n<p>Beyond 12 weeks of gestation-400mcg&nbsp;vaginally, buccally or sublingually every 3 hours<br><br><br></p>\n<p>Misoprostol only regimen:<br>Upto 12 weeks-800mcg&nbsp;vaginally, buccally or sublingually<br><br>Beyond 12 weeks-400mcg&nbsp;vaginally, bucally or sublingually every 3 hours<br><br></p>\n</td>\n<td>\n<p>Ideally used 1-2 days after 200mg of Mifepristone in the combined regimen.</p>\n<p><br>The mucosal route of administration is preferred over the oral route of administration.</p>\n<p><br>These doses also apply for incomplete and missed abortion.<br><br></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>SURGICAL ABORTION</strong></p>\n<p>12-14weeks- both medical and surgical methods can be done</p>\n<p>&gt;14weeks-surgical method&nbsp;</p>\n</td>\n<td>Cervical preparation: 400mcg vaginally/ sublingually</td>\n<td>\n<p>Another method: Oral Mifepristone 200mg 24-48 hours before the procedure</p>\n<p>&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td><strong>PPH Prophylaxis</strong></td>\n<td>\n<p>800mcg sublingual single dose OR 600mcg orally/ sublingually/rectally</p>\n<p>single dose&nbsp;for PPH secondary prevention (approx &gt;350ml blood loss)</p>\n</td>\n<td rowspan=\2\>&nbsp;Where oxytocin is not available or storage conditions are inadequate</td>\n</tr>\n<tr>\n<td><strong>PPH treatment</strong></td>\n<td>800 mcg sublingually single dose</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(580, 'Obstetrics & Gynaecology,', 'PM1783. Types of monophasic combined oral pills', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\0\>\n<tbody>\n<tr>\n<td>Type</td>\n<td>Estrogen</td>\n<td>Progestogen</td>\n</tr>\n<tr>\n<td>1st Generation</td>\n<td>Ethinyl oestradiol</td>\n<td>Norethindrone</td>\n</tr>\n<tr>\n<td>2nd&nbsp;Generation</td>\n<td>Ethinyl oestradiol</td>\n<td>Norgestrel, LNG</td>\n</tr>\n<tr>\n<td>3rd&nbsp;Generation</td>\n<td>Ethinyl oestradiol</td>\n<td>Desogestrel,gestodene norgestimate</td>\n</tr>\n<tr>\n<td>4th&nbsp;Generation<br>(Yasmin)</td>\n<td>Ethinyl oestradiol</td>\n<td>Drospirenone</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(581, 'Medicine,', 'PM1542. Causes of SIADH', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Neoplasms</strong></p>\n<p><strong>Carcinomas</strong></p>\n<ul>\n<li>Lung</li>\n<li>Duodenum</li>\n<li>Pancreas</li>\n<li>Ovary</li>\n<li>Bladder, ureter</li>\n</ul>\n<p><strong>Other neoplasms</strong></p>\n<ul>\n<li>Thymoma</li>\n<li>Mesothelioma</li>\n<li>Bronchial adenoma</li>\n<li>Carcinoid</li>\n<li>Gangliocytoma</li>\n<li>Ewing’s sarcoma</li>\n</ul>\n<p><strong>Head trauma</strong> (closed and penetrating)</p>\n<p><strong>Infections</strong></p>\n<ul>\n<li>Pneumonia</li>\n<li>Abscess</li>\n<li>Cavitation (aspergillosis)</li>\n<li>Tuberculosis</li>\n<li>Meningitis, Encephalitis</li>\n<li>AIDS</li>\n</ul>\n<p><strong>Vascular</strong></p>\n<ul>\n<li>Cerebrovascular occlusions, haemorrhage</li>\n<li>Cavernous sinus thrombosis</li>\n</ul>\n<p><strong>Neurologic</strong></p>\n<ul>\n<li>Guillain-Barre syndrome</li>\n<li>Multiple sclerosis</li>\n<li>Delirium tremens</li>\n<li>Amyotrophic lateral sclerosis</li>\n<li>Hydrocephalus</li>\n<li>Psychosis</li>\n<li>Peripheral neuropathy</li>\n</ul>\n<p><strong>Congenital malformations</strong></p>\n<p><strong>Metabolic</strong></p>\n<ul>\n<li>Acute intermittent porphyria</li>\n</ul>\n<p><strong>Respiratory</strong></p>\n<ul>\n<li>Asthma</li>\n<li>Pneumothorax</li>\n<li>Positive-pressure respiration</li>\n</ul>\n<p><strong>Drugs</strong></p>\n<ul>\n<li>Vasopressin or desmopressin</li>\n<li>Serotonin reuptake inhibitors</li>\n<li>Oxytocin (high dose)</li>\n<li>Vincristine</li>\n<li>Carbamazepine</li>\n<li>Nicotine</li>\n<li>Phenothiazines</li>\n<li>Cyclophosphamide</li>\n<li>Tricyclic antidepressants</li>\n<li>Monoamine oxidase inhibitors</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(582, 'Medicine,', 'PM1526. Drugs causing hyperprolactinemia', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ul>\n<li>Dopamine receptor blockers:\n<ul>\n<li>Atypical antipsychotics: Risperidone</li>\n<li>Phenothiazines: Chlorpromazine, perphenazine</li>\n<li>Butyrophenones: Haloperidol</li>\n<li>Thioxanthenes</li>\n<li>Metoclopramide</li>\n</ul>\n</li>\n</ul>\n<ul>\n<li>Dopamine synthesis inhibitors:\n<ul>\n<li>α-Methyldopa</li>\n</ul>\n</li>\n</ul>\n<ul>\n<li>Catecholamine depletors:\n<ul>\n<li>Reserpine</li>\n</ul>\n</li>\n</ul>\n<ul>\n<li>Opiates</li>\n</ul>\n<p>&nbsp;</p>\n<ul>\n<li>H<sub>2</sub> antagonists:\n<ul>\n<li>Cimetidine, ranitidine</li>\n</ul>\n</li>\n</ul>\n<ul>\n<li>Imipramines:\n<ul>\n<li>Amitriptyline, amoxapine</li>\n</ul>\n</li>\n</ul>\n<ul>\n<li>Serotonin reuptake inhibitors:\n<ul>\n<li>Fluoxetine</li>\n</ul>\n</li>\n</ul>\n<ul>\n<li>Calcium channel blockers:\n<ul>\n<li>Verapamil</li>\n</ul>\n</li>\n</ul>\n<ul>\n<li>Estrogens</li>\n</ul>\n<p>&nbsp;</p>\n<ul>\n<li>Thyrotropin-releasing hormone</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(583, 'Medicine,', 'PM1143. Averbuch’s Chemotherapy Protocol for Malignant Pheochromocytoma', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ul>\n<li><strong>Averbuch’s chemotherapy protocol</strong> for malignant pheochromocytoma includes\n<ul>\n<li><strong>Dacarbazine </strong>(600 mg/m<sup>2 </sup>on days 1 and 2)</li>\n<li><strong>Cyclophosphamide</strong> (750 mg/m<sup>2</sup> on day 1)</li>\n<li><strong>Vincristine</strong> (1.4 mg/m<sup>2 </sup>on day 1)</li>\n</ul>\n</li>\n<li>All repeated <strong>every 21 days</strong> for three to six cycles.</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(584, 'Medicine,', 'PM0991. Insulin Classification According to Duration of Action', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Inhaled insulin&nbsp;(2-3 hours)</strong></p>\n<ul>\n<li>Afrezza&nbsp;</li>\n</ul>\n<p><strong>Rapid acting (3-4 hours)</strong></p>\n<ul>\n<li>Aspart</li>\n<li>Glulisine</li>\n<li>Lispro</li>\n</ul>\n<p><strong>Short acting&nbsp;(6-8 hours)</strong></p>\n<ul>\n<li>Regular</li>\n</ul>\n<p><strong>Intermediate acting (10-16 hours)</strong></p>\n<ul>\n<li>NPH</li>\n<li>Lente</li>\n</ul>\n<p><strong>Long acting&nbsp;(12-24 hours)</strong></p>\n<ul>\n<li>Detemir</li>\n<li>Glargine</li>\n<li>Degludec <strong>(24-42 hours)</strong></li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(585, 'Pharmacology', 'PM0924. Activity of corticosteroids', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Highest mineralocorticoid </strong>activity is seen with <strong>aldosterone</strong>.</p>\n<p>Highest mineralocorticoid activity in a drug that is <strong>clinically used</strong> is seen with <strong>fludrocortisone</strong>&nbsp;(aldosterone cannot be used clinically).</p>\n<p><strong>Least </strong>mineralocorticoid activity is seen with <strong>methylprednisolone</strong>.</p>\n<p>&nbsp;</p>\n<p><strong>Highest glucocorticoid</strong> activity is seen with <strong>dexamethasone </strong>and<strong> betamethasone</strong>.</p></div><!---->\n      <!---->\n    </div><!---->'),
(586, 'Pharmacology,', 'PM0886. Hormonal therapy in breast carcinoma given for ER/PR-positive cases', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Role:</strong></p>\n<ul>\n<li>Reduce risk of recurrence</li>\n<li>Reduce risk of contralateral breast cancer</li>\n<li>Reduce risk of progression of in situ to invasive carcinoma</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>Selective estrogen- receptor modulator</td>\n<td>Aromatase inhibitors</td>\n<td>Ovarian suppression/ ablation</td>\n</tr>\n<tr>\n<td>\n<ul>\n<li>Tamoxifen</li>\n<li>Raloxifen</li>\n<li>Arzoxifen</li>\n<li>Fenoxifen</li>\n</ul>\n</td>\n<td>\n<ul>\n<li>Anastrazole</li>\n<li>Letrozole</li>\n<li>Exemestane</li>\n</ul>\n</td>\n<td>\n<ul>\n<li>Oophorectomy</li>\n<li>GnRH analogues</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(587, 'Pharmacology', 'PM0752. Classification of Bisphosphonates (mainly based on potency)', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>First generation</p>\n<ul>\n<li>Etidronate ( least potent)</li>\n<li>Clodronate</li>\n<li>Tiludronate</li>\n</ul>\n<p>Second generation: 10-100 times more potent than the first generation &nbsp;</p>\n<ul>\n<li>Alendronate</li>\n<li>Pamidronate</li>\n<li>Ibandronate</li>\n</ul>\n<p>Third generation: 10,000 times more potent than the first generation &nbsp;</p>\n<ul>\n<li>Risedronate</li>\n<li>Zoledronate</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(588, 'Medicine,', 'PM0740. Side effects of Corticosteroids: mnemonic \CORTICOSTEROIDS\', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>The mnemonic is \<strong>CORTICOSTEROIDS</strong>\</p>\n<p><strong>C</strong> - Cushing’s syndrome</p>\n<p><strong>O</strong> - Osteoporosis</p>\n<p><strong>R</strong> - Retardation of growth</p>\n<p><strong>T</strong> - Thin skin and easy bruisability</p>\n<p><strong>I</strong> - Infections and immunosuppression</p>\n<p><strong>C</strong> - Cataract</p>\n<p><strong>O</strong> - Oedema</p>\n<p><strong>S</strong> - Suppression of HPA axis</p>\n<p><strong>T</strong> - Thinning and ulceration of gastric mucosa</p>\n<p><strong>E</strong> - Emotional disturbance</p>\n<p><strong>R</strong> - Rise in BP</p>\n<p><strong>O</strong> - Others fatal abnormalities like hypokalemia</p>\n<p><strong>I</strong> - Increase in hair growth (hirsutism)</p>\n<p><strong>D</strong> - Diabetes mellitus precipitation</p>\n<p><strong>S</strong> - Striae</p></div><!---->\n      <!---->\n    </div><!---->'),
(589, 'Pharmacology', 'PM0739. Classification of Glucocorticoids', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Short-acting</strong> (t<sub>1/2</sub> &lt; 12 hours)</p>\n<ul>\n<li>Hydrocortisone</li>\n</ul>\n<p><strong>Intermediate-acting</strong> (t<sub>1/2</sub>&nbsp;of 12-36 hours)</p>\n<ul>\n<li>Prednisolone</li>\n<li>Methylprednisolone</li>\n<li>Triamcinolone</li>\n<li>Deflazacort</li>\n</ul>\n<p><strong>Long-acting</strong> (t<sub>1/2</sub> &gt; 36 hours)</p>\n<ul>\n<li>Dexamethasone</li>\n<li>Betamethasone</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(590, 'Obstetrics & Gynaecology,', 'PM0679. Drug of choice for hyperthyroidism/thyrotoxicosis in pregnancy', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>In the 1st trimester: <strong>Propylthiouracil</strong></p>\n<p>In the 2nd and 3rd trimester: <strong>Methimazole</strong></p></div><!---->\n      <!---->\n    </div><!---->'),
(591, 'Pharmacology', 'PM0553. PDE inhibitors', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><span><strong>PDE inhibitors</strong></span></td>\n<td><span><strong>Drugs</strong></span></td>\n<td><span><strong>Clinical application</strong></span></td>\n</tr>\n<tr>\n<td>\n<p><span><strong>Non-selective PDE inhibitors</strong></span></p>\n<p><span><strong>&nbsp;</strong></span></p>\n</td>\n<td>\n<p><span>Methylated xanthines, Caffeine, Theophylline</span></p>\n</td>\n<td>\n<p><span>Asthma, COPD</span></p>\n</td>\n</tr>\n<tr>\n<td><span><strong>&nbsp;</strong></span></td>\n<td>\n<p><span>Pentoxifylline</span></p>\n</td>\n<td>\n<p><span>Peripheral artery disease &nbsp;</span></p>\n</td>\n</tr>\n<tr>\n<td><span><strong>PDE 2 inhibitor</strong></span></td>\n<td>\n<p><span>Anagrelide</span></p>\n</td>\n<td><span>Essential thrombocytosis</span></td>\n</tr>\n<tr>\n<td><span><strong>PDE 3 inhibitor</strong></span></td>\n<td>\n<p><span>Cilostazol</span></p>\n</td>\n<td><span>Peripheral artery disease</span></td>\n</tr>\n<tr>\n<td><span><strong>&nbsp;</strong></span></td>\n<td>\n<p><span>Milrinone, Amrinone</span></p>\n</td>\n<td><span>Cardiac failure&nbsp;&nbsp;</span></td>\n</tr>\n<tr>\n<td><span><strong>PDE 4 inhibitor</strong></span></td>\n<td>\n<p><span>Roflumilast</span></p>\n</td>\n<td><span>COPD</span></td>\n</tr>\n<tr>\n<td><span><strong>&nbsp;</strong></span></td>\n<td>\n<p><span>Drotaverine</span></p>\n</td>\n<td><span>Antispasmodic drug</span></td>\n</tr>\n<tr>\n<td><span><strong>PDE 5 inhibitor</strong></span></td>\n<td>\n<p><span>Sildenafil, Vardenafil, Tadalafil (Also inhibit PDE6)</span></p>\n</td>\n<td><span>Erectile dysfunction Pulmonary hypertension</span></td>\n</tr>\n</tbody>\n</table>\n<p><span>&nbsp;</span></p></div><!---->\n      <!---->\n    </div><!---->'),
(592, 'Medicine,', 'PM2335. Important drugs for rheumatoid arthritis', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><div dir=\ltr\>\n<p><strong>1) NSAIDs -&nbsp;</strong>analgesic and anti-inflammatory, used as <strong>adjunctive</strong> for management of uncontrollable <strong>symptoms</strong> and in acute <strong>flares.</strong></p>\n<p dir=\ltr\><strong>2) Glucocorticoids - </strong>used in acute <strong>flare</strong> for&nbsp;rapid control and as <strong>initial therapy</strong> before the onset of action of DMARDs.&nbsp;Chronic administration of low doses is needed in patients with inadequate response to DMARDs.</p>\n<ul>\n<li dir=\ltr\>Prednisolone - oral&nbsp;</li>\n<li dir=\ltr\>Triamcinolone - intraarticular<br>&nbsp;</li>\n</ul>\n<strong><span>3) DMARDs - </span></strong><span>disease-modifying anti-rheumatoid drugs:</span></div>\n<div dir=\ltr\>&nbsp;</div>\n<div dir=\ltr\><strong>- Conventional DMARDs:</strong></div>\n<div dir=\ltr\>\n<ul>\n<li dir=\ltr\>Methotrexate (MTX)- is an antifolate drug. The DMARD of <strong>choice</strong> and the anchor drug for combination therapy. Regular <strong>LFT</strong> monitoring is required.</li>\n<li dir=\ltr\>Hydroxychloroquine- is used as <strong>monotherapy</strong> for <strong>early</strong> and <strong>mild</strong> disease and&nbsp;in combination with other DMARDs for severe cases.</li>\n<li dir=\ltr\>Sulfasalazine- is a salicylate. It <strong>reduces</strong> the radiographic <strong>progression</strong> of the disease.</li>\n<li dir=\ltr\>Leflunomide- is a pyrimidine synthase inhibitor used as an <strong>alternative</strong> to MTX.</li>\n</ul>\n</div>\n<div dir=\ltr\>\n<p><span><strong>- Biologicals:</strong></span></p>\n<ul>\n<li>TNF-α antagonists- used in combination with MTX. Has a risk of serious bacterial infections, opportunistic fungal infection &amp; reactivation of latent TB. The TNF-α inhibitors are <strong>adalimumab,</strong> golimumab, etanercept, infliximab, and certolizumab.</li>\n<li>Abatacept- a T-cell costimulation inhibitor, used in combination with MTX, leflunomide, etc.</li>\n<li>Rituximab- an <strong>anti-CD20</strong> antibody. Used in refractory disease in combination with MTX.</li>\n<li>Anakinra- an<strong> IL-1</strong> receptor <strong>antagonist</strong>. It should not be combined with anti-TNF alpha.</li>\n<li><strong>IL-6</strong> receptor <strong>antagonists</strong>- tocilizumab and sarilumab are used as monotherapy&nbsp;or combination therapy in moderate to severe disease.&nbsp;&nbsp;</li>\n</ul>\n</div>\n<div dir=\ltr\>\n<p><span><strong>- Small molecules:&nbsp;</strong></span></p>\n<ul>\n<li>Janus kinase inhibitors:&nbsp;<strong>Tofacitinib</strong>, baricitinib, and upadacitinib are used as monotherapy or combination therapy.</li>\n</ul>\n</div></div><!---->\n      <!---->\n    </div><!---->'),
(593, 'Medicine,', 'PM1929. Drugs precipitating Gout', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Drugs that precipitate gout are-</p>\n<p><strong>C</strong>yclosporine or cancer drugs<br><strong>A</strong>spirin (low dose)<br><strong>N</strong>iacin<br><strong>T</strong>hiazide<br><strong>L</strong>asix (furosemide)<br><strong>E</strong>thambutol<br><strong>A</strong>lcohol<br><strong>P</strong>yrazinamide</p>\n<p>Mnemonic for drugs precipitating gout is <strong>CANT LEAP</strong></p></div><!---->\n      <!---->\n    </div><!---->'),
(594, 'Medicine,', 'PM0642. Acid resistant penicillins: mnemonic \VODKA\', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>The mnemonic \<strong>VODKA</strong>\ can be used to for acid-resistant penicillins that are active orally.</p>\n<ul>\n<li><strong>V</strong>&nbsp;- Penicillin <strong>V</strong></li>\n<li><strong>O</strong> - <strong>O</strong>xacillin</li>\n<li><strong>D</strong>&nbsp;-&nbsp;<strong>D</strong>icloxacillin</li>\n<li><strong>K</strong> - <strong>C</strong>loxacillin</li>\n<li><strong>A</strong> - <strong>A</strong>moxycillin/ Ampicillin</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(595, 'Pharmacology', 'PM0605. Factor Xa: IIa inhibition activity of antithrombin activators', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Preparation</strong></p>\n</td>\n<td>\n<p><strong>Factor Xa: IIa inhibition activity</strong></p>\n</td>\n</tr>\n</tbody>\n</table>\n<table>\n<tbody>\n<tr>\n<td>\n<p>Unfractionated heparin</p>\n</td>\n<td>\n<p><strong>1:1</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>LMWH</p>\n</td>\n<td>\n<p><strong>2:1 to 3:1</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Fondaparinux</p>\n</td>\n<td>\n<p><strong>Only Xa inhibition </strong></p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(596, 'Pharmacology', 'PM0580. Triptans - 5HT 1B/1D agonists', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Few important properties of triptans:</p>\n<ul>\n<li><strong>Longest </strong>acting triptan: <strong>Frovatriptan</strong></li>\n<li><strong>Fastest</strong> (Oral) acting triptan: <strong>Rizatriptan</strong></li>\n<li><strong>Least bioavailability</strong> (Oral): <strong>Sumatriptan</strong></li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(597, 'Pharmacology', 'PM0523. Some Important Points about Selective COX-2 Inhibitors', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>They are also known as <strong>coxibs</strong>. They are different from non-selective COX inhibitors as follows:</p>\n<ul>\n<li aria-level=\1\>Fewer GI side effects as COX-1 is not inhibited</li>\n<li aria-level=\1\>No antiplatelet action as thromboxane A2 production is unaffected.</li>\n</ul>\n<p>Adverse effects and contraindications:</p>\n<ul>\n<li aria-level=\1\>Myocardial infarction</li>\n<li aria-level=\1\>Stroke</li>\n<li aria-level=\1\>Congestive heart failure</li>\n<li aria-level=\1\>Hypertension</li>\n<li aria-level=\1\>Renal impairment.</li>\n</ul>\n<p>Order of COX-2 selectivity</p>\n<ul>\n<li aria-level=\1\><strong>Lumiracoxib</strong> &gt; etoricoxib &gt; rofecoxib &gt; valdecoxib &gt; <strong>celecoxib</strong>.</li>\n<li aria-level=\1\>Lumiracoxib - most selective</li>\n<li aria-level=\1\>Celecoxib - least selective</li>\n</ul>\n<p>The only coxib which can be given parenterally is <strong>Parecoxib</strong>.</p></div><!---->\n      <!---->\n    </div><!---->'),
(598, 'Pharmacology', 'PM0522. Preferential COX-2 inhibitors: mnemonic \NAMED\', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>The mnemonic for preferential COX-2 inhibitors is \<strong>NAMED</strong>\:</p>\n<ul>\n<li><strong>N</strong>imesulide</li>\n<li><strong>A</strong>ceclofenac</li>\n<li><strong>M</strong>eloxicam</li>\n<li><strong>E</strong>todolac</li>\n<li><strong>D</strong>iclofenac</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(599, 'Pathology,', 'PM0521. Prostaglandins effect on platelet action', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Anti-aggregatory</strong> prostaglandins:</p>\n<ul>\n<li>PGI<sub>2</sub></li>\n<li>PGD<sub>2</sub></li>\n</ul>\n<p><strong>Pro-aggregatory</strong> prostaglandins:</p>\n<ul>\n<li>Thromboxane A2</li>\n<li>PGH<sub>2</sub></li>\n<li>PGG<sub>2</sub></li>\n</ul>\n<p>Variable effects are seen with PGE<sub>2</sub></p></div><!---->\n      <!---->\n    </div><!---->'),
(600, 'Medicine,', 'PM2398. Classification of anti-platelet drugs', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><span><strong>Class</strong></span></td>\n<td><span><strong>Drugs</strong></span></td>\n</tr>\n<tr>\n<td><span>Cyclooxygenase- I inhibitors</span></td>\n<td><span>Aspirin</span></td>\n</tr>\n<tr>\n<td><span>Irreversible P2Y<sub>12&nbsp;</sub>inhibitors</span></td>\n<td>\n<p><span>Ticlopidine, Clopidogrel, Prasugrel</span></p>\n</td>\n</tr>\n<tr>\n<td><span>Reversible P2Y<sub>12&nbsp;</sub>inhibitors</span></td>\n<td><span>Ticagrelor, Cangrelor</span></td>\n</tr>\n<tr>\n<td><span>GpIIb/IIIa inhibitors</span></td>\n<td><span>Abciximab, Eptifibatide, Tirofiban</span></td>\n</tr>\n<tr>\n<td><span>Thrombin (PAR-1) receptor antagonist</span></td>\n<td><span>Vorapaxar&nbsp;</span></td>\n</tr>\n<tr>\n<td><span>Phosphodiesterase inhibitors</span></td>\n<td>\n<p class=\p1\><span>Dipyridamole</span></p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(601, 'Pharmacology,', 'PM2385. Heparin vs. LMWH vs. Fondaparinux', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><span><strong>Features</strong></span></td>\n<td><span><strong>Unfractionated heparin</strong></span></td>\n<td><span><strong>LMWH</strong></span></td>\n<td><span><strong>Fondaparinux</strong></span></td>\n</tr>\n<tr>\n<td><strong><span>Structure</span></strong></td>\n<td>\n<p class=\p1\><span>A glycosaminoglycan consisting of long saccharide chains</span></p>\n</td>\n<td><span>A glycosaminoglycan consisting of short saccharide chains</span></td>\n<td><span>A synthetic pentasaccharide</span></td>\n</tr>\n<tr>\n<td><strong><span>Factor Xa inhibition&nbsp;</span></strong></td>\n<td><span>Yes</span></td>\n<td><span>Yes</span></td>\n<td><span>Yes</span></td>\n</tr>\n<tr>\n<td><strong><span>Factor IIa inhibition</span></strong></td>\n<td>\n<p><span>Yes (Xa = IIa)</span></p>\n<p><span>The long chains act Xa and IIa equally.</span></p>\n</td>\n<td>\n<p><span>Yes (Xa &gt; IIa)</span></p>\n<p><span>LMWH contains a mixture of long and short chains.</span></p>\n</td>\n<td>\n<p><span>No</span></p>\n<p><span>The pentasaccharide has no action on factor IIa</span></p>\n</td>\n</tr>\n<tr>\n<td><strong><span>Bioavailability</span></strong></td>\n<td>\n<p><span>Oral: Not absorbed</span></p>\n<p><span>S.C.: Not good&nbsp;<span>(longer chains are not absorbed well)&nbsp;</span></span></p>\n<p><span>I.V.: Good</span></p>\n</td>\n<td>\n<p><span>Oral: Not absorbed</span></p>\n<p><span>S.C.: Good&nbsp;</span></p>\n</td>\n<td>\n<p><span>Oral: Not absorbed</span></p>\n<p><span>S.C.: Very good</span></p>\n</td>\n</tr>\n<tr>\n<td><strong><span>Variable response</span></strong></td>\n<td><span>Yes&nbsp;</span></td>\n<td><span>No</span></td>\n<td><span>No</span></td>\n</tr>\n<tr>\n<td><strong><span>Metabolism</span></strong></td>\n<td><span>Cleared by the reticuloendothelial system</span>&nbsp;</td>\n<td><span>Cleared by the kidneys</span></td>\n<td><span>Cleared by the kidneys</span></td>\n</tr>\n<tr>\n<td><strong><span>Safety in renal disease</span></strong></td>\n<td><span>Safe in renal impairment</span></td>\n<td><span>Used with close monitoring</span></td>\n<td><span>Contraindicated in renal failure</span></td>\n</tr>\n<tr>\n<td><strong><span>Monitoring&nbsp;</span></strong></td>\n<td><span>Required, with <strong>aPTT</strong></span></td>\n<td><span>Required only in <strong>renal impairment</strong>,<strong>&nbsp;</strong>with <strong>anti-factor Xa assay</strong></span></td>\n<td><span>Not required</span></td>\n</tr>\n<tr>\n<td><strong><span>Protamine sulfate as antidote</span></strong></td>\n<td><span>Full reversal</span></td>\n<td><span>Partial reversal</span> <span>(acts only on the longer chains)</span></td>\n<td><span>No&nbsp;reversal</span></td>\n</tr>\n<tr>\n<td><strong><span>Risk of Heparin-induced thrombocytopenia</span></strong></td>\n<td><span>High&nbsp;</span></td>\n<td><span>Lower</span></td>\n<td><span>Least</span></td>\n</tr>\n<tr>\n<td><strong><span>Risk of osteoporosis</span></strong></td>\n<td><span>Occasionally</span></td>\n<td><span>Lower</span></td>\n<td><span>Lower</span></td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/804286e8b58a478caeb89f36ccb6b8fbx1280x2261.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(602, 'Pharmacology', 'PM1712. Specific reversal agents for anticoagulants', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ul>\n<li>Heparin: protamine sulfate </li>\n<li>Warfarin: vitamin K </li>\n<li>Rivaroxaban and other oral Xa inhibitors: andexanet alfa </li>\n<li>Dabigatran: idarucizumab</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(603, 'Medicine,', 'PM0610. Contraindications for thrombolytic therapy', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>The <strong>absolute contraindications</strong> for thrombolytic therapy are:</p>\n<ul>\n<li>Prior intracranial hemorrhage</li>\n<li>Known structural cerebral vascular lesion</li>\n<li>Known malignant intracranial neoplasm</li>\n<li>Ischemic stroke within 3 months</li>\n<li>Suspected aortic dissection</li>\n<li>Active bleeding or bleeding diathesis (excluding menses)</li>\n<li>Significant closed head trauma or facial trauma within 3 months</li>\n</ul>\n<p>The <strong>relative contraindications</strong> for thrombolytic therapy are:</p>\n<ul>\n<li>Uncontrolled hypertension (SBP&gt;180 mm Hg; DBP&gt;110 mmHg)</li>\n<li>Traumatic or prolonged CPR or major surgery within 3 months</li>\n<li>Recent (2-4 weeks) internal bleeding</li>\n<li>Noncompressible vascular punctures</li>\n<li>Pregnancy</li>\n<li>Active peptic ulcer</li>\n<li>Current use of warfarin and INR&gt;1.7</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(604, 'Medicine,', 'PM0609. Classification of Anticoagulants', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/b14dd7e06cd44c95a1d5289e3a32ad8ax1280x1944.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td colspan=\2\><strong>Classification of anticoagulants</strong></td>\n</tr>\n<tr>\n<td>1) Vitamin K antagonist</td>\n<td>Warfarin</td>\n</tr>\n<tr>\n<td>2) Direct thrombin inhibitors (DTI)&nbsp;&nbsp;&nbsp;</td>\n<td>\n<p><strong>Parenteral:&nbsp;Monovalent</strong> DTI-&nbsp;Argatroban</p>\n<p><strong>Bivalent</strong> DTI- Lepirudin, Desirudin, Bivalirudin</p>\n<p><strong>Oral</strong>:&nbsp;Dabigatran&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>3) Indirect thrombin inhibitors&nbsp;</td>\n<td>\n<p>Unfractionated heparin</p>\n<p>Low molecular weight heparins:&nbsp;Enoxaparin, Dalteparin, Tinzaparin, Nadroparin</p>\n</td>\n</tr>\n<tr>\n<td>4) Indirect factor Xa inhibitors</td>\n<td>Fondaparinux</td>\n</tr>\n<tr>\n<td>5) <strong>Oral</strong> Direct factor Xa inhibitors</td>\n<td>Rivaro<strong>xa</strong>ban,Api<strong>xa</strong>ban, Edo<strong>xa</strong>ban,Betri<strong>xa</strong>ban</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(605, 'Medicine,', 'PM0603. Plasminogen activators (classification of fibrinolytics)', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Fibrin non-specific</strong> plasminogen activators</p>\n<ul>\n<li>Streptokinase - least fibrin specific and has the lowest incidence of intracranial hemorrhage</li>\n<li>Anistreplase</li>\n<li>Urokinase</li>\n</ul>\n<p><strong>Fibrin specific</strong> plasminogen activators</p>\n<ul>\n<li>Alteplase - has the shortest half-life</li>\n<li>Tenecteplase - most fibrin specific and given as a single bolus dose</li>\n<li>Reteplase</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(606, 'Medicine,', 'PM2457. Drugs used in irritable bowel syndrome (IBS)', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>For abdominal pain</p>\n<ul>\n<li>Antispasmodics - Dicyclomine</li>\n<li>Tricyclic antidepressants - Amitriptyline, Imipramine</li>\n<li>Selective serotonin reuptake inhibitor - Citalopram</li>\n</ul>\n<p>For diarrhoea</p>\n<ul>\n<li>Opiate - Loperamide</li>\n<li>5-HT3 antagonist - Alosetron</li>\n<li>Bile acid sequestrant - Cholestyramine resin</li>\n</ul>\n<p>For constipation</p>\n<ul>\n<li>Laxatives such as psyllium/ispaghula husk and polyethylene glycol</li>\n<li>Cl<sup>-</sup> channel activator - Lubiprostone</li>\n<li>Guanylate cyclase agonist - Linaclotide</li>\n<li>5-HT4 agonist - Tegaserod</li>\n<li>Selective serotonin reuptake inhibitor - Paroxetine</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(607, 'Medicine,', 'PM1565. Uses of methotrexate (mnemonic: CANCER)', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Mnemonic: <strong>CANCER</strong></p>\n<p><strong>C</strong>rohn’s disease</p>\n<p><strong>A</strong>bortion</p>\n<p><strong>N</strong>on-Hodgkin lymphoma, ALL</p>\n<p><strong>C</strong>horiocarcinoma, Sarcoma</p>\n<p><strong>E</strong>ctopic pregnancy</p>\n<p><strong>R</strong>heumatoid arthritis, Psoriasis, Vasculitis</p></div><!---->\n      <!---->\n    </div><!---->'),
(608, 'Pharmacology', 'PM1517. Unique adverse effect profile of Zidovudine', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ul>\n<li>Bone marrow suppression: anemia and granulocytopenia </li>\n<li>Skeletal muscle myopathy </li>\n<li>Nail hyperpigmentation </li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(609, 'Pharmacology', 'PM1353. Classification of antineoplastic drugs', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/fd047e306bdc4310be749978ffaf0c5dx1280x1780.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>&nbsp; &nbsp;Non-cell-cycle specific drugs</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Alkylating agents</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Nitrogen mustards</p>\n<ul>\n<li>Cyclophosphamide</li>\n<li>Ifosfamide</li>\n<li>Mechlorethamine</li>\n<li>Melphalan</li>\n<li>Chlorambucil</li>\n<li>Bendamustine</li>\n</ul>\n<p>Nitrosoureas</p>\n<ul>\n<li>Carmustine</li>\n<li>Lomustine</li>\n<li>Semustine</li>\n<li>Streptozocin</li>\n</ul>\n<p>Triazines (methylating agents)</p>\n<ul>\n<li>Procarbazine</li>\n<li>Dacarbazine</li>\n<li>Temozolomide</li>\n</ul>\n<p>Ethyleneimines</p>\n<ul>\n<li>Altretamine</li>\n<li>Thio-TEPA</li>\n</ul>\n<p>Alkyl sulfonates</p>\n<ul>\n<li>Busulfan</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td><strong>Platinum compounds</strong></td>\n</tr>\n<tr>\n<td>\n<ul>\n<li>Cisplatin</li>\n<li>Carboplatin</li>\n<li>Oxaliplatin&nbsp;</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Anti-tumor antibiotics</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Anthracyclines</p>\n<ul>\n<li>Doxorubicin (adriamycin)</li>\n<li>Daunorubicin (rubidomycin)</li>\n<li>Epirubicin</li>\n<li>Valrubicin</li>\n<li>Mitoxantrone</li>\n</ul>\n<p>Others</p>\n<ul>\n<li>Actinomycin-D (Dactinomycin)</li>\n<li>Mitomycin-C</li>\n<li>Bleomycin (also a G2-M phase inhibitor)</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table>\n<tbody>\n<tr>\n<td><strong> Cell-cycle specific drugs</strong></td>\n</tr>\n<tr>\n<td><strong>S-phase inhibitors</strong></td>\n</tr>\n<tr>\n<td><strong>Antimetabolites</strong></td>\n</tr>\n<tr>\n<td>Antifolate\n<ul>\n<li>Methotrexate</li>\n<li>Pemetrexed</li>\n<li>Pralatrexate</li>\n<li>Raltitrexed</li>\n</ul>\nPurine analogues\n<ul>\n<li>6-Mercaptopurine</li>\n<li>6-Thioguanine</li>\n<li>Fludarabine</li>\n<li>Cladribine</li>\n<li>Pentostatin</li>\n</ul>\nPyrimidine analogues\n<ul>\n<li>5-Fluorouracil</li>\n<li>Capecitabine</li>\n<li>Gemcitabine</li>\n<li>Cytarabine</li>\n</ul>\n&nbsp;</td>\n</tr>\n<tr>\n<td><strong>Topoisomerase inhibitors</strong></td>\n</tr>\n<tr>\n<td>Topoisomerase-1 inhibitors (ca)\n<ul>\n<li>Irinotecan</li>\n<li>Topotecan</li>\n</ul>\nTopoisomerase-2 inhibitors\n<ul>\n<li>Etoposide</li>\n<li>Teniposide</li>\n</ul>\n&nbsp;</td>\n</tr>\n<tr>\n<td><strong>Histone deacetylase inhibitors</strong></td>\n</tr>\n<tr>\n<td>\n<ul>\n<li>Vorinostat</li>\n<li>Belinostat</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td><strong>Others </strong></td>\n</tr>\n<tr>\n<td>\n<ul>\n<li>&nbsp;Hydroxyurea</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td><strong>M-phase inhibitors (antimitotics or microtubule damaging agents)</strong></td>\n</tr>\n<tr>\n<td><strong>Vinca alkaloids</strong></td>\n</tr>\n<tr>\n<td>\n<ul>\n<li>Vincristine(Oncovin)</li>\n<li>Vinblastine</li>\n<li>Vinorelbine</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td><strong>Taxanes</strong></td>\n</tr>\n<tr>\n<td>\n<ul>\n<li>Paclitaxel</li>\n<li>Docetaxel</li>\n<li>Cabazitaxel</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td><strong>Others</strong></td>\n</tr>\n<tr>\n<td>\n<ul>\n<li>Epothilones</li>\n<li>Eribulin</li>\n<li>Estramustine</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td><strong>G1-S phase inhibitor</strong></td>\n</tr>\n<tr>\n<td>\n<ul>\n<li>Epipodophyllotoxin</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(610, 'Pharmacology', 'PM1082. Monoclonal antibodies nomenclature', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Murine - <strong>0%</strong> human: “<strong>omab</strong>” (Tositumomab)</p>\n<p>Chimeric - <strong>65%</strong> human: “<strong>ximab</strong>” (Rituximab)</p>\n<p>Humanized - <strong>95%</strong> human: “<strong>zumab</strong>” (Trastuzumab)</p>\n<p>Fully human - <strong>100%</strong> human: “<strong>umab</strong>” (Adalimumab)</p></div><!---->\n      <!---->\n    </div><!---->'),
(611, 'Medicine,', 'PM1081. Indications for Imatinib', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>BCR-ABL kinase mutations</strong></p>\n<ul>\n<li>Chronic myelogenous leukaemia (CML)</li>\n<li>Acute lymphoblastic leukaemia (ALL)</li>\n</ul>\n<p><strong>C-KIT mutations</strong></p>\n<ul>\n<li>Gastrointestinal stromal tumors (GIST)</li>\n</ul>\n<p><strong>PDGFR mutations</strong></p>\n<ul>\n<li>Dermatofibrosarcoma protuberans</li>\n<li>Hypereosinophilia syndrome</li>\n<li>Chronic myelomonocytic leukaemia (CML)</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(612, 'Pharmacology', 'PM0925. Microtubule stabilising vs polymerisation inhibiting agents', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Mnemonic</p>\n<p><strong>Tax</strong>es stabilise economy</p>\n<ul>\n<li><strong>Tax</strong>anes stabilise microtubules</li>\n</ul>\n<p>V<strong>in</strong>ca alklaoids</p>\n<ul>\n<li>Polymerisation <strong>in</strong>hibiting agents</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(613, 'Pharmacology', 'PM0914. Cell cycle specific and non-specific cytotoxic agents', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Cell cycle specific agents</p>\n<ul>\n<li>Antimetabolites: S phase</li>\n<li>Camptothecin analogs: S Phase</li>\n<li>Taxanes, Vinca alkaloids: M phase</li>\n<li>Epipodophyllotoxin: G1-S phase</li>\n<li>Bleomycin: G2-M phase</li>\n</ul>\n<p>Cell cycle non-specific agents</p>\n<ul>\n<li>Alkylating agents</li>\n<li>Platinum analogs</li>\n<li>Anthracyclines</li>\n<li>Mitomycin, dactinomycin</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(614, 'Pharmacology', 'PM0910. Topoisomerase Inhibitors', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Topoisomerase I inhibitors</p>\n<ul>\n<li>Camptothecin analogs: Irinotecan, topotecan</li>\n</ul>\n<p>Topoisoemrase II inhibitors</p>\n<ul>\n<li>Anthracyclines: doxorubicin, daunorubicin</li>\n<li>Epipodophyllotoxins: etoposide, teniposide</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->');
INSERT INTO db_pearl_m (pid, subject, pearl_title, pearl_desc) VALUES
(615, 'Pharmacology', 'PM2405. FDA approved drugs in 2019', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><div dir=\ltr\ align=\left\>\n<table>\n<tbody>\n<tr>\n<th>\n<p dir=\ltr\><span><strong>Drug</strong></span></p>\n</th>\n<th>\n<p dir=\ltr\><span><strong>Description, mechanism of action and usage</strong></span></p>\n</th>\n</tr>\n<tr>\n<th colspan=\2\>\n<p dir=\ltr\ role=\presentation\><span><strong>I. Autonomic nervous system</strong></span></p>\n</th>\n</tr>\n<tr>\n<td>\n<p><span>1. Prabotulinum toxin A -xvfs</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a <strong>botulinum toxin type A</strong>, which is produced from the fermentation of Clostridium botulinum.</span></p>\n<p dir=\ltr\><span>It blocks the neuromuscular transmission by inhibiting the release of acetylcholine.</span></p>\n<p dir=\ltr\><span>It is indicated for the temporary improvement in the appearance of moderate to severe <strong>glabellar</strong> <strong>lines</strong> associated with corrugator and/or procerus muscle activity in adult patients.</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>2. Solriamfetol</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a <strong>dopamine and norepinephrine reuptake inhibitor</strong> (<strong>DNRI</strong>).</span></p>\n<p dir=\ltr\><span>Used to treat excessive daytime sleepiness in patients with <strong>narcolepsy</strong> or <strong>obstructive sleep apnea</strong> (OSA).</span></p>\n</td>\n</tr>\n<tr>\n<th colspan=\2\>\n<p dir=\ltr\ role=\presentation\><span><strong>II. Central nervous system</strong></span></p>\n</th>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>1. Brexanolone</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a neuroactive steroid gamma-aminobutyric acid (<strong>GABA) A receptor positive modulator,</strong> which is chemically identical to endogenous <strong>allopregnanolone</strong>.</span></p>\n<p dir=\ltr\><span>Used to treat p<strong>ostpartum depression (PPD)</strong> in adult women.</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>2. Lumateperone tosylate</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is an atypical <strong>antipsychotic</strong> indicated for the treatment of <strong>schizophrenia</strong>.</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>3. Lemborexant</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is an <strong>Orexin receptor antagonist</strong> used in the treatment of <strong>insomnia</strong>.</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>4. Siponimod</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a <strong>sphingosine1-phosphate receptor modulator.</strong></span></p>\n<p dir=\ltr\><span>It binds to <strong>S1P receptors</strong> <strong>1</strong> <strong>and 5</strong>, and blocks the capacity of lymphocytes to egress from lymph nodes, thereby, reducing the number of lymphocytes in peripheral blood and may involve reduction of lymphocyte migration into the central nervous system.</span></p>\n<br>\n<p dir=\ltr\><span>It is indicated for the treatment of relapsing forms of <strong>multiple</strong> <strong>sclerosis</strong> (MS).</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>5. Istradefylline</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is an <strong>adenosine receptor antagonist,</strong> which has a xanthine derivative structure.&nbsp;</span></p>\n<br>\n<p dir=\ltr\><span>It is indicated as adjunctive treatment to levodopa/carbidopa in adult patients with <strong>Parkinson’s</strong> <strong>disease</strong> (PD) experiencing “off” episodes.</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>6. Cenobamate</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a <strong>carbamate</strong> derivative used in the treatment of<strong> partial-onset seizures</strong> in adult patients.</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>7. Ubrogepant</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a <strong>calcitonin gene-related peptide (CGRP) receptor antagonist.&nbsp;</strong></span></p>\n<p dir=\ltr\><span>Used for acute treatment of <strong>migraine</strong> with or without aura.</span></p>\n</td>\n</tr>\n<tr>\n<th colspan=\2\>\n<p dir=\ltr\ role=\presentation\><span><strong>III. Cardiovascular system</strong></span></p>\n</th>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>1. Caplacizumab -yhdp</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a <strong>von Willebrand factor (vWF)-directed antibody fragment.&nbsp;</strong></span></p>\n<p dir=\ltr\><span>It targets the A1 domain of vWF, and inhibits the interaction between vWF and platelets, thereby reducing both vWF-mediated platelet adhesion and platelet consumption.</span></p>\n<p dir=\ltr\><span>It is indicated for the treatment of adult patients with <strong>acquired</strong> <strong>thrombotic</strong> <strong>thrombocytopenic</strong> <strong>purpura</strong> (<strong>aTTP</strong>), in combination with plasma exchange and immunosuppressive therapy.</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>2. Tafamidis meglumine</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a selective <strong>stabilizer of transthyretin (TTR).</strong></span></p>\n<p dir=\ltr\><span>It binds to the TTR at the thyroxine-binding sites, thereby stabilizing the tetramer and slowing dissociation into monomers, which is the rate-limiting step in amyloidogenic process.</span></p>\n<p dir=\ltr\><span>Used for the treatment of the <strong>cardiomyopathy</strong> of wild type or hereditary transthyretin-mediated amyloidosis (ATTR-CM) in adults to reduce cardiovascular-related mortality or hospitalization.</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>3. Ferric maltol</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is an orally administered, <strong>iron</strong> <strong>replacement product</strong> containing 30 mg of iron in a stable ferric state and 201.5 mg of maltol in the form of trimaltol ligand.</span></p>\n<p dir=\ltr\><span>Used for the treatment of <strong>iron deficiency anemia.</strong></span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>4. Luspatercept –&nbsp;aamt</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is an <strong>erythroid maturation agent.&nbsp;</strong></span></p>\n<p dir=\ltr\><span>It binds several endogenous TGF-β superfamily ligands, thereby diminishing Smad2/3 signaling and thereby promoting maturation and differentiation of erythroid precursors.</span></p>\n<p dir=\ltr\><span>It is indicated for the treatment of anemia in adult patients with <strong>beta thalassemia</strong> who require regular red blood cell (RBC) transfusions.</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>5. Crizanlizumab-tmca</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a <strong>selectin blocker</strong> humanized IgG2 kappa monoclonal antibody that binds to P-selectin and blocks interactions with its ligands including P-selectin glycoprotein ligand 1.</span></p>\n<p dir=\ltr\><span>It is indicated to reduce the frequency of vasoocclusive crises in patients with <strong>sickle cell disease.</strong></span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>6. Voxelotor</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a <strong>hemoglobin S polymerization inhibitor.</strong></span></p>\n<p dir=\ltr\><span>It binds to HbS and increases the affinity of Hb for oxygen and thereby dose-dependent inhibition of HbS polymerization.&nbsp;</span></p>\n<p dir=\ltr\><span>Used to treat <strong>sickle cell disease.</strong>&nbsp;</span></p>\n</td>\n</tr>\n<tr>\n<th colspan=\2\>\n<p dir=\ltr\ role=\presentation\><span><strong>IV. Respiratory system</strong></span></p>\n</th>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>Combination of elexacaftor/ ivacaftor/ tezacaftor</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a combination of <strong>ivacaftor</strong>, a CFTR potentiator, <strong>tezacaftor,</strong> and <strong>elexacaftor</strong> indicated for the treatment of <strong>cystic</strong> <strong>fibrosis</strong> (CF) in patients aged 12 years and older who have at least one F508 del mutation in the CFTR gene.</span></p>\n</td>\n</tr>\n<tr>\n<th colspan=\2\>\n<p dir=\ltr\ role=\presentation\><span><strong>V. Gastrointestinal system</strong></span></p>\n</th>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>Tenapanor</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a <strong>sodium/hydrogen exchanger 3 (NHE3) inhibitor.</strong></span></p>\n<p dir=\ltr\><span>It causes local inhibition of NHE3, which is an antiporter expressed on the apical surface of the small intestine and colon primarily responsible for the absorption of dietary sodium. As a result, it reduces the sodium absorption from these sites, and thereby causes an increase in water secretion into the lumen.&nbsp;</span></p>\n<p dir=\ltr\><span>Used to treat <strong>irritable</strong> <strong>bowel</strong> <strong>syndrome with constipation (IBS-C).</strong></span></p>\n</td>\n</tr>\n<tr>\n<th colspan=\2\>\n<p dir=\ltr\ role=\presentation\><span><strong>VI. Endocrine system</strong></span></p>\n</th>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>Romosozumab-&nbsp;aqqg</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a <strong>humanized monoclonal antibody (IgG2)</strong> produced in a mammalian cell line (Chinese Hamster Ovary) by recombinant DNA technology</span></p>\n<p dir=\ltr\><span>It binds to and inhibits the action of sclerostin(a regulatory protein in bone metabolism), and thereby increases bone formation and reduces bone resorption.</span></p>\n<p dir=\ltr\><span>Used to treat <strong>osteoporosis in postmenopausal women</strong> at high risk of fracture.&nbsp;</span></p>\n</td>\n</tr>\n<tr>\n<th colspan=\2\>\n<p dir=\ltr\ role=\presentation\><span><strong>VII. Autacoids</strong></span></p>\n</th>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>1. Pitolisant</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is an <strong>antagonist/inverse agonist</strong> of the <strong>histamine-3 (H3) receptor.&nbsp;</strong></span></p>\n<p dir=\ltr\><span>Used to treat excessive daytime sleepiness (EDS) in adult patients with <strong>narcolepsy</strong>.</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>2. Lasmiditan</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a <strong>serotonin (5-HT) 1F receptor agonist.</strong></span></p>\n<p dir=\ltr\><span>It is used for the acute treatment of <strong>migraine</strong> with or without aura in adults.&nbsp;</span></p>\n</td>\n</tr>\n<tr>\n<th colspan=\2\>\n<p dir=\ltr\ role=\presentation\><span><strong>VIII. Antimicrobials</strong></span></p>\n</th>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>1. Triclabendazole</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is an <strong>antihelminthic</strong> <strong>drug</strong> that acts against the fasciola species.</span></p>\n<p dir=\ltr\><span>Used to treat <strong>fascioliasis</strong> in patients 6 years of age and older.</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>2. Combination of imipenem, cilastatin, and relebactam</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a combination of <strong>imipenem</strong>, a penem antibacterial, <strong>cilastatin</strong>, a renal dehydropeptidase inhibitor, and <strong>relebactam</strong>, a beta­ lactamase inhibitor.</span></p>\n<p dir=\ltr\><span>It is approved for use in the treatment of <strong>complicated urinary tract infections</strong> and <strong>complicated abdominal infections</strong> caused by susceptible gram-negative bacteria.</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>3. Cefiderocol</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a <strong>cephalosporin</strong> antibacterial drug with activity against Gram-negative aerobic bacteria.&nbsp;</span></p>\n<p dir=\ltr\><span>It exerts <strong>bactericidal</strong> action by inhibiting cell wall biosynthesis through binding to penicillin-binding proteins (PBPs).</span></p>\n<p dir=\ltr\><span>It is indicated for the treatment of patients with <strong>complicated urinary tract infections</strong> who have limited or no alternative treatment options.</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>4. Pretomanid</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is an oral <strong>nitroimidazooxazine</strong> antimycobacterial agent.</span></p>\n<p dir=\ltr\><span>It is used in combination with bedaquiline and linezolid for the treatment of adults with pulmonary extensively drug-resistant (<strong>XDR</strong>), treatment-intolerant or nonresponsive multidrug-resistant (<strong>MDR</strong>) <strong>tuberculosis (TB).&nbsp;</strong></span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>5. Lefamulin</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a semi-synthetic <strong>pleuromutiilin</strong> antibacterial agent.</span></p>\n<p dir=\ltr\><span>It inhibits bacterial protein synthesis by interacting with the A- and P-sites of the peptidyl transferase center (PTC) in domain V of the 23s rRNA of the 50S subunit.&nbsp;</span></p>\n<p dir=\ltr\><span>In vitro, it is <strong>bactericidal</strong> against S. pneumoniae, H. influenzae and M. pneumoniae (including macrolide-resistant strains), and <strong>bacteriostatic</strong> against S. aureus and S. pyogenes.</span></p>\n<p dir=\ltr\><span>Used in the treatment of <strong>community-acquired bacterial pneumonia (CABP).&nbsp;</strong></span></p>\n</td>\n</tr>\n<tr>\n<th colspan=\2\>\n<p dir=\ltr\ role=\presentation\><span><strong>IX. Anticancer drugs</strong></span></p>\n</th>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>1. Erdafitinib</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a <strong>kinase inhibitor.</strong></span></p>\n<p dir=\ltr\><span>Used for the treatment of locally advanced or metastatic <strong>urothelial</strong> <strong>carcinoma</strong>, that has:</span></p>\n<ul>\n<li dir=\ltr\><span>&nbsp;susceptible FGFR3 or FGFR2 genetic alterations and</span></li>\n<li dir=\ltr\><span>progressed during or following at least one line of prior platinum-containing chemotherapy including within 12 months of neoadjuvant or adjuvant platinum-containing chemotherapy.</span></li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>2. Enfortumab vedotin-ejfv</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a <strong>Nectin-4-directed antibody</strong> and <strong>microtubule</strong> <strong>inhibitor</strong> <strong>conjugate</strong>.</span></p>\n<p dir=\ltr\><span>It is&nbsp; indicated for the treatment of adult patients with locally advanced or metastatic <strong>urothelial cancer</strong> who have:</span></p>\n<ul>\n<li><span>previously received a programmed death receptor-1 (PD-1) or programmed death-ligand 1 (PD-L1) inhibitor, and&nbsp;</span></li>\n<li><span>a platinum-containing chemotherapy in the neoadjuvant/adjuvant, locally advanced or metastatic setting.</span></li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>3. Darolutamide</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is an <strong>androgen receptor (AR) inhibitor.</strong></span></p>\n<p dir=\ltr\><span>It acts by competitively inhibiting of androgen binding, AR nuclear translocation, and AR-mediated transcription.</span></p>\n<p dir=\ltr\><span>Indicated for the treatment of patients with non-metastatic <strong>castration-resistant prostate cancer.</strong></span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>4. Alpelisib</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a <strong>kinase</strong> <strong>inhibitor</strong> acting against <strong>phosphatidylinositol-3-kinase</strong> (PI3K), mainly <strong>PI3Kα</strong>.</span></p>\n<p dir=\ltr\><span>It is used in combination with fulvestrant for the treatment of <strong>breast cancer.</strong></span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>5. Fam-trastuzumab deruxtecan-nxki</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a <strong>HER2-directed antibody</strong> and <strong>topoisomerase</strong> <strong>inhibitor</strong> <strong>conjugate</strong>.</span></p>\n<p dir=\ltr\><span>It is used in the treatment of adult patients with unresectable or metastatic HER2-positive <strong>breast cancer</strong> who have received two or more prior anti-HER2-based regimens in the metastatic setting.&nbsp;</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>6. Selinexor</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a <strong>nuclear export inhibitor.</strong></span></p>\n<p dir=\ltr\><span>It blocks <strong>exportin 1</strong> (<strong>XPO1</strong>), and thereby reversibly inhibits nuclear export of tumor suppressor proteins (TSPs), and oncoproteins, such as c‐myc and cyclin D1. This leads to accumulation of these substances causing cell cycle arrest, and apoptosis of cancer cells.&nbsp;</span></p>\n<p dir=\ltr\><span>It is used to treat&nbsp; patients with relapsed or refractory <strong>multiple</strong> <strong>myeloma</strong> (RRMM)</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>7. Polatuzumab vedotin-piiq</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a <strong>CD79b-directed antibody–drug conjugate.</strong> It consists of three components:&nbsp;</span></p>\n<ul>\n<li dir=\ltr\><span>IgG1 monoclonal antibody</span></li>\n<li dir=\ltr\><span>Anti-mitotic agent called MMAE (monomethyl auristatin E), and</span></li>\n<li dir=\ltr\><span>A protein cleavable linker called mc-vc-PAB</span></li>\n</ul>\n<p dir=\ltr\><span>The IgG1 component binds to the binds to CD79b, a B-cell specific surface protein and gets internalized into the cell. The lysosomal enzymes then cleaves the linker mc-vc-PAB, causing release of MMAE. The MMAE then binds to the microtubules and kills dividing cells by inhibiting cell division and inducing apoptosis.</span></p>\n<p dir=\ltr\><span>It is used in combination with bendamustine and rituximab product for the treatment of&nbsp; relapsed or refractory <strong>diffuse large B-cell lymphoma.&nbsp;</strong></span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>8. Zanubrutinib</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is <strong>kinase inhibitor</strong> acting against <strong>Bruton’s tyrosine kinase (BTK).</strong></span></p>\n<p dir=\ltr\><span>BTK is a signaling molecule on B-cell, and plays a role in B-cell proliferation, trafficking, chemotaxis, and adhesion. Inhibition of BTK activity leads to inhibited malignant B-cell proliferation and reduced tumor growth.</span></p>\n<p dir=\ltr\><span>It is used for the treatment of adult patients with <strong>mantle cell lymphoma (MCL)</strong> who have received at least one prior therapy.</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>9. Fedratinib</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a <strong>kinase</strong> <strong>inhibitor</strong> that acts against wild type and mutationally activated <strong>Janus Associated Kinase 2 (JAK2)</strong> and <strong>FMS-like tyrosine kinase 3 (FLT3).&nbsp;</strong></span></p>\n<br>\n<p dir=\ltr\><span>It is used to treat intermediate-2 or high-risk primary or secondary (post-polycythemia vera or post-essential thrombocythemia) <strong>myelofibrosis (MF).</strong></span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>10. Pexidartinib</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a kinase inhibitor that mainly targets the following:</span></p>\n<ul>\n<li dir=\ltr\><span>Colony stimulating factor 1 receptor (<strong>CSF1R</strong>)</span></li>\n<li dir=\ltr\><span>KIT proto-oncogene receptor tyrosine kinase (<strong>KIT</strong>), and</span></li>\n<li dir=\ltr\><span>FMS-like tyrosine kinase 3 (<strong>FLT3</strong>) harboring an internal tandem duplication (<strong>ITD</strong>) mutation.</span></li>\n</ul>\n<p dir=\ltr\><span>Used for the treatment of symptomatic <strong>tenosynovial giant cell tumor (TGCT)</strong> associated with severe morbidity or functional limitations and not amenable to improvement with surgery.</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>11. Entrectinib</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a <strong>kinase</strong> <strong>inhibitor</strong> that acts against tropomyosin receptor tyrosine kinases (<strong>TRK</strong>), proto-oncogene tyrosine-protein kinase&nbsp;(<strong>ROS1</strong>), and anaplastic lymphoma kinase (<strong>ALK</strong>).</span></p>\n<p dir=\ltr\><span>It is used in the treatment of:</span></p>\n<ul>\n<li dir=\ltr\><span>Adult patients with metastatic <strong>non-small cell lung cancer (NSCLC)</strong> whose tumors are <strong>ROS1-positive.</strong></span></li>\n<li dir=\ltr\><span>Adult and pediatric patients 12 years of age and older with <strong>solid</strong> <strong>tumors</strong>.</span></li>\n</ul>\n</td>\n</tr>\n<tr>\n<th colspan=\2\>\n<p dir=\ltr\ role=\presentation\><span><strong>X. Dermatological Drugs</strong></span></p>\n</th>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>1. Risankizumab-rzaa</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a humanized i<strong>mmunoglobulin G1 (IgG1) monoclonal antibody.</strong></span></p>\n<p dir=\ltr\><span>It binds to the P19 subunit of IL-23 cytokine and inhibits its interaction with IL-23 receptor. IL-23 is involved in inflammatory and immune responses.</span></p>\n<p dir=\ltr\><span>It is indicated for the treatment of moderate-to-severe <strong>plaque psoriasis</strong> in adults who are candidates for systemic therapy or phototherapy.</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>2. Trifarotene</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a <strong>retinoid</strong> used for the topical treatment of <strong>acne vulgaris</strong> in patients 9 years of age and older.&nbsp;</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>3. Afamelanotide</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a <strong>melanocortin receptor agonist</strong> and binds predominantly to <strong>MC1-R.</strong></span></p>\n<p dir=\ltr\><span>It is used to increase pain-free light exposure in adult patients with a history of phototoxic reactions from <strong>erythropoietic protoporphyria (EPP).</strong></span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>4. Givosiran</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a double-stranded <strong>small interfering RNA (siRNA)</strong> that acts against the aminolevulinate synthase 1 (<strong>ALAS1</strong>) mRNA in hepatocytes.&nbsp;</span></p>\n<p dir=\ltr\><span>The siRNA is covalently bound to N-acetylgalactosamine (<strong>GalNAc</strong>) residues, which enables the delivery of siRNA to hepatocytes. The siRNA through RNA interference causes degradation of ALAS1 mRNA and reduces its levels in the liver, thereby reducing the circulating levels of neurotoxic intermediates aminolevulinic acid (ALA) and porphobilinogen (PBG).</span></p>\n<p dir=\ltr\><span>It is used for the treatment of adults with <strong>acute hepatic porphyria (AHP).&nbsp;</strong></span></p>\n</td>\n</tr>\n<tr>\n<th colspan=\2\>\n<p dir=\ltr\ role=\presentation\><span><strong>XI. Dyes and radioactive agents</strong></span></p>\n</th>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>1. Brilliant Blue G Ophthalmic Solution</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is an <strong>ophthalmic solution</strong> containing<strong> brilliant blue G (BBG)</strong> <strong>dye</strong> that selectively stains the <strong>internal limiting membrane (ILM)</strong>, which makes it easier to visualize the membrane for removal.</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>2. Air polymer-type A</span></p>\n</td>\n<td>\n<p dir=\ltr\><span><strong>Air polymer-type A</strong> intrauterine foam, is an <strong>ultrasound contrast agent.</strong></span></p>\n<p dir=\ltr\><span>It is formed by mixing the clear Gel with air and sterile purified water, creating an echogenic contrast agent. When visualized with ultrasound, the foam appears echogenic or bright within the fallopian tubes and peritoneal cavity.</span></p>\n<p dir=\ltr\><span>It is indicated for <strong>sono hysterosalpingography</strong> to assess <strong>fallopian tube patency</strong> in women with known or suspected infertility.</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>3. Fluorodopa F-18</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a radioactive diagnostic agent used in <strong>PET imaging</strong> to visualize <strong>dopaminergic</strong> <strong>nerve</strong> <strong>terminals</strong> in the <strong>striatum</strong> for the evaluation of adult patients with suspected <strong>Parkinsonian syndromes (PS)</strong>.</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>4. Ga-68-DOTATOC</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a radioactive diagnostic agent that consists of <strong>Gallium-68</strong> and <strong>octreotide</strong>. It specifically binds to somatostatin receptors of subtype 2 (sstr2).</span></p>\n<p dir=\ltr\><span>It is indicated for localization of <strong>somatostatin receptor-positive neuroendocrine tumors (NETs).</strong></span></p>\n</td>\n</tr>\n<tr>\n<th colspan=\2\>\n<p dir=\ltr\ role=\presentation\><span><strong>XII. Other drugs</strong></span></p>\n</th>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>1. Upadacitinib</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a is a<strong> Janus kinase (JAK) inhibitor.</strong></span></p>\n<p dir=\ltr\><span>It is used in the treatment of patients with moderately to severely active <strong>rheumatoid arthritis</strong> who have had an inadequate response or intolerance to methotrexate.</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>2. Golodirsen</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is an a<strong>ntisense oligonucleotide</strong> that binds to exon 53 of dystrophin pre-mRNA.</span></p>\n<p dir=\ltr\><span>It is used in the treatment of patients with <strong>Duchenne muscular dystrophy (DMD)</strong> who have a confirmed mutation of the DMD gene that is amenable to exon 53 skipping.&nbsp;</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>3. Brolucizumab–dbll</span></p>\n</td>\n<td>\n<p dir=\ltr\><span>It is a recombinant human vascular endothelial growth factor <strong>(VEGF) inhibitor.</strong></span></p>\n<p dir=\ltr\><span>It binds to VEGF-A and inhibits the interaction with VEGFR-1 and VEGFR-2 and thereby suppresses endothelial cell proliferation, neovascularization, and vascular permeability.</span></p>\n<p dir=\ltr\><span>Used for the treatment of <strong>Neovascular (Wet) Age-Related Macular Degeneration (AMD)</strong></span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p dir=\ltr\><span>4. Bremelanotide</span></p>\n<p dir=\ltr\><span>&nbsp;</span></p>\n</td>\n<td><span>&nbsp;</span>\n<p dir=\ltr\><span>It is a <strong>melanocortin receptor agonist.</strong></span></p>\n<p dir=\ltr\><span>It is used in the treatment of <strong>generalized hypoactive sexual desire disorder (HSDD)</strong>, which is not due to existing medical or psychiatric conditions, relationship issues, or drug abuse.</span></p>\n</td>\n</tr>\n</tbody>\n</table>\n</div>\n<p><span>&nbsp;</span></p></div><!---->\n      <!---->\n    </div><!---->'),
(616, 'Pharmacology', 'PM1787. FDA approved drugs in 2017', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>The following is the list of drugs approved by FDA in 2017, organized into specialities based on their indication.</p>\n<table border=\1\>\n<tbody>\n<tr>\n<td><strong>Drug</strong></td>\n<td><strong>Indication</strong></td>\n</tr>\n<tr>\n<td><strong>A. Cardiology/Vascular Diseases</strong></td>\n</tr>\n<tr>\n<td>1.&nbsp;&nbsp;&nbsp; Betrixaban</td>\n<td>It is a Factor Xa (FXa) inhibitor used or the prophylaxis of <strong>venous thromboembolism (VTE)</strong> in adult patients hospitalized for an acute medical illness</td>\n</tr>\n<tr>\n<td><strong>B. Hematology</strong></td>\n</tr>\n<tr>\n<td>2.&nbsp;&nbsp;&nbsp; Copanlisib</td>\n<td>Kinase inhibitor used to treat adults with relapsed <strong>follicular lymphoma</strong> who have received at least two prior systemic therapies</td>\n</tr>\n<tr>\n<td>3.&nbsp;&nbsp;&nbsp; Inotuzumab ozogamicin</td>\n<td>CD22-directed antibody-drug conjugate (ADC) to treat adults with relapsed or refractory B-cell precursor <strong>Acute</strong> <strong>Lymphoblastic leukemia (ALL).</strong></td>\n</tr>\n<tr>\n<td>4.&nbsp;&nbsp;&nbsp; Acalabrutinib</td>\n<td>Kinase inhibitor used to treat adults with <strong>mantle cell lymphoma (MCL)</strong> who have received at least one prior therapy</td>\n</tr>\n<tr>\n<td>5.&nbsp;&nbsp;&nbsp; L-glutamine oral powder</td>\n<td>An N amino acid used to reduce the acute complications of <strong>sickle cell disease</strong> in adult and pediatric patients 5 years of age and older.</td>\n</tr>\n<tr>\n<td>6.&nbsp;&nbsp;&nbsp; Emicizumab-kxwh</td>\n<td>Bispecific factor IXa- and factor X-directed antibody used for routine prophylaxis to prevent or reduce the frequency of bleeding episodes in adult and pediatric patients with <strong>Hemophilia A</strong> (congenital factor VIII deficiency) with factor VIII inhibitors</td>\n</tr>\n<tr>\n<td>7.&nbsp;&nbsp;&nbsp; Enasidenib</td>\n<td>Isocitrate dehydrogenase-2 inhibitor used to treat relapsed or refractory <strong>acute myeloid leukemia</strong> <strong>(AML) </strong>with an isocitrate dehydrogenase-2 (IDH2) mutation as detected by an FDA approved test.</td>\n</tr>\n<tr>\n<td>8.&nbsp;&nbsp;&nbsp; Midostaurin</td>\n<td>Kinase inhibitor used in newly diagnosed cases of <strong>acute myeloid leukemia (AML)</strong> that is FLT3 mutation positive as detected by an FDA-approved test, in combination with standard cytarabine and daunorubicin induction and cytarabine consolidation.&nbsp;Also used in aggressive systemic mastocytosis (ASM), systemic mastocytosis with associated hematological neoplasm (SM-AHN), or mast cell leukemia (MCL).&nbsp;</td>\n</tr>\n<tr>\n<td><strong>C. Gastroenterology</strong></td>\n</tr>\n<tr>\n<td>9.&nbsp;&nbsp;&nbsp; Naldemedine</td>\n<td>An opioid antagonist indicated for the treatment of <strong>opioid-induced constipation</strong></td>\n</tr>\n<tr>\n<td>10.Plecanatide</td>\n<td>Guanylate cyclase-C agonist to treat <strong>Chronic Idiopathic Constipation (CIC)</strong> in adult patients.</td>\n</tr>\n<tr>\n<td>11.Telotristat ethyl</td>\n<td>Tryptophan hydroxylase inhibitor, to treat <strong>carcinoid syndrome diarrhea</strong></td>\n</tr>\n<tr>\n<td><strong>D. Endocrinology</strong></td>\n</tr>\n<tr>\n<td>12.Semaglutide</td>\n<td>Glucagon-like peptide 1 (GLP-1) receptor agonist indicated to improve glycemic control in adults with <strong>type 2 diabetes mellitus</strong></td>\n</tr>\n<tr>\n<td>13.Dapagliflozin and saxagliptin</td>\n<td>Sodium-glucose cotransporter 2 (SGLT-2) inhibitor and a dipeptidyl peptidase-4 (DPP-4) inhibitor combination, indicated as an adjunct to diet and exercise to improve glycemic control in adults with <strong>type 2 Diabetes mellitus</strong> (T2DM) who have inadequate control with dapagliflozin or who are already treated with dapagliflozin and saxagliptin.</td>\n</tr>\n<tr>\n<td><strong>E. Hepatology</strong></td>\n</tr>\n<tr>\n<td>14.Hepatitis B vaccine (recombinant), Adjuvanted</td>\n<td>Indicated for prevention of infection caused by all known subtypes of <strong>hepatitis b virus</strong>. It isApproved for use in adults 18 years of age and older.</td>\n</tr>\n<tr>\n<td>15.Glecaprevir and Pibrentasvir</td>\n<td>Fixed-dose combination of glecaprevir, a hepatitis C virus (HCV) NS3/4A protease inhibitor, and pibrentasvir, an HCV NS5A inhibitor indicated for the treatment of patients with <strong>chronic HCV</strong> genotype (GT) 1, 2, 3, 4, 5 or 6 infection without cirrhosis and with compensated cirrhosis (Child-Pugh A)</td>\n</tr>\n<tr>\n<td>16.Sofosbuvir, Velpatasvir and Voxilaprevir</td>\n<td>Fixed-dose combination of sofosbuvir, a hepatitis C virus (HCV) nucleotide analog NS5B polymerase inhibitor, velpatasvir, an HCV NS5A inhibitor, and voxilaprevir, an HCV NS3/4A protease inhibitor indicated for the treatment of adult patients with <strong>chronic HCV</strong> infection without cirrhosis or with compensated cirrhosis (Child-Pugh A) who have (1, 2.2, 14).</td>\n</tr>\n<tr>\n<td><strong>F. Nephrology</strong></td>\n</tr>\n<tr>\n<td>17.Etelcalcetide</td>\n<td>Calcium-sensing receptor agonist to treat <strong>secondary hyperparathyroidism</strong> in adult patients with <strong>chronic kidney disease</strong> undergoing dialysis</td>\n</tr>\n<tr>\n<td>18.Meropenem and Vaborbactam</td>\n<td>Combination of meropenem, a penem antibacterial, and vaborbactam, a beta-lactamase inhibitor to treat adults with <strong>complicated urinary tract infections</strong> including <strong>pyelonephritis</strong> caused by designated susceptible bacteria</td>\n</tr>\n<tr>\n<td><strong>G. Neurology</strong></td>\n</tr>\n<tr>\n<td>19.Deutetrabenazine</td>\n<td>Vesicular monoamine transporter 2 (VMAT2) inhibitor for the treatment of <strong>chorea</strong> associated with <strong>Huntington’s disease</strong> and <strong>tardive dyskinesia</strong> in adults</td>\n</tr>\n<tr>\n<td>20.Valbenazine</td>\n<td>Vesicular monoamine transporter 2 (VMAT2) inhibitor to treat adults with <strong>tardive dyskinesia</strong></td>\n</tr>\n<tr>\n<td>21.Edaravone</td>\n<td>Member of the substituted 2-pyrazolin-5-oneclass used to treat patients with <strong>amyotrophic lateral sclerosis (ALS)</strong></td>\n</tr>\n<tr>\n<td>22.Safinamide</td>\n<td>Monoamine oxidase type B (MAO-B) inhibitor indicated as an adjunctive treatment to levodopa/carbidopa in patients with <strong>Parkinson’s Disease (PD)</strong> experiencing “off” episodes</td>\n</tr>\n<tr>\n<td><strong>H. Rheumatology</strong></td>\n</tr>\n<tr>\n<td>23.Sarilumab</td>\n<td>Interleukin-6 (IL-6) receptor antagonist to treat adult <strong>rheumatoid arthritis</strong></td>\n</tr>\n<tr>\n<td>24.Ocrelizumab</td>\n<td>CD20-directed cytolytic antibody to treat patients with relapsing and primary progressive forms of <strong>multiple sclerosis</strong></td>\n</tr>\n<tr>\n<td><strong>I. Oncology</strong></td>\n</tr>\n<tr>\n<td>25.Abemaciclib</td>\n<td>Kinase inhibitor to treat certain advanced or <strong>metastatic breast cancers</strong></td>\n</tr>\n<tr>\n<td>26.Neratinib maleate</td>\n<td>Kinase inhibitor indicated for extended adjuvant treatment of adult patients with early stage HER2-overexpressed/amplified <strong>breast cancer.</strong></td>\n</tr>\n<tr>\n<td>27.Ribociclib</td>\n<td>Kinase inhibitor used to treat postmenopausal women with hormone receptor (HR)-positive, human epidermal growth factor receptor 2 (HER2)-negative advanced or metastatic <strong>breast cancer</strong>.</td>\n</tr>\n<tr>\n<td>28.Durvalumab</td>\n<td>Programmed death-ligand 1 (PD-L1) blocking antibody to treat patients with locally advanced or metastatic <strong>urothelial carcinoma.</strong></td>\n</tr>\n<tr>\n<td>29.Brigatinib</td>\n<td>Kinase inhibitor to treat patients with anaplastic lymphoma kinase (ALK)-positive metastatic <strong>Non-small cell lung cancer (NSCLC)</strong> who have progressed on or are intolerant to crizotinib.</td>\n</tr>\n<tr>\n<td>30.Niraparib <strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></td>\n<td>Poly (ADP-ribose) polymerase (PARP) inhibitor indicated for the maintenance treatment for <strong>recurrent epithelial ovarian, fallopian tube or primary peritoneal cancers </strong>who are in a complete or partial response to platinum-based chemotherapy.</td>\n</tr>\n<tr>\n<td><strong>J. Pulmonology</strong></td>\n</tr>\n<tr>\n<td>31.Benralizumab</td>\n<td>Interleukin-5 receptor alpha-directed cytolytic monoclonal antibody (IgG1, kappa) for add-on maintenance treatment of patients with <strong>severe asthma</strong> aged 12 years and older, and with an eosinophilic phenotype.</td>\n</tr>\n<tr>\n<td><strong>K. Internal medicine</strong></td>\n</tr>\n<tr>\n<td>32.Letermovir</td>\n<td>CMV DNA terminase complex inhibitor indicated for the prevention of <strong>cytomegalovirus</strong> <strong>infection</strong> following allogenic <strong>hematopoietic stem cell transplant.</strong></td>\n</tr>\n<tr>\n<td><strong>L. Dermatology</strong></td>\n</tr>\n<tr>\n<td>33.Avelumab</td>\n<td>Programmed death ligand-1 (PD-L1) blocking antibody to treat metastatic <strong>Merkel cell carcinoma</strong></td>\n</tr>\n<tr>\n<td>34.Delafloxacin</td>\n<td>Fluoroquinolone antibacterial to treat patients with <strong>acute</strong> <strong>bacterial skin infections</strong></td>\n</tr>\n<tr>\n<td>35.Dupilumab</td>\n<td>Interleukin-4 receptor alpha antagonist to treat adults with moderate-to-severe eczema (<strong>atopic</strong> <strong>dermatitis</strong>)</td>\n</tr>\n<tr>\n<td>36.Brodalumab</td>\n<td>Human interleukin-17 receptor A (IL-17RA) antagonist to treat adults with moderate-to-severe <strong>plaque psoriasis</strong></td>\n</tr>\n<tr>\n<td>37.Guselkumab</td>\n<td>Interleukin-23 blocker for the treatment of adult patients with moderate-to-severe <strong>plaque psoriasis</strong></td>\n</tr>\n<tr>\n<td>38.Ozenoxacin</td>\n<td>A quinolone antimicrobial indicated to treat <strong>impetigo</strong></td>\n</tr>\n<tr>\n<td><strong>M. Pediatrics</strong></td>\n</tr>\n<tr>\n<td>39.Vestronidase alfavjbk</td>\n<td>Recombinant human lysosomal beta glucuronidase indicated to treat pediatric and adult patients with an inherited metabolic condition called <strong>mucopolysaccharidosis type VII (MPS VII),</strong> also known as <strong>sly syndrome.</strong></td>\n</tr>\n<tr>\n<td>40.Benznidazole</td>\n<td>Nitroimidazole antimicrobial indicated to treat children ages 2 to 12 years old with <strong>chagas</strong> <strong>disease</strong></td>\n</tr>\n<tr>\n<td>41.Deflazacort</td>\n<td>Corticosteroid used to treat patients age 5 years and older with <strong>duchenne muscular dystrophy</strong> (<strong>DMD</strong>)</td>\n</tr>\n<tr>\n<td>42.Cerliponase alfa</td>\n<td>Hydrolytic lysosomal N-terminal tripeptidyl peptidase indicated to slow the loss of ambulation in symptomatic pediatric patients 3 years of age and older with late <strong>infantile neuronal ceroid lipofuscinosis type 2 (CLN2</strong>), also known as tripeptidyl peptidase 1 (TPP1) deficiency.</td>\n</tr>\n<tr>\n<td><strong>N. Obstetrics &amp; Gynecology</strong></td>\n</tr>\n<tr>\n<td>43.Secnidazole</td>\n<td>Nitroimidazole antimicrobial used for the treatment of <strong>bacterial vaginosis</strong></td>\n</tr>\n<tr>\n<td>44.Abaloparatide</td>\n<td>Human parathyroid hormone related peptide [PTHrP(1-34)] analog for the treatment of <strong>osteoporosis</strong> <strong>in postmenopausal women</strong> at high risk of fracture or those who have failed other therapies.</td>\n</tr>\n<tr>\n<td><strong>O. Ophthalmology</strong></td>\n</tr>\n<tr>\n<td>45.Latanoprostene Bunod ophthalmic solution</td>\n<td>Prostaglandin analog to treat <strong>intraocular pressure</strong> in patients with <strong>open-angle glaucoma or ocular hypertension.</strong></td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(617, 'Dermatology,', 'PM0296. Treatment Ladder for Psoriasis', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Treatment Ladder for Psoriasis</strong></p>\n<p><strong>I. In Mild Psoriasis Without Arthritis</strong></p>\n<p><strong>First line</strong></p>\n<ul>\n<li>Coal tar</li>\n<li>Dithranol</li>\n<li>Potent topical steroid</li>\n<li>Vitamin D analogue</li>\n</ul>\n<p><strong>Second line</strong></p>\n<ul>\n<li>Local NB-UVB or PUVA</li>\n<li>Excimer laser</li>\n</ul>\n<p><strong>II. In Moderate or Severe Psoriasis Without Arthritis</strong></p>\n<p><strong>First line</strong></p>\n<ul>\n<li>NB-UVB or PUVA</li>\n</ul>\n<p><strong>Second line</strong></p>\n<ul>\n<li>Acitretin</li>\n<li>Apremilast</li>\n<li>Ciclosporin</li>\n<li>Fumaric acid esters (where available)</li>\n<li>Methotrexate</li>\n</ul>\n<p><strong>Third line</strong></p>\n<ul>\n<li>Adalimumab</li>\n<li>Etanercept</li>\n<li>Infliximab</li>\n<li>Secukinumab</li>\n<li>Ustekinumab</li>\n</ul>\n<p><strong>III. In Moderate or Severe Psoriasis with Psoriatic Arthritis</strong></p>\n<p><strong>First line</strong></p>\n<ul>\n<li>Apremilast</li>\n<li>Methotrexate</li>\n</ul>\n<p><strong>Second line</strong></p>\n<ul>\n<li>Adalimumab</li>\n<li>Etanercept</li>\n<li>Infliximab</li>\n<li>Ustekinumab</li>\n</ul>\n<p><strong>Third line</strong></p>\n<ul>\n<li>Combination therapy</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(618, 'Obstetrics & Gynaecology,', 'PM2438. Tocolytic agents', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><div>\n<table width=\148\>\n<tbody>\n<tr>\n<td><strong>Drugs</strong></td>\n<td><strong>Examples</strong></td>\n</tr>\n<tr>\n<td>\n<div class=\section\>\n<div class=\layoutArea\>\n<div class=\column\>Calcium channel blockers</div>\n</div>\n</div>\n</td>\n<td>\n<div class=\section\>\n<div class=\layoutArea\>\n<div class=\column\>Nifedipine <br>Nicardipine<br> Verapamil</div>\n</div>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div class=\section\>\n<div class=\layoutArea\>\n<div class=\column\>\n<div class=\page\ title=\Page 606\>\n<div class=\section\>\n<div class=\layoutArea\>\n<div class=\column\>Competitive inhibition to calcium ion</div>\n</div>\n</div>\n</div>\n</div>\n</div>\n</div>\n</td>\n<td>Magnesium sulfate</td>\n</tr>\n<tr>\n<td>\n<div class=\section\>\n<div class=\layoutArea\>\n<div class=\column\>Cyclo-oxygenase inhibitors</div>\n</div>\n</div>\n</td>\n<td>\n<div class=\section\>\n<div class=\layoutArea\>\n<div class=\column\>Indomethacin<br>Sulindac</div>\n</div>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div class=\section\>\n<div class=\layoutArea\>\n<div class=\column\>Beta mimetics</div>\n</div>\n</div>\n</td>\n<td>\n<div class=\section\>\n<div class=\layoutArea\>\n<div class=\column\>Terbutaline<br>Ritodrine<br>Isoxsuprine</div>\n</div>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div class=\section\>\n<div class=\layoutArea\>\n<div class=\column\>Oxytocin Antagonists</div>\n</div>\n</div>\n</td>\n<td>\n<div class=\section\>\n<div class=\layoutArea\>\n<div class=\column\>Atosiban</div>\n</div>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div class=\section\>\n<div class=\layoutArea\>\n<div class=\column\>Nitric oxide (NO) Donors</div>\n</div>\n</div>\n</td>\n<td>&nbsp;Glyceryl trinitrate (GTN)</td>\n</tr>\n</tbody>\n</table>\n</div></div><!---->\n      <!---->\n    </div><!---->'),
(619, 'Microbiology', 'PM2458. Laboratory Biosafety Levels (BSL)', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<table>\n<tbody>\n<tr>\n<td><strong>Biosafety level</strong></td>\n<td><strong>Lab practices</strong></td>\n<td><strong>Safety Equipment</strong></td>\n<td><strong>Facility</strong></td>\n</tr>\n<tr>\n<td><strong>BSL 1</strong></td>\n<td>\n<p>Work on open lab benches or tables.</p>\n<p>Good microbiological techniques followed</p>\n</td>\n<td>PPE (lab coats, gloves, eye protection) are worn as needed.</td>\n<td>\n<p>Sink for hand washing.</p>\n<p>Separate doors from the rest of the facility.</p>\n</td>\n</tr>\n<tr>\n<td><strong>BSL 2</strong></td>\n<td>Access restricted to the laboratory</td>\n<td>\n<p>Appropriate PPE (lab coats, gloves, eye protection, and face shields).</p>\n<p>Biological safety cabinets (BSC) to prevent potential aerosols.</p>\n<p>Autoclave or alternative method for decontamination</p>\n</td>\n<td>\n<p>Sink and eyewash easily available.</p>\n<p>Self-closing doors.&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td><strong>BSL 3</strong></td>\n<td>\n<p>Access restricted and controlled at all times.</p>\n<p>Medical surveillance of laboratory workers.</p>\n</td>\n<td>\n<p>Appropriate PPE must be worn, and respirators might be required.</p>\n<p>BSC for all activities.</p>\n</td>\n<td>\n<p>Hands-free sink and eyewash.</p>\n<p>Directional airflow from clean areas towards potentially contaminated areas.</p>\n<p>Entrance to the lab is through 2 self-closing and interlocked doors.</p>\n</td>\n</tr>\n<tr>\n<td><strong>BSL 4</strong></td>\n<td>\n<p>Change clothing before entering and shower upon exiting.</p>\n<p>Decontaminate all materials before exiting.</p>\n</td>\n<td>\n<p>BSC and full-body, air-supplied, positive pressure suit.</p>\n<p>Double-ended autoclave (through the wall).</p>\n</td>\n<td>\n<p>Laboratory in a separate building or isolated in a restricted zone.</p>\n<p>Dedicated supply and exhaust air, as well as vacuum lines and decontamination systems.</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(620, 'Microbiology', 'PM2347. Types of capsules in microorganisms', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Capsule&nbsp;</strong></td>\n<td><strong>Organsim</strong></td>\n</tr>\n<tr>\n<td>Polysaccharide capsule&nbsp;</td>\n<td>\n<p>&nbsp;Streptococcus pneumonia</p>\n<p>&nbsp;Klebsiella pneumonia</p>\n<p>&nbsp;Hemophilius influenza</p>\n<p>&nbsp;Neiserria meningitidis</p>\n<p>&nbsp;Salmonella typhi</p>\n<p>&nbsp;Cryptococcus neoformans</p>\n<p>&nbsp;Bordetella pertussis</p>\n<p>Clostridium perfringens</p>\n</td>\n</tr>\n<tr>\n<td>Hyaluronic acid capsule&nbsp;</td>\n<td>Streptococcus pyogenes</td>\n</tr>\n<tr>\n<td>Polypeptide (Poly-D -Glutamate) Capsule&nbsp;</td>\n<td>Bacillus anthracis</td>\n</tr>\n<tr>\n<td>F1 Protein Capsule&nbsp;</td>\n<td>Yersinia pestis</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(621, 'Microbiology', 'PM2346. History of Microbiology', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>&nbsp;<strong>Achievement</strong></td>\n<td>&nbsp;<strong>Scientist</strong></td>\n</tr>\n<tr>\n<td>Father of Microscopy/Microbiology</td>\n<td>Anton von Leeuwenhoek &nbsp;&nbsp;</td>\n</tr>\n<tr>\n<td>Father of Modern Microbiology</td>\n<td>Louis Pasteur &nbsp;&nbsp;</td>\n</tr>\n<tr>\n<td>Father of Bacteriology</td>\n<td>Robert Koch &nbsp;</td>\n</tr>\n<tr>\n<td>Father of Virology</td>\n<td>W M Stanley &nbsp;&nbsp;</td>\n</tr>\n<tr>\n<td>Father of Mycology</td>\n<td>Raymond Sabouraud &nbsp;</td>\n</tr>\n<tr>\n<td>Father of Chemotherapy</td>\n<td>Paul Ehrlich &nbsp;</td>\n</tr>\n<tr>\n<td>Father of Antiseptic surgery</td>\n<td>Joseph Lister &nbsp;&nbsp;</td>\n</tr>\n<tr>\n<td>Founder of Electron Microscope</td>\n<td>Ernst Ruska &nbsp;</td>\n</tr>\n<tr>\n<td>Founder of L forms of Bacteria</td>\n<td>Kleinberger &nbsp;&nbsp;</td>\n</tr>\n<tr>\n<td>Founder of Gram Stain</td>\n<td>Hans Christian Gram&nbsp;</td>\n</tr>\n<tr>\n<td>First to describe phagocytosis</td>\n<td>Elie Metchnikoff &nbsp;</td>\n</tr>\n<tr>\n<td>Founder of PCR</td>\n<td>Karry B Mullis</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->');
INSERT INTO db_pearl_m (pid, subject, pearl_title, pearl_desc) VALUES
(622, 'Pathology,', 'PM2206. Various stains and their applications in Pathology and Microbiology', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Staining technique</strong></td>\n<td><strong>Cells/inclusions stained</strong></td>\n<td><strong>Disorders</strong></td>\n</tr>\n<tr>\n<td><strong>H&amp;E</strong></td>\n<td>Most common stain used in routine&nbsp;<strong>histopathology</strong></td>\n<td>Commonly used for all slides</td>\n</tr>\n<tr>\n<td><strong>Papanicolaou stain</strong></td>\n<td>Universal stain for <strong>cytological</strong> preparations</td>\n<td>Cytological studies</td>\n</tr>\n<tr>\n<td><strong>PAS</strong></td>\n<td>Demonstrate&nbsp;<strong>glycogen,&nbsp;mucin,&nbsp;fungi, </strong>macrophages&nbsp;</td>\n<td>Whipples disease, adenocarcinomas,&nbsp;and glycogen-positive tumors (Ewings sarcoma, renal cell carcinoma)</td>\n</tr>\n<tr>\n<td><strong><strong>PTAH (</strong></strong><strong>Phosphotungstic acid hematoxylin)</strong></td>\n<td>Fibrin</td>\n<td>Gliosis, Ependymomas, Tumours of skeletal muscles, Fibrin deposits</td>\n</tr>\n<tr>\n<td><strong>Alcian blue</strong></td>\n<td>For <strong>acid mucins</strong>: Positive stain for mucins of the intestinal mucosa and negative stain for mucins of gastric mucosa and Brunners gland</td>\n<td>Barretts esophagus, Gastric disorders</td>\n</tr>\n<tr>\n<td><strong>Perls Prussian blue</strong></td>\n<td>Demonstrate&nbsp;<strong>hemosiderin and iron</strong></td>\n<td>Hemochromatosis&nbsp;</td>\n</tr>\n<tr>\n<td><strong>Masson–Fontana stain</strong></td>\n<td>Demonstrate <strong>melanin</strong></td>\n<td>Nevus and malignant melanoma</td>\n</tr>\n<tr>\n<td><strong>von Kossa method</strong></td>\n<td>Demonstrate&nbsp;<strong>calcium</strong></td>\n<td>Calcification of tissues</td>\n</tr>\n<tr>\n<td><strong>Van Gieson stain</strong></td>\n<td>\n<p>Demonstrate <strong>collagen </strong>in; Differentiate amyloid from collagen</p>\n</td>\n<td>Spindle-cell neoplasm; Delineate extent of fibrosis&nbsp;</td>\n</tr>\n<tr>\n<td><strong>Masson trichrome stain</strong></td>\n<td>Demonstrate <strong>collagen and muscle</strong></td>\n<td>Connective tissue disorders, muscle and fibroblast tumors, liver cirrhosis, neuromuscular diseases&nbsp;</td>\n</tr>\n<tr>\n<td><strong>Reticulin stain</strong></td>\n<td>Demonstrate <strong>reticulin fibers</strong></td>\n<td>Distinguish epithelial from non-epithelial neoplasm, Hodgkins lymphoma, early cirrhosis of the&nbsp;liver, myelofibrosis</td>\n</tr>\n<tr>\n<td><strong>Verhoeffs method</strong></td>\n<td>Demonstrate <strong>elastic fibers</strong></td>\n<td>Malignant hypertension, arteriovenous malformation aneurysms, Marfans syndrome, emphysema lung</td>\n</tr>\n<tr>\n<td><strong>Oil red O</strong> <strong>Sudan Black</strong> <strong>Sudan III/IV</strong></td>\n<td>Demonstrate&nbsp;<strong>fat/lipids</strong></td>\n<td>Fatty liver-fat embolism-fibroma vs. thecoma of the&nbsp;ovary-liposarcoma&nbsp;</td>\n</tr>\n</tbody>\n</table>\n<p><strong>Microbiology stains:</strong></p>\n<table>\n<tbody>\n<tr>\n<td><strong>Stain</strong></td>\n<td><strong>&nbsp;Uses</strong></td>\n</tr>\n<tr>\n<td><strong>Ziehl–Neelsen Stain</strong></td>\n<td>Mycobacteria (all species, including <em>Mycobacterium leprae</em>), <em>Nocardia </em>spp. Cyst of&nbsp;<em>Cryptosporidium parvum</em>, <em>Isospora </em>spp., and&nbsp;<em>Cyclospora</em>&nbsp;spp., Head of sperms</td>\n</tr>\n<tr>\n<td><strong>Masson’s Fontana</strong></td>\n<td>Melanin and argentaffin Cells</td>\n</tr>\n<tr>\n<td><strong>Gomori Methenamine Silver</strong></td>\n<td>Fungi</td>\n</tr>\n<tr>\n<td><strong>Luxol Fast Blue (LFB)</strong></td>\n<td>Myelin</td>\n</tr>\n<tr>\n<td><strong>Phosphotungstic Acid-hematoxylin</strong></td>\n<td>Cross striations of skeletal muscle, fibrin</td>\n</tr>\n<tr>\n<td><strong>Toluidine blue</strong></td>\n<td>Mast cells</td>\n</tr>\n<tr>\n<td><strong>Warthin–Starry</strong></td>\n<td>Helicobacter pylori, spirochetes</td>\n</tr>\n<tr>\n<td><strong>Van Gieson</strong></td>\n<td>Collagen fibres</td>\n</tr>\n<tr>\n<td><strong>Mucicarmine</strong></td>\n<td>Mucin, <em>Cryptococcus</em> capsule</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(623, 'Microbiology', 'PM1942. Acid fast Organisms', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\447\>\n<tbody>\n<tr>\n<td><strong>Acid-fast organisms</strong></td>\n<td><strong>Sulfuric acid (%) required for decolorization&nbsp;</strong></td>\n</tr>\n<tr>\n<td>\n<ul>\n<li>Mycobacterium tuberculosis&nbsp;</li>\n</ul>\n</td>\n<td>25%</td>\n</tr>\n<tr>\n<td>\n<ul>\n<li>Mycobacterium leprae</li>\n</ul>\n</td>\n<td>5%</td>\n</tr>\n<tr>\n<td>\n<p>&nbsp;</p>\n<ul>\n<li>Eggs of Taenia saginata</li>\n<li>Eggs of Schistosoma mansoni</li>\n<li>Oocyst of Cryptosporidium</li>\n<li>Oocyst of Cyclospora</li>\n<li>Oocyst of Cystoisospora</li>\n</ul>\n</td>\n<td>1%</td>\n</tr>\n<tr>\n<td>\n<ul>\n<li>Sperm head</li>\n<li>Legionella micdadei</li>\n<li>Nocardia</li>\n</ul>\n</td>\n<td>0.5-1%</td>\n</tr>\n<tr>\n<td>\n<ul>\n<li>Bacterial spore</li>\n</ul>\n</td>\n<td>0.25-0.5%</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(624, 'Microbiology', 'PM1786. Various stains and their applications in microbiology', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>A. Simple stains: </strong>Provides color contrast but gives same color to all bacteria and cells.</p>\n<table>\n<tbody>\n<tr>\n<td><strong>Type of stain</strong></td>\n<td><strong>Applications</strong></td>\n</tr>\n<tr>\n<td><strong>Acidic stains</strong></td>\n<td>\n<p>These include<strong> eosin, acid fuchsin, rose bengal, congo red</strong>, etc.</p>\n<p>Used to stain pathological specimens</p>\n</td>\n</tr>\n<tr>\n<td><strong>Basic stains</strong>.</td>\n<td>\n<p><strong>Loeffler’s methylene blue</strong> stain is used in <strong>Quellung reaction</strong>.&nbsp;</p>\n<p><strong>Polychrome methylene blue</strong> is used in <strong>McFadyean’s</strong> <strong>reaction.</strong></p>\n</td>\n</tr>\n<tr>\n<td><strong>Neutral stains</strong></td>\n<td>\n<p><strong>Wright</strong> and <strong>Giemsa stains</strong> are <strong>Romanowsky stains</strong> used to stain peripheral blood and bone marrow smears. &nbsp;</p>\n<p><strong>Giemsa stain</strong> is used for histopathological diagnosis of malaria and other protozoan blood parasites.&nbsp;&nbsp;</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>B. Differential stains: </strong>Imparts different colors to different bacteria. (These contain more than one stain).</p>\n<table>\n<tbody>\n<tr>\n<td><strong>Type of stain</strong></td>\n<td><strong>Applications</strong></td>\n</tr>\n<tr>\n<td><strong>Gram stain:</strong></td>\n<td>To differentiate between <strong>gram</strong> <strong>positive</strong> (stain purple/violet) and <strong>gram</strong> <strong>negative</strong> bacteria (stain pink or red).</td>\n</tr>\n<tr>\n<td><strong>Acid-fast stain:&nbsp;</strong></td>\n<td>Used in <strong>Ziehl–Neelsen staining</strong> technique for identification of <strong>acid–fast microorganisms </strong>such as <em>M. tuberculosis</em>, <em>M. leprae</em>, and Nocardia.</td>\n</tr>\n<tr>\n<td><strong>Fluorochrome staining:</strong></td>\n<td><strong>Auramine-rhodamine</strong> is used in fluorochrome staining for screening for <strong>tuberculosis</strong>.</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>C. Special stains:</strong></p>\n<table>\n<tbody>\n<tr>\n<td><strong>Type of stain</strong></td>\n<td><strong>Applications</strong></td>\n</tr>\n<tr>\n<td><strong>Staining volutin-containing organisms</strong></td>\n<td><em><strong>Corynebacterium</strong> <strong>diphtheriae</strong> </em>stained by <strong>Albert’s stain</strong>. Green colored bacilli showing \<strong>chinese-letter\ </strong>pattern<strong>, </strong>containing bluish-black <strong>metachromatic</strong> granules of cells<strong>.</strong></td>\n</tr>\n<tr>\n<td><strong>Flagella stain</strong></td>\n<td><em><strong>Salmonella</strong> <strong>typhimurium</strong></em> stained by <strong>modified Ryu stain</strong> to show peritrichous flagella</td>\n</tr>\n<tr>\n<td><strong>Endospore stain</strong></td>\n<td>Used in <strong>Schaeffer and Fulton staining technique</strong> to stain the endospores, where the primary stain is <strong>malachite</strong> <strong>green</strong>, and the counterstain is safranin.</td>\n</tr>\n<tr>\n<td><strong>Capsule stain</strong></td>\n<td>Used in negative staining (<em><strong>Cryptococcus</strong> <strong>neoformans</strong></em>), McFadyean’s reaction,&nbsp;Polychrome methylene blue (<strong>anthrax</strong> <strong>bacilli</strong>).</td>\n</tr>\n<tr>\n<td><strong>Silver impregnation</strong></td>\n<td><strong>Fontana’s silver impregnation</strong> method used for staining <strong>treponeme</strong> and <strong>leptospire</strong>.</td>\n</tr>\n<tr>\n<td><strong>Silver staining</strong></td>\n<td>Used in <strong>Grocott’s – Gomori’s methenamine silver stain. </strong>GMS stain is used to identify <em><strong>Pneumocystis jirovecii</strong></em>, which classically appear as <strong>crushed ping-pong balls.</strong> <strong>&nbsp;</strong> Warthin–starry silver stain for spirochetes.</td>\n</tr>\n<tr>\n<td><strong>Calcofluor white staining</strong></td>\n<td>The cell walls of fungi will bind the stain <strong>calcofluor white.&nbsp;</strong>It is especially useful for the identification of<strong> mucor</strong> and the causative agents of <strong>zygomycosis</strong>.&nbsp; It is also used in staining <strong>acanthamoeba</strong></td>\n</tr>\n<tr>\n<td><strong>Chances staining</strong></td>\n<td>For cell wall demonstration</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(625, 'Microbiology', 'PM1767. Types of flagella:', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://cdn1.dailyrounds.org/uploads/a0958d31ed27401cbae49da4e8424bd8x846x699.PNG\><!---->\n      </div><!---->\n    </div><!---->'),
(626, 'Microbiology', 'PM1764. Methods of sterilisation', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td><strong>Method of disinfection or sterilisation</strong></td>\n<td><strong>Material</strong></td>\n</tr>\n<tr>\n<td>Red Heat</td>\n<td>Inoculating wire loop, tip of forceps</td>\n</tr>\n<tr>\n<td>Incineration</td>\n<td>Contaminated clothes, animal carcasses, plastics like polythene</td>\n</tr>\n<tr>\n<td>Hot air oven</td>\n<td>Glasswares, scalpels, forceps, cutting instruments, test tubes</td>\n</tr>\n<tr>\n<td>Inspissator</td>\n<td>Heat labile culture media containing egg, sugar or serum</td>\n</tr>\n<tr>\n<td>Autoclave</td>\n<td>Culture media, dressings, gloves, aprons, all instruments except sharps, suture materials except catgut</td>\n</tr>\n<tr>\n<td>Isopropyl alcohol</td>\n<td>Clinical thermometers, stethoscopes, probes</td>\n</tr>\n<tr>\n<td>Methyl alcohol</td>\n<td>Fungal spores</td>\n</tr>\n<tr>\n<td>\n<p>Beta propiolactone/</p>\n<p>formaldehyde</p>\n</td>\n<td>Fumigation of operating theatres, wards and labs</td>\n</tr>\n<tr>\n<td>2% gluteraldehyde</td>\n<td>Cystoscopes, bronchoscopes, fibre-optic endoscopes, plastic ETT, face masks</td>\n</tr>\n<tr>\n<td>Plasma sterilisation</td>\n<td>Arthroscopes, urethroscopes</td>\n</tr>\n<tr>\n<td>Ethylene oxide</td>\n<td>Heart lung machines, respirators, dental equipments, sutures, books, clothings</td>\n</tr>\n<tr>\n<td>Filtration (Seitz filters)</td>\n<td>Bacterial vaccines, intravenous solutions</td>\n</tr>\n<tr>\n<td>5% cresol, hot air oven</td>\n<td>Sharp instruments</td>\n</tr>\n<tr>\n<td>\n<p>Ionising radiation</p>\n<p>(Cold air sterilization)</p>\n</td>\n<td>Catgut, swabs, plastic, syringes, needles, gloves, catheters, grafts</td>\n</tr>\n<tr>\n<td>Chlorhexidine</td>\n<td>Disinfecting surgeon’s hands prior to surgery</td>\n</tr>\n<tr>\n<td>Iodophor</td>\n<td>Disinfecting surgical site prior to surgery</td>\n</tr>\n<tr>\n<td>70% ethanol</td>\n<td>Disinfectant skin prior to venepuncture or immunisation</td>\n</tr>\n<tr>\n<td>Thiomersal</td>\n<td>Preservative in vaccine</td>\n</tr>\n<tr>\n<td>Silver sulfadiazine</td>\n<td>Cleanse burn wounds</td>\n</tr>\n<tr>\n<td>Hypochlorite</td>\n<td>Cleanup of blood spill from a patient with hepatitis B or C</td>\n</tr>\n<tr>\n<td>Hydrogen peroxide</td>\n<td>Cleanse wounds</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(627, 'Microbiology', 'PM1732. Types of motility of microorganisms', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td><strong>Motility</strong></td>\n<td><strong>Microorganism</strong></td>\n</tr>\n<tr>\n<td>Darting/vibratory motility&nbsp;</td>\n<td><em>Vibrio,Campylobacter</em></td>\n</tr>\n<tr>\n<td>Swarming motility</td>\n<td><em>Cl. septicum, B.cereus, Proteus</em>, some Vibrio sp</td>\n</tr>\n<tr>\n<td>Tumbling motility&nbsp;</td>\n<td><em>Listeria monocytogenes</em></td>\n</tr>\n<tr>\n<td>Stately motility</td>\n<td>Clostridia</td>\n</tr>\n<tr>\n<td>Spinning motility</td>\n<td><em>Fusobacterium gyrans</em></td>\n</tr>\n<tr>\n<td>Cork-screw motility&nbsp;</td>\n<td><em>Treponema pallidum</em></td>\n</tr>\n<tr>\n<td>Falling leaf motility</td>\n<td>Giardia</td>\n</tr>\n<tr>\n<td>Twitching motility</td>\n<td><em>Eikenella corrodens</em></td>\n</tr>\n<tr>\n<td>Differential motility (Motility varies at different temperatures)</td>\n<td><em>Yersinia enterocolitica </em>&amp;<em> Listeria monocytogenes</em></td>\n</tr>\n<tr>\n<td>Jerky motility&nbsp;</td>\n<td><em>Trichomonas vaginalis</em></td>\n</tr>\n<tr>\n<td>Gliding motility</td>\n<td><em>Mycoplasma, Entamoeba</em></td>\n</tr>\n<tr>\n<td>Lashing motility&nbsp;</td>\n<td>Saprophytic spirochetes</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(628, 'Microbiology', 'PM1730. Diseases with usual incubation period', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td><strong>Disease</strong></td>\n<td><strong>Incubation period(days)</strong></td>\n</tr>\n<tr>\n<td>Dengue</td>\n<td>5-8</td>\n</tr>\n<tr>\n<td>Typhoid</td>\n<td>10-14</td>\n</tr>\n<tr>\n<td>Herpes simplex</td>\n<td>5-8</td>\n</tr>\n<tr>\n<td>Enterovirus disease</td>\n<td>6-12</td>\n</tr>\n<tr>\n<td>Poliomyelitis</td>\n<td>5-20</td>\n</tr>\n<tr>\n<td>Measles</td>\n<td>9-12</td>\n</tr>\n<tr>\n<td>Smallpox</td>\n<td>12-14</td>\n</tr>\n<tr>\n<td>Chickenpox</td>\n<td>13-17</td>\n</tr>\n<tr>\n<td>Mumps</td>\n<td>16-20</td>\n</tr>\n<tr>\n<td>Rubella</td>\n<td>17-20</td>\n</tr>\n<tr>\n<td>Mononucleosis</td>\n<td>30-50</td>\n</tr>\n<tr>\n<td>Hepatitis A</td>\n<td>15-40</td>\n</tr>\n<tr>\n<td>Hepatitis B &amp; C</td>\n<td>50-150</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(629, 'Community Medicine,', 'PM0814. Methods of Sterilization and Disinfection', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>&nbsp;</td>\n<td>Spectrum</td>\n<td>Uses</td>\n</tr>\n<tr>\n<td><strong>1. Heat</strong></td>\n<td>&nbsp;</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>Hot air oven</td>\n<td>All</td>\n<td>Glasswares, sterilizing powders, oils, swabs and dressings</td>\n</tr>\n<tr>\n<td>Autoclaving</td>\n<td>All</td>\n<td>Linen, surgical instruments, laboratory ware, media and pharmaceutical products</td>\n</tr>\n<tr>\n<td>Boiling</td>\n<td>Most pathogens, some spores</td>\n<td>Metal tools, rubber or plastic equipment, cloth&nbsp;</td>\n</tr>\n<tr>\n<td>Pasteurization</td>\n<td>Vegetative bacteria</td>\n<td>Beverages, plastic hospital equipment</td>\n</tr>\n<tr>\n<td><strong>2. Ethylene oxide gas</strong></td>\n<td>All</td>\n<td>Heat-sensitive equipment like heart-lung machines, artificial valves, fabrics, respirators, cardiac catheters, books</td>\n</tr>\n<tr>\n<td><strong>3. Radiation</strong></td>\n<td>&nbsp;</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>UV</td>\n<td>All</td>\n<td>Limited value due to poor penetration, can be used for plastic syringes, catheters, and laboratory cabinets</td>\n</tr>\n<tr>\n<td>Ionising</td>\n<td>All</td>\n<td>Surgical supplies, food</td>\n</tr>\n<tr>\n<td><strong>4. Chemicals</strong></td>\n<td>&nbsp;</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>Alcohol</td>\n<td>Vegetative bacteria, viruses some spores</td>\n<td>Skin disinfection, hand washing</td>\n</tr>\n<tr>\n<td>Hydrogen peroxide</td>\n<td>Viruses, vegetative bacteria, spores and fungi</td>\n<td>High-level disinfectant, antiseptic for wounds and discharging ulcers</td>\n</tr>\n<tr>\n<td>Chlorine</td>\n<td>Viruses, vegetative bacteria, fungi</td>\n<td>To disinfect water</td>\n</tr>\n<tr>\n<td>Iodophors</td>\n<td>Viruses, vegetative bacteria, fungi, lethal to spore-bearing organisms</td>\n<td>Skin disinfection, disinfect blood culture bottles and medical equipment</td>\n</tr>\n<tr>\n<td>Crude phenol</td>\n<td>Gram-positive and gram-negative bacteria, but only slowly effective against spores and acid-fast bacteria.</td>\n<td>Disinfection of faeces, mopping floors and cleaning drains.</td>\n</tr>\n<tr>\n<td><strong>5. Glutaraldehyde&nbsp;</strong></td>\n<td>&nbsp;All</td>\n<td>Endoscopes, bronchoscopes</td>\n</tr>\n<tr>\n<td>6. <strong>Quarternary ammonium compounds</strong></td>\n<td>Most bacteria, fungi, lipophilic viruses&nbsp;</td>\n<td>General cleaning</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(630, 'Microbiology,', 'PM0805. Temperatures in Sterilization and Disinfection', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\181\>\n<p><strong>Method</strong></p>\n</td>\n<td width=\214\>\n<p><strong>Temperature (degree Celsius)</strong></p>\n</td>\n<td width=\195\>\n<p><strong>Usual Duration</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\181\>\n<p>Hot air oven</p>\n</td>\n<td width=\214\>\n<p>160</p>\n</td>\n<td width=\195\>\n<p>2 hours</p>\n</td>\n</tr>\n<tr>\n<td width=\181\>\n<p>Classic autoclave</p>\n</td>\n<td width=\214\>\n<p>121</p>\n</td>\n<td width=\195\>\n<p>10–15 minutes</p>\n</td>\n</tr>\n<tr>\n<td width=\181\>\n<p>Flash autoclave</p>\n</td>\n<td width=\214\>\n<p>134</p>\n</td>\n<td width=\195\>\n<p>3 minutes</p>\n</td>\n</tr>\n<tr>\n<td width=\181\>\n<p>Ethylene oxide</p>\n</td>\n<td width=\214\>\n<p>50 to 60</p>\n</td>\n<td width=\195\>\n<p>&nbsp;1 - 6 hours</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(631, 'Microbiology', 'PM0450. Differentiating features between prokaryotes and eukaryotes', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Character</strong></p>\n</td>\n<td>\n<p><strong>Prokaryotes</strong></p>\n</td>\n<td>\n<p><strong>Eukaryotes</strong></p>\n</td>\n</tr>\n<tr>\n<td colspan=\3\>\n<p><strong>Nucleus</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Nucleolus</p>\n</td>\n<td>\n<p>Absent</p>\n</td>\n<td>\n<p>Present</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Nuclear membrane</p>\n</td>\n<td>\n<p>Absent</p>\n</td>\n<td>\n<p>Present</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Deoxyribonucleoprotein</p>\n</td>\n<td>\n<p>Absent</p>\n</td>\n<td>\n<p>Present</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Chromosome</p>\n</td>\n<td>\n<p>One (circular)</p>\n</td>\n<td>\n<p>More than one (linear)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Mitotic division</p>\n</td>\n<td>\n<p>Absent</p>\n</td>\n<td>\n<p>Present</p>\n</td>\n</tr>\n<tr>\n<td colspan=\3\>\n<p><strong>Cytoplasm</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Cytoplasmic streaming</p>\n</td>\n<td>\n<p>Absent</p>\n</td>\n<td>\n<p>Present</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Pinocytosis</p>\n</td>\n<td>\n<p>Absent</p>\n</td>\n<td>\n<p>Present</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Mitochondria</p>\n</td>\n<td>\n<p>Absent</p>\n</td>\n<td>\n<p>Present</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Lysosomes</p>\n</td>\n<td>\n<p>Absent</p>\n</td>\n<td>\n<p>Present</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Golgi apparatus</p>\n</td>\n<td>\n<p>Absent</p>\n</td>\n<td>\n<p>Present</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Endoplasmic reticulum</p>\n</td>\n<td>\n<p>Absent</p>\n</td>\n<td>\n<p>Present</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Cell wall</strong></p>\n</td>\n<td>\n<p>Present</p>\n<p>Muramic acid present</p>\n</td>\n<td>\n<p>Absent</p>\n<p>(if present, muramic acid is absent)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Sterols (In cell membrane)</p>\n</td>\n<td>\n<p>Absent&nbsp;</p>\n</td>\n<td>\n<p>Present</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(632, 'Microbiology', 'PM0144. Inclusion bodies seen in various conditions/diseases', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>1. Bacterial Inclusion Bodies&nbsp;</strong></p>\n<table width=\508\>\n<tbody>\n<tr>\n<td><strong>Disease/Bacteria&nbsp;</strong></td>\n<td><strong>Inclusion bodies&nbsp;</strong></td>\n</tr>\n<tr>\n<td>Granuloma inguinale,&nbsp;<em>Klebsiella granulomatis</em></td>\n<td>Donovan bodies</td>\n</tr>\n<tr>\n<td>Psittacosis, <em>Chlamydia psittacci&nbsp;</em></td>\n<td>Levinthal-Cole-Lille bodies (LCL bodies)</td>\n</tr>\n<tr>\n<td>Trachoma, <em>Chlamydia trachomatis</em></td>\n<td>Halberstaedter-Prowazek bodies</td>\n</tr>\n<tr>\n<td>Lymphogranuloma venereum,&nbsp;<em>Chlamydia trachomatis</em></td>\n<td>Miyagawa’s granulocorpusles</td>\n</tr>\n<tr>\n<td>Diphtheria,&nbsp;<em>Corynebacterium diptheriae</em></td>\n<td>Babes-Ernst granules</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>2. Viral Inclusion Bodies</strong></p>\n<ul>\n<li><strong>Intra-cytoplasmic Inclusion Bodies</strong></li>\n</ul>\n<table width=\513\>\n<tbody>\n<tr>\n<td><strong>Disease/Virus</strong></td>\n<td><strong>Inclusion bodies</strong></td>\n</tr>\n<tr>\n<td>Rabies</td>\n<td>Negri bodies</td>\n</tr>\n<tr>\n<td>Vaccinia, variola (smallpox)</td>\n<td>Guarnieri bodies</td>\n</tr>\n<tr>\n<td>Variola (smallpox)</td>\n<td>Paschen bodies</td>\n</tr>\n<tr>\n<td>Molluscum contagiosum</td>\n<td>Henderson-Patterson bodies</td>\n</tr>\n<tr>\n<td>Fowlpox</td>\n<td>Bollinger bodies</td>\n</tr>\n</tbody>\n</table>\n<ul>\n<li><strong>Intra-nuclear Inclusion Bodies&nbsp;</strong></li>\n</ul>\n<table>\n<tbody>\n<tr>\n<td>&nbsp;</td>\n<td><strong>Inclusion bodies&nbsp;</strong></td>\n<td><strong>Disease/Virus</strong></td>\n</tr>\n<tr>\n<td rowspan=\2\>Cowdry type A</td>\n<td>Lipschutz inclusions</td>\n<td>Herpes Virus</td>\n</tr>\n<tr>\n<td>Torres bodies</td>\n<td>Yellow fever</td>\n</tr>\n<tr>\n<td rowspan=\2\>Cowdry type B</td>\n<td>Basophilic inclusions</td>\n<td>Adenovirus</td>\n</tr>\n<tr>\n<td>Acidophilic inclusions</td>\n<td>Poliovirus</td>\n</tr>\n</tbody>\n</table>\n<ul>\n<li><strong>Both Intra-nuclear and Intra-cytoplasmic Inclusion Bodies&nbsp;</strong></li>\n</ul>\n<table width=\521\>\n<tbody>\n<tr>\n<td><strong>Disease/Virus&nbsp;</strong></td>\n<td><strong>Inclusion bodies&nbsp;</strong></td>\n</tr>\n<tr>\n<td>Measles Virus</td>\n<td>Warthin-Finkedly giant cells</td>\n</tr>\n<tr>\n<td>Cytomegalovirus</td>\n<td>Owl-eye inclusions (basophilic,&nbsp;predominantly intra-nuclear, small granular intracytoplasmic inclusions are found)</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>3. Inclusion Bodies in Parasitic Infections&nbsp;</strong></p>\n<table width=\522\>\n<tbody>\n<tr>\n<td><strong>Disease/Parasite</strong></td>\n<td><strong>Inclusion bodies&nbsp;</strong></td>\n</tr>\n<tr>\n<td>Trypanosomiasis</td>\n<td>Morula cells with IgM Deposits</td>\n</tr>\n<tr>\n<td>Kala-azar</td>\n<td>Leishman-Donovan Bodies</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>4. Miscellaneous Inclusion Bodies&nbsp;</strong></p>\n<table width=\521\>\n<tbody>\n<tr>\n<td><strong>Disease/Location</strong></td>\n<td><strong>Inclusion bodies&nbsp;</strong></td>\n</tr>\n<tr>\n<td>Ovarian cortex</td>\n<td>Walthard Inclusions</td>\n</tr>\n<tr>\n<td>Spherical globules surrounded by granulosa cells in the ovary</td>\n<td>Call-Exner bodies</td>\n</tr>\n<tr>\n<td>Parkinsonism, Lewy body dementia&nbsp;</td>\n<td>Lewy Bodies</td>\n</tr>\n<tr>\n<td>Amyotrophic Lateral Sclerosis&nbsp;</td>\n<td>Bunina bodies</td>\n</tr>\n<tr>\n<td>Multiple Myeloma</td>\n<td>Russell Bodies</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(633, 'Microbiology', 'PM0099. Toxins that are coded by phage DNA - Mnemonic', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>A:</strong> Group A strep (S. pyogenes): pyrogenic exotoxins SpeA and SpeC</p>\n<p><strong>B:</strong> Botulinum toxin C &amp; D</p>\n<p><strong>C:</strong> Cholera toxin</p>\n<p><strong>D:</strong> Diphtheria toxin</p>\n<p><strong>E: </strong>E.coli(EHEC): Verocytotoxin (Shiga-like toxin)</p></div><!---->\n      <!---->\n    </div><!---->'),
(634, 'Microbiology,', 'PM0038. Biomedical waste disposal', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<table>\n<tbody>\n<tr>\n<td colspan=\2\>\n<p><span><strong>&nbsp;<span> Yellow bag </span></strong><strong><span>(non-chlorinated)</span></strong><strong><span>&nbsp;</span>&nbsp;</strong></span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Type of Waste</p>\n</td>\n<td>&nbsp;Treatment and Disposal Options</td>\n</tr>\n<tr>\n<td>\n<p><strong>Human anatomical waste</strong></p>\n<p><strong>Animal anatomical waste</strong></p>\n</td>\n<td>\n<p><strong>Incineration</strong>/Plasma pyrolysis/Deep Burial</p>\n</td>\n</tr>\n<tr>\n<td><strong>Soiled Waste: </strong>Items like dressings, cotton swabs, and plaster casts contaminated with blood and body fluids and bags containing residual discarded blood.</td>\n<td>\n<p><strong>Incineration</strong>/Plasma pyrolysis/Deep Burial</p>\n<p>or</p>\n<p>Autoclaving/Microwaving/ Hydroclaving</p>\n<p>Later - Shredding/mutilation</p>\n</td>\n</tr>\n<tr>\n<td>Discarded linen, mattresses, beddings contaminated with blood or body fluid,<strong> routine masks, and gowns</strong></td>\n<td>\n<p>Non- chlorinated chemical disinfection<br>followed by incineration or Plazma<br>Pyrolysis or for energy recovery.<br>In absence of the above facilities, shredding,&nbsp;mutilation, or a combination of&nbsp;sterilization and shredding is used.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Microbiology, biotechnology, clinical laboratory waste</strong></p>\n<p>(Autoclave safe plastic bags used)</p>\n<p><strong>Blood bags</strong>, laboratory<br>cultures, stocks or&nbsp;specimens of microorganisms,<br>live or&nbsp;attenuated vaccines,&nbsp;human and animal cell&nbsp;cultures used in&nbsp;research, industrial&nbsp;laboratories, production&nbsp;of biological, residual&nbsp;toxins, dishes, and&nbsp;devices used for&nbsp;cultures.</p>\n</td>\n<td>\n<p>Pre-treatment with nonchlorinated chemicals</p>\n<p>Later - Incineration</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Expired/discarded medicine, cytotoxic drugs</p>\n</td>\n<td>Incineration/plasma pyrolysis/ disposal in a secured landfill</td>\n</tr>\n<tr>\n<td>&nbsp;\n<p><strong>Chemical waste</strong></p>\n<p>Chemicals used in biological production&nbsp;and used or discarded<br>disinfectants.</p>\n</td>\n<td>\n<p>Disposal- incineration/plasma<br>pyrolysis</p>\n<p>Encapsulation-&nbsp;hazardous waste treatment, storage, and&nbsp;disposal facility.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Chemical liquid</strong>&nbsp;<strong>waste</strong> :<br>Liquid waste generated&nbsp;due to the use of chemicals&nbsp;in the production of<br>biological and used or&nbsp;<strong>discarded disinfectants,</strong>&nbsp;silver X-ray film<br>developing liquid,<br>discarded formalin,<br>infected <strong>secretions,</strong><br><strong>aspirated body&nbsp;fluids</strong>,&nbsp;liquid from&nbsp;laboratories and floor&nbsp;washings, cleaning,&nbsp;house-keeping and<br>disinfecting activities&nbsp;etc.</p>\n<p>&nbsp;</p>\n</td>\n<td>After resource recovery, the chemical<br>liquid waste shall be pre-treated before&nbsp;mixing with other wastewater.&nbsp;</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table>\n<tbody>\n<tr>\n<td colspan=\2\>\n<p><span><strong>&nbsp; Red&nbsp;bag (non-chlorinated)&nbsp;&nbsp;</strong></span></p>\n</td>\n</tr>\n<tr>\n<td><strong>&nbsp;Type of Waste</strong></td>\n<td>\n<p><strong>&nbsp;<span>Treatment and Disposal Options</span></strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Recyclable contaminated</strong> waste</p>\n<p>Contaminated waste tubing, bottles, intravenous tubes and sets, <strong>catheters,</strong> <strong>urine bags</strong>, syringes (without needles and fixed needle syringes with their needles cut), <strong>vacutainers with</strong> <strong>needles cut,</strong> and gloves.</p>\n</td>\n<td>\n<p><strong>Autoclaving/Microwaving/<br>Hydroclaving</strong></p>\n<p>Followed by Shredding/mutilation</p>\n<p>And later, Energy recovery/Plastics to diesel or fuel oil/road-making</p>\n<p>Not to be sent to landfill</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table>\n<tbody>\n<tr>\n<td colspan=\2\>\n<p><strong>&nbsp; White (translucent)&nbsp;&nbsp;<br>(puncture-proof, leak-proof containers)</strong></p>\n</td>\n</tr>\n<tr>\n<td><strong>Type of Waste</strong></td>\n<td>\n<p><strong>Treatment and Disposal Options</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Waste Sharps including metals.</strong></p>\n<p>Needles, syringes with fixed needles, needles from needle tip cutter or burner, scalpels, blades, or any other <strong>contaminated sharp</strong> object that may cause puncture and cuts. This includes both used, discarded, and contaminated metal sharps</p>\n</td>\n<td>\n<p>Autoclaving/ Dry heat sterilization</p>\n<p>Followed by - <strong>Shredding/mutilation</strong>/<br>encapsulation</p>\n<p>Later by - Iron foundries/sanitary landfill/waste sharps</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table>\n<tbody>\n<tr>\n<td colspan=\2\>\n<p>&nbsp;<span><strong><span>&nbsp; Blue&nbsp;&nbsp;</span><br></strong></span><span><strong><span>(Puncture proof and leak proof containers with blue colored marking)</span></strong></span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Type of Waste&nbsp;</strong></p>\n</td>\n<td>\n<p><strong>&nbsp;Treatment and Disposal Options</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Glassware</strong></p>\n<p>Broken or discarded and contaminated glass including medicine <strong>vials</strong> and <strong>ampules except</strong> those containing <strong>cytotoxic</strong> waste.</p>\n<p><strong>Metallic body implants</strong></p>\n</td>\n<td>\n<p>Sodium hypochlorite/Autoclaving/<br>Microwaving/Hydroclaving</p>\n<p>Later - Recycling</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p>&nbsp;Note: <strong>Ash</strong> from the <strong>incineration</strong> of biomedical waste shall be disposed of at a <strong>common hazardous waste treatment and disposal facility</strong>. However, it may also be disposed of in a <strong>municipal landfill</strong>, <strong>if</strong> the toxic metals in incineration ash are <strong>within</strong> the <strong>regulatory quantities</strong> as defined under the Hazardous Waste (Management and Handling and Transboundary Movement) Rules, 2008 as amended from time to time.</p></div><!---->\n      <!---->\n    </div><!---->'),
(635, 'Pathology,', 'PM2412. Types of Hypersensitivity', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Type of reaction</strong></td>\n<td><strong>Clinical Syndrome</strong></td>\n<td><strong>Time for manifestation</strong></td>\n<td><strong>Mediators</strong></td>\n</tr>\n<tr>\n<td><strong>Type I</strong>: <br>IgE type</td>\n<td>\n<p>1. Anaphylaxis</p>\n<p>2. Atopy</p>\n</td>\n<td>Minutes</td>\n<td>IgE: histamine and other pharmacological agents</td>\n</tr>\n<tr>\n<td><strong>Type II</strong>: <br>Cytolytic, cytotoxic</td>\n<td>\n<p>Antibody-mediated damage: e.g. thrombocytopenia, agranulocytosis, hemolytic anemia</p>\n</td>\n<td>Hours to days</td>\n<td>IgG, IgM</td>\n</tr>\n<tr>\n<td><strong>Type III</strong>:<br> Immune complex</td>\n<td>\n<p>1.Arthus-reaction</p>\n<p>2.Serum-sickness</p>\n</td>\n<td>Hours to days</td>\n<td>IgG, IgM, leucocytes</td>\n</tr>\n<tr>\n<td><strong>Type IV</strong>: <br>Delayed hypersensitivity</td>\n<td>\n<p>1. Tuberculin</p>\n<p>2. Contact dermatitis</p>\n</td>\n<td>Hours to days</td>\n<td>T cells, lymphokines, macrophages.</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(636, 'Microbiology,', 'PM2352. Chronic granulomatous disease', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Chronic granulomatous disease&nbsp;</strong>(CGD) is due to the defect in <strong>NADPH oxidase</strong>. This interferes with the killing of bacteria by oxidative burst. Hence, the patients are at increased risk of infection from catalase-positive bacteria.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/e4da3044afb34df1b36f9873fda741a7x1280x1775.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;It can be:</p>\n<ul>\n<li><strong>X</strong>&nbsp;<strong>linked -</strong>&nbsp;Defect in one of the membrane-bound components (gp91phox) or</li>\n<li><strong>Autosomal recessive -</strong>&nbsp;Cytoplasmic components (p47phox and p67phox)</li>\n</ul>\n<p>Diagnosis of CGD includes:</p>\n<ul>\n<li aria-level=\1\><strong>Nitroblue tetrazolium test</strong>&nbsp;- This is the standard for phagocyte oxidase activity measurement. The colorless compound NBT is reduced to blue formazan by the activity of the functional phox enzyme system. Normally, more than 95% of the granulocytes will be positive. The NBT is&nbsp;<strong>negative in CGD</strong>&nbsp;due to&nbsp;<strong>absence of phox enzyme activity</strong>.</li>\n<li aria-level=\1\><strong>Dihydrorhodamine (DHR) test&nbsp;</strong>-&nbsp;The most accurate diagnostic test for CGD. Normally functioning phagocytic cells oxidize DHR to the strongly fluorescent compound rhodamine which is quantified by flow cytometry.</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(637, 'Microbiology', 'PM2187. Serological tests in microbiology', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Precipitation test:</strong></p>\n<ul>\n<li>Ascolis thermoprecipitin test: Anthrax</li>\n<li>Streptococci grouping: Lancefield technique</li>\n<li>VDRL test for syphilis (slide flocculation test)</li>\n<li>Kahn test for syphilis (tube flocculation test)</li>\n<li>Elek gel precipitation test for C.diphtheria</li>\n</ul>\n<p><strong>Agglutination test:</strong></p>\n<ul>\n<li>Widal test- typhoid</li>\n<li>Weil- Felix reaction- Typhus fever</li>\n<li>Streptococcus MG agglutination test- Mycoplasma pneumoniae</li>\n<li>Paul Bunnel test- Infectious mononucleiosis</li>\n<li>Coombs test</li>\n<li>Rose Waaler test- Rheumatoid arthritis</li>\n<li>Microscopic Agglutination test-&nbsp;Leptospirosis</li>\n<li>Anti streptolysin O test</li>\n</ul>\n<p><strong>Complement fixation test:</strong></p>\n<ul>\n<li>Wasserman reaction(syphilis)</li>\n<li>Treponema pallidum immobilization test</li>\n</ul>\n<p><strong>Neutralization test:</strong></p>\n<ul>\n<li>Schik test for diptheria</li>\n<li>Anti streptolysin O test</li>\n<li>Naglers reaction (clostridium perfringens)</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(638, 'Medicine,', 'PM2137. Transplant Rejection and Hypersensitivity', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\419\>\n<tbody>\n<tr>\n<td>Type of Rejection&nbsp;</td>\n<td>Type of Hypersensitivity</td>\n</tr>\n<tr>\n<td colspan=\2\>\n<p><strong>Host vs Graft</strong></p>\n</td>\n</tr>\n<tr>\n<td>Hyperacute</td>\n<td>Type 2</td>\n</tr>\n<tr>\n<td>Acute</td>\n<td>Type 2 and 4</td>\n</tr>\n<tr>\n<td>Chronic</td>\n<td>Type 2 and 4</td>\n</tr>\n<tr>\n<td colspan=\2\><strong>Graft vs Host</strong></td>\n</tr>\n<tr>\n<td>Graft vs host reaction</td>\n<td>Type 4</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p>The image given below explains the pathophysiology of hyperacute graft rejection (<strong>type II</strong> hypersensitivity).&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/988706e6c52640cabe45b6c598d1b709x1280x2089.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>The image below shows the mechanism of<strong> type IV</strong> hypersensitivity in graft rejection.&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/740d6c7dadda46d38620f36124adea54x1280x4218.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(639, 'Pathology,', 'PM2056. Properties of immunoglobulin classes', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>IgG:</strong></p>\n<ul>\n<li>Half-life- 23 days</li>\n<li>Appears late, indicates <strong>chronic infection</strong></li>\n<li>It is the only maternal Ig to cross the placenta.</li>\n<li>Has 4 subclasses.</li>\n<li>Causes complement fixation, precipitation, and neutralization of toxins and virus.</li>\n</ul>\n<p><strong>IgM</strong>:</p>\n<ul>\n<li>Half-life- 5 days</li>\n<li>Has the highest molecular weight and is a pentamer.</li>\n<li>Polysaccharides and lipids mainly stimulate IgM secretion.</li>\n<li>Intravascular distribution.</li>\n<li>Appears early, indicates <strong>recent infection.</strong></li>\n<li>Acts<strong>&nbsp;</strong>as surface immunoglobulin on the B cells.</li>\n<li>It causes agglutination, hemolysis, complement fixation and opsonization.</li>\n</ul>\n<p><strong>IgD:</strong></p>\n<ul>\n<li>Half-life- 2-8 days.</li>\n<li>Acts as a surface immunoglobulin on the B cells.</li>\n<li>Serves as recognition receptors for antigens.</li>\n</ul>\n<p><strong>IgE:</strong></p>\n<ul>\n<li>Half-life- 1-5 days</li>\n<li>Causes type I hypersensitivity.</li>\n<li>It is the only heat labile antibody.</li>\n<li>Levels are raised in helminthic infections.</li>\n</ul>\n<p><strong>IgA:</strong></p>\n<ul>\n<li>Half-life- 6 days</li>\n<li>It is of 2 types- Serum IgA and Secretory IgA&nbsp;</li>\n<li>Secretory IgA (A dimer) results in mucosal immunity.</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(640, 'Medicine,', 'PM1923. Complement deficiency and associated diseases', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<table width=\490\>\n<tbody>\n<tr>\n<td>Complement protein</td>\n<td>Effects of deficiency</td>\n</tr>\n<tr>\n<td>C1, C2, C4</td>\n<td>Immune- complex disease (SLE)</td>\n</tr>\n<tr>\n<td>C3, Factor I</td>\n<td>Susceptibility to capsulated bacteria</td>\n</tr>\n<tr>\n<td>C5-C9</td>\n<td>Susceptibility to Neisseria</td>\n</tr>\n<tr>\n<td>Factor D. properdin (factor P)</td>\n<td>Susceptibility to capsulated bacteria and Neisseria.</td>\n</tr>\n<tr>\n<td>\n<p>DAF (Decay accelerating factor)</p>\n<p>CD 59</p>\n</td>\n<td>Paroxysmal nocturnal hemoglobinuria</td>\n</tr>\n<tr>\n<td>C1-inhibitor</td>\n<td>Hereditary angioneurotic edema</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(641, 'Microbiology,', 'PM1559. Examples of Type IV Hypersensitivity', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Type IV Hypersensitivity Reactions&nbsp;</p>\n<ul>\n<li>Tuberculin reaction</li>\n<li>Lepromin test</li>\n<li>Multiple sclerosis</li>\n<li>Chronic graft rejection</li>\n<li>Contact dermatitis</li>\n<li>Sarcoidosis</li>\n<li>Primary biliary cirrhosis</li>\n<li>Type I lepra reaction</li>\n<li>Crohn’s disease</li>\n<li>Psoriasis</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/f2bf7fce299546679d2c2cac15eca714x1280x4218.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(642, 'Microbiology,', 'PM1557. Types of Type II Antibody-Mediated Hypersentivity', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>There are 3 mechanisms involved in type 2 hypersensitivity reactions-&nbsp;</p>\n<ol>\n<li>Antibody-mediated <strong>cellular dysfunction-</strong>&nbsp;<strong>antibodies</strong>&nbsp;react with antigens present on <strong>cell surfaces</strong> or in the <strong>extracellular matrix</strong> cause disease by <strong>destroying</strong> these <strong>cells,</strong> triggering <strong>inflammation,</strong> or interfering with normal functions<br>\n<ol>\n<li>Myasthenia gravis (against acetylcholine receptor)</li>\n<li>Graves’ disease (against TSH receptor)</li>\n<li>Pemphigus Vulgaris (against epidermal cadherin)</li>\n<li>Pernicious anemia (against intrinsic factor)</li>\n<li>Insulin resistant diabetes (against insulin receptor</li>\n</ol>\n</li>\n<li>Antibody-mediated <strong>opsonization</strong> and <strong>phagocytosis</strong> -<strong>antibodies</strong> may deposit in <strong>tissues</strong> or <strong>blood vessels,</strong> and elicit an acute <strong>inflammatory</strong> reaction by activating <strong>complement</strong>&nbsp;and<strong> Recruiting Phagocytes</strong>\n<ol>\n<li>Transfusion reactions</li>\n<li>Erythroblastosis fetalis</li>\n<li>Autoimmune hemolytic anemia</li>\n<li>Autoimmune thrombocytopenic purpura</li>\n</ol>\n</li>\n<li><strong>Complement</strong> and <strong>Fc receptor-mediated inflammation-</strong>&nbsp;<strong>antibodies</strong>&nbsp;may deposit in&nbsp;<strong>tissues</strong>&nbsp;or&nbsp;<strong>blood vessels,</strong>&nbsp;and elicit an acute&nbsp;<strong>inflammatory</strong>&nbsp;reaction by activating&nbsp;<strong>complement</strong>\n<ol>\n<li>Goodpasture syndrome</li>\n<li>Vasculitis due to ANCA</li>\n<li>Acute rheumatic fever</li>\n<li>Vascular rejection in organ graft&nbsp;</li>\n</ol>\n</li>\n</ol></div><!---->\n      <!---->\n    </div><!---->'),
(643, 'Microbiology,', 'PM1556. Mechanisms of Hypersensitivity Reactions', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\746\>\n<tbody>\n<tr>\n<td>\n<p><strong>Type</strong></p>\n</td>\n<td>\n<p><strong>Immune Mechanisms</strong></p>\n</td>\n<td>\n<p><strong>Histopathological lesions</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Immediate </strong><br><strong>&nbsp;hypersensitivity<br>(type I)<br></strong></p>\n<ul>\n<li>Anaphylaxis</li>\n<li>Allergies</li>\n<li>Bronchial asthma</li>\n<li>Atopic dermatitis</li>\n</ul>\n</td>\n<td>\n<p>Production of IgE antibody →immediate release of vasoactive amines from mast cells</p>\n</td>\n<td>\n<p>Vascular dilatation, edema, smooth muscle contraction, mucus production, tissue injury, inflammation</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Antibody-mediated hypersensitivity<br>(type II)<br></strong></p>\n<ul>\n<li>Autoimmune hemolytic anemia</li>\n<li>Goodpasture syndrome</li>\n</ul>\n</td>\n<td>\n<p>Production of IgG, IgM → binds to antigen on target cell or tissue → phagocytosis or lysis of target cell by activated complement or Fc receptors; recruitment of leukocytes</p>\n</td>\n<td>\n<p>Phagocytosis and lysis of cells; inflammation;</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Immune complex-mediated hypersensitivity<br> (type III)<br></strong></p>\n<ul>\n<li>SLE</li>\n<li>Serum sickness</li>\n<li>Arthus reaction</li>\n</ul>\n</td>\n<td>\n<p>Deposition of antigen-antibody complexes → complement activation →recruitment of leukocytes by complement products and Fc receptors → release of enzymes and other toxic molecules</p>\n</td>\n<td>\n<p>Inflammation, necrotizing vasculitis (fibrinoid necrosis)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Cell-mediated hypersensitivity<br>(type IV)</strong></p>\n<ul>\n<li>Contact dermatitis</li>\n<li>Multiple sclerosis</li>\n<li>Type 1 diabetes</li>\n<li>Tuberculosis</li>\n</ul>\n</td>\n<td>\n<p>Activated T lymphocytes →</p>\n<p>1. Release of cytokines, inflammation, and macrophage activation</p>\n<p>2. T-cell-mediated cytotoxicity</p>\n</td>\n<td>\n<p>Perivascular cellular infiltrates; edema; granuloma formation; cell destruction</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/f55cab6c8fd44a7abfe8a663dde3980ex1280x2567.JPEG\><img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/b2a98b94d6a9479b8e6e885ee59366cex1280x2089.JPEG\><img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/d92e092ef5874477a6da091ca3b1234ax1280x1360.JPEG\><img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/56fc1dfb49434818b5057534a97d6621x1280x4218.JPEG\><!---->\n      </div><!---->\n    </div><!---->');
INSERT INTO db_pearl_m (pid, subject, pearl_title, pearl_desc) VALUES
(644, 'Pathology,', 'PM1555. Disorders Caused by Immediate Hypersensitivity', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\231\>\n<p><strong>Clinical Syndrome</strong></p>\n</td>\n<td width=\231\>\n<p><strong>Clinical and Pathologic Manifestations</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Anaphylaxis (may be caused by drugs, bee sting, food)</p>\n</td>\n<td width=\231\>\n<p>Fall in blood pressure (shock) caused by vascular dilation; airway obstruction due to laryngeal edema</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Bronchial asthma</p>\n</td>\n<td width=\231\>\n<p>Airway obstruction caused by bronchial smooth muscle hyperactivity; inflammation and tissue injury caused by late-phase reaction</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Allergic rhinitis, sinusitis (hay fever)</p>\n</td>\n<td width=\231\>\n<p>Increased mucus secretion; inflammation of upper airways, sinuses</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Food allergies</p>\n</td>\n<td width=\231\>\n<p>Increased peristalsis due to contraction of intestinal muscles</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(645, 'Microbiology,', 'PM1552. MHC Classes I and II', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>MHC Class I</strong></p>\n</td>\n<td>\n<p><strong>MHC Class II</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Expressed on all <strong>nucleated cells&nbsp;</strong>(except sperms) and <strong>platelets</strong></p>\n</td>\n<td>\n<p>Expressed on <strong>antigen-presenting cells</strong>:&nbsp;macrophages, B lymphocytes, dendritic cells</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Consist of a <strong>polymorphic α</strong>, heavy-chain, linked non-covalently to <strong>β2-microglobulin</strong></p>\n</td>\n<td>\n<p>Heterodimer consisting of non-covalently associated <strong>α</strong> and<strong> β chains, </strong>both of which are <strong>polymorphic.</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>α chains are encoded by three genes-&nbsp;<em><strong>HLA-A, HLA-B, </strong></em><strong>and </strong><em><strong>HLA-C</strong></em></p>\n</td>\n<td>\n<p>Encoded by <strong>HLA-D,</strong> with&nbsp;subregions:<strong>&nbsp;</strong>HLA-DP, HLA-DQ, and HLA-DR.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Display antigenic peptides that are derived from&nbsp;<strong>viral</strong> and<strong> tumor proteins</strong></p>\n</td>\n<td>\n<p>Present antigens that are internalized into vesicles, and derived from <strong>extracellular microbes</strong>&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Class I complexes are&nbsp;recognized by<strong> CD8+ T cells</strong></p>\n<p>&nbsp;</p>\n</td>\n<td>\n<p>Class II complexes are&nbsp;recognized by<strong> CD4+ T cells</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Endogenous peptide</strong> is the type of antigen presented to&nbsp;CD8+ T cells</p>\n</td>\n<td><strong>Exogenous antigen</strong> is the type of antigen presented to CD4+ T cells&nbsp;</td>\n</tr>\n<tr>\n<td>\n<p>Presents antigen via the<strong> cytosolic pathway</strong></p>\n</td>\n<td>Presents antigen via the&nbsp;<strong>endocytic pathway&nbsp;</strong></td>\n</tr>\n<tr>\n<td>\n<p>Antigen binding site is present <strong>between α1/α2 groove</strong></p>\n</td>\n<td>Antigen binding site is present <strong>between α1/β1 groove&nbsp;</strong></td>\n</tr>\n<tr>\n<td>\n<p>α3 binds to CD8 molecules on cytotoxic T cells</p>\n</td>\n<td>β2 binds to CD4 on helper T cells</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(646, 'Pathology,', 'PM1551. T Helper cells- Types and functions', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Cell Type </strong></p>\n</td>\n<td>\n<p><strong>T helper cell 1</strong></p>\n</td>\n<td>\n<p><strong>T helper 2 </strong></p>\n</td>\n<td>\n<p><strong>T helper 17</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Major cytokines produced</strong></p>\n</td>\n<td>\n<p>IFN-γ</p>\n</td>\n<td>\n<p>IL-4, IL-5, IL-13</p>\n</td>\n<td>\n<p>IL-17, IL-22</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Cytokines that induce this subset</strong></p>\n</td>\n<td>\n<p>IFN-γ, IL-12</p>\n</td>\n<td>\n<p>IL-4</p>\n</td>\n<td>\n<p>TGF-β, IL-6, IL-1, IL-23</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Immunological reactions triggered</strong></p>\n</td>\n<td>\n<p><strong>Macrophage activation,</strong> stimulation of IgG antibody production</p>\n</td>\n<td>\n<p>Stimulation of IgE production, activation of mast cells and <strong>eosinophils</strong></p>\n</td>\n<td>\n<p>Recruitment of <strong>neutrophils,</strong> monocytes</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Host defense against</strong></p>\n</td>\n<td>\n<p>Intracellular microbes</p>\n</td>\n<td>\n<p>Helminthic parasites</p>\n</td>\n<td>\n<p>Extracellular bacteria, fungi</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Role in disease</strong></p>\n</td>\n<td>\n<p>Autoimmune and other chronic inflammatory diseases (such as IBD, psoriasis, granulomatous inflammation)</p>\n</td>\n<td>\n<p>Allergies</p>\n</td>\n<td>\n<p>Autoimmune and other chronic inflammatory diseases (such as IBD, psoriasis, MS)</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(647, 'Microbiology,', 'PM1548. Differences – CD 4 and CD8 cells', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\231\>\n<p><strong>CD 4 cells</strong></p>\n</td>\n<td width=\231\>\n<p><strong>CD 8 cells</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p><strong>60% of mature T cells</strong></p>\n</td>\n<td width=\231\>\n<p>30% of mature T cells</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p><strong>Helper T cell</strong></p>\n</td>\n<td width=\231\>\n<p>Cytotoxic/Killer T cell.</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p><strong>Secrete cytokines and help macrophages and B cells to fight infections</strong></p>\n</td>\n<td width=\231\>\n<p>Destroy host cells having microbes like viruses and tumor cells.</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p><strong>Bind to class II major histocompatibility complex (MHC) molecules that are displaying antigen</strong></p>\n</td>\n<td width=\231\>\n<p>Bind to class I MHC molecules.</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/ddf1469437184354bbd18b29260e71fex1280x1739.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(648, 'Paediatrics,', 'PM0601. Inactivated and Live Attenuated Polio Vaccines', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Characteristic</strong></td>\n<td>\n<p><strong>Inactivated (</strong><strong>IPV/Salk)</strong></p>\n</td>\n<td>\n<p><strong>Attenuated </strong><strong>(</strong><strong>OPV/Sabin)</strong></p>\n</td>\n</tr>\n<tr>\n<td>Prevents disease</td>\n<td>Yes</td>\n<td>Yes</td>\n</tr>\n<tr>\n<td>Induces humoral IgG</td>\n<td>Yes</td>\n<td>Yes</td>\n</tr>\n<tr>\n<td>Induces intestinal sIgA</td>\n<td>No</td>\n<td>Yes</td>\n</tr>\n<tr>\n<td>Prevents intestinal colonization</td>\n<td>No</td>\n<td>Yes</td>\n</tr>\n<tr>\n<td>Secondary (herd) protection by spread to others</td>\n<td>No</td>\n<td>Yes</td>\n</tr>\n<tr>\n<td>Reverts to virulence</td>\n<td>No</td>\n<td>Yes, very rarely</td>\n</tr>\n<tr>\n<td>May cause disease in immunocompromised</td>\n<td>No</td>\n<td>Yes</td>\n</tr>\n</tbody>\n</table>\n<p>Salk has K, so it is killed.&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(649, 'Pathology,', 'PM0536. Primary immunodeficiency disorders', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Affected component</strong><strong>&nbsp;of the immune system</strong></p>\n</td>\n<td>\n<p><strong>Disorder</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Humoral immunity:&nbsp;</strong>Problems with<strong> B cells</strong>&nbsp;and their <strong>antibody&nbsp;</strong><strong>production</strong></p>\n</td>\n<td>\n<ul>\n<li>Common variable immunodeficiency</li>\n<li>Selective immunoglobulin deficiency, such as IgA deficiency</li>\n<li>Transient hypogammaglobulinemia of infancy</li>\n<li>X-linked agammaglobulinemia</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Cellular immunity:&nbsp;</strong>Problems with<strong> T cells&nbsp;</strong></p>\n</td>\n<td>\n<ul>\n<li>Chronic mucocutaneous candidiasis</li>\n<li>DiGeorge syndrome</li>\n<li>X-linked lymphoproliferative syndrome</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Combined </strong>humoral and cellular immunity:&nbsp;Problems with<strong> B and T cells</strong></p>\n</td>\n<td>\n<ul>\n<li>Ataxia-telangiectasia</li>\n<li>Hyperimmunoglobulinemia M syndrome</li>\n<li>Severe combined immunodeficiency</li>\n<li>Wiskott-Aldrich syndrome</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Phagocytes:&nbsp;</strong>Problems with the<strong> movement or killing&nbsp;</strong></p>\n</td>\n<td>\n<ul>\n<li>Chronic granulomatous disease</li>\n<li>Chédiak-Higashi syndrome&nbsp;(rare)</li>\n<li>Tuftsin deficiency</li>\n<li>Cyclic neutropenia</li>\n<li>Leukocyte adhesion defects</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Complement </strong>proteins:<strong>&nbsp;</strong></p>\n<p><strong>Deficiency of complement </strong>proteins</p>\n</td>\n<td>\n<ul>\n<li>Complement component 1 (C1) inhibitor deficiency (hereditary angioedema)</li>\n<li>Terminal complement deficiency (C5-C9): Recurrent Neisseria infections</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(650, 'Microbiology', 'PM0509. Various Types of Chemokines Based on their Structure', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>1.&nbsp;&nbsp; </strong><strong>CXC chemokines </strong>(α-chemokines): They are <strong>separated by one amino acid</strong>, represented by X. e.g., interleukin-8 (CXCL8)</p>\n<p><strong>2. CC chemokines </strong>(β-chemokines): They have a total of four cysteines, of which <strong>two are adjacent cysteines </strong>(amino acids), near their amino terminus. e.g., CC chemokine ligands (CCL)-1 to 28</p>\n<p><strong>3. C chemokines </strong>(γ-chemokines): They have <strong>only two cysteines</strong>; one N-terminal cysteine and one cysteine downstream. e.g., XCL1 (lymphotactin-α) and XCL2 (lymphotactin-β).</p>\n<p><strong>4. CX3C chemokines </strong>(d-chemokines): They have <strong>three amino acids between the two cysteines</strong> and are termed CX3C chemokine. e.g., fractalkine (or CX3CL1), acts as a&nbsp;chemoattractant and as an adhesion molecule.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/255f7a51de0c44fa93b73119050d6973x1280x1316.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(651, 'Pathology,', 'PM0508. Modified monocytes/macrophages in various tissues', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>The following table shows various sites and the designation of the modified monocytes/macrophages:</p>\n<table width=\278\>\n<tbody>\n<tr>\n<td>\n<p><strong>Site</strong></p>\n</td>\n<td>\n<p><strong>Macrophage designation</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Peripheral blood</p>\n</td>\n<td>\n<p>Monocytes</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Placenta</p>\n</td>\n<td>\n<p>Hofbauer cells</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Liver</p>\n</td>\n<td>\n<p>Kupffer cells</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Brain</p>\n</td>\n<td>\n<p>Microglial cells</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Kidney</p>\n</td>\n<td>\n<p>Mesangial cells</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Lungs</p>\n</td>\n<td>\n<p>Alveolar macrophages</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Bone</p>\n</td>\n<td>\n<p>Osteoclasts</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Connective tissue</p>\n</td>\n<td>\n<p>Histiocytes</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(652, 'Dermatology,', 'PM2456. Sexually transmitted infections', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Genital herpes</strong>- caused by <strong>herpes simplex virus</strong> (human herpesvirus 1 and 2).</p>\n<ul>\n<li>It appears as <strong>blisters</strong> (usually multiple) that lead to ulcer formation on breaking.</li>\n<li>The ulcers are <strong>painful</strong>.</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/8ba0cafb8f724e8caf99ab7778d022acx630x761.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Granuloma inguinale&nbsp;</strong>/ Donovanosis- caused by&nbsp;<strong>Klebsiella granulomatis</strong>&nbsp;(formerly known as Calymmatobacterium granulomatis).</p>\n<ul>\n<li>It usually causes <strong>beefy red genital ulcers</strong> that may bleed on touch.</li>\n<li>The ulcers are usually <strong>painless</strong>.</li>\n<li>Lymphadenopathy is usually absent but subcutaneous granulomas cause <strong>pseudo buboes</strong>.&nbsp;</li>\n<li>Microscopy shows <strong>Donovan bodies</strong> with closed safety pin appearance</li>\n</ul>\n<p>The given images show genital ulcer in granuloma inguinale:</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/4aaa8224b2e7443e94743e1015a44778x560x358.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Chancroid</strong>/ Soft chancre-&nbsp;caused by&nbsp;<strong>Haemophilus ducreyi</strong>.</p>\n<ul>\n<li>Causes genital ulcers that are <strong>non indurated</strong> with undermined edges.</li>\n<li>The ulcer is <strong>painful</strong>.</li>\n<li>Multiple/ Kissing ulcers develop due to autoinoculation from the primary ulcer.</li>\n<li>It usually causes tender unilateral<strong>&nbsp;inguinal buboes</strong>.&nbsp;</li>\n<li>Microscopy- School of fish/Railroad track appearance</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/875f994ab0a44d3a8d26f376c6b54874x597x313.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Syphilitic chancre</strong>/ Hard chancre- caused by <strong>Treponema pallidum</strong>.</p>\n<ul>\n<li>It presents as a <strong>small, button-like, indurated ulcer</strong> that is usually round/oval.</li>\n<li>The ulcer is <strong>painless</strong>.</li>\n<li>Causes <strong>painless lymphadenopathy</strong>.</li>\n<li>Microscopy- Spirochetes seen on dark ground illumination.</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/03d21e32213a42c9ab3487da627b053ex336x288.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Lymphogranuloma venereum</strong>- caused by&nbsp;<strong>Chlamydia trachomatis</strong>.</p>\n<ul>\n<li>It usually presents with a transient single <strong>shallow ulcer</strong> that <strong>heals rapidly</strong> and usually goes unnoticed.</li>\n<li>The secondary stage presents with <strong>tender&nbsp;inguinal buboes</strong>.</li>\n<li><strong>Groove sign</strong> is seen when the lymph nodes above and below the inguinal ligament are enlarged leading to the appearance of a groove.</li>\n</ul>\n<p>The given images show the ulcer and the groove sign seen in LGV:</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/6faad0dd79004ce4898a0d4ec94391e9x495x711.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/be01c14fe5b34f6fb923eca0f40d42cex253x357.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Gonorrhea</strong>- caused by <strong>Neisseria gonorrhoeae</strong>.</p>\n<ul>\n<li>It leads to <strong>purulent inflammation</strong> of the genital mucous membranes.</li>\n<li>It may present as&nbsp;urethritis, cervicitis, epididymo‐orchitis, or pelvic inflammatory disease.</li>\n<li>The usual presentation is <strong>acute urethritis</strong> with complaints of burning dysuria and purulent discharge.</li>\n</ul>\n<p>The given image shows purulent genital inflammation due to gonorrhea:</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/f4fd185c143045fe944bb99f1299a4bbx379x412.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(653, 'Dermatology,', 'PM2455. Skin and soft tissue lesions caused by Staphylococcus', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Staphylococcus causes a variety of skin and soft tissue infections. These include:</p>\n<p>a. <strong>Furuncle</strong>/boil- Infection of the hair follicle.</p>\n<p>The given image shows a furuncle:</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/e85d867deb494725875ce9b6fcc453d4x1024x683.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<p>b. <strong>Carbuncle</strong>: A cluster of several furuncles:</p>\n<p>The given image shows a carbuncle:</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/87a5f9e1f77542b29243f0335be022b5x1280x960.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<p>c. <strong>Bullous impetigo</strong>: Results in large blisters called bullae, usually seen in areas with skin folds such as armpits, groin, buttocks, etc.</p>\n<p>The given image shows ruptured bullous impetigo:</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/281f980ab1544bf8ba1a75e41288ebd0x1280x1706.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<p>d. <strong>Cellulitis</strong>: It is a rapidly spreading infection of the skin and subcutaneous tissue, usually associated with wounds, incisions, burns, etc.</p>\n<p>The given image shows cellulitis following an abrasion. The red streaks indicate the involvement of the lymphatics:</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/03beb37fad62438c82351579ddb30787x1280x960.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<p>e. <strong>Staphylococcal scalded skin syndrome</strong>: Caused by exfoliating toxin that causes peeling of the skin.</p>\n<p>The given image shows SSSS:</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/d2c892194a074752b3318b1d2172c9ffx363x274.PNG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<p>Note: The conditions shown above are most commonly caused by Staphylococcus. It is important to remember that they may also be caused by other bacteria. Cellulitis is more commonly caused by Streptococcus.</p></div><!---->\n      <!---->\n    </div><!---->'),
(654, 'Dermatology,', 'PM2454. Skin and soft tissue lesions caused by Streptococcus', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Streptococcus causes a variety of skin and soft tissue infections. These include:</p>\n<p>a. <strong>Impetigo</strong>- Infection of superficial layers of the skin. It presents as superficial vesicles which break down and are then encrusted by honey-colored scab. It usually occurs in children.</p>\n<p>The given image shows impetigo:</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/402a0b1bedb94b8bb54931282ab18a5ax508x200.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<p>b.&nbsp;<strong>Erysipelas</strong>-</p>\n<ul>\n<li>Infection of the superficial epidermis.\n<ul>\n<li>It presents as an erythematous raised lesion with sharply demarcated edges that differentiate it from the surrounding skin.\n<ul>\n<li>The given image shows erysipelas- note the clear demarcation at the nasolabial folds seen in facial erysipelas.</li>\n</ul>\n</li>\n</ul>\n</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/af1d1306363b448490fd435fc60ee51bx1280x1211.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<p>c.&nbsp;<strong>Cellulitis</strong>- Infection of the skin and subcutaneous tissue. It presents with erythema and edema. It can be differentiated from erysipelas by - i. Erysipelas is a raised lesion whereas cellulitis is not, ii. Cellulitis is poorly demarcated from the surrounding skin whereas erysipelas is clearly demarcated.</p>\n<p>The given image shows cellulitis:</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/c81644e4fd8c407ea15b8e0f59c2f0bfx1280x960.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<p>d.&nbsp;<strong>Necrotizing fasciitis</strong>- Infection involving the fascial planes. It presents with dark areas of necrosis, erythema, and edema.</p>\n<p>The given image shows necrotizing fasciitis with areas of erythema and necrosis:</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/23196395a943480fae2f0cfdcac858dbx1280x734.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<p>d.&nbsp;<strong>Myositis and myonecrosis</strong>- Infection of the muscle.</p>\n<p>The given image shows necrotizing myositis:</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/0e639e386c9d433e985751e75051ca46x996x690.PNG\><!---->\n      </div><!---->\n    </div><!---->'),
(655, 'Microbiology', 'PM2361. Runyon classification of Non-tubercululous Mycobacteria', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Group</strong></td>\n<td><strong>Pigment</strong></td>\n<td><strong>Examples</strong></td>\n<td><strong>Disease</strong></td>\n</tr>\n<tr>\n<td>\n<p><strong>I</strong></p>\n<p>Photo-chromogens</p>\n</td>\n<td>yellow-orange on light&nbsp;</td>\n<td>\n<p>1. M. kansaii</p>\n<p>2. M. marinum</p>\n</td>\n<td>\n<p>1. Similar to TB</p>\n<p>2. Swimming pool granuloma</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>II</strong></p>\n<p>Scoto-chromogens</p>\n</td>\n<td>yellow or orange in light or dark&nbsp;</td>\n<td>\n<p>M. scrofulaceum</p>\n<p>M. gordonae</p>\n</td>\n<td>Cervical adenitis</td>\n</tr>\n<tr>\n<td>\n<p><strong>III</strong></p>\n<p>Non photo-chromogens</p>\n</td>\n<td>\n<p>no pigment</p>\n</td>\n<td>\n<p>M. avium intracellulare complex (MAC),</p>\n<p>M. ulcerans,</p>\n<p>M. xenopi&nbsp;</p>\n</td>\n<td>Similar to TB, esp. in AIDS</td>\n</tr>\n<tr>\n<td>\n<p><strong>IV</strong></p>\n<p>Rapid growers</p>\n</td>\n<td>no pigment</td>\n<td>\n<p>M. fortuitum</p>\n<p>M. chelonei</p>\n</td>\n<td>Soft tissue, lung, bone, CNS, eye infections</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(656, 'Medicine,', 'PM2350. Food Poisoning', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td><strong>Organism</strong></td>\n<td><strong>Symptoms</strong></td>\n<td><strong>Common food source</strong></td>\n</tr>\n<tr>\n<td colspan=\3\><strong>1–6 hrs incubation&nbsp;period</strong>&nbsp;&nbsp;</td>\n</tr>\n<tr>\n<td><em>Staphylococcus aureus</em></td>\n<td>Nausea, vomiting, diarrhea</td>\n<td>Ham, poultry, potato, cream pastries, mayonnaise</td>\n</tr>\n<tr>\n<td><em>Bacillus cereus</em></td>\n<td>Nausea, vomiting, diarrhea</td>\n<td>Fried rice</td>\n</tr>\n<tr>\n<td colspan=\3\><strong>8–16 hrs incubation&nbsp;period</strong>&nbsp;&nbsp;</td>\n</tr>\n<tr>\n<td><em>Clostridium perfringes</em></td>\n<td>Abdominal cramps, diarrhea</td>\n<td>Beef, poultry, legume, gravies</td>\n</tr>\n<tr>\n<td><em>Bacillus cereus</em></td>\n<td>Abdominal cramps, diarrhea</td>\n<td>Meat, vegetables, dried beans, cereals</td>\n</tr>\n<tr>\n<td colspan=\3\><strong>&gt;16 hrs incubation period</strong>&nbsp;&nbsp;</td>\n</tr>\n<tr>\n<td><em>Vibrio cholerae</em></td>\n<td>Watery diarrhea</td>\n<td>Shellfish, water</td>\n</tr>\n<tr>\n<td><em>Salmonella</em> species</td>\n<td>Inflammatory diarrhea</td>\n<td>Beef, poultry, eggs</td>\n</tr>\n<tr>\n<td>Enterotoxigenic <em>Escherichia coli</em></td>\n<td>Watery diarrhea</td>\n<td>Salads, cheese, meats</td>\n</tr>\n<tr>\n<td>Enterohemorrhagic <em>E. coli</em></td>\n<td>Bloody diarrhea</td>\n<td>Raw milk, raw vegetables, apple juice</td>\n</tr>\n<tr>\n<td><em>Campylobacter jejuni</em></td>\n<td>Inflammatory diarrhea</td>\n<td>Poultry, raw milk</td>\n</tr>\n<tr>\n<td><em>Shigella</em> species</td>\n<td>Dysentery</td>\n<td>Potato, egg salad, lettuce</td>\n</tr>\n<tr>\n<td><em>Vibrio parahaemolyticus</em></td>\n<td>Dysentery</td>\n<td>Molluscs, crustaceans</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(657, 'Medicine,', 'PM2349. Comparison between Acute Rheumatic Fever(ARF) and Post streptococcal glomerulonephritis(PSGN)', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>&nbsp;</td>\n<td><strong>Acute Rheumatic Fever</strong></td>\n<td><strong>Post streptococcal glomerulonephritis</strong></td>\n</tr>\n<tr>\n<td>\n<p><strong>Site of infection</strong></p>\n</td>\n<td>Throat</td>\n<td>Throat or skin</td>\n</tr>\n<tr>\n<td><strong>Prior sensitisation</strong></td>\n<td>Essential</td>\n<td>Not necessary</td>\n</tr>\n<tr>\n<td><strong>Serotype of S.pyogenes</strong></td>\n<td>Any</td>\n<td>Pyodermal types-49,53-55,59-61 and pharyngitis strains 1 and 12</td>\n</tr>\n<tr>\n<td><strong>Immune response</strong></td>\n<td>Marked</td>\n<td>Moderate</td>\n</tr>\n<tr>\n<td><strong>Complement level</strong></td>\n<td>Unaffected</td>\n<td>Decreased</td>\n</tr>\n<tr>\n<td><strong>Genetic susceptibility</strong></td>\n<td>Present</td>\n<td>Not known</td>\n</tr>\n<tr>\n<td><strong>Repeated attacks</strong></td>\n<td>Common</td>\n<td>Absent</td>\n</tr>\n<tr>\n<td>\n<p><strong>Penicillin prophylaxis</strong></p>\n</td>\n<td>Essential</td>\n<td>Not indicated</td>\n</tr>\n<tr>\n<td><strong>Course</strong></td>\n<td>Progressive or static</td>\n<td>Spontaneous resolution</td>\n</tr>\n<tr>\n<td><strong>Prognosis</strong></td>\n<td>Variable</td>\n<td>Good</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(658, 'Microbiology', 'PM2345. Comparison between Meningococci and Gonococci', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Properties</strong></td>\n<td><strong>Meningococcus</strong></td>\n<td><strong>Gonococcus</strong></td>\n</tr>\n<tr>\n<td>Carbohydrates fermented</td>\n<td>Glucose and Maltose&nbsp;</td>\n<td>Only Glucose</td>\n</tr>\n<tr>\n<td>Location</td>\n<td>Both intra- and extracellular</td>\n<td>Predominantly intracellular&nbsp;</td>\n</tr>\n<tr>\n<td>Spread</td>\n<td>Respiratory or oral secretions&nbsp;</td>\n<td>Sexual or perinatal transmission</td>\n</tr>\n<tr>\n<td>Plasmids</td>\n<td>Rare&nbsp;</td>\n<td>Has plasmids that code for drug resistance</td>\n</tr>\n<tr>\n<td>\n<p>Vaccine</p>\n</td>\n<td>\n<p>Capsule-based polysaccharide and conjugate vaccines available&nbsp;</p>\n<p>(except for Type B - subcapsular antigen-based vaccine is available)&nbsp;</p>\n</td>\n<td>No vaccine due to antigenic variation of pilus proteins</td>\n</tr>\n<tr>\n<td>\n<p>Capsule</p>\n</td>\n<td>\n<p>Present</p>\n</td>\n<td>Absent</td>\n</tr>\n<tr>\n<td>\n<p>Shape</p>\n</td>\n<td>\n<p>Lens/half-moon&nbsp;</p>\n</td>\n<td>Kidney&nbsp;</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/48d2ba254fc0410f925d202060935357x1280x769.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(659, 'Microbiology', 'PM2343. Oxygen Requirements of Bacteria', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Type of organism</strong></p>\n</td>\n<td><strong>Feature</strong></td>\n<td><strong>Examples&nbsp;</strong></td>\n</tr>\n<tr>\n<td><strong>Obligate Aerobes</strong></td>\n<td>\n<p><strong>Always require oxygen</strong> for their growth.&nbsp;</p>\n<p>Cannot ferment or respire anaerobically.</p>\n</td>\n<td>\n<p><em>M.tuberculosis&nbsp;</em></p>\n<p><em>Bacillus sp</em></p>\n<p><em>Legionella sp</em></p>\n<p><em>Bordetella sp</em></p>\n<p><em>Pseudomonas aeruginosa</em></p>\n</td>\n</tr>\n<tr>\n<td><strong>Obligate Anaerobes</strong></td>\n<td>\n<p>&nbsp;</p>\n<p><strong>Cant grow in the presence of oxygen </strong>as most of them don’t have Superoxide dismutase and Catalase.</p>\n</td>\n<td>\n<p>&nbsp;</p>\n<p><em>Clostridium sp.</em></p>\n<p><em>Bacteroides sp.</em></p>\n</td>\n</tr>\n<tr>\n<td><strong>Micro-aerophilic</strong></td>\n<td>\n<p><strong>Require small amounts of oxygen</strong> (2–10%) for aerobic respiration. Higher oxygen concentrations are inhibitory.</p>\n</td>\n<td>\n<p><em>H. pylori</em></p>\n<p><em>Campylobacter jejuni&nbsp;</em></p>\n<p><em>M.bovis</em></p>\n</td>\n</tr>\n<tr>\n<td><strong>Facultative anaerobe</strong></td>\n<td>\n<p><strong>Can grow aerobically or anaerobically</strong> based on the surrounding environment</p>\n</td>\n<td>\n<p>Many organisms including Enteric gram-negative&nbsp;bacteria, <em>S. aureus, Vibrio cholerae</em></p>\n</td>\n</tr>\n<tr>\n<td><strong>Aero-tolerant</strong></td>\n<td>\n<p><strong>Are indifferent to Oxygen</strong>. They are basically anaerobic organisms but can tolerate oxygen.</p>\n</td>\n<td>\n<p><em>Peptococcus anaerobius</em></p>\n<p><em>Lactobacillus sp.</em></p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(660, 'Microbiology,', 'PM2340. Stages of syphilis', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong><strong>Primary syphilis:</strong></strong></p>\n<ul>\n<li aria-level=\1\><strong>Chancre</strong> - Painless, indurated, usually single. The chancre self heals in 1-4 months.</li>\n<li aria-level=\1\>Lymphadenopathy - Painless, unilateral or bilateral, shotty and rubbery inguinal nodes</li>\n<li aria-level=\1\><strong>Dory flop sign</strong> - As the chancre is too hard to fold, it will flip over once the prepuce is retracted back.</li>\n</ul>\n<p><strong>Secondary syphilis:</strong></p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/45e19098c36e4e0ba69bdbc3358cbb5fx559x788.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ul>\n<li aria-level=\1\>A &amp; B: Generalized non-itchy rash with no vesicles present on the palm and sole. This is the most common lesion.</li>\n<li aria-level=\1\>C &amp; D:<strong> Condyloma lata</strong>.</li>\n<li aria-level=\1\>E:<strong> Moth-eaten non-scarring alopecia</strong></li>\n<li aria-level=\1\>F: Mucous patches are flat erosions on the tongue that can coalesce to form<strong> snail track ulcers.</strong> These are the most infectious lesions.</li>\n<li aria-level=\1\>G: Pigmentary plaques</li>\n<li aria-level=\1\>H: Psoriasiform syphilitic lesions</li>\n<li aria-level=\1\>Generalized diffuse non-tender lymphadenopathy</li>\n</ul>\n<p><strong>Tertiary syphilis:</strong></p>\n<ul>\n<li aria-level=\1\><strong>Gummatous syphilis</strong> are deep punched-out ulcers, with gum-like secretions on the floor</li>\n<li aria-level=\1\><strong>Pseudo-chancre redux</strong> is a gumma occurring at the site of a previously healed ulcer&nbsp;&nbsp;</li>\n<li aria-level=\1\>CVS - Aortitis and aneurysm of the ascending aorta</li>\n<li aria-level=\1\>CNS - General paresis of the insane and tabes dorsalis&nbsp;</li>\n</ul>\n<p><strong>Latent syphilis</strong> is the period between secondary and tertiary syphilis. In this period the patient is asymptomatic clinically and only serological evidence of the infection is present. It is classified into:</p>\n<ul>\n<li aria-level=\1\>Early latent syphilis - Within 1 year of infection</li>\n<li aria-level=\1\>Late latent syphilis - After 1 year of infection</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(661, 'Microbiology,', 'PM2211. Vaccines and their Strains', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Vaccine</strong></td>\n<td>\n<p><strong>Strains</strong></p>\n</td>\n</tr>\n<tr>\n<td>BCG</td>\n<td>\n<p>Danish-1331 strain</p>\n</td>\n</tr>\n<tr>\n<td>OPV/IPV</td>\n<td>\n<p>Bivalent OPV</p>\n</td>\n</tr>\n<tr>\n<td>Measles</td>\n<td>\n<ul>\n<li>Edmonston Zagreb strain</li>\n<li>Schwartz strain</li>\n<li>Moraten strain</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Mumps</td>\n<td>\n<p>Jeryll-Lynn strain</p>\n</td>\n</tr>\n<tr>\n<td>Rubella</td>\n<td>\n<p>RA 27/3</p>\n</td>\n</tr>\n<tr>\n<td>Yellow fever</td>\n<td>\n<p>17D strain&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>Varicella</td>\n<td>\n<p>OKA strain&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>Japanese encephalitis</td>\n<td>\n<ul>\n<li>Nakayama strain</li>\n<li>Beijing P3 Strain</li>\n<li>SA-14-14-2 strain(used in India)</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Swine flu vaccine</td>\n<td>\n<p>A7/ California / 2009&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>HIV vaccine</td>\n<td>\n<ul>\n<li>mVA strain</li>\n<li>rAAV strain</li>\n<li>CTL strain</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Malaria vaccine</td>\n<td>\n<ul>\n<li>SPf 66 strain ( Lytic cocktail)</li>\n<li>Pf 25 strain</li>\n<li>RTS,S/AS01 is effective against <em>P. falciparum.</em>\n<ul>\n<li>Phase 3 trial of this vaccine is complete.</li>\n<li>Phase 4 trial is under evaluation.</li>\n</ul>\n</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(662, 'Obstetrics & Gynaecology,', 'PM2035. Asymptomatic bacteriuria', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><div class=\topbullet\>&nbsp;</div>\n<div class=\topbullet\><strong>Asymptomatic bacteriuria: </strong>Urinary tract infection without any symptoms. Criteria for diagnosis are as follows:</div>\n<div class=\topbullet\>&nbsp;</div>\n<table>\n<tbody>\n<tr>\n<td>Type of specimen</td>\n<td>Number of samples required&nbsp;</td>\n<td>Minimum colony-forming units (cfu)/mL required for diagnosis of UTI</td>\n</tr>\n<tr>\n<td>Midstream clean-catch urine specimen</td>\n<td>\n<p>For women: 2 consecutive samples</p>\n<p>For men: single sample</p>\n</td>\n<td><strong><strong>1,00,000&nbsp;</strong></strong>of the same bacterial species&nbsp;</td>\n</tr>\n<tr>\n<td>Catheterized urine specimen&nbsp;</td>\n<td>Single sample</td>\n<td>\n<p><strong><strong>100 </strong></strong>for&nbsp;asymptomatic bacteriuria (CA-ASB)</p>\n<p><strong>1000</strong> for catheter-associated urinary tract infection&nbsp;(CA-UTI)</p>\n</td>\n</tr>\n<tr>\n<td>Suprapubic aspirate</td>\n<td>Single sample</td>\n<td><strong>Any growth of microorganisms</strong> in the culture&nbsp;</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(663, 'Microbiology', 'PM2032. Mechanism of action of various bacterial toxins', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>The mechanism of action of various bacterial toxins are as given below:</p>\n<p><span><strong>Increase in&nbsp;</strong></span><strong>cAMP</strong></p>\n<ul>\n<li>Bordetella pertusis</li>\n<li>Bacillus anthracis</li>\n<li>Vibrio cholerae - Cholera toxin</li>\n<li>Enterotoxigenic E.coli (ETEC) - heat labile toxin</li>\n</ul>\n<p><strong>Increase in cGMP</strong></p>\n<ul>\n<li>Enterotoxigenic E.coli (ETEC) - heat stable toxin</li>\n</ul>\n<p><strong>Inhibition of protein synthesis</strong></p>\n<ul>\n<li>Diptheria toxin: inhibit elongation factor-2 (EF-2)</li>\n<li>Pseudomonas: inhibit elongation factor-2 (EF-2)</li>\n<li>Clostridium difficile</li>\n<li>Enterohemorrhagic E.coli (EHEC)-Shiga-like toxin: inhibit 60S</li>\n<li>Shigella dysenteriae-&nbsp;Shiga toxin:&nbsp;inhibit 60S</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(664, 'Pathology,', 'PM1984. Vegetations in Various Types of Endocarditis', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\85\>\n<p><strong>&nbsp;</strong></p>\n</td>\n<td width=\63\>\n<p><strong>RHD</strong></p>\n</td>\n<td width=\87\>\n<p><strong>Infective endocarditis</strong></p>\n</td>\n<td width=\87\>\n<p><strong>Non- bacterial thrombotic or marantic endocarditis</strong></p>\n</td>\n<td width=\87\>\n<p><strong>Libman–Sacks endocarditis</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\85\>\n<p><strong>Etiology</strong></p>\n</td>\n<td width=\63\>\n<p>Rheumatic</p>\n<p>heart disease</p>\n</td>\n<td width=\87\>\n<p>Infective endocarditis</p>\n</td>\n<td width=\87\>\n<p>Malignancy</p>\n<p>(M3-AML, pancreatic</p>\n<p>carcinoma), deep</p>\n<p>vein thrombosis,</p>\n<p>Trosseau syndrome</p>\n</td>\n<td width=\87\>\n<p>Systemic lupus erythematosus</p>\n</td>\n</tr>\n<tr>\n<td width=\85\>\n<p><strong>Vegetations</strong></p>\n</td>\n<td width=\63\>\n<p>Small, warty</p>\n<p>Firm</p>\n<p>Friable</p>\n</td>\n<td width=\87\>\n<p>Large</p>\n<p>Bulky</p>\n<p>friable,</p>\n<p>destructive</p>\n</td>\n<td width=\87\>\n<p>Small, single or multiple, non- destructive</p>\n</td>\n<td width=\87\>\n<p>Small to medium-sized, flat, verrucuous</p>\n</td>\n</tr>\n<tr>\n<td width=\85\>\n<p><strong>Site</strong></p>\n</td>\n<td width=\63\>\n<p>Along lines of closure</p>\n</td>\n<td width=\87\>\n<p>Cusps of valves, mural endocardium</p>\n</td>\n<td width=\87\>\n<p>Line of closure</p>\n</td>\n<td width=\87\>\n<p>Either or</p>\n<p>both sides of the valve leaflets.</p>\n</td>\n</tr>\n<tr>\n<td width=\85\>\n<p><strong>Sterile/Infective</strong></p>\n</td>\n<td width=\63\>\n<p>Sterile</p>\n</td>\n<td width=\87\>\n<p>Non-sterile</p>\n</td>\n<td width=\87\>\n<p>Sterile</p>\n</td>\n<td width=\87\>\n<p>Sterile</p>\n</td>\n</tr>\n<tr>\n<td width=\85\>\n<p><strong>Embolisation</strong></p>\n</td>\n<td width=\63\>\n<p>Uncommon</p>\n</td>\n<td width=\87\>\n<p>Very common</p>\n</td>\n<td width=\87\>\n<p>Common</p>\n</td>\n<td width=\87\>\n<p>Uncommon</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/8cbba8132d2c40e08cdaa19e5a970968x1280x2262.JPEG\><!---->\n      </div><!---->\n    </div><!---->');
INSERT INTO db_pearl_m (pid, subject, pearl_title, pearl_desc) VALUES
(665, 'Microbiology', 'PM1933. Tuberculin reaction size & candidates for treatment of latent Mycobacterium tuberculosis infection', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Risk Group</strong></td>\n<td><strong>Positive Tuberculin Reaction Size</strong></td>\n</tr>\n<tr>\n<td>HIV infected persons</td>\n<td>≥5 mm</td>\n</tr>\n<tr>\n<td>Recent contacts of a patient with TB</td>\n<td>≥5 mm</td>\n</tr>\n<tr>\n<td>Organ transplant recipients</td>\n<td>≥5 mm</td>\n</tr>\n<tr>\n<td>Persons with fibrotic lesions consistent with old TB on chest radiography</td>\n<td>≥5 mm</td>\n</tr>\n<tr>\n<td>Immunosuppressed persons</td>\n<td>≥5 mm</td>\n</tr>\n<tr>\n<td>Persons with high risk medical conditions (silicosis &amp; end stage renal disease)</td>\n<td>≥5 mm</td>\n</tr>\n<tr>\n<td>Injection drug users</td>\n<td>≥10 mm</td>\n</tr>\n<tr>\n<td>Mycobacteriology lab personnel</td>\n<td>≥10 mm</td>\n</tr>\n<tr>\n<td>Children &lt;5 years of age</td>\n<td>≥10 mm</td>\n</tr>\n<tr>\n<td>Recent immigrants from high prevalence countries</td>\n<td>\n<p>≥10 mm</p>\n</td>\n</tr>\n<tr>\n<td>Low-risk persons</td>\n<td>\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp;≥15 mm</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Tuberculin reactions are read at 48-72 hr as the transverse diameter of the <strong>induration, </strong>and&nbsp;the diameter of the<strong> erythema is NOT considered.&nbsp;</strong></p></div><!---->\n      <!---->\n    </div><!---->'),
(666, 'Surgery,', 'PM1367. Post-Splenectomy Care', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Vaccination:</strong></p>\n<ul>\n<li aria-level=\1\>Patients should be vaccinated <strong>at least 2 weeks prior</strong>, and ideally 10 to 12 weeks prior to <strong>elective</strong> splenectomy.&nbsp;</li>\n<li aria-level=\1\>When vaccines cannot be given prior to <strong>emergency</strong> splenectomy, vaccines should be given <strong>at least two weeks after</strong> splenectomy.</li>\n</ul>\n<p>Following vaccines are given:</p>\n<ul>\n<li aria-level=\2\><strong>Pneumococcal</strong> vaccine:&nbsp;</li>\n<ul>\n<li aria-level=\3\>A dose of PCV13 followed by PPSV23 ≥8 weeks later is advised.&nbsp;</li>\n<li aria-level=\3\>Revaccinate every <strong>five to seven years</strong> thereafter.</li>\n</ul>\n<li aria-level=\2\><strong>H. influenzae type b</strong> vaccine:</li>\n<ul>\n<li aria-level=\3\>A single dose of Hib vaccine is recommended.&nbsp;</li>\n<li aria-level=\3\>Revaccination is needed every <strong>ten years</strong>.</li>\n</ul>\n<li aria-level=\2\><strong>Meningococcal</strong> vaccination:</li>\n<ul>\n<li aria-level=\3\>Two doses of the MenACWY vaccine are given ≥8 weeks apart.</li>\n<li aria-level=\3\>Revaccination is needed every <strong>five years</strong>.</li>\n</ul>\n<li aria-level=\2\><strong>Influenza</strong> vaccination:</li>\n<ul>\n<li aria-level=\3\>Vaccination against seasonal influenza should be given annually.</li>\n</ul>\n</ul>\n<p><strong>Antibiotics:&nbsp;</strong></p>\n<p>Daily prophylaxis:&nbsp;</p>\n<ul>\n<li aria-level=\1\><strong>Penicillin</strong> and <strong>amoxicillin</strong> are preferred agents</li>\n<ul>\n<li aria-level=\2\>For at least<strong> 1 year</strong> after surgery (at least up to the age of 5 yrs in children)</li>\n<li aria-level=\2\>For immunocompromised, at least up to the age of 18 yrs</li>\n</ul>\n</ul>\n<p>Early empirical:</p>\n<ul>\n<li aria-level=\1\>Early empirical treatment to be initiated in presence of systemic <strong>symptoms of infection</strong> e.g - chills, rigor, fever, vomiting, etc.</li>\n<li aria-level=\1\><strong>Amoxicillin-clavulanic acid</strong> is the preferred choice for most adults and children</li>\n<li aria-level=\1\>If allergic to beta-lactam, use <strong>levofloxacin/moxifloxacin.</strong></li>\n</ul>\n<p><strong>Antithrombotics:</strong></p>\n<ul>\n<li aria-level=\1\><strong>Low dose heparin</strong> for at least <strong>4 weeks</strong> helps in perioperative thromboembolism prevention</li>\n<li aria-level=\1\>In presence of extreme reactive thrombocytosis (&gt;10,00,000/mm3) <strong>low dose aspirin</strong> can also be given.</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(667, 'Surgery,', 'PM1366. Overwhelming Post-splenectomy Infection (OPSI)', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>It is the most common <strong>fatal</strong> infection following splenectomy.&nbsp;</p>\n<p>Certain components called <strong>opsonins</strong>&nbsp;<strong>attach</strong> to the <strong>encapsulated organisms</strong>, and then they are <strong>delivered to the spleen</strong>, where their uptake is facilitated by receptors present on the surface of <strong>splenic phagocytes</strong>. After splenectomy, this defence mechanism is lost leaving patients susceptible to infection from these bacteria.</p>\n<p>Most common within <strong>2-3 years</strong> following splenectomy.&nbsp;</p>\n<p><strong>Causative agents:</strong> Usually capsulated organisms like</p>\n<ul>\n<li><strong><em>Streptococcus pneumoniae</em> (Most common)</strong></li>\n<li><em>Haemophilus influenzae</em></li>\n<li><em>Neisseria meningitidis</em></li>\n<li><em>Streptococcus</em>&nbsp;species</li>\n<li><em>Salmonella</em> species</li>\n<li><em>Capnocytophaga canimorsus</em>, implicated in OPSI as a result of dog bites</li>\n</ul>\n<p><strong>Clinical features</strong></p>\n<ul>\n<li>Prodromal features like fever, chills, sore throat, malaise, diarrhea, and vomiting</li>\n<li>Rapid progression to hypotension, disseminated intravascular coagula­tion, respiratory distress, coma, and death within hours of presen­tation</li>\n<li><strong>High-grade bacteremia with no identifiable foci of infection</strong></li>\n<li><strong>Poor response to antibiotics</strong></li>\n</ul>\n<p>If an<strong> elective splenectomy </strong>is planned, prophylactic vaccinations&nbsp;against pneumococcus, meningococcus C (both repeated every 5 years), and <em>H. influenzae</em> type B (Hib) must be given <strong>atleast 2 weeks</strong> before the elective surgery.</p>\n<p>In case of an<strong> emergency splenectomy,&nbsp;</strong>patients should be&nbsp;<strong>vaccinated</strong> <strong>as soon as possible</strong> after recovery from surgery but before discharge from the&nbsp;hospital.</p></div><!---->\n      <!---->\n    </div><!---->'),
(668, 'Pathology,', 'PM1316. Rhinosporidiosis', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ul>\n<li>It is a chronic granulomatous condition caused by <em><strong>Rhinosporidium seeberi</strong> </em>which is recently reclassified as a member of the protistan Mesomycetozoa</li>\n<li>It is more common in the&nbsp;<strong>southern states of India</strong></li>\n<li>It affects <strong>immunocompetent </strong>individuals</li>\n<li>Most commonly a<strong> waterborne&nbsp;</strong>disease</li>\n<li>Clinical features: Blood tinged nasal discharge, frank epistaxis, nasal stuffiness</li>\n<li>Findings:&nbsp;<br>\n<ul>\n<li>Leafy, <strong>polypoidal mass</strong>, pink to purple in color and <strong>attached to the nasal septum</strong> or lateral wall</li>\n<li>Sometimes, it extends into the nasopharynx and may hang behind the soft palate</li>\n<li>The mass is very<strong> vascular </strong>and bleeds easily on touch</li>\n<li>Its surface is studded with white dots representing the sporangia</li>\n</ul>\n</li>\n<li>Diagnosis:<strong>&nbsp;Biopsy</strong> which shows <strong>sporangia</strong></li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/a813c201198c4800bbe43437de8562b9x512x382.PNG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Histologic section showing <strong>sporangium</strong> (arrow) which is fully packed with <strong>immature sporoblasts</strong> at the periphery and mature ones at the center</p>\n<p>Treatment: <strong>Complete excision</strong> of the mass with diathermy knife and <strong>cauterization</strong> of its base. Recurrence may occur after surgical excision. <strong>Dapsone</strong> is also used.</p></div><!---->\n      <!---->\n    </div><!---->'),
(669, 'Medicine,', 'PM1234. Infectious Agents Associated with the Development of Lymphoid Malignancies', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\516\>\n<thead>\n<tr>\n<td>\n<p><strong>Infectious Agent</strong></p>\n</td>\n<td>\n<p><strong>Lymphoid Malignancy</strong></p>\n</td>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td>\n<p>HTLV-I</p>\n</td>\n<td>\n<p>Adult T cell leukemia/lymphoma</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Epstein-Barr virus</p>\n</td>\n<td>\n<p>Burkitts lymphoma</p>\n<p>Post–organ transplant lymphoma</p>\n<p>Primary CNS diffuse large B cell lymphoma</p>\n<p>Hodgkins disease (Mixed cellularity and lymphocyte depleted)</p>\n<p>Extranodal NK/T cell lymphoma, nasal type</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>HIV</p>\n</td>\n<td>\n<p>Diffuse large B cell lymphoma</p>\n<p>Burkitts lymphoma</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Hepatitis C virus</p>\n</td>\n<td>\n<p>Lymphoplasmacytic lymphoma (Waldenstroms macroglobulinemia)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Helicobacter pylori&nbsp;</p>\n</td>\n<td>\n<p>Gastric MALT lymphoma (Extranodal marginal zone lymphoma)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Human herpesvirus 8</p>\n</td>\n<td>\n<p>Primary effusion lymphoma</p>\n<p>Multicentric Castlemans disease</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(670, 'Pathology,', 'PM0809. Microbial Etiology of Infective Endocarditis', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\231\>\n<p><strong>Organism</strong></p>\n</td>\n<td width=\231\>\n<p><strong>Predisposing condition</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Streptococcus viridians</p>\n</td>\n<td width=\231\>\n<p>Damaged valves</p>\n<p>Prosthetic valve</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Staphylococcus aureus</p>\n</td>\n<td width=\231\>\n<p>Intravenous drug abusers</p>\n<p>Native cardiac valve</p>\n<p>Prosthetic valve</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Staphylococcus epididermis</p>\n</td>\n<td width=\231\>\n<p>Prosthetic valve</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Streptococcus mutans</p>\n</td>\n<td width=\231\>\n<p>Tooth extraction</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>Other causes include enterococci and the so-called <strong>HACEK group (Haemophilus, Actinobacillus, Cardiobacterium, Eikenella, and Kingella)</strong>, all commensals in the oral cavity, gram-negative bacilli and fungi.</p></div><!---->\n      <!---->\n    </div><!---->'),
(671, 'Community Medicine,', 'PM0689. Types of Vaccines', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Type</strong></p>\n</td>\n<td>\n<p><strong>Vaccines</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>Live attenuated</p>\n<p>(<strong>M</strong>ost<strong>&nbsp;</strong><strong>B</strong>eautiful&nbsp;<strong>P</strong>lace&nbsp;<strong>I</strong>n&nbsp;<strong>T</strong>he <strong>W</strong>orld<strong>&nbsp;</strong>-<strong> ROME)</strong></p>\n</td>\n<td>\n<p>&nbsp;</p>\n<p><strong>M</strong>umps</p>\n<p><strong>B</strong>CG</p>\n<p><strong>P</strong>lague</p>\n<p><strong>I</strong>nfluenza (cold-adapted)</p>\n<p><strong>T</strong>yphoid (oral)</p>\n<p>Yello<strong>W&nbsp;</strong>fever</p>\n<p><strong>R</strong>ubella and&nbsp;<strong>R</strong>otavirus</p>\n<p><strong>O</strong>ral Polio (Sabin)</p>\n<p><strong>M</strong>easles</p>\n<p>Japanese&nbsp;<strong>E</strong>ncephalitis</p>\n<p>Varicella</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>Killed whole organism</p>\n<p>(<strong>RIP</strong> <strong>ACE</strong>)</p>\n</td>\n<td>\n<p><strong>R</strong>abies</p>\n<p><strong>I</strong>nfluenza</p>\n<p><strong>P</strong>olio (IPV, Salk)</p>\n<p>Hep <strong>A</strong></p>\n<p><strong>C</strong>holera</p>\n<p><strong>E</strong>ncephalitis</p>\n<p>Typhoid</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>Toxoid/Protein</p>\n</td>\n<td>\n<p>Diphtheria</p>\n<p>Tetanus</p>\n<p>Acellular Pertussis</p>\n<p>Anthrax</p>\n<p>Influenza subunit&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>Polysaccharide</p>\n</td>\n<td>\n<p>Pneumococcus</p>\n<p>Meningococcus</p>\n<p>Hemophilus influenza type b (Hib)</p>\n<p>Typhoid (Vi)&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>&nbsp;</p>\n<p>Glycoconjugate</p>\n</td>\n<td>\n<p>Hib</p>\n<p>Pneumococcus</p>\n<p>MenACWY(meningococcal vaccine)&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>Recombinant</p>\n</td>\n<td>\n<p>Hepatitis B vaccine</p>\n<p>Lyme disease</p>\n<p>Cholera toxin B</p>\n<p>HPV&nbsp;</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(672, 'Medicine,', 'PM0545. Microbiology of Infective Endocarditis', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td><strong>&nbsp;Condition</strong></td>\n<td>\n<p><strong>Most common organism</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Most common organism overall</p>\n</td>\n<td>\n<p>Staphylococcus aureus</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Community-acquired native valve endocarditis</p>\n</td>\n<td>\n<p>Streptococci</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Healthcare-associated native valve endocarditis</p>\n</td>\n<td>\n<p>Staphylococcus aureus</p>\n</td>\n</tr>\n<tr>\n<td>Endocarditis in injection drug users (Right side)</td>\n<td>Staphylococcus aureus</td>\n</tr>\n<tr>\n<td>Endocarditis in injection drug users (Left side)</td>\n<td>Enterococci and Staphylococcus aureus</td>\n</tr>\n<tr>\n<td>\n<p>Single most common organism causing prosthetic valve endocarditis</p>\n</td>\n<td>\n<p>Staphylococcus aureus</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Most common group of organism causing early prosthetic valve endocarditis (&lt;12 months after surgery)</p>\n</td>\n<td>\n<p>&nbsp;</p>\n<p>Coagulase-negative staphylococci (CoNS)*<br><br></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Late prosthetic valve endocarditis (&gt;12 months after surgery)</p>\n</td>\n<td>\n<p>Streptococci</p>\n<p>&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Acute infective endocarditis</p>\n</td>\n<td>\n<p>Staphylococcus aureus</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Subacute infective endocarditis</p>\n</td>\n<td>\n<p>Streptococcus viridans</p>\n<p>Enterococci</p>\n<p>Coagulase-negative Staphylococci (CoNS)</p>\n<p>HACEK</p>\n</td>\n</tr>\n<tr>\n<td>\n<p class=\p1\>Cardiovascular implantable electronic device (CIED)-associated endocarditis</p>\n</td>\n<td>\n<p>Staphylococcus aureus&nbsp; and coagulase-negative Staphylococci (CoNS)*</p>\n</td>\n</tr>\n<tr>\n<td>\n<p class=\p1\>Culture negative endocarditis</p>\n</td>\n<td>\n<p>Prior antibiotic use</p>\n<p>Nutritionally variant streptococci</p>\n<p>HACEK organisms</p>\n<p>Coxiella burnetii</p>\n<p>Bartonella</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>NOTE :</p>\n<p>*CoNS includes Staphylococcus epidermidis&nbsp;and other coagulase-negative types<br><br>*Streptococci includes Viridans streptococci (VGS),&nbsp;Streptococcus gallolyticus&nbsp;(previously&nbsp;Streptococcus bovis&nbsp;biotype 1) and other&nbsp;non-groupable and groupable streptococci</p>\n<p>&nbsp;</p>\n<table width=\419\>\n<tbody>\n<tr>\n<td width=\210\>\n<p><strong>Microorganism </strong></p>\n</td>\n<td width=\209\>\n<p><strong>Source </strong></p>\n</td>\n</tr>\n<tr>\n<td width=\210\>\n<p>Streptococcus viridans</p>\n</td>\n<td width=\209\>\n<p>Oral cavity</p>\n</td>\n</tr>\n<tr>\n<td width=\210\>\n<p>Staphylococcus aureus</p>\n</td>\n<td width=\209\>\n<p>Skin</p>\n</td>\n</tr>\n<tr>\n<td width=\210\>\n<p>HACEK organisms</p>\n</td>\n<td width=\209\>\n<p>Upper respiratory tract</p>\n</td>\n</tr>\n<tr>\n<td width=\210\>\n<p>Streptococcus gallolyticus</p>\n</td>\n<td width=\209\>\n<p>GIT (Colon )</p>\n</td>\n</tr>\n<tr>\n<td width=\210\>\n<p>Enterococci</p>\n</td>\n<td width=\209\>\n<p>Genitourinary tract</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(673, 'Microbiology', 'PM0409. Culture media used for Vibrio cholerae', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>The following are various culture media used for <em>Vibrio cholerae</em>:&nbsp;</p>\n<p><strong>Transport Media</strong></p>\n<ul>\n<li>Venkatraman-Ramakrishnan (VR) medium</li>\n<li>Cary-Blair medium</li>\n<li>Autoclaved sea water</li>\n</ul>\n<p><strong>Enrichment Media</strong></p>\n<ul>\n<li>Alkaline peptone water (APW)</li>\n<li>Monsur’s taurocholate tellurite peptone water</li>\n</ul>\n<p><strong>Selective Media</strong></p>\n<ul>\n<li>Alkaline bile salt agar (BSA)</li>\n<li>Monsur’s gelatin taurocholate trypticase tellurite agar (GTTA)</li>\n<li>Thiosulphate citrate bile sucrose (TCBS) agar <strong>- </strong>\n<ul>\n<li>Yellow colonies-<em> Vibrio cholerae</em></li>\n<li>Green colonies- <em>Vibrio parahemolyticus</em></li>\n</ul>\n</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(674, 'Microbiology', 'PM0402. Differences between classical and El Tor vibrios', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Differences between classical and El Tor<em>&nbsp;</em>vibrios</strong></p>\n<table>\n<tbody>\n<tr>\n<td><strong>Test</strong></td>\n<td><strong>Classical <em>V. cholerae</em></strong></td>\n<td><strong>El tor vibrios</strong></td>\n</tr>\n<tr>\n<td>Haemolysis</td>\n<td>&nbsp; &nbsp; &nbsp; &nbsp; -</td>\n<td>+</td>\n</tr>\n<tr>\n<td>Voges-Proskauer test</td>\n<td>&nbsp; &nbsp; &nbsp; &nbsp; -</td>\n<td>+</td>\n</tr>\n<tr>\n<td>Chick red cell agglutination</td>\n<td>&nbsp; &nbsp; &nbsp; &nbsp; -</td>\n<td>+</td>\n</tr>\n<tr>\n<td>Polymyxin-B sensitivity</td>\n<td>&nbsp; &nbsp; &nbsp; &nbsp; +</td>\n<td>-</td>\n</tr>\n<tr>\n<td>Group IV phage sensitivity</td>\n<td>&nbsp; &nbsp; &nbsp; &nbsp; +</td>\n<td>-</td>\n</tr>\n<tr>\n<td>El Tor phage 5 suseptibility</td>\n<td>-</td>\n<td>+</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(675, 'Dermatology,', 'PM0360. Cutaneous Infections/Diseases caused by Staphylococcus aureus', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Cutaneous Infections/Diseases caused by </strong><em>Staphylococcus aureus</em></p>\n<p><strong>A. Direct infection of skin and adjacent tissues</strong></p>\n<ul>\n<li>Carbuncle</li>\n<li>Ecthyma</li>\n<li>Folliculitis</li>\n<li>Furunculosis</li>\n<li>Impetigo</li>\n<li>Sycosis</li>\n</ul>\n<p><strong>B. Secondary infection</strong></p>\n<ul>\n<li>Eczema, infestations, ulcers, etc.</li>\n</ul>\n<p><strong>C. Cutaneous disease due to effect of bacterial toxin</strong></p>\n<ul>\n<li>Staphylococcal scalded skin syndrome (SSSS)</li>\n<li>Toxic shock syndrome</li>\n<li>Staphylococcal scarlatina</li>\n<li>Recurrent toxin‐mediated perineal erythema</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(676, 'Dermatology,', 'PM0359. Cutaneous Infections/Diseases caused by Streptococcus pyogenes', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Cutaneous infections/diseases caused by Streptococcus pyogenes</strong></p>\n<p><strong>Direct infections of skin or subcutaneous tissue</strong></p>\n<ul>\n<li>Cellulitis</li>\n<li>Impetigo</li>\n<li>Ecthyma, Erysipelas</li>\n<li>Vulvovaginitis</li>\n<li>Perianal infection</li>\n<li>Streptococcal ulcers</li>\n<li>Blistering distal dactylitis</li>\n<li>Necrotizing fasciitis</li>\n</ul>\n<p><strong>Secondary infection</strong></p>\n<ul>\n<li>Eczema, infestations, ulcers, etc.</li>\n</ul>\n<p><strong>Tissue damage from circulating toxin</strong></p>\n<ul>\n<li>Scarlet fever</li>\n<li>Toxic‐shock‐like syndrome</li>\n<li>Recurrent toxin‐mediated perineal erythema</li>\n</ul>\n<p><strong>Skin lesions due to allergic hypersensitivity to Streptococcal&nbsp;</strong><strong>antigens</strong></p>\n<ul>\n<li>Erythema nodosum</li>\n<li>Vasculitis</li>\n</ul>\n<p><strong>Skin disease provoked or influenced by Streptococcal infection&nbsp;</strong><strong>(mechanism uncertain)</strong></p>\n<ul>\n<li>Psoriasis, especially guttate forms</li>\n<li>Kawasaki disease</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(677, 'Community Medicine,', 'PM0152. Bioterrorism Agents', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>Description</td>\n<td>Characteristics</td>\n<td>Examples</td>\n</tr>\n<tr>\n<td>Category A</td>\n<td>\n<p>High priority agents</p>\n<p>Easily disseminated</p>\n<p>High mortality rate</p>\n<p>Capacity to get disseminated</p>\n<p>Social disruption</p>\n<p>Requires specialised public health resources</p>\n<p>&nbsp;</p>\n</td>\n<td>\n<p><em>Bacillus anthracis</em></p>\n<p><em>Clostridium botulinum</em></p>\n<p><em>Yersinia pestis</em></p>\n<p><em>Variola major</em></p>\n<p><em>Francisella tularensis</em></p>\n<p>Ebola and other hemorrhagic fevers</p>\n<p>Lassa fever and other arenaviruses</p>\n<p>&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>Category B&nbsp;</td>\n<td>\n<p>Minority priority agents</p>\n<p>Moderately well disseminated</p>\n<p>Moderate morbidity and low mortality</p>\n<p>Require enhancement of CD and diagnostic capacity</p>\n</td>\n<td>\n<p><em>Coxiella burnetti</em></p>\n<p><em>Burkholderia&nbsp;mallei</em></p>\n<p><em>Burkholderia&nbsp;pseudomallei</em></p>\n<p>Ricin toxin</p>\n<p><em>Cryptosporidium parvum</em></p>\n<p><em>Salmonella typhi</em></p>\n<p><em>Chlamydia psittaci</em></p>\n</td>\n</tr>\n<tr>\n<td>Category C</td>\n<td>\n<p>Low priority agents</p>\n<p>Could be engineered for mass dissemination</p>\n<p>Readily available</p>\n<p>Easy to produce</p>\n<p>Potentially high morbidity and mortality</p>\n</td>\n<td>Emergent viruses -Nipah and Hantavirus</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(678, 'Microbiology', 'PM0145. Characteristics of Chlamydia species causing Human infections', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\ width=\45\>\n<tbody>\n<tr>\n<td><strong>Species</strong></td>\n<td><strong>Disease</strong></td>\n<td><strong>Natural host&nbsp;</strong></td>\n<td><strong>Mode of Transmission to Human</strong></td>\n<td><strong>Number of immunological types</strong></td>\n<td><strong>Diagnosis</strong></td>\n<td><strong>Treatment</strong></td>\n</tr>\n<tr>\n<td>C. trachomatis</td>\n<td>Urethritis, pneumonia, conjunctivitis, lymphogranuloma, venerum<br>Trachoma</td>\n<td>Humans</td>\n<td>Sexual contacts,<br>Perinatal transmission</td>\n<td>More than 15</td>\n<td>Inclusions in epithelial cells seen with giemsa stain or by immunofluorescence; also cell culture</td>\n<td>Tetracyclin, erythromycin</td>\n</tr>\n<tr>\n<td>C. pneumonia</td>\n<td>Atypical&nbsp;pneumonia</td>\n<td>Humans</td>\n<td>Respiratory droplets</td>\n<td>1</td>\n<td>Serological test&nbsp;</td>\n<td>Tetracyclin</td>\n</tr>\n<tr>\n<td>C. psittaci</td>\n<td>Psittacosis (pneumonia)</td>\n<td>Birds</td>\n<td>Inhalation of dried bird feces</td>\n<td>1</td>\n<td>Serological test(Cell culture rarely done)</td>\n<td>Tetracyclin</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(679, 'Microbiology,', 'PM0143. Human diseases caused by the Rickettsia and Orientia species', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Organism</strong></p>\n</td>\n<td>\n<p><strong>Disease</strong></p>\n</td>\n<td>\n<p><strong>Vector</strong></p>\n</td>\n<td>\n<p><strong>Reservoir</strong></p>\n</td>\n<td>\n<p><strong>Clinical feature</strong></p>\n</td>\n</tr>\n<tr>\n<td colspan=\5\><strong>Typhus group</strong></td>\n</tr>\n<tr>\n<td>\n<p><em>R.prowazekii</em></p>\n</td>\n<td>\n<p>Epidemic typhus &amp; Brill-Zinsser disease</p>\n</td>\n<td>\n<p>Louse</p>\n</td>\n<td>\n<p>Humans</p>\n</td>\n<td>\n<p>Rash+<br> No eschar</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><em>R.typhi</em></p>\n</td>\n<td>\n<p>Endemic typhus</p>\n</td>\n<td>\n<p>Rat Flea</p>\n</td>\n<td>\n<p>Rodents</p>\n</td>\n<td>\n<p>Rash +<br> No eschar</p>\n</td>\n</tr>\n<tr>\n<td colspan=\5\><strong>Spotted fever group</strong></td>\n</tr>\n<tr>\n<td>\n<p><em>R. rickettsii</em></p>\n</td>\n<td>\n<p>Rocky mountain spotted fever</p>\n</td>\n<td>\n<p>Tick (Dermacentor andersoni)</p>\n</td>\n<td>\n<p>Rodents, dogs</p>\n</td>\n<td>\n<p>Rash+<br> No eschar<br>Many systemic manifestations</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><em>R.conori</em></p>\n</td>\n<td>\n<p>Indian tick typhus</p>\n</td>\n<td>\n<p>Tick</p>\n</td>\n<td>\n<p>Rodents,</p>\n<p>dogs</p>\n</td>\n<td>\n<p>Tache noire +</p>\n<p>Rash +</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><em>R.akari</em></p>\n</td>\n<td>\n<p>Rickettsial pox</p>\n</td>\n<td>\n<p>Gamasid mite</p>\n</td>\n<td>&nbsp;Mice</td>\n<td>\n<p>&nbsp;Vesicular rash</p>\n<p>Eschar +</p>\n</td>\n</tr>\n<tr>\n<td colspan=\5\><strong>Scrub Typhus</strong></td>\n</tr>\n<tr>\n<td>\n<p><em>O.</em></p>\n<p><em>tsutsugamushi</em></p>\n</td>\n<td>\n<p>Scrub typhus</p>\n</td>\n<td>\n<p>Trombiculid Mite</p>\n</td>\n<td>\n<p>Rodents</p>\n</td>\n<td>\n<p>Rash+<br> Eschar +<br> Atypical lymphocytes+</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(680, 'Microbiology', 'PM0114. Post exposure prophylaxis  for tetanus', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Prophylaxis is given based on patient category:</p>\n<p>A. Full immunization or a booster dose within the past 5 years.</p>\n<p>B. Full immunization or a booster dose &gt; 5 years ago but &lt; 10 years.</p>\n<p>C. Full immunization or a booster dose &gt; 10 years ago.</p>\n<p>D. Unknown or never taken immunization</p>\n<table>\n<tbody>\n<tr>\n<td>Category</td>\n<td>Clean wound</td>\n<td>Unclean wound</td>\n</tr>\n<tr>\n<td>A</td>\n<td>Only wound care</td>\n<td>Only wound care</td>\n</tr>\n<tr>\n<td>B</td>\n<td>Wound care + TT single dose</td>\n<td>Wound care + TT single dose</td>\n</tr>\n<tr>\n<td>C</td>\n<td>Wound care + TT single dose</td>\n<td>Wound care + TT single dose + Human tetanus immunoglobulin&nbsp;</td>\n</tr>\n<tr>\n<td>D</td>\n<td>Wound care + TT complete course</td>\n<td>Wound care + TT complete course + Human tetanus immunoglobulin</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(681, 'Microbiology', 'PM0110. Differentiating features between anthrax and anthracoid bacilli.', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>The following are the&nbsp;Differentiating features between anthrax and anthracoid bacilli.</p>\n<table cellspacing=\5\ cellpadding=\5\>\n<tbody>\n<tr>\n<td><strong>S.N.</strong></td>\n<td><strong>Characteristics</strong></td>\n<td><strong>Anthrax Bacilli</strong></td>\n<td><strong>Anthracoid Bacilli</strong></td>\n</tr>\n<tr>\n<td><strong>1.</strong></td>\n<td><strong>Motility</strong></td>\n<td>Non-Motile</td>\n<td>Generally Motile</td>\n</tr>\n<tr>\n<td><strong>2.</strong></td>\n<td><strong>Capsule</strong></td>\n<td>Capsulated</td>\n<td>Non-Capsulated</td>\n</tr>\n<tr>\n<td><strong>3.</strong></td>\n<td><strong>Shape</strong></td>\n<td>Grow in long chains</td>\n<td>Grow in short chains</td>\n</tr>\n<tr>\n<td><strong>4.</strong></td>\n<td><strong>Medusa Head Colony</strong></td>\n<td>Present</td>\n<td>Not Present</td>\n</tr>\n<tr>\n<td><strong>5.</strong></td>\n<td><strong>Growth in Penicillin Agar (10 units/ml)</strong></td>\n<td>No Growth</td>\n<td>Grow Usually</td>\n</tr>\n<tr>\n<td><strong>6.</strong></td>\n<td><strong>Hemolysis</strong></td>\n<td>Hemolysis absent or weak.</td>\n<td>Usually well marked</td>\n</tr>\n<tr>\n<td><strong>7.</strong></td>\n<td><strong>Gelatin Liquefaction</strong></td>\n<td>Slow Gelatin Liquefaction</td>\n<td>Rapid Gelatin Liquefaction</td>\n</tr>\n<tr>\n<td><strong>8.</strong></td>\n<td><strong>Turbidity</strong></td>\n<td>No Turbidity in broth</td>\n<td>Turbidity usually</td>\n</tr>\n<tr>\n<td><strong>9.</strong></td>\n<td><strong>Salicin Fermentation</strong></td>\n<td>Negative</td>\n<td>Usually Positive</td>\n</tr>\n<tr>\n<td><strong>10.</strong></td>\n<td><strong>Growth at 45°C</strong></td>\n<td>No Growth</td>\n<td>Grows usually</td>\n</tr>\n<tr>\n<td><strong>11.</strong></td>\n<td><strong>Chloral Hydrate</strong></td>\n<td>Growth inhibited by&nbsp; Chloral Hydrate</td>\n<td>Not Inhibited</td>\n</tr>\n<tr>\n<td><strong>12.</strong></td>\n<td><strong>Gamma Phage</strong></td>\n<td>Susceptible to gamma phage</td>\n<td>Not susceptible</td>\n</tr>\n<tr>\n<td><strong>13.</strong></td>\n<td><strong>Pathogenesis</strong></td>\n<td>Pathogenic to laboratory animals</td>\n<td>Not pathogenic</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(682, 'Microbiology', 'PM0109. Common names of various bacteria', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>List of common names and scientific names of medically important bacteria:</p>\n<table cellspacing=\5\ cellpadding=\5\>\n<tbody>\n<tr>\n<td><strong>Common name</strong></td>\n<td><strong>Scientific name</strong></td>\n</tr>\n<tr>\n<td>Klebs-Loffler bacillus</td>\n<td>Corynebacterium diphtheria</td>\n</tr>\n<tr>\n<td>Koch week bacillus</td>\n<td>Haemophilus aegypticus</td>\n</tr>\n<tr>\n<td>Tubercle or Koch bacillus</td>\n<td>Mycobacterium tuberculosis</td>\n</tr>\n<tr>\n<td>Hansen’s bacillus</td>\n<td>Mycobacterium leprae</td>\n</tr>\n<tr>\n<td>Eaton agent</td>\n<td>Mycoplasma pneumonia</td>\n</tr>\n<tr>\n<td>Pfeiffer’s bacillus</td>\n<td>Haemophilus influenza</td>\n</tr>\n<tr>\n<td>Friedlander’s bacillus</td>\n<td>Klebsiella pneumoniae</td>\n</tr>\n<tr>\n<td>Glander’s Bacillus</td>\n<td>Burkholderia mallei</td>\n</tr>\n<tr>\n<td>Whitmore Bacillus</td>\n<td>Burkholderia pseudomallei</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(683, 'Microbiology', 'PM0101. Differentiation between Streptococcus pneumoniae and Streptococcus viridans', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table cellspacing=\5\ cellpadding=\5\>\n<tbody>\n<tr>\n<td><strong>Characteristics</strong></td>\n<td><strong>Streptococcus pneumoniae</strong></td>\n<td><strong>Streptococcus viridans</strong></td>\n</tr>\n<tr>\n<td>Morphology</td>\n<td>Lanceolate diplococci</td>\n<td>Cocci in chains</td>\n</tr>\n<tr>\n<td>Capsule</td>\n<td>Capsulated</td>\n<td>Non-capsulated</td>\n</tr>\n<tr>\n<td>Quellung Test</td>\n<td>Positive</td>\n<td>Negative</td>\n</tr>\n<tr>\n<td>Colonies</td>\n<td>Initially Dome Shaped, Later Draughtsman Colonies</td>\n<td>Dome shaped</td>\n</tr>\n<tr>\n<td>Growth in Liquid Media</td>\n<td>Uniform Turbidity</td>\n<td>Granular Turbidity, powdery deposit</td>\n</tr>\n<tr>\n<td>Bile solubility</td>\n<td>Bile soluble</td>\n<td>Bile insoluble</td>\n</tr>\n<tr>\n<td>Inulin fermentation</td>\n<td>Positive</td>\n<td>Negative</td>\n</tr>\n<tr>\n<td>Optochin sensitivity</td>\n<td>Sensitive</td>\n<td>Resistant</td>\n</tr>\n<tr>\n<td>Intraperitoneal inoculation in Mice</td>\n<td>Fatal infection</td>\n<td>Non-pathogenic</td>\n</tr>\n<tr>\n<td>Hemolysis</td>\n<td>Alpha-hemolytic (under aerobic conditions) or Beta-hemolytic (under anaerobic conditions)</td>\n<td>Alpha hemolytic or non-hemolytic</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(684, 'Medicine,', 'PM0100. Streptococcal antigens cross-reactive with human tissues', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table cellspacing=\5\ cellpadding=\5\>\n<tbody>\n<tr>\n<td><strong>Streptococcal antigen</strong></td>\n<td>\n<p><strong>Human antigen</strong></p>\n</td>\n<td><strong>Disease</strong></td>\n</tr>\n<tr>\n<td>Hyaluronic acid</td>\n<td>\n<p>Synovial fluid</p>\n</td>\n<td>Reactive arthritis</td>\n</tr>\n<tr>\n<td>Cell wall M protein</td>\n<td>Myocardium</td>\n<td>ARF(Acute rheumatic fever)</td>\n</tr>\n<tr>\n<td>Cell wall C carbohydrate</td>\n<td>Cardiac valves</td>\n<td>ARF (Acute rheumatic fever)</td>\n</tr>\n<tr>\n<td>Peptidoglycan</td>\n<td>Skin antigens</td>\n<td>Guttate psoriasis</td>\n</tr>\n<tr>\n<td>Cytoplasmic membrane</td>\n<td>Glomerular vascular intima</td>\n<td>Acute glomerulonephritis (AGN)</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(685, 'Microbiology', 'PM0098. Methicillin-Resistant Staphylococcus aureus (MRSA)', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>MRSA Definition</strong>: <strong>Methicillin</strong> MIC 8 μg/ml or more, <strong>Oxacillin</strong> MIC 4 μg/ml or more.</p>\n<p>Resistance in MRSA is mediated by <strong>mecA gene</strong> which is <strong>chromosomally coded</strong>. It alters <strong>PBP (penicillin-binding protein)</strong> present on S.aureus cell wall to <strong>PBP-2a</strong> which has less affinity for beta-lactam antibiotics.</p>\n<p>Penicillin Binding Protein:</p>\n<ul>\n<li>Normal protein present in all bacterial cell wall.</li>\n<li><strong>Transpeptidase</strong> in nature.</li>\n<li>Required for cross-linking of the peptidoglycan layer (essential for cell wall synthesis).</li>\n<li>It is also the site of attachment for- beta-lactam drugs to bind &amp; inhibit.</li>\n</ul>\n<p>Types of MRSA:</p>\n<ul>\n<li><strong>Community-acquired&nbsp;</strong>- mediated by <strong>mec A subtype IV</strong> and Possess <strong>PV Toxin(Panton-Valentine Toxin)</strong>, monodrug resistance,more virulent</li>\n<li><strong>Hospital-acquired</strong> - mediated by <strong>mec A subtype I, II, III</strong>, multidrug resistance,less virulent.</li>\n</ul>\n<p>Detection of MRSA:</p>\n<ul>\n<li>Antibiotic susceptibility test on <strong>Muller Hilton agar</strong> <strong>(Disk diffusion test)</strong> by using Methicillin or Oxacillin disc or cefoxitin disc.</li>\n<li><strong>PCR</strong> for detection of mecA gene.</li>\n</ul>\n<p>Treatment:</p>\n<ul>\n<li><strong>Vancomycin</strong> is Drug Of Choice (should be used only if other drugs are resistant ).</li>\n<li>Others- Teicoplanin, Linezolid, streptogramin, cipro/levofloxacin, cotrimoxazole, clindamycin, mupirocin, minocycline.</li>\n<li><strong>Newer Drugs</strong> – Daptomycin, <strong>ceftobiprole</strong>, tigecycline, oritavancin, dalbavancin, telavancin, delafloxacin.</li>\n<li><strong>Ceftobiprole </strong>and<strong> ceftaroline </strong>are 5th generation β lactams effective against MRSA.</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(686, 'Microbiology', 'PM0026. Syphilis - Diagnostic Serology', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Nontreponemal tests (VDRL, RPR)</strong></p>\n<p><strong>VDRL- </strong>Venereal disease Research Laboratory test</p>\n<p><strong>RPR</strong>- Rapid Plasma Reagin test</p>\n<ul>\n<li>Quantitative tests(titres)</li>\n<li>Reagin antibody tests; based on cardiolipin antigen</li>\n<li>VDRL can be used to test CSF whereas RPR cannot be used</li>\n<li>RPR does not require preheating of serum unlike VDRL</li>\n<li>VDRL requires light microscope for interpretation whereas RPR can be read macroscopically</li>\n<li>Decrease in titers confirms treatment</li>\n<li>Sensitivity of RPR(86%)&gt;VDRL(76%) in primary syphilis</li>\n</ul>\n<p><strong>Treponemal Tests</strong></p>\n<p><strong>FTA-ABS- </strong>Fluorescent Treponemal Antibody Absorption test</p>\n<p><strong>TPI-</strong>Treponema pallidum immobilization test</p>\n<p><strong>TPPA-</strong>Treponema pallidum particle agglutination assay</p>\n<p><strong>TPHA-</strong>Treponema pallidum haemagglutination test</p>\n<p><strong>EIA-</strong>Enzyme-linked ImmunoAssay</p>\n<ul>\n<li>Detect antibody to treponemal antigens</li>\n<li>Qualitative tests</li>\n<li>Greater sensitivity in early infection</li>\n<li>Positive even after treatment hence cannot be used to detect reinfection.</li>\n<li>First test to be positive: FTA-ABS</li>\n<li>Most specific: TPHA,TPPA, EIA</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(687, 'Microbiology', 'PM0010. Biological False Positive reactions in serologic testing of syphilis', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Acute biological false-positive reactions are seen in (&lt;6 months):</p>\n<ul>\n<li>Pregnancy</li>\n<li>Upper respiratory tract infections</li>\n<li>Measles</li>\n<li>Chickenpox</li>\n<li>Hepatitis</li>\n<li>Viral pneumonia</li>\n<li>Malaria&nbsp;</li>\n<li>Following vaccination and immunization</li>\n<li>Lymphogranuloma venereum</li>\n<li>Chancroid</li>\n<li>Tuberculosis</li>\n<li>Pneumococcal pneumonia</li>\n<li>Leptospirosis</li>\n</ul>\n<p>Chronic biological false-positive reactions are seen in (&gt;6 months):&nbsp;&nbsp;</p>\n<ul>\n<li>Leprosy</li>\n<li>Systemic lupus erythematosus</li>\n<li>Rheumatoid arthritis</li>\n<li>Rheumatic fever</li>\n<li>Polyarteritis nodosa</li>\n<li>Leukemia</li>\n<li>Pernicious&nbsp;anemia</li>\n<li>Metastatic carcinoma</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(688, 'Microbiology,', 'PM0008. Comparison of Genital Ulcers', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><span>&nbsp;</span></td>\n<td><span><strong>Genital herpes</strong></span></td>\n<td><span><strong>&nbsp;Syphilitic chancre (Hard chancre)</strong></span></td>\n<td><span><strong>Donovanosis/ granuloma inguinale</strong></span></td>\n<td><span><strong>Chancroid (soft chancre)</strong></span></td>\n<td><span><strong>LGV</strong></span></td>\n</tr>\n<tr>\n<td><span><strong>Incubation period</strong></span></td>\n<td><span>2-7 days</span></td>\n<td>\n<p><span>10-90 days&nbsp;</span></p>\n<p><span>Average: 21days</span></p>\n</td>\n<td><span>1-12 weeks</span></td>\n<td><span>1-14 days</span></td>\n<td><span>3 days - 6 weeks</span></td>\n</tr>\n<tr>\n<td><span><strong>Causative agent</strong></span></td>\n<td><span>HSV-2&gt;HSV-1</span></td>\n<td><em><span>Treponema pallidum</span></em></td>\n<td><em><span>Klebsiella granulomatis</span></em></td>\n<td><em><span>Hemophilus ducreyi</span></em></td>\n<td>\n<p><span><em>Chlamydia trachomatis</em></span><span>&nbsp;serovars L1,L2,L3</span></p>\n</td>\n</tr>\n<tr>\n<td><span><strong>Clinical presentation</strong></span></td>\n<td>\n<p><span>Cluster of vesicles on erythematous base.</span></p>\n<p><span> Painful and pruritic lymphadenopathy</span></p>\n</td>\n<td>\n<p><span>Single painless ulcer with a clean base and a firm raised border. </span></p>\n<p><span>Painless lymphadenopathy</span></p>\n</td>\n<td>\n<p><span>Painless papule or nodule erodes into beefy red granulomatous ulcer with rolled edges.</span></p>\n<p><span> No lymphadenopathy, pseudobubos are seen</span></p>\n</td>\n<td>\n<p><span>Painful ulcer (ducreyi makes you cry).</span></p>\n<p><span>Painful inguinal lymphadenopathy</span></p>\n</td>\n<td>\n<p><span>Painless genital ulcer.</span></p>\n<p><span>Painful inguinal lymphadenopathy</span></p>\n</td>\n</tr>\n<tr>\n<td><span><strong>Diagnosis&nbsp;</strong></span></td>\n<td><span>Tzanck preparation shows multinucleated giant cells, PCR</span></td>\n<td><span>Clinical diagnosis</span></td>\n<td><span>Donovan bodies on biopsy</span></td>\n<td><span>Clinical diagnosis</span></td>\n<td><span>Nucleic acid amplification tests</span></td>\n</tr>\n<tr>\n<td><span><strong>Treatment</strong></span></td>\n<td><span>Acyclovir/famcyclovir or valacyclovir</span></td>\n<td><span>Penicillin/Ceftriaxone</span></td>\n<td>\n<p><span>Azithromycin/</span><span>Doxycycline</span></p>\n</td>\n<td><span>Azithromycin/ceftriaxone</span></td>\n<td><span>&nbsp;Doxycycline</span></td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(689, 'Microbiology', 'PM2506. Interpretation of common serologic patterns in hepatitis B infection', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/5552cae694994040b6b13be6442fc5adx1280x1466.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<table>\n<tbody>\n<tr>\n<td>&nbsp;<strong>Clinical Phases</strong></td>\n<td><span><strong>HBsAg</strong></span></td>\n<td><span><strong>Anti-HBs</strong></span></td>\n<td><span><strong>HBeAg</strong></span></td>\n<td><span><strong>Anti-HBe</strong></span></td>\n<td><span><strong>Anti-HBc</strong></span></td>\n<td><span><strong>HBV DNA</strong></span></td>\n</tr>\n<tr>\n<td><span>Acute HBV - Early-phase, high infectivity</span></td>\n<td>\n<p><span>+</span></p>\n</td>\n<td><span>-</span></td>\n<td><span>+</span></td>\n<td><span>-</span></td>\n<td><span>IgM</span></td>\n<td><span>+++</span></td>\n</tr>\n<tr>\n<td>\n<p><span>Acute HBV -</span><span>Window phase</span></p>\n</td>\n<td>\n<p><span>-</span></p>\n</td>\n<td><span>-</span></td>\n<td><span>-</span></td>\n<td><span>-</span></td>\n<td><span>IgM</span></td>\n<td><span>+</span></td>\n</tr>\n<tr>\n<td>\n<p><span>Acute HBV - recovery phase</span></p>\n</td>\n<td>\n<p><span>-</span></p>\n</td>\n<td><span>+</span></td>\n<td><span>-</span></td>\n<td><span>+</span></td>\n<td><span>IgG</span></td>\n<td><span>+/-</span></td>\n</tr>\n<tr>\n<td><span>Chronic HBV, high infectivity</span></td>\n<td><span>+</span></td>\n<td><span>-</span></td>\n<td><span>+</span></td>\n<td><span>-</span></td>\n<td><span>IgG</span></td>\n<td><span>+++</span></td>\n</tr>\n<tr>\n<td>\n<p><span>Chronic HBV, low infectivity</span></p>\n</td>\n<td><span>+</span></td>\n<td><span>-</span></td>\n<td><span>-</span></td>\n<td><span>+</span></td>\n<td><span>IgG</span></td>\n<td><span>+</span></td>\n</tr>\n<tr>\n<td>\n<p><span>HBV infection in the recent past</span></p>\n</td>\n<td><span>-</span></td>\n<td><span>+</span></td>\n<td><span>-</span></td>\n<td><span>-</span></td>\n<td><span>IgG</span></td>\n<td><span>-</span></td>\n</tr>\n<tr>\n<td>\n<p><span>HBV infection in the remote past</span></p>\n</td>\n<td><span>-</span></td>\n<td><span>-</span></td>\n<td><span>-</span></td>\n<td><span>+/-</span></td>\n<td><span>IgG</span></td>\n<td><span>-</span></td>\n</tr>\n<tr>\n<td>\n<p><span>Post-vaccination</span></p>\n</td>\n<td><span>-</span></td>\n<td><span>+</span></td>\n<td><span>-</span></td>\n<td><span>-</span></td>\n<td><span>-</span></td>\n<td><span>-</span></td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(690, 'Microbiology', 'PM2008. Types of symmetry of viruses', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td><strong>Type of symmetry&nbsp;</strong></td>\n<td><strong>Examples</strong></td>\n</tr>\n<tr>\n<td><strong>Naked icosahedral</strong></td>\n<td>\n<p>Poliovirus</p>\n<p>Adenovirus</p>\n<p>Hepatitis A virus</p>\n</td>\n</tr>\n<tr>\n<td><strong>Naked helical</strong></td>\n<td>Tobacco mosaic virus</td>\n</tr>\n<tr>\n<td><strong>Enveloped icosahedral</strong></td>\n<td>\n<p>Herpes virus</p>\n<p>Yellow fever virus</p>\n<p>Rubella virus</p>\n</td>\n</tr>\n<tr>\n<td><strong>Enveloped helical</strong></td>\n<td>\n<p>Corona virus</p>\n<p>Rabies virus</p>\n<p>Influenza virus</p>\n<p>Parainfluenza virus</p>\n<p>Mumps virus</p>\n<p>Measles virus</p>\n</td>\n</tr>\n<tr>\n<td><strong>Complex</strong></td>\n<td>\n<p>Poxvirus</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(691, 'Microbiology', 'PM2006. Enveloped and non-enveloped viruses', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>&nbsp;</td>\n<td><strong>Non-enveloped viruses</strong></td>\n<td><strong>Enveloped viruses</strong></td>\n</tr>\n<tr>\n<td><strong>DNA viruses</strong></td>\n<td>\n<p>Parvovirus</p>\n<p>Adenovirus</p>\n</td>\n<td>\n<p>Herpes virus</p>\n<p>Hepatitis B virus</p>\n<p>Poxvirus</p>\n</td>\n</tr>\n<tr>\n<td><strong>RNA viruses</strong></td>\n<td>\n<p>Picornavirus</p>\n<p>Astrovirus</p>\n<p>Reovirus</p>\n<p>Calicivirus</p>\n<p>Hepatitis A and E virus</p>\n</td>\n<td>\n<p>Hepatitis c virus</p>\n<p>Rubella virus</p>\n<p>Chikungunya virus</p>\n<p>Dengue virus</p>\n<p>Japanese encephalitis virus</p>\n<p>Kyasanur Forest disease virus</p>\n<p>Zika virus</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(692, 'Microbiology', 'PM2005. Cultivation of viruses', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Animal inoculation</strong>:</p>\n<ul>\n<li>Suckling mice are used for inoculation of coxsackie and arbovirus</li>\n</ul>\n<p><strong>Tissue culture:</strong>&nbsp;Cell lines</p>\n<ul>\n<li>Primary cell lines- Rhesus kidney cell line, Human amniotic cell line, Chick embryo fibroblast</li>\n<li>Secondary cell lines- Human fibroblast cell line, MRC-5, and WI- 38</li>\n<li>Continuous cell lines- HeLa cell line, HEp-2, KB, Vero</li>\n</ul>\n<p><strong>Embryonated egg inoculation:</strong></p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/1d7806bf5e5b4fe8a325cfa18450c809x1280x2559.JPEG\><!---->\n      </div><!---->\n    </div><!---->');
