(693, 'Medicine,', 'PM1894. Antibodies in Hepatitis', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<table>\n<tbody>\n<tr>\n<td><strong>Antibodies </strong></td>\n<td><strong>Seen in </strong></td>\n</tr>\n<tr>\n<td>\n<p>ANA, ASMA, pANCA, anti-SLA/LP, AMA</p>\n</td>\n<td>\n<p>Autoimmune hepatitis Type 1</p>\n</td>\n</tr>\n<tr>\n<td>Anti-liver cytosol 1</td>\n<td>\n<p>Autoimmune hepatitis Type 2</p>\n</td>\n</tr>\n<tr>\n<td>Anti-LKM 1</td>\n<td>\n<p>Autoimmune hepatitis Type 2&nbsp;</p>\n<p>Chronic hepatitis C</p>\n</td>\n</tr>\n<tr>\n<td>Anti-LKM 2</td>\n<td>Drug-induced hepatitis</td>\n</tr>\n<tr>\n<td>Anti-LKM 3</td>\n<td>\n<p>Chronic hepatitis D</p>\n</td>\n</tr>\n</tbody>\n</table>\n<ul>\n<li>ANA: Anti-nuclear antibodies (most common antibody in autoimmune hepatitis)</li>\n<li>LKM: Liver–kidney microsomes</li>\n<li>SLA/LP: Soluble liver antigen/liver-pancreas</li>\n<li>ASMA: Anti-smooth muscle actin</li>\n<li>pANCA: Perinuclear anti-neutrophil cytoplasmic antibodies</li>\n<li>AMA: Antimitochondrial antibodies</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(694, 'Medicine,', 'PM1717. AIDS-defining opportunistic infections and neoplasm', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Infections:</strong></p>\n<ul>\n<li><strong>Protozoal and helminthic infections-</strong>\n<ul>\n<li>Cryptosporidiosis or Isosporiasis (cystoisosporiasis) - Chronic intestinal</li>\n<li>Toxoplasmosis (pneumonia or CNS infection)</li>\n</ul>\n</li>\n<li><strong>Fungal infections-</strong>\n<ul>\n<li>Candidiasis (esophageal, tracheal, or pulmonary)</li>\n<li>Cryptococcosis (CNS infection)</li>\n<li>Coccidioidomycosis (disseminated)</li>\n<li>Pneumocystosis (pneumonia or disseminated infection)</li>\n<li>Histoplasmosis (disseminated)</li>\n</ul>\n</li>\n<li><strong>Bacterial infections-</strong>\n<ul>\n<li>Mycobacteriosis (“atypical,” e.g., <em>Mycobacterium avium-intracellulare</em>)&nbsp;disseminated or extrapulmonary</li>\n<li>Mycobacterium tuberculosis (pulmonary&nbsp;or extrapulmonary)</li>\n<li>Nocardiosis (pneumonia, meningitis, disseminated)</li>\n<li>Salmonella infections, disseminated</li>\n</ul>\n</li>\n<li><strong>Viral infections-</strong>\n<ul>\n<li>Cytomegalovirus (pulmonary, intestinal, retinitis, or CNS infections)</li>\n<li>Herpes simplex virus (localized or disseminated infection)</li>\n<li>Varicella-zoster virus (localized or disseminated infection)</li>\n<li>Progressive multifocal leukoencephalopathy</li>\n</ul>\n</li>\n</ul>\n<p><strong>Neoplasms:</strong></p>\n<ul>\n<li>Kaposi sarcoma</li>\n<li>Primary lymphoma of brain</li>\n<li>Invasive cancer of uterine cervix&nbsp;</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(695, 'Microbiology,', 'PM1680. Interpretation of common serologic patterns in hepatitis B infection', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/5552cae694994040b6b13be6442fc5adx1280x1466.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<table>\n<tbody>\n<tr>\n<td>&nbsp;<strong>Clinical Phases</strong></td>\n<td><span><strong>HBsAg</strong></span></td>\n<td><span><strong>Anti-HBs</strong></span></td>\n<td><span><strong>HBeAg</strong></span></td>\n<td><span><strong>Anti-HBe</strong></span></td>\n<td><span><strong>Anti-HBc</strong></span></td>\n<td><span><strong>HBV DNA</strong></span></td>\n</tr>\n<tr>\n<td><span>Acute HBV - Early-phase, high infectivity</span></td>\n<td>\n<p><span>+</span></p>\n</td>\n<td><span>-</span></td>\n<td><span>+</span></td>\n<td><span>-</span></td>\n<td><span>IgM</span></td>\n<td><span>+++</span></td>\n</tr>\n<tr>\n<td>\n<p><span>Acute HBV -</span><span>Window phase</span></p>\n</td>\n<td>\n<p><span>-</span></p>\n</td>\n<td><span>-</span></td>\n<td><span>-</span></td>\n<td><span>-</span></td>\n<td><span>IgM</span></td>\n<td><span>+</span></td>\n</tr>\n<tr>\n<td>\n<p><span>Acute HBV - recovery phase</span></p>\n</td>\n<td>\n<p><span>-</span></p>\n</td>\n<td><span>+</span></td>\n<td><span>-</span></td>\n<td><span>+</span></td>\n<td><span>IgG</span></td>\n<td><span>+/-</span></td>\n</tr>\n<tr>\n<td><span>Chronic HBV, high infectivity</span></td>\n<td><span>+</span></td>\n<td><span>-</span></td>\n<td><span>+</span></td>\n<td><span>-</span></td>\n<td><span>IgG</span></td>\n<td><span>+++</span></td>\n</tr>\n<tr>\n<td>\n<p><span>Chronic HBV, low infectivity</span></p>\n</td>\n<td><span>+</span></td>\n<td><span>-</span></td>\n<td><span>-</span></td>\n<td><span>+</span></td>\n<td><span>IgG</span></td>\n<td><span>+</span></td>\n</tr>\n<tr>\n<td>\n<p><span>HBV infection in the recent past</span></p>\n</td>\n<td><span>-</span></td>\n<td><span>+</span></td>\n<td><span>-</span></td>\n<td><span>-</span></td>\n<td><span>IgG</span></td>\n<td><span>-</span></td>\n</tr>\n<tr>\n<td>\n<p><span>HBV infection in the remote past</span></p>\n</td>\n<td><span>-</span></td>\n<td><span>-</span></td>\n<td><span>-</span></td>\n<td><span>+/-</span></td>\n<td><span>IgG</span></td>\n<td><span>-</span></td>\n</tr>\n<tr>\n<td>\n<p><span>Post-vaccination</span></p>\n</td>\n<td><span>-</span></td>\n<td><span>+</span></td>\n<td><span>-</span></td>\n<td><span>-</span></td>\n<td><span>-</span></td>\n<td><span>-</span></td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(696, 'Microbiology,', 'PM1476. The Hepatitis Viruses', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>&nbsp;</td>\n<td>HAV</td>\n<td>HBV</td>\n<td>HCV</td>\n<td>HDV</td>\n<td>HEV</td>\n</tr>\n<tr>\n<td>Specific name</td>\n<td>Hepatovirus A</td>\n<td>Hepatitis B virus</td>\n<td>Hepacivirus C</td>\n<td>Hepatitis D virus</td>\n<td>Orthohepevirus A</td>\n</tr>\n<tr>\n<td>Family</td>\n<td>Picornaviridae</td>\n<td>Hepadnaviridae</td>\n<td>Flaviviridae</td>\n<td>Kolmioviridae (genus deltavirus)</td>\n<td>Hepeviridae</td>\n</tr>\n<tr>\n<td>Envelope</td>\n<td>Non enveloped</td>\n<td>Enveloped</td>\n<td>Enveloped</td>\n<td>Envelope from HBV</td>\n<td>Quasi-enveloped</td>\n</tr>\n<tr>\n<td>Nuclear material</td>\n<td>ssRNA</td>\n<td>Partially dsDNA</td>\n<td>ssRNA</td>\n<td>Circular defective ssRNA</td>\n<td>ssRNA</td>\n</tr>\n<tr>\n<td>Antigens</td>\n<td>HAV Ag</td>\n<td>HBsAg, HBcAg, HBeAg, HBxAg</td>\n<td>HCV Ag</td>\n<td>HBsAg, HDAg</td>\n<td>HEV Ag</td>\n</tr>\n<tr>\n<td>Antibodies</td>\n<td>Anti-HAV Ab</td>\n<td>Anti-HBs Ab, Anti-HBc Ab, Anti-HBe Ab</td>\n<td>Anti-HCV Ab</td>\n<td>Anti-HBs Ab, Anti-HDV Ab</td>\n<td>Anti-HEV Ab</td>\n</tr>\n<tr>\n<td>Auto-antibodies</td>\n<td>&nbsp;</td>\n<td>&nbsp;</td>\n<td>Anti-LKM 1</td>\n<td>Anti-LKM 3</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>Routes of transmission</td>\n<td>Feco-oral, parenteral, sexual</td>\n<td>Perinatal, parenteral, sexual, percutaneous, direct mucosal contact</td>\n<td>Parenteral, perinatal, percutaneous, sexual</td>\n<td>Parenteral, sexual</td>\n<td>Feco-oral, zoonotic, vertical</td>\n</tr>\n<tr>\n<td>Incubation period</td>\n<td>30 days</td>\n<td>60 days</td>\n<td>50 days</td>\n<td>60 days</td>\n<td>40 days</td>\n</tr>\n<tr>\n<td>Chances of fulminant hepatic failure</td>\n<td>0.1%</td>\n<td>0.1 to 1%</td>\n<td>0.1%</td>\n<td>5% in coinfections and 20% in superinfections</td>\n<td>1-2% in adults and up to 25% in pregnant women</td>\n</tr>\n<tr>\n<td>Extrahepatic manifestations</td>\n<td>Rash, arthralgia</td>\n<td>Polyarteritis nodosa and membranous nephropathy</td>\n<td>Essential mixed cryoglobulinemia, MPGN, Metabolic syndrome</td>\n<td>&nbsp; &nbsp; &nbsp; -</td>\n<td>GBS, Bell’s palsy, pancreatitis, &nbsp;and glomerulonephritis</td>\n</tr>\n<tr>\n<td>Resolution of acute infection</td>\n<td>2 months</td>\n<td>6 months</td>\n<td>6 months</td>\n<td>6 months</td>\n<td>3 months</td>\n</tr>\n<tr>\n<td>Progression to chronic infection</td>\n<td>No</td>\n<td>5-10%</td>\n<td>&gt;80%</td>\n<td>\n<p>10% of co-infections,</p>\n<p>90–100% of superinfections</p>\n</td>\n<td>\n<p>Only in immuno-compromised</p>\n</td>\n</tr>\n<tr>\n<td>HCC</td>\n<td>No</td>\n<td>May develop in chronic infections</td>\n<td>May develop in chronic infections</td>\n<td>May develop in chronic infections</td>\n<td>May develop in chronic infections</td>\n</tr>\n<tr>\n<td>Vaccine</td>\n<td>Available</td>\n<td>Available</td>\n<td>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;-</td>\n<td>HBV vaccine</td>\n<td>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; -</td>\n</tr>\n<tr>\n<td>Therapy</td>\n<td>None</td>\n<td>Entecavir, tenofovir, pegIFN-????</td>\n<td>Velpatasvir, sofosbuvir, ribavirin</td>\n<td>PegIFN-????</td>\n<td>None</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(697, 'Paediatrics,', 'PM1455. Viral infections associated with rash', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Viral infection&nbsp;</strong></td>\n<td><strong>Rash characteristics&nbsp;</strong></td>\n</tr>\n<tr>\n<td>Chickenpox</td>\n<td>Appears 1st&nbsp;day of fever, <strong>centripetal</strong>, pleomorphic, <strong>dewdrops on rose petal</strong> appearance</td>\n</tr>\n<tr>\n<td>Measles</td>\n<td>Appears on the 4th&nbsp;day over the face and <strong>retroauricular</strong> origin</td>\n</tr>\n<tr>\n<td>Rubella</td>\n<td>Rash on day 1 over the face</td>\n</tr>\n<tr>\n<td>\n<p>Erythema infectiosum</p>\n</td>\n<td>Rash on&nbsp;cheeks (<strong>slapped&nbsp;cheek</strong> appearance), moves to trunk and extremities</td>\n</tr>\n<tr>\n<td>Exanthem&nbsp;subitum</td>\n<td>Macules&nbsp;or maculopapular rash</td>\n</tr>\n<tr>\n<td>Infectious mononucleosis</td>\n<td>Associated with maculopapular rash</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(698, 'Microbiology,', 'PM1452. Incubation period of important viral infections', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Viral infection&nbsp;</strong></td>\n<td><strong>Incubation period&nbsp;</strong></td>\n</tr>\n<tr>\n<td><strong>Measles</strong></td>\n<td>7 - 18 days</td>\n</tr>\n<tr>\n<td><strong>Chickenpox</strong></td>\n<td>14 - 21 days</td>\n</tr>\n<tr>\n<td><strong>Mumps</strong></td>\n<td>14 - 21 days</td>\n</tr>\n<tr>\n<td><strong>Rubella</strong></td>\n<td>14 - 21 days</td>\n</tr>\n<tr>\n<td>Erythema&nbsp;infectiosum</td>\n<td>4 - 14 days</td>\n</tr>\n<tr>\n<td>Exanthem&nbsp;subitum</td>\n<td>5 - 15 days</td>\n</tr>\n<tr>\n<td>Infectious mononucleosis</td>\n<td><strong>4 - 5 weeks</strong></td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(699, 'Microbiology,', 'PM1450. Rubella and congenital rubella syndrome', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Congenital rubella syndrome</strong>:</p>\n<ul>\n<li>The triad of Gregg in congenital rubella syndrome&nbsp;includes:\n<ul>\n<li><strong>Sensorineural hearing loss -&nbsp;</strong>Nerve deafness is the <strong>single most common</strong> finding among infants with congenital rubella syndrome</li>\n<li>Congenital <strong>cataract</strong></li>\n<li><strong>Congenital heart disease</strong>&nbsp;-<strong>&nbsp;</strong>The most common cardiac defect is&nbsp;<strong>patent ductus arteriosus</strong></li>\n</ul>\n</li>\n<li><strong>Maximum damage</strong> to the fetus occurs in the <strong>first trimester</strong></li>\n<li>Infection in the <strong>second&nbsp;trimester</strong> causes <strong>only</strong> <strong>deafness</strong></li>\n<li>If infection occurs&nbsp;<strong>after 16 weeks</strong> there are <strong>no major abnormalities</strong></li>\n<li>Diagnosis - Presence of IgM rubella antibodies in the infant shortly after birth or persistence of IgG antibodies for &gt;6 months.</li>\n</ul>\n<p>The given image shows <strong>bilateral cataract</strong> in an&nbsp;infant with congenital rubella syndrome</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/f6ed3c62561f49ba90d0fb8f2eaa34a9x700x478.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>The given image shows <strong>blueberry muffin rash</strong> in an&nbsp;infant with congenital rubella syndrome</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/c5ddb3e330a841c3b9ba2c7cd124b750x720x508.PNG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>The image given below shows the&nbsp;<strong>salt and pepper fundus.</strong></p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/c98db18501ac4a4c8f755bcef23f5267x512x341.PNG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;<strong>Rubella</strong>:</p>\n<ul>\n<li>Also called the <strong>German measles</strong></li>\n<li>Causative organism belongs to the family <strong>Matonaviridae</strong></li>\n<li>Period of infectivity is -5 to +6 days after the onset of the rash</li>\n<li>Droplet trasmission</li>\n<li>The incubation period is 2-3 weeks</li>\n<li>The rash appears on day 1</li>\n<li><strong>Forchheimer spots are</strong> seen, which are tiny, rose-colored lesions in the oropharynx</li>\n<li>Associated with post-infectious thrombocytopenia</li>\n<li>The most serious complication is&nbsp;<strong>encephalitis</strong></li>\n<li><strong>Vaccination</strong>&nbsp;is done with&nbsp;the <strong>live attenuated RA 27/3 strain</strong> of the rubella</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(700, 'Microbiology', 'PM0619. Oncoviruses and the cancers associated with them', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Virus</strong></p>\n</td>\n<td>\n<p><strong>Genome</strong></p>\n</td>\n<td>\n<p><strong>Associated cancers&nbsp;</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Hepatitis B</p>\n<p>(HBV)</p>\n</td>\n<td>\n<p>DNA</p>\n</td>\n<td>\n<p>Hepatocarcinoma</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Hepatitis C</p>\n<p>(HCV)</p>\n</td>\n<td>\n<p>RNA</p>\n</td>\n<td>\n<p>Hepatocarcinoma</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Human</p>\n<p>T-</p>\n<p>lymphotropic</p>\n<p>virus (HTLV)</p>\n</td>\n<td>\n<p>RNA</p>\n</td>\n<td>\n<p>Adult T-cell leukemia</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Human</p>\n<p>papilloma</p>\n<p>viruses</p>\n<p>(HPV)</p>\n</td>\n<td>\n<p>DNA</p>\n</td>\n<td>\n<p>Cervical cancer</p>\n<p>Oropharyngeal cancer</p>\n<p>Anogenital cancer</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Kaposi’s</p>\n<p>sarcoma-</p>\n<p>associated</p>\n<p>herpesvirus</p>\n<p>(HHV-8)</p>\n</td>\n<td>\n<p>DNA</p>\n</td>\n<td>\n<p>Kaposi’s sarcoma, multicentric Castlemans disease</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Merkel cell polyomavirus</p>\n<p>(MCV)</p>\n</td>\n<td>\n<p>DNA</p>\n</td>\n<td>\n<p>Merkel cell carcinoma</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Epstein–Barr</p>\n<p>virus (EBV)</p>\n</td>\n<td>\n<p>DNA</p>\n</td>\n<td>\n<p>Burkitt’s lymphoma, Hodgkin’s lymphoma, Post-transplant lymphoproliferative disease, and Nasopharyngeal carcinoma.</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(701, 'Microbiology', 'PM0616. Differentiating Features between Smallpox and Chickenpox', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\103%\>\n<tbody>\n<tr>\n<td width=\26%\>\n<p>&nbsp;</p>\n</td>\n<td width=\35%\>\n<p>&nbsp;</p>\n<p>Smallpox (Variola)</p>\n</td>\n<td width=\37%\>\n<p>&nbsp;</p>\n<p>Chickenpox (Varicella)</p>\n</td>\n</tr>\n<tr>\n<td width=\26%\>\n<p>Incubation Period&nbsp;</p>\n</td>\n<td width=\35%\>\n<p>7–17 days</p>\n</td>\n<td width=\37%\>\n<p>14–21 days</p>\n</td>\n</tr>\n<tr>\n<td width=\26%\>\n<p>Transmission</p>\n</td>\n<td width=\35%\>\n<p>Respiratory viral shedding&nbsp;&nbsp;</p>\n<p>Through contact with the fluid found in the patients sores.&nbsp;</p>\n<p>Rarely airborne route.</p>\n</td>\n<td width=\37%\>\n<p>Aerosolized droplets from nasopharyngeal secretions.</p>\n<p>By direct cutaneous contact with vesicle fluid from skin lesions.</p>\n<p>Airborne transmission rarely</p>\n</td>\n</tr>\n<tr>\n<td width=\26%\>\n<p>Prodrome&nbsp;</p>\n</td>\n<td width=\35%\>\n<p>Fever and&nbsp;malaise&nbsp;for 2–4 days before onset of rash</p>\n</td>\n<td width=\37%\>\n<p>Minimal to none</p>\n<p>&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td width=\26%\>\n<p>Rash</p>\n</td>\n<td width=\35%\>\n<p>centrifugal spread</p>\n<p>Usually on palms and soles</p>\n<p>Axilla is usually free</p>\n<p>Rash on extensor surfaces</p>\n</td>\n<td width=\37%\>\n<p>centripetal distribution</p>\n<p>Seldom on palms and soles; appears in crops</p>\n<p>Axilla affected</p>\n<p>Rash on flexor surfaces</p>\n</td>\n</tr>\n<tr>\n<td width=\26%\>\n<p>Pock Appearance</p>\n</td>\n<td width=\35%\>\n<p>Vesicular&nbsp; &gt;&nbsp;pustular&nbsp; &gt;</p>\n<p>umbilicated&nbsp;&gt;scab</p>\n</td>\n<td width=\37%\>\n<p>Macule &gt; Papule &gt; Vesicular on erythematous</p>\n<p>Base &gt;pustular &gt;scab</p>\n</td>\n</tr>\n<tr>\n<td width=\26%\>\n<p>Evolution of Pocks</p>\n</td>\n<td width=\35%\>\n<p>Synchronous</p>\n</td>\n<td width=\37%\>\n<p>Asynchronous</p>\n</td>\n</tr>\n<tr>\n<td width=\26%\>\n<p>Scab Formation</p>\n</td>\n<td width=\35%\>\n<p>10–14 days after onset of rash</p>\n</td>\n<td width=\37%\>\n<p>4–7 days after onset of rash</p>\n</td>\n</tr>\n<tr>\n<td width=\26%\>\n<p>Scab Separation</p>\n</td>\n<td width=\35%\>\n<p>14–28 days after onset of rash</p>\n</td>\n<td width=\37%\>\n<p>Within 14 days after onset of rash</p>\n</td>\n</tr>\n<tr>\n<td width=\26%\>\n<p>Infectivity</p>\n</td>\n<td width=\35%\>\n<p>From onset of&nbsp;exanthem&nbsp;until all scabs separate</p>\n</td>\n<td width=\37%\>\n<p>&nbsp;From 1 day before rash until all vesicles become scab. Scabs are non-infectious.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(702, 'Dermatology,', 'PM0615. Clinical manifestations of Herpes simplex virus', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ol start=\1\>\n<li><strong> Cutaneous lesions:</strong> Fever blister, herpetic whitlow, herpetic gladiatorum, eczema herpeticum.<strong> Eczema herpeticum</strong> is a generalised eruption caused by Herpes infections in children suffering from eczema</li>\n<li><strong>Mucosal</strong>: <strong>most common&nbsp;site</strong> involved is <strong>buccal mucosa</strong>. It may cause Gingivostomatitis &amp; Pharyngitis, Recurrent herpes labialis.</li>\n<li><strong>Ophthalmic:&nbsp;</strong>Follicular conjunctivitis and acute keratoconjunctivitis also occur.&nbsp;</li>\n<li><strong> Nervous System:</strong> HSV encephalitis, HSV meningitis.&nbsp;</li>\n</ol></div><!---->\n      <!---->\n    </div><!---->'),
(703, 'Microbiology', 'PM0606. Slow Virus Diseases', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Slow virus diseases</strong> are characterized by a long asymptomatic period, often months or years in duration, between the introduction of the infectious agent and the appearance of clinical illness.</p>\n<p>It can be classified into 3 groups:</p>\n<p><strong>Group A</strong>: Slowly progressive infections of sheep</p>\n<ul>\n<li>Visna</li>\n<li>Medi</li>\n</ul>\n<p><strong>Group B</strong>: Prion diseases</p>\n<p>Prion Diseases of Animals</p>\n<ul>\n<li>Scrapie</li>\n<li>MINK encephalopathy</li>\n<li>Bovine spongiform encephalopathy</li>\n</ul>\n<p>Prion Diseases of Humans</p>\n<ul>\n<li>CJD</li>\n<li>Kuru</li>\n<li>Gerstmann-Straussler-Scheinkersyndrome</li>\n<li>Fatal familial insomnia</li>\n</ul>\n<p><strong>Group C</strong></p>\n<ul>\n<li>SSPE</li>\n<li>PML</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(704, 'Microbiology', 'PM0600. Taxonomy of Arboviruses', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td>\n<p><strong>FAMILY</strong></p>\n</td>\n<td>\n<p><strong>GENUS</strong></p>\n</td>\n<td>\n<p><strong>IMPORTANT SPECIES</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Togaviridae</p>\n</td>\n<td>\n<p>Alphavirus</p>\n</td>\n<td>\n<p>Chikungunya, Eastern equine encephalitis virus, Western equine encephalitis virus, Venezuelan equine encephalitis virus&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Flaviviridae</p>\n</td>\n<td>\n<p>Flavivirus</p>\n</td>\n<td>\n<p>Japanese encephalitis, West Nile virus, Dengue virus, Kayasanur forest disease virus, Murray valley encephalitis virus, Yellow fever virus, Zika virus</p>\n</td>\n</tr>\n<tr>\n<td rowspan=\3\>\n<p>Bunyaviridae</p>\n</td>\n<td>\n<p>Bunyavirus</p>\n</td>\n<td>\n<p>California encephalitis</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Phlebovirus</p>\n<p>&nbsp;</p>\n</td>\n<td>\n<p>Rift valley fever virus, Sandfly fever virus</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Nairovirus</p>\n</td>\n<td>\n<p>Ganjam virus, Nairobi sheep disease virus</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Reoviridae</p>\n</td>\n<td>\n<p>Orbivirus</p>\n</td>\n<td>\n<p>African horse sickness, Blue-tongue viruses</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Hantaviridae</p>\n</td>\n<td>\n<p>Orthohantavirus&nbsp;</p>\n</td>\n<td>\n<p>Prospect hill virus, Hantaan orthohantavirus</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(705, 'Microbiology', 'PM0596. Differentiating Properties of Orthomyxoviruses and Paramyxoviruses', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Property</strong></td>\n<td><strong>Orthomyxovirus</strong></td>\n<td><strong>Paramyxovirus</strong></td>\n</tr>\n<tr>\n<td>Size of virion</td>\n<td>80–120 nm</td>\n<td>100–300 nm</td>\n</tr>\n<tr>\n<td>Shape</td>\n<td>Spherical; filaments in fresh isolates</td>\n<td>Pleomorphic</td>\n</tr>\n<tr>\n<td>Genome</td>\n<td>Segmented; eight pieces of RNA</td>\n<td>Single linear molecule of RNA</td>\n</tr>\n<tr>\n<td>Diameter of Nucleocapsid</td>\n<td>9 nm</td>\n<td>18 nm</td>\n</tr>\n<tr>\n<td>Site of synthesis of ribonucleoprotein</td>\n<td>Nucleus</td>\n<td>Cytoplasm</td>\n</tr>\n<tr>\n<td>Genetic reassortment</td>\n<td>Common</td>\n<td>Absent</td>\n</tr>\n<tr>\n<td>Dependant RNA synthesis</td>\n<td>Required for multiplication</td>\n<td>Not required</td>\n</tr>\n<tr>\n<td>Effect of Actinomycin D</td>\n<td>Inhibits multiplication</td>\n<td>Does not inhibit</td>\n</tr>\n<tr>\n<td>Antigenic stability</td>\n<td>Variable</td>\n<td>Stable</td>\n</tr>\n<tr>\n<td>Hemolysin</td>\n<td>Absent</td>\n<td>Present</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(706, 'Medicine,', 'PM0594. 2021 NACO Guidelines on Post-exposure Prophylaxis (PEP) for HIV', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Exposure Code</strong></p>\n</td>\n<td>\n<p><strong>HIV Source Code</strong></p>\n</td>\n<td>\n<p><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; PEP Recommendation</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>1</p>\n</td>\n<td>\n<p>&nbsp;1</p>\n</td>\n<td>\n<p>&nbsp;Not required</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>1</p>\n</td>\n<td>\n<p>&nbsp;2</p>\n</td>\n<td>\n<p>&nbsp;Recommended</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>2</p>\n</td>\n<td>\n<p>&nbsp;1</p>\n</td>\n<td>\n<p>&nbsp;Recommended</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>2</p>\n</td>\n<td>\n<p>&nbsp;2</p>\n</td>\n<td>\n<p>&nbsp;Recommended</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>3</p>\n</td>\n<td>\n<p>&nbsp;1/2</p>\n</td>\n<td>\n<p>&nbsp;Recommended</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>2/3</p>\n</td>\n<td>\n<p>Not known</p>\n</td>\n<td>\n<p>Consider PEP if HIV prevalence is high in the given population</p>\n</td>\n</tr>\n<tr>\n<td colspan=\3\>\n<p><strong>Exposure Codes</strong></p>\n<ul>\n<li>EC1: <strong>Mild exposure:</strong> mucous membrane/non-intact skin with small volumes/eyes or mucous membrane or subcutaneous injections following small-bore needles</li>\n<li>EC2:&nbsp;<strong>Moderate exposure: </strong>mucous membrane/non-intact skin with large volumes or percutaneous superficial exposure with a solid needle</li>\n<li>EC3:&nbsp;<strong>Severe exposure: </strong>percutaneous with large volume by hollow needle, deep puncture</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td colspan=\3\>\n<p><strong>HIV Source Codes</strong></p>\n<ul>\n<li>SC 1- HIV +ve, asymptomatic, CD count high, low viral load</li>\n<li>SC 2- HIV +ve, symptomatic, CD count low, high viral load</li>\n<li>SC unknown: HIV status unknown</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td colspan=\3\>\n<p><strong>Recommended PEP regimens</strong></p>\n<p>1) <strong>Adolescents and Adults</strong> (&gt;10 years of age and &gt;30 kg weight): Tenofovir 300 mg + Lamivudine 300 mg + Dolutegravir 50 mg</p>\n<p>2) <strong>Children&nbsp;</strong>(&gt;6 years of age and &gt;20 kg weight): Zidovudine&nbsp;(dosage as per weight band) + Lamivudine (dosage as per weight band) + Dolutegravir 50 mg</p>\n<ul>\n<li aria-level=\1\>PEP must be initiated ideally <strong>within 2 hours</strong> and certainly within 72 hours.</li>\n<li aria-level=\1\>Duration of PEP: <strong>28 days/4weeks</strong></li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(707, 'Microbiology', 'PM0587. Cytopathic effects caused by viruses', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Morphological changes in the cells that are caused by viruses are known as <strong>cytopathic</strong> <strong>effects</strong> (CPE) and the viruses causing these CPEs are known as <strong>cytopathogenic</strong> <strong>viruses</strong>. These CPEs can be detected by a light microscope.</p>\n<table>\n<tbody>\n<tr>\n<td>\n<p><strong>Type of cytopathic effect</strong></p>\n</td>\n<td>\n<p><strong>Virus</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Rapid crenation of cells and degeneration of the cells</p>\n</td>\n<td>\n<p>Enterovirus</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Syncytium/multinucleated giant cell formation</p>\n</td>\n<td>\n<p>Measles, mumps, respiratory syncytial virus (RSV)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Discrete focal degeneration</p>\n</td>\n<td>\n<p>Herpes virus</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Large granular clumps resembling grapes</p>\n</td>\n<td>\n<p>Adenovirus</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Cytoplasmic vacuolation</p>\n</td>\n<td>\n<p>Simian virus 40 (SV 40)</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(708, 'Microbiology', 'PM0586. Viruses with Helical Symmetry: Mnemonic: ABC FOR PARAmedics', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Viruses with Helical Symmetry: Mnemonic: ABC FOR PARAmedics</strong></p>\n<ul>\n<li><strong>A</strong>renavirus</li>\n<li><strong>B</strong>unyavirus</li>\n<li><strong>C</strong>oronavirus</li>\n<li><strong>F</strong>ilovirus</li>\n<li><strong>O</strong>rthomyxovirus</li>\n<li><strong>R</strong>habdovirus</li>\n<li><strong>Para</strong>myxovirus</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(709, 'Microbiology', 'PM0582. Important Shapes of Some Viruses', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>VIRUS</strong></p>\n</td>\n<td>\n<p><strong>SHAPE</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Rabies virus</p>\n</td>\n<td>\n<p>Bullet shaped</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Pox virus</p>\n</td>\n<td>\n<p>Brick shaped</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Tobacco mosaic virus</p>\n</td>\n<td>\n<p>Rod shaped</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Ebola virus</p>\n</td>\n<td>\n<p>Filamentous</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Astro virus</p>\n</td>\n<td>\n<p>Star shaped</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Adenovirus</p>\n</td>\n<td>\n<p>Space vehicle shaped</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Rotavirus</p>\n</td>\n<td>\n<p>Wheel shaped</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(710, 'Microbiology', 'PM2370. Systemic fungi - Histopathological findings', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/0eb743b778a54477a01712589ecfe242x958x666.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Histoplasma</strong>: Tiny oval yeast cells (2- 4 μm size) with narrow-based budding within the macrophages with an underlying granulomatous response.</p>\n<hr>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/540a9635ff20406ca14c9f44eabab597x958x649.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Blastomyces</strong> : Thick-walled round yeast cells of 8- 15 μm size with single broad-based budding (figure of 8 appearance)</p>\n<hr>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/b92b3162957241d9aad7ef723c0cd59ex958x656.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Coccidoides</strong> : Mature spherules of coccidioidomycus is seen in the above image. Spherules which are large sac like structures (20--80 μm size), have thick, double refractile wall, and are filled with endospores.</p>\n<hr>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/b8b9b1242fbf4b278077f5ddba9dddcax800x524.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Paracoccidioides</strong> : Round thick-walled yeasts, with multiple narrow-necked buds attached circumferencially giving rise to Mickey mouse or pilot wheel appearance.</p></div><!---->\n      <!---->\n    </div><!---->'),
(711, 'Microbiology', 'PM1762. Classification of fungus based on spore formation', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>It is classified into 4 types:</strong></p>\n<p><strong>Glomeromycota:</strong></p>\n<ul>\n<li>Lower fungi with non-septate hyphae.</li>\n<li>Asexual spores are called sporangiospores – within swollen sac-like structure called sporangia</li>\n<li>Sexual spores are called zygospores and oospores</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/6ebc667408fd40cebe4674fab2af2abfx800x474.PNG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Ascomycota:&nbsp;</strong></p>\n<ul>\n<li>Asexual spores are called conidia.</li>\n<li>Sexual spores are called ascospores -&nbsp;formed within a sac or ascus.</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/9398bc9d32bd4bb298b87d16c5afb3bex800x552.PNG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Basidiomycota:</strong></p>\n<ul>\n<li>Asexual spores are called conidia.</li>\n<li>Sexual spores are called basidiospores (on a basidium or base).</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Deuteromycetes:&nbsp;</strong>(fungi imperfecti or hypomycetes)<strong> – </strong>sexual stages yet to be discovered.</p></div><!---->\n      <!---->\n    </div><!---->'),
(712, 'Microbiology', 'PM1761. Classification of fungus based on morphology', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Fungi are classified into 4 types based on their morphology:</p>\n<ul>\n<li>Yeast</li>\n<li>Yeast-like</li>\n<li>Molds</li>\n<li>Dimorphic fungi</li>\n</ul>\n<p><strong>Yeast:</strong></p>\n<ul>\n<li>Unicellular fungi</li>\n<li>Reproduces by budding</li>\n<li>Oval to round forms</li>\n<li>Example: Cryptococcus,&nbsp;Saccharomyces.</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/0f583d17fa494e93b42864de8e82507cx597x599.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Yeast like:</strong></p>\n<ul>\n<li>Unicellular fungi</li>\n<li>Reproduces by budding or binary fission</li>\n<li>Spherical or oval forms</li>\n<li>Pseudohyphae may be seen.</li>\n<li>Example: Candida,&nbsp;Malassezia furfur.</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/bf7e12378e26462194d6b6f9155af000x800x501.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Molds/filamentous fungi:</strong></p>\n<ul>\n<li>Have hyphae which may be septate or aseptate</li>\n<li>Aseptate or coenocytic hyphae – Rhizopus, mucor,&nbsp;Lichtheimia.</li>\n<li>Septate hyphae – Penicillium, Dermatophytes, Aspergillus, fusarium.</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/129499f438c04855bf91806ab6191d23x800x547.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Dimorphic fungi</strong></p>\n<ul>\n<li>Filamentous forms in the culture at 22<sup>0</sup>C – 25<sup>0</sup>C and in the environment.</li>\n<li>Yeast form in the culture at 37<sup>0</sup>C and in tissues.</li>\n<li>Examples – Histoplasma capsulatum, Blastomyces dermatitidis, Coccidioides immitis, Paracoccidioides brasiliensis, Talaromyces&nbsp;marneffei, Sporothrix schenckii.</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(713, 'Microbiology', 'PM1729. Important causative agents and colour of the grains of various types of mycetoma', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td><strong>Causative agent</strong></td>\n<td><strong>Color of the grains</strong></td>\n</tr>\n<tr>\n<td colspan=\2\><strong>Eumycetoma</strong></td>\n</tr>\n<tr>\n<td>Madurella mycetomatis</td>\n<td>Black</td>\n</tr>\n<tr>\n<td>M. grisea</td>\n<td>Black</td>\n</tr>\n<tr>\n<td>Pseudallescheria boydii</td>\n<td>White -yellow &nbsp;</td>\n</tr>\n<tr>\n<td colspan=\2\><strong>Actinomycetoma</strong></td>\n</tr>\n<tr>\n<td>Actinomadura madurae</td>\n<td>White -yellow &nbsp;</td>\n</tr>\n<tr>\n<td>A. pellitieri</td>\n<td>Red-yellow &nbsp;</td>\n</tr>\n<tr>\n<td>Nocardia asteroids</td>\n<td>White -yellow &nbsp;</td>\n</tr>\n<tr>\n<td>Streptomyces somaliensis</td>\n<td>yellow &nbsp;</td>\n</tr>\n<tr>\n<td colspan=\2\><strong>Botryomycosis</strong></td>\n</tr>\n<tr>\n<td>Staphylococcus species</td>\n<td>White</td>\n</tr>\n<tr>\n<td>Streptococcus species</td>\n<td>White</td>\n</tr>\n<tr>\n<td>Escherichia coli</td>\n<td>White</td>\n</tr>\n<tr>\n<td>proteus species</td>\n<td>White</td>\n</tr>\n<tr>\n<td>Pseudomonas aeruginosa</td>\n<td>White</td>\n</tr>\n<tr>\n<td>Actinobacillus lignieresi</td>\n<td>yellow &nbsp;</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(714, 'Microbiology', 'PM0568. Differences between Eumycetoma and Actinomycetoma', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Feature</strong></p>\n</td>\n<td>\n<p><strong>Eumycetoma</strong></p>\n</td>\n<td>\n<p><strong>Actino</strong><strong>mycetoma</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Etiology</strong></p>\n</td>\n<td>\n<p>Filamentous fungi</p>\n</td>\n<td>\n<p>Fungus-like bacteria</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Grains</strong></p>\n</td>\n<td>\n<p>Madurella mycetomatis and grisia- black grains</p>\n</td>\n<td>Actinomadura madurae and Nocardia asteroids- white-yellow grains</td>\n</tr>\n<tr>\n<td>\n<p><strong>Progression</strong></p>\n</td>\n<td>\n<p>Slow</p>\n</td>\n<td>\n<p>Rapid</p>\n</td>\n</tr>\n<tr>\n<td><strong>Sites of involvement</strong>\n<p>&nbsp;</p>\n</td>\n<td>\n<p>Feet and hands</p>\n</td>\n<td>\n<p>Involvement of covered parts</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Swelling</strong></p>\n</td>\n<td>\n<p>Well circumscribed and marked in the later stage</p>\n</td>\n<td>\n<p>Ill-defined and merges with surrounding tissue</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Tissue destruction</strong></p>\n</td>\n<td>\n<p>Marked in the later stage</p>\n</td>\n<td>\n<p>Involvement of bone is earlier and extensive</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Filaments</strong></p>\n</td>\n<td>\n<p>Wide (2–5</p>\n<p>μm) and septate hyphae, Periodic Schiff (PAS) positive and gram-negative easily visualized at ×400</p>\n</td>\n<td>\n<p>Slender (0.5–</p>\n<p>μm) and nonseptate, PAS negative and gram-positive filaments difficult to visualize even at ×800</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Response to treatment</strong></p>\n</td>\n<td>\n<p>Unresponsive and requires surgical debulking</p>\n</td>\n<td>\n<p>Good response</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(715, 'Microbiology', 'PM0567. Common names of various fungal infections', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Disease</strong></p>\n</td>\n<td>\n<p><strong>Common name</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Histoplasmosis</p>\n</td>\n<td>\n<p>Darling’s disease</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Sporotrichosis</p>\n</td>\n<td>\n<p>Rose-thorn or Rose gardener’s disease</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Para-coccidioidomycosis</p>\n</td>\n<td>\n<p>South American blastomycosis</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Coccidioidomycosis</p>\n</td>\n<td>\n<p>Valley fever or desert rheumatism</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Blastomycosis</p>\n</td>\n<td>\n<p>North American blastomycosis, Gilchrists disease</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(716, 'Microbiology', 'PM0566. Characteristic features of macroconidia and microconidia of dermatophytes', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Dermatophyte&nbsp;&nbsp; </strong></p>\n</td>\n<td>\n<p><strong>Macroconidia</strong></p>\n</td>\n<td>\n<p><strong>Microconidia</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Trichophyton</strong></p>\n</td>\n<td>\n<p>Thin-walled, pencil-shaped</p>\n</td>\n<td>\n<p>Small unicellular</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Microsporum</strong></p>\n</td>\n<td>\n<p>Multicellular, spindle-shaped</p>\n</td>\n<td>\n<p>Scanty</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Epidermophyton</strong></p>\n</td>\n<td>\n<p>Multicellular, pear-shaped, arranged in clusters</p>\n</td>\n<td>\n<p>Absent</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Trichophyton</strong></p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/b5c0756f2d86465bba74a9074871fe9ax958x650.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Microsporum</strong></p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/70a7695c37c84a5fa926a24250a7d9ffx800x600.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Epidermophyton</strong></p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/943737c2d7fa45f889fe26be57d41866x720x496.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(717, 'Microbiology,', 'PM2390. Comparison between mosquito-borne and transfusion-transmitted malaria.', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>&nbsp;</strong></td>\n<td><strong>Mosquito-borne malaria</strong></td>\n<td><strong>Transfusion transmitted malaria</strong></td>\n</tr>\n<tr>\n<td><strong>Mode of transmission</strong></td>\n<td>Mosquito bite</td>\n<td>Transfusion of infected blood or blood products</td>\n</tr>\n<tr>\n<td><strong>Infective stage</strong></td>\n<td>Sporozoite</td>\n<td>Trophozoite</td>\n</tr>\n<tr>\n<td><strong>Pre-erythrocytic stage</strong></td>\n<td>Present</td>\n<td>Absent</td>\n</tr>\n<tr>\n<td><strong>Incubation period</strong></td>\n<td>Long</td>\n<td>Short&nbsp;&nbsp;</td>\n</tr>\n<tr>\n<td><strong>Complications</strong></td>\n<td>Comparatively less</td>\n<td>Associated with severe complications</td>\n</tr>\n<tr>\n<td><strong>Hypnozoites</strong></td>\n<td>May be seen in infection with P. vivax and ovale</td>\n<td>Absent</td>\n</tr>\n<tr>\n<td><strong>Relapse</strong></td>\n<td>May occur</td>\n<td>Does not occur</td>\n</tr>\n<tr>\n<td><strong>Radical treatment</strong></td>\n<td>Required</td>\n<td>Not required</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->');
INSERT INTO db_pearl_m (pid, subject, pearl_title, pearl_desc) VALUES
(718, 'Microbiology', 'PM2374. Infective form and diagnostic forms - Worms', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table dir=\ltr\ border=\1\ cellspacing=\0\ cellpadding=\0\><colgroup><col width=\292\><col width=\271\><col width=\293\></colgroup>\n<tbody>\n<tr>\n<td><strong>Parasite</strong></td>\n<td><strong>Infective form</strong></td>\n<td><strong>Diagnostic form</strong></td>\n</tr>\n<tr>\n<td colspan=\3\ rowspan=\1\><strong>Cestode</strong></td>\n</tr>\n<tr>\n<td><em>Diphyllobothrium latum</em></td>\n<td>Plerocercoid larva</td>\n<td>Eggs or segments of the tapeworm</td>\n</tr>\n<tr>\n<td><em>Taenia saginata</em></td>\n<td>Cysticercus bovis</td>\n<td>Egg</td>\n</tr>\n<tr>\n<td><em>Taenia solium</em></td>\n<td>Cysticercus cellulosae</td>\n<td>Egg (for intestinal taeniasis ) / cysticercus cellulosae larva (for cysticercosis)</td>\n</tr>\n<tr>\n<td><em>Echinococcus granulosus</em></td>\n<td>Egg</td>\n<td>Protoscolices and brood capsules</td>\n</tr>\n<tr>\n<td><em>Hymenolepsis nana</em></td>\n<td>Egg</td>\n<td>Egg</td>\n</tr>\n<tr>\n<td colspan=\3\ rowspan=\1\><strong>Trematode</strong></td>\n</tr>\n<tr>\n<td><em>Schistosoma spp</em>.</td>\n<td>Cercariae</td>\n<td>Nonoperculated Eggs</td>\n</tr>\n<tr>\n<td>Other trematodes - <em>Fasciola hepatica, Fasciolopsis buski, Paragonimus westermani, Clonorchis sinensis</em></td>\n<td>Metacercaria larva</td>\n<td>Operculated Eggs</td>\n</tr>\n<tr>\n<td colspan=\3\ rowspan=\1\><strong>Nematode</strong></td>\n</tr>\n<tr>\n<td><em>Ascaris lumbricoides</em></td>\n<td>Egg</td>\n<td>Unembryonated eggs in stool</td>\n</tr>\n<tr>\n<td>\n<p><em>Ancylostoma duodenale</em></p>\n<p><em>Necator americanus</em></p>\n</td>\n<td>Filariform larva</td>\n<td>Eggs (in fresh stool), larva ( in old stool )</td>\n</tr>\n<tr>\n<td><em>Strongyloides stercoralis</em></td>\n<td>Filariform larva</td>\n<td>Rhabditiform larva</td>\n</tr>\n<tr>\n<td><em>Trichuris trichuria</em></td>\n<td>Embryonated Egg</td>\n<td>Unembryonated Egg</td>\n</tr>\n<tr>\n<td><em>Enterobius vermicularis</em></td>\n<td>Egg</td>\n<td>Eggs from perianal skin</td>\n</tr>\n<tr>\n<td>Filarial nematodes</td>\n<td>Third stage Filarial larva (L3)</td>\n<td>Microfilariae</td>\n</tr>\n<tr>\n<td><em>Trichinella spiralis</em></td>\n<td>First stage larva (L1)</td>\n<td>Trichinella antibody test</td>\n</tr>\n<tr>\n<td><em>Dracunculus medinensis</em></td>\n<td>Third stage filariform larva (L3)</td>\n<td>Adult worm</td>\n</tr>\n<tr>\n<td colspan=\3\ rowspan=\1\><strong>Hint</strong>: The infective and diagnostic forms are usually the same in the parasites causing <strong>autoinfection</strong>.</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(719, 'Microbiology', 'PM2373. Infective form and diagnostic forms of various protozoa', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table dir=\ltr\ border=\1\ width=\284\ cellspacing=\0\ cellpadding=\0\><colgroup><col width=\292\><col width=\271\><col width=\293\></colgroup>\n<tbody>\n<tr>\n<td><strong>Parasite</strong></td>\n<td><strong>Infective form</strong></td>\n<td><strong>Diagnostic form</strong></td>\n</tr>\n<tr>\n<td><em>Entamoeba histolytica</em></td>\n<td>Mature quadrinucleate cyst</td>\n<td>Trophozoites, quadrinucleate cyst</td>\n</tr>\n<tr>\n<td><em>Giardia lamblia</em></td>\n<td>Quadrinucleate cyst</td>\n<td>Trophozoites, cyst</td>\n</tr>\n<tr>\n<td><em>Trichomonas vaginalis</em></td>\n<td>Trophozoites</td>\n<td>Trophozoites</td>\n</tr>\n<tr>\n<td><em>Naegleria fowleri</em></td>\n<td>Trophozoite</td>\n<td>Trophozoite in CSF</td>\n</tr>\n<tr>\n<td><em>Acanthamoeba</em></td>\n<td>Trophozoites and cysts</td>\n<td>Trophozoite in CSF</td>\n</tr>\n<tr>\n<td><em>Leishmania donovani</em></td>\n<td>Promastigote</td>\n<td>amastigotes in macrophage</td>\n</tr>\n<tr>\n<td><em>Trypanosoma cruzi</em></td>\n<td>Metacyclic Trypomastigote</td>\n<td>Trypomastigote</td>\n</tr>\n<tr>\n<td><em>Trypanosoma brucei</em></td>\n<td>Metacyclic Trypomastigote</td>\n<td>Trypomastigote</td>\n</tr>\n<tr>\n<td><em>Balantidium coli</em></td>\n<td>Cysts</td>\n<td>Cysts</td>\n</tr>\n<tr>\n<td><em>Plasmodium </em>spp.</td>\n<td>Sporozoites</td>\n<td>Schizont, immature trophozoite, mature trophozoite, gametocyte</td>\n</tr>\n<tr>\n<td><em>Babesia </em>spp.</td>\n<td>Sporozoites</td>\n<td>Maltese cross form</td>\n</tr>\n<tr>\n<td><em>Toxoplasma gondii</em></td>\n<td>All 3 morphological forms - oocyst, tachyzoite, bradyzoite</td>\n<td>Tachyzoites, tissue cysts</td>\n</tr>\n<tr>\n<td><em>Cryptosporidium parvum</em></td>\n<td>Sporulated oocyst</td>\n<td>Sporulated oocyst</td>\n</tr>\n<tr>\n<td><em>Cyclospora cayetanensis</em></td>\n<td>Sporulated oocyst</td>\n<td>unsporulated oocyst</td>\n</tr>\n<tr>\n<td><em>Isospora belli</em></td>\n<td>Sporulated oocyst</td>\n<td>unsporulated oocyst</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(720, 'Microbiology', 'PM2371. Leishmania', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/4bb32dd49e0347eaaede8aacef9c84b9x1280x1280.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table dir=\ltr\ border=\1\ cellspacing=\0\ cellpadding=\0\><colgroup><col width=\160\><col width=\57\><col width=\226\></colgroup>\n<tbody>\n<tr>\n<td><strong>New world</strong></td>\n<td><strong>Disease</strong></td>\n<td><strong>Old world</strong></td>\n</tr>\n<tr>\n<td>L.chagasi</td>\n<td colspan=\1\ rowspan=\2\>\n<div>VL</div>\n</td>\n<td>L. donovani</td>\n</tr>\n<tr>\n<td>&nbsp;</td>\n<td>L. infantum</td>\n</tr>\n<tr>\n<td>L. mexicana (Chiclero ulcer)</td>\n<td colspan=\1\ rowspan=\2\>\n<div>CL</div>\n</td>\n<td>L. tropica (Delhi boil / oriental sore )</td>\n</tr>\n<tr>\n<td>&nbsp;</td>\n<td>L. major</td>\n</tr>\n<tr>\n<td>-</td>\n<td>DCL</td>\n<td>L. ethiopica</td>\n</tr>\n<tr>\n<td>L. braziliensis (Espundia)</td>\n<td>MCL</td>\n<td>-</td>\n</tr>\n</tbody>\n</table>\n<table dir=\ltr\ border=\1\ cellspacing=\0\ cellpadding=\0\>\n<tbody>\n<tr>\n<td>Lutzomiya</td>\n<td>Vector</td>\n<td>Phlebotomus&nbsp;</td>\n</tr>\n</tbody>\n</table>\n<p>where,</p>\n<p>VL - Visceral Leishmania</p>\n<p>CL - Cutaneous Leishmania</p>\n<p>DCL - Diffuse cutaneous Leishmania</p>\n<p>MCL - Mucocutaneous Leishmania</p></div><!---->\n      <!---->\n    </div><!---->'),
(721, 'Microbiology', 'PM1766. Filarial worms and their morphology', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/97c1647e773140ee8b5a9458edd4825ex1280x3506.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(722, 'Microbiology', 'PM1765. Eggs in parasitology', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/0fa0f4c159e7489eb20351c22109ef72x600x3219.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(723, 'Microbiology', 'PM1760. Lifecycle of Schistosoma', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://cdn1.dailyrounds.org/uploads/272e31fbc91348669c7cb449afe8acb5x624x528.PNG\><!---->\n      </div><!---->\n    </div><!---->'),
(724, 'Microbiology', 'PM1759. Eggs of Schistosoma', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/5d38d4b525f546e1a1b8e731ca886441x1280x572.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(725, 'Microbiology', 'PM1758. Difference between plasmodium species', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Feature</strong></td>\n<td><strong>P.vivax</strong></td>\n<td><strong>P.ovale</strong></td>\n<td><strong>P.falciparum</strong></td>\n<td><strong>P.malaria</strong></td>\n</tr>\n<tr>\n<td>Discovered by</td>\n<td>Grassi &amp; Feletti</td>\n<td>Stephens</td>\n<td>Welch</td>\n<td>Laveran</td>\n</tr>\n<tr>\n<td>Erythrocyte preference</td>\n<td>Reticulocytes</td>\n<td>Reticulocytes</td>\n<td>All stages</td>\n<td>Old erythrocytes</td>\n</tr>\n<tr>\n<td>Stages found in peripheral blood</td>\n<td>Trophozoite, schizonts, gametocytes</td>\n<td>As in vivax</td>\n<td>Ring form, accole form, gametocytes</td>\n<td>Band forms</td>\n</tr>\n<tr>\n<td>Infected erythrocyte</td>\n<td>Enlarged pale with Schuffner’s dots</td>\n<td>Pear-shaped, fimbriated, Schuffner’s dots (Jame’s dots)</td>\n<td>Normal size,&nbsp;Maurer’s cleft</td>\n<td>Normal size, Ziemann’s stippling</td>\n</tr>\n<tr>\n<td>Gametocyte</td>\n<td>Spherical, fills the cell</td>\n<td>Similar to but smaller than vivax</td>\n<td>Crescent, Banana shaped</td>\n<td>Similar to but smaller than vivax</td>\n</tr>\n<tr>\n<td>Color of pigment</td>\n<td>Yellowish-brown</td>\n<td>Yellowish-brown</td>\n<td>Black</td>\n<td>Dark brown</td>\n</tr>\n<tr>\n<td>Hypnozoites</td>\n<td>Present</td>\n<td>Present</td>\n<td>Absent</td>\n<td>Absent</td>\n</tr>\n<tr>\n<td>Relapse</td>\n<td>Yes</td>\n<td>Yes</td>\n<td>No</td>\n<td>No</td>\n</tr>\n<tr>\n<td>Average IP(days)</td>\n<td>14</td>\n<td>14</td>\n<td>12 (shortest)</td>\n<td>30 (longest)</td>\n</tr>\n<tr>\n<td>Duration of erythrocyte cycle(hours)</td>\n<td>48 (Benign tertian)</td>\n<td>48 (ovale tertian)</td>\n<td>48 (malignant tertian)</td>\n<td>72(Quartan malaria)</td>\n</tr>\n<tr>\n<td>Others</td>\n<td>&nbsp;</td>\n<td>&nbsp;</td>\n<td>&nbsp;</td>\n<td>Nephrotic syndrome</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(726, 'Microbiology', 'PM1757. Various morphological stages of hemoflagellates', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>It is based on the&nbsp;arrangement of the flagella in relation to the position of the nucleus and its point of emergence from the cells.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/b2a3d60c324049fbb159d7eb6e51e0b1x1280x657.PNG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Stage</strong></td>\n<td><strong>Morphological characteristics</strong></td>\n<td><strong>Seen in</strong></td>\n</tr>\n<tr>\n<td><strong>Amastigote</strong></td>\n<td>\n<p>Rounded or ovoid, without any external flagellum.</p>\n<p>The nucleus, kinetoplast, and axial filaments can be seen.</p>\n<p>The axoneme extends up to the anterior end of the cell</p>\n</td>\n<td>Trypanosoma cruzi and Leishmania as the intracellular form in the vertebrate host.</td>\n</tr>\n<tr>\n<td><strong>Promastigote</strong></td>\n<td>\n<p>Lanceolate in shape.</p>\n<p><strong>Antenuclear kinetoplast-&nbsp;</strong>kinetoplast is anterior to the nucleus near the anterior end of the cell, from which flagellum emerges.</p>\n<p>There is <strong>no undulating membrane</strong></p>\n</td>\n<td>It is the infective stage of Leishmania, found in the insect vector as well as in cultures in­vitro.</td>\n</tr>\n<tr>\n<td><strong>Epimastigote</strong></td>\n<td>\n<p>Elongated.&nbsp;</p>\n<p><strong>Juxtanuclear kinetoplast </strong>-<strong>&nbsp;</strong>kinetoplast placed more posteriorly, close to and in front of the nucleus.</p>\n<p>The flagellum runs alongside the body as a short undulating membrane, before emerging from the anterior end.</p>\n</td>\n<td>\n<p>It is the form in which Trypanosoma brucei occurs in the salivary gland of the vector tsetse fly and Trypanosoma cruzi in the midgut of the vector reduviid bug.</p>\n<p><strong>Note:</strong> This stage is lacking in Leishmania.</p>\n</td>\n</tr>\n<tr>\n<td><strong>Trypo-mastigote</strong></td>\n<td>\n<p>This stage is elongated, spindle-shaped with a <strong>central nucleus</strong>.</p>\n<p><strong>Postnuclear kinetoplast</strong>&nbsp;- kinetoplast is posterior to the nucleus and situated at the posterior end of the body.</p>\n<p>The flagellum runs alongside the entire length of the cell to form a long undulating membrane before emerging as a free flagellum from the anterior end.</p>\n</td>\n<td>\n<p>This is the infective stage of trypanosomes found in arthropod vector and in the blood of an infected vertebrate.</p>\n<p><strong>Note:</strong> This stage is lacking in Leishmania.&nbsp;</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(727, 'Microbiology', 'PM1756. Differential features of intestinal Entamoeba', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Features</strong></td>\n<td><strong>E. histolytica</strong></td>\n<td><strong>E. coli</strong></td>\n<td><strong>E. hartmanni</strong></td>\n</tr>\n<tr>\n<td colspan=\4\><strong>&nbsp; &nbsp;1. TROPHOZOITE</strong></td>\n</tr>\n<tr>\n<td>Size (µm)</td>\n<td>12 - 60</td>\n<td>20 - 50</td>\n<td>4 - 12</td>\n</tr>\n<tr>\n<td>Motility</td>\n<td>Active</td>\n<td>Sluggish</td>\n<td>Active</td>\n</tr>\n<tr>\n<td>Pseudopodia</td>\n<td>Finger shaped, rapidly extruded</td>\n<td>Short, blunt, slowly extruded</td>\n<td>Finger shaped, rapidly extruded</td>\n</tr>\n<tr>\n<td><strong>Cytoplasm</strong></td>\n<td><strong>Clearly defined into endoplasm and ectoplasm</strong></td>\n<td><strong>Not defined</strong></td>\n<td>Clearly defined into endoplasm and ectoplasm</td>\n</tr>\n<tr>\n<td><strong>Inclusion</strong></td>\n<td><strong>RBCs (erythro-phagocytosis), no bacteria</strong></td>\n<td>Bacteria and other particles, no RBCs</td>\n<td>Bacteria and other particles, no RBCs</td>\n</tr>\n<tr>\n<td>Nucleus</td>\n<td>Eccentric, not clearly visible in unstained films</td>\n<td>Visible in unstained films</td>\n<td>Not clearly visible in unstained films</td>\n</tr>\n<tr>\n<td><strong>Karyosome</strong></td>\n<td><strong>Small, central</strong></td>\n<td><strong>Large, eccentric</strong></td>\n<td><strong>Small, eccentric</strong></td>\n</tr>\n<tr>\n<td>Nuclear-membrane</td>\n<td>Delicate with fine chromatin dots</td>\n<td>Thick, coarse chromatin</td>\n<td>Coarse chromatin granules</td>\n</tr>\n<tr>\n<td colspan=\4\><strong>&nbsp; &nbsp;2. CYST</strong>&nbsp;&nbsp;&nbsp;</td>\n</tr>\n<tr>\n<td>Size (µm)</td>\n<td>10 -15</td>\n<td>10 - 30</td>\n<td>5 – 10</td>\n</tr>\n<tr>\n<td><strong>Nuclei in mature cyst</strong></td>\n<td><strong>4, central karyosome</strong></td>\n<td><strong>8, eccentric karyosome</strong></td>\n<td><strong>4</strong></td>\n</tr>\n<tr>\n<td><strong>Glycogen mass</strong></td>\n<td><strong>Seen in uninucleate stage only</strong></td>\n<td><strong>Seen upto quadrinucleate stage</strong></td>\n<td><strong>Seen in uninucleate stage only</strong></td>\n</tr>\n<tr>\n<td><strong>Chromatid bars</strong></td>\n<td><strong>1-4, with rounded ends</strong></td>\n<td><strong>Splinter like with angular ends</strong></td>\n<td><strong>Numerous, irregular shape</strong></td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(728, 'Community Medicine,', 'PM1731. Arthropod- borne diseases', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td><strong>Arthropod</strong></td>\n<td><strong>Disease transmitted</strong></td>\n</tr>\n<tr>\n<td>Anopheles mosquito</td>\n<td>Malaria, Filariasis</td>\n</tr>\n<tr>\n<td>Culex mosquito</td>\n<td>Bancroftian filariasis, Japenese encephalitis, West nile fever, Viral arthritis</td>\n</tr>\n<tr>\n<td>Aedes mosquito</td>\n<td>Yellow fever, Dengue, Chikungunya, filariasis, zika</td>\n</tr>\n<tr>\n<td>Mansoni Mosquito</td>\n<td>Filariasis, Chikungunya</td>\n</tr>\n<tr>\n<td>Housefly</td>\n<td>Typhoid and paratyphoid fever, diarrhea, dysentery, cholera, gastroenteritis, amoebiasis, helminthic infestations, poliomyelitis, conjunctivitis, trachoma, anthrax, yaws, etc.</td>\n</tr>\n<tr>\n<td>Sandfly (Phlebotomus)</td>\n<td>Kala-azar(Visceral Leishmaniasis), oriental sore, Oroya fever, Sandfly fever</td>\n</tr>\n<tr>\n<td>Tsetse fly</td>\n<td>Sleeping sickness(African Trypanosomiasis)</td>\n</tr>\n<tr>\n<td>Louse</td>\n<td>Epidemic typhus, relapsing fever, trench fever, pediculosis</td>\n</tr>\n<tr>\n<td>Rat flea</td>\n<td>Bubonic plague, endemic typhus(Murine Typhus), hymenolepis diminuta</td>\n</tr>\n<tr>\n<td>Black fly(Simulium)</td>\n<td>Onchocerciasis</td>\n</tr>\n<tr>\n<td>Reduviid bug(Kissing bug/triatominae)</td>\n<td>Chagas disease(American Trypanosomiasis)</td>\n</tr>\n<tr>\n<td>Hard tick (Ixodes species)</td>\n<td>Tick typhus, viral encephalitis, viral fevers, viral hemorrhagic fever,(e.g. Kyasanur forest disease), tularemia, tick paralysis, human babesiosis, lyme disease</td>\n</tr>\n<tr>\n<td>Soft Tick&nbsp;(Argas species)</td>\n<td>Q fever, relapsing fever, Kyasanur forest disease (KFD) (can be casued by hard tick as well)</td>\n</tr>\n<tr>\n<td>Trombiculid mite</td>\n<td>Scrub typhus, Rickettsial pox</td>\n</tr>\n<tr>\n<td>Itch mites</td>\n<td>Scabies</td>\n</tr>\n<tr>\n<td>Cyclops</td>\n<td>Guinea-worm disease, fish tapeworm (D. latum)</td>\n</tr>\n<tr>\n<td>Cockroaches</td>\n<td>Enteric pathogens</td>\n</tr>\n<tr>\n<td>Sand flea&nbsp;(Tunga penetrance, Jiggar, Chigoe flea)</td>\n<td>&nbsp;Tungiasis, Ulcer in the feetnails</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(729, 'Microbiology', 'PM0664. Helminth eggs that float in saturated salt solution', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>The following are the helminths whose eggs float in a saturated salt solution. (mnemonic: <strong>THE FAN)</strong></p>\n<ul>\n<li><em><strong>T</strong>richuris trichiura</em></li>\n<li><em><strong>H</strong>ymenolepis nana</em></li>\n<li><em><strong>E</strong>nterobius vermicularis</em></li>\n<li><strong>F</strong>ertilized eggs of&nbsp;<em><strong>A</strong>scaris lumbricoides</em></li>\n<li><em><strong>A</strong>ncylostoma duodenale</em></li>\n<li><em><strong>N</strong>ecator americanus.</em></li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(730, 'Microbiology', 'PM0663. Parasites with bile-stained eggs', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>The following are the parasites with<strong> bile-stained eggs. (</strong>mnemonic:<strong> FaT CAT)</strong></p>\n<ul>\n<li><em><strong>Fa</strong>sciola hepatica</em> (sheep liver fluke)</li>\n<li><em><strong>Fa</strong>sciolopsis buski</em> (giant intestinal fluke)</li>\n<li><em><strong>T</strong>richuris trichiura</em> (whipworm)</li>\n<li><em><strong>C</strong>lonorchis sinensis</em> (chinese liver fluke, oriental liver fluke)</li>\n<li><em><strong>A</strong>scaris lumbricoides</em> (round worm)</li>\n<li><em><strong>T</strong>aenia solium</em> (pork tapeworm)</li>\n<li><em><strong>T</strong>aenia saginata</em> (beef tapeworm)</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(731, 'Microbiology', 'PM0662. Difference between T. solium and T. saginata', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td>\n<p><strong>Properties</strong></p>\n</td>\n<td>\n<p><em><strong>Taenia solium</strong></em></p>\n</td>\n<td>\n<p><em><strong>Taenia saginata</strong></em></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Common name</strong></p>\n</td>\n<td>\n<p>Pork tapeworm</p>\n</td>\n<td>\n<p>Beef tapeworm</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Definitive host</strong></p>\n</td>\n<td>\n<p>Human</p>\n</td>\n<td>\n<p>Human</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Intermediate</strong></p>\n<p><strong>host</strong></p>\n</td>\n<td>\n<p>Pig/Humans</p>\n</td>\n<td>\n<p>Cow</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>&nbsp;Disease&nbsp;</strong></p>\n</td>\n<td>\n<p>Taeniasis and cysticercosis</p>\n</td>\n<td>\n<p>Taeniasis only</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Transmission</strong></p>\n</td>\n<td>\n<p>Infection is common among those&nbsp;<strong>eating raw or&nbsp;</strong><strong>insufficiently cooked&nbsp;</strong><strong>measly pork</strong>&nbsp;containing the cysticerci.</p>\n</td>\n<td>\n<p>Human beings are infected through the&nbsp;<strong>eating of undercooked beef&nbsp;</strong><strong>containing the&nbsp;cysticerci</strong>&nbsp;(“measly” beef)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Size of adult worm</strong></p>\n</td>\n<td>\n<p>2-3 m</p>\n</td>\n<td>\n<p>5-10m</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Scolex</strong></p>\n</td>\n<td>\n<p>The scolex head is <strong>globular</strong> in outline and has four circular suckers.</p>\n<p>The head is provided with the rostellum armed with double row of alternating large and small hooklets. The rostellar hooklets are shaped like daggers or Arabian poniards (<strong>armed scolex</strong>)</p>\n</td>\n<td>\n<p>The scolex (“head”) is&nbsp;<strong>quadrate</strong> in outline and has four circular suckers.</p>\n<p>Rostellum and hooklets are absent (i.e.&nbsp;<strong>unarmed scolex</strong>)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>&nbsp;Proglottid&nbsp;</strong></p>\n</td>\n<td>\n<p>Less than 1000 proglottids are present.</p>\n<p>The gravid uterus consists of a median longitudinal stem with<strong>&nbsp;5-13 compound lateral branches</strong>&nbsp;on each side</p>\n<p>The gravid segments are expelled passively, in chains of 5-6 at a time, and not singly.</p>\n<p><em>T.&nbsp;solium&nbsp;</em>may produce 50,000 eggs per proglottid.</p>\n</td>\n<td>\n<p>The number of proglottids varies from 1000- 2000.</p>\n<p>The gravid uterus consists of a central longitudinal stem with&nbsp;<strong>15-30 lateral branches on each side</strong>; these in turn sub-branch leaving practically no space in between.</p>\n<p>The gravid segments are expelled singly&nbsp;<em>T.&nbsp;saginata&nbsp;</em>may produce up to 100,000 eggs per proglottid.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>&nbsp;Eggs&nbsp;</strong></p>\n</td>\n<td>\n<p>The eggs are about 30-35 micrometers in diameter&nbsp;and are&nbsp;<strong>bile stained.</strong></p>\n<p>The internal oncosphere contains six refractile hooks.</p>\n<p>The&nbsp;<strong>eggs do not float&nbsp;</strong>in the saturated solution of NaCl.</p>\n<p><em>T.solium&nbsp;</em>and&nbsp;<em>T. saginata</em> eggs are morphologically indistinguishable.</p>\n</td>\n<td>\n<p>Same as <em>T. solium</em>.</p>\n<p><em>Taenia&nbsp;solium</em>&nbsp;and&nbsp;<em>Taenia&nbsp;</em><em>saginata</em>&nbsp;eggs are morphologically indistinguishable.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>&nbsp;<strong>Eggs (Infectious nature)</strong></p>\n</td>\n<td>\n<p>Infectious to humans (Human who ingested food contaminated with the egg of&nbsp;<em>Taenia solium</em>&nbsp;may develop cysticercosis)</p>\n</td>\n<td>\n<p>Eggs of&nbsp;<em>Taenia saginata</em>&nbsp;are not infectious. Humans who ingest <em>T. saginata</em> eggs do not develop cysticercosis.</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(732, 'Microbiology', 'PM0661. Difference between cestodes, trematodes, and nematodes', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Features</strong></p>\n</td>\n<td>\n<p><strong>Cestodes</strong></p>\n</td>\n<td>\n<p><strong>Trematodes</strong></p>\n</td>\n<td>\n<p><strong>Nematodes</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Shape</p>\n</td>\n<td>\n<p>Tape-like, segmented</p>\n</td>\n<td>\n<p>Leaf-like unsegmented</p>\n</td>\n<td>\n<p>Elongated, cylindrical, unsegmented</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Head end</p>\n</td>\n<td>\n<p>Suckers are present, some have attached hooks</p>\n</td>\n<td>\n<p>Suckers are present but no hooks</p>\n</td>\n<td>\n<p>Hooks and suckers are absent. Well-developed buccal capsule with teeth or cutting plates are seen in some species</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Alimentary canal</p>\n</td>\n<td>\n<p>Absent</p>\n</td>\n<td>\n<p>Present but is incomplete, no anus</p>\n</td>\n<td>\n<p>Complete with anus</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Body cavity</p>\n</td>\n<td>\n<p>Absent, but it is filled with spongy undifferentiated mesenchymal cells, in the midst of which lie the viscera</p>\n</td>\n<td>\n<p>Same as cestodes</p>\n</td>\n<td>\n<p>Present and known as pseudocele. Viscera remains suspended in the pseudocele</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Sex</p>\n</td>\n<td>\n<p>Not separate: hermaphrodite (monecious)</p>\n</td>\n<td>\n<p>Not separate: hermaphrodite except Schistosoma</p>\n</td>\n<td>\n<p>Separate (diecious)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Life cycle</p>\n</td>\n<td>\n<p>Requires 2 host except Hymenolepis (1 host) and Diphyllobothrium (3 host)</p>\n</td>\n<td>\n<p>&nbsp;Requires 3 host except schistosoma (2 host)</p>\n</td>\n<td>\n<p>Requires 1 host except filarial worms</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(733, 'Microbiology', 'PM0659. Commonly known names of some important parasites', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>a. Trematodes</strong></p>\n<table>\n<tbody>\n<tr>\n<td width=\308\>\n<p><strong>Parasite</strong></p>\n</td>\n<td width=\308\>\n<p><strong>Common name</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p><em>Paragonimus westermani</em></p>\n</td>\n<td width=\308\>\n<p>Oriental lung fluke</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p><em>Fasciolopsis buski</em></p>\n</td>\n<td width=\308\>\n<p>Giant intestinal fluke</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p><em>Fasciola hepatica</em></p>\n</td>\n<td width=\308\>\n<p>Sheep liver fluke</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p><em>Clonorchis sinensis</em></p>\n</td>\n<td width=\308\>\n<p>Chinese liver fluke/oriental liver fluke</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p><em>Schistosoma japonicum</em></p>\n</td>\n<td width=\308\>\n<p>Oriental blood fluke</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p><strong>&nbsp;b.&nbsp;</strong><strong>Cestodes</strong></p>\n<table>\n<tbody>\n<tr>\n<td width=\308\>\n<p><strong>Parasite</strong></p>\n</td>\n<td width=\308\>\n<p><strong>Common name</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p><em>Taenia saginata</em></p>\n</td>\n<td width=\308\>\n<p>Beef tapeworm</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p><em>Diphyllobothrium latum</em></p>\n</td>\n<td width=\308\>\n<p>Fish tapeworm</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p><em>Taenia solium</em></p>\n</td>\n<td width=\308\>\n<p>Pork tapeworm</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p><em>Hymenolepis nana</em></p>\n</td>\n<td width=\308\>\n<p>Dwarf tapeworm</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p><em>Hymenolepis diminuta</em></p>\n</td>\n<td width=\308\>\n<p>Rat tapeworm</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p><em>Dipylidium caninum</em></p>\n</td>\n<td width=\308\>\n<p>Double-pored dog tapeworm</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p><strong>&nbsp;c.&nbsp;</strong><strong>Nematodes:</strong></p>\n<table>\n<tbody>\n<tr>\n<td width=\308\>\n<p><strong>Parasite</strong></p>\n</td>\n<td width=\308\>\n<p><strong>Common name</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p><em>Ascaris lumbricoides</em></p>\n</td>\n<td width=\308\>\n<p>Common roundworm</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p><em>Ancylostoma duodenale</em></p>\n</td>\n<td width=\308\>\n<p>Old world hookworm</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p><em>Necator americanus</em></p>\n</td>\n<td width=\308\>\n<p>American or new world hookworm</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p><em>Trichuris trichiura</em></p>\n</td>\n<td width=\308\>\n<p>Whipworm</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p><em>Enterobius vermicularis</em></p>\n</td>\n<td width=\308\>\n<p>Thread or pinworm</p>\n</td>\n</tr>\n<tr>\n<td width=\308\>\n<p><em>Dracunculus medinensis</em></p>\n</td>\n<td width=\308\>\n<p>Guinea worm</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(734, 'Microbiology', 'PM0658. Skin tests for the diagnosis of parasitic infections', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Skin tests</strong> are performed by injecting parasitic antigen intradermally and observing the reaction. In immediate hypersensitivity reaction, wheal and flare response is seen within 30 minutes of injection, whereas in delayed type of hypersensitivity, erythema and induration are seen after 48 hours of injection.</p>\n<p>The table below shows some of the important skin tests used in the diagnosis of parasitic infections.</p>\n<table>\n<tbody>\n<tr>\n<td>\n<p><strong>Skin test</strong></p>\n</td>\n<td>\n<p><strong>Diagnosis</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Casoni’s test</p>\n</td>\n<td>\n<p>Hydatid disease</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Montenegro test/Leishmanin test</p>\n</td>\n<td>\n<p>Kala-azar</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Frenkel’s test</p>\n</td>\n<td>\n<p>Toxoplasmosis</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Fairley’s test</p>\n</td>\n<td>\n<p>Schistosomiasis</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Bachman test</p>\n</td>\n<td>\n<p>Trichinellosis</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(735, 'Microbiology', 'PM0657. Important serological tests for the diagnosis of parasitic infections', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Serological tests</strong> are helpful for the detection and surveillance of many protozoal and helminthic infections.</p>\n<p>These tests are basically of 2 types:</p>\n<ul>\n<li>Tests for antigen detection</li>\n<li>Tests for antibody detection</li>\n</ul>\n<p><strong>Antigen Detection:</strong></p>\n<table>\n<tbody>\n<tr>\n<td>\n<p><strong>Antigen</strong></p>\n</td>\n<td>\n<p><strong>Parasite</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Galactose lectin antigen</p>\n</td>\n<td>\n<p><em>Entamoeba histolytica</em></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Giardia-specific antigen 65</p>\n</td>\n<td>\n<p><em>Giardia lamblia</em></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>WKK and rk39 antigen</p>\n</td>\n<td>\n<p><em>Leishmania donovani</em></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>HRP-2 antigen</p>\n</td>\n<td>\n<p><em>Plasmodium falciparum</em></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Vivax-specific pLDH</p>\n</td>\n<td>\n<p><em>Plasmodium vivax</em></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>200 KD Ag and OG4C3 antigen</p>\n</td>\n<td>\n<p><em>Wuchereria bancrofti</em></p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong>Antibody detection:</strong></p>\n<ul>\n<li>Complement fixation test (CFT)</li>\n<li>Indirect hemagglutination test (IHA)</li>\n<li>Indirect immunofluorescent antibody test (IFA)</li>\n<li>Rapid immunochromatography test</li>\n<li>ELISA test</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(736, 'Microbiology', 'PM0656. Definitive and intermediate hosts of various parasites', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Parasite</strong></p>\n</td>\n<td>\n<p><strong>Definitive host</strong></p>\n</td>\n<td>\n<p><strong>Intermediate host</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Protozoa</strong></p>\n</td>\n<td>\n<p><strong>&nbsp;</strong></p>\n</td>\n<td>\n<p><strong>&nbsp;</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Plasmodium spp.</p>\n</td>\n<td>\n<p>Female Anopheles mosquito</p>\n</td>\n<td>\n<p>Man</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Babesia</p>\n</td>\n<td>\n<p>Tick</p>\n</td>\n<td>\n<p>Man</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Leishmania</p>\n</td>\n<td>\n<p>Man, dog</p>\n</td>\n<td>\n<p>Sandfly</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Trypanosoma brucei</p>\n</td>\n<td>\n<p>Man</p>\n</td>\n<td>\n<p>Tsetse fly</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Trypanosoma cruzi</p>\n</td>\n<td>\n<p>Man</p>\n</td>\n<td>\n<p>Triatomine bug</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Toxoplasma gondii</p>\n</td>\n<td>\n<p>Cat</p>\n</td>\n<td>\n<p>Man</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Cestodes</strong></p>\n</td>\n<td>\n<p><strong>&nbsp;</strong></p>\n</td>\n<td>\n<p><strong>&nbsp;</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Taenia solium</p>\n</td>\n<td>\n<p>Man</p>\n</td>\n<td>\n<p>Pig</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Taenia saginata</p>\n</td>\n<td>\n<p>Man</p>\n</td>\n<td>\n<p>Cattle</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Echinococcus granulosus</p>\n</td>\n<td>\n<p>Dog</p>\n</td>\n<td>\n<p>Sheep, horses.</p>\n<p>Man is the accidental intermediate host.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Trematodes</strong></p>\n</td>\n<td>\n<p><strong>&nbsp;</strong></p>\n</td>\n<td>\n<p><strong>&nbsp;</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Fasciola hepatica</p>\n</td>\n<td>\n<p>Man</p>\n</td>\n<td>\n<p>Snail</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Fasciolopsis buski</p>\n</td>\n<td>\n<p>Man, pig</p>\n</td>\n<td>\n<p>Snail</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Schistosoma spp.</p>\n</td>\n<td>\n<p>Man</p>\n</td>\n<td>\n<p>Snail</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Nematodes</strong></p>\n</td>\n<td>\n<p><strong>&nbsp;</strong></p>\n</td>\n<td>\n<p><strong>&nbsp;</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Trichinella spiralis</p>\n</td>\n<td>\n<p>Man</p>\n</td>\n<td>\n<p>Pig</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Wuchereria bancrofti</p>\n</td>\n<td>\n<p>Man</p>\n</td>\n<td>\n<p>Mosquito</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Brugia malayi</p>\n</td>\n<td>\n<p>Man</p>\n</td>\n<td>\n<p>Mosquito</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Dracunculus medinensis</p>\n</td>\n<td>\n<p>Man</p>\n</td>\n<td>\n<p>Cyclops</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(737, 'Dermatology,', 'PM2069. Vaginal infections', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>&nbsp;</td>\n<td><strong>Bacterial vaginosis</strong></td>\n<td><strong>Trichomonas vaginitis</strong></td>\n<td>\n<p><strong>Candidal vaginitis</strong></p>\n</td>\n</tr>\n<tr>\n<td>Causative organism&nbsp;</td>\n<td>Altered vaginal flora- increased concentration of <strong><em>Gardnerella vaginalis</em></strong></td>\n<td><strong><em>Trichomonas vaginalis</em></strong></td>\n<td><strong><em>Candida albicans</em></strong></td>\n</tr>\n<tr>\n<td>Aetiology</td>\n<td>Increased vaginal pH</td>\n<td>Sexually transmitted</td>\n<td>Decreased immunity</td>\n</tr>\n<tr>\n<td>Sexual transmission</td>\n<td>No</td>\n<td>Yes</td>\n<td>No</td>\n</tr>\n<tr>\n<td>Discharge</td>\n<td>Fishy, thin, yellow</td>\n<td>Frothy, green</td>\n<td>Curdy, white</td>\n</tr>\n<tr>\n<td>Symptoms</td>\n<td>Fishy odour, no itching</td>\n<td>Itching and burning</td>\n<td>Itching, burning</td>\n</tr>\n<tr>\n<td>pH</td>\n<td>&gt;4.5</td>\n<td>&gt;4.5</td>\n<td>&lt;4.5</td>\n</tr>\n<tr>\n<td>Speculum</td>\n<td>No vaginal epithelium inflammation</td>\n<td>Strawberry cervix</td>\n<td>Inflamed-epithelium</td>\n</tr>\n<tr>\n<td>KOH</td>\n<td>Positive Whiff test</td>\n<td>The whiff test can be positive.</td>\n<td>Pseudohyphae seen.</td>\n</tr>\n<tr>\n<td>Wet mount</td>\n<td>Clue cells. No WBCs seen</td>\n<td>Motile trichomaonads, WBCs seen</td>\n<td>WBCs seen</td>\n</tr>\n<tr>\n<td>Treatment</td>\n<td>Metronidazole or Clindamycin</td>\n<td>Metronidazole, treat&nbsp;partner also</td>\n<td>Oral or vaginal azole, no need to treat partner.</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(738, 'Medicine,', 'PM0660. Features of amoebic and bacillary dysentery', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Features</strong></p>\n</td>\n<td>\n<p><strong>Amoebic dysentery</strong></p>\n</td>\n<td>\n<p><strong>Bacillary dysentery</strong></p>\n</td>\n</tr>\n<tr>\n<td colspan=\3\>\n<p><strong>Clinical</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Onset</p>\n</td>\n<td>\n<p>Slow</p>\n</td>\n<td>\n<p>Acute</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Fever</p>\n</td>\n<td>\n<p>Absent</p>\n</td>\n<td>\n<p>Present</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Toxicity</p>\n</td>\n<td>\n<p>Absent</p>\n</td>\n<td>\n<p>Present</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Abdominal tenderness</p>\n</td>\n<td>\n<p>Localised</p>\n</td>\n<td>\n<p>Generalised</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Tenesmus</p>\n</td>\n<td>\n<p>Absent</p>\n</td>\n<td>\n<p>Present</p>\n</td>\n</tr>\n<tr>\n<td colspan=\3\>\n<p><strong>Stool</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Frequency</p>\n</td>\n<td>\n<p>6–8 per day</p>\n</td>\n<td>\n<p>Over 10 per day</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Odor</p>\n</td>\n<td>\n<p>Offensive</p>\n</td>\n<td>\n<p>Nil</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Colour</p>\n</td>\n<td>\n<p>Dark red</p>\n</td>\n<td>\n<p>Bright red</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Nature</p>\n</td>\n<td>\n<p>Feces mixed with blood and mucus</p>\n</td>\n<td>\n<p>Blood and mucus with little or no feces</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Consistency</p>\n</td>\n<td>\n<p>Not adherent</p>\n</td>\n<td>\n<p>Adherent to container</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Reaction</p>\n</td>\n<td>\n<p>Acid</p>\n</td>\n<td>\n<p>Alkaline</p>\n</td>\n</tr>\n<tr>\n<td colspan=\3\>\n<p><strong>Microscopy</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Cellular exudates</p>\n</td>\n<td>\n<p>Scanty</p>\n</td>\n<td>\n<p>Abundant</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Red blood cells</p>\n</td>\n<td>\n<p>Clumped, yellowish brown</p>\n</td>\n<td>\n<p>Discrete or in rouleaux, bright red</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Macrophages</p>\n</td>\n<td>\n<p>Few</p>\n</td>\n<td>\n<p>Several, some with ingested red blood cells</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Eosinophils</p>\n</td>\n<td>\n<p>Present</p>\n</td>\n<td>\n<p>Absent</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Charcot-Leyden crystals</p>\n</td>\n<td>\n<p>Present</p>\n</td>\n<td>\n<p>Absent</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Motile organisms</p>\n</td>\n<td>\n<p>Many motile amoeba</p>\n</td>\n<td>\n<p>Scanty, nonmotile (Shigella&nbsp;is a non motile bacteria)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Amoeba</p>\n</td>\n<td>\n<p>Motile trophozoites with ingested red blood cells</p>\n</td>\n<td>\n<p>Absent</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(739, 'Dermatology,', 'PM0618. Skin rashes: 1st to 6th diseases', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p>1<sup>st</sup> disease</p>\n</td>\n<td>\n<p>Measles</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>2<sup>nd</sup> disease</p>\n</td>\n<td>\n<p>Scarlet fever</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>3<sup>rd</sup> disease</p>\n</td>\n<td>\n<p>Rubella</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>4<sup>th</sup> disease</p>\n</td>\n<td>\n<p>Duke’s disease</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>5<sup>th</sup> disease</p>\n</td>\n<td>\n<p>Erythema infectiosum</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>6<sup>th</sup> disease</p>\n</td>\n<td>\n<p>Roseola infantum</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(740, 'Microbiology', 'PM0106. Organisms and their transport media', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Organism</strong></td>\n<td><strong>Transport media</strong></td>\n</tr>\n<tr>\n<td>Streptococcus</td>\n<td>Pike’s media</td>\n</tr>\n<tr>\n<td>Neisseria</td>\n<td>Stuart media</td>\n</tr>\n<tr>\n<td>Shigella,&nbsp;Salmonella</td>\n<td>Cary Blair medium</td>\n</tr>\n<tr>\n<td>Vibrio cholera</td>\n<td>\n<p>Cary Blair medium</p>\n<p>Venkatraman–Ramakrishnan&nbsp;medium</p>\n<p>Autoclaved sea water</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(741, 'Microbiology', 'PM0001. Organisms and selective Culture Media', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Appropriate culture media for various bacteria:</p>\n<table>\n<tbody>\n<tr>\n<td><strong>Organism</strong></td>\n<td><strong>Medium for culture</strong></td>\n</tr>\n<tr>\n<td>Staph aureus</td>\n<td>Mannitol salt agar<br>ludlam’s medium<br>salt milk agar</td>\n</tr>\n<tr>\n<td>Strep. Pyogenes</td>\n<td>Crystal violet blood agar</td>\n</tr>\n<tr>\n<td>Neisseria meningitidis</td>\n<td>Thayer martin medium</td>\n</tr>\n<tr>\n<td>Corynebacterium</td>\n<td>Potassium tellurite agar<br>Tinsdale medium<br>Mcleod medium</td>\n</tr>\n<tr>\n<td>B.anthrax</td>\n<td>PLET medium</td>\n</tr>\n<tr>\n<td>B.cereus</td>\n<td>MYPA (Mannitol egg yolk phenol red polymyxin agar)<br>PEMBA(polymyxin in egg yolk mannitol bromothymol blue agar</td>\n</tr>\n<tr>\n<td>Clostridium&nbsp;</td>\n<td>CCFA ( cefoxitin cycloserine fructose agar)</td>\n</tr>\n<tr>\n<td>Shigella</td>\n<td>DCA, XLD, SSA</td>\n</tr>\n<tr>\n<td>Salmonella</td>\n<td>Wilson and Blair bismuth sulphite medium</td>\n</tr>\n<tr>\n<td>Vibrio</td>\n<td>TCBS<br>Monsur GTTA</td>\n</tr>\n<tr>\n<td>Pseudomonas</td>\n<td>Cetrimide agar</td>\n</tr>\n<tr>\n<td>B.pseudomallei</td>\n<td>Ashdown medium</td>\n</tr>\n<tr>\n<td>H. influenza</td>\n<td>Fildes agar <br>Levinthal medium</td>\n</tr>\n<tr>\n<td>Bordetella</td>\n<td>Bordet-Gengou glycerine potato blood agar</td>\n</tr>\n<tr>\n<td>Brucella</td>\n<td>Casteneda medium</td>\n</tr>\n<tr>\n<td>B.burgdorferi</td>\n<td>BSK medium</td>\n</tr>\n<tr>\n<td>Campylobacter</td>\n<td>\n<p>Skirrows, Butzlers and Campy BAP media</p>\n</td>\n</tr>\n<tr>\n<td>Legionella pneumophilia</td>\n<td>\n<p>BCYE medium</p>\n</td>\n</tr>\n<tr>\n<td>Leptospira</td>\n<td>EMJH medium&nbsp;</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(742, 'Pathology', 'PM2357. Types of calcifications', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>&nbsp;</td>\n<td><strong>Dystrophic calcification</strong></td>\n<td><strong>Metastatic calcification</strong></td>\n</tr>\n<tr>\n<td>Location</td>\n<td>Dead and dying tissues</td>\n<td>Normal interstitial tissues of the vasculature, kidneys, lungs, and gastric mucosa.</td>\n</tr>\n<tr>\n<td>Calcium metabolism</td>\n<td>Normal</td>\n<td>deranged causing hypercalcemia</td>\n</tr>\n<tr>\n<td>Examples</td>\n<td>\n<p>Calcific valvular stenosis</p>\n<p>Advanced Atheromatous plaque</p>\n<p>Monckeberg medial sclerosis</p>\n</td>\n<td>\n<p>Parathyroid tumors</p>\n<p>Hypercalcemia of malignancy</p>\n<p>Paget disease</p>\n<p>vitamin D intoxication</p>\n<p>Sarcoidosis</p>\n<p>Renal failure</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(743, 'Pathology', 'PM2356. Intracellular accumulations', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\326\>\n<tbody>\n<tr>\n<td>Substance accumulated</td>\n<td>Seen in</td>\n</tr>\n<tr>\n<td>\n<p>Cholesterol and cholesterol esters</p>\n</td>\n<td>\n<p>Atherosclerosis</p>\n<p>Xanthomas</p>\n<p>Cholesterolosis</p>\n<p>Niemann Pick disease type c</p>\n</td>\n</tr>\n<tr>\n<td>Mallory bodies(keratin filaments)</td>\n<td>\n<p>Alcoholic liver disease</p>\n<p>Morbid obesity</p>\n<p>Indian childhood cirrhosis</p>\n<p>Wilson’s disease</p>\n<p>Primary biliary cirrhosis</p>\n</td>\n</tr>\n<tr>\n<td>Lipofuscin</td>\n<td>Free radical injury</td>\n</tr>\n<tr>\n<td>Immunoglobulins</td>\n<td>Russel bodies and Dutcher bodies of multiple myeloma</td>\n</tr>\n<tr>\n<td>Melanin</td>\n<td>\n<p>Normal endocrine cells of the duodenal mucosa</p>\n<p>Carcinoid syndrome</p>\n<p>Alkaptonuria</p>\n</td>\n</tr>\n<tr>\n<td>Copper&nbsp;</td>\n<td>Wilson disease</td>\n</tr>\n<tr>\n<td>Hemosiderin</td>\n<td>Hemochromatosis</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(744, 'Pathology', 'PM2355. Types of granulomas', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>GRANULOMA</strong></td>\n<td><strong>SEEN IN</strong></td>\n</tr>\n<tr>\n<td>Noncaseating granuloma</td>\n<td>\n<p>Crohn’s disease</p>\n<p>Leprosy</p>\n<p>Sarcoidosis</p>\n<p>Hodgkin’s lymphoma</p>\n</td>\n</tr>\n<tr>\n<td>Caseating granuloma</td>\n<td>\n<p>Tuberculosis</p>\n<p>Histoplasmosis</p>\n<p>Syphilis</p>\n<p>Coccidiomycosis</p>\n</td>\n</tr>\n<tr>\n<td>Foreign body granuloma</td>\n<td>\n<p>Silicosis</p>\n<p>Berylliosis</p>\n</td>\n</tr>\n<tr>\n<td>Immune granuloma</td>\n<td>\n<p>Wegener’s granulomatosis</p>\n<p>Giant cell arteritis</p>\n</td>\n</tr>\n<tr>\n<td>Durck’s granuloma</td>\n<td>Cerebral malaria</td>\n</tr>\n<tr>\n<td>Stellate granuloma</td>\n<td>Cat scratch disease</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(745, 'Pathology', 'PM2353. Types of giant cells', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Giant cells</strong></td>\n<td><strong>Seen in</strong></td>\n</tr>\n<tr>\n<td>Langhans giant cells</td>\n<td>TB Granuloma</td>\n</tr>\n<tr>\n<td>Reed Sternberg&nbsp;cells</td>\n<td>Hodgkin lymphoma</td>\n</tr>\n<tr>\n<td>Aschoff&nbsp;giant cells</td>\n<td>Rheumatic fever</td>\n</tr>\n<tr>\n<td>Touton giant cells</td>\n<td>\n<p>Juvenile xanthogranuloma,</p>\n<p>Xanthoma disseminatum,</p>\n<p>dermatofibroma</p>\n</td>\n</tr>\n<tr>\n<td>Warthin Finkeldey giant cells</td>\n<td>Measles</td>\n</tr>\n<tr>\n<td>Physiological giant cells</td>\n<td>Bone marrow osteoclast cells and megakaryocytes</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->');
