(762, 'Pathology', 'PM1792. Cancer Predisposition Syndromes and Associated Genes', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Syndrome </strong></td>\n<td><strong>Gene</strong></td>\n<td><strong>Chromosome</strong></td>\n<td><strong>Inheritance</strong></td>\n<td><strong>Tumors</strong></td>\n</tr>\n<tr>\n<td>Ataxia telangiectasia</td>\n<td>ATM</td>\n<td>11q22-q23</td>\n<td>AR</td>\n<td>Breast</td>\n</tr>\n<tr>\n<td>Bloom syndrome</td>\n<td>BLM</td>\n<td>15q26.1</td>\n<td>AR</td>\n<td>Several types</td>\n</tr>\n<tr>\n<td>Cowden syndrome</td>\n<td>PTEN</td>\n<td>10q23</td>\n<td>AD</td>\n<td>Breast, thyroid</td>\n</tr>\n<tr>\n<td>Autoimmune lymphoproliferative syndrome</td>\n<td>FASFASL&nbsp;</td>\n<td>10q24 1q23</td>\n<td>AD</td>\n<td>Lymphomas</td>\n</tr>\n<tr>\n<td>Familial adenomatous polyposis&nbsp;</td>\n<td>APC</td>\n<td>5q21</td>\n<td>AD</td>\n<td>Intestinal adenoma, colorectal</td>\n</tr>\n<tr>\n<td>Familial melanoma 1</td>\n<td>p16INK4</td>\n<td>9p2</td>\n<td>AD</td>\n<td>Melanoma, pancreatic</td>\n</tr>\n<tr>\n<td>Familial Wilms’ tumor</td>\n<td>WT1</td>\n<td>11p13</td>\n<td>AD</td>\n<td>Kidney (pediatric)</td>\n</tr>\n<tr>\n<td>Hereditary breast/ovarian cancer &nbsp;</td>\n<td>BRCA1BRCA2&nbsp;</td>\n<td>17q2113q12.3&nbsp;</td>\n<td>AD</td>\n<td>Breast, ovarian, colon, prostate</td>\n</tr>\n<tr>\n<td>Hereditary diffuse gastric cancer</td>\n<td>CDH1</td>\n<td>16q22</td>\n<td>AD</td>\n<td>Stomach</td>\n</tr>\n<tr>\n<td>Hereditary multiple exostoses</td>\n<td>EXT1EXT2&nbsp;</td>\n<td>8q2411p11-12&nbsp;</td>\n<td>AD</td>\n<td>Exostoses, chondrosarcoma</td>\n</tr>\n<tr>\n<td>Hereditary prostate cancer</td>\n<td>HPC1</td>\n<td>1q24-25</td>\n<td>AD</td>\n<td>Prostate</td>\n</tr>\n<tr>\n<td>Hereditary retinoblastoma</td>\n<td>RB1</td>\n<td>13q14.2</td>\n<td>AD</td>\n<td>Retinoblastoma, osteosarcoma</td>\n</tr>\n<tr>\n<td>H