(763, 'Pathology', 'PM1740. Pathological Named Bodies', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td><strong>Bodies</strong></td>\n<td><strong>Found in</strong></td>\n</tr>\n<tr>\n<td>Asteroid bodies</td>\n<td>Sarcoidosis, Sporotrichosis</td>\n</tr>\n<tr>\n<td>\n<p>Halberstaedter–Prowazek bodies</p>\n</td>\n<td>Trachoma&nbsp;</td>\n</tr>\n<tr>\n<td>Miyagawa bodies</td>\n<td>Lymphogranuloma venereum&nbsp;</td>\n</tr>\n<tr>\n<td>Levinthal–Cole–Lillie bodies</td>\n<td>Psittacosis</td>\n</tr>\n<tr>\n<td>Leishman–Donovan bodies</td>\n<td>Kala-azar&nbsp;</td>\n</tr>\n<tr>\n<td>Negri bodies</td>\n<td>Rabies</td>\n</tr>\n<tr>\n<td>Guarnieri bodies</td>\n<td>Vaccinia</td>\n</tr>\n<tr>\n<td>Bollinger bodies</td>\n<td>Fowl pox</td>\n</tr>\n<tr>\n<td>Henderson–Paterson bodies</td>\n<td>Molluscum contagiosum&nbsp;</td>\n</tr>\n<tr>\n<td>Cowdry type-A bodies</td>\n<td>Yellow fever, Herpes virus infection&nbsp;</td>\n</tr>\n<tr>\n<td>Cowdry type-B bodies</td>\n<td>Adenovirus, poliovirus</td>\n</tr>\n<tr>\n<td>Rusell bodies</td>\n<td>Multiple myeloma&nbsp;</td>\n</tr>\n<tr>\n<td>Aschoff bodies</td>\n<td>Rheumatic fever&nbsp;</td>\n</tr>\n<tr>\n<td>Call–Exner bodies</td>\n<td>Granulosa cell tumor of the&nbsp;ovary&nbsp;</td>\n</tr>\n<tr>\n<td>Civatte (colloid) bodies</td>\n<td>Lichen planus&nbsp;</td>\n</tr>\n<tr>\n<td>Michaelis–Gutmann bodies</td>\n<td>Malakoplakia&nbsp;</td>\n</tr>\n<tr>\n<td>Lewy bodies</td>\n<td>Parkinsons disease&nbsp;</td>\n</tr>\n<tr>\n<td>Pappenheimer bodies</td>\n<td>Non-heme iron pigments in siderocytes&nbsp;</td>\n</tr>\n<tr>\n<td>Howell–Jolly bodies</td>\n<td>Splenectomy, Megaloblastic Anemia, Hemolytic anemia&nbsp;</td>\n</tr>\n<tr>\n<td>Heinz bodies</td>\n<td>G-6-PD deficiency&nbsp;</td>\n</tr>\n<tr>\n<td>Gamna–Gandy bodies</td>\n<td>Congestive splenomegaly&nbsp;</td>\n</tr>\n<tr>\n<td>Mallory bodies</td>\n<td>Hyaline inclusions in hepatocytes seen in alcoholic cirrhosis, Indian childhood cirrhosis</td>\n</tr>\n<tr>\n<td>Councilman bodies</td>\n<td>Hepatocytes undergoing apoptosis,&nbsp;seen mainly in acute viral hepatitis.</td>\n</tr>\n<tr>\n<td>Psammoma bodies</td>\n<td>Dystrophic calcification in meningioma, papillary serous cystadenoma of ovary &amp; papillary carcinoma of thyroid, papillary type of renal cell carcinoma.</td>\n</tr>\n<tr>\n<td>Donovan bodies</td>\n<td>Granuloma inguinale&nbsp;</td>\n</tr>\n<tr>\n<td>Nissls bodies</td>\n<td>Cytoplasmic inclusions in neurons&nbsp;</td>\n</tr>\n<tr>\n<td>Mooser bodies</td>\n<td>Typhus fever</td>\n</tr>\n<tr>\n<td>Creola bodies</td>\n<td>Asthma&nbsp;</td>\n</tr>\n<tr>\n<td>Verocay bodies</td>\n<td>Schwannoma&nbsp;</td>\n</tr>\n<tr>\n<td>Schiller–Duval bodies</td>\n<td>Endodermal sinus tumor&nbsp;</td>\n</tr>\n<tr>\n<td>\n<p>Hirano bodies</p>\n</td>\n<td>&nbsp;Alzheimer’s disease</td>\n</tr>\n<tr>\n<td>\n<p>Ferruginous bodies</p>\n</td>\n<td>Asbestosis&nbsp;</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(764, 'Pathology', 'PM1572. Defects in Leukocyte Function', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Disease</strong></p>\n</td>\n<td>\n<p><strong>Defect</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Leukocyte adhesion deficiency 1</p>\n</td>\n<td>\n<p>Mutations in β chain of CD11/CD18 integrins</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Leukocyte adhesion deficiency 2</p>\n</td>\n<td>\n<p>Mutations in fucosyltransferase</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Chediak–Higashi syndrome</p>\n</td>\n<td>\n<p>Decreased leukocyte functions because of mutations affecting protein involved in lysosomal membrane traffic (LYST)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Chronic granulomatous disease</p>\n</td>\n<td>\n<p>Defect in gene encoding phagocyte oxidase</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Myeloperoxidase deficiency</p>\n</td>\n<td>\n<p>Decreased microbial killing because of defective MPO-H<sub>2</sub>O<sub>2</sub> system</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->');
