(765, 'Pathology', 'PM1571. Classification of Transplant Rejection Reactions', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Hyperacute</strong>&nbsp;rejection<strong> (minutes to hours):</strong></p>\n<ul>\n<li class=\page\ title=\Page 246\>Mechanism:&nbsp;<strong>Antidonor antibodies</strong> present in the circulation of the recipient</li>\n<li class=\page\ title=\Page 246\>Histopathological findings:\n<ul>\n<li>Grossly, cyanotic and mottled graft with pale white areas</li>\n<li>Neutrophils accumulate</li>\n<li>Acute fibrinoid necrosis of vessel walls or complete occlusion by thrombi</li>\n</ul>\n</li>\n</ul>\n<p><strong>Acute</strong>&nbsp;rejection<strong> (days to weeks):</strong></p>\n<ol>\n<li class=\page\ title=\Page 246\>Acute<strong> cellular</strong> rejection (acute T cell-mediated rejection):&nbsp;&nbsp;\n<ul>\n<li class=\page\ title=\Page 246\>Mechanism:&nbsp;<strong>CD8+ CTLs</strong> directly destroy graft or <strong>CD4+</strong> cells secrete cytokines that damage graft.</li>\n<li class=\page\ title=\Page 246\>Histopathological findings:\n<ul>\n<li><strong>Tubulointerstitial pattern (type I):</strong> extensive interstitial and tubular inflammation associated with focal tubular injury</li>\n<li><strong>Vascular pattern </strong>- Inflammation (type II) and necrosis (type III): <strong>Endotheliitis</strong> (swollen endothelial cells with lymphocytes between endothelium and vessel wall)</li>\n</ul>\n</li>\n</ul>\n</li>\n<li>Acute<strong> antibody-mediated</strong> (humoral) rejection:&nbsp;\n<ul>\n<li class=\page\ title=\Page 246\>Mechanism: antibodies bind to endothelium and activate complement causing damage and graft failure</li>\n<li class=\page\ title=\Page 246\>Histopathological findings:\n<ul>\n<li>Inflammation of glomeruli and peritubular capillaries.</li>\n<li>Activation of the antibody-dependent classical complement pathway.</li>\n<li>Focal thrombosis in small vessels.&nbsp;</li>\n</ul>\n</li>\n</ul>\n</li>\n</ol>\n<p><strong>Chronic</strong>&nbsp;rejection<strong> (months to years):</strong></p>\n<ul>\n<li class=\page\ title=\Page 246\>Mechanism:&nbsp;<strong>T-cells</strong> react against alloantigens and secrete cytokines which stimulate <strong>fibroblasts</strong> and <strong>vascular smooth muscle cells</strong> (graft arteriosclerosis). Chronic rejection may also be <strong>antibody-mediated</strong>.</li>\n<li class=\page\ title=\Page 246\>Histopathological findings:\n<ul>\n<li>Predominantly vascular changes (intimal thickening and vascular occlusion)</li>\n<li>Glomerulopathy with duplication of basement membrane</li>\n<li>Interstitial fibrosis and tubular atrophy with loss of renal parenchyma</li>\n</ul>\n</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(766, 'Pathology', 'PM1566. Staining Patterns of Antinuclear Antibodies', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>Staining pattern</td>\n<td>Antigen</td>\n</tr>\n<tr>\n<td>Homogenous or diffuse nuclear staining</td>\n<td>Antibodies to <strong>chromatin, histones, and, occasionally, double-stranded DNA</strong></td>\n</tr>\n<tr>\n<td>Rim or peripheral staining</td>\n<td>Antibodies <strong>to double-stranded DNA and, sometimes, to nuclear envelope proteins</strong></td>\n</tr>\n<tr>\n<td>Speckled pattern</td>\n<td>\n<p>One of the most commonly observed patterns of fluorescence, the least specific.&nbsp;</p>\n<p>Antibodies to non-DNA nuclear constituents such as <strong>Sm antigen, ribonucleoprotein, and SS-A and SS-B reactive antigens</strong>.</p>\n</td>\n</tr>\n<tr>\n<td>Nucleolar pattern</td>\n<td>\n<p>Few discrete spots of fluorescence within the nucleus and represent&nbsp;<strong>antibodies to RNA</strong>.</p>\n<p><strong>Seen in systemic sclerosis.</strong></p>\n</td>\n</tr>\n<tr>\n<td>Centromeric pattern</td>\n<td>\n<p>Seen in systemic scleroisis</p>\n<p>Often contain antibodies specific for centromeres, which give rise to this pattern.</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/3325c7c0883f4d26b5d9bfe137cc1ef3x418x262.PNG\><img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/92f4b5c8c24d4479a80293425d9af6f1x418x287.PNG\><!---->\n      </div><!---->\n    </div><!---->'),
(767, 'Pathology', 'PM1562. Non–HLA Genes Associated with Autoimmune Diseases', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\231\>\n<p><strong>Gene involved</strong></p>\n</td>\n<td width=\243\>\n<p><strong>Disease</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p><strong>FOXP3</strong></p>\n</td>\n<td width=\243\>\n<p>IPEX (an acronym for immune dysregulation, polyendocrinopathy, enteropathy, X-linked).</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p><strong>AIRE</strong></p>\n</td>\n<td width=\243\>\n<p>Autoimmune polyendocrinopathy syndrome type 1 (APS-1).</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p><strong>PTPN22</strong></p>\n</td>\n<td width=\243\>\n<p>Rheumatoid arthritis</p>\n<p>Type I DM</p>\n<p>IBD</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p><strong>IL23R</strong></p>\n</td>\n<td width=\243\>\n<p>IBD</p>\n<p>Psoriasis</p>\n<p>Ankylosing spondylitis</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p><strong>CTLA4</strong></p>\n</td>\n<td width=\243\>\n<p>Rheumatoid arthritis</p>\n<p>Type I Diabetes mellitus</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p><strong>L2RA</strong></p>\n</td>\n<td width=\243\>\n<p>Type I Diabetes mellitus</p>\n<p>Multiple sclerosis</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p><strong>NOD2</strong></p>\n<p><strong>ATG16</strong></p>\n</td>\n<td width=\243\>\n<p>Inflammatory bowel disease (IBD)&nbsp;</p>\n<p>&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p><strong>IRF5, IFIH1</strong></p>\n</td>\n<td width=\243\>\n<p>Systemic lupus erythematosus (SLE)</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(768, 'Pathology', 'PM1553. HLA association with diseases', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td colspan=\2\><strong>Autoimmune Gut and Skin</strong> <strong>Diseases and HLA associations</strong></td>\n</tr>\n<tr>\n<td>Gluten-sensitive enteropathy (celiac disease)</td>\n<td>DQ2 , DQ8</td>\n</tr>\n<tr>\n<td>Chronic active hepatitis</td>\n<td>DR3</td>\n</tr>\n<tr>\n<td>Dermatitis herpetiformis</td>\n<td>DR3</td>\n</tr>\n<tr>\n<td>SLE(white)</td>\n<td>DR3</td>\n</tr>\n<tr>\n<td>Sjogren’s syndrome</td>\n<td>DR3</td>\n</tr>\n<tr>\n<td>Psoriasis vulgaris</td>\n<td>Cw6</td>\n</tr>\n<tr>\n<td>Pemphigus vulgaris</td>\n<td>DR4, DQ1</td>\n</tr>\n<tr>\n<td>Bullous pemphigoid variant</td>\n<td>DQ7</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Autoimmune Endocrine</strong> <strong>Diseases and HLA associations</strong></td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>Type 1 diabetes mellitus</td>\n<td>DQ8, DR4, DR3, DR2</td>\n</tr>\n<tr>\n<td>Hyperthyroidism (Graves’)</td>\n<td>B8, DR3</td>\n</tr>\n<tr>\n<td>Hyperthyroidism (Japanese)</td>\n<td>B35</td>\n</tr>\n<tr>\n<td>Adrenal insufficiency</td>\n<td>DR3</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Autoimmune Neurologic</strong> <strong>Diseases and HLA associations</strong></td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>Myasthenia gravis</td>\n<td>B8</td>\n</tr>\n<tr>\n<td>Multiple sclerosis</td>\n<td>B16, DR2</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>&nbsp;<strong>Other Important Diseases and their HLA Associations</strong></td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>\n<p>Ankylosing Spondylitis</p>\n</td>\n<td>B27</td>\n</tr>\n<tr>\n<td>\n<p>Reactive Arthritis</p>\n</td>\n<td>B27</td>\n</tr>\n<tr>\n<td>\n<p>Psoriatic Arthritis</p>\n</td>\n<td>B27</td>\n</tr>\n<tr>\n<td>Behcet’s disease</td>\n<td>B51</td>\n</tr>\n<tr>\n<td>Congenital adrenal hyperplasia</td>\n<td>B47</td>\n</tr>\n<tr>\n<td>Narcolepsy</td>\n<td>DR2 (<strong>DQB1*06:02)</strong></td>\n</tr>\n<tr>\n<td>Goodpasture’s syndrome (anti-GBM)</td>\n<td>DR2</td>\n</tr>\n<tr>\n<td>Abacavir hypersensitivity</td>\n<td>B57 <strong>(</strong><strong>B*57:01)</strong></td>\n</tr>\n<tr>\n<td>Hemochromatosis</td>\n<td>A3</td>\n</tr>\n<tr>\n<td>Narcolepsy</td>\n<td>Q1</td>\n</tr>\n<tr>\n<td>Rheumatoid arthritis</td>\n<td>DR4</td>\n</tr>\n<tr>\n<td>\n<p>Pernicious anemia</p>\n</td>\n<td>DR5&nbsp;</td>\n</tr>\n<tr>\n<td>&nbsp;Hashimoto thyroiditis</td>\n<td>DR3, DR5</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(769, 'Pathology', 'PM1546. Innate and Adaptive Immunity', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\231\>\n<p><strong>Innate immunity</strong></p>\n</td>\n<td width=\231\>\n<p><strong>Adaptive immunity</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Immediate maximal response</p>\n</td>\n<td width=\231\>\n<p>Lag phase between exposure and maximal response</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Non-specific</p>\n</td>\n<td width=\231\>\n<p>Highly pathogen-specific</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Limited diversity</p>\n</td>\n<td width=\231\>\n<p>Extensively diverse</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>No memory</p>\n</td>\n<td width=\231\>\n<p>Memory present</p>\n<p>Subsequent exposure → Amplified response</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Receptors: Pattern-recognition receptors</p>\n</td>\n<td width=\231\>\n<p>Receptors: T-cell and B-cell receptors</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(770, 'Pathology', 'PM0956. Classification of Vascular Tumors and Tumor-like Conditions', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\303\>\n<p><strong>Benign Neoplasms, Developmental and Acquired Conditions</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\303\>\n<ul>\n<li><strong>Hemangioma</strong>\n<ul>\n<li>Capillary hemangioma</li>\n</ul>\n<ul>\n<li>Cavernous hemangioma</li>\n</ul>\n</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td width=\303\>\n<ul>\n<li><strong>Pyogenic granuloma</strong></li>\n</ul>\n</td>\n</tr>\n<tr>\n<td width=\303\>\n<ul>\n<li><strong>Lymphangioma</strong>\n<ul>\n<li>Simple/capillary lymphangioma</li>\n<li>Cavernous lymphangioma (cystic hygroma)</li>\n</ul>\n</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td width=\303\>\n<ul>\n<li><strong>Glomus tumor</strong></li>\n</ul>\n</td>\n</tr>\n<tr>\n<td width=\303\>\n<ul>\n<li><strong>Vascular ectasias</strong>\n<ul>\n<li>Nevus flammeus</li>\n<li>Spider telangiectasia (arterial spider)</li>\n<li>Hereditary hemorrhagic telangiectasis (Osler–Weber–Rendu disease)</li>\n</ul>\n</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td width=\303\>\n<ul>\n<li><strong>Reactive vascular proliferation</strong>\n<ul>\n<li>Bacillary angiomatosis</li>\n</ul>\n</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td width=\303\>\n<p><strong>Intermediate-grade Neoplasms</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\303\>\n<ul>\n<li>Kaposi sarcoma</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td width=\303\>\n<ul>\n<li>Hemangioendothelioma</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td width=\303\>\n<p><strong>Malignant Neoplasm</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\303\>\n<ul>\n<li>Angiosarcoma</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td width=\303\>\n<ul>\n<li>Hemangiopericytoma</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(771, 'Pathology', 'PM0806. Named Cells in Histopathology', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\231\>\n<p><strong>Named cells</strong></p>\n</td>\n<td width=\231\>\n<p><strong>Condition</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Anitschkow or caterpillar cells</p>\n</td>\n<td width=\231\>\n<p>Rheumatic heart disease</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Globoid cells</p>\n</td>\n<td width=\231\>\n<p>Krabbe disease</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Kulchitsky cells</p>\n</td>\n<td width=\231\>\n<p>Small cell carcinoma of lung</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Signet ring cells</p>\n</td>\n<td width=\231\>\n<p>Signet ring cell carcinoma</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Crumpled tissue paper macrophages</p>\n</td>\n<td width=\231\>\n<p>Gauchers disease</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Clue cells</p>\n</td>\n<td width=\231\>\n<p>Bacterial vaginosis</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Fried egg cells</p>\n</td>\n<td width=\231\>\n<p>Oligodendroglioma</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Hallmark cells</p>\n</td>\n<td width=\231\>\n<p>Anaplastic large cell lymphoma</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Shadow cells</p>\n</td>\n<td width=\231\>\n<p>Pilomatricoma</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Mott cells</p>\n</td>\n<td width=\231\>\n<p>Plasma cell myeloma</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Lepidic cells</p>\n</td>\n<td width=\231\>\n<p>Cardiac myxoma</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Spider cells</p>\n</td>\n<td width=\231\>\n<p>Rhabdomyoma</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(772, 'Pathology', 'PM0692. Biochemical Forms of Amyloid and Associated Clinical Conditions', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td width=\231\>\n<p><strong>Clinicopathological category</strong></p>\n</td>\n<td width=\231\>\n<p><strong>Biochemical form of amyloid</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p><strong>Systemic amyloidosis</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Immunocyte dyscrasias with amyloidosis (primary amyloidosis)</p>\n</td>\n<td width=\231\>\n<p>AL</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Reactive systemic amyloidosis (secondary amyloidosis)</p>\n</td>\n<td width=\231\>\n<p>AA</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Hemodialysis-associated amyloidosis</p>\n</td>\n<td width=\231\>\n<p>Aβ2m</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p><strong>Hereditary amyloidosis</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Familial Mediterranean fever</p>\n</td>\n<td width=\231\>\n<p>AA</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Familial amyloidotic neuropathies</p>\n</td>\n<td width=\231\>\n<p>ATTR</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Systemic senile amyloidosis</p>\n</td>\n<td width=\231\>\n<p>ATTR</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p><strong>Localised</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Senile cerebral</p>\n</td>\n<td width=\231\>\n<p>Aβ</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Medullary carcinoma of thyroid</p>\n</td>\n<td width=\231\>\n<p>A Cal</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Islets of Langerhans</p>\n</td>\n<td width=\231\>\n<p>AIAPP</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Isolated atrial amyloidosis</p>\n</td>\n<td width=\231\>\n<p>AANF</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(773, 'Pathology', 'PM0673. Immune Cell Antigens Detected by Monoclonal Antibodies', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Immune cells</strong></p>\n</td>\n<td>\n<p><strong>Antigens associated</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>T-cell</p>\n</td>\n<td>\n<p>CD1, CD3, CD4, CD5, CD8</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>B-cell</p>\n</td>\n<td>\n<p>CD10, CD19, CD20, CD21, CD23, CD79a</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Monocyte or macrophage</p>\n</td>\n<td>\n<p>CD11c, CD13, CD14, CD15, CD33, CD64</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>NK-cell</p>\n</td>\n<td>\n<p>CD16, CD56</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Hematopoietic stem cells</p>\n</td>\n<td>\n<p>CD34</p>\n</td>\n</tr>\n<tr>\n<td>Leukocyte common antigen</td>\n<td>CD45</td>\n</tr>\n<tr>\n<td>Activated B cells, T cells, monocytes, and Reed-Sternberg cells</td>\n<td>CD30</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(774, 'Pathology', 'PM0473. Various Banding Techniques and stains used in Karyotyping', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>The following are the banding techniques and stains used in cytogenetics to produce a visible karyotype by staining condensed chromosomes.</p>\n<table>\n<tbody>\n<tr>\n<td>Banding type</td>\n<td>Staining method</td>\n</tr>\n<tr>\n<td>G-banding (Most common)</td>\n<td>Giemsa stain</td>\n</tr>\n<tr>\n<td>C-banding</td>\n<td>constitutive heterochromatin</td>\n</tr>\n<tr>\n<td>Q-banding</td>\n<td>quinacrine</td>\n</tr>\n<tr>\n<td>R-banding</td>\n<td>reverse Giemsa staining</td>\n</tr>\n<tr>\n<td>T-banding</td>\n<td>telomeric</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(775, 'Surgery,', 'PM0470. Cytokines', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Interleukins</strong></td>\n<td><strong>Main sources</strong></td>\n</tr>\n<tr>\n<td>IL-1 (α and β)</td>\n<td>Macrophages and other cell types</td>\n</tr>\n<tr>\n<td>IL-2</td>\n<td>T cells</td>\n</tr>\n<tr>\n<td>IL-3</td>\n<td>T cells</td>\n</tr>\n<tr>\n<td>IL-4</td>\n<td>T<sub>H&nbsp;</sub>cells</td>\n</tr>\n<tr>\n<td>IL-5</td>\n<td>T<sub>H&nbsp;</sub>cells</td>\n</tr>\n<tr>\n<td>IL-6</td>\n<td>T<sub>H</sub>, macrophages, fibroblasts</td>\n</tr>\n<tr>\n<td>IL-8</td>\n<td>Macrophages, others</td>\n</tr>\n<tr>\n<td>IL-10</td>\n<td>T, B cells, macrophages</td>\n</tr>\n</tbody>\n</table>\n<p><strong>Pro-inflammatory cytokines:&nbsp;</strong></p>\n<ul>\n<li><strong>IL-1: </strong>i<span class=\fontstyle0\>nduces inﬂammation, fever, and sepsis, activates TNF-</span><span class=\fontstyle2\>α; p</span>roliferation and differentiation of T, B, and other cells; bone marrow cell proliferation</li>\n<li><strong>IL-2: </strong>promote growth and differentiation of T and B cells; cytotoxicity of T and NK cells; secretion of other lymphokines</li>\n<li><strong>IL-6: </strong>promote B cell stimulation &amp; differentiation;<span class=\fontstyle0\>&nbsp;mediator of acute phase reactions;&nbsp;</span>IgG production</li>\n<li><strong>IL-8:</strong>&nbsp;attracts and activates neutrophils in inflammatory regions; neutrophil chemotaxis</li>\n<li><strong><strong><strong>IFN-α</strong></strong></strong><strong>:</strong>&nbsp;a<span class=\fontstyle0\>ntiviral, immunoregulatory (enhance MHC class I, NK-cell activity),&nbsp;antiproliferative</span></li>\n<li><strong>TNF-α: </strong>a<span class=\fontstyle0\>ctivate macrophages and cytotoxic cells, induce cachexia, acute phase&nbsp;proteins, cytokines such as IL-1 and IL-6</span></li>\n</ul>\n<p><strong>Anti-inflammatory cytokines:&nbsp;</strong></p>\n<ul>\n<li>IL-4: proliferation of B and cytotoxic cells; increases IgG1 and IgE production; enhances MHC-class II receptor</li>\n<li><strong>IL-10:</strong>&nbsp;i<span class=\fontstyle0\>nhibits IFN-</span><span class=\fontstyle2\>γ </span><span class=\fontstyle0\>and IL-12 production; inhibits</span>&nbsp;IFN production and mononuclear cell functions</li>\n<li>IL-13</li>\n</ul>\n<p><strong>Acute inflammation and sepsis:&nbsp;</strong></p>\n<ul>\n<li>TNF-α</li>\n<li>IL-1</li>\n</ul>\n<p><strong>Special Cytokines:&nbsp;</strong></p>\n<ol>\n<li><strong>IFN-γ:&nbsp;</strong>\n<ul>\n<li>Granuloma formation</li>\n<li><span class=\fontstyle0\>Antiviral</span></li>\n<li><span class=\fontstyle0\>Immunoregulatory (enhances MHC classes I and II and macrophage&nbsp;activation)</span></li>\n<li><span class=\fontstyle0\>Antiproliferative</span></li>\n</ul>\n</li>\n<li><strong>TGF-</strong><strong>β:&nbsp;</strong>\n<ul>\n<li>Fibrosis&nbsp;</li>\n<li><span class=\fontstyle0\>Activate macrophages</span></li>\n</ul>\n</li>\n<li><strong>VEGF: </strong>\n<ul>\n<li>promotes angiogenesis&nbsp;</li>\n</ul>\n</li>\n</ol>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(776, 'Pathology', 'PM0410. Functions of Complement', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/61ef4ad6c59b4001beb2223b04b3ba29x498x600.PNG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ul>\n<li><strong>Inflammation:</strong>&nbsp;C3a, C5a, and, to a lesser extent, C4a are called anaphylatoxins.</li>\n<li>C5a is also a chemotactic agent.</li>\n<li><strong>Opsonization and phagocytosis:</strong>&nbsp;C3b and its cleavage product iC3b (inactive C3b) act as opsonins and promote phagocytosis by neutrophils and macrophages.</li>\n<li><strong>Cell lysis</strong></li>\n</ul>\n<p>This role of complement is important mainly for the killing of microbes with thin cell walls, such as <em>Neisseria </em>species, and deficiency of the terminal components of complement predisposes to <em>Neisseria </em>infections.</p></div><!---->\n      <!---->\n    </div><!---->'),
(777, 'Pathology', 'PM0394. Factors Influencing Healing', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ul>\n<li><strong>Infection:</strong>&nbsp;one of the most important causes of delay in healing; prolongs inflammation; increases the local tissue injury</li>\n<li><strong>Diabetes:&nbsp;</strong>most important systemic cause of abnormal wound healing</li>\n<li><strong>Nutritional status:&nbsp;</strong>protein deficiency and vitamin C deficiency inhibit collagen synthesis and retard healing.</li>\n<li><strong>Glucocorticoids (steroids):</strong>&nbsp;anti-inflammatory effects; results in weakness of the scar due to inhibition of TGF-β production and diminished fibrosis</li>\n<li><strong>Mechanical factors: </strong>such as&nbsp;increased local pressure or torsion; may cause wounds to pull apart or dehisce</li>\n<li><strong>Poor perfusion:&nbsp;</strong>due to arteriosclerosis, diabetes, or obstructed venous drainage; also impairs healing</li>\n<li><strong>Foreign bodies:&nbsp;</strong>such as fragments of steel, glass, or even bone; impede healing</li>\n<li><strong>Type and extent of tissue injury:&nbsp;</strong>affect subsequent repair;</li>\n<li><strong>Location of the injury </strong>and the <strong>character</strong> of the injured<strong> tissue:</strong>&nbsp;also important</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(778, 'Pathology', 'PM0393. Acute phase reactants and their functions', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td colspan=\2\>\n<p><strong>\Positive\ acute-phase proteins:</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Protein</strong></p>\n</td>\n<td>\n<p><strong>Immune system&nbsp;function</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>C-reactive protein</strong></p>\n</td>\n<td>\n<p>Opsonin&nbsp;on microbes</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Serum amyloid P component</strong></p>\n</td>\n<td>\n<p>Opsonin</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Serum amyloid A</strong></p>\n</td>\n<td>\n<p>Recruitment of immune cells to inflammatory sites</p>\n<p>Induction of enzymes that degrade extracellular matrix</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Complement factors</strong></p>\n</td>\n<td>\n<p>Opsonization, lysis, and clumping of target cells; Chemotaxis</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Mannan-binding lectin</strong></p>\n</td>\n<td>\n<p>Mannan-binding lectin pathway of complement activation</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Fibrinogen,&nbsp;prothrombin, factor VIII,<br>von Willebrand factor</strong></p>\n</td>\n<td>\n<p>Coagulation factors, trapping invading microbes in blood clots.<br>Some cause chemotaxis, Fibrinogen binds to red cells and induce rouleaux formation</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Plasminogen activator inhibitor-1 (PAI-1)</strong></p>\n</td>\n<td>\n<p>Prevents the degradation of blood clots by inhibiting plasminogen&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Alpha 2-macroglobulin</strong></p>\n</td>\n<td>\n<p>Inhibitor of coagulation by inhibiting thrombin</p>\n<p>Inhibitor of fibrinolysis by inhibiting plasmin</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Ferritin</strong></p>\n</td>\n<td>\n<p>Binding iron, inhibiting microbe iron uptake</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Hepcidin</strong></p>\n</td>\n<td>\n<p>Stimulates the internalization of ferroportin, preventing release of iron bound by ferritin within intestinal enterocytes and macrophages</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Ceruloplasmin</strong></p>\n</td>\n<td>\n<p>Oxidizes iron, facilitating for ferritin, inhibiting microbe iron uptake</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Haptoglobin</strong></p>\n</td>\n<td>\n<p>Binds hemoglobin, inhibiting microbe iron uptake</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Orosomucoid</strong><strong><br> </strong><strong>(Alpha-1-acid glycoprotein, AGP)</strong></p>\n</td>\n<td>\n<p>Steroid carrier</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Alpha 1-antitrypsin</strong></p>\n</td>\n<td>\n<p>Serpin, downregulates inflammation</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Alpha 1-antichymotrypsin</strong></p>\n</td>\n<td>\n<p>Serpin, downregulates inflammation</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Thrombopoietin</strong></p>\n</td>\n<td>\n<p>Thrombocytosis</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(779, 'Pathology', 'PM0389. Selectins and Associated Ligands', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\150\>\n<p><strong>Molecule</strong></p>\n</td>\n<td width=\150\>\n<p><strong>Distribution</strong></p>\n</td>\n<td width=\150\>\n<p><strong>Ligand</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\150\>\n<p>L- selectin (CD62L)</p>\n</td>\n<td width=\150\>\n<p>Neutrophils, monocytes</p>\n<p>T cells (naïve and central memory)</p>\n<p>B cells (naïve)</p>\n</td>\n<td width=\150\>\n<p>Sialyl–Lewis X/PNAd on GlyCAM-1, CD34, MAdCAM-1 Others; expressed on endothelium (HEV)</p>\n</td>\n</tr>\n<tr>\n<td width=\150\>\n<p>E- selectin (CD62E)</p>\n</td>\n<td width=\150\>\n<p>Endothelium activated by cytokines (TNF, IL-1</p>\n</td>\n<td width=\150\>\n<p>Sialyl–Lewis X (e.g., CLA) on glycoproteins; expressed on neutrophils, monocytes, T cells (effector, memory)</p>\n</td>\n</tr>\n<tr>\n<td width=\150\>\n<p>P- selectin (CD62P)</p>\n</td>\n<td width=\150\>\n<p>Endothelium activated by cytokines (TN, IL-1), histamine, or thrombin</p>\n</td>\n<td width=\150\>\n<p>Sialyl–Lewis X on PSGL- 1 and other glycoproteins; expressed on neutrophils, monocytes, T cells (effector, memory)</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(780, 'Pathology', 'PM0388. Integrins with Associated Ligands', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\150\>\n<p><strong>Integrins</strong></p>\n</td>\n<td width=\150\>\n<p><strong>Distribution</strong></p>\n</td>\n<td width=\150\>\n<p><strong>Ligands</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\150\>\n<p>LFA-1 (CD11aCD18)</p>\n</td>\n<td width=\150\>\n<p>Neutrophils, monocytes, T cells (naïve, effector, memory)</p>\n</td>\n<td width=\150\>\n<p>ICAM-1 (CD54), ICAM-2 (CD 102); expressed on endothelium (upregulated on activated endothelium)</p>\n</td>\n</tr>\n<tr>\n<td width=\150\>\n<p>MAC-1 (CD11bCD18)</p>\n</td>\n<td width=\150\>\n<p>Monocytes, dendritic cells (DCs)</p>\n</td>\n<td width=\150\>\n<p>ICAM- 1 (CD54), ICAM-2 (CD- 102); expressed on endothelium (upregulated on activated endothelium)</p>\n</td>\n</tr>\n<tr>\n<td width=\150\>\n<p>VLA-4 (CD49aCD29)</p>\n</td>\n<td width=\150\>\n<p>Monocytes</p>\n<p>T cells (naïve, effector, memory)</p>\n</td>\n<td width=\150\>\n<p>VCAM-1 (CD 106); expressed on endothelium (upregulated on activated endothelium)</p>\n</td>\n</tr>\n<tr>\n<td width=\150\>\n<p>a4b7 (CD49DCD29)</p>\n</td>\n<td width=\150\>\n<p>Monocytes</p>\n<p>T cells (gut homing naïve effector, memory)</p>\n</td>\n<td width=\150\>\n<p>VCAM- 1 (CD106), MAdCAM- 1; expressed on endothelium in gut and gut- associated lymphoid tissues</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(781, 'Pathology', 'PM0385. Comparison of Acute and Chronic Inflammation', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\150\>\n<p><strong>Feature</strong></p>\n</td>\n<td width=\150\>\n<p><strong>Acute</strong></p>\n</td>\n<td width=\150\>\n<p><strong>Chronic</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\150\>\n<p>Onset</p>\n</td>\n<td width=\150\>\n<p>Rapid: minutes or hours</p>\n</td>\n<td width=\150\>\n<p>Slow: days</p>\n</td>\n</tr>\n<tr>\n<td width=\150\>\n<p>Cellular infiltrate</p>\n</td>\n<td width=\150\>\n<p>Mainly neutrophils</p>\n</td>\n<td width=\150\>\n<p>Monocytes/macrophages and lymphocytes</p>\n</td>\n</tr>\n<tr>\n<td width=\150\>\n<p>Tissue injury, fibrosis</p>\n</td>\n<td width=\150\>\n<p>Usually mild and self-limited</p>\n</td>\n<td width=\150\>\n<p>Often severe and progressive</p>\n</td>\n</tr>\n<tr>\n<td width=\150\>\n<p>Local and systemic signs</p>\n</td>\n<td width=\150\>\n<p>Promient</p>\n</td>\n<td width=\150\>\n<p>Less</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(782, 'Pathology', 'PM0384. Diseases Caused by Inflammation and Cells Involved', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\231\>\n<p><strong>Disorders</strong></p>\n</td>\n<td width=\231\>\n<p><strong>Cells and Molecules involved</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p><strong>Acute</strong><em><br></em></p>\n</td>\n<td width=\231\>\n<p>&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>ARDS</p>\n</td>\n<td width=\231\>\n<p>Neutrophils</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Asthma</p>\n</td>\n<td width=\231\>\n<p>Eosinophls, IgE antibodies</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Glomerulonephritis</p>\n</td>\n<td width=\231\>\n<p>Antibodies complement, neutrophils, monocytes</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Septic shock</p>\n</td>\n<td width=\231\>\n<p>Cytokines</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>&nbsp;</p>\n</td>\n<td width=\231\>\n<p>&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p><strong>Chronic</strong></p>\n</td>\n<td width=\231\>\n<p>&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Arthritis</p>\n</td>\n<td width=\231\>\n<p>Lymphocytes and macrophages</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Asthma</p>\n</td>\n<td width=\231\>\n<p>Eosinophls, IgE antibodies</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Atherosclerosis</p>\n</td>\n<td width=\231\>\n<p>Macrophages, lymphocytes</p>\n</td>\n</tr>\n<tr>\n<td width=\231\>\n<p>Pulmonary fibrosis</p>\n</td>\n<td width=\231\>\n<p>Macrophages, fibroblasts</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>ARDS: Acute respiratory distress syndrome</p></div><!---->\n      <!---->\n    </div><!---->'),
(783, 'Pathology', 'PM0324. Causes of Disseminated Intravascular Coagulation (DIC)', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Disseminated intravascular coagulation (DIC) is an acute, subacute, or chronic thrombohemorrhagic disorder occurring as a secondary complication in a variety of diseases. It is characterized by the activation of the coagulation sequence that leads to the formation of microthrombi throughout the microcirculation of the body. As a consequence of the thrombotic diathesis, there is a consumption of platelets, fibrin, and coagulation factors and, secondarily, activation of fibrinolytic mechanisms.</p>\n<p><strong>Causes of DIC:</strong></p>\n<table>\n<tbody>\n<tr>\n<td><strong>Obstetric complications</strong></td>\n<td>\n<ul>\n<li>Abruptio placenta</li>\n<li>Retained dead fetus</li>\n<li>Septic abortion</li>\n<li>Amniotic fluid embolism</li>\n<li>Toxemia</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td><strong>Infections</strong></td>\n<td>\n<ul>\n<li>Sepsis</li>\n<li>Meningococcemia</li>\n<li>Histoplasmosis</li>\n<li>Aspergillosis</li>\n<li>Malaria</li>\n<li>Toxemia&nbsp;</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td><strong>Neoplasms</strong></td>\n<td>\n<ul>\n<li>Ca. pancreas</li>\n<li>Ca. prostate</li>\n<li>Ca. lung</li>\n<li>Acute promyelocytic leukemia</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td><strong>Massive Tissue Injury</strong></td>\n<td>\n<ul>\n<li>Traumatic injury</li>\n<li>Burns</li>\n<li>Extensive surgery</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td><strong>Miscellaneous</strong></td>\n<td>\n<ul>\n<li>Acute intravascular hemolysis</li>\n<li>Snake bite</li>\n<li>Shock</li>\n<li>Stroke</li>\n<li>Vasculitis</li>\n<li>Aortic aneurysm</li>\n<li>Liver disease</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(784, 'Pathology', 'PM0322. Embolism: Key Points', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Embolism: Key Points</strong></p>\n<ul>\n<li><strong>An embolus</strong> is a solid, liquid, or gaseous mass carried by blood to a site distant from its origin; most are dislodged thrombi.</li>\n<li><strong>Pulmonary emboli</strong> derive primarily from lower extremity deep vein thrombi; their effects depend mainly on the size of the embolus and the location in which it lodges. Consequences may include right-sided heart failure, pulmonary hemorrhage, pulmonary infarction, or sudden death.</li>\n<li><strong>Systemic emboli</strong>&nbsp;arise primarily from cardiac mural or valvular thrombi, aortic aneurysms, or atherosclerotic plaques; whether an embolus causes tissue infarction depends on the site of embolization and the presence or absence of collateral circulation.</li>\n<li><strong>Fat emboli:</strong>&nbsp;Fat embolism syndrome is characterized by <strong>pulmonary insufficiency</strong>, neurologic symptoms (irritability, restlessness, and even coma), <strong>anemia</strong>, and <strong>thrombocytopenia</strong> (manifesting as diffuse petechial rash). It is seen after fractures of long bones (which contain fatty marrow) or after soft-tissue trauma. It is fatal only in 10% of cases. The pathogenesis involves both mechanical obstruction and free fatty acids causing local toxic injury to endothelium.</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(785, 'Pathology', 'PM0321. Thrombosis-Key Points', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ul>Thrombosis-Key Points:</ul>\n<ul>\n<li>Thrombus development usually is related to one or more components of the Virchow triad:\n<ul>\n<li><strong>Endothelial injury</strong> (e.g., by toxins, hypertension, inflammation, or metabolic products) associated with endothelial activation and changes in endothelial gene expression that favor coagulation</li>\n<li><strong>Abnormal blood flow</strong>—stasis or turbulence (e.g., due to aneurysms, atherosclerotic plaque)</li>\n<li><strong>Hypercoagulability</strong>, either primary (e.g., factor V Leiden, increased prothrombin synthesis, antithrombin III deficiency) or secondary (e.g., bed rest, tissue damage, malignancy, or development of antiphospholipid antibodies [antiphospholipid antibody syndrome]) or antibodies against platelet factor IV/heparin complexes [heparin-induced thrombocytopenia])</li>\n</ul>\n</li>\n<li>Thrombi may <strong>propagate</strong>, <strong>resolve</strong>, become <strong>organized</strong>, or <strong>embolize</strong>.</li>\n<li>Thrombosis causes tissue injury by <strong>local vascular occlusion</strong> or by <strong>distal embolization</strong>.</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(786, 'Pathology', 'PM0320. Arterial and Venous Thrombi', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Feature</strong></td>\n<td><strong>Arterial thrombus</strong></td>\n<td><strong>Venous thrombus</strong></td>\n</tr>\n<tr>\n<td>Pathogenesis</td>\n<td>Endothelial injury or site of turbulence</td>\n<td>Stasis of blood</td>\n</tr>\n<tr>\n<td>Blood flow</td>\n<td>Associated with active blood flow</td>\n<td>Associated with sluggish blood flow</td>\n</tr>\n<tr>\n<td>Sites</td>\n<td>Coronary, cerebral and femoral arteries</td>\n<td>Superficial and deep leg veins, ovarian periuterine veins</td>\n</tr>\n<tr>\n<td>Propagation</td>\n<td>Grows in a retrograde manner from point of attachment</td>\n<td>Grows in an antegrade manner from point of attachment</td>\n</tr>\n<tr>\n<td>Line of Zahn</td>\n<td>Present</td>\n<td>Present</td>\n</tr>\n<tr>\n<td>Microscopic</td>\n<td>\n<div class=\page\ title=\Page 135\>\n<div class=\section\>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>Friable meshwork of platelets, fibrin, red cells, and degenerating leukocytes (<strong>White thrombi</strong>).</p>\n</div>\n</div>\n</div>\n</div>\n</td>\n<td>Red cells mixed with relatively less platelets, so also called as <strong>red thrombi</strong></td>\n</tr>\n<tr>\n<td>Occlusion</td>\n<td>Incomplete lumen occlusion</td>\n<td>Complete vessel occlusion</td>\n</tr>\n<tr>\n<td>Complications</td>\n<td>Ischemia and infarction of organs</td>\n<td>Embolism, edema and ulceration</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(787, 'Pathology,', 'PM0319. Hypercoagulable states', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Primary (Genetic Conditions):</p>\n<ul>\n<li>Mutations in factor V (Most common)</li>\n<li>Antithrombin III deficiency</li>\n<li>Protein C or S deficiency</li>\n<li>Fibrinolysis defects</li>\n<li>Homocysteinemia</li>\n<li>Allelic variations in prothrombin levels</li>\n<li>Mutations in the methyl tetra hydro folate (MTHF) gene</li>\n</ul>\n<p>Secondary (Acquired Conditions):</p>\n<ul>\n<li>Prolonged bed rest or immobilization</li>\n<li>Homocysteinemia</li>\n<li>Tissue damage (Surgery, fracture, burns)</li>\n<li>Cancer</li>\n<li>Myocardial infarction, Prosthetic cardiac valves</li>\n<li>Disseminated intravascular coagulation (DIC)</li>\n<li>Heparin-induced thrombocytopenia</li>\n<li>Antiphospholipid antibody syndrome</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(788, 'Pathology,', 'PM0318. Important Anticoagulant Substances', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ul>\n<li><strong>Antithrombin III:</strong>&nbsp;It inhibits the&nbsp;activity of thrombin and other factors like XIIa, XIa, Xa,&nbsp;and IXa. It is activated by binding to heparin-like molecules.</li>\n<li><strong>Proteins C and S:</strong>&nbsp;These proteins are vitamin K–dependent proteins which inactivate Va and VIIIa.</li>\n<li><strong>Endothelial prostacyclin (PGI2) and nitric oxide (NO):</strong>&nbsp;These are potent vasodilators and inhibitors of platelet aggregation.</li>\n<li><strong>Tissue factor pathway inhibitor: </strong>This is derived from the endothelium and inhibits tissue factor VIIa and Xa molecules.</li>\n<li><strong>Thrombomodulin:</strong>&nbsp;It is produced by all endothelial cells except those in the cerebral microcirculation. It binds to thrombin, and this complex activates protein C (in the presence&nbsp;of protein S), which finally inactivates factor V and VIII. This action results in an&nbsp;anticoagulant effect.</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(789, 'Pathology', 'PM0314. Hyperemia and Congestion', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Hyperemia and Congestion:</strong></p>\n<table>\n<tbody>\n<tr>\n<td><strong>Hyperemia</strong></td>\n<td><strong>Congestion</strong></td>\n</tr>\n<tr>\n<td>Active process due to arteriolar dilation</td>\n<td>Passive process due to impaired venous outflow</td>\n</tr>\n<tr>\n<td>Edema is absent</td>\n<td>Edema is present</td>\n</tr>\n<tr>\n<td>Red color of the tissues</td>\n<td>Blue-red color of the tissue (due to deoxyhemoglobin)&nbsp;</td>\n</tr>\n<tr>\n<td>Seen in inflammation</td>\n<td>Seen in right heart failure and portal venous obstruction in cirrhosis</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(790, 'Pathology', 'PM0313. Edema: Key Points', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Edema: Key Points</strong></p>\n<p><strong>Edema</strong> is the result of the movement of fluid from the vasculature into the interstitial spaces; the fluid may be <strong>protein-poor (transudate)</strong> or <strong>protein-rich (exudate).</strong></p>\n<p><strong>Causes:</strong></p>\n<ul>\n<li><strong>Increased hydrostatic pressure</strong> (e.g., heart failure)</li>\n<li><strong>Decreased colloid osmotic pressure</strong> caused by reduced plasma albumin, either due to decreased synthesis (e.g., liver disease, protein malnutrition) or to increased loss (e.g., nephrotic syndrome)</li>\n<li><strong>Increased vascular permeability</strong> (e.g., inflammation), which is usually localized but may occur throughout the body in severe systemic inflammatory states such as sepsis</li>\n<li><strong>Lymphatic obstruction</strong> (e.g., infection or neoplasia)</li>\n<li><strong>Sodium and water retention</strong> (e.g., renal failure)</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(791, 'Surgery,', 'PM0298. Tumor Markers', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Tumor Markers</strong></p>\n<table>\n<tbody>\n<tr>\n<td>\n<p><strong>Tumor Markers</strong></p>\n</td>\n<td>\n<p><strong>Tumor Types</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Hormones</strong></p>\n</td>\n<td>\n<p>&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Human chorionic gonadotropin</p>\n</td>\n<td>\n<p>Trophoblastic tumors, nonseminonmatous testicular tumors</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Calcitonin</p>\n</td>\n<td>\n<p>Medullary carcinoma of thyroid</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Catecholamine and metabolites</p>\n</td>\n<td>\n<p>Pheochromocytoma and related tumors</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Oncofetal Antigens</strong></p>\n</td>\n<td>\n<p>&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>α-Fetoprotein</p>\n</td>\n<td>\n<p>Liver cell cancer, nonseminonmatous germ-cell tumors of the&nbsp;testis</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Carcinoembryonic antigen</p>\n</td>\n<td>\n<p>Carcinomas of the colon, lung, stomach, and heart</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><strong>Enzymes</strong></p>\n</td>\n<td>\n<p>&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Prostatic acid phosphatase</p>\n</td>\n<td>\n<p>Prostate cancer</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Neuron-specific enolase</p>\n</td>\n<td>Small-cell cancer of the lung, neuroblastoma</td>\n</tr>\n<tr>\n<td>\n<p>Lactate dehydrogenase (LDH)</p>\n</td>\n<td>Germ-cell tumors, lymphomas, leukemias</td>\n</tr>\n<tr>\n<td>Thymidine kinase</td>\n<td>Leukemias</td>\n</tr>\n<tr>\n<td>Pyruvate kinase</td>\n<td>Renal cell carcinoma</td>\n</tr>\n<tr>\n<td>Placental alkaline phosphatase</td>\n<td>Seminoma</td>\n</tr>\n<tr>\n<td>\n<p><strong>Specific Proteins</strong></p>\n</td>\n<td>\n<p>&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Immunoglobulins</p>\n</td>\n<td>\n<p>Multiple myeloma and other gammopathies</p>\n</td>\n</tr