(793, 'Pathology', 'PM0290. Protooncogenes, Oncogenes and Oncoproteins', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Protooncogenes, Oncogenes, and Oncoproteins</strong></p>\n<ul>\n<li><strong>Proto-oncogenes</strong>: normal cellular genes whose products promote cell proliferation</li>\n<li><strong>Oncogenes</strong>: mutated or overexpressed versions of proto-oncogenes that function autonomously, having lost dependence on normal growth promoting signals</li>\n<li><strong>Oncoprotein: </strong>a protein encoded by an oncogene that drives increased cell proliferation through one of the several mechanisms</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(794, 'Pathology', 'PM0282. Inhibitors of Mitogenic Signaling Pathway', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\86\>\n<p><strong>Gene</strong></p>\n</td>\n<td width=\89\>\n<p><strong>Protein</strong></p>\n</td>\n<td width=\88\>\n<p><strong>Function</strong></p>\n</td>\n<td width=\95\>\n<p><strong>Familial Syndromes</strong></p>\n</td>\n<td width=\92\>\n<p><strong>Sporadic Cancers</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\86\>\n<p><em>APC</em></p>\n</td>\n<td width=\89\>\n<p>Adenomatous polyposis coli protein</p>\n</td>\n<td width=\88\>\n<p>Inhibitor of WNT signaling</p>\n</td>\n<td width=\95\>\n<p>Familial colonic polyps and carcinomas</p>\n</td>\n<td width=\92\>\n<p>Carcinomas of stomach, colon, pancreas, melanoma</p>\n</td>\n</tr>\n<tr>\n<td width=\86\>\n<p><em>NF1</em></p>\n</td>\n<td width=\89\>\n<p>Neurofibromin-1</p>\n</td>\n<td width=\88\>\n<p>Inhibitor of RAS/MAPK signaling</p>\n</td>\n<td width=\95\>\n<p>Neurofibromatosis type 1</p>\n<p>(neurofibromas and malignant peripheral nerve sheath tumors)</p>\n<p>&nbsp;</p>\n</td>\n<td width=\92\>\n<p>Neuroblastoma, juvenile myeloid leukemia</p>\n</td>\n</tr>\n<tr>\n<td width=\86\>\n<p><em>NF2</em></p>\n</td>\n<td width=\89\>\n<p>Merlin</p>\n</td>\n<td width=\88\>\n<p>Cytoskeletal stability, Hippo pathway signaling</p>\n</td>\n<td width=\95\>\n<p>Neurofibromatosis type 2 (acoustic schwannoma and meningioma</p>\n</td>\n<td width=\92\>\n<p>Schwannoma, meningioma</p>\n</td>\n</tr>\n<tr>\n<td width=\86\>\n<p><em>PTCH</em></p>\n</td>\n<td width=\89\>\n<p>Patched</p>\n</td>\n<td width=\88\>\n<p>Inhibitor of Hedgehog signaling</p>\n</td>\n<td width=\95\>\n<p>Gorlin syndrome (basal cell carcinoma, medulloblastoma, several benign tumors)</p>\n</td>\n<td width=\92\>\n<p>Basal cell carcinoma, medulloblastoma</p>\n</td>\n</tr>\n<tr>\n<td width=\86\>\n<p><em>PTEN</em></p>\n</td>\n<td width=\89\>\n<p>Phosphatase and tensin homologue</p>\n</td>\n<td width=\88\>\n<p>Inhibitor of PI3K/AKT signaling</p>\n</td>\n<td width=\95\>\n<p>Cowden syndrome (variety of benign skin, GI and CNS growths; Breast, endometrial and thyroid carcinoma)</p>\n</td>\n<td width=\92\>\n<p>Diverse cancers, particularly carcinomas and lymphoid tumors</p>\n</td>\n</tr>\n<tr>\n<td width=\86\>\n<p><em>SMAD2, SMAD4</em></p>\n</td>\n<td width=\89\>\n<p>SMAD2, SMAD4</p>\n</td>\n<td width=\88\>\n<p>Component of TGFb signaling pathway, repressors of MYC and CDK4 expression, inducers of CDK inhibitor expression</p>\n</td>\n<td width=\95\>\n<p>Juvenile polyposis</p>\n</td>\n<td width=\92\>\n<p>Frequently mutated (along with other components of the TGFb signaling pathway) in colonic and pancreatic carcinoma</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(795, 'Pathology', 'PM0281. Cell Cycle Regulators and Associated Cancers', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\113\>\n<p><strong>Cell cycle regulator</strong></p>\n</td>\n<td width=\113\>\n<p><strong>Proto-oncogene</strong></p>\n</td>\n<td width=\113\>\n<p><strong>Mode of activation in tumor</strong></p>\n</td>\n<td width=\113\>\n<p><strong>Associated tumor</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\113\>\n<p>Cyclins</p>\n</td>\n<td width=\113\>\n<p>CCND1 (Cyclin D1)</p>\n</td>\n<td width=\113\>\n<p>Translocation</p>\n<p>Amplification</p>\n</td>\n<td width=\113\>\n<p>Mantle cell lymphoma, multiple myeloma</p>\n<p>Breast and esophageal cancers</p>\n</td>\n</tr>\n<tr>\n<td width=\113\>\n<p>Cyclin-dependent kinase</p>\n</td>\n<td width=\113\>\n<p>CDK4</p>\n</td>\n<td width=\113\>\n<p>Amplification or point mutation</p>\n</td>\n<td width=\113\>\n<p>Glioblastoma, melanoma, sarcoma</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(796, 'Pathology', 'PM0280. Cancers associated with important oncogenes', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\0\>\n<tbody>\n<tr>\n<td>\n<p><strong>Oncogenes</strong></p>\n</td>\n<td><strong>Associated cancers</strong></td>\n</tr>\n<tr>\n<td>\n<p><em>c-MYC</em></p>\n</td>\n<td>\n<p>Burkitt’s lymphoma</p>\n<p>Adenocarcinomas of the breast, lung, and colon</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><em>N-MYC</em></p>\n</td>\n<td>\n<p>Neuroblastoma</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><em>L-MYC</em></p>\n</td>\n<td>\n<p>Small-cell carcinoma of the lung</p>\n</td>\n</tr>\n<tr>\n<td><em>K-RAS</em></td>\n<td>Colon, lung, and pancreatic tumors</td>\n</tr>\n<tr>\n<td><em>N-RAS</em></td>\n<td>\n<p>Melanoma</p>\n<p>AML</p>\n</td>\n</tr>\n<tr>\n<td><em>H-RAS</em></td>\n<td>Bladder and kidney tumors</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(797, 'Pathology', 'PM0279. Protein involved in Signal transduction and Associated Cancers', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Protein involved in Signal transduction and Associated Cancers</strong></p>\n<table border=\1\>\n<tbody>\n<tr>\n<td>\n<p>GTP-binding (G) proteins</p>\n</td>\n<td>\n<p><em>KRAS</em></p>\n<p><em>HRAS</em></p>\n<p><em>NRAS</em></p>\n<p><em>GNAQ</em></p>\n<p><em>GNAS</em></p>\n</td>\n<td>\n<p>Point mutation</p>\n<p>Point mutation</p>\n<p>Point mutation</p>\n<p>Point mutation</p>\n<p>Point mutation</p>\n</td>\n<td>\n<p>Colon, lung, and pancreatic tumors</p>\n<p>Bladder and kidney tumors</p>\n<p>Melanomas, hematologic, malignancies</p>\n<p>Uveal melanoma</p>\n<p>Pituitary adenoma, other endocrine tumors</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Nonreceptor tyrosine kinase</p>\n</td>\n<td>\n<p><em>ABL</em></p>\n</td>\n<td>\n<p>Translocation</p>\n<p>Point mutation</p>\n</td>\n<td>\n<p>Chronic myelogenous leukemia</p>\n<p>Acute lymphoblastic leukemia</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>RAS signal transduction</p>\n</td>\n<td>\n<p><em>BRAF</em></p>\n</td>\n<td>\n<p>Point mutation, Translocation</p>\n</td>\n<td>\n<p>Melanomas, leukemias, colon carcinoma, others</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Notch signal transduction</p>\n</td>\n<td>\n<p><em>NOTCH1</em></p>\n</td>\n<td>\n<p>Point mutation, Translocation Gene rearrangement</p>\n</td>\n<td>\n<p>Leukemias, lymphomas, breast carcinoma</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>JAK/STAT signal transduction</p>\n</td>\n<td>\n<p><em>JAK2</em></p>\n</td>\n<td>\n<p>Translocation</p>\n</td>\n<td>\n<p>Myeloproliferative disorders</p>\n<p>Acute lymphoblastic leukemia</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
