(800, 'Pathology', 'PM0275. Etiological Agents for Cancers', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\150\>\n<p><strong>Agents</strong></p>\n</td>\n<td width=\150\>\n<p><strong>Associated cancers</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\150\>\n<p>Arsenic and arsenic compounds</p>\n</td>\n<td width=\150\>\n<p>Lung carcinoma, skin carcinoma</p>\n</td>\n</tr>\n<tr>\n<td width=\150\>\n<p>Asbestos</p>\n</td>\n<td width=\150\>\n<p>Lung, oesophageal, gastric, and colon carcinomas; Mesothelioma</p>\n</td>\n</tr>\n<tr>\n<td width=\150\>\n<p>Benzene</p>\n</td>\n<td width=\150\>\n<p>Acute myeloid leukemia</p>\n</td>\n</tr>\n<tr>\n<td width=\150\>\n<p>Beryllium and beryllium compounds</p>\n</td>\n<td width=\150\>\n<p>Lung carcinoma</p>\n</td>\n</tr>\n<tr>\n<td width=\150\>\n<p>Cadmium and cadmium compounds</p>\n</td>\n<td width=\150\>\n<p>Prostate carcinoma</p>\n</td>\n</tr>\n<tr>\n<td width=\150\>\n<p>Chromium compounds</p>\n</td>\n<td width=\150\>\n<p>Lung carcinoma</p>\n</td>\n</tr>\n<tr>\n<td width=\150\>\n<p>Nickel compounds</p>\n</td>\n<td width=\150\>\n<p>Lung and oropharyngeal carcinoma</p>\n</td>\n</tr>\n<tr>\n<td width=\150\>\n<p>Radon and its decay products</p>\n</td>\n<td width=\150\>\n<p>Lung carcinoma</p>\n</td>\n</tr>\n<tr>\n<td width=\150\>\n<p>Vinyl chloride</p>\n</td>\n<td width=\150\>\n<p>Hepatic angiosarcoma</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(801, 'Medicine,', 'PM0272. Paraneoplastic Syndromes and Underlying Cancers', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\0\ width=\472\>\n<tbody>\n<tr>\n<td width=\157\>\n<p><strong>Clinical Syndromes/&nbsp;</strong></p>\n<p><strong>Endocrinopathies</strong></p>\n</td>\n<td width=\157\>\n<p><strong>Major Forms of Underlying Cancer</strong></p>\n</td>\n<td width=\157\>\n<p><strong>Causal Mechanism</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\157\>\n<p>Cushing Syndrome</p>\n</td>\n<td width=\157\>\n<p>Small-cell carcinoma of lung</p>\n<p>Pancreatic carcinoma</p>\n<p>Neural tumours</p>\n<p>Renal carcinoma</p>\n</td>\n<td width=\157\>\n<p>ACTH or ACTH- like substance</p>\n</td>\n</tr>\n<tr>\n<td width=\157\>\n<p>Syndrome of inappropriate antidiuretic hormone secretion (SIADH)</p>\n</td>\n<td width=\157\>\n<p>Small-cell carcinoma of lung</p>\n<p>Intracranial neoplasms</p>\n</td>\n<td width=\157\>\n<p>Antidiuretic hormone or atrial natriuretic hormones</p>\n</td>\n</tr>\n<tr>\n<td width=\157\>\n<p>Hypercalcemia</p>\n</td>\n<td width=\157\>\n<p>Squamous cell carcinoma of lung</p>\n<p>Breast carcinoma</p>\n<p>Renal carcinoma</p>\n<p>Adult T-cell leukemia/lymphoma</p>\n</td>\n<td width=\157\>\n<p>Parathyroid hormone-related protein(PTHRP), TGF-a, TNF, IL-1</p>\n</td>\n</tr>\n<tr>\n<td width=\157\>\n<p>Hypoglycaemia</p>\n</td>\n<td width=\157\>\n<p>Ovarian carcinoma</p>\n<p>Fibrosarcoma</p>\n<p>Other mesenchymal sarcomas</p>\n</td>\n<td width=\157\>\n<p>Insulin or insulin-like substance</p>\n</td>\n</tr>\n<tr>\n<td width=\157\>\n<p>Polycythemia</p>\n</td>\n<td width=\157\>\n<p>Renal carcinoma</p>\n<p>Cerebellar haemangioma</p>\n<p>Hepatocellular carcinoma</p>\n</td>\n<td width=\157\>\n<p>Erythropoietin</p>\n</td>\n</tr>\n<tr>\n<td width=\157\>\n<p>Osteomalacia</p>\n</td>\n<td width=\157\>\n<p>Phosphaturic mesenchymal tumor</p>\n</td>\n<td width=\157\>\n<p>FGF-23</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Disseminated intrvascular coagulation</p>\n</td>\n<td>\n<p>Acute promyelocytic leukemia</p>\n<p>Prostatic carcinoma</p>\n</td>\n<td>\n<p>Tumor products activate clotting</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>ACTH:&nbsp;Adrenocorticotropic hormone&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(802, 'Pathology,', 'PM0271. Risk Factors for Squamous Cell Carcinoma', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Risk factors for squamous cell carcinoma</strong></p>\n<ul>\n<li>UV exposure</li>\n<li><strong>Male sex</strong>- due to more sun exposure</li>\n<li>Environmental factors-<br>\n<ol>\n<li><strong>Psoralen </strong>and UVA (PUVA)</li>\n<li>Physical agents such as ionizing radiation</li>\n<li>Chemical agents such as arsenics</li>\n</ol>\n</li>\n<li><strong>HPV-16 &amp; 18</strong></li>\n<li>Immunosuppression&nbsp;</li>\n<li>Smoking</li>\n<li><strong>Chronic nonhealing wounds</strong>, burn scars, and chronic dermatosis</li>\n<li>Hereditary conditions such as xeroderma pigmentosum, <strong>epidermolysis bullosa</strong>, and oculo-cutaneous albinism</li>\n<li>Premalignant conditions like actinic keratosis, Bowen’s disease, erythroplasia of Queyrat</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(803, 'Pathology', 'PM0210. Important features of inheritance patterns', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>X-linked Recessive Conditions:</strong></p>\n<ul>\n<li><strong>All sex-linked disorders are X-linked, and almost all are recessive.</strong></li>\n<li>Several genes are located in the “male-specific region of Y;” all these are related to spermatogenesis.</li>\n<li>Males with mutations affecting the Y-linked genes are usually infertile, and hence, there is no Y-linked inheritance.</li>\n<li><strong>An affected male does not transmit the disorder to his sons, but all daughters are carriers.</strong></li>\n<li><strong>Sons of heterozygous women have, of course, a one-in-two chance of receiving the mutant gene.</strong></li>\n<li><strong><strong>Heterozygous females&nbsp;</strong>express the disorder partially</strong>&nbsp;<strong>because of random inactivation of one X chromosome</strong>.</li>\n</ul>\n<p><strong>Autosomal Dominant Disorders:</strong></p>\n<ul>\n<li>With every autosomal dominant disorder, <strong>some patients do not have affected parents.</strong> Such patients owe their disorder to new mutations involving either the egg or the sperm from which they were derived.</li>\n</ul>\n<ul>\n<li><strong>Clinical features </strong>can be <strong>modified by variations in penetrance and expressivity.</strong></li>\n</ul>\n<ul>\n<li>In many conditions, the <strong>age at onset </strong>is <strong>delayed</strong>, and <strong>symptoms and signs </strong>may not appear until<strong> adulthood (as in Huntington disease).</strong></li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(804, 'Pathology', 'PM0132. Apoptotic genes', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td>Group</td>\n<td>Members</td>\n<td>Details</td>\n</tr>\n<tr>\n<td>Anti-apoptotlc</td>\n<td>BCL2,<br>BCL-XL<br><br>MCL1</td>\n<td>Present in outer mitochondrial&nbsp;membrane<br>Prevent leakage of cytochrome-c and<br>other death lnducing proteins into<br>cytosol.</td>\n</tr>\n<tr>\n<td>Pro-apoptotlc</td>\n<td>BAX,<br>BAK</td>\n<td>On activation, they increase&nbsp;mitochondrial membrane permeability.</td>\n</tr>\n<tr>\n<td>Stress Sensors</td>\n<td>BAD,<br>BIM,<br>BID,<br>Puma, Noxa</td>\n<td>Act as sensors of cellular stress and&nbsp;damage and regulate the balance<br>between the other two groups</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(805, 'Pathology', 'PM0130. Classification of Amyloidosis', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><div class=\page\ title=\Page 270\>\n<table><colgroup><col><col><col><col> </colgroup>\n<tbody>\n<tr>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p><strong>Clinicopathologic Category</strong></p>\n</div>\n</div>\n</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p><strong>Major Fibril Protein</strong></p>\n</div>\n</div>\n</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p><strong>Chemically Related Precursor Protein</strong></p>\n</div>\n</div>\n</td>\n</tr>\n<tr>\n<td colspan=\3\ rowspan=\1\>\n<div class=\layoutArea\>\n<div class=\column\>\n<p><strong>Systemic (Generalized) Amyloidosis:</strong></p>\n</div>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div class=\layoutArea\>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>Immunoglobulin light chain amyloidosis (<strong>primary amyloidosis</strong>)</p>\n<p>Seen in Multiple myeloma and other monoclonal plasma cell proliferation</p>\n</div>\n</div>\n</div>\n</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>AL</p>\n</div>\n</div>\n</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>Immunoglobulin light chains, chiefly λ type</p>\n</div>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div class=\layoutArea\>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>Reactive systemic amyloidosis (<strong>secondary amyloidosis</strong>)</p>\n<p>Seen in chronic inflammatory conditions</p>\n</div>\n</div>\n</div>\n</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>AA</p>\n</div>\n</div>\n</td>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>SAA</p>\n</div>\n</div>\n</td>\n</tr>\n<tr>\n<td>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>Hemodialysis-associated amyloidosis</p>\n<p>Seen in chronic renal failure</p>\n</div>\n</div>\n</td>\n<td>\n<div c