(1138, 'Community Medicine', 'PM0160. Responsibilities of Health Workers', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\594\>\n<tbody>\n<tr>\n<td width=\289\>\n<p><strong>Female Health Worker</strong></p>\n</td>\n<td width=\305\>\n<p><strong>Male Health Worker</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\289\>\n<p>Maternal and child health services</p>\n<p>Family planning services and registers</p>\n<p>Medical Termination of Pregnancy</p>\n<p>Universal Immunization Program</p>\n<p>Detection of non communicable diseases</p>\n<p>Dai training</p>\n<p>Treatment of minor ailments</p>\n<p>Participate in team activities</p>\n</td>\n<td width=\305\>\n<p>National Health Programmes</p>\n<p>Malaria slides and surveillance</p>\n<p>Leprosy follow-up</p>\n<p>Identification and follow-up cases of blindness</p>\n<p>Reproductive and child health services</p>\n<p>RNTCP</p>\n<p>Expanded Immunization Program</p>\n<p>Environmental sanitation including chlorination of water and proper waste disposal</p>\n</td>\n</tr>\n<tr>\n<td colspan=\2\ width=\594\>\n<p><strong>Common Responsibilities</strong></p>\n</td>\n</tr>\n<tr>\n<td colspan=\2\ width=\594\>\n<p>Nutrition</p>\n<p>Distribution of contraceptives</p>\n<p>Work on communicable diseases like DOTS activities</p>\n<p>Record and report vital events</p>\n<p>Record keeping and maintaining registers</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1139, 'Community Medicine', 'PM0159. Community Health Centre Human Resources', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\174\>\n<p><strong>Personnel</strong></p>\n</td>\n<td width=\150\>\n<p><strong>Strength (Essential)</strong></p>\n</td>\n<td width=\234\>\n<p><strong>Desirable Qualifications</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\174\>\n<p>Block Medical Officer</p>\n</td>\n<td width=\150\>\n<p>1</p>\n</td>\n<td width=\234\>\n<p>Senior most specialist</p>\n</td>\n</tr>\n<tr>\n<td width=\174\>\n<p>Public Health Specialist</p>\n</td>\n<td width=\150\>\n<p>1</p>\n</td>\n<td width=\234\>\n<p>MD/MBA/MPH/DPH</p>\n</td>\n</tr>\n<tr>\n<td width=\174\>\n<p>Public Health Nurse</p>\n</td>\n<td width=\150\>\n<p>1</p>\n</td>\n<td width=\234\>&nbsp;</td>\n</tr>\n<tr>\n<td width=\174\>\n<p>General surgeon</p>\n</td>\n<td width=\150\>\n<p>1</p>\n</td>\n<td width=\234\>\n<p>MS/DNB</p>\n</td>\n</tr>\n<tr>\n<td width=\174\>\n<p>Physician</p>\n</td>\n<td width=\150\>\n<p>1</p>\n</td>\n<td width=\234\>\n<p>MD/DNB</p>\n</td>\n</tr>\n<tr>\n<td width=\174\>\n<p>OG-cian</p>\n</td>\n<td width=\150\>\n<p>1</p>\n</td>\n<td width=\234\>\n<p>MD/DNB/DGO</p>\n</td>\n</tr>\n<tr>\n<td width=\174\>\n<p>Paediatrician</p>\n</td>\n<td width=\150\>\n<p>1</p>\n</td>\n<td width=\234\>\n<p>MD/DNB/DCH</p>\n</td>\n</tr>\n<tr>\n<td width=\174\>\n<p>Anesthetist</p>\n</td>\n<td width=\150\>\n<p>1</p>\n</td>\n<td width=\234\>\n<p>MD/DNB/DA/LSAS trained MO</p>\n</td>\n</tr>\n<tr>\n<td width=\174\>\n<p>Dental surgeon</p>\n</td>\n<td width=\150\>\n<p>1</p>\n</td>\n<td width=\234\>\n<p>BDS</p>\n</td>\n</tr>\n<tr>\n<td width=\174\>\n<p>General Duty MO</p>\n</td>\n<td width=\150\>\n<p>2</p>\n</td>\n<td width=\234\>\n<p>MBBS</p>\n</td>\n</tr>\n<tr>\n<td width=\174\>\n<p>AYUSH Medical Officer</p>\n</td>\n<td width=\150\>\n<p>1</p>\n</td>\n<td width=\234\>\n<p>Graduate in