(1243, 'Forensic Medicine', 'PM2106. Plant poisons', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Datura stramonium (Jimsonweed) : Flower and fruit are poisonous</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/2eaede053909497389cac1e1b0c4d530x1280x960.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Ricinus communis : Seed is poisonous</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/7180cc0b0cf94045b876f6cbdb0ebd6dx1280x1706.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Digitalis purpurea&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/a517f2827afa4cdcad5aaf1327a7b82bx1280x1786.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Atropa belladonna</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/5aad720936ff44a697c2504ece8f5fa0x1280x1052.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Calotropis gigantea</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/0bd879b96af64132a731d35608addbeax1280x1065.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(1244, 'Forensic Medicine', 'PM2105. Common names for plant poisons', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\386\>\n<tbody>\n<tr>\n<td>Plant poison</td>\n<td>Common name</td>\n</tr>\n<tr>\n<td>Conium maculatum</td>\n<td>Poison hemlock</td>\n</tr>\n<tr>\n<td>Digitalis purpurea</td>\n<td>Foxglove</td>\n</tr>\n<tr>\n<td>Nerium odorum</td>\n<td>White oleander</td>\n</tr>\n<tr>\n<td>Ricinus communis</td>\n<td>Castor bean</td>\n</tr>\n<tr>\n<td>Abrus precatorius</td>\n<td>Rosary pea</td>\n</tr>\n<tr>\n<td>Aconite</td>\n<td>Monks hood</td>\n</tr>\n<tr>\n<td>Atropa belladona</td>\n<td>Deadly nightshade</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1245, 'Forensic Medicine', 'PM2103. Phosphorus poisoning', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\476\>\n<tbody>\n<tr>\n<td>Features</td>\n<td>\n<p>Phosphorus</p>\n</td>\n</tr>\n<tr>\n<td>Chemistry</td>\n<td>\n<p>2 forms - White and Red Phosphorus</p>\n<p>White Phosphorus is toxic, Red Phosphorus is not.</p>\n</td>\n</tr>\n<tr>\n<td>Mechanism of action</td>\n<td>Protoplasmic irritant poison</td>\n</tr>\n<tr>\n<td>Organs affected</td>\n<td>\n<p>Liver</p>\n<p>Bone</p>\n<p>Kidneys</p>\n</td>\n</tr>\n<tr>\n<td>Characteristic clinical findings</td>\n<td>\n<p>Smoky stool syndrome</p>\n<p>Luminescent stools</p>\n<p>Garlicky odor</p>\n<p>Liver necrosis</p>\n<p>Phossy jaw</p>\n</td>\n</tr>\n<tr>\n<td>Treatment</td>\n<td>\n<p>Symptomatic therapy</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1246, 'Forensic Medicine', 'PM2101. Lead Poisoning/Plumbism/Saturnism', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td colspan=\2\><strong>Lead Toxicity</strong></td>\n</tr>\n<tr>\n<td>Source of exposure</td>\n<td>\n<p>Occupational exposure</p>\n<p>Lead paint</p>\n</td>\n</tr>\n<tr>\n<td>Route of absorption</td>\n<td>\n<p>Children - GI tract</p>\n<p>Adults - Respiratory tract</p>\n</td>\n</tr>\n<tr>\n<td>Mechanism of action</td>\n<td>\n<ul>\n<li>Binds to sulfhydryl groups and inhibits\n<ul>\n<li>ALA dehydratase</li>\n<li>Ferrochelatase</li>\n</ul>\n</li>\n<li>Inhibits pyrimidine 5 nucleotidase</li>\n<li>Competes with <strong>calcium</strong></li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Organs affected <strong>(acute</strong> toxicity)</td>\n<td>\n<p>GI tract, Bone marrow, Peripheral nerves</p>\n</td>\n</tr>\n<tr>\n<td>Organs affected <strong>(chronic</strong> toxicity)</td>\n<td>\n<p>CNS, Kidney</p>\n</td>\n</tr>\n<tr>\n<td>Characteristic clinical findings</td>\n<td>\n<p><strong>A - </strong>Anemia with basophilic stippling</p>\n<p><strong>B - </strong>Burtonian line (blue stippled line on gums)</p>\n<p><strong>C - </strong>Constipation and colic (abdominal pain)</p>\n<p><strong>D - </strong>Drop (wrist and foot drop) due to neuropathy</p>\n<p><strong>E - </strong>Encephalopathy-&nbsp;<strong>Mostly seen in infants</strong> presenting with severe ataxia, vomiting, lethargy, stupor, convulsions, and coma</p>\n<p><strong>F - Facial pallor (Earliest sign)</strong></p>\n</td>\n</tr>\n<tr>\n<td>Diagnosis</td>\n<td>Blood lead levels</td>\n</tr>\n<tr>\n<td>Treatment</td>\n<td>Calcium EDTA; succimer(DMSA)</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1247, 'Forensic Medicine', 'PM2100. Mercury Poisoning', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>Features</td>\n<td>\n<p>Mercury</p>\n</td>\n</tr>\n<tr>\n<td>Source of exposure</td>\n<td>\n<p>Thermometers (elemental mercury)</p>\n<p>Industries (inorganic mercury)</p>\n<p>Fish (organic mercury)&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>Route of absorption</td>\n<td>\n<p>Elemental - Respiratory tract (negligible through GI unless the mucosa is damaged)&nbsp;</p>\n<p>Inorganic - GI tract</p>\n<p>Organic - GI tract</p>\n</td>\n</tr>\n<tr>\n<td>Mechanism of action</td>\n<td>\n<p>binds to sulhydryl groups of enzymes and affects</p>\n<ul>\n<li>glutathione metabolism</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Organs affected (acute toxicity)</td>\n<td>\n<p>Respiratory tract (elemental mercury)</p>\n<p>GI tract (inorganic mercury)</p>\n<p>Respiratory + GI tract (organic mercury)</p>\n</td>\n</tr>\n<tr>\n<td>Organs affected (chronic toxicity)</td>\n<td>\n<p>CNS, Renal (elemental mercury)</p>\n<p>Renal (inorganic mercury)</p>\n<p>CNS (organic mercury)</p>\n</td>\n</tr>\n<tr>\n<td>Characteristic clinical findings</td>\n<td>\n<p>&nbsp;</p>\n<p><strong>Mnemonic: MEATS</strong></p>\n<p><strong>M</strong> -</p>\n<ul>\n<li>Mercuria lentis</li>\n<li>Membranous colitis (diphtheria like)</li>\n<li>Minimata disease</li>\n<li>Membranous glomerulopathy</li>\n</ul>\n<p><strong>E-</strong>Erethism (personality disturbance, insomnia, and loss of memory)</p>\n<p><strong>A</strong>-Acrodynia (Pink disease)</p>\n<p><strong>T-</strong>Tremors (Coarse tremors) - Include Danbury tremors, hatter’s or Glass blower’s shakes</p>\n<p><strong>S-</strong>Salivation (excessive salivation)</p>\n</td>\n</tr>\n<tr>\n<td>Diagnosis</td>\n<td>Blood and/or 24 hour urine mercury level</td>\n</tr>\n<tr>\n<td>Treatment</td>\n<td>BAL; succimer(DMSA)</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1248, 'Forensic Medicine', 'PM2099. Arsenic poisoning', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>Summary of Arsenic poisoning</p>\n<p><strong>Source of exposure</strong>:&nbsp;Arsenic-containing pesticides, natural sources - drinking water and soil</p>\n<p><strong>Route of absorption</strong>:&nbsp;GI tract</p>\n<p><strong>Mechanism of action</strong>:&nbsp;binds to sulfhydryl groups and inhibits pyruvate dehydrogenase, gluconeogenesis, glutathione metabolism</p>\n<p>Characteristic clinical findings are as follows: Mnemonic-ARSENIC</p>\n<p><strong>A-&nbsp;</strong>Aldrich mees lines</p>\n<p><strong>R-&nbsp;</strong>raindrop pigmentation, rash,&nbsp;red velvety mucosa</p>\n<p><strong>S-&nbsp;</strong>subendocardial haemorrhage</p>\n<p><strong>E-&nbsp;</strong>Excessive pigmentation- Blackfoot&nbsp;disease</p>\n<p><strong>N-&nbsp;</strong>Neuritis</p>\n<p><strong>I-&nbsp;</strong>Iron oxide + BAL –treatment</p>\n<p><strong>C-&nbsp;</strong>Cholera like illness</p>\n<p><strong>Diagnosis</strong>:&nbsp;24-hour urine arsenic</p>\n<p><strong>Treatment</strong>:&nbsp;BAL; succimer(DMSA)</p>\n<p>Clinicopathological findings in acute and chronic arsenic poisoning:</p>\n<table>\n<tbody>\n<tr>\n<td>\n<p><strong>System</strong></p>\n</td>\n<td>\n<p><strong>Acute</strong></p>\n</td>\n<td>\n<p><strong>Chronic</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Skin</p>\n</td>\n<td>\n<p>Delayed hair loss (alopecia)</p>\n</td>\n<td>\n<p>Melanosis, Bowen’s disease facial edema, hyperkeratosis, cutaneous cancer, hyperpigmentation.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Nails</p>\n</td>\n<td>\n<p>Mees’s lines in 2-3 weeks post ingestion.</p>\n</td>\n<td>\n<p><strong>Mees lines</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Neurologic</p>\n</td>\n<td>\n<p>Hyperpyrexia, convulsions, tremors, coma</p>\n</td>\n<td>\n<p>Encephalopathy, polyneuropathy, tremors, axonal degeneration</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>GI tract</p>\n</td>\n<td>\n<p>Abdominal pain, dysphagia, vomiting, bloody or rice water diarrhea, mucosal erosions.</p>\n</td>\n<td>\n<p>Nausea, vomiting, diarrhea, anorexia, weight loss</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Liver</p>\n</td>\n<td>\n<p>Fatty infiltration</p>\n</td>\n<td>\n<p>Hepatomegaly, jaundice, cirrhosis</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Kidney</p>\n</td>\n<td>\n<p>Tubular and glomerular changes – oliguria and uremia</p>\n</td>\n<td>\n<p>Nephritic finding</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Haemetologic</p>\n</td>\n<td>\n<p>thrombocytopenia, impaired folate metabolism</p>\n</td>\n<td>\n<p>Bone marrow hypoplasia, anemia, leukopenia, basophilic stippling, and karyorrhexis.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Cardiovascular</p>\n</td>\n<td>\n<ul>\n<li>S-T wave abnormalities,</li>\n<li>prolonged QT interval</li>\n<li>ventricular fibrillation</li>\n<li>atypical ventricular tachycardia&nbsp;</li>\n</ul>\n</td>\n<td>\n<p>There is platelet activation and hypercoagulability of blood in peripheral arteries leading to blackfoot disease</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Reproductive system</p>\n</td>\n<td>\n<p>Maternal ingestion of inorganic</p>\n<p>Arsenic crosses the placental barrier and infant death shortly after birth.</p>\n</td>\n<td>\n<p>-Nil-</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1249, 'Forensic Medicine', 'PM2094. Poisoning - Modalities of General Management', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>&nbsp;Management</td>\n<td>Examples</td>\n</tr>\n<tr>\n<td>Gastrointestinal decontamination</td>\n<td>\n<p>Orogastric Lavage</p>\n<p>Activated Charcoal</p>\n<p>Whole Bowel Irrigation</p>\n</td>\n</tr>\n<tr>\n<td>Enhanced elimination</td>\n<td>\n<p>Multidose activated charcoal</p>\n<p>Urinary alkalinization</p>\n<p>Extracorporeal removal(Hemodialysis, Hemoperfusion)</p>\n</td>\n</tr>\n<tr>\n<td>Administration of antidote</td>\n<td>\n<p>Mechanical Antidotes</p>\n<p>Chemical Antidotes</p>\n<p>Physiological Antidotes</p>\n<p>Pharmacological Antidotes</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1250, 'Forensic Medicine', 'PM0900. Signs and Symptoms of Strychnine Poisoning', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p>The clinical features of&nbsp;strychnine poisoning are:</p>\n<ul>\n<li>Convulsions - clonic-tonic type</li>\n<li>Reflex increased</li>\n<li>Awful taste</li>\n<li>Mind is conscious</li>\n<li>Pupils dilated</li>\n<li>Opisthotonos: arching back</li>\n<li>Emprosthotonos: arching forward</li>\n<li>Pleurothotonus: bending sideward</li>\n<li>Risus Sardonicus&nbsp;-<strong>&nbsp;</strong>fixed contraction of the facial muscles resulting in a peculiar distorted grin.</li>\n</ul>\n<p>Strychnine poisoning case may resemble tetanus.</p>\n<p>Although a poison, in relatively low doses it can be used as a medical stimulant for respiration.</p>\n<p><strong>Strychnine plant</strong></p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/d8690926df9547d79adc83295934d6b3x615x600.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><p><strong>Strychnine seeds</strong></p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/5af4813b6ca74316a15d06f04cfd67b6x1280x910.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(1251, 'Forensic Medicine', 'PM0899. Toxic manifestations of Datura', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><ul>\n<li>Mnemonic: \hot as a hare, blind as a bat, dry as a bone, red as a beet, and mad as a hatter\</li>\n<li>Also, remember <strong>the Ds of Datura</strong>\n<ul>\n<li><strong>D</strong>ry hot skin (Temp rises upto 42 C, a scarlet rash may also be appreciated).</li>\n<li><strong>D</strong>ilatation of pupils along with loss of accommodation</li>\n<li><strong>D</strong>iplopia</li>\n<li><strong>D</strong>ryness of the mouth and throat, unquenchable thirst</li>\n<li><strong>D</strong>ysphagia</li>\n<li><strong>D</strong>ilatation of cutaneous blood vessels</li>\n<li><strong>D</strong>ysarthria (Difficulty in talking)</li>\n<li><strong>D</strong>runken gait</li>\n<li><strong>D</strong>elirium</li>\n<li><strong>D</strong>rowsiness</li>\n<li><strong>D</strong>eath due to respiratory or cardiac failure</li>\n</ul>\n</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(1252, 'Forensic Medicine', 'PM0898. Difference between Poisonous and Non- Poisonous Snakes', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\><table width=\495\>\n<tbody>\n<tr>\n<td width=\165\>\n<p>&nbsp;</p>\n</td>\n<td width=\165\>\n<p><strong>Poisonous snake</strong></p>\n</td>\n<td width=\165\>\n<p><strong>Non poisonous snake</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\165\>\n<p>Belly scales</p>\n</td>\n<td width=\165\>\n<p><strong>Large and it covers the entire belly</strong></p>\n</td>\n<td width=\165\>\n<p>Small and do not cover the entire belly</p>\n</td>\n</tr>\n<tr>\n<td width=\165\>\n<p>Head scales</p>\n</td>\n<td width=\165\>\n<p><strong>Small</strong></p>\n</td>\n<td width=\165\>\n<p>Large</p>\n</td>\n</tr>\n<tr>\n<td width=\165\>\n<p>Fang</p>\n</td>\n<td width=\165\>\n<p>Long and canalised</p>\n</td>\n<td width=\165\>\n<p>Short and solid</p>\n</td>\n</tr>\n<tr>\n<td width=\165\>\n<p>Tail</p>\n</td>\n<td width=\165\>\n<p><strong>Compressed </strong></p>\n</td>\n<td width=\165\>\n<p>Not markedly compressed</p>\n</td>\n</tr>\n<tr>\n<td width=\165\>\n<p>Habits</p>\n</td>\n<td width=\165\>\n<p><strong>Nocturnal</strong></p>\n</td>\n<td width=\165\>\n<p>Not so</p>\n</td>\n</tr>\n<tr>\n<td width=\165\>\n<p>Bite</p>\n</td>\n<td width=\165\>\n<p><strong>Two fang marks</strong></p>\n</td>\n<td width=\165\>\n<p>A number of small teeth marks in a row</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/6cab6f8345e14069980e9050a74e2923x448x480.PNG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-2 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-2 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/a0406e65d06544ef8873500c09037370x472x480.PNG\><!---->\n      </div><!---->\n    </div><!---->');
