(1552, 'Radiology', 'PM2380. Classification of iodinated contrast medias:', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><span>&nbsp;</span></td>\n<td colspan=\4\><span><strong>Iodinated contrast materials&nbsp;&nbsp;</strong></span></td>\n</tr>\n<tr>\n<td><span><strong>Types</strong></span></td>\n<td colspan=\2\><span>Ionic&nbsp;</span></td>\n<td colspan=\2\><span>Non-ionic</span></td>\n</tr>\n<tr>\n<td><span><strong>Subtypes&nbsp;</strong></span></td>\n<td><span>Monomer</span></td>\n<td><span>Dimer</span></td>\n<td><span>Monomer</span></td>\n<td><span>Dimer</span></td>\n</tr>\n<tr>\n<td><span><strong>Osmolarity</strong></span></td>\n<td>\n<p><span>1400- 1600 mOsm <span>(Not preferred due to high osmolarity)</span></span></p>\n</td>\n<td><span>600- 800 mOsm</span></td>\n<td><span>600- 800 mOsm</span></td>\n<td><span>300 mOsm</span></td>\n</tr>\n<tr>\n<td><span><strong>Iodine: particle ratio</strong></span></td>\n<td><span>3:2</span></td>\n<td><span>6:2 or 3:1</span></td>\n<td><span>3:1</span></td>\n<td><span>6:1</span></td>\n</tr>\n<tr>\n<td><span><strong>Examples&nbsp;</strong></span></td>\n<td>\n<p><span>Diatrizoate <span>(Urograffin)</span></span></p>\n<p><span>Iothalamate</span></p>\n</td>\n<td>\n<p><span>Ioxaglate</span></p>\n</td>\n<td>\n<p><span>Iohexol <span>(most commonly used</span></span><span>)</span></p>\n<p><span>Ioversol</span></p>\n<p><span>Iopamidol</span></p>\n</td>\n<td>\n<p><span>Iodixanol</span></p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->');
