(1553, 'Radiology', 'PM0644. T1 vs T2 MRI', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<div><strong>Structure</strong></div>\n</td>\n<td>\n<div><strong>T1 image</strong></div>\n</td>\n<td>\n<div><strong>T2 image</strong></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><strong>Grey matter</strong></div>\n</td>\n<td>\n<div>Darker</div>\n</td>\n<td>Lighter</td>\n</tr>\n<tr>\n<td>\n<div><strong>White matter</strong></div>\n</td>\n<td>Lighter&nbsp;</td>\n<td>Darker</td>\n</tr>\n<tr>\n<td>\n<div><strong>CSF/ Fluids</strong></div>\n</td>\n<td>\n<div>Dark</div>\n</td>\n<td>\n<div>Bright</div>\n</td>\n</tr>\n<tr>\n<td>\n<div><strong>Fat</strong></div>\n</td>\n<td>\n<div>White</div>\n</td>\n<td>\n<div>Less white</div>\n</td>\n</tr>\n<tr>\n<td>\n<div><strong>Air</strong></div>\n</td>\n<td>\n<div>Dark</div>\n</td>\n<td>\n<div>Dark</div>\n</td>\n</tr>\n<tr>\n<td>\n<div><strong>Cortical bone</strong></div>\n</td>\n<td>\n<div>Dark</div>\n</td>\n<td>\n<div>Dark</div>\n</td>\n</tr>\n<tr>\n<td>\n<div><strong>Tendons and ligaments</strong></div>\n</td>\n<td>\n<div>Dark</div>\n</td>\n<td>\n<div>Dark</div>\n</td>\n</tr>\n<tr>\n<td>\n<div><strong>Calcification</strong></div>\n</td>\n<td>Variable</td>\n<td>\n<div>Dark</div>\n</td>\n</tr>\n<tr>\n<td>\n<div><strong>Flowing blood and hemosiderin</strong></div>\n</td>\n<td>\n<div>Dark</div>\n</td>\n<td>\n<div>Dark</div>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1554, 'Radiology', 'PM0283. Radionuclide agents and their uses', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Radionuclide agent</strong></p>\n</td>\n<td>\n<p><strong>Use</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Tc-99m Medronate (MDP) (most common)</p>\n<p>Tc-99m Oxidronate (HMDP)</p>\n<p>Tc-99m HEDP</p>\n</td>\n<td>\n<p>Bone imaging</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Tc-99m Apcitide</p>\n</td>\n<td>\n<p>Peptide imaging of DVT</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Tc-99m Arcitumomab</p>\n</td>\n<td>\n<p>Monoclonal antibody for colorectal cancer</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Tc-99m albumin colloid</p>\n</td>\n<td>\n<p>Imaging of RES (liver/spleen)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Tc-99m bicisate (ECD)</p>\n<p>Tc-99m exametazine (HMPAO</p>\n</td>\n<td>\n<p>Cerebral perfusion imaging</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Tc-99m Depreotide</p>\n</td>\n<td>\n<p>Somatostatin receptor-bearing pulmonary masses</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Tc-99m disofenin (DISIDA)</p>\n</td>\n<td>\n<p>Hepatobiliary imaging</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Tc-99m Glucepatate</p>\n<p>Tc-99m Meriatide</p>\n<p>Tc-99m Succimer (DMSA)</p>\n</td>\n<td>\n<p>Renal imaging</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Tc-99m Human Serum Albumin(HAS)</p>\n</td>\n<td>\n<p>Imaging of cardiac chambers</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Tc-99m Lidofenin (HIDA)</p>\n<p>Tc-99m Mebrofenin</p>\n</td>\n<td>\n<p>Hepatobiliary imaging</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Tc-99m nofetumomab Merpentan NR-LU-10</p>\n</td>\n<td>\n<p>Monoclonal antibody Fab fragment for imaging small cell lung cancer</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Tc-99m Pentetate (DTPA)</p>\n</td>\n<td>\n<p>Renal imaging and functional studies; radioaerosol ventilation imaging</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Tc-99m Pyrophosphate (PYP)</p>\n</td>\n<td>\n<p>Avid infarct imaging</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Tc-99m Red Blood Cells (RBCs)</p>\n</td>\n<td>\n<p>Imaging of GI bleeds, cardiac chambers</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Tc-99m Sestamibi</p>\n</td>\n<td>\n<p>Myocardial perfusion imaging; breast tumor imaging, parathyroid gland</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Tc-99m Sulfur colloid (SC)</p>\n</td>\n<td>\n<p>Imaging of RES( liver/spleen), gastric emptying, GI bleeds</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Tc-99m Teboroxime</p>\n<p>Tc-99m Tetrofosmin</p>\n</td>\n<td>\n<p>Myocardial perfusion imaging</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><sup>68</sup>Gallium</p>\n</td>\n<td>\n<p>Binds to somatostatin receptors</p>\n<p>Detection of new neuroendocrine tumors</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1555, 'Radiology', 'PM0048. Radioprotectors and Radiosensitizers', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Radioprotectors</strong>:</p>\n<p>Radioprotectors are substances that protect normal tissues from the effect of radiation. These include:</p>\n<ul>\n<li>Zinc oxide</li>\n<li>Pentoxifylline</li>\n<li>Estramustine</li>\n<li>Amifostine</li>\n<li>Chlorhexidine (radiation-induced stomatitis)</li>\n<li>Potassium iodide (for thyroid against radioactive iodine)</li>\n<li>Melatonin</li>\n<li>Antioxidants</li>\n</ul>\n<p><strong>Radiosensitizers</strong>:</p>\n<p>Radiosensitizers make the tumor more susceptible to the action of radiotherapy. These include:</p>\n<ul>\n<li>Oxygen</li>\n<li>Cisplatin</li>\n<li>5 FU</li>\n<li>Gemcitabine</li>\n<li>Nitroimidazoles</li>\n</ul>\n<p><strong>Radiation potentiators</strong>:</p>\n<p>The following drugs potentiate the action of radiation when used in conjunction:&nbsp;</p>\n<ul>\n<li>Doxorubicin</li>\n<li>Dactinomycin</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(1556, 'Radiology', 'PM0047. Important SI units for radiation', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td><strong>Quantity measured</strong></td>\n<td><strong>Old unit</strong></td>\n<td><strong>New Unit</strong></td>\n<td><strong>Conversion</strong></td>\n<td><strong>Description</strong></td>\n</tr>\n<tr>\n<td><strong>Radioactivity</strong></td>\n<td>Curie</td>\n<td>Becquerel</td>\n<td>1 curie = 3.7 x 10<sup>10 </sup>becquerel</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td><strong>Radiation exposure</strong></td>\n<td>Roentgen</td>\n<td>Coulumb/kg</td>\n<td>1 roentgen = 2.58 × 10<sup>-4&nbsp;</sup>C/Kg</td>\n<td>Amount of radiation absorbed in air. &nbsp;</td>\n</tr>\n<tr>\n<td><strong>Absorbed dose</strong></td>\n<td>Rad</td>\n<td>Gray (Gy)</td>\n<td>100 rad = 1 gray (or, 1 rad = 0.01 gray)</td>\n<td>&nbsp;Amount of radioactive energy absorbed per unit gram of tissue or material.</td>\n</tr>\n<tr>\n<td><strong>Dose equivalent</strong></td>\n<td>Rem</td>\n<td>Sievert (Sv)</td>\n<td>100 rem = 1 sievert</td>\n<td>&nbsp;Indicates potential danger to health.</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1557, 'Radiology', 'PM0045. Ionising and penetrating ability of radiation', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><ul>\n<li><strong>Penetration power</strong>: Gamma rays &gt; X-rays &gt; Beta particle &gt; Alpha particle (helium ion)</li>\n<li><strong>Ionising or tissue damaging ability</strong>: Alpha particle (helium ion) &gt; Beta particle &gt; X-ray &gt; Gamma ray</li>\n<li>Alpha waves have the <strong>highest molecular mass</strong></li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/cf6ccc6f20ed4ebe82d52753f65929a6x260x817.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(1558, 'Obstetrics & Gynaecology,', 'PM2262. Radiological Signs in Obstetrics', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Bagel Sign</strong> (USG)</p>\n<p>It is seen in <strong>unruptured tubal ectopic pregnancy</strong>. It is also known as the <strong>tubal ring sign</strong> or the <strong>blob sign</strong>. It refers to the appearance of an anechoic gestational sac in the adnexa surrounded by a hyperechoic ring.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/65667801a0fb450ab1af9a1939e7ff8dx1280x1432.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Twin Peak Sign/ Lambda Sign</strong> (USG)</p>\n<p>Chorionicity of the placenta is best diagnosed by ultrasound at 10–13 weeks of gestation. In <strong>dichorionic twins</strong>, there is a thick triangular projection of placental tissue between the two gestational sacs. This is known as the lambda sign or twin peak sign.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/96f08dd9f0fa4fc694291835f3cd15b3x510x432.GIF\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>T Sign</strong> (USG)</p>\n<p>The T sign which is seen in <strong>monochorionic diamniotic gestation</strong> is thin and T-shaped because there is no extension of placental tissue or chorion in between the amniotic membranes.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/8c59f243f5294b64ac000021835b7b5fx510x396.GIF\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Double Decidual Sac Sign</strong> (USG)</p>\n<p>It is seen in <strong>early intrauterine pregnancy</strong> as two concentric echogenic rings surrounding the gestational sac.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/41b99e48f08c46fbb6bafdd542c1891dx1280x955.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Lemon Sign</strong> (USG)</p>\n<p>At the level of the lateral ventricles, there is<strong> inward bowing or scalloping of frontal bones</strong>&nbsp;giving rise to the lemon sign. It is suggestive of <strong>spina bifida</strong>. It is also characteristically associated with <strong>Chiari II Malformation</strong>.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/0ef0a805761c4ec987831c7a0197d01cx510x418.GIF\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Banana Sign</strong> (USG)</p>\n<p>Anterior curvature of the<strong> cerebellum</strong> with effacement of the <strong>cisterna magna</strong> in the posterior fossa is seen as banana sign.&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/5e04c4b1c58a44258a37864e5518899ax510x354.GIF\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Spalding sign</strong> (USG)</p>\n<p>It usually appears 7 days after <strong>intrauterine death</strong> and is caused by liquefaction of the brain matter and softening of the ligamentous structures supporting the vault, leading to<strong> irregular overlapping of the cranial bones</strong> on one another.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/0eef8c33a1a143e38b966f4ec038a545x1076x711.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Frog Eye Sign</strong> (USG)</p>\n<p>It is seen in <strong>anencephaly</strong>. Due to the absence of calvaria, the <strong>orbits appear more prominent</strong>. This is known as the \frog-eye sign\.&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/972403fa3d0e48a08e561ff4310101acx510x620.GIF\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Omphalocele</strong> (USG)</p>\n<p>It is a <strong>congenital midline abdominal wall defect</strong> at the base of the umbilical cord insertion, with <strong>herniation</strong> of the solid abdominal viscera and/or bowel out of the fetal abdomen&nbsp; (indicated with an asterisk). The extruded abdominal contents are <strong>covered in a sac</strong>.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/96660889c8b34a0f89ee373ebdb60f40x1280x1040.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Double Bubble Sign</strong> (USG)</p>\n<p>Dilatation of stomach and first part of duodenum due to <strong>duodenal atresia</strong>.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/91de267cb9594cb6ba98e16f4495780fx443x490.PNG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><span><strong>Bunch of grapes sign&nbsp;</strong>or&nbsp;<strong>Snowstorm appearance&nbsp;</strong>(USG)&nbsp;</span></p>\n<p><span>The appearance of multiple cystic spaces or lesions in a <strong>complete mole</strong>.</span></p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/6353c90095d1471696dac8fa7bd2a3b3x676x568.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Ball sign</strong> (X-ray)</p>\n<p>It is due to the <strong>crumpled up spine</strong> of the fetus seen in case of <strong>intrauterine death</strong>.</p>\n<p>&nbsp;</p>\n<p><strong>Robert’s sign</strong> (X-ray)</p>\n<p>The presence of <strong>gas in the fetal great vessels</strong> in case of<strong> intrauterine fetal demise</strong> is known as Roberts sign.</p>\n<p>&nbsp;</p>\n<p><strong>Stuck twin sign</strong> (USG)</p>\n<p>Smaller twin is held in a fixed position along the uterine wall due to the severe oligohydramnios. It is seen in <strong>twin-twin transfusion syndrome</strong>.</p>\n<p>&nbsp;</p>\n<p><span><strong>Butterfly sign&nbsp;</strong>(USG)</span></p>\n<p><span>Normal appearance of the choroid plexuses on axial imaging of the fetal brain. An<strong> absence</strong>&nbsp;of this sign suggests <strong>holoprosencephaly</strong>.</span></p>\n<p>&nbsp;</p>\n<p><span><strong>Cloverleaf skull&nbsp;</strong>(USG)</span></p>\n<p><span>Seen in <strong>craniosynostosis</strong></span></p>\n<p>&nbsp;</p>\n<p><span><strong>Empty amnion sign&nbsp;</strong>(USG)</span></p>\n<p><span>Visualization of an amniotic sac without concomitant visualization of an embryo. It is an indicator of <strong>pregnancy failure</strong>.</span></p></div><!---->\n      <!---->\n    </div><!---->'),
(1559, 'Radiology,', 'PM2134. Congenital diaphragmatic hernias (CDH)', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><div class=\page\ title=\Page 959\>\n<div class=\section\>\n<div class=\layoutArea\>\n<div class=\column\>\n<div class=\page\ title=\Page 1892\>\n<div class=\section\>\n<div class=\layoutArea\>\n<div class=\column\>\n<div class=\page\ title=\Page 1892\>\n<div class=\section\>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>It is due to<strong>&nbsp;</strong>the <strong>failure of the closure of the pleuroperitoneal canal</strong> in the developing fetus (Normally the pleuroperitoneal cavities separate by 8-10 weeks of gestation). <strong>Left-sided </strong>hernias are<strong>&nbsp;</strong>more <strong>common</strong>(80%).</p>\n<p>The two well-recognized congenital sites where abdominal viscera can herniate into the chest are:</p>\n</div>\n</div>\n</div>\n</div>\n</div>\n</div>\n</div>\n</div>\n<ul>\n<li><strong>The foramen of Morgagni</strong>: a hernia in the <strong>anteromedial</strong>&nbsp;part of the diaphragm with a defect between the sternal and costal attachments. The most commonly involved viscus is the <strong>transverse colon.</strong></li>\n<li><strong>The foramen of Bochdalek</strong>: through the dome of the diaphragm<strong> posterolaterally</strong></li>\n</ul>\n<p>CDH causes <strong>pulmonary hypoplasia (bilateral) </strong>by compressing the developing lung. These lungs have smaller bronchi, with less bronchial branching and less alveolar surface area than lungs in normal infants. Pulmonary hypertension may also develop due to increased thickness of arteriolar smooth muscle and also by&nbsp;multiple local and systemic vasoactive factors.</p>\n<p><strong>Clinical features:</strong></p>\n<div class=\page\ title=\Page 1892\>\n<div class=\section\>\n<div class=\layoutArea\>\n<div class=\column\>\n<ul>\n<li>respiratory distress at birth</li>\n<li>grunting respiration</li>\n<li>chest retractions</li>\n<li>dyspnea</li>\n<li>cyanosis</li>\n<li>scaphoid abdomen</li>\n<li>decreased breath sounds along with bowel sound over the chest with CDH</li>\n<li>shifting of heart sounds to the right (for left-sided CDH)</li>\n</ul>\n<div class=\section\>&nbsp;</div>\n</div>\n</div>\n</div>\n</div>\n</div>\n</div>\n</div>\n</div></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/f4f1fdad64d24053b439a528cf873989x1280x1660.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(1560, 'Radiology,', 'PM1988. Causes of lytic lesions in the skull', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><ul>\n<li>Eosinophilic granuloma (geographic lytic lesions in the vault of skull with beveled edges)</li>\n<li>Multiple myeloma (punched out lytic lesion)</li>\n<li>Langerhans Cell Histiocytosis (permeative lesions)</li>\n<li>Hyperparathyroidism (salt and pepper skull)</li>\n<li>Metastasis</li>\n<li>Lymphoma</li>\n<li>Tuberculosis&nbsp;</li>\n<li>Osteomyelitis&nbsp;</li>\n<li>Radiation&nbsp;</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(1561, 'Radiology,', 'PM1960. Radiological Features of Scurvy', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>Radiological features of scurvy:</p>\n<ul>\n<li>Generalised osteopenia</li>\n<li><strong>Pencil-point cortex</strong>: Cortical thinning</li>\n<li><strong>Periosteal</strong> <strong>reaction</strong> due to subperiosteal hemorrhage</li>\n<li><strong>Scorbutic rosary</strong>: Expansion of the costochondral junctions</li>\n<li><strong>Hemarthrosis</strong></li>\n<li><strong>Wimberger</strong> <strong>ring</strong>: Circular, opaque radiological shadow surrounding epiphyseal centers of ossification, which may result from bleeding</li>\n<li><strong>Frenkel line</strong>: Dense zone of provisional calcification&nbsp;</li>\n<li><strong>Trummerfeld</strong> <strong>zone</strong>: Lucent metaphyseal band underlying Frankel line</li>\n<li><strong>Pelken spur</strong>: Metaphyseal spurs which result in cupping of the metaphysis</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/78592172dff84017a824c47084b3f181x1024x906.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(1562, 'Radiology,', 'PM1959. Radiological Features of Rickets', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>Radiological features of rickets:</p>\n<ul>\n<li><strong>Loss of provisional zone of calcification: </strong>earliest radiological finding of rickets.&nbsp;</li>\n<li>Delayed appearance of the epiphysis</li>\n<li><strong>Widening of growth plate:&nbsp;</strong>normal width of growth is 2-4mm. It is increased in cases of rickets due to excessive accumulation of uncalcified osteoid at the growth plate.</li>\n<li><strong>Cupping of metaphysis:&nbsp;</strong>normally metaphysis meets epiphysis as a smooth line of sclerosis. But in rickets the line is absent and the metaphyseal ends appear irregular and the cartilage accumulating in the growth plate creates a depression in the metaphysis causing cupping.</li>\n<li><strong>Fraying</strong>:&nbsp;indistinct margins of the metaphysis</li>\n<li><strong>Bowing of diaphysis,&nbsp;</strong>with thinning of cortices</li>\n<li>In healing rickets, zone of calcification becomes denser.&nbsp;</li>\n</ul>\n<p>The above features are prominent at the growth plates where growth is greatest:</p>\n<ul>\n<li>Knee: distal femur, proximal tibia</li>\n<li>Wrist: especially the ulna</li>\n<li>Anterior rib ends: <strong>rachitic rosary</strong></li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/3fb9e16daa324d32bc69ad013a616401x1280x1694.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>The below image shows bowing of legs in rickets.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/2540c743846448f9a7b302483397230ax256x406.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(1563, 'Radiology,', 'PM1910. Classical Radiological Appearance of Bone Tumors', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>RADIOLOGICAL APPEARANCE&nbsp;</strong></td>\n<td>\n<p><strong>TUMORS</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Sunray appearance / Codman’s triangle</p>\n</td>\n<td>\n<p>Osteosarcoma (but can be seen in any malignant lesion)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Onion peel appearance&nbsp;</p>\n</td>\n<td>\n<p>Ewing’s sarcoma (but can be seen in any malignant lesion or chronic osteomyelitis)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Soap bubble appearance&nbsp;</p>\n</td>\n<td>\n<p>Giant cell tumor, adamantinoma&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Ground glass appearance / Shepherds</p>\n<p>crook deformity&nbsp;</p>\n</td>\n<td>\n<p>Fibrous dysplasia&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Patchy calcification&nbsp;</p>\n</td>\n<td>\n<p>Chondrogenic tumors (chondrosarcoma and chondroblastoma)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Popcorn, punctate or comma-shaped calcification</p>\n</td>\n<td>\n<p>Chondrosarcoma&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Stippled or Punctate calcification</p>\n</td>\n<td>\n<p>Chondroblastoma&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Homogeneous calcification&nbsp;</p>\n</td>\n<td>\n<p>Osteogenic tumors (osteosarcoma)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Fallen fragment / Trap door sign</p>\n</td>\n<td>\n<p>Simple bone cyst</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Double-density fluid levels and intra-lesional septation</p>\n</td>\n<td>\n<p>Aneurysmal bone cyst</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Winking owl sign</p>\n</td>\n<td>\n<p>Vertebral metastasis&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Polka dot sign / Corduroy / Jailhouse sign</p>\n</td>\n<td>\n<p>Vertebral hemangioma</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Moth eaten bone</p>\n</td>\n<td>\n<p>Multiple myeloma</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1564, 'Radiology,', 'PM1831. BIRADS - Breast Imaging Reporting and Data System', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>Breast Imaging Reporting and Data System <strong>(BI-RADS)</strong> standardizes the reporting for mammography and ultrasonography and helps guide decision making:</p>\n<table>\n<tbody>\n<tr>\n<td><strong>Category</strong></td>\n<td><strong>Definition</strong></td>\n<td><strong>% Chances of Malignancy</strong></td>\n<td><strong>Follow-up</strong></td>\n</tr>\n<tr>\n<td>0</td>\n<td>Incomplete assessment</td>\n<td>Not applicable</td>\n<td>Additional imaging evaluation</td>\n</tr>\n<tr>\n<td>1</td>\n<td>Negative</td>\n<td>Essentially 0%</td>\n<td>Annual screening</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Benign finding</td>\n<td>Essentially 0%</td>\n<td>Annual screening</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Probably benign finding</td>\n<td>&gt;0% but ≤2%</td>\n<td>6 monthly mammograms for the next 2 years</td>\n</tr>\n<tr>\n<td>4\n<p>&nbsp;</p>\n</td>\n<td colspan=\2\>\n<p>4A: low level of suspicion for malignancy &gt;2% to ≤10%</p>\n<p>4B: moderate suspicion for malignancy &gt;10% to ≤50%</p>\n<p>4C: high suspicion for malignancy &gt;50% to &lt;95%</p>\n</td>\n<td>Biopsy</td>\n</tr>\n<tr>\n<td>5</td>\n<td>Highly suggestive of malignancy&nbsp;</td>\n<td>≥95%</td>\n<td>Appropriate action should be taken&nbsp;</td>\n</tr>\n<tr>\n<td>6</td>\n<td colspan=\2\>Known biopsy-proven malignancy</td>\n<td>Appropriate treatment</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1565, 'Radiology,', 'PM1827. Fetal Monitoring using Ultrasound', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><ul class=\ul1\>\n<li class=\li1\>The first definitive USG finding to suggest pregnancy is the visualization of the gestational sac.</li>\n<li class=\li1\>The first sign of intrauterine pregnancy is the presence of yolk sac within the gestational sac.</li>\n<li class=\li1\>Best time to assess gestational age by USG is 9-12 weeks(by CRL)</li>\n<li class=\li1\>CRL(mm)+42 = Days of pregnancy</li>\n<li class=\li1\>CRL(cm)+6.5 = Gestational age in weeks.</li>\n<li class=\li1\>The best indicator of fetal growth is the abdominal circumference.&nbsp;Abdominal circumference is the transverse measurement at the level of the stomach with the confluence of the umbilical vein and right portal vein.</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(1566, 'Surgery,', 'PM1804. Radiological findings in various GI disorders', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Duodenal atresia:&nbsp;</strong>Double bubble sign</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/a8e78c140797481eb09e4bcef9106cd8x720x910.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Jejunal and ileal atresia:&nbsp;</strong>Multiple air-fluid levels (plain erect X-ray abdomen) presenting as&nbsp;<strong>triple bubble sign.&nbsp;</strong></p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/9513fa34bce3499088a9a4601ae434c4x834x1186.PNG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Congenital hypertrophic pyloric stenosis: </strong>Single enlarged gastric gas bubble&nbsp;- <strong>single bubble</strong> sign</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/20dd99dfcb3e4ffa8728b296b69b5556x284x383.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Sigmoid volvulus:&nbsp;</strong>Coffee bean sign&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/81240b80138a476d8f5ca2b40d19c68bx490x600.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Achalasia cardia:&nbsp;</strong>Bird-beak sign</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/fdcc40d8b70549d4b0c489b623ab32c8x720x1526.PNG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Small bowel obstruction</strong>: The obstructed bowel loops are central in location and show <strong>valvulae conniventes</strong> (mucosal folds that cross the entire diameter of the bowel) characteristic of jejunal involvement.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/c32bbb60d2824af6b74fbd5585c9bb98x1279x1662.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Large bowel obstruction</strong>: The presence of <strong>haustrations</strong> is a feature of large bowel obstruction. Haustral folds are irregular and do not cross the entire diameter of the bowel. These are absent in the caecum.&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/6bf0090dbba54d08bf6d460f3f8c3a01x1279x1642.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Caecal volvulus</strong>: Presence of caecal dilatation, single air-fluid level extending into the left upper quadrant, and absence of gas in the distal colon. The colonic haustrations are well maintained.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/cee8aa378b1148b198c0d8a7c9f23822x720x608.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Midgut volvulus</strong>: Corkscrew appearance showing the duodenum and jejunum spiraling around the mesenteric vessels in upper GI contrast studies.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/300e1080a96d4fe19194df39a503fef0x660x476.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Necrotizing enterocolitis (NEC)</strong>: Abdominal X-ray findings include pneumatosis intestinalis (air in the bowel wall).</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/13531a6af0434a64bf763df3a4c61fb8x479x600.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Cystic fibrosis</strong> (CF): Abdominal x-ray with contrast enema reveals a microcolon with several meconium plugs.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/6d13f1689c1141ed93c2e447931546f0x512x683.PNG\><!---->\n      </div><!---->\n    </div><!---->'),
(1567, 'Radiology', 'PM1737. Sites of normal intracranial calcification', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><ul>\n<li>Pineal gland</li>\n<li>Habenular commissure</li>\n<li>Choroid plexus</li>\n<li>The dura</li>\n<li>Ligaments (petroclinoid and interclinoid)</li>\n<li>Pacchionian bodies</li>\n<li>Basal ganglia - physiological only&nbsp;in a senile person</li>\n<li>Pituitary gland</li>\n<li>Arachnoid granulations</li>\n</ul>\n<p>NOTE: The lens (not intracranial) can also be seen as a calcified structure</p></div><!---->\n      <!---->\n    </div><!---->'),
(1568, 'Radiology', 'PM1733. Difference between Normal Radiography and Mammography', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>&nbsp;</td>\n<td><strong>Radiography</strong></td>\n<td><strong>Mammography</strong></td>\n</tr>\n<tr>\n<td><strong>Type of X rays</strong></td>\n<td>Bremsstrahlung reaction</td>\n<td>Characteristic radiation</td>\n</tr>\n<tr>\n<td><strong>Target</strong></td>\n<td>Tungsten</td>\n<td>Molybdenum</td>\n</tr>\n<tr>\n<td><strong>Exit Window</strong></td>\n<td>Glass</td>\n<td>Beryllium</td>\n</tr>\n<tr>\n<td><strong>Filter</strong></td>\n<td>Aluminum</td>\n<td>Molybdenum</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1569, 'Medicine,', 'PM1721. Radiological Signs of Hyperparathyroidism', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Rugger jersey spine</strong>: It refers to the prominent hyperdensities at the&nbsp;endplates of successive vertebrae, which mimics the striped&nbsp;appearance of a rugby jersey.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/666a460de5f9487294c804cd28a46d5bx720x1197.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Subperiosteal bone resorption</strong>: This is seen on the radial side of the proximal and middle phalanges of the index and middle fingers. It&nbsp;is the most consistent and specific finding of hyperparathyroidism and is virtually<strong> pathognomonic</strong> of the condition.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/49d4133d5b6e49a394882e929d8be212x1280x1246.PNG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Brown tumors / Osteitis fribrosa cystica /&nbsp;Von Recklinghausens disease of bone</strong>: These are composed of pseudotumors that are made up of macrophages, blood, and connective tissue. They are seen as radiolytic areas due to the reduction in the bone trabeculae. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/57436a7d9dac4a02a03a9d08d991b811x720x720.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Salt and pepper appearance of the skull</strong>:&nbsp; Refers to multiple tiny well-defined lucencies in the calvaria of the skull ( top part of the skull) due to resorption of the trabecular bone. Alternate areas of mineralization (salt-white) and decalcification (pepper-black)</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/ea56ff526ee445e7ba55188fea556f7fx672x526.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Acral osteolysis</strong>: This is also known as the tufting of phalanges&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(1570, 'Radiology,', 'PM1630. Radiological findings in Acute Pancreatitis', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>Radiographic findings of acute pancreatitis are non-specific.</p>\n<p>The&nbsp;<strong>sentinel loop</strong>&nbsp;is seen in an X-ray. It&nbsp;is a focal area of&nbsp;adynamic ileus&nbsp;close to an intra-abdominal inflammatory process. A sentinel loop in the upper abdomen indicates pancreatitis.</p>\n<p>The sentinel loop sign may aid in localizing the source of inflammation. For example, a sentinel loop in the upper abdomen may indicate pancreatitis, while one in the right lower quadrant may be due to appendicitis.</p>\n<p><strong>Colon cut-off sign:</strong> <strong>Functional spasm at the splenic flexure</strong> due to the extension of inflammation to phrenicocolic ligament via transverse mesocolon.</p>\n<p>The X-ray given below shows <strong>sentinel loop sign</strong>&nbsp;(marked as 1) and <strong>colon cut-off sign</strong>&nbsp;(marked as 2) in a patient with acute pancreatitis:</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/51fba8412fcf4e6cba1d6b1304190a1ax1280x1503.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Renal halo sign:</strong>&nbsp;Edema around the kidney due to the involvement of paranephric fat resulting in:</p>\n<ul>\n<li>Obliteration of psoas shadow</li>\n<li>Localised ground-glass appearance</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/1e91a2c0705b412da5c4e381216d902cx1280x1025.JPEG\><!---->\n      </div><!---->\n    </div><!---->');
INSERT INTO db_pearl_m (pid, subject, pearl_title, pearl_desc) VALUES
(1571, 'Radiology,', 'PM1520. Important radiological signs in OBG', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<div><span><strong>Sign</strong></span></div>\n</td>\n<td>\n<div><span><strong>Significance</strong></span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span><strong>Bagel sign</strong></span></div>\n</td>\n<td>\n<div><span>Gestational sac in the adnexa with a hyperechoic ring.</span></div>\n<div><span>USG sign in <strong>tubal ectopic</strong>.</span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span><strong>Ball Sign</strong></span></div>\n</td>\n<td>\n<div><span>X-ray shows a crumpled up spine of the fetus.</span></div>\n<div><span>Radiological sign of <strong>intrauterine fetal death</strong>.</span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span><strong>Banana Sign</strong></span></div>\n</td>\n<td>\n<div><span>Abnormal anterior curvature of the cerebellum due to the associated <strong>Arnold-Chiari</strong> malformation.</span></div>\n<div><span>USG sign in <strong>open spina bifida</strong>.</span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span><strong>Double decidual sac Sign</strong></span></div>\n</td>\n<td>\n<div><span>Two concentric echogenic rings separated by a hyperechoic space.</span></div>\n<div><span><strong>Normal</strong> USG appearance of an intrauterine gestational sac.</span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span><strong>Lambda Sign</strong></span></div>\n</td>\n<td>\n<div><span>The chorionic tissue in between the two layers of the membrane between the twins.</span></div>\n<div><span>USG sign seen in <strong>di-chorionic pregnancies</strong>.</span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span><strong>‘T’ Sign</strong></span></div>\n</td>\n<td>\n<div><span>USG sign seen in <strong>monochorionic twins</strong>. As the intertwin membrane does not have any chorionic tissue it gives rise to ‘T’ sign in ultrasound.</span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span><strong>Bunch of grapes sign</strong></span></div>\n</td>\n<td>\n<div><span>USG appearance of multiple cystic spaces or lesions in a <strong>complete mole</strong>.</span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span><strong>Butterfly sign</strong></span></div>\n</td>\n<td>\n<div><span>Normal appearance of the choroid plexuses on axial imaging of the fetal brain. An<strong> absence</strong>&nbsp;of this sign suggests <strong>holoprosencephaly</strong>.</span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span><strong>Cloverleaf skull</strong></span></div>\n</td>\n<td>\n<div><span>Seen in <strong>craniosynostosis</strong></span></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><span><strong>Empty amnion sign</strong></span></div>\n</td>\n<td>\n<div><span>Visualization of an amniotic sac without concomitant visualization of an embryo. It is an indicator of <strong>pregnancy failure</strong>.</span></div>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1572, 'Medicine,', 'PM0584. Chest X Ray signs in pulmonary embolism', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Westermarks sign:&nbsp;</strong>Focal area of oligemia (decreased vascular markings) distal to the embolism.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/1320503c26a8431b903e7e15089c8ed4x720x655.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Hamptons hump:&nbsp;</strong>Peripherally placed wedge-shaped or dome-shaped opacity due to infarction of lung tissue.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/5faac4b6986f4b6da649d4ca027f99b3x1063x874.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Pallas sign:&nbsp;</strong>Increased size of the right descending pulmonary artery with a sausage-like appearance.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/9d6849e258104848bfc976a72abaae15x1069x804.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Fleishner sign</strong>: enlarged pulmonary artery.</p>\n<p><strong>Melting sign</strong>:&nbsp;an infarct shows rapid clearing in contrast to pneumonic consolidation.</p>\n<p><strong>McConnells</strong>&nbsp;<strong>sign</strong> (indirect sign of PE seen in transthoracic echo):&nbsp;<strong>hypokinesis</strong>&nbsp;of the right ventricular free wall with&nbsp;<strong>normal motion</strong>&nbsp;of the right ventricle apex.</p>\n<p><strong>Knuckle&nbsp;</strong>or<strong>&nbsp;sausage’ sign:&nbsp;</strong>dilatation of the pulmonary artery,&nbsp;proximal to the embolus with collapse or constriction of the distal arteries resulting in an abrupt tapering of these arteries. &nbsp;</p>\n<p>Note - A normal chest X-ray does not rule out pulmonary embolism in a patient with unexplained acute tachypnea or hypoxemia.</p></div><!---->\n      <!---->\n    </div><!---->'),
(1573, 'Radiology', 'PM0369. Tram Track Appearance in Various Conditions', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Tram tracks </strong>or<strong> tram track signs</strong> are medical signs that bear some resemblance to <strong>tramway tracks (Railway Track)</strong>.</p>\n<p><strong>1. Pulmonology</strong></p>\n<ul>\n<li>When found in the lungs, tram tracks are radiologic signs that are usually accompanied by <strong>pulmonary edema</strong> in cases of <strong>congestive heart failure</strong> and <strong>bronchiectasis</strong>.</li>\n<li><strong>Tram tracks</strong> are caused by <strong>bronchial</strong> <strong>wall</strong> <strong>thickening</strong>, and can be detected on a <strong>lateral chest X-ray</strong>.</li>\n</ul>\n<p><strong>2. Nephrology</strong></p>\n<ul>\n<li>The term \<strong>tram tracks</strong>\ is also used to describe the basement membrane duplication found on light microscopy that is characteristic of membranoproliferative glomerulonephritis<strong> (MPGN) type I</strong>.</li>\n<li>It is less commonly associated with types II and III.</li>\n</ul>\n<p><strong>3. Neurology</strong></p>\n<ul>\n<li>The term has also been used to describe findings associated with <strong>optic nerve sheath meningioma.</strong></li>\n<li><strong>Tram track-shaped calcifications</strong> in the cerebral cortex indicate <strong>Sturge-Weber syndrome</strong>.</li>\n</ul>\n<p><strong>4. Mammary glands:</strong></p>\n<ul>\n<li>Tram track appearance in mammography/USG indicates <strong>Duct Ectasia.</strong></li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/005dd7f21675470cad600851cab9e990x1280x960.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(1574, 'Radiology,', 'PM0284. Bone scan findings', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Causes of a hot spot</strong></p>\n</td>\n<td>\n<p><strong>Causes of a cold spot</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Osteomyelitis</p>\n<p><strong>Bone&nbsp;tumors and m</strong><strong>etastases</strong></p>\n<p><strong>Fibrous dysplasia</strong></p>\n<p>Trauma (fracture healing)</p>\n<p><strong>Avascular necrosis</strong></p>\n<p><strong>Perthes&nbsp;disease</strong></p>\n<p>Paget’s disease</p>\n<p>Hyperparathyroidism</p>\n</td>\n<td>\n<p><strong>Multiple myeloma</strong></p>\n<p>Fibrous cortical defect</p>\n<p>Pseudoarthrosis</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1575, 'Radiology,', 'PM0251. Important radiological signs in cardiology', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td>\n<div><strong>Sign</strong></div>\n</td>\n<td>\n<div><strong>Condition</strong></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><strong>Flask</strong> shaped/ Pear shaped/ <strong>Leather bottle </strong>shaped/ <strong>Money-bag</strong> shaped heart</div>\n</td>\n<td>\n<div><strong>Pericardial effusion</strong></div>\n</td>\n</tr>\n<tr>\n<td>\n<div>Boot shaped heart/ <strong>Coeur-en-sabot</strong></div>\n</td>\n<td>\n<div><strong>Fallot’s tetralogy</strong></div>\n</td>\n</tr>\n<tr>\n<td>\n<div>Gooseneck sign</div>\n</td>\n<td>\n<div>Endocardial cushion defects</div>\n</td>\n</tr>\n<tr>\n<td>\n<div><strong>Box-shaped heart</strong></div>\n</td>\n<td>\n<div><strong>Ebsteins anomaly</strong></div>\n</td>\n</tr>\n<tr>\n<td>\n<div>Jug handle appearance</div>\n</td>\n<td>\n<div>Primary pulmonary artery hypertension</div>\n</td>\n</tr>\n<tr>\n<td>\n<div>“3” like appearance of the aorta</div>\n</td>\n<td>\n<div>Coarctation of aorta</div>\n</td>\n</tr>\n<tr>\n<td>\n<div><strong>Egg on string appearance</strong></div>\n</td>\n<td>\n<div><strong>Transposition of great vessels</strong></div>\n</td>\n</tr>\n<tr>\n<td>\n<div>Egg in cup appearance, Pericardial calcification</div>\n</td>\n<td>\n<div>Constrictive pericarditis</div>\n</td>\n</tr>\n<tr>\n<td>\n<div><strong>Snowman’s heart, Cottage loaf appearance, Figure of Eight appearance</strong></div>\n</td>\n<td>\n<div><strong>Total anomalous pulmonary venous circulation</strong></div>\n</td>\n</tr>\n<tr>\n<td>\n<div><strong>Scimitar</strong> sign</div>\n</td>\n<td>\n<div><strong>Partial anomalous pulmonary venous return</strong></div>\n</td>\n</tr>\n<tr>\n<td>\n<div>Calcification in the heart wall</div>\n</td>\n<td>\n<div>Carcinoid syndrome</div>\n</td>\n</tr>\n<tr>\n<td>\n<div>Calcification of ascending aorta</div>\n</td>\n<td>\n<div>Atherosclerosis, Syphilis</div>\n</td>\n</tr>\n<tr>\n<td>\n<div>Hilar dance on fluoroscopy</div>\n</td>\n<td>\n<div>Atrial septal defect</div>\n</td>\n</tr>\n<tr>\n<td>\n<div>Batwing appearance</div>\n</td>\n<td>\n<div>Pulmonary edema</div>\n</td>\n</tr>\n<tr>\n<td>\n<div>Pruned tree appearance of the pulmonary vasculature</div>\n</td>\n<td>\n<div>Pulmonary artery hypertension</div>\n</td>\n</tr>\n</tbody>\n</table>\n<div>&nbsp;</div></div><!---->\n      <!---->\n    </div><!---->'),
(1576, 'Radiology', 'PM0236. Causes of pathological basal ganglia calcification', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><ul>\n<li>Endocrinopathies\n<ul>\n<li>Hypoparathyroidism (primary or secondary)</li>\n<li>Pseudohypoparathyroidism</li>\n<li>Hyperparathyroidism</li>\n<li>Hypothyroidism</li>\n</ul>\n</li>\n<li>Vasculitis (e.g. SLE)</li>\n<li>Mitochondrial disorders (e.g. Mitochondrial myopathy)</li>\n<li>Infections\n<ul>\n<li>Brucellosis</li>\n<li>TORCH&nbsp;</li>\n<li>Epstein-Barr virus (EBV)</li>\n<li>HIV</li>\n</ul>\n</li>\n<li>Other inherited disorders\n<ul>\n<li>Neuroferritinonpathy</li>\n<li>Cockayne syndrome</li>\n</ul>\n</li>\n<li>Radiation</li>\n<li>Chemotherapy</li>\n<li>Carbon monoxide poisoning</li>\n<li>Lead poisoning</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(1577, 'Paediatrics,', 'PM0193. Radiological findings in some congenital heart defects', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Boot shaped heart - Tetralogy of Fallot (TOF)</strong></p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/b8d455f894f24e8e9c147e7c3a178428x510x558.GIF\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Egg on string appearance - Transposition of Great Arteries (TGA)</strong></p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/ad3fe6b6422548c8856299317c3e47c5x510x522.GIF\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Snowmans heart appearance or figure of 8 appearance</strong> -&nbsp;<strong>Supracardiac TAPVC</strong></p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/c4d273d1af7445fdad02e659555c9e69x510x494.GIF\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Box Shaped Heart - Ebsteins Anomaly</strong></p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/d07d5041004645849e8cce1dd37bd092x510x640.GIF\><!---->\n      </div><!---->\n    </div><!---->'),
(1578, 'Radiology,', 'PM0124. Differences between the two types of endemic cretinism', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td><strong>Neurological type</strong></td>\n<td><strong>Myxedematous type</strong></td>\n</tr>\n<tr>\n<td>Goiter&nbsp;</td>\n<td>No goiter (USG shows thyroid atrophy)</td>\n</tr>\n<tr>\n<td>Euthyroid&nbsp;</td>\n<td>Permanent hypothyroidism</td>\n</tr>\n<tr>\n<td>Intellectual disability, deaf-mutism, squint, proximal spasticity,&nbsp;and rigidity</td>\n<td>Intellectual disability, Deaf, coarse facial features, and myxedema.</td>\n</tr>\n<tr>\n<td>Normal pubertal development and adult stature</td>\n<td>Delayed skeletal maturation and sexual development, severe short stature</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(1579, 'Radiology,', 'PM0111. Types of Intracranial Hemorrhage', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>1. Extradural hemorrhage/epidural hematoma (EDH)</strong></p>\n<ul>\n<li>Source: Arterial - <strong>middle meningeal artery&nbsp;</strong>(m/c)</li>\n<li>Cause: Trauma - fracture of the squamous part of the <strong>temporal bone</strong></li>\n<li><strong>Lens or lentiform</strong> shaped opacity</li>\n<li>Presence of a characteristic <strong>lucid interval</strong></li>\n<li>Treatment: Surgical evacuation of large/rapidly evolving EDH</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/507e530f4b3c4faba01eac509037b78bx784x963.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>2. Acute subdural hemorrhage&nbsp;</strong>(SDH)</p>\n<ul>\n<li>Source: Venous - bridging veins</li>\n<li>Cause: Trauma</li>\n<li>Common in <strong>old age</strong>, <strong>alcoholics</strong>, patients on <strong>anticoagulants</strong>, <strong>shaken baby syndrome</strong> (a form of child abuse seen in infants and toddlers)</li>\n<li>Clinical features: Acute SDH may be associated with a lucid interval; chronic SDH is associated with drowsiness, headache</li>\n<li>CT findings: <strong>Crescent</strong>&nbsp;opacity</li>\n<li>Treatment: May resolve spontaneously or require surgical drainage</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/a7e5c451c38e4dedbdc912ca6a22c4dax1280x1026.PNG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>3. Acute on chronic SDH</strong></p>\n<ul>\n<li>Source: Venous - bridging veins</li>\n<li>Cause: Usually, <strong>trivial trauma</strong> in old age or patients on anticoagulants</li>\n<li><strong>Crescent</strong>&nbsp;clots</li>\n<li>Presence of a history of headache, confusion, and <strong>being prone to falls</strong></li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/da30bfdd9fb343269e21219017fbda42x1280x1149.PNG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>4. Subarachnoid hemorrhage</strong></p>\n<ul>\n<li>Source: Arterial</li>\n<li>Causes: <strong>Trauma</strong> (most common), <strong>berry aneurysm</strong> (2nd most common)</li>\n<li>M/c sites: Terminal ICA, MCA bifurcation, top of basilar artery</li>\n<li>Clinical features: Characteristic <strong>thunderclap headache</strong></li>\n<li><strong>CSF: Xanthochromia</strong></li>\n<li><strong>Hyperdensity</strong> in the subarachnoid space</li>\n<li>Treatment:&nbsp; <strong>T</strong><strong>riple H therapy</strong> (hypertension, hypervolemia, and hemodilution) to prevent vasospasm, Clipping/coiling of berry aneurysm</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/d7cb2373c2794c4684e64759ca20c19ex647x745.PNG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>5. Intra-parenchymal hemorrhage</strong></p>\n<ul>\n<li><strong>Most common</strong> type of intracranial hemorrhage</li>\n<li>Cause:<strong> Hypertension</strong> (most common cause)</li>\n<li>Hyperdensity within the brain parenchyma</li>\n<li>Site: <strong>P</strong><strong>utamen</strong> of the basal ganglia (most common)</li>\n<li>Treatment: Correction of any identified coagulopathy by infusing prothrombin complex concentrates, FFP, or fresh platelets as required; surgical evacuation for cerebellar hematoma &gt;3 cm in size</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/917c06cd8b0241e8995ff2379c1b42f0x831x977.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(1580, 'Radiology,', 'PM0066. Investigation of Choice in Respiratory Diseases', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td><strong>Disease</strong></td>\n<td><strong>Investigation of choice</strong></td>\n</tr>\n<tr>\n<td><strong>Interstitial lung disease</strong></td>\n<td>HRCT</td>\n</tr>\n<tr>\n<td><strong>Bronchiectasis</strong></td>\n<td>HRCT</td>\n</tr>\n<tr>\n<td><strong>Pulmonary embolism</strong></td>\n<td>CT Pulmonary Angiography &gt;&nbsp;Ventilation-Perfusion Scan</td>\n</tr>\n<tr>\n<td><strong>Calcification</strong></td>\n<td>CT</td>\n</tr>\n<tr>\n<td><strong>Solitary pulmonary nodule</strong></td>\n<td>CT &gt;&nbsp;PET</td>\n</tr>\n<tr>\n<td><strong>Lung Malignancy</strong></td>\n<td>CT scan</td>\n</tr>\n<tr>\n<td><strong>Superior sulcus tumor</strong></td>\n<td>MRI scan &gt; CT scan</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1581, 'Radiology', 'PM0065. Eggshell calcification of lymph node on chest X-ray', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>Eggshell calcification refers to fine calcification seen at the periphery of a lymph node. Causes are -</p>\n<ul>\n<li><strong>Silicosis</strong> (most common)</li>\n<li>Sarcoidosis</li>\n<li>Post radiotherapy</li>\n<li>Post-irradiation lymphoma</li>\n<li>Histoplasmosis</li>\n<li>Blastomycosis</li>\n<li>Amyloidosis</li>\n<li>Tuberculosis</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/4a4d813b4cc64ebbb0bdff38920873e8x1280x930.PNG\><!---->\n      </div><!---->\n    </div><!---->'),
(1582, 'Radiology,', 'PM0064. Causes of miliary shadow in Chest X-ray', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>Miliary shadow&nbsp;refers to innumerable, small&nbsp;(1-4 mm) pulmonary nodules&nbsp;scattered throughout the&nbsp;lungs. It is seen in -&nbsp;</p>\n<ul>\n<li><strong>Miliary tuberculosis</strong></li>\n<li>Histoplasmosis</li>\n<li><strong>Sarcoidosis</strong></li>\n<li>Pneumoconiosis – coal workers pneumoconiosis and silicosis</li>\n<li>Healed varicella</li>\n<li><strong>Long-standing MS</strong> – can lead to pulmonary hemosiderosis</li>\n<li>Alveolar microlithiasis</li>\n<li>Papillary and medullary Ca thyroid</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/cd37e41e724146cd94276c610fdefbbcx720x568.PNG\><!---->\n      </div><!---->\n    </div><!---->'),
(1583, 'Orthopaedics,', 'PM1339. Important radiosenstive and radioresistant bone tumours', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Radiosensitive</strong> tumors:</p>\n<ol>\n<li>Metastasis (except metastasis from renal cell carcinoma which is radioresistant)</li>\n<li>Ewings sarcoma</li>\n<li>Multiple myeloma</li>\n<li>Lymphoma</li>\n</ol>\n<p><strong>Radioresistant</strong> tumors:</p>\n<ol>\n<li>Osteosarcoma</li>\n<li>Chondrosarcoma</li>\n<li>Fibrosarcoma</li>\n<li>Malignant fibrous histiocytoma</li>\n</ol></div><!---->\n      <!---->\n    </div><!---->'),
(1584, 'Radiology', 'PM2445. Barium swallow', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Diffuse esophageal spasm</strong>: Corkscrew appearance</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/d8236260ddee416fbac986f814ef6970x720x938.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Feline esophagus</strong>: Transient transverse bands seen in the mid and lower esophagus on a double-contrast barium swallow. It is seen in active gastroesophageal reflux, hiatus hernia, and eosinophilic esophagitis.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/270761a611f6405a961fcecfe050e5edx720x1107.PNG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Achalasia</strong>: Dilated esophagus with a retained column of barium and bird beak sign.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/9d0869506ae542e1bb72aa5e0c3cc94cx245x520.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Zenkers diverticulum: </strong>Posterior outpouching of the hypopharynx through a weakness in the muscle layer called Killian dehiscence.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/35c984e64ee642878526077ceef5e961x1080x1503.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Esophageal carcinoma</strong>: Irregular narrowing, mucosal destruction, and shouldered margins.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/9c598150228f4315b254e5ba8af9b13fx1071x1024.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Schatzkis ring</strong>: Thin submucosal circumferential ring in the lower esophagus at the squamocolumnar junction.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/f58822b0b33945d69d9a97c60e34bc24x720x996.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Esophageal web</strong> in a patient with Plummer Vinson syndrome. Esophageal webs are thin membranes of normal esophageal tissue consisting of mucosa and submucosa. Translucent part denotes esophageal web.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/b37419089d4840df98207a1eaa0b6781x720x1339.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/d9fbe2bf49d9490a91f5525c33acbb34x592x571.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Esophageal leiomyoma</strong>&nbsp;is the <strong>most common</strong>&nbsp;benign tumor of the esophagus. On barium swallow, it is seen as a discrete ovoid mass that is well outlined by barium (smooth submucosal mass). Its borders form slightly obtuse angles with the esophagal wall.</p>\n<p>The image below depicts a barium study showing the classic<strong> semilunar filling defect</strong>.&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/cc584152656442f6bcbb0fb47e97fc92x388x385.PNG\><!---->\n      </div><!---->\n    </div><!---->'),
(1585, 'Radiology', 'PM2443. Barium Studies', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Barium swallow:&nbsp;</strong>Used to assess the pharynx, esophagus, and stomach. (Barium paste is used)</p>\n<p>The image below is a barium swallow showing eosinophilic esophagitis and associated stricture:</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/b5bdec3be8214149b4321669d20ce417x323x461.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Barium meal: </strong>Used to assess stomach and duodenum. (95% BaSO4 is used)</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/26d315aabed64925a095dd613db26b5cx720x720.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Barium meal followthrough: </strong>Used to assess the small intestine (50% BaSO4 is used)</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/2581dc5431c74171abcd83814d250beex484x698.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Barium enema: </strong>Used to assess the large intestine (25% BaSO4 is used)</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/d548af6e9d5a4b16880804dd8b4d6cfax387x450.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(1586, 'Medicine', 'PM2040. Conditions commonly associated with Klippel-Feil syndrome', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<table>\n<tbody>\n<tr>\n<td><strong>Condition&nbsp;</strong>&nbsp; &nbsp;</td>\n<td><strong>Description</strong>&nbsp; &nbsp;&nbsp;</td>\n</tr>\n<tr>\n<td><strong>Scoliosis&nbsp;</strong></td>\n<td>\n<p><strong>Most frequent</strong>&nbsp;orthopaedic complication</p>\n</td>\n</tr>\n<tr>\n<td><strong>Renal anomalies&nbsp;</strong></td>\n<td>\n<p>Usually asymptomatic</p>\n</td>\n</tr>\n<tr>\n<td><strong>Cardiovascular anomalies&nbsp;</strong></td>\n<td>\n<p>Ventricular septal defect most common&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td><strong>Deafness&nbsp;</strong></td>\n<td>\n<p>Obtain audiometric testing</p>\n</td>\n</tr>\n<tr>\n<td><strong>Respiratory anomalies&nbsp;</strong></td>\n<td>\n<p>Restriction of lung function</p>\n</td>\n</tr>\n<tr>\n<td><strong>Sprengel deformity&nbsp;</strong></td>\n<td>\n<p>High placed scapula. Affects appearance and shoulder motion</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1587, 'Medicine', 'PM1233. Indications of Plasma exchange Therapy', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Speciality</strong></p>\n</td>\n<td>\n<p><strong>Condition</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Neurology</p>\n</td>\n<td>\n<p>Acute Guillain Barre syndrome</p>\n<p>Chronic inflammatory demyelinating polyneuropathy</p>\n<p>Myasthenia gravis</p>\n<p>Polyneuropathy associated with paraproteinaemias</p>\n<p>PANDAS</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Hematology</p>\n</td>\n<td>\n<p>Thrombotic thrombocytopenic purpura</p>\n<p>Atypical hemolytic-uraemic syndrome</p>\n<p>Hyperviscosity syndromes</p>\n<p>Severe/symptomatic cryoglobulinemia</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Renal</p>\n</td>\n<td>\n<p>Goodpasture’s syndrome</p>\n<p>Antineutrophil cytoplasmic antibody (ANCA)-associated rapidly progressive glomerulonephritis</p>\n<p>Recurrent focal segmental glomerular sclerosis</p>\n<p>Antibody-mediated renal transplant rejection</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Metabolic</p>\n</td>\n<td>\n<p>Familial hypercholesterolemia</p>\n<p>Fulminant Wilson’s disease</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1588, 'Medicine', 'PM0133. Important dates and events', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>Some important days and dates:</p>\n<table border=\1\>\n<tbody>\n<tr>\n<td><strong>Days</strong></td>\n<td><strong>Dates</strong></td>\n</tr>\n<tr>\n<td>World Leprosy eradication day</td>\n<td>January last Sunday</td>\n</tr>\n<tr>\n<td>World cancer day</td>\n<td>&nbsp;February 4</td>\n</tr>\n<tr>\n<td>&nbsp;International day of Zero Tolerance to Female Genital Mutilation</td>\n<td>&nbsp;February 6</td>\n</tr>\n<tr>\n<td>National deworming day (2019)</td>\n<td>\n<p>February 8 (first round) Feb 14 (Mop-up), August 8.</p>\n</td>\n</tr>\n<tr>\n<td>Sexual reproductive health awareness day</td>\n<td>February 12</td>\n</tr>\n<tr>\n<td>World Glaucoma week</td>\n<td>March 12 – 18</td>\n</tr>\n<tr>\n<td>World kidney day</td>\n<td>March Second Thursday</td>\n</tr>\n<tr>\n<td>Measles immunization day</td>\n<td>March 16</td>\n</tr>\n<tr>\n<td>World Oral health day</td>\n<td>March 20</td>\n</tr>\n<tr>\n<td>World Downs Syndrome day</td>\n<td>March 21</td>\n</tr>\n<tr>\n<td>World Water day</td>\n<td>March 22</td>\n</tr>\n<tr>\n<td><strong>World TB day</strong></td>\n<td>March 24</td>\n</tr>\n<tr>\n<td>World autism awareness day</td>\n<td>April 2</td>\n</tr>\n<tr>\n<td><strong>World health day</strong></td>\n<td>April 7</td>\n</tr>\n<tr>\n<td>World Hemophilia day</td>\n<td>April 17</td>\n</tr>\n<tr>\n<td>World Liver day</td>\n<td>April 19</td>\n</tr>\n<tr>\n<td><strong>World Malaria day</strong></td>\n<td>April 25</td>\n</tr>\n<tr>\n<td><strong>World Immunization week</strong></td>\n<td>April 24 - 30</td>\n</tr>\n<tr>\n<td>World Asthma day</td>\n<td>May first Tuesday</td>\n</tr>\n<tr>\n<td>World Red Cross day, World Thalassemia day</td>\n<td>May 8</td>\n</tr>\n<tr>\n<td>World Chronic Fatigue syndrome awareness day</td>\n<td>May 12</td>\n</tr>\n<tr>\n<td>National Dengue day