(1589, 'Medicine,', 'PM1599. Causes of Hypercalcemia', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><ul>\n<li><strong>Parathyroid</strong>-Related\n<ul>\n<li>Primary hyperparathyroidism\n<ul>\n<li>Adenoma(s)</li>\n<li>Multiple endocrine neoplasia</li>\n<li>Carcinoma</li>\n</ul>\n</li>\n<li>Lithium therapy</li>\n<li>Familial hypocalciuric hypercalcemia</li>\n</ul>\n</li>\n<li><strong>Malignancy</strong>-Related\n<ul>\n<li>Solid tumour with metastases (breast)</li>\n<li>Solid tumour with humoral mediation of hypercalcemia (lung, kidney)</li>\n<li>Hematologic malignancies (multiple myeloma, lymphoma, leukemia)</li>\n</ul>\n</li>\n<li><strong>Vitamin</strong> <strong>D</strong>–Related\n<ul>\n<li>Vitamin D intoxication</li>\n<li>↑ 1,25(OH)2D :\n<ul>\n<li>Sarcoidosis and other granulomatous diseases</li>\n<li>Impaired 1,25(OH)2D metabolism due to 24-hydroxylase deficiency</li>\n</ul>\n</li>\n<li>Associated with <strong>high bone turnover</strong>\n<ul>\n<li>Hyperthyroidism</li>\n<li>Immobilization</li>\n<li>Thiazides</li>\n<li>Vitamin A intoxication</li>\n<li>Fat necrosis</li>\n</ul>\n</li>\n<li>Associated with <strong>renal failure</strong>\n<ul>\n<li>Severe secondary hyperparathyroidism</li>\n<li>Aluminium intoxication</li>\n<li>Milk-alkali syndrome</li>\n</ul>\n</li>\n</ul>\n</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(1590, 'Medicine', 'PM1589. Chronic Complications of Type2 DM', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td colspan=\2\ width=\414\>\n<p><strong>Vascular</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\206\>\n<p><strong>Microvascular</strong></p>\n</td>\n<td width=\208\>\n<p><strong>Macrovascular</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\206\>\n<p>Retinopathy</p>\n<p>Macular edema</p>\n</td>\n<td width=\208\>\n<p>Coronary heart disease</p>\n</td>\n</tr>\n<tr>\n<td width=\206\>\n<p>Nephropathy</p>\n</td>\n<td width=\208\>\n<p>Peripheral arterial disease</p>\n</td>\n</tr>\n<tr>\n<td width=\206\>\n<p>Neuropathy</p>\n</td>\n<td width=\208\>\n<p>Cerebrovascular disease</p>\n</td>\n</tr>\n<tr>\n<td colspan=\2\ width=\414\>\n<p><strong>Non Vascular</strong></p>\n</td>\n</tr>\n<tr>\n<td colspan=\2\ width=\414\>\n<p>Gastrointestinal (gastroparesis, diarrhea)</p>\n</td>\n</tr>\n<tr>\n<td colspan=\2\ width=\414\>\n<p>Genitourinary (uropathy/sexual dysfunction)</p>\n</td>\n</tr>\n<tr>\n<td colspan=\2\ width=\414\>\n<p>Dermatologic</p>\n</td>\n</tr>\n<tr>\n<td colspan=\2\ width=\414\>\n<p>Infectious</p>\n</td>\n</tr>\n<tr>\n<td colspan=\2\ width=\414\>\n<p>Cataracts</p>\n</td>\n</tr>\n<tr>\n<td colspan=\2\ width=\414\>\n<p>Glaucoma</p>\n</td>\n</tr>\n<tr>\n<td colspan=\2\ width=\414\>\n<p>Cheiroarthropathy</p>\n</td>\n</tr>\n<tr>\n<td colspan=\2\ width=\414\>\n<p>Periodontal disease</p>\n</td>\n</tr>\n<tr>\n<td colspan=\2\ width=\414\>\n<p>Hearing loss</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->');
