(1742, 'Medicine', 'PM1999. Prognostic factors in ALL', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>Prognostic factors in ALL</p>\n<table>\n<tbody>\n<tr>\n<td><strong>&nbsp;Prognostic factors</strong></td>\n<td><strong>Good</strong></td>\n<td><strong>Bad</strong></td>\n</tr>\n<tr>\n<td>Age</td>\n<td>2-9 years</td>\n<td>&lt;1 year or &gt;10 years</td>\n</tr>\n<tr>\n<td>Sex</td>\n<td>Female</td>\n<td>Male</td>\n</tr>\n<tr>\n<td>Race</td>\n<td>White</td>\n<td>Black</td>\n</tr>\n<tr>\n<td>CNS leukemia</td>\n<td>Absent</td>\n<td>Overt</td>\n</tr>\n<tr>\n<td>Hepatosplenomegaly, Lymphadenopathy</td>\n<td>Absent</td>\n<td>Massive</td>\n</tr>\n<tr>\n<td>Mediastinal mass</td>\n<td>Absent</td>\n<td>Present</td>\n</tr>\n<tr>\n<td>Testicular involvement</td>\n<td>Absent</td>\n<td>Present</td>\n</tr>\n<tr>\n<td>WBC count</td>\n<td>&lt;50,000/mm<sup>3</sup></td>\n<td>&gt;50,000/mm<sup>3</sup></td>\n</tr>\n<tr>\n<td>FAB Type</td>\n<td>L1</td>\n<td>L2,L3</td>\n</tr>\n<tr>\n<td>Cytogenetics</td>\n<td>Hyperdiploidy</td>\n<td>Hypodiploidy</td>\n</tr>\n<tr>\n<td>Immunophenotype</td>\n<td>B-cell, early pre-B</td>\n<td>T cell</td>\n</tr>\n<tr>\n<td>Translocation</td>\n<td>T(12,21),Notch 1,HOX 11</td>\n<td>t(9,22),t(4,11)</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1743, 'Medicine', 'PM1964. Blood components', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Component</strong></td>\n<td><strong>Volume&nbsp; &nbsp; &nbsp;(1 unit)</strong></td>\n<td><strong>Content</strong></td>\n<td><strong>Clinical response, increase in&nbsp;</strong></td>\n<td><strong>Storage temperature</strong></td>\n<td><strong>Shelf-life</strong></td>\n</tr>\n<tr>\n<td>Whole blood</td>\n<td>450 ml</td>\n<td>RBCs, platelets, plasma</td>\n<td>Hb and plasma volume</td>\n<td>&nbsp;</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>Packed red blood cells (PRBCs)</td>\n<td>300-350 ml</td>\n<td>RBCs</td>\n<td>\n<p>Hb by 1 g/dl</p>\n<p>Hematocrit by 3%</p>\n</td>\n<td>\n<p>1° to 6°C</p>\n</td>\n<td>\n<p>ACD/CPD-21 days, CPD-A-35 days, additive-42 days</p>\n</td>\n</tr>\n<tr>\n<td>Random donor platelets (RDP)</td>\n<td>50-70 ml</td>\n<td>Platelets</td>\n<td>platelet count by 5000-10,000/µL</td>\n<td rowspan=\2\>20 to 24°C</td>\n<td rowspan=\2\>5 days</td>\n</tr>\n<tr>\n<td>Single donor apheretic platelets (SDP*)</td>\n<td>200-400 ml</td>\n<td>Platelets</td>\n<td>platelet count by 30,000/µL</td>\n</tr>\n<tr>\n<td>Fresh frozen plasma (FFP)</td>\n<td>200-250 ml</td>\n<td>Coagulation factors, protein C, protein S, anti thrombin</td>\n<td>coagulation factors by 2%</td>\n<td>-18 to -30°C</td>\n<td>1 year</td>\n</tr>\n<tr>\n<td>Cryoprecipitate</td>\n<td>10-15 ml</td>\n<td>fibrinogen, factor VIII, VWF</td>\n<td>80 IU Factor VIII</td>\n<td>-30 °C</td>\n<td>1 year</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.ne