(1932, 'Orthopaedics', 'PM2164. Factors that impair bone healing and cause non-union', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Biological etiologies:</strong></p>\n<ul>\n<li><strong>Local -</strong>\n<ul>\n<li>Excessive soft-tissue stripping</li>\n<li>Bone loss</li>\n<li>Vascular injury</li>\n<li>Radiation</li>\n<li>Infection</li>\n</ul>\n</li>\n<li><strong>Systemic -</strong>&nbsp;\n<ul>\n<li>Age</li>\n<li>Chronic diseases</li>\n<li>Metabolic or endocrine abnormalities (Vit D def)</li>\n<li>Malnutrition</li>\n<li>Smoking</li>\n</ul>\n</li>\n</ul>\n<p><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Mechanical etiologies:</strong></p>\n<ul>\n<li><strong>Malreduction -</strong>\n<ul>\n<li>Malposition</li>\n<li>Malalignment</li>\n<li>Distraction</li>\n</ul>\n</li>\n<li><strong>Inappropriate stabilization -</strong>\n<ul>\n<li>Insufficient or&nbsp;too rigid fixation</li>\n<li>Inappropriate implant choice or position</li>\n</ul>\n</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(1933, 'Orthopaedics', 'PM2159. Orthopedic trauma and associated vascular injuries', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Injuries</strong></td>\n<td><strong>Vessel involved</strong></td>\n</tr>\n<tr>\n<td colspan=\2\><strong>Dislocations:</strong></td>\n</tr>\n<tr>\n<td>Shoulder dislocation</td>\n<td>Axillary</td>\n</tr>\n<tr>\n<td>Posterior dislocation of the elbow</td>\n<td>Brachial</td>\n</tr>\n<tr>\n<td>Hip dislocation</td>\n<td>Femoral</td>\n</tr>\n<tr>\n<td>Knee dislocation</td>\n<td>Popliteal</td>\n</tr>\n<tr>\n<td colspan=\2\><strong>Fractures:</strong></td>\n</tr>\n<tr>\n<td>Fracture proximal humerus</td>\n<td>Axillary</td>\n</tr>\n<tr>\n<td>Supracondylar fracture humerus</td>\n<td>Brachial</td>\n</tr>\n<tr>\n<td>Fracture both bones forearm</td>\n<td>Anterior interosseous artery</td>\n</tr>\n<tr>\n<td>First rib fracture</td>\n<td>Subclavian</td>\n</tr>\n<tr>\n<td>Pelvic fracture</td>\n<td>Presacral and internal iliac</td>\n</tr>\n<tr>\n<td>Fracture femur</td>\n<td>Femoral</td>\n</tr>\n<tr>\n<td>Supracondylar fracture femur</td>\n<td>Popliteal</td>\n</tr>\n<tr>\n<td>Proximal tibial fracture</td>\n<td>Popliteal or its branches</td>\n</tr>\n<tr>\n<td>Ankle injuries</td>\n<td>Posterior tibial</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1934, 'Orthopaedics', 'PM2158. Fractures/dislocations and associated nerve injuries', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Fracture/Dislocation</strong></td>\n<td><strong>Nerve injured</strong></td>\n</tr>\n<tr>\n<td>Fracture clavicle</td>\n<td>Brachial plexus</td>\n</tr>\n<tr>\n<td>Anterior or inferior shoulder dislocation</td>\n<td>Axillary (circumflex humeral) nerve</td>\n</tr>\n<tr>\n<td>Proximal humerus fracture</td>\n<td>Axillary nerve</td>\n</tr>\n<tr>\n<td>Shaft of humerus fracture</td>\n<td>Radial nerve</td>\n</tr>\n<tr>\n<td>Supracondylar fracture humerus&nbsp;</td>\n<td>Anterior interosseous nerve &gt; Median nerve &gt; Radial nerve</td>\n</tr>\n<tr>\n<td>Medial condyle of humerus&nbsp;</td>\n<td>Ulnar nerve</td>\n</tr>\n<tr>\n<td>Posterior dislocation elbow</td>\n<td>Ulnar nerve &gt;Median nerve</td>\n</tr>\n<tr>\n<td>Monteggia fracture</td>\n<td>Posterior interosseous nerve</td>\n</tr>\n<tr>\n<td>Volkman’s ischemic contracture</td>\n<td>Anterior interosseous nerve</td>\n</tr>\n<tr>\n<td>Hook of hamate fracture</td>\n<td>Deep branch of the ulnar nerve</td>\n</tr>\n<tr>\n<td>Lunate dislocation<strong>&nbsp;</strong></td>\n<td>Median nerve</td>\n</tr>\n<tr>\n<td>Wrist injury</td>\n<td>Median nerve</td>\n</tr>\n<tr>\n<td>Posterior hip dislocation</td>\n<td>Sciatic nerve</td>\n</tr>\n<tr>\n<td>Anterior dislocation of hip and shaft femur fracture</td>\n<td>Femoral nerve</td>\n</tr>\n<tr>\n<td>Knee dislocation</td>\n<td>Common peroneal nerve</td>\n</tr>\n<tr>\n<td>Proximal tibial fractures and ankle injury</td>\n<td>Posterior tibial nerve</td>\n</tr>\n<tr>\n<td>Fracture neck of the fibula</td>\n<td>Lateral popliteal nerve (common peroneal nerve)</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1935, 'Orthopaedics', 'PM2011. Plaster casts and their uses:', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>&nbsp; <strong>Name of cast</strong>&nbsp;&nbsp;</td>\n<td><strong>Usage</strong></td>\n</tr>\n<tr>\n<td>\n<p><strong>Minerva cast</strong></p>\n</td>\n<td>Cervical and upper thoracic spine disease</td>\n</tr>\n<tr>\n<td>\n<p><strong>Risser’s cast</strong></p>\n</td>\n<td>Scoliosis&nbsp;</td>\n</tr>\n<tr>\n<td>\n<p><strong>Turn-buckle cast</strong></p>\n</td>\n<td>Scoliosis&nbsp;</td>\n</tr>\n<tr>\n<td>\n<p><strong>Shoulder spica</strong></p>\n</td>\n<td>Shoulder immobilization&nbsp;</td>\n</tr>\n<tr>\n<td>\n<p><strong>U-slab/hanging cast</strong></p>\n</td>\n<td>Fracture of the humerus&nbsp;</td>\n</tr>\n<tr>\n<td>\n<p><strong>Hip spica</strong></p>\n</td>\n<td>Fracture of the femur</td>\n</tr>\n<tr>\n<td>\n<p><strong>Patellar tendon bearing cast</strong></p>\n</td>\n<td>Fracture of tibia</td>\n</tr>\n<tr>\n<td>\n<p><strong>Colle’s cast (handshaking)</strong></p>\n</td>\n<td>Fracture lower end radius</td>\n</tr>\n<tr>\n<td>\n<p><strong>Bachelor cast</strong></p>\n</td>\n<td>Developmental Dysplasia of Hip</td>\n</tr>\n<tr>\n<td>\n<p><strong>Glass holding cast</strong></p>\n</td>\n<td>Fracture scaphoid</td>\n</tr>\n<tr>\n<td>\n<p><strong>Burkhalter cast</strong></p>\n</td>\n<td>Metacarpal or phalangeal fractures</td>\n</tr>\n<tr>\n<td>\n<p><strong>James slab</strong></p>\n</td>\n<td>Metacarpal and phalangeal fracture&nbsp;</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/2ddfb024ce794679a717efd532820487x1280x4597.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(1936, 'Orthopaedics', 'PM1997. Gustilo and Anderson classification for open fracture', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td colspan=\5\><strong>Gustilo-Andersons classification for open injuries</strong>&nbsp;&nbsp;&nbsp;&nbsp;</td>\n</tr>\n<tr>\n<td><strong>Type</strong></td>\n<td><strong>Wound</strong></td>\n<td><strong>Level of contamination</strong></td>\n<td><strong>Soft tissue injury</strong></td>\n<td><strong>Bone injury</strong></td>\n</tr>\n<tr>\n<td><strong>I</strong></td>\n<td>&lt;1 cm</td>\n<td>Clean</td>\n<td>Minimal</td>\n<td>Simple, minimal comminution</td>\n</tr>\n<tr>\n<td><strong>II</strong></td>\n<td>&gt;1 cm</td>\n<td>Moderate</td>\n<td>Moderate; some muscle damage</td>\n<td>Moderate comminution</td>\n</tr>\n<tr>\n<td><strong>IIIA</strong></td>\n<td>usually &gt;10 cm</td>\n<td>High</td>\n<td>Severe with crushing</td>\n<td>Usually comminuted; soft tissue coverage of bone possible</td>\n</tr>\n<tr>\n<td><strong>IIIB</strong></td>\n<td>usually &gt;10 cm</td>\n<td>High</td>\n<td>Very severe loss of cover</td>\n<td>Bone cover poor; usually requires soft tissue reconstructive surgery</td>\n</tr>\n<tr>\n<td><strong>IIIC</strong></td>\n<td>usually &gt;10 cm</td>\n<td>High</td>\n<td>Very severe loss of cover; <strong>vascular injury</strong> requiring repair</td>\n<td>Bone cover poor; usually requires soft tissue reconstructive surgery</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><span><br>Management according to Gustilo-Andersons classification:</span></p>\n<table>\n<tbody>\n<tr>\n<td><strong>Types</strong></td>\n<td><strong>Methods of fixation</strong></td>\n</tr>\n<tr>\n<td><strong>Grade &nbsp;I &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong></td>\n<td>A technique that is suitable for closed fracture management i.e, debridement and internal fixation by intramedullary nail and plate&nbsp;&nbsp;</td>\n</tr>\n<tr>\n<td><strong>Grade II, IIIA</strong></td>\n<td>\n<p>Within 6hr same as grade I and after 6hr same as IIIB, IIIC</p>\n</td>\n</tr>\n<tr>\n<td><strong>Grade IIIB, IIIC</strong></td>\n<td>\n<p>Debridement and external fixation</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1937, 'Orthopaedics', 'PM0704. Types of Salter–Harris fractures- Mnemonic', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>There are <strong>nine types</strong> of Salter-Harris fractures. Among these, types I to V are more common.</p>\n<p>The mnemonic \<strong>SALTER</strong>\ can be used to help remember the first five types.</p>\n<table>\n<tbody>\n<tr>\n<td><strong>TYPE&nbsp;</strong></td>\n<td><strong>MNEMONIC</strong></td>\n<td><strong>DESCRIPTION</strong></td>\n</tr>\n<tr>\n<td><strong>Type I </strong></td>\n<td><strong> S</strong> = Slip/straight across</td>\n<td>Transverse fracture through the growth plate</td>\n</tr>\n<tr>\n<td><strong>Type II</strong></td>\n<td><strong>A</strong> = Away</td>\n<td>The fracture lies away from the joint. A fracture through the growth plate and the metaphysis, sparing the epiphysis</td>\n</tr>\n<tr>\n<td><strong>Type III </strong></td>\n<td><strong>L</strong> = Lower</td>\n<td>The fracture is below the physis in the epiphysis.</td>\n</tr>\n<tr>\n<td><strong>Type IV</strong></td>\n<td><strong>TE</strong> = Through Everything</td>\n<td>&nbsp;A fracture through all three elements of the bone, the growth plate, metaphysis, and epiphysis</td>\n</tr>\n<tr>\n<td><strong>Type V </strong></td>\n<td><strong>R</strong> = Rammed (crushed)</td>\n<td>&nbsp;A compression fracture of the growth plate</td>\n</tr>\n</tbody>\n</table>\n<p>Rarer types VI to IX have been added subsequently.</p>\n<div class=\topbullet\>\n<ul>\n<li><strong>Type VI</strong>&nbsp;- Injury to the perichondral structures</li>\n<li><strong>Type VII</strong>&nbsp;- Isolated injury to the epiphyseal plate</li>\n<li><strong>Type VIII</strong>&nbsp;- Isolated injury to the metaphysis, with a potential injury related to endochondral ossification</li>\n<li><strong>Type IX</strong>&nbsp;- Injury to the periosteum that may interfere with membranous growth</li>\n</ul>\n</div>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/70d16f2f00854a4394a72ef62cee9855x1280x2934.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(1938, 'Orthopaedics', 'PM0655. Pathological fracture', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Pathological f</strong><span class=\fontstyle0\><strong>ractures</strong> occur with normal stresses in the bones that have been weakened by a change in its structure or through a lytic lesion.</span></p>\n<p><strong>Factors suggestive of a pathologic fracture:</strong></p>\n<ul>\n<li>Fracture after minor trauma</li>\n<li>Pain at the site before the fracture</li>\n<li>Multiple recent fractures</li>\n<li>History of primary malignancy</li>\n</ul>\n<p><strong>Causes:</strong></p>\n<p><strong>1. Generalized bone disease:</strong>&nbsp;</p>\n<ul>\n<li>Osteoporosis</li>\n<li>Metabolic bone diseases</li>\n<li>Osteogenesis imperfecta</li>\n<li>Pagets disease</li>\n<li>Polyostotic fibrous dysplasia</li>\n<li>Myelomatosis</li>\n</ul>\n<p><strong>2. Local benign conditions:</strong></p>\n<ul>\n<li>Chronic infection</li>\n<li>Solitary bone cyst</li>\n<li>Aneurysmal bone cyst</li>\n<li>Chondroma</li>\n</ul>\n<p><strong>3. Primary malignant tumors:</strong></p>\n<ul>\n<li>Chondrosarcoma</li>\n<li>Osteosarcoma</li>\n<li>Ewings tumor</li>\n</ul>\n<p><strong>4. Metastatic tumors</strong></p>\n<ul>\n<li>Mets from breast, lung, kidney, thyroid, colon and prostate</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(1939, 'Orthopaedics', 'PM0650. Torticollis', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/546443ca02414588bac937884f106e6bx1280x1386.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Torticollis</strong> is a cervical deformity in which the head is rotated and tilted towards one side, with some lateral rotation.</p>\n<p><strong>Causes:</strong></p>\n<ul>\n<li class=\p1\><strong>Congenital torticollis (infantile torticollis)- </strong>\n<ul>\n<li class=\p1\>More common</li>\n<li class=\p1\>Caused due to:\n<ul>\n<li class=\p1\>Malposition in utero</li>\n<li class=\p1\>Birth injury in difficult labour or breech delivery</li>\n</ul>\n</li>\n<li class=\p1\>It is due to sternocleidomastoid (SCM) muscle fibrosis</li>\n</ul>\n</li>\n<li class=\p1\><strong>Secondary torticollis</strong>&nbsp;is an acquired condition and can result due to infections, tumours, inflammatory disorders or trauma.</li>\n</ul>\n<p><strong><span class=\fontstyle0\>Clinical features:</span></strong></p>\n<ul>\n<li class=\p1\>Head tilt towards the affected side,&nbsp;face rotated towards the contralateral shoulder (<strong>cock robin position</strong>).</li>\n<li class=\p1\>A palpable nodule in the affected sternocleidomastoid muscle in the first few weeks after birth, disappearing within a few months.</li>\n<li class=\p1\>Asymmetry of the face (hemihypoplasia) and flattening of one side of the skull (plagiocephaly) may be present.&nbsp;</li>\n</ul>\n<p><strong>Management:</strong></p>\n<ul>\n<li>Conservative treatment:&nbsp;Stretching of the SCM muscle by&nbsp;manipulating the infant’s head manually.&nbsp;</li>\n<li>Operative management: Release of SCM muscle<strong>. </strong>It is delayed until school age.&nbsp;</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(1940, 'Orthopaedics', 'PM2325. Methods of reduction of shoulder dislocation', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Kochers technique</strong> - TEAM (<strong>T</strong>raction <strong>→</strong>&nbsp;<strong>E</strong>xternal Rotation →&nbsp;<strong>A</strong>dduction →&nbsp;<strong>M</strong>edial Rotation)&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/25d97ccf3047455eb3bec691261b5711x510x568.GIF\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Hippocratic method&nbsp;</strong>- The doctor applies a firm and steady pull on the semi-abducted arm of the patient by keeping his foot on the axilla against the chest wall. The foot is used as the fulcrum.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/563adb46a7004d0a994680906a860148x1280x1055.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Stimsons gravity technique: </strong>Patient is left prone and weight is attached to the elbow or wrist on the affected side. Gravity stretches the muscle and reduction occurs in 15-20 minutes.&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/cf4dba09b0144793bb5cbeccef5b9e82x1280x1817.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(1941, 'Orthopaedics', 'PM2324. Common dislocations at different joints', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td colspan=\2\><strong>&nbsp;Common dislocation at different joints</strong></td>\n</tr>\n<tr>\n<td><strong>Shoulder</strong></td>\n<td>Anterior&nbsp;</td>\n</tr>\n<tr>\n<td><strong>Elbow</strong></td>\n<td>Posterior and posterolateral</td>\n</tr>\n<tr>\n<td><strong>Wrist</strong></td>\n<td>Lunate</td>\n</tr>\n<tr>\n<td><strong>Spine</strong></td>\n<td>Cervical Spine (anterior C5 over C6)</td>\n</tr>\n<tr>\n<td><strong>Hip</strong></td>\n<td>Posterior</td>\n</tr>\n<tr>\n<td><strong>Knee</strong></td>\n<td>Posterior</td>\n</tr>\n<tr>\n<td><strong>Patella</strong></td>\n<td>Lateral</td>\n</tr>\n<tr>\n<td><strong>Ankle</strong></td>\n<td>Anterolateral</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1942, 'Orthopaedics', 'PM2203. Important tests and signs to assess the function of rotator cuff muscles', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Tests</strong></td>\n<td><strong>Muscles tested</strong></td>\n</tr>\n<tr>\n<td>\n<p>Empty can test</p>\n</td>\n<td>&nbsp;Supraspinatus</td>\n</tr>\n<tr>\n<td>Resisted external rotation</td>\n<td>&nbsp;Infraspinatus</td>\n</tr>\n<tr>\n<td>\n<p>Lift-off test</p>\n<p>Belly press test</p>\n</td>\n<td>&nbsp;Subscapularis</td>\n</tr>\n<tr>\n<td>\n<p>Abduction paradox</p>\n<p>Drop arm sign</p>\n</td>\n<td>&nbsp;Seen in the complete&nbsp; &nbsp;rupture of rotator cuff</td>\n</tr>\n<tr>\n<td>&nbsp;Lag sign</td>\n<td>&nbsp;Seen in tear of&nbsp; &nbsp;supraspinatus or&nbsp; &nbsp;infraspinatus</td>\n</tr>\n<tr>\n<td>&nbsp;Drop sign</td>\n<td>&nbsp;Seen in the tear in the&nbsp; &nbsp;infraspinatus and the&nbsp; &nbsp;posterior part of the&nbsp; &nbsp;rotator cuff</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1943, 'Orthopaedics', 'PM2125. Mechanisms of Injury around the elbow', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>&nbsp; &nbsp; &nbsp;</strong></p>\n<table border=\1\>\n<tbody>\n<tr>\n<td>\n<p><strong>Mechanism&nbsp;of injury</strong></p>\n</td>\n<td><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Injury</strong></td>\n</tr>\n<tr>\n<td><strong>Valgus injury</strong></td>\n<td>\n<ul>\n<li>Fracture head and neck of the radius</li>\n<li>Fracture of medial epicondyle of humerus</li>\n<li>Ulnar collateral ligament injury</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td><strong>Varus injury</strong></td>\n<td>\n<ul>\n<li>Fracture of lateral epicondyle of humerus</li>\n<li>Radial collateral ligament injury</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td><strong>Hyperextension injury</strong></td>\n<td>\n<ul>\n<li>Supracondylar fracture humerus</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td><strong>Axial force</strong></td>\n<td>\n<ul>\n<li>Fracture of capitulum</li>\n<li>Dislocation of the elbow</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td><strong>Fall on the point of the elbow</strong></td>\n<td>\n<ul>\n<li>Olecranon fracture</li>\n<li>Intercondylar fractures of the humerus</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/a12ca8cd3a714175a8b833f27b1767e8x1280x2560.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(1944, 'Orthopaedics', 'PM2052. Important conditions of the elbow:', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><span>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<strong>Condition&nbsp;</strong></span></td>\n<td><span>&nbsp; <strong>Description</strong> &nbsp; &nbsp; &nbsp;</span></td>\n</tr>\n<tr>\n<td>\n<p><span><strong>Tennis elbow</strong></span></p>\n</td>\n<td>\n<p><span>Lateral epicondylitis involving common extensor origin</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><span><strong>Golfers elbow</strong></span></p>\n</td>\n<td>\n<p><span>Medial epicondylitis involving the common flexor-pronator origin</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><span><strong>Baseball pitchers elbow</strong></span></p>\n</td>\n<td>\n<p><span>Damage to the bones or soft tissue attached around the elbow d</span><span>ue to repeated vigorous throwing activities. Hypertrophy of the lower humerus and incongruity of the joint, or loose body formation and osteoarthritis</span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><span><strong>Little leaguers elbow</strong></span></p>\n</td>\n<td><span>Partial avulsion of the medial epicondyle</span></td>\n</tr>\n<tr>\n<td>\n<p><span><strong>Javelin throwers elbow</strong></span></p>\n</td>\n<td><span>Avulsion of the tip of olecranon due to overarm action</span></td>\n</tr>\n<tr>\n<td>\n<p><span><strong>Students elbow/Miners elbow</strong></span></p>\n</td>\n<td><span>Olecranon bursitis</span></td>\n</tr>\n<tr>\n<td>\n<p><span><strong>Pulled elbow/Nurse maids elbow</strong></span></p>\n</td>\n<td><span>Downward dislocation of the radial head from the annular ligament</span></td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1945, 'Orthopaedics', 'PM2033. Important Injuries of the Hand', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Gamekeepers thumb:&nbsp;</strong>It is an injury of the thumb leading to avulsion, rupture or damage of the <strong>ulnar collateral ligament</strong>. It is commonly seen in gamekeepers and following a fall on an outstretched hand.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/a2dcf1df7aa547569edb9b23b5b4fe4ex1280x1222.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><div class=\mod\>\n<div class=\qtR3Y\>&nbsp;</div>\n<div class=\qtR3Y\><span class=\Y0NH2b CLPzrc\><strong>Mallet finger</strong>/baseball finger: It is a disruption to the <strong>extensor tendon</strong>&nbsp;over the DIP joint. It occurs when a collision with a&nbsp;hard object (e.g., a ball) forces it to flex more than its normal range of motion.</span></div>\n</div>\n<div class=\g\>&nbsp;</div></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/e6f1cb40d29a4415833349642c8a605bx1280x844.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Jersey finger</strong>:&nbsp;It is an avulsion injury of the <strong>flexor digitorum profundus</strong> at the insertion of the base of the distal phalanx. The ring finger most commonly involved.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/9c2040ddd70745199f16395e93f78b58x1280x988.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Trigger finger</strong>: A <strong>nodule</strong> on the flexor tendon prevents it from moving along the A1 pulley. This causes the finger to be stuck in a flexed position.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/3deb8ddc550345bc883002661184a0d4x1280x1157.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Boxers fracture</strong>: It<span class=\Y0NH2b CLPzrc\>&nbsp;is a&nbsp;break&nbsp;in the<strong> neck of the metacarpal</strong>. It is caused by&nbsp;striking an object<strong>&nbsp;</strong>with a closed fist, as seen in boxing. The little finger is the most commonly affected.&nbsp;</span></p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/1ea28df97a50400b87329ff762655acax1280x1179.JPEG\><!---->\n      </div><!---->\n    </div><!---->');
INSERT INTO db_pearl_m (pid, subject, pearl_title, pearl_desc) VALUES
(1946, 'Orthopaedics', 'PM2016. Complications of Colle’s fracture:', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><ul>\n<li><strong>Joint stiffness:&nbsp;</strong>Finger stiffness is the <strong>most common</strong> complication. Wrist, elbow, and shoulder are the other joints that become stiff</li>\n<li><strong>Malunion</strong> is the <strong>second most common</strong> complication, and it leads to the \dinner fork\ deformity</li>\n<li>Sudeck’s osteodystrophy/reflex sympathetic dystrophy. Colle’s fracture is the <strong>most</strong> <strong>common</strong> cause of Sudeck’s dystrophy in upper limb</li>\n<li>Rupture of extensor pollicis longus tendon</li>\n<li>Carpal instability&nbsp;</li>\n<li>Triangular-fibro cartilage complex (TFCC) injury and subluxation of the inferior radio-ulnar joint</li>\n<li>Delayed union and non-union are <strong>extremely rare</strong></li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(1947, 'Orthopaedics', 'PM2015. Tension Band Wiring', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>A&nbsp;<strong>tension band wire</strong>&nbsp;is a form of internal fixation used to convert distraction forces into compression forces, promoting bone healing.</p>\n<p>It is used in:</p>\n<ul>\n<li>Fracture patella</li>\n<li>Fracture olecranon&nbsp;</li>\n<li>Fracture lateral malleolus</li>\n<li>Fracture greater trochanter</li>\n</ul>\n<p>The below image shows tension band wiring for a fracture olecranon</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/7ae08a62681e4cda9a000bc8e7bf2c71x1280x1994.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(1948, 'Orthopaedics', 'PM2014. Fractures of necessity', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>Fractures of necessity are those that require surgery for management. These include:</p>\n<ul>\n<li>Lateral condyle fracture humerus&nbsp;</li>\n<li>Displaced fractured of olecranon and patella</li>\n<li>Fracture neck of femur</li>\n<li>Galeazzi fracture-dislocation&nbsp;</li>\n<li>Monteggia fracture in adults</li>\n<li>Intra-articular fractures</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(1949, 'Orthopaedics', 'PM2013. Three point bony relationship in elbow:', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>The&nbsp;<strong>three bony points in the elbow</strong> are&nbsp;:</p>\n<ul>\n<li>Tip of the&nbsp;medial epicondyle</li>\n<li>Tip of the&nbsp;lateral epicondyle</li>\n<li>Olecranon</li>\n</ul>\n<p>When the <strong>elbow</strong> is in <strong>90 degrees of flexion</strong>, these points form an&nbsp;isosceles <strong>triangle</strong>. When the elbow is <strong>extended</strong>, they lie transversely in a<strong> straight line</strong>.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/c00eaa4fd8a34f2bbe183c84122fea27x1280x1409.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>The three-point bony relationship is <strong>maintained</strong> in the case of <strong>supracondylar fracture of the humerus</strong> as the fracture occurs above the level of these bony landmarks.</p>\n<p>Conditions where it is <strong>distorted</strong>:</p>\n<ul>\n<li>Fracture medial condyle and epicondyle</li>\n<li>Fracture lateral condyle and epicondyle</li>\n<li>Intercondylar fracture of humerus</li>\n<li>Fracture olecranon</li>\n<li>Elbow dislocation</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(1950, 'Orthopaedics', 'PM2009. Tests for anterior shoulder dislocation', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Bryant’s test:&nbsp;</strong>Anterior axillary fold is at a lower level on the dislocated side.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/f233d810676246f6a010e89a0a5a0bddx1280x1412.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Dugas test: </strong>The patient is made to place the hand on the opposite shoulder. The examiner then applies gentle pressure on the flexed elbow to make it touch the chest. In shoulder dislocation, the elbow cannot be made to touch the chest.&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/993e9fb89e9346deaebb6a42ea3bd38ex600x749.GIF\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Hamilton ruler test:&nbsp;</strong>In a&nbsp;normal shoulder, a ruler (scale) placed on the lateral side of the arm will not touch the lateral condyle of the humerus and the acromion at the same time.</p>\n<p>In anterior shoulder dislocation, the ruler will simultaneously touch the lateral condyle of the humerus and the acromion.&nbsp;</p>\n<p class=\p1\>&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/4b10dd054e604dde8a83037dfb07e741x1280x1526.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p class=\p1\>&nbsp;</p>\n<p class=\p1\><strong>Callway Test:&nbsp;</strong>The vertical circumference of the axilla is increased on the dislocated side as compared to the normal side.&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/be589ce9527049c88a0adac7b0f18aa4x1280x1412.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(1951, 'Orthopaedics', 'PM2001. Named classification systems for  fractures and dislocations', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><span><strong>Classification</strong></span></p>\n</td>\n<td><span><strong>Site</strong></span></td>\n</tr>\n<tr>\n<td>\n<p>Stanmore Instability Classification</p>\n</td>\n<td>Shoulder instability&nbsp;</td>\n</tr>\n<tr>\n<td>\n<p>Neer’s&nbsp;</p>\n</td>\n<td>Fracture proximal humerus&nbsp;</td>\n</tr>\n<tr>\n<td>\n<p>Gartland’s</p>\n</td>\n<td>Supracondylar fracture humerus&nbsp;</td>\n</tr>\n<tr>\n<td>\n<p>Milch’s</p>\n</td>\n<td>&nbsp;Fracture lateral condyle of humerus</td>\n</tr>\n<tr>\n<td>\n<p>Mayo classification</p>\n</td>\n<td>Olecranon fracture</td>\n</tr>\n<tr>\n<td>Denis and AO/ASIF</td>\n<td>Fracture spine</td>\n</tr>\n<tr>\n<td>Anderson and D’Alonzo</td>\n<td>Odontoid fracture</td>\n</tr>\n<tr>\n<td>\n<p>Ferguson and Allen classification</p>\n<p>Subaxial Cervical Spine Injury Classification (SLIC)</p>\n</td>\n<td>Fracture subaxial cervical spine (C3-C7)&nbsp;</td>\n</tr>\n<tr>\n<td>AO-Magerl classification</td>\n<td>Thoracolumbar spine fractures</td>\n</tr>\n<tr>\n<td>Wiltse−Newman classification</td>\n<td>Spondylolisthesis&nbsp;</td>\n</tr>\n<tr>\n<td>\n<p>Tile classification</p>\n<p>Young and Burgess classification</p>\n</td>\n<td>&nbsp;Pelvis</td>\n</tr>\n<tr>\n<td>\n<p>Judet,</p>\n<p>Letournel’s</p>\n</td>\n<td>Fracture acetabulum&nbsp;</td>\n</tr>\n<tr>\n<td>\n<p>Pauwel’s</p>\n<p>Garden’s</p>\n</td>\n<td>Fracture neck of femur</td>\n</tr>\n<tr>\n<td>\n<p>Pipkin’s</p>\n</td>\n<td>Fracture head of femur</td>\n</tr>\n<tr>\n<td>\n<p>Boyd &amp; Griffin</p>\n</td>\n<td>Fracture inter trochanter femur</td>\n</tr>\n<tr>\n<td>\n<p>Thompson and Epstein’s</p>\n</td>\n<td>Posterior dislocation of the hip</td>\n</tr>\n<tr>\n<td>Winquist and Hansen’s</td>\n<td>\n<p>Fracture shaft of femur</p>\n</td>\n</tr>\n<tr>\n<td>Hawkin’s</td>\n<td>\n<p>Fracture neck of talus</p>\n</td>\n</tr>\n<tr>\n<td>Bados&nbsp;</td>\n<td>\n<p>Monteggia fracture dislocations&nbsp;</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1952, 'Orthopaedics', 'PM2000. Test for evaluation of glenohumeral joint instability in recurrent dislocation.', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>1. Anterior instability:</strong></p>\n<p><strong>Anterior apprehension test:&nbsp;</strong>In general these manoeuvre mimics the positioning of subluxation or dislocation, application of a force that directs humeral head into abducation and external rotation results in patients apprehension that the joint will dislocate and the patient does not allow the test to be performed.</p>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/8fd48417fd4c49e283b0917a901b3180x227x214.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;Variations include:</p>\n<ul>\n<li><strong>fulcrum test&nbsp;</strong></li>\n<li><strong>crank test</strong></li>\n<li><strong>surprise test</strong></li>\n<li><strong>jobe relocation test</strong></li>\n</ul>\n<p>2. Posterior<strong> instability </strong>(here adduction and internal rotation force is applied):</p>\n<ul>\n<li><strong>Jerk (provocative)test</strong></li>\n<li><strong>posterior apprehension test</strong></li>\n<li><strong>posterior clunk test</strong></li>\n<li><strong>push-pull test.</strong></li>\n</ul>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/1c5af6fef4244360a6267c72e1332c09x600x298.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>3. Inferior stability test and sign </strong>(indicative of multidirectional instability):</p>\n<ul>\n<li><strong>Sulcus test:&nbsp;</strong>with the patient standing,grasp the arm and pull it down, if there is inferior instability a sulcus will appear between the acromian and the humeral head.</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/575b14ad1fc742d7ae6911628a99c864x200x249.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(1953, 'Orthopaedics', 'PM1418. Management of Pulseless Supracondylar Fracture', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/91f7ec643add4389971a527b6e830a13x1280x1571.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(1954, 'Orthopaedics', 'PM1336. Common X-Ray views', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Schullers view:</strong> Mastoid and temporal bone&nbsp;</p>\n<p><strong>Open mouth view:</strong> Odontoid</p>\n<p><strong>Waters view:</strong>&nbsp;Paranasal sinuses</p>\n<p><strong>Swimmers view:</strong> Cervicothoracic junction</p>\n<p><strong>Stryker notch view:</strong> Hillsachs lesion</p>\n<p><strong>West point axillary view</strong>: Bankarts lesion&nbsp;</p>\n<p><strong>Zanca view:</strong> Acromioclavicular joint</p>\n<p><strong>Oblique view:</strong> Scaphoid</p>\n<p><strong>Judet view:</strong> Acetabulum</p>\n<p><strong>Shentons arch:</strong> Pelvis</p>\n<p><strong>Von Rosen view</strong>: Developmental dysplasia of hip</p>\n<p><strong>Skyline view:</strong> Patella</p>\n<p><strong>Mortise view:</strong> talus&nbsp;</p>\n<p><strong>Brodens view: </strong>Subtalar joint</p>\n<p><strong>Kites view:</strong> CTEV</p></div><!---->\n      <!---->\n    </div><!---->'),
(1955, 'Orthopaedics', 'PM1099. Important angles used in the diagnosis of Orthopedic conditions', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Angles</strong></td>\n<td><strong>Conditions&nbsp;</strong></td>\n</tr>\n<tr>\n<td>Carrying angle</td>\n<td>Cubitus varus and cubitus valgus</td>\n</tr>\n<tr>\n<td>Baumanns angle</td>\n<td>Elbow fractures</td>\n</tr>\n<tr>\n<td>Cobbs angle</td>\n<td>Scoliosis</td>\n</tr>\n<tr>\n<td>\n<p>Alpha angle</p>\n<p>Beta angle</p>\n</td>\n<td>Developmental dysplasia of hip</td>\n</tr>\n<tr>\n<td>Southwick angle</td>\n<td>Slipped capital femoral epiphysis</td>\n</tr>\n<tr>\n<td>Neck shaft angle</td>\n<td>Coxa vara</td>\n</tr>\n<tr>\n<td>Q angle</td>\n<td>Patellar alignment</td>\n</tr>\n<tr>\n<td>Kites angle&nbsp;</td>\n<td>Congenital talipes equinus varus</td>\n</tr>\n<tr>\n<td>Mearys angle</td>\n<td>Pes cavus</td>\n</tr>\n<tr>\n<td>\n<p>Bohlers angle</p>\n<p>Gissanes angle</p>\n</td>\n<td>Fracture of calcaneum</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1956, 'Orthopaedics', 'PM0651. Medial and lateral epicondylitis', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Tennis elbow or Lateral epicondylitis</strong></p>\n<ul>\n<li>Pain is felt over or below the&nbsp;<strong>lateral epicondyle&nbsp;</strong>of the humerus</li>\n<li>Pain is aggravated by lifting movements</li>\n<li>It is common among tennis players and in those who perform repetitive wrist extension</li>\n<li><strong>Extensor carpi radialis brevis tendon</strong>&nbsp;is commonly involved</li>\n</ul>\n<p><strong>Tests:</strong></p>\n<ul>\n<li><strong>Cozens test: </strong>The patient is asked to pronate the forearm and make&nbsp;a tight fist.&nbsp; Wrist extension against resistance causes pain at the lateral epicondyle</li>\n<li><strong>Mills sign</strong>: Pain at the lateral condyle when the elbow is extended in pronation with a flexed wrist</li>\n<li><strong>Wringing test:</strong> The patient is asked to wring a towel. Pain is felt at the lateral epicondyle in the tennis elbow</li>\n<li><strong>Maudsleys test:&nbsp;</strong>Resisted extension of the middle finger produces pain at the lateral epicondyle</li>\n</ul>\n<p class=\p1\><strong>Treatment:</strong>&nbsp;</p>\n<ul>\n<li><strong>Conservative treatment: </strong>Activity modification and usage of a tennis elbow clasp are sufficient in most cases. Corticosteroid and local anesthetic injections into the tender area may reduce the pain, but is not curative</li>\n<li><strong>Operative treatment: </strong>Detachment of the common extensor muscle from the lateral epicondyle</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/8353b774c9954354acd313d2e446a07cx1200x1737.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Golfers elbow or Medial epicondylitis</strong></p>\n<ul>\n<li>Pain is felt over the medial epicondyle of the humerus where pronators have their origin</li>\n<li>Associated with ulnar nerve neuropathy</li>\n<li>Less common than tennis elbow</li>\n</ul>\n<p><strong>Tests:</strong></p>\n<ul>\n<li><strong>Reverse Cozen</strong>: The elbow is kept in slight flexion with the hand and wrist in supination. The examiner attempts to extend the elbow, while the patient is asked to resist this movement with active flexion. If pain is elicited at the medial epicondyle, it suggests golfer’s elbow</li>\n</ul>\n<p><strong>Treatment:</strong>&nbsp;</p>\n<ul>\n<li><strong>Conservative treatment:&nbsp;</strong>Activity modification and pain killers is sufficient in most cases</li>\n<li><strong>Operative treatment:&nbsp;</strong>&nbsp;Excision of the abnormal tendons at the flexor-pronator origin</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/87634f9e04f24b56a296745d43275e9bx1280x1066.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(1957, 'Orthopaedics', 'PM0649. Named fractures of the upper limb', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Colles fracture</strong>: Extra-articular fracture of the distal radius with dorsal angulation of the distal bone fragment.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/65a93223993740c495daa155786048ccx1280x1420.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Galeazzi fracture-dislocation</strong>: Fracture of the lower third of the radius and subluxation of the distal radio-ulnar joint.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/46eff51c35a54ce08988149eed8e8e96x1279x1421.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Monteggia fracture-dislocation</strong>:<strong>&nbsp;</strong>Fracture of the shaft of the ulna with dislocation of a proximal radio-ulnar joint.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/8cf2fa6ca59d47d79f5eed22bbb29c38x1280x2778.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Barton’s fracture</strong>: Intra-articular fracture of the distal radius. Bartons fracture can be dorsal or volar (reverse Bartons).</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/811391f8302644efb808c405e35235efx1280x1891.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Rolandos fracture</strong>: Intra-articular comminuted fracture of the base of the first metacarpal with a T or Y configuration.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/c3bec28a9c1a41b1bc3d923a6dd0cd96x1280x1316.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Smiths fracture</strong>: Reverse Colles fracture. Extra-articular fracture of the distal one-third of the radius with palmar or ventral displacement (Image below).</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/92ac8de8dac8499a8f31180cc8e83371x1280x1133.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>&nbsp;</p>\n<p><strong>Chauffeurs fracture or the Hutchinson fracture</strong>: Fracture of the radial styloid.</p>\n<p><strong>Essex–Lopresti lesion</strong>: Fracture of the radial head with disruption of the interosseous membrane and distal&nbsp;radial ulnar joint ligaments.</p>\n<p><strong>Nightstick fracture</strong>: Isolated fracture of radial or ulnar bone.&nbsp;&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(1958, 'Orthopaedics', 'PM2408. Anterior and posterior dislocation of the hip', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>&nbsp;</td>\n<td><strong>Anterior dislocation</strong></td>\n<td><strong>Posterior dislocation</strong></td>\n</tr>\n<tr>\n<td>Prevalence</td>\n<td>Less common</td>\n<td>Most common</td>\n</tr>\n<tr>\n<td>Type of injury</td>\n<td>Deceleration injury/ Fall from height</td>\n<td>Dashboard injury</td>\n</tr>\n<tr>\n<td>Attitude of the limb</td>\n<td>Flexion, abduction, external rotation (FABER)</td>\n<td>Flexion, adduction, internal rotation (FADIR)</td>\n</tr>\n<tr>\n<td>Length of the limb</td>\n<td>Lengthened</td>\n<td>Shortened</td>\n</tr>\n<tr>\n<td>Radiological findings</td>\n<td>\n<ul>\n<li>Femoral head may be seen inferior to the acetabulum</li>\n<li>Femoral head appears <strong>larger</strong> than normal&nbsp;</li>\n<li>Lesser trochanter is <strong>prominent</strong></li>\n</ul>\n</td>\n<td>\n<ul>\n<li>Femoral head is displaced superiorly and laterally</li>\n<li>Femoral head appears <strong>smaller</strong> than normal</li>\n<li>Lesser trochanter is not visualised</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td>Nerve injured</td>\n<td>&nbsp;Femoral nerve</td>\n<td>&nbsp;Sciatic nerve</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1959, 'Orthopaedics', 'PM2221. Named fractures of the lower limb', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td><strong>Fracture</strong></td>\n<td><strong>Location</strong></td>\n</tr>\n<tr>\n<td>Hoffa fracture</td>\n<td>Coronal fracture of femoral condyles</td>\n</tr>\n<tr>\n<td>Bumper fracture</td>\n<td>Comminuted fracture of lateral condyle of tibia&nbsp;</td>\n</tr>\n<tr>\n<td>Pilon fracture</td>\n<td>Comminuted intra-articular fracture of the distal end of the tibia&nbsp;</td>\n</tr>\n<tr>\n<td>Tillaux fracture</td>\n<td>Avulsion fracture of the tibia by the anterior tibiofibular ligament</td>\n</tr>\n<tr>\n<td>Runners fracture</td>\n<td>Fracture of the distal shaft of fibula&nbsp;</td>\n</tr>\n<tr>\n<td>Pott’s fracture</td>\n<td>Bimalleolar ankle fracture</td>\n</tr>\n<tr>\n<td>Cotton’s fracture</td>\n<td>Trimalleolar ankle fracture</td>\n</tr>\n<tr>\n<td>Aviators fracture</td>\n<td>Fracture of neck of talus</td>\n</tr>\n<tr>\n<td>Chopart’s fracture</td>\n<td>Fracture between talonavicular and calcaneocuboid joints&nbsp;</td>\n</tr>\n<tr>\n<td>Lisfranc’s fracture-dislocation</td>\n<td>Fracture of metatarsal bases with associated tarsometatarsal dislocation</td>\n</tr>\n<tr>\n<td>March fracture</td>\n<td>Fracture of the neck of 2<sup>nd</sup> or 3<sup>rd</sup> metatarsal</td>\n</tr>\n<tr>\n<td>Jones fracture</td>\n<td>\n<p>Extra-articular fracture at the base of the 5th metatarsal</p>\n</td>\n</tr>\n<tr>\n<td>Dancer’s fracture</td>\n<td>Fracture of distal shaft of the fifth metatarsal</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1960, 'Orthopaedics', 'PM2202. Treatment of fracture neck of femur', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/bb1c42274c1048e188d398495dcc963ex1280x1967.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(1961, 'Orthopaedics', 'PM2178. Age wise treatment of fracture shaft of femur', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>&nbsp; &nbsp;Age group</strong></td>\n<td><strong>&nbsp; &nbsp;Management</strong></td>\n</tr>\n<tr>\n<td>0 - 6 months</td>\n<td>Pavlik harness</td>\n</tr>\n<tr>\n<td>6 months - 5 years</td>\n<td>Spica casting</td>\n</tr>\n<tr>\n<td>5 - 10 years</td>\n<td>\n<p>Flexible intramedullary nails as they cannot tolerate prolonged immobilization by spica cast</p>\n</td>\n</tr>\n<tr>\n<td>&gt;10 years</td>\n<td>Locked intramedullary nail</td>\n</tr>\n</tbody>\n</table>\n<p>Gallows traction&nbsp;can be used in displaced fractures of the femoral shaft in children less than 2 years of age and weight less than 12kgs.</p></div><!---->\n      <!---->\n    </div><!---->'),
(1962, 'Orthopaedics', 'PM2170. Treatment of Genu Valgum', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/78f85e8078cd405c8cc9fd0ed46eedc5x1081x914.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(1963, 'Orthopaedics', 'PM2167. Primary and secondary deformities in Clubfoot', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Clubfoot complex</strong></p>\n<p>Primary deformities:</p>\n<ul>\n<li>Equinus</li>\n<li>Varus</li>\n<li>Cavus</li>\n<li>Forefoot adduction</li>\n<li>Internal tibial torsion</li>\n</ul>\n<p>Secondary deformities:</p>\n<ul>\n<li>Concave medial border and convex lateral border</li>\n<li>Reduced foot size</li>\n<li>Forefoot is plantarflexed</li>\n<li>Skin is stretched over dorsum of the foot</li>\n<li>Stumbling gait</li>\n<li>Atrophy of leg muscles</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(1964, 'Orthopaedics', 'PM2034. Osteochondritis', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Osteochondritis</strong></td>\n<td><strong>Bones affected</strong></td>\n</tr>\n<tr>\n<td>Kienbock</td>\n<td>Lunate</td>\n</tr>\n<tr>\n<td>Scheuermann</td>\n<td>Ring epiphysis of the vertebra</td>\n</tr>\n<tr>\n<td>Perthes</td>\n<td>Proximal femoral epiphysis &nbsp;</td>\n</tr>\n<tr>\n<td>Osgood-Schlatter</td>\n<td>Tibial tuberosity</td>\n</tr>\n<tr>\n<td>Sever</td>\n<td>Calcaneum</td>\n</tr>\n<tr>\n<td>Kohler</td>\n<td>Navicular bone</td>\n</tr>\n<tr>\n<td>Freiberg</td>\n<td>\n<p>Second</p>\n<p>(or 3rd,4th,5th)metatarsal head&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>Iselin</td>\n<td>&nbsp;Fifth metatarsal base</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(1965, 'Orthopaedics', 'PM1489. Commonly used splints and their indications', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><span><strong>Name of the Splint</strong></span></p>\n</td>\n<td>\n<p><span><strong>Uses</strong></span></p>\n</td>\n</tr>\n<tr>\n<td>\n<p><span><strong>Used in Lower Limbs</strong></span></p>\n</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>\n<p>Thomas Splint</p>\n<p>Bohler-Braun splint</p>\n</td>\n<td>\n<p>Fracture femur</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Toe raising splint</p>\n</td>\n<td>\n<p>Foot drop</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Dennis Brown splint</p>\n</td>\n<td>\n<p>CTEV</p>\n</td>\n</tr>\n<tr>\n<td>\n<p><span><strong>Used in Upper Limbs</strong></span></p>\n</td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>\n<p>Cock up splint</p>\n</td>\n<td>\n<p>Radial nerve palsy</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Volkmanns turn buckle splint</p>\n<p>&nbsp;</p>\n</td>\n<td>Volkmanns ischemic contracture&nbsp;</td>\n</tr>\n<tr>\n<td>\n<p>Knuckle bender splint</p>\n</td>\n<td>\n<p>Claw hand (Ulnar Nerve)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Aeroplane splint</p>\n</td>\n<td>\n<p>Brachial plexus injury</p>\n</td>\n</tr>\n<tr>\n<td><span><strong>Used for Spine</strong></span></td>\n<td>&nbsp;</td>\n</tr>\n<tr>\n<td>\n<p>Milwaukee brace</p>\n<p>Boston brace</p>\n</td>\n<td>Scoliosis&nbsp;</td>\n</tr>\n<tr>\n<td>\n<p>SOMI brace</p>\n</td>\n<td>\n<p>Cervical spine injury</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>ASHE (anterior spinal hyperextension) brace&nbsp;</p>\n</td>\n<td>\n<p>Dorso lumbar spinal injury</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1966, 'Orthopaedics', 'PM2127. Neurological examination in Intervertebral disc prolapse (IVDP)', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table border=\1\>\n<tbody>\n<tr>\n<td><strong>Disc affected</strong></td>\n<td><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; L3-L4</strong></td>\n<td><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; L4-L5</strong></td>\n<td><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; L5-S1</strong></td>\n</tr>\n<tr>\n<td><strong>Root usually compressed</strong></td>\n<td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; L4</td>\n<td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; L5</td>\n<td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; S1</td>\n</tr>\n<tr>\n<td><strong>Reflex diminished</strong></td>\n<td>Knee jerk -Weakness of quadriceps femoris (Westphals sign)</td>\n<td>Medial hamstring reflex</td>\n<td>Ankle jerk - Achilles tendon</td>\n</tr>\n<tr>\n<td><strong>Motor weakness</strong></td>\n<td>\n<p>Quadriceps femoris</p>\n<p>Hip adductors</p>\n</td>\n<td>\n<p>Tibialis anterior</p>\n<p>Extensor hallucis longus</p>\n<p>Gluteus medius</p>\n<p>Extensor digitorum longus and brevis</p>\n</td>\n<td>\n<p>Peroneus longus and brevis</p>\n<p>Gastrocnemius-soleus complex</p>\n<p>Gluteus maximus</p>\n</td>\n</tr>\n<tr>\n<td><strong>Decreased sensation</strong></td>\n<td>\n<p>Posterolateral thigh</p>\n<p>Anterior knee</p>\n<p>Medial malleolus</p>\n<p>Medial foot</p>\n</td>\n<td>\n<p>Anterolateral leg</p>\n<p>Large toe web</p>\n<p>Dorsum of foot</p>\n</td>\n<td>\n<p>Lateral malleolus</p>\n<p>Lateral foot</p>\n<p>Heel</p>\n<p>Web of fourth and ﬁfth toes</p>\n</td>\n</tr>\n<tr>\n<td><strong>Pain distribution</strong></td>\n<td>Anterior thigh</td>\n<td>Posterolateral leg</td>\n<td>Posterior leg, often involves ankle</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/d1d2f65b960e47248ed30840a8739848x1280x2558.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(1967, 'Orthopaedics', 'PM1477. Named fractures of the vertebra', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Burst&nbsp;</strong>fracture - <strong>Vertical</strong> compression injury</p>\n<p><strong>Chance</strong> fracture - <strong>horizontal</strong> fracture of the vertebral body</p>\n<p><strong>Clay-shovellers</strong> fracture -&nbsp;spinous process fracture of <strong>C6, C7 or T1</strong></p>\n<p><strong>Dens</strong> fracture - <strong>Odontoid</strong> process fracture</p>\n<p><strong>Hangmans</strong> fracture - fracture of both pedicles of <strong>C2</strong></p>\n<p><strong>Holdsworth</strong> fracture -&nbsp;unstable spinal fracture-dislocation at the <strong>thoracolumbar junction</strong></p>\n<p><strong>Jeffersons</strong> fracture - fracture of <strong>first</strong> cervical vertebra</p>\n<p><strong>Motorcyclists</strong>&nbsp;fracture - transverse fracture across <strong>base of the skull</strong></p>\n<p><strong>Whiplashs</strong> injury - hyperextension followed by flexion&nbsp;</p>\n<p><strong>Undertakers</strong> fracture -Post-mortem fracture of the lower cervical spine with <strong>C6-C7 disc disruption</strong>&nbsp;</p>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(1968, 'Orthopaedics', 'PM1448. Coxa Vara', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>The Hilgenreiners epiphyseal angle is the angle between the horizontal line joining the center of each hip and the line parallel to physis&nbsp;. Hilgenreiners angle is 30 degrees.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://cdn1.dailyrounds.org/uploads/8728bc31a6b348d6a890c22e562ce718x496x289.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(1969, 'Orthopaedics', 'PM1420. Delbet Classification of Hip Fractures in Children', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/e1be9d69c6f94670875cee8763c4a5f9x787x749.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><span class=\fontstyle0\>Type I </span><span class=\fontstyle2\>is a fracture-separation of the epiphysis, sometimes the epiphyseal fragment is dislocated from the acetabulum. </span></p>\n<p><span class=\fontstyle0\>Type II </span><span class=\fontstyle2\>is a transcervical fracture of the femoral neck; this is the commonest variety, accounting for almost half of the injuries.</span></p>\n<p><span class=\fontstyle0\>Type III </span><span class=\fontstyle2\>is a basal (cervico-trochanteric) fracture, the second most common injury. </span></p>\n<p><span class=\fontstyle0\>Type IV </span><span class=\fontstyle2\>is an intertrochanteric fracture</span> </p></div><!---->\n      <!---->\n    </div><!---->'),
(1970, 'Orthopaedics', 'PM1379. Ankylosing Spondylitis VS Diffuse idiopathic skeletal hyperostosis', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>&nbsp;</strong></td>\n<td><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;DISH</strong></td>\n<td><strong>&nbsp; &nbsp; &nbsp; &nbsp; AS</strong></td>\n</tr>\n<tr>\n<td><strong>Etiology</strong></td>\n<td>Idiopathic</td>\n<td>Autoimmune</td>\n</tr>\n<tr>\n<td><strong>Age group</strong></td>\n<td>Older patients (middle-aged)</td>\n<td>Younger patients</td>\n</tr>\n<tr>\n<td><strong>HLA</strong></td>\n<td>No evidence of association with HLA-B27</td>\n<td>Strong association with HLA-B27</td>\n</tr>\n<tr>\n<td><strong>Lab investigations</strong></td>\n<td>Non-specific and inconclusive</td>\n<td>High ESR and CRP</td>\n</tr>\n<tr>\n<td><strong>Syndesmophytes</strong></td>\n<td>Nonmarginal</td>\n<td>Marginal</td>\n</tr>\n<tr>\n<td><strong>Radiograph</strong></td>\n<td>\Flowing candle wax\</td>\n<td>&nbsp;\Bamboo spine\&nbsp;</td>\n</tr>\n<tr>\n<td><strong>Disc space</strong></td>\n<td>Preservation of disc space</td>\n<td>AS in cervical spine will show ossification of disc space&nbsp;</td>\n</tr>\n<tr>\n<td><strong>SI joint involvement</strong></td>\n<td>&nbsp;No involvement (SI joint abnormality generally&nbsp;excludes the&nbsp;diagnosis of DISH)</td>\n<td>&nbsp;Bilateral sacroiliitis</td>\n</tr>\n<tr>\n<td><strong>Associated disease</strong></td>\n<td>Obesity, diabetes mellitus, hypervitaminosis A</td>\n<td>Autoimmune conditions like iritis, uveitis, ulcerative colitis</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1971, 'Orthopaedics', 'PM1378. Important DD for Sacroiliitis in AS', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>&nbsp;</strong><strong>Osteitis condensans ilii</strong></p>\n<ul>\n<li>It occurs as mild to moderate SI joint pain in <strong>postpartum women of 30–40 years of age.</strong>. The symptoms are rarely severe, and the radiographic presentation is classic.</li>\n<li>The triangular sclerosis is <strong>confined to the iliac side of the SI joint</strong>, with <strong>no evidence of the destruction of the SI joint</strong> that occurs as in AS.</li>\n</ul>\n<p>The course of osteitis condensans ilii is almost <strong>always benign</strong>. Hence treatment consists of reassurance and use of analgesics.</p>\n<p>X-ray shows&nbsp;bilateral symmetrical triangular-shaped iliac sclerosis.&nbsp;The sacroiliac joints are normal width, without evidence of inflammatory arthropathy. No&nbsp;bony changes&nbsp;on the sacral side of the joint.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/88361f5894db4a03bf3949ec54018f22x389x390.PNG\><!---->\n      </div><!---->\n    </div><!---->'),
(1972, 'Orthopaedics', 'PM1350. Differential Diagnosis of Lesions in Spine', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td width=\325\>\n<p><strong>Older than 40 years</strong></p>\n</td>\n<td width=\325\>\n<p><strong>Younger than 30 years</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\325\>\n<p><strong>Metastasis</strong></p>\n</td>\n<td width=\325\>\n<p><strong>Vertebral Body</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\325\>\n<p><strong>Multiple Myeloma</strong></p>\n</td>\n<td width=\325\>\n<p>Histiocytosis</p>\n</td>\n</tr>\n<tr>\n<td width=\325\>\n<p>Hemangioma</p>\n</td>\n<td width=\325\>\n<p>Hemangioma</p>\n</td>\n</tr>\n<tr>\n<td width=\325\>\n<p>Chordoma</p>\n</td>\n<td width=\325\>\n<p><strong>Posterior elements</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\325\>\n<p><strong>&nbsp;</strong></p>\n</td>\n<td width=\325\>\n<p>Osteoid Osteoma</p>\n</td>\n</tr>\n<tr>\n<td width=\325\>\n<p><strong>&nbsp;</strong></p>\n</td>\n<td width=\325\>\n<p>Osteoblastoma</p>\n</td>\n</tr>\n<tr>\n<td width=\325\>\n<p><strong>&nbsp;</strong></p>\n</td>\n<td width=\325\>\n<p>Aneurysmal bone cyst</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1973, 'Orthopaedics', 'PM1189. Spondylolisthesis classification', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>It is the displacement of a vertebra over the other in the <strong>anterior</strong> direction.&nbsp;</p>\n<p><strong>Meyerding Classification</strong> of spondylolisthesis</p>\n<table>\n<tbody>\n<tr>\n<td width=\239\>\n<p><strong>Grade</strong></p>\n</td>\n<td width=\239\>\n<p><strong>Displacement</strong></p>\n</td>\n</tr>\n<tr>\n<td width=\239\>\n<p>I</p>\n</td>\n<td width=\239\>\n<p>0-25%</p>\n</td>\n</tr>\n<tr>\n<td width=\239\>\n<p>II</p>\n</td>\n<td width=\239\>\n<p>26-50%</p>\n</td>\n</tr>\n<tr>\n<td width=\239\>\n<p>III</p>\n</td>\n<td width=\239\>\n<p>51-75%</p>\n</td>\n</tr>\n<tr>\n<td width=\239\>\n<p>IV</p>\n</td>\n<td width=\239\>\n<p>76-100%</p>\n</td>\n</tr>\n<tr>\n<td width=\239\>\n<p>V (Spondyloptosis)</p>\n</td>\n<td width=\239\>\n<p>&gt;100%</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/8da66fe5170144028f57ba0526eaf668x1059x562.PNG\><!---->\n      </div><!---->\n    </div><!---->'),
(1974, 'Orthopaedics', 'PM1186. Classification of Congenital Scoliosis', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table width=\313\>\n<tbody>\n<tr>\n<td>\n<p><strong>Classification of Congential Scoliosis</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<ul>\n<li>Failure of formation\n<ul>\n<li>Partial failure of formation (wedge vertebra)</li>\n<li>Complete failure of formation (hemivertebra)</li>\n</ul>\n</li>\n<li>Failure of segmentation\n<ul>\n<li>Unilateral failure segmentation (unilateral unsegmented bar)</li>\n<li>Bilateral failure of segmentation (block vertebrate)</li>\n</ul>\n</li>\n<li>Miscellaneous</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(1975, 'Orthopaedics', 'PM1182. Chance Fracture', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>Flexion-distraction mechanism of injury results in the <strong>Chance fracture</strong>, usually seen with the use of lap-belts in vehicles (Lap Belt injury).</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://cdn1.dailyrounds.org/uploads/2144df260a20451b9f00e59b8f4bd015x382x354.PNG\><!---->\n      </div><!---->\n    </div><!---->'),
(1976, 'Orthopaedics', 'PM1181. Column Theory of Spine', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>The column theory for vertebral fractures was put forth by Denis.</p>\n<ul>\n<li><strong><span class=\fontstyle0\>Anterior column </span></strong><span class=\fontstyle2\>composed of the anterior 2/3rd of the vertebral body, the anterior part of the intervertebral disc and the anterior longitudinal ligament.</span></li>\n<li><span class=\fontstyle0\><strong>Middle column</strong> </span><span class=\fontstyle2\>comprising the posterior 1/3rd of the vertebral body, the posterior part of the intervertebral disc and the posterior longitudinal ligament.</span></li>\n<li><span class=\fontstyle2\><span class=\fontstyle0\><strong>Posterior column</strong>&nbsp;</span>consists of the pedicles, facet joints, posterior bony arch, interspinous and supraspinous ligaments.</span></li>\n</ul>\n<p><span class=\fontstyle2\> <span class=\fontstyle0\>All fractures involving the middle column and at least one other column should be regarded as unstable.</span> <br> </span></p></div><!---->\n      <!---->\n    </div><!---->');
INSERT INTO db_pearl_m (pid, subject, pearl_title, pearl_desc) VALUES
(1977, 'Orthopaedics', 'PM1035. Bladder Injuries in Pelvic Fractures', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><ul>\n<li>Because of its location behind the symphysis pubis, the bladder can be injured easily; extraperitoneal (EPBR) bladder rupture is common. In this type of injury, the urine may communicate with the pelvis.</li>\n<li>An intraperitoneal (IPBR) bladder rupture, which is often caused by compression on the distended bladder resulting in rupture of the dome, communicates with the peritoneal cavity but will not contaminate the pelvis</li>\n<li>Bladder injuries occur in up to 10% of pelvic ring fractures, with approximately 60% EPBR, 30% IPBR, and 10% both.</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(1978, 'Orthopaedics', 'PM1034. Spondylolisthesis', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>The indications for surgical management in spondylolisthesis are:</p>\n<ul>\n<li>Failure of conservative therapy</li>\n<li>Signs of root compression</li>\n<li>Progressive slipping</li>\n<li>Slip of more than 30% even when painless</li>\n<li>Persistent sciatica</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(1979, 'Orthopaedics', 'PM1031. Scheuermann Disease', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><ul>\n<li>Results from osteonecrosis of the ring apophysis of the vertebral body hence known as <strong>Vertebral Osteochondritis</strong>.</li>\n<li>Leads to rigid structural juvenile kyphosis. <strong>Thoracic involvement</strong> is the most common form.</li>\n<li>Usually appears around the <strong>adolescent growth spurt</strong>. The presenting complaint is either pain in the middle or lower back though concern about deformity is more common.</li>\n<li>Typical Radiographic finding is Schmorl Node - Small radiolucent defects in the subchondral bone.</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(1980, 'Orthopaedics', 'PM1030. Surgery in Scoliosis', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>The proper indications of surgery are:</p>\n<ul>\n<li>Curves&gt; 50 degrees in the mature patients</li>\n<li>Curves &gt; 10 degrees with marked rotations</li>\n<li>Double major curves&gt; 30 degrees</li>\n</ul>\n<p>Surgery is also indicated when the curve is over 60 degrees and aims at obtaining fusion at the spine. The most common form of surgical intervention in idiopathic scoliosis is the segmental instrumentation with the multi-back system.</p></div><!---->\n      <!---->\n    </div><!---->'),
(1981, 'Orthopaedics', 'PM1028. Risser Sign', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>Rissers classification uses ossification of iliac apophysis to grade the remaining skeletal growth. The ossification progresses from lateral to medial:</p>\n<ul>\n<li>Grade 1- ossification of lateral 25%</li>\n<li>Grade 2- ossification of lateral 50%</li>\n<li>Grade 3- ossification of lateral 75%</li>\n<li>Grade 4- ossification of lateral 100%</li>\n<li>Grade 5- fusion of ilium</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(1982, 'Orthopaedics', 'PM1022. SCIWORA', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>SCIWORA</strong>-&nbsp;<strong>S</strong>pinal <strong>C</strong>ord <strong>I</strong>njury <strong>W</strong>ithout <strong>R</strong>adiographic <strong>A</strong>bnormality.</p>\n<p>It is defined as a spinal cord injury in a patient with no visible fracture or dislocation on plain radiographs or CT scans.</p>\n<p>It is almost completely unique to <strong>children</strong>. It is believed to occur because the vertebral column (vertebrae and disc space) in children is more elastic than the spinal cord. Hence,&nbsp;the vertebral column in children can undergo considerable <strong>deformation</strong> without being disrupted. The vertebral column in children can elongate up to 2 inches without disruption, whereas the spinal cord gets injured with only a quarter-inch of elongation.</p>\n<p>X-ray/CT may not show vertebral injuries, but <strong>MRI</strong> shows&nbsp;evidence of <strong>spinal cord injury.</strong></p></div><!---->\n      <!---->\n    </div><!---->'),
(1983, 'Orthopaedics', 'PM1021. Jeffersons Fracture', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>A <strong>burst fracture</strong> of <strong>C1</strong> ring resulting from an <strong>axial load</strong> is known as <strong>Jeffersons Fracture</strong>.</p>\n<p>The fracture is identified on the <strong>open-mouth X-ray view</strong> (<strong>odontoid view</strong>).&nbsp;A CT scan will confirm and define the fracture.</p>\n<p>If combined <strong>overhang of lateral processes</strong> of <strong>C1 on C2 &gt; 7mm</strong>&nbsp;in the open-mouth view, the transverse ligament is likely to be ruptured (<strong>rule of Spence</strong>).</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/105a11018c4c472c8e0d2ba072a81e41x1280x1344.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/e326d01a8d7147efb75d992709a721f0x1200x1871.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(1984, 'Orthopaedics', 'PM0654. Septic arthritis', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td colspan=\2\><strong>Septic arthritis and associated organism</strong></td>\n</tr>\n<tr>\n<td colspan=\2\><strong>PATIENT AGE</strong>&nbsp;</td>\n</tr>\n<tr>\n<td>Neonate</td>\n<td><em>Staphylococcus aureus</em></td>\n</tr>\n<tr>\n<td>&lt;2 yr</td>\n<td><em><em>S.aureus,&nbsp;</em></em><em>Haemophilus influenzae</em></td>\n</tr>\n<tr>\n<td>&gt;2 yr</td>\n<td><em>S. aureus</em></td>\n</tr>\n<tr>\n<td>Young adults (healthy, sexually active)</td>\n<td><em>Neisseria gonorrhoeae</em></td>\n</tr>\n<tr>\n<td colspan=\2\><strong>PROSTHESIS</strong></td>\n</tr>\n<tr>\n<td>Early infection</td>\n<td><em>S. epidermidis</em></td>\n</tr>\n<tr>\n<td>Late infection</td>\n<td>Gram-positive cocci, anaerobes</td>\n</tr>\n<tr>\n<td colspan=\2\><strong>OTHER CONDITIONS</strong></td>\n</tr>\n<tr>\n<td>IV drug abusers</td>\n<td>Atypical gram-negative bacilli (e.g. Pseudomonas species)</td>\n</tr>\n<tr>\n<td>Sickle cell anemia, Systematic lupus erythematosus</td>\n<td><em>Salmonella</em> species</td>\n</tr>\n<tr>\n<td>Immunosuppression</td>\n<td><em>S. aureus</em>, <em>Mycobacterium</em> species, fungi</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1985, 'Orthopaedics', 'PM2413. Age wise management of DDH', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>AGE OF THE PATIENT</strong></td>\n<td><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; MANAGEMENT</strong></td>\n</tr>\n<tr>\n<td>Less than 6 months</td>\n<td>Closed reduction with Pavlik harness or von Rosen splint</td>\n</tr>\n<tr>\n<td>6-18 months</td>\n<td>Closed or open reduction followed by hip spica after reduction</td>\n</tr>\n<tr>\n<td>18-36 months</td>\n<td>Open reduction with femoral shortening and pelvic osteotomy (Salters osteotomy, Pemberton osteotomy, Steel osteotomy)</td>\n</tr>\n<tr>\n<td>3-8 year</td>\n<td>Open reduction with femoral shortening and pelvic osteotomy&nbsp;</td>\n</tr>\n<tr>\n<td>8-18 year</td>\n<td>\n<p>Open procedure with femoral shortening with pelvic osteotomy or total hip replacement</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1986, 'Orthopaedics', 'PM2411. Age wise management of CTEV', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>Initial treatment starts with Ponsetis POP cast as early as possible, but within 1 year of age.</p>\n<p>If deformity gets corrected:</p>\n<ul>\n<li>Dennis Brown splint is used till 1 year of age</li>\n<li>After 1 year upto 5 years CTEV shoes are used in day, and Dennis Brown splint is used at night</li>\n</ul>\n<p>If deformity is not corrected or untreated previously:</p>\n<ul>\n<li>1-3 years: Posteromedial soft tissue release (PMSTR)&nbsp;</li>\n<li>3-5 years: Dillwyn Evans procedure</li>\n<li>5-8 years: Dwyer osteotomy</li>\n<li>8-10 years: Wedge tarsectomy</li>\n<li>&gt;10 years: Triple arthrodesis, external fixators</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/a1d7a0ec619d4540a6619b318298f007x440x1622.PNG\><!---->\n      </div><!---->\n    </div><!---->'),
(1987, 'Orthopaedics', 'PM2168. Order of correction of deformities in CTEV', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/384f44ef2e6242c0b5d068c3ac43d88bx579x561.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(1988, 'Orthopaedics', 'PM2050. Structures involved in congenital talipes equinovarus', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/9987da0ba1c445cab6ce1e7d1e9f35f3x1280x928.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(1989, 'Orthopaedics', 'PM2043. Standard skeletal survey for suspected child abuse:', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<strong>Category&nbsp;</strong></td>\n<td>&nbsp; &nbsp; &nbsp;<strong> View</strong>&nbsp;</td>\n</tr>\n<tr>\n<td><strong>Minimum Required views</strong></td>\n<td>\n<ul>\n<li>Anteroposterior views of entire skeleton</li>\n<li>Dedicated views of hands and feet</li>\n<li>Lateral views of axial skeleton-skull and &nbsp;spine</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td><strong>Imaging also suggested</strong></td>\n<td>\n<ul>\n<li>Oblique views of ribs</li>\n<li>Oblique views of the hands and feet</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td><strong>Optional useful images</strong></td>\n<td>\n<ul>\n<li>Lateral views of the joints-wrists,ankles,knees</li>\n<li>Orthogonal views of any fractures found</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1990, 'Orthopaedics', 'PM1453. Vicker’s Ligament in Madelung Deformity', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><ul>\n<li>Vickers described an abnormal ligament that tethers the lunate to the distal radius proximal to the physis.</li>\n<li>This ligament is believed to impede the growth of the ulnopalmar aspect of the distal radius and is commonly known as the ligament of Vickers.</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://cdn1.dailyrounds.org/uploads/d39b609ddf474c6ba7768b6b3aae7982x180x262.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(1991, 'Orthopaedics', 'PM1447. Congenital Coxa Vara', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>A painless limp in a child who has just started walking with shortening of the limb, and limitation of abduction and internal rotation is indicative of a case of congenital coxa vara. Angle formed between the head and neck of the femur and its shaft (Mikulicz angle) is decreased to less than 120<sup>o</sup>.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/fc2d1c5b50ef49a3bd8e96422fad8731x403x391.PNG\><!---->\n      </div><!---->\n    </div><!---->'),
(1992, 'Orthopaedics', 'PM1442. Blount Disease Classification', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>Langenskiold classification is used in Blount’s disease.</p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://cdn1.dailyrounds.org/uploads/530b470109d94d9faf09796b95714d60x428x457.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(1993, 'Orthopaedics', 'PM1431. CTEV Procedures', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/25f043ff91954947b8cecdd07de56565x1280x939.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(1994, 'Orthopaedics,', 'PM2198. Vitamin D activation and Rickets', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/fc2bbad956284f25ac60ba74c376ac0ex780x802.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(1995, 'Orthopaedics', 'PM2188. Risk factors for osteoporosis:', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Risk factor for osteoporosis</strong></td>\n</tr>\n<tr>\n<td>Low BMI</td>\n</tr>\n<tr>\n<td>Family history of osteoporosis </td>\n</tr>\n<tr>\n<td>Old age</td>\n</tr>\n<tr>\n<td>Previous fragility fracture</td>\n</tr>\n<tr>\n<td>Chronic use of glucocorticoids</td>\n</tr>\n<tr>\n<td>Family history of hip fractures</td>\n</tr>\n<tr>\n<td>Smoking and alcohol intake</td>\n</tr>\n<tr>\n<td>Drugs like anticonvulsants, anticoagulants and thyroid replacement therapy</td>\n</tr>\n<tr>\n<td>Hyperparathyroidism, multiple myeloma, and malabsorption syndrome</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(1996, 'Orthopaedics', 'PM2162. Bone turnover markers', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><div class=\ttl\>&nbsp;</div>\n<div class=\cntnt\>\n<table border=\1\ cellspacing=\0\>\n<tbody>\n<tr>\n<td class=\subtitle1_single\><strong>Resorption markers:</strong></td>\n</tr>\n<tr>\n<td class=\indent1\>Pyridinium crosslinks (PYD and DPD)</td>\n</tr>\n<tr>\n<td class=\indent1\>C- and N-telopeptides (CTX, ICTP, NTX)</td>\n</tr>\n<tr>\n<td class=\indent1\>Tartrate-resistant acid phosphatase (TRACP)</td>\n</tr>\n<tr>\n<td class=\indent1\>Cathepsin K</td>\n</tr>\n<tr>\n<td class=\indent1\>Matrix metalloproteinases (MMPs)</td>\n</tr>\n<tr>\n<td class=\indent1\>Urine hydroxyproline</td>\n</tr>\n<tr>\n<td class=\subtitle1_single\><strong>Formation markers:</strong></td>\n</tr>\n<tr>\n<td class=\indent1\>Procollagen type I propeptides</td>\n</tr>\n<tr>\n<td class=\indent2\>C-terminal (PICP)</td>\n</tr>\n<tr>\n<td class=\indent2\>N-terminal (PINP)</td>\n</tr>\n<tr>\n<td class=\indent1\>Osteocalcin (OC)</td>\n</tr>\n<tr>\n<td class=\indent1\>Bone isoform of alkaline phosphatase (BALP)</td>\n</tr>\n</tbody>\n</table>\n</div></div><!---->\n      <!---->\n    </div><!---->'),
(1997, 'Orthopaedics', 'PM1958. Z-score and T-score for osteoporosis:', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong><span>Z-score:</span></strong></p>\n<p><span>It is the number of standard deviations a patient’s BMD(bone mass density) differs from the average BMD of <strong>their age, sex, and ethnicity</strong>.</span></p>\n<p><span>A Z-score of -2 or below should trigger investigations for underlying disease.</span></p>\n<p><strong><span>T-score:</span></strong></p>\n<p><span>It is the BMD at the site when compared to the <strong>young normal reference</strong> mean. It is a comparison of a patient’s BMD to that of a healthy <strong>30-year old</strong>.</span></p>\n<ul>\n<li><span><strong>Normal</strong> is a T-score of -1 or higher.</span></li>\n<li><span><strong>Osteopenia</strong> is defined as a T-score between -1.0 and -2.5.</span></li>\n<li><span><strong>Osteoporosis</strong> is defined as a T-score less than -2.5, that is a BMD which is two and a half standard deviations below the mean of a 30-year old man/woman.</span></li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(1998, 'Paediatrics,', 'PM1859. Important differences between vitamin D and vitamin C deficiencies', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table width=\156\>\n<tbody>\n<tr>\n<td>&nbsp;</td>\n<td>\n<div><strong>Vitamin D &nbsp; &nbsp; deficiency&nbsp;&nbsp;<br></strong></div>\n</td>\n<td>\n<div><strong>Vitamin C &nbsp; &nbsp; deficiency<br></strong></div>\n</td>\n</tr>\n<tr>\n<td><strong>Pathology</strong></td>\n<td>Poor mineralization of bone</td>\n<td>Poor osteoid&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; formation</td>\n</tr>\n<tr>\n<td><strong>Clinical features</strong></td>\n<td>\n<p>No/less irritability</p>\n<p>Rachitic rosary (nodularity at the costochondral junction)</p>\n</td>\n<td>\n<p>Irritability is predominant</p>\n<p>Scorbutic rosary (angular &amp; sharp costochondral junction)</p>\n</td>\n</tr>\n<tr>\n<td><strong>Radiological features</strong></td>\n<td>\n<p>Cupping&nbsp;</p>\n<p>Fraying</p>\n<p>Splaying</p>\n</td>\n<td>\n<p>White line of Frankel</p>\n<p>Pelkan spur</p>\n<p>Wimberger ring sign</p>\n<p>Subperiosteal hemorrhage</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p><strong>Note:</strong> Irritability in scurvy is due to hampered neurotransmitter production (conversion of dopamine to norepinephrine and tryptophan to serotonin requires vitamin C).</p></div><!---->\n      <!---->\n    </div><!---->'),
(1999, 'Orthopaedics,', 'PM1613. Important Clinical Features of Rickets', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/ce742f2400be4e62813aa4a052447418x1280x1326.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(2000, 'Orthopaedics', 'PM1079. Biochemical parameters in metabolic bone diseases', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Metabolic bone disease</strong></p>\n</td>\n<td><strong>Calcium</strong></td>\n<td><strong>PTH</strong></td>\n<td><strong>Phosphate</strong></td>\n<td><strong>ALP</strong></td>\n</tr>\n<tr>\n<td>\n<p>Rickets / Osteomalacia</p>\n</td>\n<td>&nbsp;↓/N</td>\n<td>&nbsp; &nbsp;↑</td>\n<td>&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;↓</td>\n<td>&nbsp; &nbsp;↑</td>\n</tr>\n<tr>\n<td>\n<p>Primary</p>\n<p>hyperparathyroidism</p>\n</td>\n<td>&nbsp; &nbsp; &nbsp;↑</td>\n<td>&nbsp;&nbsp;&nbsp;↑</td>\n<td>&nbsp; &nbsp; &nbsp; &nbsp;↓</td>\n<td>&nbsp;↑</td>\n</tr>\n<tr>\n<td>\n<p>Secondary</p>\n<p>hyperparathyroidism</p>\n</td>\n<td>&nbsp; &nbsp; &nbsp;↓</td>\n<td>&nbsp;&nbsp; ↑</td>\n<td>&nbsp; &nbsp; &nbsp; &nbsp;↑</td>\n<td>&nbsp; &nbsp; ↑</td>\n</tr>\n<tr>\n<td>\n<p>Tertiary hyperparathyroidism</p>\n</td>\n<td>&nbsp; &nbsp; &nbsp;↑</td>\n<td>&nbsp; &nbsp; ↑</td>\n<td>&nbsp; &nbsp; &nbsp; &nbsp;↑</td>\n<td>&nbsp; &nbsp;↑</td>\n</tr>\n<tr>\n<td>Osteoporosis</td>\n<td>&nbsp; &nbsp; &nbsp;N</td>\n<td>&nbsp; &nbsp;N</td>\n<td>&nbsp; &nbsp; &nbsp; N</td>\n<td>&nbsp; N</td>\n</tr>\n<tr>\n<td>\n<p>Pagets&nbsp;disease</p>\n</td>\n<td>&nbsp; &nbsp; &nbsp;N</td>\n<td>&nbsp; &nbsp;N</td>\n<td>&nbsp; &nbsp; &nbsp; N</td>\n<td>&nbsp; ↑</td>\n</tr>\n<tr>\n<td>\n<p>Osteopetrosis</p>\n</td>\n<td>&nbsp; ↓/N</td>\n<td>↑/N</td>\n<td>&nbsp; &nbsp; ↓/N</td>\n<td>&nbsp; N</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;↑: Increased,&nbsp;&nbsp;↓: Decreased, N: Normal</p></div><!---->\n      <!---->\n    </div><!---->'),
(2001, 'Orthopaedics', 'PM2039. Classification Criteria for Rheumatoid Arthritis - ACR/EULAR (2010)', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>Criteria</td>\n<td>&nbsp;</td>\n<td>\n<p>Score</p>\n</td>\n</tr>\n<tr>\n<td>Joint involvement</td>\n<td>\n<p>&nbsp;</p>\n<p>1 large joint(shoulder,elbow,hip,knee)</p>\n<p>2-10 large joints</p>\n<p>1-3 small joints(MCP,PIP,IP,MTP,wrist)</p>\n<p>4-10 small joints</p>\n<p>&gt;10 joints(atleast 1 small joint)</p>\n</td>\n<td>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>0</p>\n<p>1</p>\n<p>2</p>\n<p>3</p>\n<p>5</p>\n<p>&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>Serology</td>\n<td>\n<p>-ve RF and -ve ACPA</p>\n<p>Low +ve RF or anti-CCP (&lt;3 times ULN)</p>\n<p>High +ve RF or anti-CCP (&gt;3 times ULN)</p>\n</td>\n<td>\n<p>0</p>\n<p>2</p>\n<p>3</p>\n</td>\n</tr>\n<tr>\n<td>Acute phase reactants</td>\n<td>\n<p>Normal CRP and ESR</p>\n<p>Abnormal CRP or ESR</p>\n</td>\n<td>\n<p>0</p>\n<p>1</p>\n</td>\n</tr>\n<tr>\n<td>Duration of symptoms</td>\n<td>\n<p>&lt;6 weeks</p>\n<p>&gt;=6 weeks</p>\n</td>\n<td>\n<p>0</p>\n<p>1</p>\n</td>\n</tr>\n</tbody>\n</table>\n<div class=\page\ title=\Page 2898\>\n<div class=\section\>\n<div class=\layoutArea\>\n<div class=\column\>\n<p>Maximum score is 10. A score of ≥6 fulfills requirements for definite RA.</p>\n<p>Abbreviations:</p>\n<ul>\n<li>ACPA, anti-citrullinated peptide antibodies;</li>\n<li>CCP, cyclic citrullinated pep- tides;</li>\n<li>IP, interphalangeal joint;</li>\n<li>MCP, metacarpophalangeal joint;</li>\n<li>MTP, metatarsophalangeal joint;</li>\n<li>PIP, proximal interphalangeal joint;</li>\n<li>RF, rheumatoid factor;</li>\n<li>ULN, upper limit of normal.</li>\n</ul>\n</div>\n</div>\n</div>\n</div></div><!---->\n      <!---->\n    </div><!---->'),
(2002, 'Orthopaedics', 'PM1920. Diseases and Usual Joints affected', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>Disease</td>\n<td>Joint</td>\n</tr>\n<tr>\n<td>Gout</td>\n<td>MTP of great toe</td>\n</tr>\n<tr>\n<td>Pseudogout</td>\n<td>Knee</td>\n</tr>\n<tr>\n<td>Rheumatoid arthritis</td>\n<td>Metacarpophalangeal joint</td>\n</tr>\n<tr>\n<td>Ankylosing spondylitis</td>\n<td>Sacro-iliac joint</td>\n</tr>\n<tr>\n<td>Septic arthritis</td>\n<td>Knee</td>\n</tr>\n<tr>\n<td>Syphilitic arthritis</td>\n<td>Knee</td>\n</tr>\n<tr>\n<td>Gonococcal arthritis</td>\n<td>Knee</td>\n</tr>\n<tr>\n<td>Diabetic Charcot joint</td>\n<td>Foot joint(midtarsal)</td>\n</tr>\n<tr>\n<td>Senile osteoporosis</td>\n<td>Vertebra</td>\n</tr>\n<tr>\n<td><strong>P</strong>agets disease<strong><br></strong></td>\n<td><strong>P</strong>elvis &gt; Femur &gt;Skull &gt;Tibia</td>\n</tr>\n<tr>\n<td>Osteochondritis dissecans</td>\n<td>Knee &gt;Elbow<br>(Most common cause of loose bodies in young)</td>\n</tr>\n<tr>\n<td>Actinomycosis</td>\n<td>Mandible</td>\n</tr>\n<tr>\n<td>Hemophilic arthritis</td>\n<td>Knee(Children-Ankle)</td>\n</tr>\n<tr>\n<td>Acute osteomyelitis</td>\n<td>Lower end of femur(Metaphysis)</td>\n</tr>\n<tr>\n<td>Brodies abscess</td>\n<td>Upper end of tibia</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(2003, 'Orthopaedics', 'PM1382. Arthritis Mutilans', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>It is the destructive form of psoriatic arthritis, where there is widespread shortening of digits (TELESCOPING) with ankylosis and contracture in other digits.</p>\n<p><strong>Clinical appearance and Radiograph of Arthritis mutilans</strong></p></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/a113337359a5405cbbc3a1a3bec14150x498x324.PNG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/0465b129cc9e40a984fb97eb2ea40b8bx440x316.PNG\><!---->\n      </div><!---->\n    </div><!---->'),
(2004, 'Orthopaedics', 'PM1381. Symmetric Polyarthritis', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><ul>\n<li>Symmetric polyarthritis occurs in about 40% of psoriatic arthritis.</li>\n<li>It may be indistinguishable from RA in terms of the joints involved, but other features of psoriatic arthritis are also present.</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(2005, 'Orthopaedics', 'PM1380. Joints Affected by Diseases', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>Disease</strong></p>\n</td>\n<td>\n<p><strong>Joint affected</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Diabetes Mellitus</p>\n</td>\n<td>\n<p>Tarsal &amp; Tarsometatarsal joint</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Tabes Dorsalis</p>\n</td>\n<td>\n<p>Knee</p>\n<p>Hip</p>\n<p>Ankle</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Syringomyelia</p>\n</td>\n<td>\n<p>Glenohumeral joint (shoulder)</p>\n<p>Elbow</p>\n<p>Wrist</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(2006, 'Orthopaedics', 'PM2200. Seddons  classification of nerve injuries', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p>Seddons&nbsp;classification includes 3 groups:</p>\n<ol>\n<li>Neuropraxia</li>\n<li>Axonotmesis</li>\n<li>Neurotmesis</li>\n</ol>\n<p><strong>Neuropraxia:</strong></p>\n<ul>\n<li>Reversible physiologic conduction block</li>\n<li>Occurs due to mechanical pressure causing segmental demyelination</li>\n<li>Examples: Saturday night palsy, tourniquet, and crutch palsy</li>\n</ul>\n<p><strong>Axonotmesis:</strong>&nbsp;</p>\n<ul>\n<li>Axonal interruption</li>\n<li>Conduction is lost but the nerve is in continuity and neural tubes are intact</li>\n<li>Examples: Closed fractures and dislocations</li>\n</ul>\n<p><strong>Neurotmesis:&nbsp;</strong></p>\n<ul>\n<li>Division of the nerve trunk</li>\n<li>Endoneurial tube destruction also occurs</li>\n<li>Example: Complete section of nerve in open fractures</li>\n</ul></div><!---->\n      <!---->\n    </div><!---->'),
(2007, 'Orthopaedics', 'PM2495. Summary of Bone Tumors', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Bone tumor</strong></td>\n<td><strong>Most common site</strong></td>\n<td><strong>Age</strong></td>\n</tr>\n<tr>\n<td>Simple bone cyst</td>\n<td>Proximal Humeral Metaphysis</td>\n<td>&lt;20years</td>\n</tr>\n<tr>\n<td>Enchondroma</td>\n<td>\n<p>Hand bones</p>\n<p>(Metaphysis of small bones of hand)</p>\n</td>\n<td>\n<p>4<sup>th</sup>&nbsp;&amp; 5<sup>th</sup></p>\n<p>Decade</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Osteochondroma</p>\n<p>(Exostosis)</p>\n</td>\n<td>Distal Femoral Metaphysis</td>\n<td>&lt;18years</td>\n</tr>\n<tr>\n<td>Osteoid osteoma</td>\n<td>Diaphysis of Femur</td>\n<td>\n<p>2<sup>nd</sup>&nbsp;&amp; 3<sup>rd</sup></p>\n<p>decade</p>\n</td>\n</tr>\n<tr>\n<td>Osteoblastoma</td>\n<td>Posterior column of vertebra</td>\n<td>\n<p>2<sup>nd</sup>&nbsp;&amp; 3<sup>rd</sup></p>\n<p>decade</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Osteoclastoma (GCT)</p>\n</td>\n<td>Distal femoral epiphysis</td>\n<td>\n<p>2<sup>nd</sup> -4<sup>th</sup></p>\n<p>Decade</p>\n</td>\n</tr>\n<tr>\n<td>Aneurysmal Bone Cyst</td>\n<td>Long bone Metaphysis</td>\n<td>&lt;18years</td>\n</tr>\n<tr>\n<td>Osteosarcoma</td>\n<td>Distal Femoral Metaphysis</td>\n<td>\n<p>BIMODAL</p>\n<p>Primary- 2<sup>nd</sup> Decade Secondary-6<sup>th</sup> and 7<sup>th</sup> Decade</p>\n</td>\n</tr>\n<tr>\n<td>Ewings sarcoma</td>\n<td>Diaphysis of Femur</td>\n<td>5-25 years</td>\n</tr>\n<tr>\n<td>Chondrosarcoma</td>\n<td>Any site in the bone - mc in proximal femur</td>\n<td>3<sup>rd</sup>-5<sup>th</sup> decade</td>\n</tr>\n<tr>\n<td>Clear cell chondrosarcoma</td>\n<td>Proximal femoral epiphysis</td>\n<td>&nbsp;3<sup>rd</sup>-5<sup>th</sup> decade</td>\n</tr>\n<tr>\n<td>Adamantinoma</td>\n<td>Diaphysis of Tibia</td>\n<td>&nbsp;20-40 years</td>\n</tr>\n<tr>\n<td>&nbsp;Fibrous dysplasia</td>\n<td>&nbsp;The upper end of femur &gt; tibia</td>\n<td>&nbsp;1st to 3rd decade</td>\n</tr>\n<tr>\n<td>&nbsp;Chordoma</td>\n<td>&nbsp;Sacrum</td>\n<td>&nbsp;5th to 7th decade</td>\n</tr>\n<tr>\n<td>Multiple myeloma</td>\n<td>Vertebrae</td>\n<td>5th to 7th decade</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p></div><!---->\n      <!---->\n    </div><!---->'),
(2008, 'Orthopaedics', 'PM1943. Enneking System for Staging Benign Musculoskeletal Tumors', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>&nbsp;</td>\n<td><span><strong>Latent</strong></span></td>\n<td><span><strong>Active</strong></span></td>\n<td><span><strong>Aggressive</strong></span></td>\n</tr>\n<tr>\n<td>Location</td>\n<td><span>Intracapsular</span></td>\n<td><span>Intracapsular</span></td>\n<td><span>Extracapsular</span></td>\n</tr>\n<tr>\n<td>Clinical findings</td>\n<td><span>Often incidental findings.</span></td>\n<td><span>Symptomatic,&nbsp;may present with pathological fracture.</span></td>\n<td><span>Symptomatic</span></td>\n</tr>\n<tr>\n<td>X-ray features</td>\n<td><span>Well defined margin with a thick rim of reactive bone on X ray. No cortical destruction or expansion.</span></td>\n<td><span>Well defined margin and a thin rim of reactive bone on X ray. Narrow zone of activity;&nbsp;limited bone destruction.</span></td>\n<td><span>Bone destruction/soft tissue extension. Metastasis may be present in 5% of patients.</span>&nbsp;</td>\n</tr>\n<tr>\n<td>Examples</td>\n<td>&nbsp;N<span>on-ossifying fibroma</span></td>\n<td>&nbsp;<span>Aneurysmal bone cyst</span></td>\n<td><span>Giant cell tumor</span></td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->'),
(2009, 'Orthopaedics', 'PM1337. Giant Cell Tumours', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>Giant cell tumour</strong> is usually eccentrically located in the <strong>epiphysis </strong>but it adopts a <strong>metaphyseal location</strong> in the skeletally immature which includes children. Although, <strong>chondrosarcomas</strong> are <strong>metaphyseal lesions</strong>, <strong>clear cell chondrosarcoma</strong> which is a rare variety usually seen in adults, is an <strong>epiphyseal lesion</strong>.</p></div><!---->\n      <!---->\n    </div><!---->'),
(2010, 'Orthopaedics', 'PM1493. Some Important Traction and Their Uses', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>1) Gallow/Bryant traction:&nbsp;</strong></p>\n<ul>\n<li>Type - Skin traction&nbsp;</li>\n<li>Use -&nbsp;Fracture shaft femur in children below 2 years</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/ca9068b3be484aa890c2cdcf144ae69ax315x208.PNG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>2) Perkin traction:&nbsp;</strong></p>\n<ul>\n<li>Type - Skeletal traction&nbsp;</li>\n<li>Use -&nbsp;Fracture shaft femur in adults</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/cd82e52a00094730945cb29990c414ebx600x743.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>3) Russell’s traction:&nbsp;</strong></p>\n<ul>\n<li>Type - Skin traction&nbsp;</li>\n<li>Use -&nbsp;Trochanteric fractures</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/6356785331eb4819b6d4055e99632f64x1279x1542.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>4) Dunlop/Smith traction:&nbsp;</strong></p>\n<ul>\n<li>Type - Skeletal traction&nbsp;</li>\n<li>Use -&nbsp;Supracondylar fracture humerus</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/812187193a7d4eaa90eafa94ff56cb20x1280x1474.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>5) Bucks traction:&nbsp;</strong></p>\n<ul>\n<li>Most common lower limb traction</li>\n<li>Type - Skin traction&nbsp;</li>\n<li>Use -&nbsp;Femoral fractures, acetabular and hip fractures</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/6dc8b1d46819421c8ec20549cefae878x1280x1463.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>6)Crutchfield traction:&nbsp;</strong></p>\n<ul>\n<li>Type - Skeletal traction&nbsp;</li>\n<li>Use -&nbsp;Cervical spine injuries</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/2fbcdc3b4e5c446f90df3408aef00788x1063x1333.PNG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>7) Head halter traction:&nbsp;</strong></p>\n<ul>\n<li>Type - Skin traction&nbsp;</li>\n<li>Use - Cervical spine injuries</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/83537b5c921e4919b992cf4b12aeaeaex1063x1718.PNG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><p><strong>8) Halo-pelvic traction:&nbsp;</strong></p>\n<ul>\n<li>Type - Skeletal traction&nbsp;</li>\n<li>Use - Scoliosis&nbsp;</li>\n</ul></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/234c8e358bc5464a8eff036d50d773cbx1063x1368.PNG\><!---->\n      </div><!---->\n    </div><!---->'),
(2011, 'Orthopaedics', 'PM1017. Orthopedic Instruments', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/d3f6d0247c704a669cfad724f363b9eex720x1552.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/84a1042aff90448bb49f5408fed46b9bx720x403.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/306a732260ff4ce2bbc077954052b977x720x540.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/0e2b55361d02462d9d349046ab6dc932x800x600.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/8b868b0c26134874a2eeeb9c693faa4dx800x600.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/465c935b02614de4a6ef0c385131f6e5x720x540.JPEG\><!---->\n      </div><!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <!---->\n      </div><!---->\n    </div><!---->'),
(2012, 'Orthopaedics', 'PM1519. List of Names of Amputations and Their Levels', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td>\n<p><strong>TYPE</strong><strong>&nbsp;</strong></p>\n</td>\n<td>\n<p><strong>LEVEL OF AMPUTATION</strong><strong>&nbsp;</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Lisfranc</p>\n</td>\n<td>\n<p>Tarsometatarsal joint<strong>&nbsp;</strong></p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Chopart&nbsp;</p>\n</td>\n<td>\n<p>Transverse tarsal joints&nbsp;(articulation of calcaneus with cuboid and talus with navicular)</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Pirogoff</p>\n</td>\n<td>\n<p>Calcaneum is rotated forward to be fused with tibia after vertical section through its middle&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Syme&nbsp;</p>\n</td>\n<td>\n<p>Distal tibia &amp; fibula 0.6 cm proximal to the periphery of ankle joint passing through the dome of the ankle. Involves removal of both medial and lateral malleoli.</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Boyd&nbsp;</p>\n</td>\n<td>\n<p>Talectomy, forward shift of the calcaneus and calcaneo-tibial arthrodesis&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td>\n<p>Sarmiento&nbsp;</p>\n</td>\n<td>\n<p>Distal tibia and fibula: 1.3 cm proximal to the ankle joint and excision of medial and lateral malleoli&nbsp;</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <!---->\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\>\n        <img _ngcontent-smq-c132=\\ class=\img-responsive ng-tns-c132-18 ng-star-inserted\ src=\https://dhmbxeygs57ff.cloudfront.net/uploads/09ec2c4f725f4f2d8c8ee1d692677ae6x1280x1153.JPEG\><!---->\n      </div><!---->\n    </div><!---->'),
(2013, 'Orthopaedics', 'PM1509. Mechanism of injury and tests for knee injuries', '<div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\ style=\\>\n      <div _ngcontent-smq-c132=\\ class=\ng-tns-c132-18 ng-star-inserted\><table>\n<tbody>\n<tr>\n<td><strong>Structures</strong></td>\n<td><strong>Mechanism of injury</strong></td>\n<td><strong>Tests</strong></td>\n</tr>\n<tr>\n<td>Menisci</td>\n<td>Rotational force</td>\n<td>\n<p>McMurray test</p>\n<p>Apleys grinding test</p>\n<p>Squat test</p>\n<p>Thessaly test</p>\n</td>\n</tr>\n<tr>\n<td>Medial collateral ligament</td>\n<td>Valgus force</td>\n<td>Valgus stress test</td>\n</tr>\n<tr>\n<td>Lateral collateral ligament</td>\n<td>Varus force</td>\n<td>Varus stress test</td>\n</tr>\n<tr>\n<td>Anterior cruciate ligament</td>\n<td>Anterior tibial displacement</td>\n<td>\n<p>Anterior drawer test&nbsp;</p>\n<p>Lachman test</p>\n<p>Pivot shift test</p>\n</td>\n</tr>\n<tr>\n<td>Posterior cruciate ligament</td>\n<td>Posterior tibial displacement</td>\n<td>\n<p>Posterior drawer test</p>\n<p>Sag sign</p>\n</td>\n</tr>\n</tbody>\n</table></div><!---->\n      <!---->\n    </div><!---->');
